ROUTE_VERBOSE=y\nCONFIG_APP_UDHCPC=y\nCONFIG_FEATURE_RFC3397=y\nCONFIG_WGET=y\nCONFIG_FEATURE_WGET_STATUSBAR=y\nCONFIG_FEATURE_WGET_AUTHENTICATION=y\nCONFIG_FEATURE_WGET_LONG_OPTIONS=y\nCONFIG_FREE=y\nCONFIG_FUSER=y\nCONFIG_KILL=y\nCONFIG_KILLALL=y\nCONFIG_PIDOF=y\nCONFIG_FEATURE_PIDOF_SINGLE=y\nCONFIG_FEATURE_PIDOF_OMIT=y\nCONFIG_PS=y\nCONFIG_FEATURE_PS_WIDE=y\nCONFIG_BB_SYSCTL=y\nCONFIG_TOP=y\nCONFIG_FEATURE_TOP_CPU_USAGE_PERCENTAGE=y\nCONFIG_UPTIME=y\nCONFIG_FEATURE_SH_IS_ASH=y\nCONFIG_ASH=y\nCONFIG_ASH_JOB_CONTROL=y\nCONFIG_ASH_READ_NCHARS=y\nCONFIG_ASH_READ_TIMEOUT=y\nCONFIG_ASH_ALIAS=y\nCONFIG_ASH_MATH_SUPPORT=y\nCONFIG_ASH_GETOPTS=y\nCONFIG_ASH_BUILTIN_ECHO=y\nCONFIG_ASH_BUILTIN_TEST=y\nCONFIG_ASH_MAIL=y\nCONFIG_ASH_OPTIMIZE_FOR_SIZE=y\nCONFIG_ASH_EXPAND_PRMT=y\nCONFIG_SYSLOGD=y\nCONFIG_FEATURE_ROTATE_LOGFILE=y\nCONFIG_FEATURE_IPC_SYSLOG=y\nCONFIG_FEATURE_IPC_SYSLOG_BUFFER_SIZE=16\nCONFIG_LOGREAD=y\nCONFIG_FEATURE_LOGREAD_REDUCED_LOCKING=y\nCONFIG_KLOGD=y\nCONFIG_LOGGER=y','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(113,'busybox无法ping域名的解决办法','337319372009327101324432',2,'2009-04-27 22:13:24',NULL,'2009-04-27 22:13:24','2014-07-13 18:57:14','$ ping www.google.com<br />ping : bad address \'www.google.com<br />1.5.1和1.14.0的busybox都一样, 网上有人讲需要libnss_dns* ...文件, 拷贝过去还是不能用,<br /><br />手编2.6.21内核+手编1.5.1busybox+一堆deb包解压缩出gcc, 一个自定义的linux基本完成了,就差这么一个东西, hack源代码... <br />最终发现getaddrinfo这个函数调用失败，解决思路就是模拟dns协议把域名发给dns服务器，把域名转化为ip，然后再调用getaddrinfo，试验成功。<br /><br />1. 把下面这段代码加入libbb/xconnect.c的开头<br />2. 把xconnect.c的str2sockaddr函数中getaddrinfo改为hgetaddrinfo，<br />3. 把下面这段代码中的dnsip改为自己的dns(懒得写读dns的代码了)<br />/** patch of hankjin */<br />#include &lt;sys/types.h&gt;<br />#include &lt;sys/socket.h&gt;<br />#include &lt;netinet/in.h&gt;<br />#include &lt;arpa/inet.h&gt;<br />#include &lt;netdb.h&gt;<br />#include &lt;stdio.h&gt;<br />#include &lt;stdlib.h&gt;<br />#include &lt;string.h&gt;<br />#define oops(msg) { perror(msg); exit(1);}<br /><br />void fillip(char* buffer, const char* ip);<br />char* name2ip(const char* name);<br />/**<br />&nbsp;*&nbsp; * head len: 12<br />&nbsp;*&nbsp;&nbsp; * query: ? + 4<br />&nbsp;*&nbsp;&nbsp;&nbsp; * total: strlen(query.buffer) + 16<br />&nbsp;*&nbsp;&nbsp;&nbsp;&nbsp; */<br />typedef struct{<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short txid;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short flag;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short question;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short answer;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short authority;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short additional;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; char buffer[256];<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; struct {<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short type;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short cls;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }query;<br />}dns_req;<br />/** length of resp: 12 */<br />typedef struct{<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short txid;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short flag;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short question;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short answer;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short authority;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short addtional;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; char buffer[1024];<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; struct{<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short name;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short type;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short cls;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short live_l;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short live_h;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short len;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; struct in_addr addr;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }resp;<br />}dns_res;<br /><br />int hgetaddrinfo(const char *node, const char *service,<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; const struct addrinfo *hints,<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; struct addrinfo **res){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; /*&nbsp;&nbsp;&nbsp; int rc = getaddrinfo(node,service,hints,res);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;*&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(rc == 0)//todo: != -&gt; ==<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;*&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return rc;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;*&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; */<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; char *ip = name2ip(node);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return getaddrinfo(ip,service,hints,res);<br />}<br />char * name2ip(const char *node){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; struct sockaddr_in dns;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; dns_req req;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; dns_res res;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; int sockid, len, index;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; char* dnsip = \"202.114.215.73\";<br /><br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; sockid = socket(PF_INET, SOCK_DGRAM, 0);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(sockid == -1)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oops(\"socket\");<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; memset((void*)&amp;dns, 0, sizeof(dns));<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; dns.sin_family = AF_INET;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; dns.sin_port = htons(53);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; dns.sin_addr.s_addr = inet_addr(dnsip);<br /><br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; memset((void*)&amp;req, 0, sizeof(req));<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; req.txid = htons(0x4419);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; req.flag = htons(0x0100);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; req.question = htons(1);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; fillip(req.buffer,node);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; req.query.type=htons(1);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; req.query.cls=htons(1);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; memcpy(req.buffer+strlen(req.buffer)+1, (void*)(&amp;req.query), sizeof(req.query));<br /><br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; sendto(sockid, (void*)&amp;req, strlen(req.buffer)+17, 0, <br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (struct sockaddr*)&amp;dns, sizeof(dns));<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; recvfrom(sockid, (void*)&amp;res, sizeof(res), 0, <br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (struct sockaddr*)&amp;dns, &amp;len);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; index = strlen(res.buffer)+5;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; while(1){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; memcpy((void*)&amp;(res.resp), res.buffer+index, 12);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(ntohs(res.resp.type)==1){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; memcpy((void*)&amp;(res.resp.addr), res.buffer+index+12, 4);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; index += ntohs(res.resp.len) + 12;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }<br /><br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return inet_ntoa(res.resp.addr);<br />}<br /><br />void fillip(char* buffer, const char* ip){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; int i,j=0;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; for(i = 0; ip[i] != 0; i ++){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(ip[i] != \'.\'){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; buffer[i+1] = ip[i];<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; else{<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; buffer[j] = i - j;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; j = i + 1;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; buffer[j] = i - j;<br />}<br />',1,1,1,0,0,0,'$ ping www.google.comping : bad address \'www.google.com1.5.1和1.14.0的busybox都一样, 网上有人讲需要libnss_dns* ...文件, 拷贝过去还是不能用,手编2.6.21内核+手编1.5.1busybox+一堆deb包解压缩出gcc, 一个自定义的linux基本完成了,就差这么一个东西, hack源代码... 最终发现getaddrinfo这个函数调用失败，解决思路就是模拟dns协议把域名发给dns服务器，把域名转化为ip，然后再调用getaddrinfo，试验成功。1. 把下面这段代码加入libbb/xconnect.c的开头2. 把xconnect.c的str2sockaddr函数中getaddrinfo改为hgetaddrinfo，3. 把下面这段代码中的dnsip改为自己的dns(懒得写读dns的代码了)/** patch of hankjin */#include &lt;sys/types.h&gt;#include &lt;sys/socket.h&gt;#include &lt;netinet/in.h&gt;#include &lt;arpa/inet.h&gt;#include &lt;netdb.h&gt;#include &lt;stdio.h&gt;#include &lt;stdlib.h&gt;#include &lt;string.h&gt;#define oops(msg) { perror(msg); exit(1);}void fillip(char* buffer, const char* ip);char* name2ip(const char* name);/**&nbsp;*&nbsp; * head len: 12&nbsp;*&nbsp;&nbsp; * query: ? + 4&nbsp;*&nbsp;&nbsp;&nbsp; * total: strlen(query.buffer) + 16&nbsp;*&nbsp;&nbsp;&nbsp;&nbsp; */typedef struct{&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short txid;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short flag;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short question;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short answer;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short authority;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short additional;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; char buffer[256];&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; struct {&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short type;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short cls;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }query;}dns_req;/** length of resp: 12 */typedef struct{&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short txid;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short flag;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short question;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short answer;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short authority;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short addtional;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; char buffer[1024];&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; struct{&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short name;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short type;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short cls;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short live_l;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short live_h;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; u_short len;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; struct in_addr addr;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }resp;}dns_res;int hgetaddrinfo(const char *node, const char *service,&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; const struct addrinfo *hints,&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; struct addrinfo **res){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; /*&nbsp;&nbsp;&nbsp; int rc = getaddrinfo(node,service,hints,res);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;*&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(rc == 0)//todo: != -&gt; ==&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;*&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return rc;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;*&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; */&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; char *ip = name2ip(node);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return getaddrinfo(ip,service,hints,res);}char * name2ip(const char *node){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; struct sockaddr_in dns;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; dns_req req;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; dns_res res;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; int sockid, len, index;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; char* dnsip = \"202.114.215.73\";&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; sockid = socket(PF_INET, SOCK_DGRAM, 0);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(sockid == -1)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oops(\"socket\");&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; memset((void*)&amp;dns, 0, sizeof(dns));&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; dns.sin_family = AF_INET;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; dns.sin_port = htons(53);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; dns.sin_addr.s_addr = inet_addr(dnsip);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; memset((void*)&amp;req, 0, sizeof(req));&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; req.txid = htons(0x4419);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; req.flag = htons(0x0100);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; req.question = htons(1);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; fillip(req.buffer,node);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; req.query.type=htons(1);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; req.query.cls=htons(1);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; memcpy(req.buffer+strlen(req.buffer)+1, (void*)(&amp;req.query), sizeof(req.query));&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; sendto(sockid, (void*)&amp;req, strlen(req.buffer)+17, 0, &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (struct sockaddr*)&amp;dns, sizeof(dns));&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; recvfrom(sockid, (void*)&amp;res, sizeof(res), 0, &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; (struct sockaddr*)&amp;dns, &amp;len);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; index = strlen(res.buffer)+5;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; while(1){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; memcpy((void*)&amp;(res.resp), res.buffer+index, 12);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(ntohs(res.resp.type)==1){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; memcpy((void*)&amp;(res.resp.addr), res.buffer+index+12, 4);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; index += ntohs(res.resp.len) + 12;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return inet_ntoa(res.resp.addr);}void fillip(char* buffer, const char* ip){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; int i,j=0;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; for(i = 0; ip[i] != 0; i ++){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(ip[i] != \'.\'){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; buffer[i+1] = ip[i];&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; else{&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; buffer[j] = i - j;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; j = i + 1;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; buffer[j] = i - j;}','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(114,'腾讯企业邮箱','337319372010101945126230',2,'2010-11-19 17:03:05',NULL,'2010-11-19 17:03:05','2014-07-13 18:57:14','1.申请域名<br />如果有米，就很好办了，万网等都有得买，淘宝上也有。如果没米呢，就去申请个免费的：<br />http://twbbs.org提供免费二级域名,xxx.twbbs.org，xxx.cnkk.org等，而且可以设置DNS，虽然被墙了，不过可以用Web代理访问，这个Web代理就很好：http://s1.proxyexe.com/，注意要去除Remove Scripts选项，因为twbbs.org上用到了Javascript.<br />选择“註冊新 twbbs 網域”，注册一个域名<br />續用?刪除 網域 <br /><br /><br />2.申请企业邮箱<br />Google提供企业邮箱服务，可是被墙了，翻啊翻好像翻不出去，总之很麻烦。<br />不过最近QQ也有了企业邮箱服务，去http://exmail.qq.com/申请个帐户，然后绑定上面的域名，再根据说明去twbbs.org进行相应的设置<br /><br />3.设置域名DNS<br />最后根据QQ企业邮箱的说明，在twbbs.org设置好域名的DNS就可以了<br />选择“設定?修改 網域 ”，然后选择设定-&gt;设定DNS，进行配置，例如：<br /><span>qqmailc41694d4.</span>cbib.cnkk.org&nbsp;&nbsp;&nbsp; IN&nbsp;&nbsp;&nbsp; CNAME &nbsp;&nbsp;&nbsp;&nbsp; mail.qq.com<br />mail.cbib.cnkk.org&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; IN&nbsp;&nbsp;&nbsp; CNAME &nbsp; &nbsp;&nbsp; exmail.qq.com<br />cbib.cnkk.org&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; IN&nbsp;&nbsp;&nbsp; MX&nbsp;&nbsp;&nbsp;&nbsp; 10&nbsp;&nbsp;&nbsp; mxbiz2.qq.com<br />cbib.cnkk.org&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; IN&nbsp;&nbsp;&nbsp; MX&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5&nbsp; &nbsp;&nbsp; mxbiz1.qq.com',1,1,1,0,0,0,'1.申请域名如果有米，就很好办了，万网等都有得买，淘宝上也有。如果没米呢，就去申请个免费的：http://twbbs.org提供免费二级域名,xxx.twbbs.org，xxx.cnkk.org等，而且可以设置DNS，虽然被墙了，不过可以用Web代理访问，这个Web代理就很好：http://s1.proxyexe.com/，注意要去除Remove Scripts选项，因为twbbs.org上用到了Javascript.选择“註冊新 twbbs 網域”，注册一个域名續用?刪除 網域 2.申请企业邮箱Google提供企业邮箱服务，可是被墙了，翻啊翻好像翻不出去，总之很麻烦。不过最近QQ也有了企业邮箱服务，去http://exmail.qq.com/申请个帐户，然后绑定上面的域名，再根据说明去twbbs.org进行相应的设置3.设置域名DNS最后根据QQ企业邮箱的说明，在twbbs.org设置好域名的DNS就可以了选择“設定?修改 網域 ”，然后选择设定-&gt;设定DNS，进行配置，例如：qqmailc41694d4.cbib.cnkk.org&nbsp;&nbsp;&nbsp; IN&nbsp;&nbsp;&nbsp; CNAME &nbsp;&nbsp;&nbsp;&nbsp; mail.qq.commail.cbib.cnkk.org&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; IN&nbsp;&nbsp;&nbsp; CNAME &nbsp; &nbsp;&nbsp; exmail.qq.comcbib.cnkk.org&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; IN&nbsp;&nbsp;&nbsp; MX&nbsp;&nbsp;&nbsp;&nbsp; 10&nbsp;&nbsp;&nbsp; mxbiz2.qq.comcbib.cnkk.org&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; IN&nbsp;&nbsp;&nbsp; MX&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5&nbsp; &nbsp;&nbsp; mxbiz1.qq.com','',0,'admin',0,'','zinnia/_entry_detail.html','entry_detail.html'),(115,'Win8','337319372012773173181',2,'2012-08-07 15:17:31',NULL,'2012-08-07 15:17:31','2014-07-13 18:57:14','1. 下载Win8 Professional版,解压缩到F盘根目录<br /><a href=\"magnet:?xt=urn:btih:5990C764FC3E36EA583B80FD3A16A2D9DB3441D9\" rel=\"nofollow\">magnet:?xt=urn:btih:5990C764FC3E36EA583B80FD3A16A2D9DB3441D9</a><br />2. 下载hdd installer, 运行<br />3. 重启后，按提示输入CODE<br />XKY4K-2NRWR-8F6P2-448RF-CRYQH<br />4. 安装完成后<br />4.1. 增加中文语言<br />4.2. 非unicode默认中文，重启<br />5. 加入域<br />5.1. lusrmgr.msc 取消帐户禁用<br />5.2. netplwiz取消输出密码<br />5.3. 用administrator登录<br />5.4. 用分公司工具加域成功重启',1,1,1,0,0,0,'1. 下载Win8 Professional版,解压缩到F盘根目录magnet:?xt=urn:btih:5990C764FC3E36EA583B80FD3A16A2D9DB3441D92. 下载hdd installer, 运行3. 重启后，按提示输入CODEXKY4K-2NRWR-8F6P2-448RF-CRYQH4. 安装完成后4.1. 增加中文语言4.2. 非unicode默认中文，重启5. 加入域5.1. lusrmgr.msc 取消帐户禁用5.2. netplwiz取消输出密码5.3. 用administrator登录5.4. 用分公司工具加域成功重启','',0,'',0,'','zinnia/_entry_detail.html','entry_detail.html'),(116,'自己动手写web服务器 ihttp 1.0[Fw]','33731937200932921237207',2,'2009-04-29 14:12:37',NULL,'2009-04-29 14:12:37','2014-07-13 18:57:14','一个简单的Web服务器, &lt;&lt;C and Linux Programming - advanced&gt;&gt;上的示例代码, 使用动态链接库机制, 动态加载模块, 有一个html模块支持html代码, <br /><br /><a href=\"http://dl.getdropbox.com/u/194885/ihttp1.0.tar.gz\" rel=\"nofollow\" target=\"_blank\">源代码下载</a>:<br /><br />使用说明:<br />tar xf ihttp1.0.tar.gz<br />cd ihttp<br />make<br />./ihttp<br />然后直接从浏览器访问即可: http://127.0.0.1:1234/<br />./ihttp -h查看说明<br /><br />截个图:<br /><a href=\"/static/163/5647232457746364225.jpg\" target=\"_blank\"><img alt=\"自己动手写web服务器 ihttp 1.0[Fw] - hankjin - 乱-Blog\" src=\"/static/163/5647232457746364225.jpg\" style=\"width: 595px; height: 352px;\" title=\"自己动手写web服务器 ihttp 1.0[Fw] - hankjin - 乱-Blog\" /></a><br /><br />',1,1,1,0,0,0,'一个简单的Web服务器, &lt;&lt;C and Linux Programming - advanced&gt;&gt;上的示例代码, 使用动态链接库机制, 动态加载模块, 有一个html模块支持html代码, 源代码下载:使用说明:tar xf ihttp1.0.tar.gzcd ihttpmake./ihttp然后直接从浏览器访问即可: http://127.0.0.1:1234/./ihttp -h查看说明截个图:','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(117,'CDLinux 0.4.9开发环境','33731937200893110523704',2,'2008-10-31 22:05:23',NULL,'2008-10-31 22:05:23','2014-07-13 18:57:14','CDLinuxd的开发环境是gcc-2.95.3+glibc-2.3.5，采用Slackware8.1作为开发环境，因为库一样的...，怀疑CDLinux0.4.9就是在Slackware8.1下开发的<br /><br />开发环境：<br />虚拟机1： Slackware 8.1<br />虚拟机2： CDLinux 0.4.9，IDE硬盘，把CDLinux包拷贝过去<br />启动时从光盘启动，启动参数: cdlinux CDL_DEV=/dev/hda1<br /><br />除了使用Slackware进行开发以外，RedHat 6.2的rpm包和一些deb包也可以用<br />常用命令：<br /><div>\n\n    \n\n\n\n\n\n\n\n\n\n\n<ol><li>解压缩rpm包：<ol><li> rpm2cpio xxx.rpm | cpio -idmv\n</li><li>使用deb包，CDlinux竟然自身提供了一个undeb工具，尝试lua40过程成功，结果失败(因为lua40用的是glibc2.3的库)。使用方法: <br /></li></ol></li><li>解压缩deb包：<ol><li> rpm2cpio xxx.rpm | cpio -idmv\n</li><li>undeb -x xxx.deb; tar xf data.tar.gz&nbsp; <br />\n</li></ol></li><li>打包\n<ol><li>tar zcvf python-2.1-cdl.tgz usr\n</li></ol></li></ol>\n\n\n\n\n\n\n        \n  </div>',1,1,1,0,0,0,'CDLinuxd的开发环境是gcc-2.95.3+glibc-2.3.5，采用Slackware8.1作为开发环境，因为库一样的...，怀疑CDLinux0.4.9就是在Slackware8.1下开发的开发环境：虚拟机1： Slackware 8.1虚拟机2： CDLinux 0.4.9，IDE硬盘，把CDLinux包拷贝过去启动时从光盘启动，启动参数: cdlinux CDL_DEV=/dev/hda1除了使用Slackware进行开发以外，RedHat 6.2的rpm包和一些deb包也可以用常用命令：\n\n    \n\n\n\n\n\n\n\n\n\n\n解压缩rpm包： rpm2cpio xxx.rpm | cpio -idmv\n使用deb包，CDlinux竟然自身提供了一个undeb工具，尝试lua40过程成功，结果失败(因为lua40用的是glibc2.3的库)。使用方法: 解压缩deb包： rpm2cpio xxx.rpm | cpio -idmv\nundeb -x xxx.deb; tar xf data.tar.gz&nbsp; \n打包\ntar zcvf python-2.1-cdl.tgz usr\n\n\n\n\n\n\n\n        \n  ','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(118,'3110c蓝牙连接电脑上网','33731937201110572915377',2,'2011-11-05 07:29:25',NULL,'2011-11-05 07:29:25','2014-07-13 18:57:14','1. Nokia套件方式:<br />安装Nokia套件,大度搜索\"Nokia套件\",然后选择相应的机型下载.<br />安装蓝牙驱动<br />打开Nokia套件, 使用蓝牙连接手机, 然后在Nokia套件上选择Internet连接<br />然后浏览器设置代理为10.0.0.172:80 <br />OK<br /><br />2. 不使用套件的方式:<br />蓝牙连接-&gt;拨号网络-&gt; *99#',1,1,1,0,0,0,'1. Nokia套件方式:安装Nokia套件,大度搜索\"Nokia套件\",然后选择相应的机型下载.安装蓝牙驱动打开Nokia套件, 使用蓝牙连接手机, 然后在Nokia套件上选择Internet连接然后浏览器设置代理为10.0.0.172:80 OK2. 不使用套件的方式:蓝牙连接-&gt;拨号网络-&gt; *99#','',0,'',0,'','zinnia/_entry_detail.html','entry_detail.html'),(119,'踟蹰','337319372009101661313596',2,'2009-11-16 18:13:13',NULL,'2009-11-16 18:13:13','2014-07-13 18:57:14','咸阳殿上性命捐<br />冲冠一怒为红颜<br />名不正兮言不顺<br />拔剑踟蹰不能前<br />',1,1,1,0,0,0,'咸阳殿上性命捐冲冠一怒为红颜名不正兮言不顺拔剑踟蹰不能前','',0,'',0,'','zinnia/_entry_detail.html','entry_detail.html'),(120,'ubuntu下安装Wine并解决中文乱码','33731937200841034521275',2,'2008-05-10 03:45:21',NULL,'2008-05-10 03:45:21','2014-07-13 18:57:14','sudo apt-get install wine<br />安装中文字体，从Windows的C:\\windows\\fonts目录下找到simsun.ttc和simfang.ttc<br />cp simsun.ttc ~/.wine/drive_c/fonts<br />cp simfang.ttc ~/.wine/drive_c/fonts<br /><br /><b>配置中文</b><br />vi ~/.wine/system.reg<br />找到<br />\"MS Shell Dlg\"=\"Tahoma\"<br /> \"MS Shell Dlg 2\"=\"Tahoma\"<br /> 修改为<br />\"MS Shell Dlg\"=\"SimSun\"<br />\"MS Shell Dlg 2\"=\"SimSun\"<br /><br /><b>配置命令行</b><br />在英文Ubuntu环境下，需要设置环境为zh_CN.UTF-8<br />alias wine=\"env LANG=zh_CN.UTF-8 wine\"<br /><br /><b>安装dll</b><br />wget <a href=\"http://www.kegel.com/wine/winetricks\" rel=\"nofollow\">http://www.kegel.com/wine/winetricks</a><br />chmod +x winetricks<br />./winetricks msxml3 gdiplus riched20 riched30 vcrun6 vcrun2005sp1<br /><br />然后执行<br />wine xxx.exe<br />即可',1,1,1,0,0,0,'sudo apt-get install wine安装中文字体，从Windows的C:\\windows\\fonts目录下找到simsun.ttc和simfang.ttccp simsun.ttc ~/.wine/drive_c/fontscp simfang.ttc ~/.wine/drive_c/fonts配置中文vi ~/.wine/system.reg找到\"MS Shell Dlg\"=\"Tahoma\" \"MS Shell Dlg 2\"=\"Tahoma\" 修改为\"MS Shell Dlg\"=\"SimSun\"\"MS Shell Dlg 2\"=\"SimSun\"配置命令行在英文Ubuntu环境下，需要设置环境为zh_CN.UTF-8alias wine=\"env LANG=zh_CN.UTF-8 wine\"安装dllwget http://www.kegel.com/wine/winetrickschmod +x winetricks./winetricks msxml3 gdiplus riched20 riched30 vcrun6 vcrun2005sp1然后执行wine xxx.exe即可','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(121,'思维脑图+计划+日志','337319372012218113134698',2,'2012-03-18 23:38:41',NULL,'2012-03-18 23:38:41','2014-07-13 18:57:14','A一直想做个计划提醒类软件。1. 支持各种循环，2. 提醒方式支持短信和邮件，3.支持程序,Web和手机，4. 方便地同步<div>1. 支持循环，包括农历生日每年提醒，阳历生日每年提醒，锻炼身体每周提醒，起床每天提醒之类的。</div><div>2. 主要是短信提醒，自动发短信到手机</div><div>3. 可以以应用程序，Web界面，手机三种方式访问，应用程序主要是制定计划，临时上网时通过Web界面进行查看和简单操作，平常用手机查看</div><div>4. 应用程序</div><div>这几个功能都有现成的软件，可是很难互相同步，而且也不复杂，因此可以自己搞，虽然有点重复发明轮子的味道。</div><div>B还想做一个日志软件，写日记的，和计划软件集成，可以查看这些计划的执行情况。</div><div>C一大堆事情列在一起，没有条理，使用思维脑图来组织会更清楚，因此集成思维脑图功能，这个就是一个比较酷的东西了。</div><div><br /></div><div>FreeMind是个Java的开源思维脑图，功能很强大，基于这个，增强计划的循环功能，增加日志功能和同步功能，基本雏形就出来了。</div><div>I have a dream.</div>',1,1,1,0,0,0,'A一直想做个计划提醒类软件。1. 支持各种循环，2. 提醒方式支持短信和邮件，3.支持程序,Web和手机，4. 方便地同步1. 支持循环，包括农历生日每年提醒，阳历生日每年提醒，锻炼身体每周提醒，起床每天提醒之类的。2. 主要是短信提醒，自动发短信到手机3. 可以以应用程序，Web界面，手机三种方式访问，应用程序主要是制定计划，临时上网时通过Web界面进行查看和简单操作，平常用手机查看4. 应用程序这几个功能都有现成的软件，可是很难互相同步，而且也不复杂，因此可以自己搞，虽然有点重复发明轮子的味道。B还想做一个日志软件，写日记的，和计划软件集成，可以查看这些计划的执行情况。C一大堆事情列在一起，没有条理，使用思维脑图来组织会更清楚，因此集成思维脑图功能，这个就是一个比较酷的东西了。FreeMind是个Java的开源思维脑图，功能很强大，基于这个，增强计划的循环功能，增加日志功能和同步功能，基本雏形就出来了。I have a dream.','',0,'favorite',0,'','zinnia/_entry_detail.html','entry_detail.html'),(122,'自己动手做红蓝3D图片','33731937201051782419374',2,'2010-06-17 20:24:19',NULL,'2010-06-17 20:24:19','2014-07-13 18:57:14','<div>红蓝3D图片很简单<br />前先取两张图片，一张是左眼看到的，一张是右眼看到的<br />然后对任一点，取左眼看到的图片的R值，右眼看到的图片的G与B值，这样合成的图片就是3D图片。<br />最后戴上红蓝眼镜就能看到效果了。<br /><br />import Image<br />imL=Image.open(\'left.jpg\')<br />imR=Image.open(\'right.jpg\')<br />w,h=imL.size<br />im=Image.new(\'RGB\',(w*2,h))<br />for i in range(w):<br />&nbsp;&nbsp;&nbsp; for j in range(h):<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cL=imL.getpixel((i,j))<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cR=imR.getpixel((i,j))<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; im.putpixel((i,j), cL)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; im.putpixel((i+w,j), cR)<br />im.save(\'final.jpg\')<br />im.show()<br />最后是left.jpg和right.jpg,以及合成的final.jpg<br />left.jpg:<br /><img alt=\"自己动手做红蓝3D图片 - hankjin - 云水居\" src=\"/static/163/3342515348439397137.jpg\" style=\"margin: 0pt 10px 0pt 0pt;\" title=\"自己动手做红蓝3D图片 - hankjin - 云水居\" /></div>&nbsp;right.jpg:<div><img alt=\"自己动手做红蓝3D图片 - hankjin - 云水居\" src=\"/static/163/1517150124470606807.jpg\" style=\"margin: 0pt 10px 0pt 0pt;\" title=\"自己动手做红蓝3D图片 - hankjin - 云水居\" /><br />final.jpg<br /><div><div><img alt=\"自己动手做红蓝3D图片 - hankjin - 云水居\" src=\"/static/163/2371145203810735372.jpg\" style=\"margin: 0pt 10px 0pt 0pt;\" title=\"自己动手做红蓝3D图片 - hankjin - 云水居\" /></div>&nbsp;</div>&nbsp;<br /></div>',1,1,1,0,0,0,'红蓝3D图片很简单前先取两张图片，一张是左眼看到的，一张是右眼看到的然后对任一点，取左眼看到的图片的R值，右眼看到的图片的G与B值，这样合成的图片就是3D图片。最后戴上红蓝眼镜就能看到效果了。import ImageimL=Image.open(\'left.jpg\')imR=Image.open(\'right.jpg\')w,h=imL.sizeim=Image.new(\'RGB\',(w*2,h))for i in range(w):&nbsp;&nbsp;&nbsp; for j in range(h):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cL=imL.getpixel((i,j))&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cR=imR.getpixel((i,j))&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; im.putpixel((i,j), cL)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; im.putpixel((i+w,j), cR)im.save(\'final.jpg\')im.show()最后是left.jpg和right.jpg,以及合成的final.jpgleft.jpg:&nbsp;right.jpg:final.jpg&nbsp;&nbsp;','',0,'mood',0,'','zinnia/_entry_detail.html','entry_detail.html'),(123,'bash的寂寞3-bash计算阶乘','337319372009829112216122',2,'2009-09-29 11:22:16',NULL,'2009-09-29 11:22:16','2014-07-13 18:57:14','效率很低，计算100！竟然要将近5秒<br />$ time ./test.sh 100<br />100!=93326215443944152681699238856266700490715968264381621468592963895217599993229915608941463976156518286253697920827223758251185210916864000000000000000000000000<br /><br />real&nbsp;&nbsp;&nbsp; 0m4.665s<br />user&nbsp;&nbsp;&nbsp; 0m4.550s<br />sys&nbsp;&nbsp;&nbsp;&nbsp; 0m0.120s<br />源码：<br />#!/bin/bash<br />function nx # calculate n!<br />{<br />&nbsp;&nbsp;&nbsp; if [ $1 -eq 1 ]; then #return condition<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for x in $@<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; do<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; total[$i]=$x<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; i=$(($i+1))<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; done<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for((i=$(($#-1));i&gt;0;i--))<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; do<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; echo -n ${total[$i]}<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; done<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; echo<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; exit 0<br />&nbsp;&nbsp;&nbsp; fi<br />&nbsp;&nbsp;&nbsp; for x in $@<br />&nbsp;&nbsp;&nbsp; do<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; total[$i]=$x<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; i=$(($i+1))<br />&nbsp;&nbsp;&nbsp; done<br />&nbsp;&nbsp;&nbsp; for((i=$(($#-1));i&gt;0;i--))<br />&nbsp;&nbsp;&nbsp; do<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; result[$i]=$((${total[$i]}*$1))<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if [ ${result[$i]} -gt 9 ]; then<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; temp=${result[$i]}<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for((j=0;j&lt;10000;j++))<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; do<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; result[(($i+$j))]=$(($temp%10))<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; result[(($i+$j+1))]=$((${result[(($i+$j+1))]}+$temp/10))<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; temp=${result[(($i+$j+1))]}<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if [ $temp -lt 10 ]; then<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; break<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fi<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; done<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fi<br />&nbsp;&nbsp;&nbsp; done<br />&nbsp;&nbsp;&nbsp; nx $(($1-1)) ${result[*]}<br />}<br />if [ \"$1\" = \"\" ]; then<br />&nbsp;&nbsp;&nbsp; echo -e \"Usage: $0 &lt;n&gt;\\neg: $0 30 \"<br />else<br />&nbsp;&nbsp;&nbsp; echo -n \"$1!=\"<br />&nbsp;&nbsp;&nbsp; nx $1 1<br />fi<br /><br />',1,1,1,0,0,0,'效率很低，计算100！竟然要将近5秒$ time ./test.sh 100100!=93326215443944152681699238856266700490715968264381621468592963895217599993229915608941463976156518286253697920827223758251185210916864000000000000000000000000real&nbsp;&nbsp;&nbsp; 0m4.665suser&nbsp;&nbsp;&nbsp; 0m4.550ssys&nbsp;&nbsp;&nbsp;&nbsp; 0m0.120s源码：#!/bin/bashfunction nx # calculate n!{&nbsp;&nbsp;&nbsp; if [ $1 -eq 1 ]; then #return condition&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for x in $@&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; do&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; total[$i]=$x&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; i=$(($i+1))&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; done&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for((i=$(($#-1));i&gt;0;i--))&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; do&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; echo -n ${total[$i]}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; done&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; echo&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; exit 0&nbsp;&nbsp;&nbsp; fi&nbsp;&nbsp;&nbsp; for x in $@&nbsp;&nbsp;&nbsp; do&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; total[$i]=$x&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; i=$(($i+1))&nbsp;&nbsp;&nbsp; done&nbsp;&nbsp;&nbsp; for((i=$(($#-1));i&gt;0;i--))&nbsp;&nbsp;&nbsp; do&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; result[$i]=$((${total[$i]}*$1))&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if [ ${result[$i]} -gt 9 ]; then&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; temp=${result[$i]}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for((j=0;j&lt;10000;j++))&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; do&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; result[(($i+$j))]=$(($temp%10))&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; result[(($i+$j+1))]=$((${result[(($i+$j+1))]}+$temp/10))&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; temp=${result[(($i+$j+1))]}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if [ $temp -lt 10 ]; then&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; break&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fi&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; done&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fi&nbsp;&nbsp;&nbsp; done&nbsp;&nbsp;&nbsp; nx $(($1-1)) ${result[*]}}if [ \"$1\" = \"\" ]; then&nbsp;&nbsp;&nbsp; echo -e \"Usage: $0 &lt;n&gt;\\neg: $0 30 \"else&nbsp;&nbsp;&nbsp; echo -n \"$1!=\"&nbsp;&nbsp;&nbsp; nx $1 1fi','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(124,'2012云南之行','33731937201282815356514',2,'2012-09-28 13:53:56',NULL,'2012-09-28 13:53:56','2014-07-13 18:57:14','转眼1个多月了，云南之行未留片言，挖个坑，国庆回来写<br /><br />',1,1,1,0,0,0,'转眼1个多月了，云南之行未留片言，挖个坑，国庆回来写','',0,'',0,'','zinnia/_entry_detail.html','entry_detail.html'),(125,'神经网络之Feedforward Backpropagation前馈反向传播','3373193720106155950898',2,'2010-07-15 17:09:50',NULL,'2010-07-15 17:09:50','2014-07-13 18:57:14','mark',1,1,1,0,0,0,'mark','',0,'other',0,'','zinnia/_entry_detail.html','entry_detail.html'),(126,'gcj的移植','337319372009388252042',2,'2009-04-08 20:25:20',NULL,'2009-04-08 20:25:20','2014-07-13 18:57:14','java虽然号称一次编译到处运行, 但是实际情况中的移植问题很多, 虽然有exe4j等工具将java封装成可执行文件,但是换汤不换药, 基本原理还是要跑虚拟机的.<br />然而在linux上, 因为有gcj, java程序的移植还是比较方便的<br />gcj Test.java --main=Test<br />然后把生成的a.out拷贝到目标机上, 运行时提示缺少libgcj.so.90, 从开发机上的/usr/lib下找到 libgcj.so.90.0.0拷贝到目标机上即可<br />',1,1,1,0,0,0,'java虽然号称一次编译到处运行, 但是实际情况中的移植问题很多, 虽然有exe4j等工具将java封装成可执行文件,但是换汤不换药, 基本原理还是要跑虚拟机的.然而在linux上, 因为有gcj, java程序的移植还是比较方便的gcj Test.java --main=Test然后把生成的a.out拷贝到目标机上, 运行时提示缺少libgcj.so.90, 从开发机上的/usr/lib下找到 libgcj.so.90.0.0拷贝到目标机上即可','',0,'j2se',0,'','zinnia/_entry_detail.html','entry_detail.html'),(127,'Hadoop源码之路一：安装Hadoop','3373193720131113620245',2,'2013-12-13 18:22:40',NULL,'2013-12-13 18:22:40','2014-07-13 18:57:14','<br /><div>1. 下载</div><div>sudo apt-get install openjdk-7-jdk</div><div>wget http://mirror.esocc.com/apache/hadoop/core/stable2/hadoop-2.2.0.tar.gz</div><div>tar xf hadoop-2.2.0.tar.gz</div><div>mv hadoop-2.2.0 hadoop</div><div>cd hadoop</div><div>2. 修改配置</div><div>2.1 vi etc/hadoop/hadoop-env.sh</div><div>export JAVA_HOME=/usr/lib/jvm/java-7-openjdk-i386</div><div>export HADOOP_HEAPSIZE=300</div><div>2.2 vi etc/hadoop/yarn-env.sh</div><div><span style=\"line-height: 28px;\">export JAVA_HOME=</span>/usr/lib/jvm/java-7-openjdk-i386</div><div><span style=\"line-height: 28px;\">2.3 vi etc/hadoop/core-site.xml</span></div><div><div style=\"line-height: 28px;\">&lt;configuration&gt;</div><div style=\"line-height: 28px;\">&nbsp; &nbsp; &lt;property&gt;</div><div style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;fs.default.name&lt;/name&gt;</div><div style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;hdfs://127.0.0.1:9000&lt;/value&gt;</div><div style=\"line-height: 28px;\">&nbsp; &nbsp; &lt;/property&gt;</div><div style=\"line-height: 28px;\">&nbsp; &nbsp; &lt;property&gt;</div><div style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;hadoop.tmp.dir&lt;/name&gt;</div><div style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;/home/hankjin/hadoop/tmp&lt;/value&gt;</div><div style=\"line-height: 28px;\">&nbsp; &nbsp; &lt;/property&gt;</div><div style=\"line-height: 28px;\">&lt;/configuration&gt;</div><div style=\"line-height: 28px;\">2.4 vi etc/hadoop/hdfs-site.xml</div><div><div>&lt;configuration&gt;</div><div>&nbsp; &nbsp; &lt;property&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;dfs.default.name&lt;/name&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;/home/hankjin/hadoop/tmp&lt;/value&gt;</div><div>&nbsp; &nbsp; &lt;/property&gt;</div><div>&nbsp; &nbsp; &lt;property&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;dfs.replication&lt;/name&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;1&lt;/value&gt;</div><div>&nbsp; &nbsp; &lt;/property&gt;</div><div>&nbsp; &nbsp; &lt;property&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;dfs.permission&lt;/name&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;false&lt;/value&gt;</div><div>&nbsp; &nbsp; &lt;/property&gt;</div><div>&lt;/configuration&gt;</div></div></div><div>2.5 vi etc/hadoop/mapred-site.xml</div><div><div>&lt;configuration&gt;</div><div>&nbsp; &nbsp; &lt;property&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;mapreduce.framework.name&lt;/name&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;yarn&lt;/value&gt;</div><div>&nbsp; &nbsp; &lt;/property&gt;</div><div>&lt;/configuration&gt;</div></div><div>2.6 vi etc/hadoop/yarn-site.xml</div><div><div>&lt;configuration&gt;</div><div>&nbsp; &nbsp; &lt;property&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.resourcemanager.resource-tracker.address&lt;/name&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;127.0.0.1:8990&lt;/value&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;</div><div>&nbsp; &nbsp; &lt;/property&gt;</div><div>&nbsp; &nbsp; &lt;property&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.resourcemanager.scheduler.address&lt;/name&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;127.0.0.1:8991&lt;/value&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;</div><div>&nbsp; &nbsp; &lt;/property&gt;</div><div>&nbsp; &nbsp; &lt;property&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.resourcemanager.scheduler.class&lt;/name&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;org.apache.hadoop.yarn.server.resourcemanager.scheduler.capacity.CapacityScheduler&lt;/value&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;</div><div>&nbsp; &nbsp; &lt;/property&gt;</div><div>&nbsp; &nbsp; &lt;property&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.resourcemanager.address&lt;/name&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;127.0.0.1:8993&lt;/value&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;</div><div>&nbsp; &nbsp; &lt;/property&gt;</div><div>&nbsp; &nbsp; &lt;property&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.nodemanager.local-dirs&lt;/name&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;/home/hankjin/hadoop/tmp/node&lt;/value&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;</div><div>&nbsp; &nbsp; &lt;/property&gt;</div><div>&nbsp; &nbsp; &lt;property&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.nodemanager.address&lt;/name&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;127.0.0.1:8994&lt;/value&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;</div><div>&nbsp; &nbsp; &lt;/property&gt;</div><div>&nbsp; &nbsp; &lt;property&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.nodemanager.resource.memory-mb&lt;/name&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;3072&lt;/value&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;</div><div>&nbsp; &nbsp; &lt;/property&gt;</div><div>&nbsp; &nbsp; &lt;property&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.nodemanager.remote-app-log-dir&lt;/name&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;/home/hankjin/hadoop/tmp/app-logs&lt;/value&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;</div><div>&nbsp; &nbsp; &lt;/property&gt;</div><div>&nbsp; &nbsp; &lt;property&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.nodemanager.log-dirs&lt;/name&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;/home/hankjin/hadoop/tmp/node&lt;/value&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;</div><div>&nbsp; &nbsp; &lt;/property&gt;</div><div>&nbsp; &nbsp; &lt;property&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.nodemanager.aux-services&lt;/name&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;mapreduce_shuffle&lt;/value&gt;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;</div><div>&nbsp; &nbsp; &lt;/property&gt;</div><div>&lt;/configuration&gt;</div></div><div>2.7 vi etc/hadoop/slaves</div><div>127.0.0.1</div><div><br /></div><div>3. 启动并测试</div><div>bin/hadoop namenode -format</div><div>./sbin/start-dfs.sh</div><div>./sbin/start-yarn.sh</div><div>wget http://www.gutenberg.org/cache/epub/20417/pg20417.txt</div><div>bin/hadoop dfs -mkdir /tmp</div><div>bin/hadoop dfs -copyFromLocal pg20417.txt /tmp/</div><div>bin/hadoop jar ./share/hadoop/mapreduce/hadoop-mapreduce-examples-2.2.0.jar wordcount /tmp/pg20417.txt /tmp-output</div><div>http://127.0.0.1:8088/</div>',1,1,1,0,0,0,'1. 下载sudo apt-get install openjdk-7-jdkwget http://mirror.esocc.com/apache/hadoop/core/stable2/hadoop-2.2.0.tar.gztar xf hadoop-2.2.0.tar.gzmv hadoop-2.2.0 hadoopcd hadoop2. 修改配置2.1 vi etc/hadoop/hadoop-env.shexport JAVA_HOME=/usr/lib/jvm/java-7-openjdk-i386export HADOOP_HEAPSIZE=3002.2 vi etc/hadoop/yarn-env.shexport JAVA_HOME=/usr/lib/jvm/java-7-openjdk-i3862.3 vi etc/hadoop/core-site.xml&lt;configuration&gt;&nbsp; &nbsp; &lt;property&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;fs.default.name&lt;/name&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;hdfs://127.0.0.1:9000&lt;/value&gt;&nbsp; &nbsp; &lt;/property&gt;&nbsp; &nbsp; &lt;property&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;hadoop.tmp.dir&lt;/name&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;/home/hankjin/hadoop/tmp&lt;/value&gt;&nbsp; &nbsp; &lt;/property&gt;&lt;/configuration&gt;2.4 vi etc/hadoop/hdfs-site.xml&lt;configuration&gt;&nbsp; &nbsp; &lt;property&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;dfs.default.name&lt;/name&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;/home/hankjin/hadoop/tmp&lt;/value&gt;&nbsp; &nbsp; &lt;/property&gt;&nbsp; &nbsp; &lt;property&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;dfs.replication&lt;/name&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;1&lt;/value&gt;&nbsp; &nbsp; &lt;/property&gt;&nbsp; &nbsp; &lt;property&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;dfs.permission&lt;/name&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;false&lt;/value&gt;&nbsp; &nbsp; &lt;/property&gt;&lt;/configuration&gt;2.5 vi etc/hadoop/mapred-site.xml&lt;configuration&gt;&nbsp; &nbsp; &lt;property&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;mapreduce.framework.name&lt;/name&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;yarn&lt;/value&gt;&nbsp; &nbsp; &lt;/property&gt;&lt;/configuration&gt;2.6 vi etc/hadoop/yarn-site.xml&lt;configuration&gt;&nbsp; &nbsp; &lt;property&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.resourcemanager.resource-tracker.address&lt;/name&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;127.0.0.1:8990&lt;/value&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;&nbsp; &nbsp; &lt;/property&gt;&nbsp; &nbsp; &lt;property&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.resourcemanager.scheduler.address&lt;/name&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;127.0.0.1:8991&lt;/value&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;&nbsp; &nbsp; &lt;/property&gt;&nbsp; &nbsp; &lt;property&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.resourcemanager.scheduler.class&lt;/name&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;org.apache.hadoop.yarn.server.resourcemanager.scheduler.capacity.CapacityScheduler&lt;/value&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;&nbsp; &nbsp; &lt;/property&gt;&nbsp; &nbsp; &lt;property&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.resourcemanager.address&lt;/name&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;127.0.0.1:8993&lt;/value&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;&nbsp; &nbsp; &lt;/property&gt;&nbsp; &nbsp; &lt;property&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.nodemanager.local-dirs&lt;/name&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;/home/hankjin/hadoop/tmp/node&lt;/value&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;&nbsp; &nbsp; &lt;/property&gt;&nbsp; &nbsp; &lt;property&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.nodemanager.address&lt;/name&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;127.0.0.1:8994&lt;/value&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;&nbsp; &nbsp; &lt;/property&gt;&nbsp; &nbsp; &lt;property&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.nodemanager.resource.memory-mb&lt;/name&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;3072&lt;/value&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;&nbsp; &nbsp; &lt;/property&gt;&nbsp; &nbsp; &lt;property&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.nodemanager.remote-app-log-dir&lt;/name&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;/home/hankjin/hadoop/tmp/app-logs&lt;/value&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;&nbsp; &nbsp; &lt;/property&gt;&nbsp; &nbsp; &lt;property&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.nodemanager.log-dirs&lt;/name&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;/home/hankjin/hadoop/tmp/node&lt;/value&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;&nbsp; &nbsp; &lt;/property&gt;&nbsp; &nbsp; &lt;property&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;name&gt;yarn.nodemanager.aux-services&lt;/name&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;value&gt;mapreduce_shuffle&lt;/value&gt;&nbsp; &nbsp; &nbsp; &nbsp; &lt;description&gt;&lt;/description&gt;&nbsp; &nbsp; &lt;/property&gt;&lt;/configuration&gt;2.7 vi etc/hadoop/slaves127.0.0.13. 启动并测试bin/hadoop namenode -format./sbin/start-dfs.sh./sbin/start-yarn.shwget http://www.gutenberg.org/cache/epub/20417/pg20417.txtbin/hadoop dfs -mkdir /tmpbin/hadoop dfs -copyFromLocal pg20417.txt /tmp/bin/hadoop jar ./share/hadoop/mapreduce/hadoop-mapreduce-examples-2.2.0.jar wordcount /tmp/pg20417.txt /tmp-outputhttp://127.0.0.1:8088/','',0,'',0,'','zinnia/_entry_detail.html','entry_detail.html'),(128,'洗牌算法','3373193720109141128016',2,'2010-10-14 11:54:03',NULL,'2010-10-14 11:54:03','2014-07-13 18:57:14','54张有序的扑克牌，设计一种算法，实现洗牌操作：<br />方法一：<br />1。随机产生一个1-n的数x，做为第一张牌。<br />2。随机产生一个1-(n-1)的数y，如果y&lt;x，则将y作为第二张牌，否则将y+1作为第二张牌。<br />3。随机产生一个1-(n-i)的数z，取第z个没有被抽出来的作为第i张牌。(i=3,4,5...54)<br />这种算法的复杂度为O(N^2)，因为计算每个随机数的牌号平均要执行(N/2)次比较。<br />对应于现实中的扑克牌，这种算法等于每次从牌堆中随机抽一张，放到另一堆上，直到抽完为止，这里新的一堆就是洗完的牌序。<br /><br />方法二：<br />1。随机产生一个1-n的数x，然后让第x张牌和第1张牌互相调换。<br />2。随机产生一个1-n的数y，然后让第y张牌和第2张牌互相调换。<br />3。随机产生一个1-n的数z，然后让第z张牌和第i张牌互相调换。(i=3,4,5...54)<br />这种算法的复杂度为O(N)。<br /><br />方法三：<br />因为一共有N!种洗牌结果，所以可以等概率地产生一个1-N!之间的随机数x，然后用康托展开的方法，根据x生成对应的排列，即为洗牌结果。<br />关于康托展开，参考http://baike.baidu.com/view/437641.htm<br /><br />方法一的改进：<br />方法一由于计算随机数对应的牌号平均要执行(N/2)次比较，所以复杂度为O(N^2)，N*N的第一个N是牌的数目，无法优化，但是第二个N是确定牌的序号，这个是否有办法优化呢？<br />第一种优化就是每次抽出一张牌后，就将之后的所有牌向前移动一个单位，下次产生的随机数就是目标牌的下标。<br />如第一次随机数为3，抽出3，扑克牌数组变为1 2 4 5 6 7...<br />第二次抽出6，直接取数组的第六个元素即7，然后把7之后的牌前移一个单位。这样牌的定位复杂度就是1了，但是移动的复杂度仍为N/2，所以这种算法并没有起到效率优化的目标。<br />第二种优化是为树状数组。用一个长为54的树状数组 used 存储 1-i 张牌中被投抽出来的牌的张数。这样 i-used[i] 就是牌号为 i 的扑克的序号。然后用2分查找的方式，就可以快速确定随机数对应的牌号，树状树的复杂度为logn，这样总的算法复杂度为O(NlogN)。<br />但是考虑到一种54张牌，树状数组优化带来的性能提升很小，比起程序代码带来的可维护性损失，颇不值得。<br /><br />方法二的修正：<br />方法二乍看好像不错，网上也有很多文章使用这种算法，但是其实这是一种错误的方法，因为方法二的所有可能性为N^N，而洗好的牌一种有N!种可能，又因为N^N % N! !=0，所以每种结果的概率是不相同的。<br />那么如何修正这个问题呢？仿照第一种方法，第i次洗牌不是产生一个1-n的随机数，而是产生一个i-n的随机数，这样可能性结果的可能性就是N!了。就有可能概率相等了。<br />证明在&lt;&lt;计算机程序设计艺术&gt;&gt;上。<br /><br />方法一代码：<br />#include &lt;stdio.h&gt;<br />#include &lt;stdlib.h&gt;<br />#define N 54<br /><br />int poker[N+1];<br />void shuffle(){<br />&nbsp;&nbsp;&nbsp; int used[N+1];<br />&nbsp;&nbsp;&nbsp; int i,j,k;<br />&nbsp;&nbsp;&nbsp; for(i=1;i&lt;=N;i++)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; used[i]=0;<br />&nbsp;&nbsp;&nbsp; for(i=1;i&lt;=N;i++){//定位<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; k = rand()%(N+1-i)+1;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(j=1;k!=0;j++){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(0==used[j])//只计算未抽出的牌<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; k--;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; poker[i]=j-1;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; used[j-1]=1;<br />&nbsp;&nbsp;&nbsp; }<br />}<br />int main(){<br />&nbsp;&nbsp;&nbsp; int i;<br />&nbsp;&nbsp;&nbsp; srand(time(NULL));<br />&nbsp;&nbsp;&nbsp; shuffle();<br />&nbsp;&nbsp;&nbsp; for(i=1;i&lt;=N;i++)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"%d \", poker[i]);<br />&nbsp;&nbsp;&nbsp; printf(\"\\n\");<br />}<br />修正后的方法二代码：<br />#include &lt;stdio.h&gt;<br />#include &lt;stdlib.h&gt;<br />#define swap(a,b) {int t=(a);(a)=(b);(b)=(t);}<br />#define N 54<br /><br />int poker[N+1];<br />void shuffle(){<br />&nbsp;&nbsp;&nbsp; int i,k;<br />&nbsp;&nbsp;&nbsp; for(i=1;i&lt;=N;i++)//初始化<br />\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; poker[i]=i;<br />&nbsp;&nbsp;&nbsp; for(i=1;i&lt;=N;i++){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; k = rand()%(N+1-i)+i; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; swap(poker[i],poker[k]);&nbsp;&nbsp; //交换<br />&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp; <br />}<br />int main(){<br />&nbsp;&nbsp;&nbsp; int i;<br />&nbsp;&nbsp;&nbsp; srand(time(NULL));<br />&nbsp;&nbsp;&nbsp; shuffle();<br />&nbsp;&nbsp;&nbsp; for(i=1;i&lt;=N;i++)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"%d \", poker[i]);<br />&nbsp;&nbsp;&nbsp; printf(\"\\n\");<br />}<br /><br />康托展开源代码(计算排列的编号)：洗牌算法要反过来，即根据排列的编号计算排列的内容。<br />#include &lt;stdio.h&gt;<br /><br />int fac[] = {1,1,2,6,24,120,720,5040,40320,362880};<br />unsigned long cantor(int s[], int n) {<br />&nbsp;&nbsp;&nbsp; int i,j,temp,num=0;<br />&nbsp;&nbsp;&nbsp; for(i=1;i&lt;n;i++){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; temp = 0;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(j=i+1;j&lt;=n;j++){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(s[j]&lt;s[i])<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; temp++;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; num+=fac[n-i]*temp;<br />&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; return num+1;<br />}<br /><br />int main(){<br />&nbsp;&nbsp;&nbsp; int x[]={0,1,2,3,5,4};<br />&nbsp;&nbsp;&nbsp; printf(\"%ld\\n\", cantor(x,5));<br />&nbsp;&nbsp;&nbsp; int y[]={0,5,4,3,2,1};<br />&nbsp;&nbsp;&nbsp; printf(\"%ld\\n\", cantor(y,5));<br />}',1,1,1,0,0,0,'54张有序的扑克牌，设计一种算法，实现洗牌操作：方法一：1。随机产生一个1-n的数x，做为第一张牌。2。随机产生一个1-(n-1)的数y，如果y&lt;x，则将y作为第二张牌，否则将y+1作为第二张牌。3。随机产生一个1-(n-i)的数z，取第z个没有被抽出来的作为第i张牌。(i=3,4,5...54)这种算法的复杂度为O(N^2)，因为计算每个随机数的牌号平均要执行(N/2)次比较。对应于现实中的扑克牌，这种算法等于每次从牌堆中随机抽一张，放到另一堆上，直到抽完为止，这里新的一堆就是洗完的牌序。方法二：1。随机产生一个1-n的数x，然后让第x张牌和第1张牌互相调换。2。随机产生一个1-n的数y，然后让第y张牌和第2张牌互相调换。3。随机产生一个1-n的数z，然后让第z张牌和第i张牌互相调换。(i=3,4,5...54)这种算法的复杂度为O(N)。方法三：因为一共有N!种洗牌结果，所以可以等概率地产生一个1-N!之间的随机数x，然后用康托展开的方法，根据x生成对应的排列，即为洗牌结果。关于康托展开，参考http://baike.baidu.com/view/437641.htm方法一的改进：方法一由于计算随机数对应的牌号平均要执行(N/2)次比较，所以复杂度为O(N^2)，N*N的第一个N是牌的数目，无法优化，但是第二个N是确定牌的序号，这个是否有办法优化呢？第一种优化就是每次抽出一张牌后，就将之后的所有牌向前移动一个单位，下次产生的随机数就是目标牌的下标。如第一次随机数为3，抽出3，扑克牌数组变为1 2 4 5 6 7...第二次抽出6，直接取数组的第六个元素即7，然后把7之后的牌前移一个单位。这样牌的定位复杂度就是1了，但是移动的复杂度仍为N/2，所以这种算法并没有起到效率优化的目标。第二种优化是为树状数组。用一个长为54的树状数组 used 存储 1-i 张牌中被投抽出来的牌的张数。这样 i-used[i] 就是牌号为 i 的扑克的序号。然后用2分查找的方式，就可以快速确定随机数对应的牌号，树状树的复杂度为logn，这样总的算法复杂度为O(NlogN)。但是考虑到一种54张牌，树状数组优化带来的性能提升很小，比起程序代码带来的可维护性损失，颇不值得。方法二的修正：方法二乍看好像不错，网上也有很多文章使用这种算法，但是其实这是一种错误的方法，因为方法二的所有可能性为N^N，而洗好的牌一种有N!种可能，又因为N^N % N! !=0，所以每种结果的概率是不相同的。那么如何修正这个问题呢？仿照第一种方法，第i次洗牌不是产生一个1-n的随机数，而是产生一个i-n的随机数，这样可能性结果的可能性就是N!了。就有可能概率相等了。证明在&lt;&lt;计算机程序设计艺术&gt;&gt;上。方法一代码：#include &lt;stdio.h&gt;#include &lt;stdlib.h&gt;#define N 54int poker[N+1];void shuffle(){&nbsp;&nbsp;&nbsp; int used[N+1];&nbsp;&nbsp;&nbsp; int i,j,k;&nbsp;&nbsp;&nbsp; for(i=1;i&lt;=N;i++)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; used[i]=0;&nbsp;&nbsp;&nbsp; for(i=1;i&lt;=N;i++){//定位&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; k = rand()%(N+1-i)+1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(j=1;k!=0;j++){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(0==used[j])//只计算未抽出的牌&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; k--;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; poker[i]=j-1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; used[j-1]=1;&nbsp;&nbsp;&nbsp; }}int main(){&nbsp;&nbsp;&nbsp; int i;&nbsp;&nbsp;&nbsp; srand(time(NULL));&nbsp;&nbsp;&nbsp; shuffle();&nbsp;&nbsp;&nbsp; for(i=1;i&lt;=N;i++)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"%d \", poker[i]);&nbsp;&nbsp;&nbsp; printf(\"\\n\");}修正后的方法二代码：#include &lt;stdio.h&gt;#include &lt;stdlib.h&gt;#define swap(a,b) {int t=(a);(a)=(b);(b)=(t);}#define N 54int poker[N+1];void shuffle(){&nbsp;&nbsp;&nbsp; int i,k;&nbsp;&nbsp;&nbsp; for(i=1;i&lt;=N;i++)//初始化\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; poker[i]=i;&nbsp;&nbsp;&nbsp; for(i=1;i&lt;=N;i++){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; k = rand()%(N+1-i)+i; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; swap(poker[i],poker[k]);&nbsp;&nbsp; //交换&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp; }int main(){&nbsp;&nbsp;&nbsp; int i;&nbsp;&nbsp;&nbsp; srand(time(NULL));&nbsp;&nbsp;&nbsp; shuffle();&nbsp;&nbsp;&nbsp; for(i=1;i&lt;=N;i++)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"%d \", poker[i]);&nbsp;&nbsp;&nbsp; printf(\"\\n\");}康托展开源代码(计算排列的编号)：洗牌算法要反过来，即根据排列的编号计算排列的内容。#include &lt;stdio.h&gt;int fac[] = {1,1,2,6,24,120,720,5040,40320,362880};unsigned long cantor(int s[], int n) {&nbsp;&nbsp;&nbsp; int i,j,temp,num=0;&nbsp;&nbsp;&nbsp; for(i=1;i&lt;n;i++){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; temp = 0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(j=i+1;j&lt;=n;j++){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(s[j]&lt;s[i])&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; temp++;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; num+=fac[n-i]*temp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return num+1;}int main(){&nbsp;&nbsp;&nbsp; int x[]={0,1,2,3,5,4};&nbsp;&nbsp;&nbsp; printf(\"%ld\\n\", cantor(x,5));&nbsp;&nbsp;&nbsp; int y[]={0,5,4,3,2,1};&nbsp;&nbsp;&nbsp; printf(\"%ld\\n\", cantor(y,5));}','',0,'other',0,'','zinnia/_entry_detail.html','entry_detail.html'),(129,'Linux进程：僵尸与孤儿','33731937201010111459588',2,'2010-11-01 11:33:05',NULL,'2010-11-01 11:33:05','2014-07-13 18:57:14','一个C++程序，以main函数的return 0退出。真正的运行机制是：<br />gcc 在链接的时候，会在程序的结尾加入sys_exit系统调用。<br />运行时，进程执行sys_exit，发送SIGCHLD信号给父进程，然后，进程进入僵尸状态，变成僵尸进程，等父进程收尸。<br />这样就出来两个问题：<br />1. 如果子进程在执行sys_exit时，父进程已经死亡怎么办？<br />2. 如果子进程退出时，父进程不收尸怎么办？<br />“子存父先死，孙在祖乃丧”，如果父进程在子进程调用sys_exit之前已经死亡，子进程在执行sys_exit时，发的SIGCHLD信号就无人接收了。谁给子进程收尸呢？<br /><b>托孤</b><br />答案是“托孤”，武侠小说的主角，往往在要死的时候，将主角托付给一个很牛的人，然后就去了。当一个进程的父进程在这个进程退出前退出的话，就会把这个进程的父进程修改为一个可以“托孤”的很牛的进程，在Linux下，这个进程是PID为1的init进程，每个进程在死亡前，都会把自己的子进程”托孤“给init进程。<br />在Linux0.11内核源代码的linux/kernel/exit.c的do_exit函数中，有这样一段”托孤“的代码：<br />108&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for (i=0 ; i&lt;NR_TASKS ; i++) <br />109&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (task[i] &amp;&amp; task[i]-&gt;father == current-&gt;pid) { <br />110&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; task[i]-&gt;father = 1; <br /><b><br />僵尸</b><br />进程在退出后就立刻变成了僵尸，然后等父进程收尸；<br />如果这时它的父进程已经死了，也就是说这个进程是个孤儿，也没问题，它的父进程临死前”托孤“的init进程，也就是它的养父，会帮它收尸。<br />如果这里它的父进程还没死，就要看这个父进程在做什么了？如果这个父进程对正在调用wait或waitpid操作，则表示这个父进程有准备棺材纸钱，这个进程就可以入土为安了。或者这个父进程设置了SIGCHLD信号处理函数，并在处理函数中执行了wait或waitpid操作，也没问题。但如果父进程没有忙得不可开交，没有任何收尸的想法，那这个进程就只好一直处于僵尸态，直到父进程什么时候想起来的时候进行wait或waitpid收尸，或者父进程死的时候收尸。<br />在linux0.11内核源代码linux/kernel/exit.c的do_exit函数中，有这样一段\"收尸\"的代码<br />111&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (task[i]-&gt;state == TASK_ZOMBIE) <br />112&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /* assumption task[1] is always init */<br />113&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (void) send_sig(SIGCHLD, task[1], 1); <br />但是，从子进程死亡，到父进程执行wai或死亡的这段时间内，子进程PID和 task_struct 就会一直被占用。<br /><br /><b>总结：</b><br />孤儿进程，就是子存父先死，进程退出时父进程已经退出，由养父收尸的进程。<br />僵尸进程，就是退出时父进程没有退出，但父进程也没有及时收尸的进程。会耗用一定时间的PID与task_struct资源。',1,1,1,0,0,0,'一个C++程序，以main函数的return 0退出。真正的运行机制是：gcc 在链接的时候，会在程序的结尾加入sys_exit系统调用。运行时，进程执行sys_exit，发送SIGCHLD信号给父进程，然后，进程进入僵尸状态，变成僵尸进程，等父进程收尸。这样就出来两个问题：1. 如果子进程在执行sys_exit时，父进程已经死亡怎么办？2. 如果子进程退出时，父进程不收尸怎么办？“子存父先死，孙在祖乃丧”，如果父进程在子进程调用sys_exit之前已经死亡，子进程在执行sys_exit时，发的SIGCHLD信号就无人接收了。谁给子进程收尸呢？托孤答案是“托孤”，武侠小说的主角，往往在要死的时候，将主角托付给一个很牛的人，然后就去了。当一个进程的父进程在这个进程退出前退出的话，就会把这个进程的父进程修改为一个可以“托孤”的很牛的进程，在Linux下，这个进程是PID为1的init进程，每个进程在死亡前，都会把自己的子进程”托孤“给init进程。在Linux0.11内核源代码的linux/kernel/exit.c的do_exit函数中，有这样一段”托孤“的代码：108&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for (i=0 ; i&lt;NR_TASKS ; i++) 109&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (task[i] &amp;&amp; task[i]-&gt;father == current-&gt;pid) { 110&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; task[i]-&gt;father = 1; 僵尸进程在退出后就立刻变成了僵尸，然后等父进程收尸；如果这时它的父进程已经死了，也就是说这个进程是个孤儿，也没问题，它的父进程临死前”托孤“的init进程，也就是它的养父，会帮它收尸。如果这里它的父进程还没死，就要看这个父进程在做什么了？如果这个父进程对正在调用wait或waitpid操作，则表示这个父进程有准备棺材纸钱，这个进程就可以入土为安了。或者这个父进程设置了SIGCHLD信号处理函数，并在处理函数中执行了wait或waitpid操作，也没问题。但如果父进程没有忙得不可开交，没有任何收尸的想法，那这个进程就只好一直处于僵尸态，直到父进程什么时候想起来的时候进行wait或waitpid收尸，或者父进程死的时候收尸。在linux0.11内核源代码linux/kernel/exit.c的do_exit函数中，有这样一段\"收尸\"的代码111&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (task[i]-&gt;state == TASK_ZOMBIE) 112&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /* assumption task[1] is always init */113&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (void) send_sig(SIGCHLD, task[1], 1); 但是，从子进程死亡，到父进程执行wai或死亡的这段时间内，子进程PID和 task_struct 就会一直被占用。总结：孤儿进程，就是子存父先死，进程退出时父进程已经退出，由养父收尸的进程。僵尸进程，就是退出时父进程没有退出，但父进程也没有及时收尸的进程。会耗用一定时间的PID与task_struct资源。','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(130,'Java6的动态语言API','33731937200833013550333',2,'2008-04-30 13:35:50',NULL,'2008-04-30 13:35:50','2014-07-13 18:57:14','/**<br />*Java6原生支持JavaScript，这个例子测试了Java调用脚本（使用脚本引擎和Java类）和脚本调用Java<br />*/<br /><br />import javax.script.*;<br /><br />public class HelloScriptWorld{<br />&nbsp;&nbsp; &nbsp;public static void main(String args[]) throws ScriptException, NoSuchMethodException {<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;ScriptEngineManager scriptEngineMgr = new ScriptEngineManager();<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;ScriptEngine jsEngine = scriptEngineMgr.getEngineByName(\"JavaScript\");<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if (jsEngine == null) {<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.err.println(\"No script engine found for JavaScript\");<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.exit(1);<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(\"Calling invokeHelloScript...\");<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;invokeHelloScript(jsEngine);<br /><br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(\"\\nCalling defineScriptFunction...\");<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;defineScriptFunction(jsEngine);<br /><br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(\"\\nCalling invokeScriptFunctionFromEngine...\");<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;invokeScriptFunctionFromEngine(jsEngine);<br /><br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(\"\\nCalling invokeScriptFunctionFromJava...\");<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;invokeScriptFunctionFromJava(jsEngine);<br /><br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(\"\\nCalling invokeJavaFromScriptFunction...\");<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;invokeJavaFromScriptFunction(jsEngine);<br />&nbsp;&nbsp; &nbsp;}<br />&nbsp;&nbsp; &nbsp;private static void invokeHelloScript(ScriptEngine jsEngine) throws ScriptException {<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;jsEngine.eval(\"println(\'Hello from JavaScript\')\");<br />&nbsp;&nbsp; &nbsp;}<br />&nbsp;&nbsp; &nbsp;private static void defineScriptFunction(ScriptEngine engine) throws ScriptException {<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;// Define a function in the script engine<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;engine.eval(<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\"function sayHello(name) {\" +<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\" println(\'Hello, \' + name);\" +<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\"}\"<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;);<br />&nbsp;&nbsp; &nbsp;}<br />&nbsp;&nbsp; &nbsp;private static void invokeScriptFunctionFromEngine(ScriptEngine engine)throws ScriptException{<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;engine.eval(\"sayHello(\'World!\')\");<br />&nbsp;&nbsp; &nbsp;}<br />&nbsp;&nbsp; &nbsp;private static void invokeScriptFunctionFromJava(ScriptEngine engine)throws ScriptException, NoSuchMethodException{<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Invocable invocableEngine = (Invocable) engine;<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;invocableEngine.invokeFunction(\"sayHello\", \"from Java\");<br />&nbsp;&nbsp; &nbsp;}<br />&nbsp;&nbsp; &nbsp;private static void invokeJavaFromScriptFunction(ScriptEngine engine)throws ScriptException&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;{<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;engine.put(\"helloScriptWorld\", new HelloScriptWorld());<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;engine.eval(<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\"println(\'Invoking getHelloReply method from JavaScript...\');\" +<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\"var msg = helloScriptWorld.getHelloReply(\'JavaScript\');\" +<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\"println(\'Java returned: \' + msg)\"<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;);<br />&nbsp;&nbsp; &nbsp;}<br />&nbsp;&nbsp; &nbsp;/** Method invoked from the above script to return a string. */<br />&nbsp;&nbsp; &nbsp;public String getHelloReply(String name) {<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return \"Java method getHelloReply says, \'Hello, \" + name + \"\'\";<br />&nbsp;&nbsp; &nbsp;}<br />}',1,1,1,0,0,0,'/***Java6原生支持JavaScript，这个例子测试了Java调用脚本（使用脚本引擎和Java类）和脚本调用Java*/import javax.script.*;public class HelloScriptWorld{&nbsp;&nbsp; &nbsp;public static void main(String args[]) throws ScriptException, NoSuchMethodException {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;ScriptEngineManager scriptEngineMgr = new ScriptEngineManager();&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;ScriptEngine jsEngine = scriptEngineMgr.getEngineByName(\"JavaScript\");&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if (jsEngine == null) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.err.println(\"No script engine found for JavaScript\");&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.exit(1);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(\"Calling invokeHelloScript...\");&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;invokeHelloScript(jsEngine);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(\"\\nCalling defineScriptFunction...\");&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;defineScriptFunction(jsEngine);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(\"\\nCalling invokeScriptFunctionFromEngine...\");&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;invokeScriptFunctionFromEngine(jsEngine);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(\"\\nCalling invokeScriptFunctionFromJava...\");&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;invokeScriptFunctionFromJava(jsEngine);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(\"\\nCalling invokeJavaFromScriptFunction...\");&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;invokeJavaFromScriptFunction(jsEngine);&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;private static void invokeHelloScript(ScriptEngine jsEngine) throws ScriptException {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;jsEngine.eval(\"println(\'Hello from JavaScript\')\");&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;private static void defineScriptFunction(ScriptEngine engine) throws ScriptException {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;// Define a function in the script engine&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;engine.eval(&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\"function sayHello(name) {\" +&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\" println(\'Hello, \' + name);\" +&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\"}\"&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;);&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;private static void invokeScriptFunctionFromEngine(ScriptEngine engine)throws ScriptException{&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;engine.eval(\"sayHello(\'World!\')\");&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;private static void invokeScriptFunctionFromJava(ScriptEngine engine)throws ScriptException, NoSuchMethodException{&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Invocable invocableEngine = (Invocable) engine;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;invocableEngine.invokeFunction(\"sayHello\", \"from Java\");&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;private static void invokeJavaFromScriptFunction(ScriptEngine engine)throws ScriptException&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;{&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;engine.put(\"helloScriptWorld\", new HelloScriptWorld());&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;engine.eval(&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\"println(\'Invoking getHelloReply method from JavaScript...\');\" +&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\"var msg = helloScriptWorld.getHelloReply(\'JavaScript\');\" +&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\"println(\'Java returned: \' + msg)\"&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;);&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;/** Method invoked from the above script to return a string. */&nbsp;&nbsp; &nbsp;public String getHelloReply(String name) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return \"Java method getHelloReply says, \'Hello, \" + name + \"\'\";&nbsp;&nbsp; &nbsp;}}','',0,'j2se',0,'','zinnia/_entry_detail.html','entry_detail.html'),(131,'Ubuntu下架设squid代理服务器','337319372008931104357863',2,'2008-10-31 22:43:57',NULL,'2008-10-31 22:43:57','2014-07-13 18:57:14','安装：anapt-get install squidan<br />配置：vi /etc/squid/squid.conf<br />acl lan 192.168.1.0/24<br />acl wan 202.114.15.10<br />host_access lan allow<br />host_access wan allow<br />用户认证配置<br />auth_param basic program /usr/lib/squid/ncsa_auth /etc/squid/passwd<br />acl normal proxy_auth REQUIRED<br /><font><font face=\"Verdana\">http_access allow normal<br /><br /></font></font><font><font face=\"Verdana\">管理代理帐户htpasswd -c /var/squid/etc/password guest</font></font><br />',1,1,1,0,0,0,'安装：anapt-get install squidan配置：vi /etc/squid/squid.confacl lan 192.168.1.0/24acl wan 202.114.15.10host_access lan allowhost_access wan allow用户认证配置auth_param basic program /usr/lib/squid/ncsa_auth /etc/squid/passwdacl normal proxy_auth REQUIREDhttp_access allow normal管理代理帐户htpasswd -c /var/squid/etc/password guest','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(132,'从ubuntu灾难想备份','3373193720099194294669',2,'2009-10-19 16:29:46',NULL,'2009-10-19 16:29:46','2014-07-13 18:57:14','Ubuntu系统升级，从8.04升到8.10，很平静，从8.10升到9.10失败<br />然后从8.10升到9.04，遇到一些问题，python2.6和python2.6-minimal互相依赖，对apt-get使用force选项仍然失败，请出google，最后发现是python-chert包的问题，从官方下载一个，安装后继续，最后发现新的内核不能用<br />用旧内核从9.04升到9.10，新内核仍然不能用，而且mysql-server坏掉了，bugzilla3坏掉了，打印机坏掉了，脑壳坏掉了，崩溃ing<br /><br />于是，决定重装回8.04的系统，目前为止，尚不知Ubuntu降级的方法，只好重装，考虑到重要数据主要在/var/和/home两个区，很傻很天真的试图不备份，直接升级，于是，大四毕业时重装系统导致整个硬盘60G内容丢失的悲剧重演了。<br /><br />安装的过程一切都很顺利，一直到分区结束后，弹出一个对话框，大意是：“你选择了保留/var分区的数据，但是如果该分区上有/boot /root之类文件夹，可能会被删除”<br />立刻纠结了，是继续呢，还是继续呢，迷迷糊糊中就按下了Enter键，然后就看着格盘的进度条狂闪，很心虚的祈祷佛祖保佑。<br /><br />安装成功后，打开/var一看，svn和www目录完全消失，系统好了，数据没了，人崩溃了。<br /><br />开始疯狂恢复系统<br />svn bugzilla samba printer mysql bbpress dhcpd<br />看看都要备份些什么东西吧<br />samba的配制文件/etc/samba/smb.conf备份了<br />mysql数据/var/lib/mysql完全丢失<br />svn数据/var/svn完全丢失<br />bugzilla因为mysql的数据丢失，一切丢失<br />bbpress因为apache的/var/www丢失，完全丢失<br />打印机恢复的忒简单 sudo apt-get install foo2zjs hpijs; sudo hp-setup <br /><br />吃一堑，长一智<br />必须备份的数据<br />/etc<br />/home<br />/var(主要是lib/mysql和svn)<br /><br />',1,1,1,0,0,0,'Ubuntu系统升级，从8.04升到8.10，很平静，从8.10升到9.10失败然后从8.10升到9.04，遇到一些问题，python2.6和python2.6-minimal互相依赖，对apt-get使用force选项仍然失败，请出google，最后发现是python-chert包的问题，从官方下载一个，安装后继续，最后发现新的内核不能用用旧内核从9.04升到9.10，新内核仍然不能用，而且mysql-server坏掉了，bugzilla3坏掉了，打印机坏掉了，脑壳坏掉了，崩溃ing于是，决定重装回8.04的系统，目前为止，尚不知Ubuntu降级的方法，只好重装，考虑到重要数据主要在/var/和/home两个区，很傻很天真的试图不备份，直接升级，于是，大四毕业时重装系统导致整个硬盘60G内容丢失的悲剧重演了。安装的过程一切都很顺利，一直到分区结束后，弹出一个对话框，大意是：“你选择了保留/var分区的数据，但是如果该分区上有/boot /root之类文件夹，可能会被删除”立刻纠结了，是继续呢，还是继续呢，迷迷糊糊中就按下了Enter键，然后就看着格盘的进度条狂闪，很心虚的祈祷佛祖保佑。安装成功后，打开/var一看，svn和www目录完全消失，系统好了，数据没了，人崩溃了。开始疯狂恢复系统svn bugzilla samba printer mysql bbpress dhcpd看看都要备份些什么东西吧samba的配制文件/etc/samba/smb.conf备份了mysql数据/var/lib/mysql完全丢失svn数据/var/svn完全丢失bugzilla因为mysql的数据丢失，一切丢失bbpress因为apache的/var/www丢失，完全丢失打印机恢复的忒简单 sudo apt-get install foo2zjs hpijs; sudo hp-setup 吃一堑，长一智必须备份的数据/etc/home/var(主要是lib/mysql和svn)','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(133,'用python生成二维数组 计算九宫','337319372009111372417212',2,'2009-12-13 19:24:17',NULL,'2009-12-13 19:24:17','2014-07-13 18:57:14','<br />用python生成一个m*n的二维数组，并全部初始化为-1：<br />[[-1 for i in range(n)] for i in range(m)]<br /><br />计算九宫格 x宫格<br />#jq.py<br />page=\'\'\'<br />&lt;form method=\'get\' action=\'/jg.py/\'&gt;<br />&lt;input teye=text name=\'n\' value=%s&gt;<br />&lt;input type=submit value=\'Calculate\'&gt;<br />&lt;/form&gt;<br />&lt;hr&gt;<br />\'\'\'<br />def index(req,n=\'3\'):<br />&nbsp;&nbsp;&nbsp; req.content_type=\"text/html;charset=utf8\"<br />&nbsp;&nbsp;&nbsp; if not n.isdigit():<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(page % \'\')<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(\"&lt;font color=\'red\'&gt;please input a number&lt;/font&gt;\")<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return<br />&nbsp;&nbsp;&nbsp; elif int(n)%2==0:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(page % \'\')<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(\"&lt;font color=\'red\'&gt;please specify a odd number&lt;/font&gt;\")<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return<br />&nbsp;&nbsp;&nbsp; elif int(n)&gt;50:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(page % \'\')<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(\"&lt;font color=\'red\'&gt;Please specify a number less then 50&lt;/font&gt;\")<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return<br />&nbsp;&nbsp;&nbsp; else:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(page % n)<br />&nbsp;&nbsp;&nbsp; n=int(n)<br />&nbsp;&nbsp;&nbsp; raw=[[-1 for i in range(n*n)] for i in range(n*n)]<br />&nbsp;&nbsp;&nbsp; i,j,v=n-1,n/2,1<br />&nbsp;&nbsp;&nbsp; for x in range(n*n):<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; raw[i][j]=v<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; v+=1<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; nexti,nextj=(i+1)%n,(j+1)%n<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(raw[nexti][nextj]!=-1):<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; i=(i+n-1)%n<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; i,j=nexti,nextj<br />&nbsp;&nbsp;&nbsp; req.write(\"&lt;table border=1&gt;\")<br />&nbsp;&nbsp;&nbsp; for i in range(n):<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(\"&lt;tr&gt;\")<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for j in range(n):<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(\"&lt;td&gt;%d&lt;/td&gt;\" % raw[i][j])<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(\"&lt;/tr&gt;\")<br />&nbsp;&nbsp;&nbsp; req.write(\"&lt;/table&gt;\")<br /><br />',1,1,1,0,0,0,'用python生成一个m*n的二维数组，并全部初始化为-1：[[-1 for i in range(n)] for i in range(m)]计算九宫格 x宫格#jq.pypage=\'\'\'&lt;form method=\'get\' action=\'/jg.py/\'&gt;&lt;input teye=text name=\'n\' value=%s&gt;&lt;input type=submit value=\'Calculate\'&gt;&lt;/form&gt;&lt;hr&gt;\'\'\'def index(req,n=\'3\'):&nbsp;&nbsp;&nbsp; req.content_type=\"text/html;charset=utf8\"&nbsp;&nbsp;&nbsp; if not n.isdigit():&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(page % \'\')&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(\"&lt;font color=\'red\'&gt;please input a number&lt;/font&gt;\")&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return&nbsp;&nbsp;&nbsp; elif int(n)%2==0:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(page % \'\')&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(\"&lt;font color=\'red\'&gt;please specify a odd number&lt;/font&gt;\")&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return&nbsp;&nbsp;&nbsp; elif int(n)&gt;50:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(page % \'\')&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(\"&lt;font color=\'red\'&gt;Please specify a number less then 50&lt;/font&gt;\")&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return&nbsp;&nbsp;&nbsp; else:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(page % n)&nbsp;&nbsp;&nbsp; n=int(n)&nbsp;&nbsp;&nbsp; raw=[[-1 for i in range(n*n)] for i in range(n*n)]&nbsp;&nbsp;&nbsp; i,j,v=n-1,n/2,1&nbsp;&nbsp;&nbsp; for x in range(n*n):&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; raw[i][j]=v&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; v+=1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; nexti,nextj=(i+1)%n,(j+1)%n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(raw[nexti][nextj]!=-1):&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; i=(i+n-1)%n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; i,j=nexti,nextj&nbsp;&nbsp;&nbsp; req.write(\"&lt;table border=1&gt;\")&nbsp;&nbsp;&nbsp; for i in range(n):&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(\"&lt;tr&gt;\")&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for j in range(n):&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(\"&lt;td&gt;%d&lt;/td&gt;\" % raw[i][j])&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(\"&lt;/tr&gt;\")&nbsp;&nbsp;&nbsp; req.write(\"&lt;/table&gt;\")','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(134,'红蓝3D的Teddy熊','3373193720106192339325',2,'2010-07-01 21:23:39',NULL,'2010-07-01 21:23:39','2014-07-13 18:57:14','<div>OpenGL 的坐标变换顺序很重要，前几天的红蓝效果虽然有，但总觉得有些不对劲，今天调了一下OpenGL 的坐标变换顺序，瞬间突破了。<br />这个Teddy熊效果自我感觉颇不错：<br /><img alt=\"红蓝3D的Teddy熊 - hankjin - 云水居\" src=\"/static/163/3827778208288223698.png\" style=\"margin: 0pt 10px 0pt 0pt;\" title=\"红蓝3D的Teddy熊 - hankjin - 云水居\" /></div>',1,1,1,0,0,0,'OpenGL 的坐标变换顺序很重要，前几天的红蓝效果虽然有，但总觉得有些不对劲，今天调了一下OpenGL 的坐标变换顺序，瞬间突破了。这个Teddy熊效果自我感觉颇不错：','',0,'graphics',0,'','zinnia/_entry_detail.html','entry_detail.html'),(135,'恐惧源于一知半解','3373193720095623843158',2,'2009-06-06 14:38:43',NULL,'2009-06-06 14:38:43','2014-07-13 18:57:14','无知者无畏。因为无知，所以不知道什么是恐怖，什么是敬畏。所谓初生牛犊不怕虎，不是不怕，是因为不知道虎可怕。<br /><br />全知者不惧。因为全知，所以看开了，伸首一刀，缩头还是一刀，何必作无谓烦恼；因为全知，所以知道如何应付，犹如闲庭信步，游刃而有余。<br /><br />恐惧源于一知半解。明知道灾难会发生，却对解决办法不甚了了，在无奈中等待灾难发生的过程才是恐惧。恐惧的不是灾难本身，而是等待灾难发生的过程。<br /><br />系统设计师要有庞大的知识库做背景。在某些情境下，能想到两种解决方案，第一种方案信手拈来，可是too old，没有新意，第二种方案完美但复杂，而且没有掌握。何解？上次很锉的选择了第二种方案，于是就有了这篇文章。<br />',1,1,1,0,0,0,'无知者无畏。因为无知，所以不知道什么是恐怖，什么是敬畏。所谓初生牛犊不怕虎，不是不怕，是因为不知道虎可怕。全知者不惧。因为全知，所以看开了，伸首一刀，缩头还是一刀，何必作无谓烦恼；因为全知，所以知道如何应付，犹如闲庭信步，游刃而有余。恐惧源于一知半解。明知道灾难会发生，却对解决办法不甚了了，在无奈中等待灾难发生的过程才是恐惧。恐惧的不是灾难本身，而是等待灾难发生的过程。系统设计师要有庞大的知识库做背景。在某些情境下，能想到两种解决方案，第一种方案信手拈来，可是too old，没有新意，第二种方案完美但复杂，而且没有掌握。何解？上次很锉的选择了第二种方案，于是就有了这篇文章。','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(136,'代码之美-漂亮代码7原则','33731937200910912913731',2,'2009-11-09 13:29:13',NULL,'2009-11-09 13:29:13','2014-07-13 18:57:14','代码之美中，谈DiffManager的一章提到了漂亮代码的7个原则，自己理解如下：<br />1. 像书本一样。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 代码应该像文章一样，整齐漂亮<br />2. 功能一致者结构一致。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 功能相似的代码，结构也一致<br />3. 缩进&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 缩进层数不要太多，否则很难理解层次结构<br />4. 打断大块&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 将大块的代码分解成小块，人的注意力集中时间有限，大块的代码较难理解<br />5. 注释&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 在必要的地方注释，更多的是代码自注释<br />6. 整理&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 好比审阅文章，调整函数的顺序等，在C#中使用#region<br />7. 一致&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 团队开发，最好能统一个人的风格<br /><br />总结为3+1<br />外观12<br />缩进34<br />注释56<br />+<br />团队7<br />',1,1,1,0,0,0,'代码之美中，谈DiffManager的一章提到了漂亮代码的7个原则，自己理解如下：1. 像书本一样。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 代码应该像文章一样，整齐漂亮2. 功能一致者结构一致。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 功能相似的代码，结构也一致3. 缩进&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 缩进层数不要太多，否则很难理解层次结构4. 打断大块&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 将大块的代码分解成小块，人的注意力集中时间有限，大块的代码较难理解5. 注释&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 在必要的地方注释，更多的是代码自注释6. 整理&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 好比审阅文章，调整函数的顺序等，在C#中使用#region7. 一致&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 团队开发，最好能统一个人的风格总结为3+1外观12缩进34注释56+团队7','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(137,'用Qt画个QQ抽屉','3373193720091016111243894',2,'2009-11-16 11:12:43',NULL,'2009-11-16 11:12:43','2014-07-13 18:57:14','来自《Linux窗口程序设计》的实例6：<br />1. 继承QToolBox<br />2. 向QToolBox添加QGroupBox，每个QGroupBox即是一个分组<br />3. 向QGroupBox添加QToolButton，每个QToolButton即是一个头像<br />注意要有几个图片<br /><br />from PyQt4.QtGui import *<br />from PyQt4.QtCore import *<br /><br />class QQ(QToolBox):<br />&nbsp;&nbsp; &nbsp;def __init__(self):<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;QToolBox.__init__(self)<br /><br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;gp1=QGroupBox()<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btn1_1=QToolButton()<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btn1_1.setText(\'beibei\')<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btn1_1.setIcon(QIcon(\'bb.jpg\'))#图标<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btn1_1.setIconSize(QSize(80,80))<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btn1_1.setAutoRaise(True)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btn1_1.setToolButtonStyle(Qt.ToolButtonTextUnderIcon)<br /><br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btn1_2=QToolButton()<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btn1_2.setText(\'jingjing\')<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;layout=QVBoxLayout(gp1)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;layout.addWidget(btn1_1)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;layout.addWidget(btn1_2)<br /><br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;gp2=QGroupBox()<br /><br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.addItem(gp1,\'friends\')<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.addItem(gp2,\'news\')<br /><br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.show()<br />&nbsp;&nbsp; &nbsp;def slogQQ(self):<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;pass<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br /><br />if __name__==\'__main__\':<br />&nbsp;&nbsp; &nbsp;app=QApplication([])<br />&nbsp;&nbsp; &nbsp;dlg=QQ()<br />&nbsp;&nbsp; &nbsp;app.setActiveWindow(dlg)<br />&nbsp;&nbsp; &nbsp;app.exec_()<br /><br />',1,1,1,0,0,0,'来自《Linux窗口程序设计》的实例6：1. 继承QToolBox2. 向QToolBox添加QGroupBox，每个QGroupBox即是一个分组3. 向QGroupBox添加QToolButton，每个QToolButton即是一个头像注意要有几个图片from PyQt4.QtGui import *from PyQt4.QtCore import *class QQ(QToolBox):&nbsp;&nbsp; &nbsp;def __init__(self):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;QToolBox.__init__(self)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;gp1=QGroupBox()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btn1_1=QToolButton()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btn1_1.setText(\'beibei\')&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btn1_1.setIcon(QIcon(\'bb.jpg\'))#图标&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btn1_1.setIconSize(QSize(80,80))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btn1_1.setAutoRaise(True)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btn1_1.setToolButtonStyle(Qt.ToolButtonTextUnderIcon)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btn1_2=QToolButton()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btn1_2.setText(\'jingjing\')&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;layout=QVBoxLayout(gp1)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;layout.addWidget(btn1_1)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;layout.addWidget(btn1_2)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;gp2=QGroupBox()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.addItem(gp1,\'friends\')&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.addItem(gp2,\'news\')&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.show()&nbsp;&nbsp; &nbsp;def slogQQ(self):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;pass&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if __name__==\'__main__\':&nbsp;&nbsp; &nbsp;app=QApplication([])&nbsp;&nbsp; &nbsp;dlg=QQ()&nbsp;&nbsp; &nbsp;app.setActiveWindow(dlg)&nbsp;&nbsp; &nbsp;app.exec_()','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(138,'西藏行，写在前面','3373193720123281233309',2,'2012-04-28 01:31:03',NULL,'2012-04-28 01:31:03','2014-07-13 18:57:14','2011年，安排五一计划的时候，想到了西藏，面对4月正式入职的状况，顺理成章地把计划延迟，定为2012年的五一，并开始了不经意的准备。<div><br /></div><div>第一个准备的是人，阿木是第一个入伙的，大雄是第二个入伙并退伙的，der是一直吵着要加班最后第二个加入的，羽是一个电话爽快定下来的，最终成行舍友四人。I have a dream, 不只我们四个，可是由于种种原因，最终是4个，有一点小遗憾，也许四人组是最好的。</div><div><br /></div><div>第二个准备的是行程，一直委托给der去安排，可惜der就职于某知名加班公司，五一前1个月，查了五一的放假安排，定了时间8天；搜了两篇游记，定了一个Draft行程。</div><div>&nbsp; &nbsp; 然后开始订票，因为听说飞机去容易高反，所以提前10天订火车票，然后发现我不知道其它三个人的身份证号，并且提前10天8:00网上订票只有座票，后来才发现7:00开始电话订票；幸运的是大家都有一点2，所以决定买座票，<span style=\"line-height: 22px;\">上车后再</span>补卧铺。der和羽无私从武汉、杭州赶到北京，一同出发，致敬。</div><div>&nbsp; &nbsp; 然后开始订返程，原计划火车，后来改了飞机。拉萨飞杭州，在杭州玩儿一天，再各飞北京、武汉、杭州。网上查的拉萨飞杭州900，犹豫了一天就变1100了，又等了一晚，果断以1100下手了，现在960。</div><div>&nbsp; &nbsp; 最终参考了一个论坛的5天行程，裁剪成3天的，然后决定用5天走这3天的行程。不催则不悲，不悲催。</div><div><font color=\"#000000\">拉萨，纳木措，羊八井，大竹卡，日喀则、江孜、羊卓雍措、拉萨</font></div><div><br /></div><div>第三个准备的是装备，一个帽子，一个眼镜，最后一周网购之；围巾？进藏后如果有需要在当地买。</div><div>&nbsp; &nbsp; 相机，手机，充电器，剃须刀，防晒霜；保暖内衣防万一，外套和短袖，简装上阵。</div><div>&nbsp; &nbsp; 由衷感谢为此赞助意见的人。</div><div><br /></div><div>第四个准备的是状态，身体很重要，怕高反，不是怕因为高反玩不了，而是丢不起这个人，先跑了两周步，最后一周停止，因为专家说xxx。</div><div>&nbsp; &nbsp; 要玩好，先工作好，工作很重要，要把玩儿的一周的工作提前一周基本消灭掉。</div><div>&nbsp; &nbsp; 这个时间，突然想写点什么，不只是为了向以后想去的人分享点什么，也是为了一个愿望的实现而感到欣慰，虽然没什么难度，但是看到一个愿望成为现实，成就感依然令人振奋。</div><div>&nbsp; &nbsp; 我思故我在，我行故我乐。</div>',1,1,1,0,0,0,'2011年，安排五一计划的时候，想到了西藏，面对4月正式入职的状况，顺理成章地把计划延迟，定为2012年的五一，并开始了不经意的准备。第一个准备的是人，阿木是第一个入伙的，大雄是第二个入伙并退伙的，der是一直吵着要加班最后第二个加入的，羽是一个电话爽快定下来的，最终成行舍友四人。I have a dream, 不只我们四个，可是由于种种原因，最终是4个，有一点小遗憾，也许四人组是最好的。第二个准备的是行程，一直委托给der去安排，可惜der就职于某知名加班公司，五一前1个月，查了五一的放假安排，定了时间8天；搜了两篇游记，定了一个Draft行程。&nbsp; &nbsp; 然后开始订票，因为听说飞机去容易高反，所以提前10天订火车票，然后发现我不知道其它三个人的身份证号，并且提前10天8:00网上订票只有座票，后来才发现7:00开始电话订票；幸运的是大家都有一点2，所以决定买座票，上车后再补卧铺。der和羽无私从武汉、杭州赶到北京，一同出发，致敬。&nbsp; &nbsp; 然后开始订返程，原计划火车，后来改了飞机。拉萨飞杭州，在杭州玩儿一天，再各飞北京、武汉、杭州。网上查的拉萨飞杭州900，犹豫了一天就变1100了，又等了一晚，果断以1100下手了，现在960。&nbsp; &nbsp; 最终参考了一个论坛的5天行程，裁剪成3天的，然后决定用5天走这3天的行程。不催则不悲，不悲催。拉萨，纳木措，羊八井，大竹卡，日喀则、江孜、羊卓雍措、拉萨第三个准备的是装备，一个帽子，一个眼镜，最后一周网购之；围巾？进藏后如果有需要在当地买。&nbsp; &nbsp; 相机，手机，充电器，剃须刀，防晒霜；保暖内衣防万一，外套和短袖，简装上阵。&nbsp; &nbsp; 由衷感谢为此赞助意见的人。第四个准备的是状态，身体很重要，怕高反，不是怕因为高反玩不了，而是丢不起这个人，先跑了两周步，最后一周停止，因为专家说xxx。&nbsp; &nbsp; 要玩好，先工作好，工作很重要，要把玩儿的一周的工作提前一周基本消灭掉。&nbsp; &nbsp; 这个时间，突然想写点什么，不只是为了向以后想去的人分享点什么，也是为了一个愿望的实现而感到欣慰，虽然没什么难度，但是看到一个愿望成为现实，成就感依然令人振奋。&nbsp; &nbsp; 我思故我在，我行故我乐。','',0,'mood',0,'','zinnia/_entry_detail.html','entry_detail.html'),(139,'欧拉回路POJ2337','337319372010111485227807',2,'2010-12-14 20:52:27',NULL,'2010-12-14 20:52:27','2014-07-13 18:57:15','#include&lt;iostream&gt;<br />#include&lt;cstdio&gt;<br />#include&lt;cstring&gt;<br />#include&lt;algorithm&gt;<br />using namespace std;<br />struct edge{int to, next,/*next e of the same v*/ id; bool visited;} e[1024];<br />int v[26], eNum, cd[26], rd[26], visited[26], path[26][26], ans[1024], ansI;<br />void insert(int from, int to, int id) {<br />&nbsp;&nbsp;&nbsp; e[eNum].to = to, e[eNum].next = v[from];<br />&nbsp;&nbsp;&nbsp; e[eNum].visited = 0;e[eNum].id = id;<br />&nbsp;&nbsp; &nbsp;v[from] = eNum++;<br />}<br />char dict[1024][26];<br />int cmp(const void *pa, const void *pb){<br />&nbsp;&nbsp;&nbsp; const char* a = (const char*)pa;<br />&nbsp;&nbsp;&nbsp; const char* b = (const char*)pb;<br />&nbsp;&nbsp;&nbsp; return -strcmp(a,b);<br />}<br />void dfs(int id) {<br />&nbsp;&nbsp;&nbsp; visited[id] = true;<br />&nbsp;&nbsp;&nbsp; for(int i = 0; i &lt; 26; i++)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (!visited[i] &amp;&amp; path[id][i])<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; dfs(i);<br />}<br />int judge() {<br />&nbsp;&nbsp;&nbsp; int num = 0, id = -1;<br />&nbsp;&nbsp; &nbsp;//is there illegal vertex<br />&nbsp;&nbsp;&nbsp; for(int i = 0; i &lt; 26; i++)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (cd[i] - rd[i] == 1) {id = i; num++;}<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else if (rd[i] - cd[i] == 1) num++;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else if (rd[i] - cd[i] &gt; 1 || rd[i] - cd[i] &lt; -1) return -1;<br />&nbsp;&nbsp;&nbsp; if (num &gt; 2) return -1;<br />&nbsp;&nbsp; &nbsp;//if all even<br />&nbsp;&nbsp;&nbsp; if (id == -1)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(int i = 0; i &lt; 26; i++)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (cd[i] &gt; 0){id = i; break;}<br />&nbsp;&nbsp;&nbsp; memset(visited, 0, sizeof(visited));<br />&nbsp;&nbsp;&nbsp; dfs(id);<br />&nbsp;&nbsp; &nbsp;//*is there seperate part?<br />&nbsp;&nbsp;&nbsp; for(int i = 0; i &lt; 26; i++)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ((cd[i] != 0 || rd[i] != 0) &amp;&amp; !visited[i]) return -1;<br />&nbsp;&nbsp;&nbsp; return id;<br />}<br />void euer(int vid, int eid) {<br />&nbsp;&nbsp;&nbsp; for(int i = v[vid]; i != -1; i = e[i].next)//foreach cd of vid.<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (!e[i].visited) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; e[i].visited = 1;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; euer(e[i].to, i);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; if(eid &gt;= 0)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ans[ansI++] = e[eid].id;<br />}<br />int main() {<br />//&nbsp;&nbsp;&nbsp; freopen(\"euer.in\",\"r\",stdin);<br />&nbsp;&nbsp; &nbsp;int t,n;<br />&nbsp;&nbsp;&nbsp; cin&gt;&gt;t;<br />&nbsp;&nbsp;&nbsp; while(t--) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; cin&gt;&gt;n;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(int i = 0; i &lt; n; i++)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; cin&gt;&gt;dict[i];<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; qsort(&amp;dict[0][0], n, 26*sizeof(char), cmp);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memset(visited, 0, sizeof(visited));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memset(cd, 0, sizeof(cd));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memset(rd, 0, sizeof(rd));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memset(path, 0, sizeof(path));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; eNum = 0;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memset(v, -1, sizeof(v));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(int i = 0; i &lt; n; i++) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int from = dict[i][0] - \'a\';<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int to = dict[i][strlen(dict[i]) - 1] - \'a\';<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; cd[from]++; rd[to]++;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; path[from][to] = 1;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; insert(from, to, i);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int id = judge();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (id == -1) cout&lt;&lt;\"***\\n\";<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else {<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;ansI = 0;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; euer(id, -1);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(int i = n-1; i &gt;= 0; i--) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (i!=n-1) cout&lt;&lt;\".\";<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; cout&lt;&lt;dict[ans[i]];<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; cout&lt;&lt;endl;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }<br />}',1,1,1,0,0,0,'#include&lt;iostream&gt;#include&lt;cstdio&gt;#include&lt;cstring&gt;#include&lt;algorithm&gt;using namespace std;struct edge{int to, next,/*next e of the same v*/ id; bool visited;} e[1024];int v[26], eNum, cd[26], rd[26], visited[26], path[26][26], ans[1024], ansI;void insert(int from, int to, int id) {&nbsp;&nbsp;&nbsp; e[eNum].to = to, e[eNum].next = v[from];&nbsp;&nbsp;&nbsp; e[eNum].visited = 0;e[eNum].id = id;&nbsp;&nbsp; &nbsp;v[from] = eNum++;}char dict[1024][26];int cmp(const void *pa, const void *pb){&nbsp;&nbsp;&nbsp; const char* a = (const char*)pa;&nbsp;&nbsp;&nbsp; const char* b = (const char*)pb;&nbsp;&nbsp;&nbsp; return -strcmp(a,b);}void dfs(int id) {&nbsp;&nbsp;&nbsp; visited[id] = true;&nbsp;&nbsp;&nbsp; for(int i = 0; i &lt; 26; i++)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (!visited[i] &amp;&amp; path[id][i])&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; dfs(i);}int judge() {&nbsp;&nbsp;&nbsp; int num = 0, id = -1;&nbsp;&nbsp; &nbsp;//is there illegal vertex&nbsp;&nbsp;&nbsp; for(int i = 0; i &lt; 26; i++)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (cd[i] - rd[i] == 1) {id = i; num++;}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else if (rd[i] - cd[i] == 1) num++;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else if (rd[i] - cd[i] &gt; 1 || rd[i] - cd[i] &lt; -1) return -1;&nbsp;&nbsp;&nbsp; if (num &gt; 2) return -1;&nbsp;&nbsp; &nbsp;//if all even&nbsp;&nbsp;&nbsp; if (id == -1)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(int i = 0; i &lt; 26; i++)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (cd[i] &gt; 0){id = i; break;}&nbsp;&nbsp;&nbsp; memset(visited, 0, sizeof(visited));&nbsp;&nbsp;&nbsp; dfs(id);&nbsp;&nbsp; &nbsp;//*is there seperate part?&nbsp;&nbsp;&nbsp; for(int i = 0; i &lt; 26; i++)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ((cd[i] != 0 || rd[i] != 0) &amp;&amp; !visited[i]) return -1;&nbsp;&nbsp;&nbsp; return id;}void euer(int vid, int eid) {&nbsp;&nbsp;&nbsp; for(int i = v[vid]; i != -1; i = e[i].next)//foreach cd of vid.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (!e[i].visited) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; e[i].visited = 1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; euer(e[i].to, i);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; if(eid &gt;= 0)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ans[ansI++] = e[eid].id;}int main() {//&nbsp;&nbsp;&nbsp; freopen(\"euer.in\",\"r\",stdin);&nbsp;&nbsp; &nbsp;int t,n;&nbsp;&nbsp;&nbsp; cin&gt;&gt;t;&nbsp;&nbsp;&nbsp; while(t--) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; cin&gt;&gt;n;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(int i = 0; i &lt; n; i++)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; cin&gt;&gt;dict[i];&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; qsort(&amp;dict[0][0], n, 26*sizeof(char), cmp);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memset(visited, 0, sizeof(visited));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memset(cd, 0, sizeof(cd));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memset(rd, 0, sizeof(rd));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memset(path, 0, sizeof(path));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; eNum = 0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memset(v, -1, sizeof(v));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(int i = 0; i &lt; n; i++) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int from = dict[i][0] - \'a\';&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int to = dict[i][strlen(dict[i]) - 1] - \'a\';&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; cd[from]++; rd[to]++;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; path[from][to] = 1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; insert(from, to, i);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int id = judge();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (id == -1) cout&lt;&lt;\"***\\n\";&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;ansI = 0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; euer(id, -1);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(int i = n-1; i &gt;= 0; i--) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (i!=n-1) cout&lt;&lt;\".\";&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; cout&lt;&lt;dict[ans[i]];&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; cout&lt;&lt;endl;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }}','',0,'math',0,'','zinnia/_entry_detail.html','entry_detail.html'),(140,'Ubuntu常用命令','337319372007874519183',2,'2007-09-07 16:51:09',NULL,'2007-09-07 16:51:09','2014-07-13 18:57:15','<p><br />查看软件xxx安装内容 </p>  <p>dpkg -L xxx 查找软件 </p>  <p>apt-cache search 正则表达式查找文件属于哪个包 </p>  <p>dpkg -S filename<br />apt-file search filename查询软件xxx依赖哪些包 </p>  <p>apt-cache depends xxx查询软件xxx被哪些包依赖 </p>  <p>apt-cache rdepends xxx增加一个光盘源 </p>  <p>sudo apt-cdrom add系统升级 </p>  <p>sudo apt-get update<br />sudo apt-get upgrade<br />sudo apt-get dist-upgrade清除所以删除包的残馀配置文件 </p>  <p>dpkg -l |grep ^rc|awk \'{print $2}\' |tr [[\\n]] [[ ]]|sudo xargs dpkg -P&nbsp; -编译时缺少h文件的自动处理 </p>  <p>sudo auto-apt run ./configure查看安装软件时下载包的临时存放目录 </p>  <p>ls /var/cache/apt/archives备份当前系统安装的所有包的列表 </p>  <p>dpkg --get-selections | grep -v deinstall &gt; ~/somefile<br />从上面备份的安装包的列表文件恢复所有包 </p>  <p>dpkg --set-selections &lt; ~/somefile<br />sudo dselect<br />清理旧版本的软件缓存 </p>  <p>sudo apt-get autoclean清理所有软件缓存 </p>  <p>sudo apt-get clean删除系统不再使用的孤立软件 </p>  <p>sudo apt-get autoremove查看包在服务器上面的地址 </p>  <p>apt-get -qq --print-uris install ssh | cut -d\\\' -f2系统 <br />查看内核 </p>  <p>uname -a查看Ubuntu版本 </p>  <p>cat /etc/issue查看内核加载的模块 </p>  <p>lsmod查看PCI设备 </p>  <p>lspci查看USB设备 </p>  <p>lsusb查看网卡状态 </p>  <p>sudo ethtool eth0查看CPU信息 </p>  <p>cat /proc/cpuinfo显示当前硬件信息 </p>  <p>lshw硬盘 <br />查看硬盘的分区 </p>  <p>sudo fdisk -l查看IDE硬盘信息 </p>  <p>sudo hdparm -i /dev/hda查看SATA硬盘信息 </p>  <p>sudo hdparm -I /dev/sda<br />或<br />sudo apt-get install blktool<br />sudo blktool /dev/sda id查看硬盘剩馀空间 </p>  <p>df -h<br />df -H查看目录占用空间 </p>  <p>du -hs 目录名优盘没法卸载 </p>  <p>sync<br />fuser -km /media/usbdisk 内存 <br />查看当前的内存使用情况 </p>  <p>free -m进程 <br />查看当前有哪些进程 </p>  <p>ps -A中止一个进程 </p>  <p>kill 进程号(就是ps -A中的第一列的数字)<br />或者&nbsp; killall 进程名强制中止一个进程(在上面进程中止不成功的时候使用) </p>  <p>kill -9 进程号<br />或者&nbsp; killall -9 进程名图形方式中止一个程序 </p>  <p>xkill 出现骷髅标志的鼠标，点击需要中止的程序即可查看当前进程的实时状况 </p>  <p>top查看进程打开的文件 </p>  <p>lsof -pADSL <br />配置 ADSL </p>  <p>sudo pppoeconf防止 ADSL 掉线 </p>  <p>在终端内 </p>  <p>sudo&nbsp; gedit /etc/network/interfaces复制下面内容完全复盖即可 </p>  <p># /etc/network/interfaces<br /># This file describes the network interfaces available on your system<br /># and how to activate them. For more information, see interfaces(5).</p>  <p># The loopback network interface<br />auto lo<br />iface lo inet loopback</p>  <p># This is a list of hotpluggable network interfaces.<br /># They will be activated automatically by the hotplug subsystem.<br />mapping hotplug<br />script grep<br />map eth1</p>  <p># The primary network interface<br />iface eth1 inet static<br />address 10.0.0.139<br />netmask 255.255.255.0<br />network 10.0.0.0<br />broadcast 10.0.0.255<br /># dns-* options are implemented by the resolvconf package, if installed<br />dns-nameservers 211.162.32.1 211.162.32.20dns-nameservers 是你的dns服务器地址，如果有路由器的同志可以打开路由器设置看看，把路由器上的默认dns服务器贴上。 </p>  <p>ADSL手工拨号 </p>  <p>sudo pon dsl-provider激活 ADSL </p>  <p>sudo /etc/ppp/pppoe_on_boot断开 ADSL </p>  <p>sudo poff查看拨号日志 </p>  <p>sudo plog如何设置动态域名 <br />#首先去 <a href=\"http://www.3322.org\" rel=\"nofollow\">http://www.3322.org</a> 申请一个动态域名<br />#然後修改 /etc/ppp/ip-up 增加拨号时更新域名指令<br />sudo vim /etc/ppp/ip-up<br />#在最後增加如下行<br />w3m -no-cookie -dump \'http://username:password@members.3322.org/dyndns/update?system=dyndns&amp;hostname=yourdns.3322.org\'<br />网络 <br />根据IP查网卡地址 </p>  <p>arping IP地址查看当前IP地址 </p>  <p>ifconfig eth0 |awk \'/inet/ {split($2,x,\":\");print x[2]}\'查看当前外网的IP地址 </p>  <p>w3m -no-cookie -dump <a href=\"http://www.ip138.com|grep\" rel=\"nofollow\">www.ip138.com|grep</a> -o \'[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\'<br />w3m -no-cookie -dump <a href=\"http://www.123cha.com|grep\" rel=\"nofollow\">www.123cha.com|grep</a> -o \'[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\'<br />w3m -no-cookie -dump ip.loveroot.com|grep -o \'[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\'查看当前监听80端口的程序 </p>  <p>lsof -i :80查看当前网卡的物理地址 </p>  <p>arp -a | awk \'{print $4}\'<br />ifconfig eth0 | head -1 | awk \'{print $5}\'立即让网络支持nat </p>  <p>echo 1 | sudo tee /proc/sys/net/ipv4/ip_forward<br />sudo iptables -t nat -I POSTROUTING -j MASQUERADE查看路由信息 </p>  <p>netstat -rn<br />sudo route -n<br />手工增加删除一条路由 </p>  <p>sudo route add -net 192.168.0.0 netmask 255.255.255.0 gw 172.16.0.1<br />sudo route del -net 192.168.0.0 netmask 255.255.255.0 gw 172.16.0.1修改网卡MAC地址的方法 </p>  <p>sudo ifconfig eth0 down #关闭网卡<br />sudo ifconfig eth0 hw ether 00:AA:BB:CC:DD:EE #然後改地址<br />sudo ifconfig eth0 up #然後启动网卡统计当前IP连接的个数 <br />netstat -na|grep ESTABLISHED|awk \'{print $5}\'|awk -F: \'{print $1}\'|sort|uniq -c|sort -r -n<br />netstat -na|grep SYN|awk \'{print $5}\'|awk -F: \'{print $1}\'|sort|uniq -c|sort -r -n统计当前20000个IP包中大于100个IP包的IP地址 <br />tcpdump -tnn -c 20000 -i eth0 | awk -F \".\" \'{print $1\".\"$2\".\"$3\".\"$4}\' | sort | uniq -c | sort -nr | awk \' $1 &gt; 100 \'屏蔽IPV6 <br />echo \"blacklist ipv6\" | sudo tee /etc/modprobe.d/blacklist-ipv6服务 <br />添加一个服务 </p>  <p>sudo update-rc.d 服务名 defaults 99删除一个服务 </p>  <p>sudo update-rc.d 服务名 remove临时重启一个服务 </p>  <p>/etc/init.d/服务名 restart临时关闭一个服务 </p>  <p>/etc/init.d/服务名 stop临时启动一个服务 </p>  <p>/etc/init.d/服务名 start设置 <br />配置默认Java使用哪个 </p>  <p>sudo update-alternatives --config java修改用户资料 </p>  <p>sudo chfn userid给apt设置代理 </p>  <p>export http_proxy=http://xx.xx.xx.xx:xxx修改系统登录信息 </p>  <p>sudo vim /etc/motd中文 <br />转换文件名由GBK为UTF8 </p>  <p>sudo apt-get install convmv<br />convmv -r -f cp936 -t utf8 --notest --nosmart *批量转换src目录下的所有文件内容由GBK到UTF8 <br />find src -type d -exec mkdir -p utf8/{} \\;<br />find src -type f -exec iconv -f GBK -t UTF-8 {} -o utf8/{} \\;<br />mv utf8/* src<br />rm -fr utf8转换文件内容由GBK到UTF8 </p>  <p>iconv -f gbk -t utf8 $i &gt; newfile 转换 mp3 标签编码 </p>  <p>sudo apt-get install python-mutagen<br />find . -iname “*.mp3” -execdir mid3iconv -e GBK {} \\;控制台下显示中文 </p>  <p>sudo apt-get install zhcon<br />使用时，输入zhcon即可文件 <br />快速查找某个文件 </p>  <p>whereis filename<br />find 目录 -name 文件名查看文件类型 </p>  <p>file filename显示xxx文件倒数6行的内容 </p>  <p>tail -n 6 xxx让tail不停地读地最新的内容 </p>  <p>tail -n 10 -f /var/log/apache2/access.log查看文件中间的第五行（含）到第10行（含）的内容 </p>  <p>sed -n \'5,10p\' /var/log/apache2/access.log查找包含xxx字符串的文件 </p>  <p>grep -l -r xxx .查找关于xxx的命令 </p>  <p>apropos xxx<br />man -k xxx通过ssh传输文件 </p>  <p>scp -rp /path/filename <a href=\"mailto:username@remoteIP:/path\" rel=\"nofollow\">username@remoteIP:/path</a>&nbsp; #将本地文件拷贝到服务器上<br />scp -rp <a href=\"mailto:username@remoteIP:/path/filename\" rel=\"nofollow\">username@remoteIP:/path/filename</a> /path&nbsp; #将远程文件从服务器下载到本地查看某个文件被哪些应用程序读写 </p>  <p>lsof 文件名把所有文件的後辍由rm改为rmvb </p>  <p>rename \'s/.rm$/.rmvb/\' *把所有文件名中的大写改为小写 </p>  <p>rename \'tr/A-Z/a-z/\' *删除特殊文件名的文件，如文件名：--help.txt <br />rm -- --help.txt 或者 rm ./--help.txt查看当前目录的子目录 <br />ls -d */. 或 echo */.将当前目录下最近30天访问过的文件移动到上级back目录 <br />find . -type f -atime -30 -exec mv {} ../back \\;将当前目录下最近2小时到8小时之内的文件显示出来 <br />find . -mmin +120 -mmin -480 -exec more {} \\;删除修改时间在30天之前的所有文件 <br />find . -type f -mtime +30 -mtime -3600 -exec rm {} \\;查找guest用户的以avi或者rm结尾的文件并删除掉 <br />find . -name \'*.avi\' -o -name \'*.rm\' -user \'guest\' -exec rm {} \\;查找的不以java和xml结尾,并7天没有使用的文件删除掉 <br />find . ! -name *.java ! -name ‘*.xml’ -atime +7 -exec rm {} \\;统计当前文件个数 <br />ls /usr/bin|wc -w统计当前目录个数 <br />ls -l /usr/bin|grep ^d|wc -l显示当前目录下2006-01-01的文件名 <br />ls -l |grep 2006-01-01 |awk \'{print $8}\'</p>  <p><br />压缩 <br />解压缩 xxx.tar.gz </p>  <p>tar -zxvf xxx.tar.gz解压缩 xxx.tar.bz2 </p>  <p>tar -jxvf xxx.tar.bz2压缩aaa bbb目录为xxx.tar.gz </p>  <p>tar -zcvf xxx.tar.gz aaa bbb压缩aaa bbb目录为xxx.tar.bz2 </p>  <p>tar -jcvf xxx.tar.bz2 aaa bbbNautilus <br />显示隐藏文件 </p>  <p>Ctrl+h显示地址栏 </p>  <p>Ctrl+l特殊 URI 地址 </p>  <p>* computer:/// - 全部挂载的设备和网络<br />* network:/// - 浏览可用的网络<br />* burn:/// - 一个刻录 CDs/DVDs 的数据虚拟目录<br />* smb:/// - 可用的 windows/samba 网络资源<br />* x-nautilus-desktop:/// - 桌面项目和图标<br />* <a href=\"file://\" rel=\"nofollow\">file:///</a> - 本地文件<br />* trash:/// - 本地回收站目录<br />* ftp:// - FTP 文件夹<br />* ssh:// - SSH 文件夹<br />* fonts:/// - 字体文件夹，可将字体文件拖到此处以完成安装<br />* themes:/// - 系统主题文件夹<br />查看已安装字体 </p>  <p>在nautilus的地址栏里输入”fonts:///“，就可以查看本机所有的fonts程序 <br />详细显示程序的运行信息 </p>  <p>strace -f -F -o outfile &lt;cmd&gt;日期和时间 <br />设置日期 </p>  <p>#date -s mm/dd/yy设置时间 </p>  <p>#date -s HH:MM将时间写入CMOS </p>  <p>hwclock --systohc读取CMOS时间 </p>  <p>hwclock --hctosys从服务器上同步时间 </p>  <p>sudo ntpdate time.nist.gov<br />sudo ntpdate time.windows.com<br />或者： </p>  <p>sudo ntpdate-debian <br />控制台 <br />不同控制台间切换 </p>  <p>Ctrl + ALT + ←<br />Ctrl + ALT + →指定控制台切换 </p>  <p>Ctrl + ALT + Fn(n:1~7)控制台下滚屏 </p>  <p>SHIFT + pageUp/pageDown控制台抓图 </p>  <p>setterm -dump n(n:1~7)数据库 <br />mysql的数据库存放在地方 </p>  <p>/var/lib/mysql从mysql中导出和导入数据 </p>  <p>mysqldump 数据库名 &gt; 文件名&nbsp; #导出数据库<br />mysqladmin create 数据库名 #建立数据库<br />mysql 数据库名 &lt; 文件名 #导入数据库忘了mysql的root口令怎么办 </p>  <p>sudo /etc/init.d/mysql stop<br />sudo mysqld_safe --skip-grant-tables &amp;<br />sudo mysqladmin -u user password \'newpassword\'\'<br />sudo mysqladmin flush-privileges修改mysql的root口令 </p>  <p>sudo mysqladmin -uroot -p password \'你的新密码\'其它 <br />下载网站文档 </p>  <p>wget -r -p -np -k <a href=\"http://www.21cn.com\" rel=\"nofollow\">http://www.21cn.com</a><br />· -r：在本机建立服务器端目录结构；<br />· -p: 下载显示HTML文件的所有图片；<br />· -np：只下载目标站点指定目录及其子目录的内容；<br />· -k: 转换非相对链接为相对链接。如何删除Totem电影播放机的播放历史记录 </p>  <p>rm ~/.recently-used如何更换gnome程序的快捷键 </p>  <p>点击菜单，鼠标停留在某条菜单上，键盘输入任意你所需要的键，可以是组合键，会立即生效；<br />如果要清除该快捷键，请使用backspacevim 如何显示彩色字符 <br />sudo cp /usr/share/vim/vimcurrent/vimrc_example.vim&nbsp; /usr/share/vim/vimrc<br />如何在命令行删除在会话设置的启动程序 <br />cd ~/.config/autostart<br />rm 需要删除启动程序<br />如何提高wine的反应速度 <br />sudo sed -ie \'/GBK/,/^}/d\' /usr/share/X11/locale/zh_CN.UTF-8/XLC_LOCALE<br /></p>',1,1,1,0,0,0,'查看软件xxx安装内容   dpkg -L xxx 查找软件   apt-cache search 正则表达式查找文件属于哪个包   dpkg -S filenameapt-file search filename查询软件xxx依赖哪些包   apt-cache depends xxx查询软件xxx被哪些包依赖   apt-cache rdepends xxx增加一个光盘源   sudo apt-cdrom add系统升级   sudo apt-get updatesudo apt-get upgradesudo apt-get dist-upgrade清除所以删除包的残馀配置文件   dpkg -l |grep ^rc|awk \'{print $2}\' |tr [[\\n]] [[ ]]|sudo xargs dpkg -P&nbsp; -编译时缺少h文件的自动处理   sudo auto-apt run ./configure查看安装软件时下载包的临时存放目录   ls /var/cache/apt/archives备份当前系统安装的所有包的列表   dpkg --get-selections | grep -v deinstall &gt; ~/somefile从上面备份的安装包的列表文件恢复所有包   dpkg --set-selections &lt; ~/somefilesudo dselect清理旧版本的软件缓存   sudo apt-get autoclean清理所有软件缓存   sudo apt-get clean删除系统不再使用的孤立软件   sudo apt-get autoremove查看包在服务器上面的地址   apt-get -qq --print-uris install ssh | cut -d\\\' -f2系统 查看内核   uname -a查看Ubuntu版本   cat /etc/issue查看内核加载的模块   lsmod查看PCI设备   lspci查看USB设备   lsusb查看网卡状态   sudo ethtool eth0查看CPU信息   cat /proc/cpuinfo显示当前硬件信息   lshw硬盘 查看硬盘的分区   sudo fdisk -l查看IDE硬盘信息   sudo hdparm -i /dev/hda查看SATA硬盘信息   sudo hdparm -I /dev/sda或sudo apt-get install blktoolsudo blktool /dev/sda id查看硬盘剩馀空间   df -hdf -H查看目录占用空间   du -hs 目录名优盘没法卸载   syncfuser -km /media/usbdisk 内存 查看当前的内存使用情况   free -m进程 查看当前有哪些进程   ps -A中止一个进程   kill 进程号(就是ps -A中的第一列的数字)或者&nbsp; killall 进程名强制中止一个进程(在上面进程中止不成功的时候使用)   kill -9 进程号或者&nbsp; killall -9 进程名图形方式中止一个程序   xkill 出现骷髅标志的鼠标，点击需要中止的程序即可查看当前进程的实时状况   top查看进程打开的文件   lsof -pADSL 配置 ADSL   sudo pppoeconf防止 ADSL 掉线   在终端内   sudo&nbsp; gedit /etc/network/interfaces复制下面内容完全复盖即可   # /etc/network/interfaces# This file describes the network interfaces available on your system# and how to activate them. For more information, see interfaces(5).  # The loopback network interfaceauto loiface lo inet loopback  # This is a list of hotpluggable network interfaces.# They will be activated automatically by the hotplug subsystem.mapping hotplugscript grepmap eth1  # The primary network interfaceiface eth1 inet staticaddress 10.0.0.139netmask 255.255.255.0network 10.0.0.0broadcast 10.0.0.255# dns-* options are implemented by the resolvconf package, if installeddns-nameservers 211.162.32.1 211.162.32.20dns-nameservers 是你的dns服务器地址，如果有路由器的同志可以打开路由器设置看看，把路由器上的默认dns服务器贴上。   ADSL手工拨号   sudo pon dsl-provider激活 ADSL   sudo /etc/ppp/pppoe_on_boot断开 ADSL   sudo poff查看拨号日志   sudo plog如何设置动态域名 #首先去 http://www.3322.org 申请一个动态域名#然後修改 /etc/ppp/ip-up 增加拨号时更新域名指令sudo vim /etc/ppp/ip-up#在最後增加如下行w3m -no-cookie -dump \'http://username:password@members.3322.org/dyndns/update?system=dyndns&amp;hostname=yourdns.3322.org\'网络 根据IP查网卡地址   arping IP地址查看当前IP地址   ifconfig eth0 |awk \'/inet/ {split($2,x,\":\");print x[2]}\'查看当前外网的IP地址   w3m -no-cookie -dump www.ip138.com|grep -o \'[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\'w3m -no-cookie -dump www.123cha.com|grep -o \'[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\'w3m -no-cookie -dump ip.loveroot.com|grep -o \'[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\\.[0-9]\\{1,3\\}\'查看当前监听80端口的程序   lsof -i :80查看当前网卡的物理地址   arp -a | awk \'{print $4}\'ifconfig eth0 | head -1 | awk \'{print $5}\'立即让网络支持nat   echo 1 | sudo tee /proc/sys/net/ipv4/ip_forwardsudo iptables -t nat -I POSTROUTING -j MASQUERADE查看路由信息   netstat -rnsudo route -n手工增加删除一条路由   sudo route add -net 192.168.0.0 netmask 255.255.255.0 gw 172.16.0.1sudo route del -net 192.168.0.0 netmask 255.255.255.0 gw 172.16.0.1修改网卡MAC地址的方法   sudo ifconfig eth0 down #关闭网卡sudo ifconfig eth0 hw ether 00:AA:BB:CC:DD:EE #然後改地址sudo ifconfig eth0 up #然後启动网卡统计当前IP连接的个数 netstat -na|grep ESTABLISHED|awk \'{print $5}\'|awk -F: \'{print $1}\'|sort|uniq -c|sort -r -nnetstat -na|grep SYN|awk \'{print $5}\'|awk -F: \'{print $1}\'|sort|uniq -c|sort -r -n统计当前20000个IP包中大于100个IP包的IP地址 tcpdump -tnn -c 20000 -i eth0 | awk -F \".\" \'{print $1\".\"$2\".\"$3\".\"$4}\' | sort | uniq -c | sort -nr | awk \' $1 &gt; 100 \'屏蔽IPV6 echo \"blacklist ipv6\" | sudo tee /etc/modprobe.d/blacklist-ipv6服务 添加一个服务   sudo update-rc.d 服务名 defaults 99删除一个服务   sudo update-rc.d 服务名 remove临时重启一个服务   /etc/init.d/服务名 restart临时关闭一个服务   /etc/init.d/服务名 stop临时启动一个服务   /etc/init.d/服务名 start设置 配置默认Java使用哪个   sudo update-alternatives --config java修改用户资料   sudo chfn userid给apt设置代理   export http_proxy=http://xx.xx.xx.xx:xxx修改系统登录信息   sudo vim /etc/motd中文 转换文件名由GBK为UTF8   sudo apt-get install convmvconvmv -r -f cp936 -t utf8 --notest --nosmart *批量转换src目录下的所有文件内容由GBK到UTF8 find src -type d -exec mkdir -p utf8/{} \\;find src -type f -exec iconv -f GBK -t UTF-8 {} -o utf8/{} \\;mv utf8/* srcrm -fr utf8转换文件内容由GBK到UTF8   iconv -f gbk -t utf8 $i &gt; newfile 转换 mp3 标签编码   sudo apt-get install python-mutagenfind . -iname “*.mp3” -execdir mid3iconv -e GBK {} \\;控制台下显示中文   sudo apt-get install zhcon使用时，输入zhcon即可文件 快速查找某个文件   whereis filenamefind 目录 -name 文件名查看文件类型   file filename显示xxx文件倒数6行的内容   tail -n 6 xxx让tail不停地读地最新的内容   tail -n 10 -f /var/log/apache2/access.log查看文件中间的第五行（含）到第10行（含）的内容   sed -n \'5,10p\' /var/log/apache2/access.log查找包含xxx字符串的文件   grep -l -r xxx .查找关于xxx的命令   apropos xxxman -k xxx通过ssh传输文件   scp -rp /path/filename username@remoteIP:/path&nbsp; #将本地文件拷贝到服务器上scp -rp username@remoteIP:/path/filename /path&nbsp; #将远程文件从服务器下载到本地查看某个文件被哪些应用程序读写   lsof 文件名把所有文件的後辍由rm改为rmvb   rename \'s/.rm$/.rmvb/\' *把所有文件名中的大写改为小写   rename \'tr/A-Z/a-z/\' *删除特殊文件名的文件，如文件名：--help.txt rm -- --help.txt 或者 rm ./--help.txt查看当前目录的子目录 ls -d */. 或 echo */.将当前目录下最近30天访问过的文件移动到上级back目录 find . -type f -atime -30 -exec mv {} ../back \\;将当前目录下最近2小时到8小时之内的文件显示出来 find . -mmin +120 -mmin -480 -exec more {} \\;删除修改时间在30天之前的所有文件 find . -type f -mtime +30 -mtime -3600 -exec rm {} \\;查找guest用户的以avi或者rm结尾的文件并删除掉 find . -name \'*.avi\' -o -name \'*.rm\' -user \'guest\' -exec rm {} \\;查找的不以java和xml结尾,并7天没有使用的文件删除掉 find . ! -name *.java ! -name ‘*.xml’ -atime +7 -exec rm {} \\;统计当前文件个数 ls /usr/bin|wc -w统计当前目录个数 ls -l /usr/bin|grep ^d|wc -l显示当前目录下2006-01-01的文件名 ls -l |grep 2006-01-01 |awk \'{print $8}\'  压缩 解压缩 xxx.tar.gz   tar -zxvf xxx.tar.gz解压缩 xxx.tar.bz2   tar -jxvf xxx.tar.bz2压缩aaa bbb目录为xxx.tar.gz   tar -zcvf xxx.tar.gz aaa bbb压缩aaa bbb目录为xxx.tar.bz2   tar -jcvf xxx.tar.bz2 aaa bbbNautilus 显示隐藏文件   Ctrl+h显示地址栏   Ctrl+l特殊 URI 地址   * computer:/// - 全部挂载的设备和网络* network:/// - 浏览可用的网络* burn:/// - 一个刻录 CDs/DVDs 的数据虚拟目录* smb:/// - 可用的 windows/samba 网络资源* x-nautilus-desktop:/// - 桌面项目和图标* file:/// - 本地文件* trash:/// - 本地回收站目录* ftp:// - FTP 文件夹* ssh:// - SSH 文件夹* fonts:/// - 字体文件夹，可将字体文件拖到此处以完成安装* themes:/// - 系统主题文件夹查看已安装字体   在nautilus的地址栏里输入”fonts:///“，就可以查看本机所有的fonts程序 详细显示程序的运行信息   strace -f -F -o outfile &lt;cmd&gt;日期和时间 设置日期   #date -s mm/dd/yy设置时间   #date -s HH:MM将时间写入CMOS   hwclock --systohc读取CMOS时间   hwclock --hctosys从服务器上同步时间   sudo ntpdate time.nist.govsudo ntpdate time.windows.com或者：   sudo ntpdate-debian 控制台 不同控制台间切换   Ctrl + ALT + ←Ctrl + ALT + →指定控制台切换   Ctrl + ALT + Fn(n:1~7)控制台下滚屏   SHIFT + pageUp/pageDown控制台抓图   setterm -dump n(n:1~7)数据库 mysql的数据库存放在地方   /var/lib/mysql从mysql中导出和导入数据   mysqldump 数据库名 &gt; 文件名&nbsp; #导出数据库mysqladmin create 数据库名 #建立数据库mysql 数据库名 &lt; 文件名 #导入数据库忘了mysql的root口令怎么办   sudo /etc/init.d/mysql stopsudo mysqld_safe --skip-grant-tables &amp;sudo mysqladmin -u user password \'newpassword\'\'sudo mysqladmin flush-privileges修改mysql的root口令   sudo mysqladmin -uroot -p password \'你的新密码\'其它 下载网站文档   wget -r -p -np -k http://www.21cn.com· -r：在本机建立服务器端目录结构；· -p: 下载显示HTML文件的所有图片；· -np：只下载目标站点指定目录及其子目录的内容；· -k: 转换非相对链接为相对链接。如何删除Totem电影播放机的播放历史记录   rm ~/.recently-used如何更换gnome程序的快捷键   点击菜单，鼠标停留在某条菜单上，键盘输入任意你所需要的键，可以是组合键，会立即生效；如果要清除该快捷键，请使用backspacevim 如何显示彩色字符 sudo cp /usr/share/vim/vimcurrent/vimrc_example.vim&nbsp; /usr/share/vim/vimrc如何在命令行删除在会话设置的启动程序 cd ~/.config/autostartrm 需要删除启动程序如何提高wine的反应速度 sudo sed -ie \'/GBK/,/^}/d\' /usr/share/X11/locale/zh_CN.UTF-8/XLC_LOCALE','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(141,'64位Ubuntu安装flashget','33731937201161031946443',2,'2011-07-10 15:25:57',NULL,'2011-07-10 15:25:57','2014-07-13 18:57:15','首先, deb安装包不行，因为版本号格式不对，需要用二进制的<br /><a href=\"http://bbs.flashget.com/download/flashget-1.0.3-0_en.tar.gz\" rel=\"nofollow\" target=\"_blank\">wget http://bbs.flashget.com/download/flashget-1.0.3-0_en.tar.gz</a><br />tar xf flashget-1.0.3-0_en.tar.gz<br />cd flashget-1.0.3-0<br />sudo sh install.sh<br /><br />然后需要创建一个软链接<br />sudo ln -s /lib32/libexpat.so.1.5.2 /usr/lib/libexpat.so.0<br /><br />最后修改flashget的启动脚本<br />sudo vi /usr/bin/flashget<br />把最前面的-e去掉 <br /><br />出错信息：<br />1. deb版本号错误<br />dpkg: error processing flashget-1.0.3-0_en.noarch.deb (--install):<br />&nbsp;parsing file \'/var/lib/dpkg/tmp.ci/control\' near line 7 package \'flashget\':<br />&nbsp;error in Version string \'v1.0.3-beta1\': version number does not start with digit<br />Errors were encountered while processing:<br />&nbsp;flashget-1.0.3-0_en.noarch.deb<br />解决办法：使用二进制包安装<br /><br />2. 找不到libexpact<br />flashget: error while loading shared libraries: libexpat.so.0: cannot open shared object file: No such file or directory<br />解决办法：创建软连接<br />sudo ln -s /lib32/libexpat.so.1.5.2 /usr/lib/libexpat.so.0<br /><br />3. 64位和32位的冲突&nbsp;&nbsp; <br />wrong ELF class: ELFCLASS64<br />解决办法：<br />sudo apt-get install ia32-libs<br />sudo ln -s /lib32/libexpat.so.1.5.2 /usr/lib/libexpat.so.0',1,1,1,0,0,0,'首先, deb安装包不行，因为版本号格式不对，需要用二进制的wget http://bbs.flashget.com/download/flashget-1.0.3-0_en.tar.gztar xf flashget-1.0.3-0_en.tar.gzcd flashget-1.0.3-0sudo sh install.sh然后需要创建一个软链接sudo ln -s /lib32/libexpat.so.1.5.2 /usr/lib/libexpat.so.0最后修改flashget的启动脚本sudo vi /usr/bin/flashget把最前面的-e去掉 出错信息：1. deb版本号错误dpkg: error processing flashget-1.0.3-0_en.noarch.deb (--install):&nbsp;parsing file \'/var/lib/dpkg/tmp.ci/control\' near line 7 package \'flashget\':&nbsp;error in Version string \'v1.0.3-beta1\': version number does not start with digitErrors were encountered while processing:&nbsp;flashget-1.0.3-0_en.noarch.deb解决办法：使用二进制包安装2. 找不到libexpactflashget: error while loading shared libraries: libexpat.so.0: cannot open shared object file: No such file or directory解决办法：创建软连接sudo ln -s /lib32/libexpat.so.1.5.2 /usr/lib/libexpat.so.03. 64位和32位的冲突&nbsp;&nbsp; wrong ELF class: ELFCLASS64解决办法：sudo apt-get install ia32-libssudo ln -s /lib32/libexpat.so.1.5.2 /usr/lib/libexpat.so.0','',0,'os',0,'','zinnia/_entry_detail.html','entry_detail.html'),(142,'幸福的人要有楼子的觉悟','3373193720120123506929',2,'2012-01-12 03:50:06',NULL,'2012-01-12 03:50:06','2014-07-13 18:57:15','<div id=\"blogContent\">  <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 话说有个和尚, 苦苦寻找那传说中的大道, 有一天路过一家酒楼, 听到有人在唱曲儿, \"你若无情我便休\", 仰天大笑三声, 得道了, 号称楼子和尚. \"你若无情我便休\", 这就是楼子和尚的道.</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 如今休去便休去, 若觅了时无了时. 当断不断, 反受其乱.</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 有一种美德, 叫做坚持; 有一种智慧, 叫做放下.</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 塞翁失马, 焉知非福, 得失之间, 又哪里说得清楚?</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 你勇敢地迈出九十九步, 等到的也许不是对方的前进或踟蹰, 反而是后退. 坚持还是放下, 小聪明还是大糊涂.</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span>往前一步是幸福, 退后一步是孤独.</span> 退一步也许海阔天空,&nbsp;前一步也许万劫不复.</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 贪嗔痴慢疑, 这一篇字, 犯尽了五毒, 却没有个所以然.</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 坚持是对的, 只要没有确认不会有结果; 放下是对的, 只要确认不是因为怕失败.</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 何以确认? 问天天不应, 问地地不灵, 万物的灵长, 发明了占卜. 不是因为相信, 而是因为无奈.</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 坚持是简单的, 只要将匀速直线进行到底, 需要的只是一点点的毅力.</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 放下是困难的, 放下的人要想幸福, 就要领悟楼子的道, 有楼子的觉悟.</p> </div>',1,1,1,0,0,0,'  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 话说有个和尚, 苦苦寻找那传说中的大道, 有一天路过一家酒楼, 听到有人在唱曲儿, \"你若无情我便休\", 仰天大笑三声, 得道了, 号称楼子和尚. \"你若无情我便休\", 这就是楼子和尚的道.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 如今休去便休去, 若觅了时无了时. 当断不断, 反受其乱.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 有一种美德, 叫做坚持; 有一种智慧, 叫做放下.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 塞翁失马, 焉知非福, 得失之间, 又哪里说得清楚?&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 你勇敢地迈出九十九步, 等到的也许不是对方的前进或踟蹰, 反而是后退. 坚持还是放下, 小聪明还是大糊涂.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 往前一步是幸福, 退后一步是孤独. 退一步也许海阔天空,&nbsp;前一步也许万劫不复.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 贪嗔痴慢疑, 这一篇字, 犯尽了五毒, 却没有个所以然.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 坚持是对的, 只要没有确认不会有结果; 放下是对的, 只要确认不是因为怕失败.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 何以确认? 问天天不应, 问地地不灵, 万物的灵长, 发明了占卜. 不是因为相信, 而是因为无奈.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 坚持是简单的, 只要将匀速直线进行到底, 需要的只是一点点的毅力.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 放下是困难的, 放下的人要想幸福, 就要领悟楼子的道, 有楼子的觉悟. ','',0,'',0,'','zinnia/_entry_detail.html','entry_detail.html'),(143,'远程桌面与远程控制','337319372009310105246563',2,'2009-04-10 10:52:46',NULL,'2009-04-10 10:52:46','2014-07-13 18:57:15','远程桌面与远程控制的方式有几种:<br />1. Windows自带的远程桌面功能: 开始-&gt;运行-&gt;mstsc<br />2. Windows自带的远程协助功能: 开始-&gt;程序-&gt;远程协助<br />3. Windows自带的NetMeeting: 开始-&gt;运行-&gt;conf<br />4. TM的远程协助<br /><br />首先, 远程桌面只能在控制端显示桌面, Pass<br />Windows自带的远程协助需要两个MSN账号, 但好像走的中间服务器, 速度很慢, 而且关闭远程桌面时自动断开连接Pass<br />Netmeeting 不太适合<br />最后只能使用TM的远程协助功能, 可惜我的绿色版TM的远程协助功能不能用, 只好新安装TM2008, 一切搞定截个图:<br /><a href=\"/static/163/2582251436344979018.jpg\" target=\"_blank\"><img alt=\"远程桌面与远程控制 - hankjin - 乱-Blog\" src=\"/static/163/2582251436344979018.jpg\" style=\"width: 650px; height: 364px;\" title=\"远程桌面与远程控制 - hankjin - 乱-Blog\" /></a><a href=\"/static/163/2582251436344979019.jpg\" target=\"_blank\"><img alt=\"远程桌面与远程控制 - hankjin - 乱-Blog\" src=\"/static/163/2582251436344979019.jpg\" style=\"width: 650px; height: 365px;\" title=\"远程桌面与远程控制 - hankjin - 乱-Blog\" /></a>',1,1,1,0,0,0,'远程桌面与远程控制的方式有几种:1. Windows自带的远程桌面功能: 开始-&gt;运行-&gt;mstsc2. Windows自带的远程协助功能: 开始-&gt;程序-&gt;远程协助3. Windows自带的NetMeeting: 开始-&gt;运行-&gt;conf4. TM的远程协助首先, 远程桌面只能在控制端显示桌面, PassWindows自带的远程协助需要两个MSN账号, 但好像走的中间服务器, 速度很慢, 而且关闭远程桌面时自动断开连接PassNetmeeting 不太适合最后只能使用TM的远程协助功能, 可惜我的绿色版TM的远程协助功能不能用, 只好新安装TM2008, 一切搞定截个图:','',0,'admin',0,'','zinnia/_entry_detail.html','entry_detail.html'),(144,'REBOL','3373193720084411190347',2,'2008-05-04 23:19:00',NULL,'2008-05-04 23:19:00','2014-07-13 18:57:15','<p>REBOL发音为“reb-ol”（类似“rebel with a cause”中的），代表“基于相关表达式的对象语言”（relative expression based object language）。他第一次发布是在1997年到目前为止已经下载超过四百万次。他对客户端和服务器端应用程序都是十分合适的</p>  <p>REBOL手册中文翻译：<a href=\"http://www.nirvanastudio.org/category/rebol\" rel=\"nofollow\">http://www.nirvanastudio.org/category/rebol</a></p>  <p>一个非常强大的GUI和网络编程软件，0.6M大小，从这里下载： <a href=\"http://www.rebol.com/view-platforms.html\" rel=\"nofollow\">http://www.rebol.com/view-platforms.html</a></p>  <p>REBOL in One Line（一行代码的程序）</p>  <p>REBOL一行代码程序有悠久的历史，这里有45个一行代码程序：</p>  <p><a href=\"http://www.rebol.com/oneliners.html\" rel=\"nofollow\">http://www.rebol.com/oneliners.html</a></p>  <p>例如： <font face=\"Courier New\" size=\"2\">print read http://www.rebol.com</font></p>  <p>REBOL的GUI程序：</p>  <p><a href=\"http://www.rebol.com/docs/view-guide.html#section-1\" rel=\"nofollow\">http://www.rebol.com/docs/view-guide.html#section-1</a></p>  <p>例如：view layout [title \"Hello World!\"]<br /></p>  <p>&nbsp;</p>  <p>&nbsp;</p>',1,1,1,0,0,0,'REBOL发音为“reb-ol”（类似“rebel with a cause”中的），代表“基于相关表达式的对象语言”（relative expression based object language）。他第一次发布是在1997年到目前为止已经下载超过四百万次。他对客户端和服务器端应用程序都是十分合适的  REBOL手册中文翻译：http://www.nirvanastudio.org/category/rebol  一个非常强大的GUI和网络编程软件，0.6M大小，从这里下载： http://www.rebol.com/view-platforms.html  REBOL in One Line（一行代码的程序）  REBOL一行代码程序有悠久的历史，这里有45个一行代码程序：  http://www.rebol.com/oneliners.html  例如： print read http://www.rebol.com  REBOL的GUI程序：  http://www.rebol.com/docs/view-guide.html#section-1  例如：view layout [title \"Hello World!\"]  &nbsp;  &nbsp;','',0,'other',0,'','zinnia/_entry_detail.html','entry_detail.html'),(145,'几个好用的linux命令和vi操作:mtr,tee,ssh-copy-id,python -m','33731937201093124722423',2,'2010-10-31 14:54:34',NULL,'2010-10-31 14:54:34','2014-07-13 18:57:15','1。查看网络连接状态用什么？<br />ping和traceroute都out了，超级牛力的是mtr，和top类似的界面，试一下吧<br />mtr www.google.com<br /><br />2。使用vi打开一个文件，修改了一大片，要保存的时候发现没有修改权限，怎么办？最土的办法是，用鼠标保存到系统剪贴板，然后再重新用sudo打开，然后再粘贴进去；稍微先进一点的是:w newfile，然后再退出后sudo mv覆盖回去。现在这两个方法都out了。试下这个命令吧<br />:w !sudo tee %<br /><br />3。玩儿过hadoop或者MPI的，都知道ssh-keygen -t rsa... scp... echo ... authorized_keys ...，一大堆命令之后，终于可以直接登录了，现在这些命令也out了，因为ssh-copy-id 出来了，试一下这个吧：<br />ssh-copy-id user@desthost<br /><br />4。想给一个同学传文件怎么办？用飞鸽，用QQ，但是如果要给100个人呢？放邮箱里吗？已经out了，自己做个web服务器就可以啦，不用安装，只要有python运行环境。进入要共享的目录，然后运行这个<br />python -m SimpleHTTPServer<br />然后别人就可以通过 http://你的IP:8000/访问你的目录了。',1,1,1,0,0,0,'1。查看网络连接状态用什么？ping和traceroute都out了，超级牛力的是mtr，和top类似的界面，试一下吧mtr www.google.com2。使用vi打开一个文件，修改了一大片，要保存的时候发现没有修改权限，怎么办？最土的办法是，用鼠标保存到系统剪贴板，然后再重新用sudo打开，然后再粘贴进去；稍微先进一点的是:w newfile，然后再退出后sudo mv覆盖回去。现在这两个方法都out了。试下这个命令吧:w !sudo tee %3。玩儿过hadoop或者MPI的，都知道ssh-keygen -t rsa... scp... echo ... authorized_keys ...，一大堆命令之后，终于可以直接登录了，现在这些命令也out了，因为ssh-copy-id 出来了，试一下这个吧：ssh-copy-id user@desthost4。想给一个同学传文件怎么办？用飞鸽，用QQ，但是如果要给100个人呢？放邮箱里吗？已经out了，自己做个web服务器就可以啦，不用安装，只要有python运行环境。进入要共享的目录，然后运行这个python -m SimpleHTTPServer然后别人就可以通过 http://你的IP:8000/访问你的目录了。','',0,'admin',0,'','zinnia/_entry_detail.html','entry_detail.html'),(146,'Ubuntu下学习busysbox','3373193720081026573349',2,'2008-11-02 18:57:33',NULL,'2008-11-02 18:57:33','2014-07-13 18:57:15','1. 获取源代码<br />cd /usr/local/src<br />sudo apt-get source busybox<br />可爱的apt-get，分别从网上下载了1.1.3 1.8.0 1.9.0 ...，都不能用，原来都需要打补丁，实际上这个apt-get下来的1.10.2的版本包括1.10.2的源代码，补丁包，描述文件和打过补丁的源代码目录<br />2. 编译<br />sudo chown -R hankjin busybox-1.10.2<br />cd busybox-1.10.2<br />make defconfig<br />make<br />./busybox ls<br />3. 加入自己的命令<br />3.1 源代码miscutils/hjzcmd.c<br />#include \"busybox.h\"<br />int hjzcmd(int argc, char *argv[])<br />{<br />&nbsp;&nbsp;&nbsp; printf(\"Hello, hjz cmd called\\n\");<br />&nbsp; &nbsp; return 0;<br />}<br />3.2 miscutils/Kbuid (源代码对应编译选) <br />lib-$(CONFIG_HJZCMD)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; += hjzcmd.o<br />3.3 miscutils/Config.in (编译选项menuconfig菜单)<br />config HJZCMD<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bool \"hjzcmd\"<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; default n<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; help<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; hankjin\'s command, do nothing, just print ok<br />3.4 include/appets.h (hjzcmd的命令存放位置)<br />USE_HJZCMD(APPLET(hjzcmd, _BB_DIR_USR_BIN, _BB_SUID_NEVER))<br />3.5 include/usage.h（用法说明，帮助命令中显示)<br />#define hjzcmd_trivial_usage&nbsp;&nbsp;&nbsp; \"None\"<br />#define hjzcmd_full_usage&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"None\"<br />3.6 测试<br />make<br />./busybox hjzcmd<br />make install<br />ls _install/usr/bin|grep hjzcmd<br /><br /><br /><br /><br />',1,1,1,0,0,0,'1. 获取源代码cd /usr/local/srcsudo apt-get source busybox可爱的apt-get，分别从网上下载了1.1.3 1.8.0 1.9.0 ...，都不能用，原来都需要打补丁，实际上这个apt-get下来的1.10.2的版本包括1.10.2的源代码，补丁包，描述文件和打过补丁的源代码目录2. 编译sudo chown -R hankjin busybox-1.10.2cd busybox-1.10.2make defconfigmake./busybox ls3. 加入自己的命令3.1 源代码miscutils/hjzcmd.c#include \"busybox.h\"int hjzcmd(int argc, char *argv[]){&nbsp;&nbsp;&nbsp; printf(\"Hello, hjz cmd called\\n\");&nbsp; &nbsp; return 0;}3.2 miscutils/Kbuid (源代码对应编译选) lib-$(CONFIG_HJZCMD)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; += hjzcmd.o3.3 miscutils/Config.in (编译选项menuconfig菜单)config HJZCMD&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bool \"hjzcmd\"&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; default n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; help&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; hankjin\'s command, do nothing, just print ok3.4 include/appets.h (hjzcmd的命令存放位置)USE_HJZCMD(APPLET(hjzcmd, _BB_DIR_USR_BIN, _BB_SUID_NEVER))3.5 include/usage.h（用法说明，帮助命令中显示)#define hjzcmd_trivial_usage&nbsp;&nbsp;&nbsp; \"None\"#define hjzcmd_full_usage&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"None\"3.6 测试make./busybox hjzcmdmake installls _install/usr/bin|grep hjzcmd','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(147,'JDK5 新特性 Annotation','3373193720085145145930',2,'2008-06-01 16:51:45',NULL,'2008-06-01 16:51:45','2014-07-13 18:57:15','参考JDK源代码java.lang.annotation<br />限制不同的类的访问权限<br /><br />package hjz.annotation;<br /><br />import java.lang.annotation.Documented;<br />import java.lang.annotation.ElementType;<br />import java.lang.annotation.Retention;<br />import java.lang.annotation.RetentionPolicy;<br />import java.lang.annotation.Target;<br /><br />@Documented<br />@Retention(RetentionPolicy.RUNTIME)<br />@Target(ElementType.TYPE)<br />@interface Access{<br />&nbsp;&nbsp; &nbsp;String name();<br />&nbsp;&nbsp; &nbsp;String pass();<br />}<br />abstract class User{}<br /><br />@Access(name=\"hank\",pass=\"123\")<br />class Student extends User{<br />&nbsp;&nbsp; &nbsp;public Student(){}<br />}<br />class Teacher extends User{<br />&nbsp;&nbsp; &nbsp;<br />}<br />public class Test {<br /><br />&nbsp;&nbsp; &nbsp;/**<br />&nbsp;&nbsp; &nbsp; * @param args<br />&nbsp;&nbsp; &nbsp; */<br />&nbsp;&nbsp; &nbsp;public static void main(String[] args) {<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;// TODO Auto-generated method stub<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(\"can Student say Hi?\" + new Test().sayHi(new Student()));<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(\"can Teacher say Hi?\" + new Test().sayHi(new Teacher()));<br />&nbsp;&nbsp; &nbsp;}<br />&nbsp;&nbsp; &nbsp;boolean sayHi(User user){<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if(user.getClass().isAnnotationPresent(Access.class) == false)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return false;<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Access access = user.getClass().getAnnotation(Access.class);<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if(access.name().equals(\"hank\") &amp;&amp; access.pass().equals(\"123\"))<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return true;<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;else<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return false;<br />&nbsp;&nbsp; &nbsp;}<br />}',1,1,1,0,0,0,'参考JDK源代码java.lang.annotation限制不同的类的访问权限package hjz.annotation;import java.lang.annotation.Documented;import java.lang.annotation.ElementType;import java.lang.annotation.Retention;import java.lang.annotation.RetentionPolicy;import java.lang.annotation.Target;@Documented@Retention(RetentionPolicy.RUNTIME)@Target(ElementType.TYPE)@interface Access{&nbsp;&nbsp; &nbsp;String name();&nbsp;&nbsp; &nbsp;String pass();}abstract class User{}@Access(name=\"hank\",pass=\"123\")class Student extends User{&nbsp;&nbsp; &nbsp;public Student(){}}class Teacher extends User{&nbsp;&nbsp; &nbsp;}public class Test {&nbsp;&nbsp; &nbsp;/**&nbsp;&nbsp; &nbsp; * @param args&nbsp;&nbsp; &nbsp; */&nbsp;&nbsp; &nbsp;public static void main(String[] args) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;// TODO Auto-generated method stub&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(\"can Student say Hi?\" + new Test().sayHi(new Student()));&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(\"can Teacher say Hi?\" + new Test().sayHi(new Teacher()));&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;boolean sayHi(User user){&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if(user.getClass().isAnnotationPresent(Access.class) == false)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return false;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Access access = user.getClass().getAnnotation(Access.class);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if(access.name().equals(\"hank\") &amp;&amp; access.pass().equals(\"123\"))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return true;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;else&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return false;&nbsp;&nbsp; &nbsp;}}','',0,'j2se',0,'','zinnia/_entry_detail.html','entry_detail.html'),(148,'制作从U盘(SD卡)启动的Linux CDLinux 0.4.9','337319372008931103556676',2,'2008-10-31 22:35:56',NULL,'2008-10-31 22:35:56','2014-07-13 18:57:15','买手机送了一个读卡器，刚好把闲置的32M佳能SD卡利用起来，虽然支持U盘启动的Linux很多，但一般都很大，最后试验成功的只有两种<br />1. 自编译的嵌入式Linux<br />全部用源代码编译成，使用busybox编译的文件系统，最后只用了几兆的空间，可惜因为用的busybox，扩展很不方便。<br />2. CDLinux 0.4.9<br />实际上最新的CDLinux已经很强大了，0.5.1和0.6.1已经支持桌面了，可惜对于32M的SD卡来讲，桌面太奢侈了，最终选择0.4.9的CDLinux，不带桌面，不过提供了cce中文输入法，很好很强大<br />安装方法：<br /><div>\n\n    \n\n\n\n\n\n\n\n\n\n\n<ol><li>首先将SD卡格式化为可启动U盘，并改为syslinux引导\n<ol><li>在Windows下，使用Dropbox:/bin/LiveUSB的FlashBootWizard将SD卡格式化为可启动FreeDOS盘，并使用USB-ZIP模式启动，测试可以进入FreeDOS\n</li><li>进入Linux下执行命令：syslinux /dev/sda\n</li></ol></li><li>拷贝文件到SD卡\n<ol><li>挂载文件(SD卡被识别为/dev/sda)\n<ol><li>mkdir /mnt/img\n</li><li>mount -o loop CDlinux-0.4.9.iso /mnt/cdrom\n</li><li>mount -o loop /mnt/cdrom/boot/CDlinux/CDlinux.img /mnt/img\n</li><li>mount /dev/sda /mnt/win\n</li></ol></li><li>拷贝kernel和ramdisk\n<ol><li>cp /mnt/img/bzImage      /mnt/win\n</li><li>cp /mnt/img/CDlinux.gz   /mnt/win\n</li></ol></li><li>拷贝syslinux的配置文件\n<ol><li>cp /mnt/img/SYSlinux.cfg /mnt/win\n</li></ol></li><li>拷贝软件包\n<ol><li>mkdir /mnt/win/CDlinux\n</li><li>cp -r /mnt/cdrom/boot/CDlinux/extra   /mnt/win/CDlinux\n</li></ol></li><li>拷贝完成后应该有4个文件(ldlinux.sys,syslinux.cfg,bzImage,CDlinux.gz)和一个文件夹(CDlinux)\n</li><li>ldlinux.sys (syslinux boodloeader的第二阶段引导代码）</li><li>syslinux.cfg (syslinux的引导配置文件)</li><li>bzImage 内核</li><li>CDlinux.gz initrd文件压缩的<br /></li></ol></li><li>修改启动参数\n<ol><li>启动时输入cdlinux CDL_DEV=/dev/sda\n</li><li>或者修改syslinux.cfg 在CDlinux.gz后加入CDL_DEV=/dev/sda\n</li></ol></li></ol>\n\n\n\n\n\n\n        \n  </div>',1,1,1,0,0,0,'买手机送了一个读卡器，刚好把闲置的32M佳能SD卡利用起来，虽然支持U盘启动的Linux很多，但一般都很大，最后试验成功的只有两种1. 自编译的嵌入式Linux全部用源代码编译成，使用busybox编译的文件系统，最后只用了几兆的空间，可惜因为用的busybox，扩展很不方便。2. CDLinux 0.4.9实际上最新的CDLinux已经很强大了，0.5.1和0.6.1已经支持桌面了，可惜对于32M的SD卡来讲，桌面太奢侈了，最终选择0.4.9的CDLinux，不带桌面，不过提供了cce中文输入法，很好很强大安装方法：\n\n    \n\n\n\n\n\n\n\n\n\n\n首先将SD卡格式化为可启动U盘，并改为syslinux引导\n在Windows下，使用Dropbox:/bin/LiveUSB的FlashBootWizard将SD卡格式化为可启动FreeDOS盘，并使用USB-ZIP模式启动，测试可以进入FreeDOS\n进入Linux下执行命令：syslinux /dev/sda\n拷贝文件到SD卡\n挂载文件(SD卡被识别为/dev/sda)\nmkdir /mnt/img\nmount -o loop CDlinux-0.4.9.iso /mnt/cdrom\nmount -o loop /mnt/cdrom/boot/CDlinux/CDlinux.img /mnt/img\nmount /dev/sda /mnt/win\n拷贝kernel和ramdisk\ncp /mnt/img/bzImage      /mnt/win\ncp /mnt/img/CDlinux.gz   /mnt/win\n拷贝syslinux的配置文件\ncp /mnt/img/SYSlinux.cfg /mnt/win\n拷贝软件包\nmkdir /mnt/win/CDlinux\ncp -r /mnt/cdrom/boot/CDlinux/extra   /mnt/win/CDlinux\n拷贝完成后应该有4个文件(ldlinux.sys,syslinux.cfg,bzImage,CDlinux.gz)和一个文件夹(CDlinux)\nldlinux.sys (syslinux boodloeader的第二阶段引导代码）syslinux.cfg (syslinux的引导配置文件)bzImage 内核CDlinux.gz initrd文件压缩的修改启动参数\n启动时输入cdlinux CDL_DEV=/dev/sda\n或者修改syslinux.cfg 在CDlinux.gz后加入CDL_DEV=/dev/sda\n\n\n\n\n\n\n\n        \n  ','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(149,'在PHP中使用SCA','3373193720084222632526',2,'2008-05-22 02:06:32',NULL,'2008-05-22 02:06:32','2014-07-13 18:57:15','首先，安装WAMPServer2.0c(http://www.wampserver.com/en/download.php)，<br />然后下载SCA(http://pecl.php.net/package/SCA_SDO),把其中的SCA文件夹解压缩<br />然后，启动WAMP，修改php.ini，增加extension=php_sdo.dll 增加include_path=\".;&lt;SCA路径&gt;\" 例如<br />include_path=\".;D:\\programe\\wamp\\bin\\php\\php5.2.6\\includes\" <br />最后重启Apache即可<br />参考文档: http://www.osoa.org/display/PHP/SCA+Documentation<br />第一个例子(hi.php)，可以通过浏览器访问(http://localhost/hjz/hi.php?wsdl)：<br /><span style=\"color: rgb(255, 0, 0);\">注意&lt;?php 这里的php不能省略，class名字要和文件名一致(大小写可以不一致)</span><br />&lt;?php<br />include \"SCA/SCA.php\";<br />/**<br />&nbsp;* @service<br />&nbsp;* @binding.soap<br />&nbsp;*/<br />class hi {<br />&nbsp;&nbsp;&nbsp; /**<br />&nbsp;&nbsp;&nbsp; &nbsp;* @param string $user username<br />&nbsp;&nbsp;&nbsp; &nbsp;* @return string<br />&nbsp;&nbsp;&nbsp; &nbsp;*/<br />&nbsp;&nbsp;&nbsp; function sayHi($user){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return \"Hello, \".$user;<br />&nbsp;&nbsp;&nbsp; }<br />}<br />?&gt;<br />第一个例子客户端(sayhi.php)：<br />&lt;?php<br />include \"SCA/SCA.php\";<br />$user = \"hank\";<br />print \"User:\".$user.\"&lt;BR&gt;\";<br /><br />$hiService = SCA::getService(\'hi.php\');<br />$resp = $hiService-&gt;sayHi($user);<br />echo \"Response:\".$resp;<br />?&gt;<br />第二个例子：(ibm.php和hp.php分别提供两个service, hjz.php通过调用这两个service而实现一个service，hjzc.php调用hjz.php)<br />ibm.php<br />&lt;?php<br />include \"SCA/SCA.php\";<br /><br />/**<br />&nbsp;* IBM\'s stock<br />&nbsp;* @service<br />&nbsp;* @binding.soap<br />&nbsp;*/<br />class IBM{<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /**<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * @return float<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; */<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; function getQuote(){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return 300;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />}<br />?&gt;<br />hp.php<br />&lt;?php<br />include \"SCA/SCA.php\";<br /><br />/**<br />&nbsp;* HP\'s stock<br />&nbsp;* @service<br />&nbsp;* @binding.soap<br />&nbsp;*/<br />class HP{<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /**<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * @return float<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; */<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; function getQuote(){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return 200;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />}<br />?&gt;<br />hjz.php<br />&lt;?php<br />include \"SCA/SCA.php\";<br /><br />/**<br />&nbsp;* IBM/HP \'s stock<br />&nbsp;* @service<br />&nbsp;* @binding.soap<br />&nbsp;*/<br />class HJZ{<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /**<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * @reference<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * @binding.php ibm.php<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; */<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public $ibm;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /**<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * @reference<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * @binding.soap hp.wsdl<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; */<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public $hp;<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /**<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * @return float<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; */<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; function getQuote(){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $ibmQuote = $this-&gt;ibm-&gt;getQuote();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $hpQuote = $this-&gt;hp-&gt;getQuote();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return $ibmQuote / $hpQuote;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />}<br />?&gt;<br />hjzc.php<br />&lt;?php<br />include \"SCA/SCA.php\";<br /><br />$hjzService = SCA::getService(\"hjz.php\");<br />$stock = $hjzService-&gt;getQuote();<br />echo $stock;<br />?&gt;<br /><br />',1,1,1,0,0,0,'首先，安装WAMPServer2.0c(http://www.wampserver.com/en/download.php)，然后下载SCA(http://pecl.php.net/package/SCA_SDO),把其中的SCA文件夹解压缩然后，启动WAMP，修改php.ini，增加extension=php_sdo.dll 增加include_path=\".;&lt;SCA路径&gt;\" 例如include_path=\".;D:\\programe\\wamp\\bin\\php\\php5.2.6\\includes\" 最后重启Apache即可参考文档: http://www.osoa.org/display/PHP/SCA+Documentation第一个例子(hi.php)，可以通过浏览器访问(http://localhost/hjz/hi.php?wsdl)：注意&lt;?php 这里的php不能省略，class名字要和文件名一致(大小写可以不一致)&lt;?phpinclude \"SCA/SCA.php\";/**&nbsp;* @service&nbsp;* @binding.soap&nbsp;*/class hi {&nbsp;&nbsp;&nbsp; /**&nbsp;&nbsp;&nbsp; &nbsp;* @param string $user username&nbsp;&nbsp;&nbsp; &nbsp;* @return string&nbsp;&nbsp;&nbsp; &nbsp;*/&nbsp;&nbsp;&nbsp; function sayHi($user){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return \"Hello, \".$user;&nbsp;&nbsp;&nbsp; }}?&gt;第一个例子客户端(sayhi.php)：&lt;?phpinclude \"SCA/SCA.php\";$user = \"hank\";print \"User:\".$user.\"&lt;BR&gt;\";$hiService = SCA::getService(\'hi.php\');$resp = $hiService-&gt;sayHi($user);echo \"Response:\".$resp;?&gt;第二个例子：(ibm.php和hp.php分别提供两个service, hjz.php通过调用这两个service而实现一个service，hjzc.php调用hjz.php)ibm.php&lt;?phpinclude \"SCA/SCA.php\";/**&nbsp;* IBM\'s stock&nbsp;* @service&nbsp;* @binding.soap&nbsp;*/class IBM{&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /**&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * @return float&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; */&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; function getQuote(){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return 300;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }}?&gt;hp.php&lt;?phpinclude \"SCA/SCA.php\";/**&nbsp;* HP\'s stock&nbsp;* @service&nbsp;* @binding.soap&nbsp;*/class HP{&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /**&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * @return float&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; */&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; function getQuote(){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return 200;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }}?&gt;hjz.php&lt;?phpinclude \"SCA/SCA.php\";/**&nbsp;* IBM/HP \'s stock&nbsp;* @service&nbsp;* @binding.soap&nbsp;*/class HJZ{&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /**&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * @reference&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * @binding.php ibm.php&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; */&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public $ibm;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /**&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * @reference&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * @binding.soap hp.wsdl&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; */&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public $hp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /**&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * @return float&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; */&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; function getQuote(){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $ibmQuote = $this-&gt;ibm-&gt;getQuote();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $hpQuote = $this-&gt;hp-&gt;getQuote();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return $ibmQuote / $hpQuote;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }}?&gt;hjzc.php&lt;?phpinclude \"SCA/SCA.php\";$hjzService = SCA::getService(\"hjz.php\");$stock = $hjzService-&gt;getQuote();echo $stock;?&gt;','',0,'\"web skills\"',0,'','zinnia/_entry_detail.html','entry_detail.html'),(150,'农历和阳历相互转换-C语言','337319372009111004626247',2,'2009-12-10 12:46:26',NULL,'2009-12-10 12:46:26','2014-07-13 18:57:15','<p>参考 <a href=\"http://blog.chinaunix.net/u/25381/showart_199996.html#include\" rel=\"nofollow\">http://blog.chinaunix.net/u/25381/showart_199996.html#include</a> </p>  <p>&lt;stdio.h&gt;<br />#include &lt;string.h&gt;</p>  <p>typedef struct _hjz{<br />&nbsp;&nbsp;&nbsp; int year;<br />&nbsp;&nbsp;&nbsp; int month;<br />&nbsp;&nbsp;&nbsp; int day;<br />&nbsp;&nbsp;&nbsp; int reserved;<br />} hjz;<br />unsigned int lunar200y[199] = {<br />&nbsp;&nbsp;&nbsp; 0x04AE53,0x0A5748,0x5526BD,0x0D2650,0x0D9544,0x46AAB9,0x056A4D,0x09AD42,0x24AEB6,0x04AE4A,/*1901-1910*/<br />&nbsp;&nbsp;&nbsp; 0x6A4DBE,0x0A4D52,0x0D2546,0x5D52BA,0x0B544E,0x0D6A43,0x296D37,0x095B4B,0x749BC1,0x049754,/*1911-1920*/<br />&nbsp;&nbsp;&nbsp; 0x0A4B48,0x5B25BC,0x06A550,0x06D445,0x4ADAB8,0x02B64D,0x095742,0x2497B7,0x04974A,0x664B3E,/*1921-1930*/<br />&nbsp;&nbsp;&nbsp; 0x0D4A51,0x0EA546,0x56D4BA,0x05AD4E,0x02B644,0x393738,0x092E4B,0x7C96BF,0x0C9553,0x0D4A48,/*1931-1940*/&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; 0x6DA53B,0x0B554F,0x056A45,0x4AADB9,0x025D4D,0x092D42,0x2C95B6,0x0A954A,0x7B4ABD,0x06CA51,/*1941-1950*/<br />&nbsp;&nbsp;&nbsp; 0x0B5546,0x555ABB,0x04DA4E,0x0A5B43,0x352BB8,0x052B4C,0x8A953F,0x0E9552,0x06AA48,0x6AD53C,/*1951-1960*/<br />&nbsp;&nbsp;&nbsp; 0x0AB54F,0x04B645,0x4A5739,0x0A574D,0x052642,0x3E9335,0x0D9549,0x75AABE,0x056A51,0x096D46,/*1961-1970*/<br />&nbsp;&nbsp;&nbsp; 0x54AEBB,0x04AD4F,0x0A4D43,0x4D26B7,0x0D254B,0x8D52BF,0x0B5452,0x0B6A47,0x696D3C,0x095B50,/*1971-1980*/<br />&nbsp;&nbsp;&nbsp; 0x049B45,0x4A4BB9,0x0A4B4D,0xAB25C2,0x06A554,0x06D449,0x6ADA3D,0x0AB651,0x093746,0x5497BB,/*1981-1990*/<br />&nbsp;&nbsp;&nbsp; 0x04974F,0x064B44,0x36A537,0x0EA54A,0x86B2BF,0x05AC53,0x0AB647,0x5936BC,0x092E50,0x0C9645,/*1991-2000*/<br />&nbsp;&nbsp;&nbsp; 0x4D4AB8,0x0D4A4C,0x0DA541,0x25AAB6,0x056A49,0x7AADBD,0x025D52,0x092D47,0x5C95BA,0x0A954E,/*2001-2010*/<br />&nbsp;&nbsp;&nbsp; 0x0B4A43,0x4B5537,0x0AD54A,0x955ABF,0x04BA53,0x0A5B48,0x652BBC,0x052B50,0x0A9345,0x474AB9,/*2011-2020*/<br />&nbsp;&nbsp;&nbsp; 0x06AA4C,0x0AD541,0x24DAB6,0x04B64A,0x69573D,0x0A4E51,0x0D2646,0x5E933A,0x0D534D,0x05AA43,/*2021-2030*/<br />&nbsp;&nbsp;&nbsp; 0x36B537,0x096D4B,0xB4AEBF,0x04AD53,0x0A4D48,0x6D25BC,0x0D254F,0x0D5244,0x5DAA38,0x0B5A4C,/*2031-2040*/<br />&nbsp;&nbsp;&nbsp; 0x056D41,0x24ADB6,0x049B4A,0x7A4BBE,0x0A4B51,0x0AA546,0x5B52BA,0x06D24E,0x0ADA42,0x355B37,/*2041-2050*/<br />&nbsp;&nbsp;&nbsp; 0x09374B,0x8497C1,0x049753,0x064B48,0x66A53C,0x0EA54F,0x06B244,0x4AB638,0x0AAE4C,0x092E42,/*2051-2060*/<br />&nbsp;&nbsp;&nbsp; 0x3C9735,0x0C9649,0x7D4ABD,0x0D4A51,0x0DA545,0x55AABA,0x056A4E,0x0A6D43,0x452EB7,0x052D4B,/*2061-2070*/<br />&nbsp;&nbsp;&nbsp; 0x8A95BF,0x0A9553,0x0B4A47,0x6B553B,0x0AD54F,0x055A45,0x4A5D38,0x0A5B4C,0x052B42,0x3A93B6,/*2071-2080*/<br />&nbsp;&nbsp;&nbsp; 0x069349,0x7729BD,0x06AA51,0x0AD546,0x54DABA,0x04B64E,0x0A5743,0x452738,0x0D264A,0x8E933E,/*2081-2090*/<br />&nbsp;&nbsp;&nbsp; 0x0D5252,0x0DAA47,0x66B53B,0x056D4F,0x04AE45,0x4A4EB9,0x0A4D4C,0x0D1541,0x2D92B5&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /*2091-2099*/<br />};</p>  <p>int monthTotal[13] = {0,31,59,90,120,151,181,212,243,273,304,334,365};<br />hjz toSolar(hjz lunar){<br />&nbsp;&nbsp;&nbsp; int year = lunar.year,<br />&nbsp;month = lunar.month,<br />&nbsp;day = lunar.day;<br />&nbsp;&nbsp;&nbsp; int byNow, xMonth, i;<br />&nbsp;&nbsp;&nbsp; hjz solar;<br />&nbsp;&nbsp;&nbsp; byNow = (lunar200y[year-1901] &amp; 0x001F) - 1;<br />&nbsp;&nbsp;&nbsp; if( ((lunar200y[year-1901]&amp;0x0060)&gt;&gt;5) == 2)<br />&nbsp;byNow += 31;<br />&nbsp;&nbsp;&nbsp; for(i = 1; i &lt; month; i ++){<br />&nbsp;if( ( lunar200y[year - 1901] &amp; (0x80000 &gt;&gt; (i-1)) ) ==0){<br />&nbsp;&nbsp;&nbsp;&nbsp; byNow += 29;<br />&nbsp;}<br />&nbsp;else<br />&nbsp;&nbsp;&nbsp;&nbsp; byNow += 30;<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; byNow += day;<br />&nbsp;&nbsp;&nbsp; xMonth = (lunar200y[year - 1901] &amp; 0xf00000)&gt;&gt;20;<br />&nbsp;&nbsp;&nbsp; if(xMonth != 0){<br />&nbsp;if(month &gt; xMonth<br />&nbsp;&nbsp;&nbsp;&nbsp; ||(month==xMonth &amp;&amp; lunar.reserved == 1)){<br />&nbsp;&nbsp;&nbsp;&nbsp; if((lunar200y[year-1901] &amp; (0x80000&gt;&gt;(month-1)))==0)<br />&nbsp;&nbsp;byNow += 29;<br />&nbsp;&nbsp;&nbsp;&nbsp; else <br />&nbsp;&nbsp;byNow += 30;<br />&nbsp;}<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; if(byNow &gt; 366<br />&nbsp;&nbsp;&nbsp;&nbsp; ||(year%4!=0 &amp;&amp; byNow == 365)){<br />&nbsp;year += 1;<br />&nbsp;if(year%4==0)<br />&nbsp;&nbsp;&nbsp;&nbsp; byNow -= 366;<br />&nbsp;else<br />&nbsp;&nbsp;&nbsp;&nbsp; byNow -= 365;<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; for(i=1; i &lt;= 13; i ++){<br />&nbsp;if(monthTotal[i] &gt;= byNow){<br />&nbsp;&nbsp;&nbsp;&nbsp; month = i;<br />&nbsp;&nbsp;&nbsp;&nbsp; break;<br />&nbsp;}<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; solar.day = byNow - monthTotal[month-1];<br />&nbsp;&nbsp;&nbsp; solar.month = month;<br />&nbsp;&nbsp;&nbsp; solar.year = year;</p>  <p>&nbsp;&nbsp;&nbsp; return solar;<br />}<br />hjz toLunar(hjz solar){<br />&nbsp;&nbsp;&nbsp; int year = solar.year,<br />&nbsp;month = solar.month,<br />&nbsp;day = solar.day;<br />&nbsp;&nbsp;&nbsp; int bySpring,bySolar,daysPerMonth;<br />&nbsp;&nbsp;&nbsp; int index,flag;<br />&nbsp;&nbsp;&nbsp; hjz lunar;</p>  <p>&nbsp;&nbsp;&nbsp; //bySpring 记录春节离当年元旦的天数。<br />&nbsp;&nbsp;&nbsp; //bySolar 记录阳历日离当年元旦的天数。<br />&nbsp;&nbsp;&nbsp; if( ((lunar200y[year-1901] &amp; 0x0060) &gt;&gt; 5) == 1)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bySpring = (lunar200y[year-1901] &amp; 0x001F) - 1;<br />&nbsp;&nbsp;&nbsp; else<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bySpring = (lunar200y[year-1901] &amp; 0x001F) - 1 + 31;<br />&nbsp;&nbsp;&nbsp; bySolar = monthTotal[month-1] + day - 1;<br />&nbsp;&nbsp;&nbsp; if( (!(year % 4)) &amp;&amp; (month &gt; 2))<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bySolar++;<br />&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; //daysPerMonth记录大小月的天数 29 或30<br />&nbsp;&nbsp;&nbsp; //index 记录从哪个月开始来计算。<br />&nbsp;&nbsp;&nbsp; //flag 是用来对闰月的特殊处理。</p>  <p>&nbsp;&nbsp;&nbsp; //判断阳历日在春节前还是春节后<br />&nbsp;&nbsp;&nbsp; if (bySolar &gt;= bySpring) {//阳历日在春节后（含春节那天）<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bySolar -= bySpring;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; month = 1;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; index = 1;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; flag = 0;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if( ( lunar200y[year - 1901] &amp; (0x80000 &gt;&gt; (index-1)) ) ==0)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; daysPerMonth = 29;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; daysPerMonth = 30;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; while(bySolar &gt;= daysPerMonth) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bySolar -= daysPerMonth;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; index++;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(month == ((lunar200y[year - 1901] &amp; 0xF00000) &gt;&gt; 20) ) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; flag = ~flag;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(flag == 0)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; month++;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; month++;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if( ( lunar200y[year - 1901] &amp; (0x80000 &gt;&gt; (index-1)) ) ==0)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; daysPerMonth=29;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; daysPerMonth=30;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; day = bySolar + 1;<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; else {//阳历日在春节前<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bySpring -= bySolar;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; year--;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; month = 12;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ( ((lunar200y[year - 1901] &amp; 0xF00000) &gt;&gt; 20) == 0)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; index = 12;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; index = 13;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; flag = 0;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if( ( lunar200y[year - 1901] &amp; (0x80000 &gt;&gt; (index-1)) ) ==0)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; daysPerMonth = 29;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; daysPerMonth = 30;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; while(bySpring &gt; daysPerMonth) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bySpring -= daysPerMonth;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; index--;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(flag == 0)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; month--;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(month == ((lunar200y[year - 1901] &amp; 0xF00000) &gt;&gt; 20))<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; flag = ~flag;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if( ( lunar200y[year - 1901] &amp; (0x80000 &gt;&gt; (index-1)) ) ==0)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; daysPerMonth = 29;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;