&nbsp;&nbsp;&nbsp; if( ((lunar200y[year-1901]&amp;0x0060)&gt;&gt;5) == 2)&nbsp;byNow += 31;&nbsp;&nbsp;&nbsp; for(i = 1; i &lt; month; i ++){&nbsp;if( ( lunar200y[year - 1901] &amp; (0x80000 &gt;&gt; (i-1)) ) ==0){&nbsp;&nbsp;&nbsp;&nbsp; byNow += 29;&nbsp;}&nbsp;else&nbsp;&nbsp;&nbsp;&nbsp; byNow += 30;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; byNow += day;&nbsp;&nbsp;&nbsp; xMonth = (lunar200y[year - 1901] &amp; 0xf00000)&gt;&gt;20;&nbsp;&nbsp;&nbsp; if(xMonth != 0){&nbsp;if(month &gt; xMonth&nbsp;&nbsp;&nbsp;&nbsp; ||(month==xMonth &amp;&amp; lunar.reserved == 1)){&nbsp;&nbsp;&nbsp;&nbsp; if((lunar200y[year-1901] &amp; (0x80000&gt;&gt;(month-1)))==0)&nbsp;&nbsp;byNow += 29;&nbsp;&nbsp;&nbsp;&nbsp; else &nbsp;&nbsp;byNow += 30;&nbsp;}&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; if(byNow &gt; 366&nbsp;&nbsp;&nbsp;&nbsp; ||(year%4!=0 &amp;&amp; byNow == 365)){&nbsp;year += 1;&nbsp;if(year%4==0)&nbsp;&nbsp;&nbsp;&nbsp; byNow -= 366;&nbsp;else&nbsp;&nbsp;&nbsp;&nbsp; byNow -= 365;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; for(i=1; i &lt;= 13; i ++){&nbsp;if(monthTotal[i] &gt;= byNow){&nbsp;&nbsp;&nbsp;&nbsp; month = i;&nbsp;&nbsp;&nbsp;&nbsp; break;&nbsp;}&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; solar.day = byNow - monthTotal[month-1];&nbsp;&nbsp;&nbsp; solar.month = month;&nbsp;&nbsp;&nbsp; solar.year = year;  &nbsp;&nbsp;&nbsp; return solar;}hjz toLunar(hjz solar){&nbsp;&nbsp;&nbsp; int year = solar.year,&nbsp;month = solar.month,&nbsp;day = solar.day;&nbsp;&nbsp;&nbsp; int bySpring,bySolar,daysPerMonth;&nbsp;&nbsp;&nbsp; int index,flag;&nbsp;&nbsp;&nbsp; hjz lunar;  &nbsp;&nbsp;&nbsp; //bySpring 记录春节离当年元旦的天数。&nbsp;&nbsp;&nbsp; //bySolar 记录阳历日离当年元旦的天数。&nbsp;&nbsp;&nbsp; if( ((lunar200y[year-1901] &amp; 0x0060) &gt;&gt; 5) == 1)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bySpring = (lunar200y[year-1901] &amp; 0x001F) - 1;&nbsp;&nbsp;&nbsp; else&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bySpring = (lunar200y[year-1901] &amp; 0x001F) - 1 + 31;&nbsp;&nbsp;&nbsp; bySolar = monthTotal[month-1] + day - 1;&nbsp;&nbsp;&nbsp; if( (!(year % 4)) &amp;&amp; (month &gt; 2))&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bySolar++;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; //daysPerMonth记录大小月的天数 29 或30&nbsp;&nbsp;&nbsp; //index 记录从哪个月开始来计算。&nbsp;&nbsp;&nbsp; //flag 是用来对闰月的特殊处理。  &nbsp;&nbsp;&nbsp; //判断阳历日在春节前还是春节后&nbsp;&nbsp;&nbsp; if (bySolar &gt;= bySpring) {//阳历日在春节后（含春节那天）&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bySolar -= bySpring;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; month = 1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; index = 1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; flag = 0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if( ( lunar200y[year - 1901] &amp; (0x80000 &gt;&gt; (index-1)) ) ==0)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; daysPerMonth = 29;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; daysPerMonth = 30;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; while(bySolar &gt;= daysPerMonth) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bySolar -= daysPerMonth;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; index++;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(month == ((lunar200y[year - 1901] &amp; 0xF00000) &gt;&gt; 20) ) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; flag = ~flag;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(flag == 0)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; month++;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; month++;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if( ( lunar200y[year - 1901] &amp; (0x80000 &gt;&gt; (index-1)) ) ==0)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; daysPerMonth=29;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; daysPerMonth=30;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; day = bySolar + 1;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; else {//阳历日在春节前&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bySpring -= bySolar;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; year--;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; month = 12;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ( ((lunar200y[year - 1901] &amp; 0xF00000) &gt;&gt; 20) == 0)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; index = 12;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; index = 13;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; flag = 0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if( ( lunar200y[year - 1901] &amp; (0x80000 &gt;&gt; (index-1)) ) ==0)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; daysPerMonth = 29;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; daysPerMonth = 30;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; while(bySpring &gt; daysPerMonth) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bySpring -= daysPerMonth;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; index--;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(flag == 0)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; month--;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(month == ((lunar200y[year - 1901] &amp; 0xF00000) &gt;&gt; 20))&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; flag = ~flag;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if( ( lunar200y[year - 1901] &amp; (0x80000 &gt;&gt; (index-1)) ) ==0)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; daysPerMonth = 29;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; daysPerMonth = 30;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; day = daysPerMonth - bySpring + 1;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; lunar.day = day;&nbsp;&nbsp;&nbsp; lunar.month = month;&nbsp;&nbsp;&nbsp; lunar.year = year;&nbsp;&nbsp;&nbsp; if(month == ((lunar200y[year - 1901] &amp; 0xF00000) &gt;&gt; 20))&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; lunar.reserved = 1;&nbsp;&nbsp;&nbsp; else&nbsp;lunar.reserved = 0;&nbsp;&nbsp;&nbsp; return lunar;}  int main(int argc, char*argv[]){&nbsp;&nbsp;&nbsp; const char *ChDay[] = {\"*\",\"初一\",\"初二\",\"初三\",\"初四\",\"初五\",&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"初六\",\"初七\",\"初八\",\"初九\",\"初十\",&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"十一\",\"十二\",\"十三\",\"十四\",\"十五\",&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"十六\",\"十七\",\"十八\",\"十九\",\"二十\",&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"廿一\",\"廿二\",\"廿三\",\"廿四\",\"廿五\",&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"廿六\",\"廿七\",\"廿八\",\"廿九\",\"三十\"};&nbsp;&nbsp;&nbsp; const char *ChMonth[] = {\"*\",\"正\",\"二\",\"三\",\"四\",\"五\",\"六\",\"七\",\"八\",\"九\",\"十\",\"十一\",\"腊\"};&nbsp;&nbsp;&nbsp; long t;&nbsp;&nbsp;&nbsp; int year,month,day;&nbsp;&nbsp;&nbsp; char str[13] = \"\";&nbsp;&nbsp;&nbsp; hjz osolar={&nbsp;.year = 2010,&nbsp;.month = 2,&nbsp;.day = 14,&nbsp;&nbsp;&nbsp; };&nbsp;&nbsp;&nbsp;&nbsp; hjz lunar = toLunar(osolar);&nbsp;&nbsp;&nbsp; hjz olunar = {&nbsp;.year = 2009,&nbsp;.month&nbsp; = 5,&nbsp;.day = 17,&nbsp;.reserved=1, //1表示闰月,0表示平月&nbsp;&nbsp;&nbsp; };  &nbsp;&nbsp;&nbsp; hjz solar = toSolar(olunar);  &nbsp;&nbsp;&nbsp; printf(\"Solar: %d-%d-%d\\n\",osolar.year,osolar.month,osolar.day);&nbsp;printf(\"=&gt;Lunar: %d-%d-%d\\n\",lunar.year,lunar.month,lunar.day);&nbsp;&nbsp;&nbsp; printf(\"Lunar: %d-%d-%d\\n\",olunar.year,olunar.month,olunar.day);&nbsp;&nbsp;&nbsp; printf(\"=&gt;Solar: %d-%d-%d\\n\",solar.year,solar.month,solar.day);&nbsp;&nbsp;&nbsp; return 0;}','',0,'cc',0,'','zinnia/_entry_detail.html','entry_detail.html'),(151,'python的类库加载机制','3373193720096218432287',2,'2009-07-21 20:04:32',NULL,'2009-07-21 20:04:32','2014-07-13 18:57:15','打印机管理窗口不能打开，从命令行下打开时发现错误，原来gtk-2.0的路径没有在sys.path下，于是查看python的类库加载机制，最后发现是/usr/lib/python2.5/site-packages/python-support.pth中缺少两行<br />/var/lib/python-support/python2.5<br />gtk-2.0<br />/var/lib/python-support/python2.5/gtk-2.0<br />想起来是当时为了解决一个python冲突问题，从一个没有GUI的电脑上拷贝的python文件。于是加上这两行，问题解决。<br /><br />1.查看Python从哪里加载类库<br />python<br />import sys<br />sys.path<br />2. 查看某个模块的文件路径<br />import urllib<br />urllib.__file__<br />3. python的类库加载机制<br />python -v<br />首先加载site.py<br />在site.py中， 首先加入site-packages，然后读该目录下的pth文件<br />',1,1,1,0,0,0,'打印机管理窗口不能打开，从命令行下打开时发现错误，原来gtk-2.0的路径没有在sys.path下，于是查看python的类库加载机制，最后发现是/usr/lib/python2.5/site-packages/python-support.pth中缺少两行/var/lib/python-support/python2.5gtk-2.0/var/lib/python-support/python2.5/gtk-2.0想起来是当时为了解决一个python冲突问题，从一个没有GUI的电脑上拷贝的python文件。于是加上这两行，问题解决。1.查看Python从哪里加载类库pythonimport syssys.path2. 查看某个模块的文件路径import urlliburllib.__file__3. python的类库加载机制python -v首先加载site.py在site.py中， 首先加入site-packages，然后读该目录下的pth文件','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(152,'Acegi设计概述[转]','3373193720071019115637612',2,'2007-11-19 23:56:37',NULL,'2007-11-19 23:56:37','2014-07-13 18:57:15','<h2>1. 简单叙述认证授权流程</h2>  <p>第一步, 认证(authentication)。Acegi调用AuthenticationManager(认证管理器)来对当前请求登陆系统用户进行验证，AuthenticationManager负责委托一个或多个Provider(认证提供者)，并逐一遍历每个Provider，直到某一个Provider能够成功的验证用户的身份。Provider成功验证用户身份后，会返回一个Authentication(证明), Authentication中包括Principal(用户名)，Credentials(通常是密码)，Authorities(该用户所拥有的权限)。这样就完成了身份验证的步骤。</p>  <p>第二步，授权(authorization)。当Acegi获得Authentication后，也就确定了用户的身份。每当用户请求访问某受保护资源时，Acegi会调用AccessDecisionManager(访问决策管理器)来决定用户的Authentication是否有恰当的权限来访问当前访问的受保护资源，有则授权用户通过，无则抛出错误信息，以达到访问控制的目的。</p>  <h2><a rel=\"nofollow\"></a>&nbsp;2. 安全保护方式</h2>  <ul>  <li>保护Web应用程序<br />当去保护Web应用程序时，Acegi使用Servlet Filter来拦截Servlet请求，并将这些请求转给AuthenticationManager，AccessDecisionManager或其他管理器来处理。 <br clear=\"all\" />Acegi提供了多个各种功能的Filter, 这些Filter按照先后顺序组成了一个 Filter Chain。当一个请求被提交道一个由Acegi所保护的Web应用程序时，该请求会按照如下顺序逐一通过Filter Chain： <br clear=\"all\" />&nbsp; <br clear=\"all\" />第一步， 请求通过ChannelProcessingFilter(通道处理过滤器，可选)。该过滤器负责检查当前请求的Channel，并判断是否已满足安全需要。如果不满足，则由非安全的通道传输(Http)重定向于安全的通道传输(Https)，以确保服务器与浏览器之间传输的数据加密。 <br clear=\"all\" />&nbsp; <br clear=\"all\" />第二步，请求通过 AuthenticationProcessingFilter(认证处理过滤器)。该过滤器会判断该请求是否是一个认证请求(通常是\"/j_acegi_security_check\")。如果是，则它会从请求中获取用户名和密码，并转交给AuthenticationManager来认证用户身份。如果不是，则会直接转到下一个Filter。 <br clear=\"all\" />&nbsp; <br clear=\"all\" />第三步，请求通过HttpSessionContextIntegrationFilter(HttpSession安全上下文信息集成过滤器)。该过滤器负责将Authentication对象保存在HttpSession中，使其在下一个请求到来时仍可被获取。故Authentication能跨越多个请求。 <br clear=\"all\" />&nbsp; <br clear=\"all\" />第四步，请求通过FilterSecurityInterceptor(过滤器安全拦截器)。该过滤器会首先调用AuthenticationManager判断用户是否已登陆认证，如还没认证成功，则重定向到登陆界面。认证成功，则并从Authentication中获取用户的权限。然后从objectDefinitionSource属性获取各种URL资源所对应的权限。最后调用AccessDecisionManager来判断用户所拥有的权限与当前受保华的URL资源所对应的权限是否相匹配。如果匹配失败，则返回403错误(禁止访问)给用户。匹配成功则用户可以访问受保护的URL资源。   </li><li>&nbsp;保护方法调用<br />当去保护方法调用时，Acegi使用Spring AOP,将\"切面\"应用于所代理的对象，以确保用户只有在拥有恰当授权时才可以调用受保护的方法。<br />MethodSecurityInterceptor(方法调用安全拦截器)同样也是在调用方法之前，先调用AuthenticationManager判断用户身份是否已验证，然后从objectDefinitionSource中获取方法所对应的权限，再调用AccessDecisionManager来匹配用户权限和方法对应的权限。如果用户没有足够权限调用当前方法，则抛出AccessDeniedException使方法不能被调用。 </li></ul>  <h2><a rel=\"nofollow\"></a>&nbsp;3.过滤器链代理</h2>  <p>Acegi的FilterChainProxy提供了一种很好的方式，使你不需要在web.xml配置Filters,而在Spring配置文件中加入, 从而能充分利用Spring IOC的优势。</p>  <p>FilterChainProxy其实也只是一个扩展后的Filter，它负责委托Spring中的各个实现了javax.servlet.Filter接口的Bean来执行过滤功能。 如httpSessionContextIntegrationFilter， authenticationProcessingFilter， filterInvocationInterceptor等。</p>  <p>在web.xml中的配置FilterToBeanProxy:</p>  <p style=\"COLOR: #99ccff; BACKGROUND-COLOR: #ffffff;\"><font color=\"#008080\"><span style=\"COLOR: #99ccff;\"><font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">filter</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">filter-name</font><font color=\"#008080\">&gt;</font><font color=\"#000000\">Acegi Filter Chain Proxy</font><font color=\"#008080\">&lt;/</font><font color=\"#3f7f7f\">filter-name</font><font color=\"#008080\">&gt;|</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">filter-class</font><font color=\"#008080\">&gt;</font><font color=\"#000000\">org.acegisecurity.util.FilterToBeanProxy</font><font color=\"#008080\">&lt;/</font><font color=\"#3f7f7f\">filter-class</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">init-param</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">param-name</font><font color=\"#008080\">&gt;</font><font color=\"#000000\">targetClass</font><font color=\"#008080\">&lt;/</font><font color=\"#3f7f7f\">param-name</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">param-value</font><font color=\"#008080\">&gt;</font><font color=\"#000000\">org.acegisecurity.util.FilterChainProxy</font><font color=\"#008080\">&lt;/</font><font color=\"#3f7f7f\">param-value</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;/</font><font color=\"#3f7f7f\">init-param</font><font color=\"#008080\">&gt;</font><br /><font color=\"#008080\">&lt;/</font><font color=\"#3f7f7f\">filter</font><font color=\"#008080\">&gt;</font><br /><font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">filter-mapping</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">filter-name</font><font color=\"#008080\">&gt;</font><font color=\"#000000\">Acegi Filter Chain Proxy</font><font color=\"#008080\">&lt;/</font><font color=\"#3f7f7f\">filter-name</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">url-pattern</font><font color=\"#008080\">&gt;</font><font color=\"#000000\">/*</font><font color=\"#008080\">&lt;/</font><font color=\"#3f7f7f\">url-pattern</font><font color=\"#008080\">&gt;</font><br /><font color=\"#008080\">&lt;/</font><font color=\"#3f7f7f\">filter-mapping</font><font color=\"#008080\">&gt;</font></span></font></p>  <p>在Spring配置文件中相应的FilterChainProxy Bean：</p>  <p style=\"BACKGROUND-COLOR: #ffffff;\"><font color=\"#008080\"><span style=\"COLOR: #99ccff;\"><font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">bean</font> <font color=\"#7f007f\">id</font><font color=\"#000000\">=</font><font color=\"#2a00ff\">\"filterChainProxy\"</font><font color=\"#2a00ff\">&nbsp;</font> &nbsp;<font color=\"#7f007f\">class</font><font color=\"#000000\">=</font><font color=\"#2a00ff\">\"org.acegisecurity.util.FilterChainProxy\"</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">property</font> <font color=\"#7f007f\">name</font><font color=\"#000000\">=</font><font color=\"#2a00ff\">\"filterInvocationDefinitionSource\"</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">value</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#000000\">CONVERT_URL_TO_LOWERCASE_BEFORE_COMPARISON</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#000000\">PATTERN_TYPE_APACHE_ANT</font><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<font color=\"#000000\">/**=httpSessionContextIntegrationFilter,logoutFilter,authenticationProcessingFilter,securityContextHolderAwareRequestFilter, rememberMeProcessingFilter,anonymousProcessingFilter,exceptionTranslationFilter,filterInvocationInterceptor</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;/</font><font color=\"#3f7f7f\">value</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;/</font><font color=\"#3f7f7f\">property</font><font color=\"#008080\">&gt;</font><br /><font color=\"#008080\">&lt;/</font><font color=\"#3f7f7f\">bean</font><font color=\"#008080\">&gt;</font></span></font></p>  <h2><a rel=\"nofollow\"></a>4. Acegi Exceptions 管理机制</h2>  <p>Acegi的异常管理机制做得不错，基本上可以满足权限管理需求。在其结构基础上继承实现自己的异常类也是很方便的。</p>  <p>所有Acegi的异常都是在 AcegiSecurityException 上继承而来的，其中最主要的是 AuthenticationException 和 AccessDeniedException。</p>  <p>这里需要注意的是 AcegiSecurityException 继承了Spring的 NestedRuntimeException，而NestedRuntimeException又是继承RuntimeException的。<br />所以抛出的时候不需要throws,&nbsp;而捕捉的时候需要专门catch RuntimeException。</p>  <p>AuthenticationException 是所有授权相关的异常的父类，当用户身份验证失败时就会抛出这个异常。</p>  <p>AccessDeniedException 是所有认证相关的异常的父类，当资源被拒绝访问时就会抛出这个异常。</p>  <p><img align=\"absMiddle\" alt=\"\" border=\"0\" src=\"http://wiki.springside.org.cn/download/attachments/1824/Acegi%20Exceptions%20Uml.JPG\" title=\"Acegi设计概述[转] - hankjin - 云水居\" /></p>  <p>在Acegi 中，通过ExceptionTranslationFilter(异常转换过滤器)来对各种异常进行捕获和处理，并重定向到不同的错误信息显示页面。</p>  <p>如遇到AuthenticationException 就会重定向到登陆页面，遇到AccessDeniedException 就会无权访问资源页面。你还可以继承ExceptionTranslationFilter基础上覆盖handleException方法来实现你自己的系统中异常的处理。</p>  <p style=\"COLOR: #99ccff;\"><span style=\"BACKGROUND-COLOR: #ffffff;\">&lt;<font color=\"#3f7f7f\">bean</font> <font color=\"#7f007f\">id</font>=<font color=\"#2a00ff\">\"exceptionTranslationFilter\"</font> <font color=\"#7f007f\">class</font>=<font color=\"#2a00ff\">\"org.acegisecurity.ui.ExceptionTranslationFilter\"</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">property</font> <font color=\"#7f007f\">name</font>=<font color=\"#2a00ff\">\"authenticationEntryPoint\"</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">bean</font> <font color=\"#7f007f\">class</font>=<font color=\"#2a00ff\">\"org.acegisecurity.ui.webapp.AuthenticationProcessingFilterEntryPoint\"</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">property</font> <font color=\"#7f007f\">name</font>=<font color=\"#2a00ff\">\"loginFormUrl\"</font> <font color=\"#7f007f\">value</font>=<font color=\"#2a00ff\">\"/login.jsp\"</font> <font color=\"#008080\">/&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">property</font> <font color=\"#7f007f\">name</font>=<font color=\"#2a00ff\">\"forceHttps\"</font> <font color=\"#7f007f\">value</font>=<font color=\"#2a00ff\">\"false\"</font> <font color=\"#008080\">/&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;/</font><font color=\"#3f7f7f\">bean</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;/</font><font color=\"#3f7f7f\">property</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">property</font> <font color=\"#7f007f\">name</font>=<font color=\"#2a00ff\">\"accessDeniedHandler\"</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">bean</font> <font color=\"#7f007f\">class</font>=<font color=\"#2a00ff\">\"org.acegisecurity.ui.AccessDeniedHandlerImpl\"</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;</font><font color=\"#3f7f7f\">property</font> <font color=\"#7f007f\">name</font>=<font color=\"#2a00ff\">\"errorPage\"</font> <font color=\"#7f007f\">value</font>=<font color=\"#2a00ff\">\"/accessDenied.jsp\"</font> <font color=\"#008080\">/&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;/</font><font color=\"#3f7f7f\">bean</font><font color=\"#008080\">&gt;</font><br /><font color=\"#000000\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font> <font color=\"#008080\">&lt;/</font><font color=\"#3f7f7f\">property</font><font color=\"#008080\">&gt;</font><br /><font color=\"#008080\">&lt;/</font><font color=\"#3f7f7f\">bean</font></span></p>',1,1,1,0,0,0,'1. 简单叙述认证授权流程  第一步, 认证(authentication)。Acegi调用AuthenticationManager(认证管理器)来对当前请求登陆系统用户进行验证，AuthenticationManager负责委托一个或多个Provider(认证提供者)，并逐一遍历每个Provider，直到某一个Provider能够成功的验证用户的身份。Provider成功验证用户身份后，会返回一个Authentication(证明), Authentication中包括Principal(用户名)，Credentials(通常是密码)，Authorities(该用户所拥有的权限)。这样就完成了身份验证的步骤。  第二步，授权(authorization)。当Acegi获得Authentication后，也就确定了用户的身份。每当用户请求访问某受保护资源时，Acegi会调用AccessDecisionManager(访问决策管理器)来决定用户的Authentication是否有恰当的权限来访问当前访问的受保护资源，有则授权用户通过，无则抛出错误信息，以达到访问控制的目的。  &nbsp;2. 安全保护方式    保护Web应用程序当去保护Web应用程序时，Acegi使用Servlet Filter来拦截Servlet请求，并将这些请求转给AuthenticationManager，AccessDecisionManager或其他管理器来处理。 Acegi提供了多个各种功能的Filter, 这些Filter按照先后顺序组成了一个 Filter Chain。当一个请求被提交道一个由Acegi所保护的Web应用程序时，该请求会按照如下顺序逐一通过Filter Chain： &nbsp; 第一步， 请求通过ChannelProcessingFilter(通道处理过滤器，可选)。该过滤器负责检查当前请求的Channel，并判断是否已满足安全需要。如果不满足，则由非安全的通道传输(Http)重定向于安全的通道传输(Https)，以确保服务器与浏览器之间传输的数据加密。 &nbsp; 第二步，请求通过 AuthenticationProcessingFilter(认证处理过滤器)。该过滤器会判断该请求是否是一个认证请求(通常是\"/j_acegi_security_check\")。如果是，则它会从请求中获取用户名和密码，并转交给AuthenticationManager来认证用户身份。如果不是，则会直接转到下一个Filter。 &nbsp; 第三步，请求通过HttpSessionContextIntegrationFilter(HttpSession安全上下文信息集成过滤器)。该过滤器负责将Authentication对象保存在HttpSession中，使其在下一个请求到来时仍可被获取。故Authentication能跨越多个请求。 &nbsp; 第四步，请求通过FilterSecurityInterceptor(过滤器安全拦截器)。该过滤器会首先调用AuthenticationManager判断用户是否已登陆认证，如还没认证成功，则重定向到登陆界面。认证成功，则并从Authentication中获取用户的权限。然后从objectDefinitionSource属性获取各种URL资源所对应的权限。最后调用AccessDecisionManager来判断用户所拥有的权限与当前受保华的URL资源所对应的权限是否相匹配。如果匹配失败，则返回403错误(禁止访问)给用户。匹配成功则用户可以访问受保护的URL资源。   &nbsp;保护方法调用当去保护方法调用时，Acegi使用Spring AOP,将\"切面\"应用于所代理的对象，以确保用户只有在拥有恰当授权时才可以调用受保护的方法。MethodSecurityInterceptor(方法调用安全拦截器)同样也是在调用方法之前，先调用AuthenticationManager判断用户身份是否已验证，然后从objectDefinitionSource中获取方法所对应的权限，再调用AccessDecisionManager来匹配用户权限和方法对应的权限。如果用户没有足够权限调用当前方法，则抛出AccessDeniedException使方法不能被调用。   &nbsp;3.过滤器链代理  Acegi的FilterChainProxy提供了一种很好的方式，使你不需要在web.xml配置Filters,而在Spring配置文件中加入, 从而能充分利用Spring IOC的优势。  FilterChainProxy其实也只是一个扩展后的Filter，它负责委托Spring中的各个实现了javax.servlet.Filter接口的Bean来执行过滤功能。 如httpSessionContextIntegrationFilter， authenticationProcessingFilter， filterInvocationInterceptor等。  在web.xml中的配置FilterToBeanProxy:  &lt;filter&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;filter-name&gt;Acegi Filter Chain Proxy&lt;/filter-name&gt;|&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;filter-class&gt;org.acegisecurity.util.FilterToBeanProxy&lt;/filter-class&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;init-param&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;param-name&gt;targetClass&lt;/param-name&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;param-value&gt;org.acegisecurity.util.FilterChainProxy&lt;/param-value&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/init-param&gt;&lt;/filter&gt;&lt;filter-mapping&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;filter-name&gt;Acegi Filter Chain Proxy&lt;/filter-name&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;url-pattern&gt;/*&lt;/url-pattern&gt;&lt;/filter-mapping&gt;  在Spring配置文件中相应的FilterChainProxy Bean：  &lt;bean id=\"filterChainProxy\"&nbsp; &nbsp;class=\"org.acegisecurity.util.FilterChainProxy\"&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;property name=\"filterInvocationDefinitionSource\"&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;value&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CONVERT_URL_TO_LOWERCASE_BEFORE_COMPARISON&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; PATTERN_TYPE_APACHE_ANT&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/**=httpSessionContextIntegrationFilter,logoutFilter,authenticationProcessingFilter,securityContextHolderAwareRequestFilter, rememberMeProcessingFilter,anonymousProcessingFilter,exceptionTranslationFilter,filterInvocationInterceptor&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/value&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/property&gt;&lt;/bean&gt;  4. Acegi Exceptions 管理机制  Acegi的异常管理机制做得不错，基本上可以满足权限管理需求。在其结构基础上继承实现自己的异常类也是很方便的。  所有Acegi的异常都是在 AcegiSecurityException 上继承而来的，其中最主要的是 AuthenticationException 和 AccessDeniedException。  这里需要注意的是 AcegiSecurityException 继承了Spring的 NestedRuntimeException，而NestedRuntimeException又是继承RuntimeException的。所以抛出的时候不需要throws,&nbsp;而捕捉的时候需要专门catch RuntimeException。  AuthenticationException 是所有授权相关的异常的父类，当用户身份验证失败时就会抛出这个异常。  AccessDeniedException 是所有认证相关的异常的父类，当资源被拒绝访问时就会抛出这个异常。    在Acegi 中，通过ExceptionTranslationFilter(异常转换过滤器)来对各种异常进行捕获和处理，并重定向到不同的错误信息显示页面。  如遇到AuthenticationException 就会重定向到登陆页面，遇到AccessDeniedException 就会无权访问资源页面。你还可以继承ExceptionTranslationFilter基础上覆盖handleException方法来实现你自己的系统中异常的处理。  &lt;bean id=\"exceptionTranslationFilter\" class=\"org.acegisecurity.ui.ExceptionTranslationFilter\"&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;property name=\"authenticationEntryPoint\"&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;bean class=\"org.acegisecurity.ui.webapp.AuthenticationProcessingFilterEntryPoint\"&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;property name=\"loginFormUrl\" value=\"/login.jsp\" /&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;property name=\"forceHttps\" value=\"false\" /&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/bean&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/property&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;property name=\"accessDeniedHandler\"&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;bean class=\"org.acegisecurity.ui.AccessDeniedHandlerImpl\"&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;property name=\"errorPage\" value=\"/accessDenied.jsp\" /&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/bean&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/property&gt;&lt;/bean','',0,'j2ee',0,'','zinnia/_entry_detail.html','entry_detail.html'),(153,'xDOM跨平台之innerHTML&amp;innerhtml','337319372008326111620119',2,'2008-04-26 11:16:20',NULL,'2008-04-26 11:16:20','2014-07-13 18:57:15','IE和MF的XMLDOM有些不同，这里有个跨平台的库xDOM.js和detectBrowser.js，可以在google的codesearch里找到。<br />下面是一个使用XMLDOM的例子，需要注意的是，innerhtml和innerHTML不同。<br />一个xml+两个xslt+1个html<br /><span style=\"text-decoration: underline; font-weight: bold; font-style: italic;\">contacts.xml</span><br />&lt;?xml version=\"1.0\" encoding=\"UTF-8\"?&gt;<br />&lt;!-- This XML document describes a contacts list --&gt;<br />&lt;contacts&gt;<br />&nbsp;&nbsp;&nbsp; &lt;person id=\"9407001\" type=\"supplier\"&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;first_name&gt;John&lt;/first_name&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;last_name&gt;Smith&lt;/last_name&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;company&gt;Banana Computing&lt;/company&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;address1&gt;1 Fiction Street&lt;/address1&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;address2&gt;Imaginary Town&lt;/address2&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;country&gt;Strangeland&lt;/country&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;postal_code&gt;ABC 567&lt;/postal_code&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;last_contact&gt;2005-05-27&lt;/last_contact&gt;<br />&nbsp;&nbsp;&nbsp; &lt;/person&gt;<br />&nbsp;&nbsp;&nbsp; &lt;person id=\"9407002\" type=\"supplier\"&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;first_name&gt;Hank&lt;/first_name&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;last_name&gt;Jin&lt;/last_name&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;company&gt;Banana Computing&lt;/company&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;address1&gt;1 Fiction Street&lt;/address1&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;address2&gt;Imaginary Town&lt;/address2&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;country&gt;Strangeland&lt;/country&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;postal_code&gt;ABC 567&lt;/postal_code&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;last_contact&gt;2005-05-27&lt;/last_contact&gt;<br />&nbsp;&nbsp;&nbsp; &lt;/person&gt;<br />&lt;/contacts&gt;<br /><span style=\"font-weight: bold; font-style: italic; text-decoration: underline;\">select.xslt<br /></span>&lt;?xml version=\"1.0\" encoding=\"UTF-8\"?&gt;<br />&lt;xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\"&gt;<br />&lt;xsl:template match=\"/\"&gt;<br />&lt;div&gt;<br />&lt;xsl:apply-templates select=\"/contacts/person\"/&gt;<br />&lt;/div&gt;<br />&lt;/xsl:template&gt;<br />&lt;xsl:template match=\"person\"&gt;<br />&lt;a href=\"javascript:showPerson({@id});\"&gt;<br />&lt;xsl:value-of select=\"first_name\"/&gt;&lt;xsl:text&gt; &lt;/xsl:text&gt;<br />&lt;xsl:value-of select=\"last_name\"/&gt;<br />&lt;/a&gt;&lt;br/&gt;<br />&lt;/xsl:template&gt;<br />&lt;/xsl:stylesheet&gt;<span style=\"text-decoration: underline;\"><br /></span><span style=\"font-weight: bold; font-style: italic; text-decoration: underline;\">display.xslt<br /></span>&lt;?xml version=\"1.0\" encoding=\"UTF-8\"?&gt;<br />&lt;xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\"&gt;<br />&lt;xsl:param name=\"personid\"&gt;0&lt;/xsl:param&gt;<br />&lt;xsl:template match=\"/\"&gt;<br />&lt;xsl:if test=\"$personid &gt; 0\"&gt;<br />&lt;div&gt;<br />&lt;xsl:apply-templates select=\"/contacts/person[@id=$personid]\"/&gt;<br />&lt;/div&gt;<br />&lt;/xsl:if&gt;<br />&lt;/xsl:template&gt;<br />&lt;xsl:template match=\"person\"&gt;<br />Name:<br />&lt;xsl:value-of select=\"first_name\"/&gt;&lt;xsl:text&gt; &lt;/xsl:text&gt;<br />&lt;xsl:value-of select=\"last_name\"/&gt;&lt;br/&gt;<br />Type: &lt;xsl:value-of select=\"@type\"/&gt;&lt;br/&gt;<br />Company: &lt;xsl:value-of select=\"company\"/&gt;&lt;br/&gt;<br />Address: &lt;xsl:value-of select=\"address1\"/&gt;, &lt;xsl:value-of select=\"address2\"/&gt;,<br />&lt;xsl:value-of select=\"country\"/&gt; &lt;xsl:text&gt; &lt;/xsl:text&gt;<br />&lt;xsl:value-of select=\"postal_code\"/&gt;<br />&lt;/xsl:template&gt;<br />&lt;/xsl:stylesheet&gt;<br /><span style=\"font-weight: bold; font-style: italic; text-decoration: underline;\">demo.html<br /><br /></span>&lt;html&gt;<br />&lt;head&gt;<br />&lt;script type=\"text/javascript\" src=\"browserDetect.js\"&gt;&lt;/script&gt;<br />&lt;script type=\"text/javascript\" src=\"xDom.js\"&gt;&lt;/script&gt;<br />&lt;script language=\"javascript\"&gt;<br />function runInit() {<br />&nbsp;&nbsp; &nbsp;if (blnFailed){<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(strFailedReason);<br />&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;<br />&nbsp;&nbsp; &nbsp;else {<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;doLoadXMLFromURL();<br />&nbsp;&nbsp; &nbsp;}<br />}<br />function doLoadXMLFromURL() {<br />&nbsp;&nbsp; &nbsp;oXMLFromURL = xDOM.createDOMDocument();<br />&nbsp;&nbsp; &nbsp;oXMLFromURL.onreadystatechange = onLoad_LoadXMLFromURL;<br />&nbsp;&nbsp; &nbsp;oXMLFromURL.load(\"contacts.xml\");<br />}<br />function onLoad_LoadXMLFromURL() {<br />&nbsp;&nbsp; &nbsp;if (oXMLFromURL.readyState == 4) {<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oXSLT=xDOM.createDOMDocument();<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oXSLT.onreadystatechange = onLoad_XSLTDOM;<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oXSLT.load(\"select.xslt\");<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oXSLTDisplay=xDOM.createDOMDocument();<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oXSLTDisplay.onreadystatechange = onLoad_XSLTDOM;<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oXSLTDisplay.load(\"display.xslt\");<br />&nbsp;&nbsp; &nbsp;}<br />}<br />function onLoad_XSLTDOM() {<br />&nbsp;&nbsp; &nbsp;var strOutput;<br />&nbsp;&nbsp; &nbsp;var oOutput = xDOM.createDOMDocument();<br />&nbsp;&nbsp; &nbsp;if (oXSLT.readyState == 4) {<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;strOutput = oXMLFromURL.transformNode(oXSLT);<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;document.getElementById(\"contacts\").innerHTML = strOutput;<br />&nbsp;&nbsp; &nbsp;}<br />}<br />function showPerson(intPersonID){<br />&nbsp;&nbsp; &nbsp;var strOutput;<br />&nbsp;&nbsp; &nbsp;for (var i=0; i &lt; oXSLTDisplay.documentElement.childNodes.length; i++) {<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if (oXSLTDisplay.documentElement.childNodes[i].nodeName == \"xsl:param\") {<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oXSLTDisplay.documentElement.childNodes[i].childNodes[0].nodeValue = intPersonID;<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />&nbsp;&nbsp; &nbsp;}<br />&nbsp;&nbsp; &nbsp;strOutput = oXMLFromURL.transformNode(oXSLTDisplay);<br />&nbsp;&nbsp; &nbsp;document.getElementById(\"displayDetails\").innerHTML = strOutput;<br />}<br />&lt;/script&gt;<br />&lt;/head&gt;<br />&lt;body onLoad=\"runInit();\"&gt;<br />&lt;div id=\"contacts\"&gt;&lt;/div&gt;<br />&lt;div id=\"displayDetails\"/&gt;<br />&lt;/body&gt;<br />&lt;/html&gt;',1,1,1,0,0,0,'IE和MF的XMLDOM有些不同，这里有个跨平台的库xDOM.js和detectBrowser.js，可以在google的codesearch里找到。下面是一个使用XMLDOM的例子，需要注意的是，innerhtml和innerHTML不同。一个xml+两个xslt+1个htmlcontacts.xml&lt;?xml version=\"1.0\" encoding=\"UTF-8\"?&gt;&lt;!-- This XML document describes a contacts list --&gt;&lt;contacts&gt;&nbsp;&nbsp;&nbsp; &lt;person id=\"9407001\" type=\"supplier\"&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;first_name&gt;John&lt;/first_name&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;last_name&gt;Smith&lt;/last_name&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;company&gt;Banana Computing&lt;/company&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;address1&gt;1 Fiction Street&lt;/address1&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;address2&gt;Imaginary Town&lt;/address2&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;country&gt;Strangeland&lt;/country&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;postal_code&gt;ABC 567&lt;/postal_code&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;last_contact&gt;2005-05-27&lt;/last_contact&gt;&nbsp;&nbsp;&nbsp; &lt;/person&gt;&nbsp;&nbsp;&nbsp; &lt;person id=\"9407002\" type=\"supplier\"&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;first_name&gt;Hank&lt;/first_name&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;last_name&gt;Jin&lt;/last_name&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;company&gt;Banana Computing&lt;/company&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;address1&gt;1 Fiction Street&lt;/address1&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;address2&gt;Imaginary Town&lt;/address2&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;country&gt;Strangeland&lt;/country&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;postal_code&gt;ABC 567&lt;/postal_code&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;last_contact&gt;2005-05-27&lt;/last_contact&gt;&nbsp;&nbsp;&nbsp; &lt;/person&gt;&lt;/contacts&gt;select.xslt&lt;?xml version=\"1.0\" encoding=\"UTF-8\"?&gt;&lt;xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\"&gt;&lt;xsl:template match=\"/\"&gt;&lt;div&gt;&lt;xsl:apply-templates select=\"/contacts/person\"/&gt;&lt;/div&gt;&lt;/xsl:template&gt;&lt;xsl:template match=\"person\"&gt;&lt;a href=\"javascript:showPerson({@id});\"&gt;&lt;xsl:value-of select=\"first_name\"/&gt;&lt;xsl:text&gt; &lt;/xsl:text&gt;&lt;xsl:value-of select=\"last_name\"/&gt;&lt;/a&gt;&lt;br/&gt;&lt;/xsl:template&gt;&lt;/xsl:stylesheet&gt;display.xslt&lt;?xml version=\"1.0\" encoding=\"UTF-8\"?&gt;&lt;xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\"&gt;&lt;xsl:param name=\"personid\"&gt;0&lt;/xsl:param&gt;&lt;xsl:template match=\"/\"&gt;&lt;xsl:if test=\"$personid &gt; 0\"&gt;&lt;div&gt;&lt;xsl:apply-templates select=\"/contacts/person[@id=$personid]\"/&gt;&lt;/div&gt;&lt;/xsl:if&gt;&lt;/xsl:template&gt;&lt;xsl:template match=\"person\"&gt;Name:&lt;xsl:value-of select=\"first_name\"/&gt;&lt;xsl:text&gt; &lt;/xsl:text&gt;&lt;xsl:value-of select=\"last_name\"/&gt;&lt;br/&gt;Type: &lt;xsl:value-of select=\"@type\"/&gt;&lt;br/&gt;Company: &lt;xsl:value-of select=\"company\"/&gt;&lt;br/&gt;Address: &lt;xsl:value-of select=\"address1\"/&gt;, &lt;xsl:value-of select=\"address2\"/&gt;,&lt;xsl:value-of select=\"country\"/&gt; &lt;xsl:text&gt; &lt;/xsl:text&gt;&lt;xsl:value-of select=\"postal_code\"/&gt;&lt;/xsl:template&gt;&lt;/xsl:stylesheet&gt;demo.html&lt;html&gt;&lt;head&gt;&lt;script type=\"text/javascript\" src=\"browserDetect.js\"&gt;&lt;/script&gt;&lt;script type=\"text/javascript\" src=\"xDom.js\"&gt;&lt;/script&gt;&lt;script language=\"javascript\"&gt;function runInit() {&nbsp;&nbsp; &nbsp;if (blnFailed){&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(strFailedReason);&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;else {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;doLoadXMLFromURL();&nbsp;&nbsp; &nbsp;}}function doLoadXMLFromURL() {&nbsp;&nbsp; &nbsp;oXMLFromURL = xDOM.createDOMDocument();&nbsp;&nbsp; &nbsp;oXMLFromURL.onreadystatechange = onLoad_LoadXMLFromURL;&nbsp;&nbsp; &nbsp;oXMLFromURL.load(\"contacts.xml\");}function onLoad_LoadXMLFromURL() {&nbsp;&nbsp; &nbsp;if (oXMLFromURL.readyState == 4) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oXSLT=xDOM.createDOMDocument();&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oXSLT.onreadystatechange = onLoad_XSLTDOM;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oXSLT.load(\"select.xslt\");&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oXSLTDisplay=xDOM.createDOMDocument();&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oXSLTDisplay.onreadystatechange = onLoad_XSLTDOM;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oXSLTDisplay.load(\"display.xslt\");&nbsp;&nbsp; &nbsp;}}function onLoad_XSLTDOM() {&nbsp;&nbsp; &nbsp;var strOutput;&nbsp;&nbsp; &nbsp;var oOutput = xDOM.createDOMDocument();&nbsp;&nbsp; &nbsp;if (oXSLT.readyState == 4) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;strOutput = oXMLFromURL.transformNode(oXSLT);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;document.getElementById(\"contacts\").innerHTML = strOutput;&nbsp;&nbsp; &nbsp;}}function showPerson(intPersonID){&nbsp;&nbsp; &nbsp;var strOutput;&nbsp;&nbsp; &nbsp;for (var i=0; i &lt; oXSLTDisplay.documentElement.childNodes.length; i++) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if (oXSLTDisplay.documentElement.childNodes[i].nodeName == \"xsl:param\") {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oXSLTDisplay.documentElement.childNodes[i].childNodes[0].nodeValue = intPersonID;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;strOutput = oXMLFromURL.transformNode(oXSLTDisplay);&nbsp;&nbsp; &nbsp;document.getElementById(\"displayDetails\").innerHTML = strOutput;}&lt;/script&gt;&lt;/head&gt;&lt;body onLoad=\"runInit();\"&gt;&lt;div id=\"contacts\"&gt;&lt;/div&gt;&lt;div id=\"displayDetails\"/&gt;&lt;/body&gt;&lt;/html&gt;','',0,'\"web skills\"',0,'','zinnia/_entry_detail.html','entry_detail.html');
INSERT INTO `zinnia_entry` VALUES (154,'SVM支持向量机+libsvm用法','33731937201082103012777',2,'2010-09-02 10:46:16',NULL,'2010-09-02 10:46:16','2014-07-13 18:57:15','SVM是一种无监督学习算法(其它无监督学习算法有kNN，SOM等)，可用于模式识别或回归时。\n但其用到大量的数学运算，需要一定的数学基础。但是如果使用libsvm，则可以不必关心svm实现细节，直接专注问题与建模。<br />libsvm是台湾大学林智仁(Lin Chih-Jen)副教授等开发设计的一个简单、易于使用和快速有效的SVM模式识别与回归的软件包，提供源代码和可执行文件。<br />使用方法：<br />\n1.下载软件包 libsvm<br />\nhttp://www.csie.ntu.edu.tw/~cjlin/cgi-bin/libsvm.cgi?+http://www.csie.ntu.edu.tw/~cjlin/libsvm+zip<br />\n 将libsvm解压缩到任意文件夹下，如E:\\libsvm<br />\n<br />\n2. 使用svm-toy进行测试,双击libsvm/windows/svm-toy.exe<br />点击Change按钮改变颜色，用鼠标点击在画布上画出如左图所示的点集，然后点Run按钮产生如右图的图像。<br />可以看出分类效果相当不错。<br />然后点Save按钮保存为tdata，供下一步使用。<br /><div><img alt=\"SVM支持向量机+libsvm用法 - hankjin - 云水居\" height=\"344\" src=\"/static/163/4934537816714574550.jpg\" style=\"margin: 10px 10px 10px 0pt; float: left;\" title=\"SVM支持向量机+libsvm用法 - hankjin - 云水居\" width=\"310\" /></div><br />&nbsp;<img alt=\"SVM支持向量机+libsvm用法 - hankjin - 云水居\" height=\"334\" src=\"/static/163/3283687078308020750.jpg\" style=\"margin: 0pt 0pt 0pt 10px; float: none;\" title=\"SVM支持向量机+libsvm用法 - hankjin - 云水居\" width=\"302\" /> <br /><br />3. 使用svm-train等进行测试<br />3.1下载gnuplot ftp://ftp.gnuplot.info/pub/gnuplot/gp400win32.zip, 解压缩到C:\\tmp目录下<br />3.2安装python<br />3.3将第2步生成的数据<br />3.4将libsvm/tools文件夹下的grid.py复制到libsvm/windows文件夹下。然后在命令行下进入libsvm/windows目录 ，执行<br />python grid.py tdata<br />会看到一堆数据和一个图在迅速变化，运行结束后，最后一行有参数，例如<br />...<br />[local] 13 -3 100.0 (best c=128.0, g=0.5, rate=100.0)<br />128.0 0.5 100.0<br />前两个分别是c和g，用这两个参数进行训练<br />svm-train.exe -c 128.0 -g 0.5 tdata<br />可以得到tdata.model，然后用这个tdata.model对tdata进行预测<br />svmp-redict.exe tdata tdata.model tdata.out <br />Accuracy = 100% (53/53) (classification)<br />这个数据比较规范，所以结果是100%<br /><br />参考文档<br />http://www.cmlab.csie.ntu.edu.tw/~cyy/learning/tutorials/libsvm.pdf<br />http://www.csie.ntu.edu.tw/~piaip/svm/svm_cjlin_dm.pdf<br />http://www.csie.ntu.edu.tw/~cjlin/libsvm/',1,1,1,0,0,0,'SVM是一种无监督学习算法(其它无监督学习算法有kNN，SOM等)，可用于模式识别或回归时。\n但其用到大量的数学运算，需要一定的数学基础。但是如果使用libsvm，则可以不必关心svm实现细节，直接专注问题与建模。libsvm是台湾大学林智仁(Lin Chih-Jen)副教授等开发设计的一个简单、易于使用和快速有效的SVM模式识别与回归的软件包，提供源代码和可执行文件。使用方法：\n1.下载软件包 libsvm\nhttp://www.csie.ntu.edu.tw/~cjlin/cgi-bin/libsvm.cgi?+http://www.csie.ntu.edu.tw/~cjlin/libsvm+zip\n 将libsvm解压缩到任意文件夹下，如E:\\libsvm\n\n2. 使用svm-toy进行测试,双击libsvm/windows/svm-toy.exe点击Change按钮改变颜色，用鼠标点击在画布上画出如左图所示的点集，然后点Run按钮产生如右图的图像。可以看出分类效果相当不错。然后点Save按钮保存为tdata，供下一步使用。&nbsp; 3. 使用svm-train等进行测试3.1下载gnuplot ftp://ftp.gnuplot.info/pub/gnuplot/gp400win32.zip, 解压缩到C:\\tmp目录下3.2安装python3.3将第2步生成的数据3.4将libsvm/tools文件夹下的grid.py复制到libsvm/windows文件夹下。然后在命令行下进入libsvm/windows目录 ，执行python grid.py tdata会看到一堆数据和一个图在迅速变化，运行结束后，最后一行有参数，例如...[local] 13 -3 100.0 (best c=128.0, g=0.5, rate=100.0)128.0 0.5 100.0前两个分别是c和g，用这两个参数进行训练svm-train.exe -c 128.0 -g 0.5 tdata可以得到tdata.model，然后用这个tdata.model对tdata进行预测svmp-redict.exe tdata tdata.model tdata.out Accuracy = 100% (53/53) (classification)这个数据比较规范，所以结果是100%参考文档http://www.cmlab.csie.ntu.edu.tw/~cyy/learning/tutorials/libsvm.pdfhttp://www.csie.ntu.edu.tw/~piaip/svm/svm_cjlin_dm.pdfhttp://www.csie.ntu.edu.tw/~cjlin/libsvm/','',0,'math',0,'','zinnia/_entry_detail.html','entry_detail.html'),(155,'中文字典的索引与树的存储与访问','33731937201010384110122',2,'2010-11-03 21:13:58',NULL,'2010-11-03 21:13:58','2014-07-13 18:57:15','中文字典可以使用索引与树两种存储与访问方式：<br /><b>索引</b><br />索引的存储方式很简单，first指向第一个子节点，next指向下一个兄弟节点。<br />如下图所示：<br />root有四个子节点 son1 son2 son3 sonx<br />son1有两个子节点son11 son12<br />访问方式为：<br />root-&gt;first获得root的第一个子节点son1，然后通过son1-&gt;next获得root的第二个子节点son2，son2-&gt;next获得son3，son3-&gt;next获得sonx<br />然后从son1-&gt;first获得son1的第一个子节点son11，然后通过son11-&gt;next获得son1的第二个子节点son12<br /><br /><div><img alt=\"中文字典的索引与树的存储与访问 - hankjin - 云水居\" src=\"/static/163/2886807361145576467.png\" style=\"margin: 0pt 10px 0pt 0pt;\" title=\"中文字典的索引与树的存储与访问 - hankjin - 云水居\" /></div>&nbsp;<br /><br /><b>哈希字典树</b><br />从前面的索引结构可以看出，要访问sonx节点，要通过root-&gt;first-&gt;next-&gt;next-&gt;next，才能达到，如果一个节点有几百几千个子节点，访问的复杂度将会非常大。<br />根据前面的索引，可以生成哈希字典树，将每个节点的所有子节点指针存储在一个指针数组中，这样就可以利用哈希的特性快速定位到子节点。复杂度为c<br /><br /><div><img alt=\"中文字典的索引与树的存储与访问 - hankjin - 云水居\" src=\"/static/163/1265792970268949606.png\" style=\"margin: 0pt 10px 0pt 0pt;\" title=\"中文字典的索引与树的存储与访问 - hankjin - 云水居\" /></div><b>&nbsp;代码:</b><br />从CSDN上下载一个字典http://d.download.csdn.net/down/382024/oldfox126<br />然后使用以下命令转换得到字典文件<br />iconv -f gbk -t utf-8 dict_chs_gbk.txt |awk \'{print $1}\' | tee cndict.in<br />然后编译执行下面的代码文件即可:<br />/*<br />&nbsp;* @desc: index and tree<br />&nbsp;* @date: 2010-11-3<br />&nbsp;* @author: hankjin<br />&nbsp;* @version: 1.0<br />&nbsp;* @platform: ubuntu 10.04<br />&nbsp;* @tree:index = 40:1<br />&nbsp;*/<br />#include &lt;iostream&gt;<br />#include &lt;fstream&gt;<br />#include &lt;cmath&gt;<br />#include &lt;cstring&gt;<br />#include &lt;ctime&gt;<br />using namespace std;<br />#define MAXN 40<br /><br />typedef struct _node{<br />&nbsp;&nbsp;&nbsp; struct _node *first;&nbsp;&nbsp;&nbsp; //first element of index<br />&nbsp;&nbsp;&nbsp; struct _node *next;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; //next element of index<br />&nbsp;&nbsp;&nbsp; struct _node **sons;&nbsp;&nbsp;&nbsp; //hash array<br />&nbsp;&nbsp;&nbsp; int size;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; //hash array size<br />&nbsp;&nbsp;&nbsp; int used;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; //son number<br />&nbsp;&nbsp;&nbsp; wchar_t value;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; //value<br />&nbsp;&nbsp;&nbsp; bool isword;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; //is end of word or middle <br />&nbsp;&nbsp;&nbsp; _node(wchar_t v){<br />&nbsp;&nbsp;&nbsp; sons=NULL;<br />&nbsp;&nbsp;&nbsp; first=next=NULL;<br />&nbsp;&nbsp;&nbsp; used=0;<br />&nbsp;&nbsp;&nbsp; value=v;<br />&nbsp;&nbsp;&nbsp; isword=false;<br />&nbsp;&nbsp;&nbsp; }<br />}node;<br />//add a element to index<br />void indexadd(node *root, wchar_t *word);<br />//convert index to tree<br />void index2tree(node *root);<br />//find by index<br />bool indexfind(node *root, wchar_t *word);<br />//find by tree<br />bool treefind(node *root, wchar_t *word);<br />//tree hash<br />inline int treehash(node *p, wchar_t value){<br />&nbsp;&nbsp;&nbsp; return (int)value % p-&gt;size;<br />}<br />//calc hash array size, accord to son number.<br />//rule: least prime number which is larger then 2 times son number<br />//eg: if a node has 5 son, 11 is the least prime larger then 2*5<br />inline int hashfactor(int x){<br />&nbsp;&nbsp;&nbsp; if(x&lt;3)//at least 3<br />&nbsp;&nbsp;&nbsp; return 3;<br />&nbsp;&nbsp;&nbsp; for(int i, res=x/2*4+1; ; res+=2){<br />&nbsp;&nbsp;&nbsp; for(i=3; i&lt;=sqrt(res); i+=2){//is prime<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(res%i==0)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; if(res%i!=0)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return res;<br />&nbsp;&nbsp;&nbsp; }<br />}<br />locale lang(\"en_US.utf-8\");<br />wifstream fin(\"cndict.in\");<br />wifstream fin1(\"cndict.in\");<br />wifstream fin2(\"cndict.in\");<br />int main(){<br />&nbsp;&nbsp;&nbsp; fin.imbue(lang);<br />&nbsp;&nbsp;&nbsp; fin1.imbue(lang);<br />&nbsp;&nbsp;&nbsp; fin2.imbue(lang);<br /><br />&nbsp;&nbsp;&nbsp; node root(\' \');<br />&nbsp;&nbsp;&nbsp; wchar_t tmp[MAXN];<br />&nbsp;&nbsp;&nbsp; //build index<br />&nbsp;&nbsp;&nbsp; while(fin&gt;&gt;tmp){<br />&nbsp;&nbsp;&nbsp; indexadd(&amp;root, tmp);<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; //convert from index to tree, with index keeped<br />&nbsp;&nbsp;&nbsp; index2tree(&amp;root);<br />&nbsp;&nbsp;&nbsp; //cin&gt;&gt;tmp;<br />&nbsp;&nbsp;&nbsp; wchar_t word[] = L\"市场\";<br />&nbsp;&nbsp;&nbsp; //int M = 10;<br />&nbsp;&nbsp;&nbsp; int M = 1;<br />&nbsp;&nbsp;&nbsp; //int N = 10000000;<br />&nbsp;&nbsp;&nbsp; int N = 1000000;<br />&nbsp;&nbsp;&nbsp; clock_t t1 = clock();<br />&nbsp;&nbsp;&nbsp; while(fin1&gt;&gt;tmp){<br />&nbsp;&nbsp;&nbsp; for(int i=0;i&lt;M;i++)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; indexfind(&amp;root, tmp);<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; clock_t t2 = clock();<br />&nbsp;&nbsp;&nbsp; while(fin2&gt;&gt;tmp){<br />&nbsp;&nbsp;&nbsp; for(int i=0;i&lt;M;i++)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; treefind(&amp;root, tmp);<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; clock_t t3 = clock();<br />&nbsp;&nbsp;&nbsp; for(int i=0;i&lt;N;i++){<br />&nbsp;&nbsp;&nbsp; indexfind(&amp;root, word);<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; clock_t t4 = clock();<br />&nbsp;&nbsp;&nbsp; for(int i=0;i&lt;N;i++){<br />&nbsp;&nbsp;&nbsp; treefind(&amp;root, word);<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; clock_t t5 = clock();<br />&nbsp;&nbsp;&nbsp; cout&lt;&lt;\"Index/Tree average find time:\"&lt;&lt;(t2-t1)&lt;&lt;\"/\"&lt;&lt;(t3-t2)&lt;&lt;endl;<br />&nbsp;&nbsp;&nbsp; cout&lt;&lt;\"Index/Tree find time:\"&lt;&lt;(t4-t3)&lt;&lt;\"/\"&lt;&lt;(t5-t4)&lt;&lt;endl;<br />&nbsp;&nbsp;&nbsp; return 0;<br />}<br />void indexadd(node *root, wchar_t *word){<br />&nbsp;&nbsp;&nbsp; node *prev, *p, *cur = root;<br />&nbsp;&nbsp;&nbsp; for(; *word; word++){<br />&nbsp;&nbsp;&nbsp; if(cur-&gt;first == NULL){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cur-&gt;first = new node(*word);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cur-&gt;used++;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cur = cur-&gt;first;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; continue;<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; for(p = cur-&gt;first; p; prev=p,p=p-&gt;next){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(p-&gt;value == *word){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cur = p;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; if(p==NULL){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; prev-&gt;next = new node(*word);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cur-&gt;used++;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cur = prev-&gt;next;<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; cur-&gt;isword=true;<br />}<br />void index2tree(node *cur){<br />&nbsp;&nbsp;&nbsp; if(cur==NULL) return ;<br />&nbsp;&nbsp;&nbsp; cur-&gt;size = hashfactor(cur-&gt;used);<br />&nbsp;&nbsp;&nbsp; cur-&gt;sons = new node*[cur-&gt;size];<br />&nbsp;&nbsp;&nbsp; memset((void*)cur-&gt;sons, 0, sizeof(node *)*cur-&gt;size);<br />&nbsp;&nbsp;&nbsp; for(node *p = cur-&gt;first; p; p=p-&gt;next){<br />&nbsp;&nbsp;&nbsp; int key=treehash(cur, p-&gt;value);<br />&nbsp;&nbsp;&nbsp; for(node *pos=cur-&gt;sons[key]; pos!=NULL; )<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; pos=cur-&gt;sons[(++key)%cur-&gt;size];<br />&nbsp;&nbsp;&nbsp; cur-&gt;sons[key%cur-&gt;size]=p;<br />&nbsp;&nbsp;&nbsp; index2tree(p);<br />&nbsp;&nbsp;&nbsp; }<br />}<br />bool indexfind(node *root, wchar_t *word){<br />&nbsp;&nbsp;&nbsp; node *p, *cur = root;<br />&nbsp;&nbsp;&nbsp; for(; *word; word++){<br />&nbsp;&nbsp;&nbsp; for(p=cur-&gt;first; p; p=p-&gt;next){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(p-&gt;value == *word){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cur = p;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; if(p==NULL)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; return p==NULL ? false : p-&gt;isword;<br />}<br />bool treefind(node *root, wchar_t *word){<br />&nbsp;&nbsp;&nbsp; node *p, *cur = root;<br />&nbsp;&nbsp;&nbsp; for(; *word; word++){<br />&nbsp;&nbsp;&nbsp; int key=treehash(cur, *word);<br />&nbsp;&nbsp;&nbsp; for(p=cur-&gt;sons[key]; p; p=cur-&gt;sons[(++key)%cur-&gt;size]){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(p-&gt;value == *word){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cur = p;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; if(p==NULL)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; return p==NULL ? false : cur-&gt;isword;<br />}',1,1,1,0,0,0,'中文字典可以使用索引与树两种存储与访问方式：索引索引的存储方式很简单，first指向第一个子节点，next指向下一个兄弟节点。如下图所示：root有四个子节点 son1 son2 son3 sonxson1有两个子节点son11 son12访问方式为：root-&gt;first获得root的第一个子节点son1，然后通过son1-&gt;next获得root的第二个子节点son2，son2-&gt;next获得son3，son3-&gt;next获得sonx然后从son1-&gt;first获得son1的第一个子节点son11，然后通过son11-&gt;next获得son1的第二个子节点son12&nbsp;哈希字典树从前面的索引结构可以看出，要访问sonx节点，要通过root-&gt;first-&gt;next-&gt;next-&gt;next，才能达到，如果一个节点有几百几千个子节点，访问的复杂度将会非常大。根据前面的索引，可以生成哈希字典树，将每个节点的所有子节点指针存储在一个指针数组中，这样就可以利用哈希的特性快速定位到子节点。复杂度为c&nbsp;代码:从CSDN上下载一个字典http://d.download.csdn.net/down/382024/oldfox126然后使用以下命令转换得到字典文件iconv -f gbk -t utf-8 dict_chs_gbk.txt |awk \'{print $1}\' | tee cndict.in然后编译执行下面的代码文件即可:/*&nbsp;* @desc: index and tree&nbsp;* @date: 2010-11-3&nbsp;* @author: hankjin&nbsp;* @version: 1.0&nbsp;* @platform: ubuntu 10.04&nbsp;* @tree:index = 40:1&nbsp;*/#include &lt;iostream&gt;#include &lt;fstream&gt;#include &lt;cmath&gt;#include &lt;cstring&gt;#include &lt;ctime&gt;using namespace std;#define MAXN 40typedef struct _node{&nbsp;&nbsp;&nbsp; struct _node *first;&nbsp;&nbsp;&nbsp; //first element of index&nbsp;&nbsp;&nbsp; struct _node *next;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; //next element of index&nbsp;&nbsp;&nbsp; struct _node **sons;&nbsp;&nbsp;&nbsp; //hash array&nbsp;&nbsp;&nbsp; int size;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; //hash array size&nbsp;&nbsp;&nbsp; int used;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; //son number&nbsp;&nbsp;&nbsp; wchar_t value;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; //value&nbsp;&nbsp;&nbsp; bool isword;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; //is end of word or middle &nbsp;&nbsp;&nbsp; _node(wchar_t v){&nbsp;&nbsp;&nbsp; sons=NULL;&nbsp;&nbsp;&nbsp; first=next=NULL;&nbsp;&nbsp;&nbsp; used=0;&nbsp;&nbsp;&nbsp; value=v;&nbsp;&nbsp;&nbsp; isword=false;&nbsp;&nbsp;&nbsp; }}node;//add a element to indexvoid indexadd(node *root, wchar_t *word);//convert index to treevoid index2tree(node *root);//find by indexbool indexfind(node *root, wchar_t *word);//find by treebool treefind(node *root, wchar_t *word);//tree hashinline int treehash(node *p, wchar_t value){&nbsp;&nbsp;&nbsp; return (int)value % p-&gt;size;}//calc hash array size, accord to son number.//rule: least prime number which is larger then 2 times son number//eg: if a node has 5 son, 11 is the least prime larger then 2*5inline int hashfactor(int x){&nbsp;&nbsp;&nbsp; if(x&lt;3)//at least 3&nbsp;&nbsp;&nbsp; return 3;&nbsp;&nbsp;&nbsp; for(int i, res=x/2*4+1; ; res+=2){&nbsp;&nbsp;&nbsp; for(i=3; i&lt;=sqrt(res); i+=2){//is prime&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(res%i==0)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; if(res%i!=0)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return res;&nbsp;&nbsp;&nbsp; }}locale lang(\"en_US.utf-8\");wifstream fin(\"cndict.in\");wifstream fin1(\"cndict.in\");wifstream fin2(\"cndict.in\");int main(){&nbsp;&nbsp;&nbsp; fin.imbue(lang);&nbsp;&nbsp;&nbsp; fin1.imbue(lang);&nbsp;&nbsp;&nbsp; fin2.imbue(lang);&nbsp;&nbsp;&nbsp; node root(\' \');&nbsp;&nbsp;&nbsp; wchar_t tmp[MAXN];&nbsp;&nbsp;&nbsp; //build index&nbsp;&nbsp;&nbsp; while(fin&gt;&gt;tmp){&nbsp;&nbsp;&nbsp; indexadd(&amp;root, tmp);&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; //convert from index to tree, with index keeped&nbsp;&nbsp;&nbsp; index2tree(&amp;root);&nbsp;&nbsp;&nbsp; //cin&gt;&gt;tmp;&nbsp;&nbsp;&nbsp; wchar_t word[] = L\"市场\";&nbsp;&nbsp;&nbsp; //int M = 10;&nbsp;&nbsp;&nbsp; int M = 1;&nbsp;&nbsp;&nbsp; //int N = 10000000;&nbsp;&nbsp;&nbsp; int N = 1000000;&nbsp;&nbsp;&nbsp; clock_t t1 = clock();&nbsp;&nbsp;&nbsp; while(fin1&gt;&gt;tmp){&nbsp;&nbsp;&nbsp; for(int i=0;i&lt;M;i++)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; indexfind(&amp;root, tmp);&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; clock_t t2 = clock();&nbsp;&nbsp;&nbsp; while(fin2&gt;&gt;tmp){&nbsp;&nbsp;&nbsp; for(int i=0;i&lt;M;i++)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; treefind(&amp;root, tmp);&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; clock_t t3 = clock();&nbsp;&nbsp;&nbsp; for(int i=0;i&lt;N;i++){&nbsp;&nbsp;&nbsp; indexfind(&amp;root, word);&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; clock_t t4 = clock();&nbsp;&nbsp;&nbsp; for(int i=0;i&lt;N;i++){&nbsp;&nbsp;&nbsp; treefind(&amp;root, word);&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; clock_t t5 = clock();&nbsp;&nbsp;&nbsp; cout&lt;&lt;\"Index/Tree average find time:\"&lt;&lt;(t2-t1)&lt;&lt;\"/\"&lt;&lt;(t3-t2)&lt;&lt;endl;&nbsp;&nbsp;&nbsp; cout&lt;&lt;\"Index/Tree find time:\"&lt;&lt;(t4-t3)&lt;&lt;\"/\"&lt;&lt;(t5-t4)&lt;&lt;endl;&nbsp;&nbsp;&nbsp; return 0;}void indexadd(node *root, wchar_t *word){&nbsp;&nbsp;&nbsp; node *prev, *p, *cur = root;&nbsp;&nbsp;&nbsp; for(; *word; word++){&nbsp;&nbsp;&nbsp; if(cur-&gt;first == NULL){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cur-&gt;first = new node(*word);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cur-&gt;used++;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cur = cur-&gt;first;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; continue;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; for(p = cur-&gt;first; p; prev=p,p=p-&gt;next){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(p-&gt;value == *word){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cur = p;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; if(p==NULL){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; prev-&gt;next = new node(*word);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cur-&gt;used++;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cur = prev-&gt;next;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; cur-&gt;isword=true;}void index2tree(node *cur){&nbsp;&nbsp;&nbsp; if(cur==NULL) return ;&nbsp;&nbsp;&nbsp; cur-&gt;size = hashfactor(cur-&gt;used);&nbsp;&nbsp;&nbsp; cur-&gt;sons = new node*[cur-&gt;size];&nbsp;&nbsp;&nbsp; memset((void*)cur-&gt;sons, 0, sizeof(node *)*cur-&gt;size);&nbsp;&nbsp;&nbsp; for(node *p = cur-&gt;first; p; p=p-&gt;next){&nbsp;&nbsp;&nbsp; int key=treehash(cur, p-&gt;value);&nbsp;&nbsp;&nbsp; for(node *pos=cur-&gt;sons[key]; pos!=NULL; )&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; pos=cur-&gt;sons[(++key)%cur-&gt;size];&nbsp;&nbsp;&nbsp; cur-&gt;sons[key%cur-&gt;size]=p;&nbsp;&nbsp;&nbsp; index2tree(p);&nbsp;&nbsp;&nbsp; }}bool indexfind(node *root, wchar_t *word){&nbsp;&nbsp;&nbsp; node *p, *cur = root;&nbsp;&nbsp;&nbsp; for(; *word; word++){&nbsp;&nbsp;&nbsp; for(p=cur-&gt;first; p; p=p-&gt;next){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(p-&gt;value == *word){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cur = p;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; if(p==NULL)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; return p==NULL ? false : p-&gt;isword;}bool treefind(node *root, wchar_t *word){&nbsp;&nbsp;&nbsp; node *p, *cur = root;&nbsp;&nbsp;&nbsp; for(; *word; word++){&nbsp;&nbsp;&nbsp; int key=treehash(cur, *word);&nbsp;&nbsp;&nbsp; for(p=cur-&gt;sons[key]; p; p=cur-&gt;sons[(++key)%cur-&gt;size]){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(p-&gt;value == *word){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cur = p;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; if(p==NULL)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; return p==NULL ? false : cur-&gt;isword;}','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(156,'Linux动态和静态库','3373193720081029536961',2,'2008-11-02 21:53:06',NULL,'2008-11-02 21:53:06','2014-07-13 18:57:15','示例代码: hello.h + hello.c + main.c<br />1. hello.h<br />#ifndef HELLO_H<br />#define HELLO_H <br />void hello(const char *name);<br />#endif //HELLO_H<br />2. hello.c<br />&nbsp;#include &lt;stdio.h&gt;<br />&nbsp; void hello(const char *name)<br />&nbsp;{<br />&nbsp;&nbsp; printf(\"Hello %s!\\n\", name);<br />&nbsp;}<br />3. main.c<br />&nbsp;#include \"hello.h\"<br />&nbsp;int main()<br />&nbsp;{<br />&nbsp;&nbsp; hello(\"everyone\");<br />&nbsp;&nbsp; return 0;<br />&nbsp;}<br />静态库<br />gcc -c hello.c<br />ar cr libhello.a hello.o<br />gcc -o hello main.c libhello.a<br />#or gcc -o hello main.c -L. -lhello<br />动态库<br />gcc -shared -fPCI -o libhello.so hello.o <br />gcc -o hello main.c -L. -lhello<br />export LD_LIBRARY_PATH=.:$LD_LIBRARY_PATH<br />#or mv libhello.so /lib<br />#or mv libhello.so /usr/lib<br />#or echo `pwd` &gt;&gt;/etc/ld.conf;ldconfig<br /><br />',1,1,1,0,0,0,'示例代码: hello.h + hello.c + main.c1. hello.h#ifndef HELLO_H#define HELLO_H void hello(const char *name);#endif //HELLO_H2. hello.c&nbsp;#include &lt;stdio.h&gt;&nbsp; void hello(const char *name)&nbsp;{&nbsp;&nbsp; printf(\"Hello %s!\\n\", name);&nbsp;}3. main.c&nbsp;#include \"hello.h\"&nbsp;int main()&nbsp;{&nbsp;&nbsp; hello(\"everyone\");&nbsp;&nbsp; return 0;&nbsp;}静态库gcc -c hello.car cr libhello.a hello.ogcc -o hello main.c libhello.a#or gcc -o hello main.c -L. -lhello动态库gcc -shared -fPCI -o libhello.so hello.o gcc -o hello main.c -L. -lhelloexport LD_LIBRARY_PATH=.:$LD_LIBRARY_PATH#or mv libhello.so /lib#or mv libhello.so /usr/lib#or echo `pwd` &gt;&gt;/etc/ld.conf;ldconfig','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(157,'HTTP的BASIC验证','3373193720071017104746809',2,'2007-11-17 22:47:46',NULL,'2007-11-17 22:47:46','2014-07-13 18:57:15','<p>服务器端：</p>\n<p>方式一：在web.xml中配置，可以参考CATALINA_HOME/server/manager/WEB-INF/web.xml</p>\n<p>方式二：在程序中coding，参考如下：</p>\n<p>if(request.getHeader(\"Authorization\")==null){ <br />&nbsp;&nbsp;&nbsp; response.setStatus(401); <br />&nbsp;&nbsp; &nbsp;response.setHeader(\"WWW-authenticate\",\"Basic realm=\\\"请输入管理员密码\\\"\");&nbsp;<br />}else if( !(request.getHeader(\"Authorization\").equals(pass))){<br />&nbsp;&nbsp;&nbsp; response.setStatus(401);&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; response.setHeader(\"WWW-authenticate\",\"Basic realm=\\\"用户名或者密码错误\\\"\");<br />&nbsp;&nbsp;&nbsp; out.print(\"对不起你没有权限！！\");<br />&nbsp;&nbsp; &nbsp;return;<br />}<br /></p>\n<p>GET /manager HTTP/1.0<br />Content-Type: application/x-www-form-urlencoded<br />Authorization: Basic &lt;base64encode(user:pass)&gt;</p>\n<p>方法一：直接使用new sun.misc.BASE64Encoder().encode(byte[])构造HTTP头</p>\n<p>方法二：&nbsp;&nbsp;Authenticator.setDefault(new AuthImpl()); 然后使用HttpURLConnection连接即可，这种方式更灵活</p>\n<p>class AuthImpl extends Authenticator {<br />&nbsp;protected PasswordAuthentication getPasswordAuthentication() {<br />&nbsp;&nbsp;String user = \"admin\";<br />&nbsp;&nbsp;char pass[] = \"admin\".toCharArray();<br />&nbsp;&nbsp;return new PasswordAuthentication(user, pass);<br />&nbsp;} <br />}</p>\n<p>BTW</p>\n<p>Http的Session和Https的Sessin不能互相传递</p>',1,1,1,0,0,0,'服务器端：\n方式一：在web.xml中配置，可以参考CATALINA_HOME/server/manager/WEB-INF/web.xml\n方式二：在程序中coding，参考如下：\nif(request.getHeader(\"Authorization\")==null){ &nbsp;&nbsp;&nbsp; response.setStatus(401); &nbsp;&nbsp; &nbsp;response.setHeader(\"WWW-authenticate\",\"Basic realm=\\\"请输入管理员密码\\\"\");&nbsp;}else if( !(request.getHeader(\"Authorization\").equals(pass))){&nbsp;&nbsp;&nbsp; response.setStatus(401);&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; response.setHeader(\"WWW-authenticate\",\"Basic realm=\\\"用户名或者密码错误\\\"\");&nbsp;&nbsp;&nbsp; out.print(\"对不起你没有权限！！\");&nbsp;&nbsp; &nbsp;return;}\nGET /manager HTTP/1.0Content-Type: application/x-www-form-urlencodedAuthorization: Basic &lt;base64encode(user:pass)&gt;\n方法一：直接使用new sun.misc.BASE64Encoder().encode(byte[])构造HTTP头\n方法二：&nbsp;&nbsp;Authenticator.setDefault(new AuthImpl()); 然后使用HttpURLConnection连接即可，这种方式更灵活\nclass AuthImpl extends Authenticator {&nbsp;protected PasswordAuthentication getPasswordAuthentication() {&nbsp;&nbsp;String user = \"admin\";&nbsp;&nbsp;char pass[] = \"admin\".toCharArray();&nbsp;&nbsp;return new PasswordAuthentication(user, pass);&nbsp;} }\nBTW\nHttp的Session和Https的Sessin不能互相传递','',0,'network',0,'','zinnia/_entry_detail.html','entry_detail.html'),(158,'谁动了我的grub','3373193720094319257291',2,'2009-05-31 09:02:57',NULL,'2009-05-31 09:02:57','2014-07-13 18:57:15','如果用Grub引导，Linux忘记密码以后，只需要在grub中修改启动参数，就可以进入单用户模式重设密码，可谓有利也有弊。好处当然是即使忘记密码也不一定要重装系统，坏处就是给xx之徒以可乘之机。<br />解决方案：<br />设置grub密码，首先用命令 grub-md5-crypt产生一个加密过的密码，然后修改menu.lst，如下所示：第一个密码是grub密码，用户要进入grub模式时才需要输入，否则一切和普通启动一样，第二和第三个是菜单项密码，用户要进入相应的系统才需要输入密码。<br />建议设置第一个密码，即grub密码。至于菜单项密码则只对安全性要求特高，平时也不用的系统设置。<br /><font color=\"#008000\">password –-md5 \n&lt;密码1&gt;<br />timeout=30<br />default=0<br />title “redhat AS 4”<br />password –-md5 \n&lt;密码2&gt;<br />root (hd0,1)<br />kernel /vmlinuz ro root=LABEL=/<br />initrd \n/initrd-xxx.img<br />title “windows”<br />password –-md5 &lt;密码3&gt;<br />rootnoverify \n（hd0,0）<br />chainloader +1</font><br /><br />',1,1,1,0,0,0,'如果用Grub引导，Linux忘记密码以后，只需要在grub中修改启动参数，就可以进入单用户模式重设密码，可谓有利也有弊。好处当然是即使忘记密码也不一定要重装系统，坏处就是给xx之徒以可乘之机。解决方案：设置grub密码，首先用命令 grub-md5-crypt产生一个加密过的密码，然后修改menu.lst，如下所示：第一个密码是grub密码，用户要进入grub模式时才需要输入，否则一切和普通启动一样，第二和第三个是菜单项密码，用户要进入相应的系统才需要输入密码。建议设置第一个密码，即grub密码。至于菜单项密码则只对安全性要求特高，平时也不用的系统设置。password –-md5 \n&lt;密码1&gt;timeout=30default=0title “redhat AS 4”password –-md5 \n&lt;密码2&gt;root (hd0,1)kernel /vmlinuz ro root=LABEL=/initrd \n/initrd-xxx.imgtitle “windows”password –-md5 &lt;密码3&gt;rootnoverify \n（hd0,0）chainloader +1','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(159,'[Fw]大型网站架构演变和知识体系','33731937200910711020291',2,'2009-11-07 11:00:20',NULL,'2009-11-07 11:00:20','2014-07-13 18:57:15','[基础]<br />1.分离数据库&nbsp; 将数据库服务器和网站服务器分别放在两台机器上<br /><br />[初级:缓存]<br />2.缓存静态页 在用户和网站服务器中间增加squid，缓存静态页面<br />3.缓存静态部分 用squid缓存动态页面的静态部分<br />4.缓存数据&nbsp; 将用户信息等常用数据缓存到内存<br /><br />[中级:增加]<br />5.增加服务器 增加一台网站服务器，负载均衡降低访问压力<br />6.增加数据库 数据库集群或将表分布到不同的数据库，从而加快数据库访问速度<br />7.分表 DAL？？<br /><br />[高级:架构]<br />8.增加服务器 硬件负载均衡，增加多台网站服务器<br />9.改变数据库 使用bigtable等部分替代数据库存储<br />10.大型分布式应用 目前的终极方案<br />',1,1,1,0,0,0,'[基础]1.分离数据库&nbsp; 将数据库服务器和网站服务器分别放在两台机器上[初级:缓存]2.缓存静态页 在用户和网站服务器中间增加squid，缓存静态页面3.缓存静态部分 用squid缓存动态页面的静态部分4.缓存数据&nbsp; 将用户信息等常用数据缓存到内存[中级:增加]5.增加服务器 增加一台网站服务器，负载均衡降低访问压力6.增加数据库 数据库集群或将表分布到不同的数据库，从而加快数据库访问速度7.分表 DAL？？[高级:架构]8.增加服务器 硬件负载均衡，增加多台网站服务器9.改变数据库 使用bigtable等部分替代数据库存储10.大型分布式应用 目前的终极方案','',0,'\"web skills\"',0,'','zinnia/_entry_detail.html','entry_detail.html'),(160,'自由切换Windows和Ubuntu双启动','33731937200931811323622',2,'2009-04-18 11:32:03',NULL,'2009-04-18 11:32:03','2014-07-13 18:57:15','Windows安装在hd0,0 Ubuntu安装在hd0,1<br />在Linux的Grub或者Windows的Grub4dos两种启动方式之间自由切换<br />1. Grub启动<br />在menu.lst中增加<br />title Grub4DOS<br />root (hd0,0)<br />kernel /boot/grub/memdisk.gz<br />initrd /boot/grub/win99.zip<br />然后在win99.zip中加入autorun.bat<br />grub --config-file=menu.lst<br /><br />2. Grub4DOS启动<br />title Ubuntu<br />root (hd0,1)<br />kernel /vmlinuz-&lt;TAB&gt; root=/dev/sda2<br />initrd /init&lt;TAB&gt;<br />',1,1,1,0,0,0,'Windows安装在hd0,0 Ubuntu安装在hd0,1在Linux的Grub或者Windows的Grub4dos两种启动方式之间自由切换1. Grub启动在menu.lst中增加title Grub4DOSroot (hd0,0)kernel /boot/grub/memdisk.gzinitrd /boot/grub/win99.zip然后在win99.zip中加入autorun.batgrub --config-file=menu.lst2. Grub4DOS启动title Ubunturoot (hd0,1)kernel /vmlinuz-&lt;TAB&gt; root=/dev/sda2initrd /init&lt;TAB&gt;','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(161,'记录bash操作记录','337319372013015102128710',2,'2013-01-15 10:21:28',NULL,'2013-01-15 10:21:28','2014-07-13 18:57:15','<p><span lang=\"EN-US\">HISTFILE=~/.bash_history</span></p>  <p><span lang=\"EN-US\">HISTSIZE=1000000&nbsp;&nbsp;&nbsp;&nbsp; </span></p>  <p><span lang=\"EN-US\">export&nbsp; HISTFILE</span></p>  <p><span lang=\"EN-US\">export HISTCONTROL=ignoredups&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p>  <p><span lang=\"EN-US\">export HISTIGNORE=\"&amp;:bg:fg:exit:ls:pwd\"&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p>  <p><span lang=\"EN-US\">shopt -s histappend&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>  <p><span lang=\"EN-US\">export HISTTIMEFORMAT=\'%F %T \'&nbsp; </span></p>  <p><span lang=\"EN-US\">mkdir -p /home/work/.history/</span></p>  <p><span lang=\"EN-US\">chown work:work /home/work/.history/ &amp;&gt;/dev/null</span></p>  <p><span lang=\"EN-US\">chmod -R a+w /home/work/.history/ &amp;&gt;/dev/null</span></p>  <p><span lang=\"EN-US\">export HISTORY_FILE=/home/work/.history/`date \'+%Y%m\'`.log&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p>  <p><span lang=\"EN-US\">export PROMPT_COMMAND=\'history -a; { echo $(ls -l `tty` |awk \"{print \\$3}\" ) $(whoami) $(history 1 | { read x cmd;echo \"$cmd\" ;});} &gt;&gt; $HISTORY_FILE\'</span></p>',1,1,1,0,0,0,'HISTFILE=~/.bash_history  HISTSIZE=1000000&nbsp;&nbsp;&nbsp;&nbsp;   export&nbsp; HISTFILE  export HISTCONTROL=ignoredups&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;   export HISTIGNORE=\"&amp;:bg:fg:exit:ls:pwd\"&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;   shopt -s histappend&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;  export HISTTIMEFORMAT=\'%F %T \'&nbsp;   mkdir -p /home/work/.history/  chown work:work /home/work/.history/ &amp;&gt;/dev/null  chmod -R a+w /home/work/.history/ &amp;&gt;/dev/null  export HISTORY_FILE=/home/work/.history/`date \'+%Y%m\'`.log&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;   export PROMPT_COMMAND=\'history -a; { echo $(ls -l `tty` |awk \"{print \\$3}\" ) $(whoami) $(history 1 | { read x cmd;echo \"$cmd\" ;});} &gt;&gt; $HISTORY_FILE\'','',0,'',0,'','zinnia/_entry_detail.html','entry_detail.html'),(162,'从博客访问来源看搜索引擎','337319372010102023434375',2,'2010-11-20 14:48:10',NULL,'2010-11-20 14:48:10','2014-07-13 18:57:16','一个毫无系统的博客，访问来源主要是搜索引擎，每天细看访问来源，也是一件有意思的事。<br />很久很久以前，主要访问来源是谷歌，其次是百度，大概是4:1的比例吧，<br />后来谷歌走了，主要访问来源是百度，大概90%吧，不到10%的访问来自谷歌<br />后来又有些新的来源，偶尔出来下，比如soso, bing, ucweb，不过好像没见过youdao，难道youdao不抓网易博客？<br /><br />来自搜索引擎的访问，主要原因就是关键词了，看下这个访问来源都是用什么关键词来到本博客的：<br />百度访问量最多，访问来源使用的关键词几乎什么都有，很难分析出特点来。<br />谷歌访问来源就很清楚了，基本上都是这几个关键词：ubuntu 三国杀 Pearson 相关系数 Myeclipse axis graphviz opengl ngx_xxx，很容易就能看出特点了：英文单词！谷歌很强大，不过中文搜索实在不给力啊。<br />Bing 的访问更少了，好像几个都是 Bing 的图片搜索，关键词是“红蓝3D”，Bing 的图片搜索比较强大？也许吧，来自Bing 的样本太少了。<br />soso 和 ucweb 的访问量也非常少，不过soso的博客搜索，搜索关键字\"邮箱\"，20小时前发的博客\"<a href=\"http://hankjin.blog.163.com/blog/static/337319372010101945126230\" target=\"_blank\">腾讯企业邮箱</a>\"竟然排名第二，实时性还是比较赞的。<br /><br />和自己架博客相比，使用博客服务的缺点就出来了，可定制性太底，很多日志数据实在没办法得到，处理也不方便。以上全部是无聊时手工分析出来的。',1,1,1,0,0,0,'一个毫无系统的博客，访问来源主要是搜索引擎，每天细看访问来源，也是一件有意思的事。很久很久以前，主要访问来源是谷歌，其次是百度，大概是4:1的比例吧，后来谷歌走了，主要访问来源是百度，大概90%吧，不到10%的访问来自谷歌后来又有些新的来源，偶尔出来下，比如soso, bing, ucweb，不过好像没见过youdao，难道youdao不抓网易博客？来自搜索引擎的访问，主要原因就是关键词了，看下这个访问来源都是用什么关键词来到本博客的：百度访问量最多，访问来源使用的关键词几乎什么都有，很难分析出特点来。谷歌访问来源就很清楚了，基本上都是这几个关键词：ubuntu 三国杀 Pearson 相关系数 Myeclipse axis graphviz opengl ngx_xxx，很容易就能看出特点了：英文单词！谷歌很强大，不过中文搜索实在不给力啊。Bing 的访问更少了，好像几个都是 Bing 的图片搜索，关键词是“红蓝3D”，Bing 的图片搜索比较强大？也许吧，来自Bing 的样本太少了。soso 和 ucweb 的访问量也非常少，不过soso的博客搜索，搜索关键字\"邮箱\"，20小时前发的博客\"腾讯企业邮箱\"竟然排名第二，实时性还是比较赞的。和自己架博客相比，使用博客服务的缺点就出来了，可定制性太底，很多日志数据实在没办法得到，处理也不方便。以上全部是无聊时手工分析出来的。','',0,'articles',0,'','zinnia/_entry_detail.html','entry_detail.html'),(163,'Ubuntu10.04编译太阳神三国杀','3373193720111261019231',2,'2011-02-26 22:38:02',NULL,'2011-02-26 22:38:02','2014-07-13 18:57:16','１。下载源代码<br />wget https://download.github.com/Moligaloo-QSanguosha-526d876.tar.gz<br />tar xf Moligaloo-QSanguosha-526d876.tar.gz<br /><br />２。安装相关库<br />２。１irrKlang库<br />wget http://www.ambiera.at/downloads/irrKlang-1.3.0b.zip<br />unzip irrKlang-1.3.0b.zip<br />sudo cp irrKlang-1.3.0/bin/linux-gcc/*.so /usr/local/lib<br />２。２lua库<br /> sudo apt-get install liblua5.1-dev<br /> sudo ln -s /usr/lib/liblua5.1.so /usr/lib/liblua.so<br />２。３plib库<br />预备工作<br />sudo apt-get install freeglut3-dev libglut3-dev<br />sudo apt-get install libxmu-dev libxmu6 <br />sudo ln -s /usr/lib/libXi.so.6 /usr/lib/libXi.so<br />安装库<br />wget http://cdnetworks-kr-1.dl.sourceforge.net/project/plib/plib/1.8.5-rc1/plib-1.8.5-rc1.tar.gz<br />tar xf plib-1.8.5-rc1.tar.gz<br />cd plib-1.8.5-rc1<br />./configure<br />make<br />sudo make install<br />cd ..<br /><br />３。编译<br />sudo apt-get install qt4-dev-tools qtcreator swig<br />sudo apt-get install liblua5.1-sql-sqlite3-dev<br />cd Moligaloo-QSanguosha-526d876<br />cd swig<br />swig -c++ -lua sanguosha.i<br />cd ..<br />qmake<br />make<br />会出现下面的错误，只要找到代码处注释掉该行即可，因为Ubuntu10.04的Qt是4.6的，没有setPlaceholderText。<br /> src/roomscene.cpp:172: error: ‘class QLineEdit’ has no member named ‘setPlaceholderText’<br /> src/server.cpp:274: error: ‘class QLineEdit’ has no member named ‘setPlaceholderText’<br /> ./ui_connectiondialog.h:158: error: ‘class QLineEdit’ has no member named ‘setPlaceholderText’<br /> ./ui_configdialog.h:394: error: ‘class QLineEdit’ has no member named ‘setPlaceholderText’<br /> ./ui_configdialog.h:396: error: ‘class QLineEdit’ has no member named ‘setPlaceholderText’<br /> ./ui_configdialog.h:398: error: ‘class QLineEdit’ has no member named ‘setPlaceholderText’<br /> ./ui_configdialog.h:400: error: ‘class QLineEdit’ has no member named ‘setPlaceholderText’<br />编译完成后<br />./QSanguosha<br /><br />４。中文<br />  打开Applications-&gt;Programming-&gt;Qt 4 Linguist<br />  用Linguist打开sanguosha.ts，然后在Linguist中File-&gt;Release<br />  然后再启动QSanguosha就是中文的了',1,1,1,0,0,0,'１。下载源代码wget https://download.github.com/Moligaloo-QSanguosha-526d876.tar.gztar xf Moligaloo-QSanguosha-526d876.tar.gz２。安装相关库２。１irrKlang库wget http://www.ambiera.at/downloads/irrKlang-1.3.0b.zipunzip irrKlang-1.3.0b.zipsudo cp irrKlang-1.3.0/bin/linux-gcc/*.so /usr/local/lib２。２lua库 sudo apt-get install liblua5.1-dev sudo ln -s /usr/lib/liblua5.1.so /usr/lib/liblua.so２。３plib库预备工作sudo apt-get install freeglut3-dev libglut3-devsudo apt-get install libxmu-dev libxmu6 sudo ln -s /usr/lib/libXi.so.6 /usr/lib/libXi.so安装库wget http://cdnetworks-kr-1.dl.sourceforge.net/project/plib/plib/1.8.5-rc1/plib-1.8.5-rc1.tar.gztar xf plib-1.8.5-rc1.tar.gzcd plib-1.8.5-rc1./configuremakesudo make installcd ..３。编译sudo apt-get install qt4-dev-tools qtcreator swigsudo apt-get install liblua5.1-sql-sqlite3-devcd Moligaloo-QSanguosha-526d876cd swigswig -c++ -lua sanguosha.icd ..qmakemake会出现下面的错误，只要找到代码处注释掉该行即可，因为Ubuntu10.04的Qt是4.6的，没有setPlaceholderText。 src/roomscene.cpp:172: error: ‘class QLineEdit’ has no member named ‘setPlaceholderText’ src/server.cpp:274: error: ‘class QLineEdit’ has no member named ‘setPlaceholderText’ ./ui_connectiondialog.h:158: error: ‘class QLineEdit’ has no member named ‘setPlaceholderText’ ./ui_configdialog.h:394: error: ‘class QLineEdit’ has no member named ‘setPlaceholderText’ ./ui_configdialog.h:396: error: ‘class QLineEdit’ has no member named ‘setPlaceholderText’ ./ui_configdialog.h:398: error: ‘class QLineEdit’ has no member named ‘setPlaceholderText’ ./ui_configdialog.h:400: error: ‘class QLineEdit’ has no member named ‘setPlaceholderText’编译完成后./QSanguosha４。中文  打开Applications-&gt;Programming-&gt;Qt 4 Linguist  用Linguist打开sanguosha.ts，然后在Linguist中File-&gt;Release  然后再启动QSanguosha就是中文的了','',0,'',0,'','zinnia/_entry_detail.html','entry_detail.html'),(164,'putty自动登录','33731937201011424503',2,'2010-02-01 16:02:04',NULL,'2010-02-01 16:02:04','2014-07-13 18:57:16','最烦的就是重复，每天用putty登录，Linux密码最初是8位，后来变为6位，后来变为3位。<br />没有最懒，只有更懒，putty可以自动登录的。使用puttygen可以无需输入帐户和密码登录<br />1. 下载puttygen.exe<br />http://the.earth.li/~sgtatham/putty/latest/x86/puttygen.exe<br />2. 使用puttygen产生公钥和私钥<br />公钥保存到服务器的在~/.ssh/authorized_keys中<br />私钥保存在本地<br />3. 设置putty.exe<br />在Connection-&gt;Data中设置用户名<br />在Connection-&gt;SSH-&gt;Auth中设置公钥文件<br />4. 保存session, 下次双击就可自动登录<br /><br />第二种方法<br />putty.exe -pw &lt;password&gt; -P &lt;port&gt; &lt;user&gt;@&lt;ip&gt;<br />例如：<br />putty.exe -pw mysecret -P 22 usera@ubuntu.unix-center.net<div>putty.exe -load config1<br /><br /></div>',1,1,1,0,0,0,'最烦的就是重复，每天用putty登录，Linux密码最初是8位，后来变为6位，后来变为3位。没有最懒，只有更懒，putty可以自动登录的。使用puttygen可以无需输入帐户和密码登录1. 下载puttygen.exehttp://the.earth.li/~sgtatham/putty/latest/x86/puttygen.exe2. 使用puttygen产生公钥和私钥公钥保存到服务器的在~/.ssh/authorized_keys中私钥保存在本地3. 设置putty.exe在Connection-&gt;Data中设置用户名在Connection-&gt;SSH-&gt;Auth中设置公钥文件4. 保存session, 下次双击就可自动登录第二种方法putty.exe -pw &lt;password&gt; -P &lt;port&gt; &lt;user&gt;@&lt;ip&gt;例如：putty.exe -pw mysecret -P 22 usera@ubuntu.unix-center.netputty.exe -load config1','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(165,'使用jQuery通过select控制显示的内容','33731937200931224219806',2,'2009-04-12 02:42:19',NULL,'2009-04-12 02:42:19','2014-07-13 18:57:16','选中配件时显示&lt;div id=\"parts\"&gt;, 选中步骤时显示&lt;div id=\"step\"&gt;, 代码如下:<br /><br />选择 : &lt;html:select property=\"type\"&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;html:option value=\"S\"&gt;步骤&lt;/html:option&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;html:option value=\"P\"&gt;配件&lt;/html:option&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/html:select&gt;<br />&lt;div id=\"parts\"&gt;Name:&lt;input type=\"text\"/&gt;&lt;/div&gt;<br />&lt;div id=\"step\"&gt;Welcome&lt;/div&gt;<br />&lt;script language=\"javscript\"&gt;<br />$(document).ready(function(){<br />&nbsp;&nbsp;&nbsp; $(\"#parts\").hide();//默认是步骤<br />&nbsp;&nbsp;&nbsp; $(\"select[@name=type]\").change(function(){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; $(\"#parts\").hide();<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; $(\"#step\").hide();<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; switch($(this).val()){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; case \'P\':<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; $(\"#parts\").show();<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; default:<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; $(\"#step\").show();<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; });<br />});<br />&lt;/script&gt;<br />',1,1,1,0,0,0,'选中配件时显示&lt;div id=\"parts\"&gt;, 选中步骤时显示&lt;div id=\"step\"&gt;, 代码如下:选择 : &lt;html:select property=\"type\"&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;html:option value=\"S\"&gt;步骤&lt;/html:option&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;html:option value=\"P\"&gt;配件&lt;/html:option&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/html:select&gt;&lt;div id=\"parts\"&gt;Name:&lt;input type=\"text\"/&gt;&lt;/div&gt;&lt;div id=\"step\"&gt;Welcome&lt;/div&gt;&lt;script language=\"javscript\"&gt;$(document).ready(function(){&nbsp;&nbsp;&nbsp; $(\"#parts\").hide();//默认是步骤&nbsp;&nbsp;&nbsp; $(\"select[@name=type]\").change(function(){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; $(\"#parts\").hide();&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; $(\"#step\").hide();&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; switch($(this).val()){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; case \'P\':&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; $(\"#parts\").show();&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; default:&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; $(\"#step\").show();&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; });});&lt;/script&gt;','',0,'\"web skills\"',0,'','zinnia/_entry_detail.html','entry_detail.html'),(166,'一个HR给应届毕业生的面试建议 后悔看到的太晚了(z)','33731937200814101417937',2,'2008-02-04 10:14:17',NULL,'2008-02-04 10:14:17','2014-07-13 18:57:16','应届生在参加面试时如何有技巧的回答一系列的问题。<br /><br />　　开始之前务必记住：<br /><br />　　黄金法则：<font color=\"#ff0000\">80/20---你要承担起80%的谈话而面试官只会说20%。<br /></font><br />　　白金法则：<font color=\"#ff0000\">你必须试着控制面试的节奏和话题。<br /></font><br />　　钻石法则：<font color=\"#ff0000\">对于没有把握的问题，抛回给面试官。<br /></font><br />　　1．在一分钟内介绍一下你自己<br /><br />　　这似乎是必答题。不要以为这很容易。如果你用一分钟来重复你的简历，恭喜你，你的印象加分没有了！建议你最多用二十秒钟介绍自己的姓名、学校、专业。然后话锋一转，引出自己的优势或强项。一定要在最短时间内激发起面试官对你的好感，或者至少是兴趣。<br /><br />　　成功的模式可以是：我叫XXX，英文名字XXX，XX省XX市人，今年6月将从XX学校XX专业本科（专科）毕业。除了简历上您看到的介绍，我愿意特别说一下我在XXX方面的特长/我最大的特点是……（给出事例）。正是基于对自己这方面的自信，使我有勇气来应聘贵公司的XXX这一职位。（看表）一分钟到了，希望我没有超时。（很阳光的微笑）<br /><br />　　如果面试官不是EQ太低，你的最后一句话应该会使他放松和微笑。资历嫩一点的还会接着问：“为什么你会这么认为呢？”如果他真的这么问你，Bingo!你完全有机会操纵这次面试！<br /><br />　　2．应届生经常会被问到的一个问题是：你为什么会选择你目前学习的专业呢？千万当心，这个问题的目的是考察你的DecisionQuality这一项胜任力，所以不要简单的说“感兴趣”或者“就业前景乐观”等。给大家一个成功的范例（同样适用于诸如‘您所做过的一个成功/最大的决定是什么”）：<br /><br />　　问：张先生，您为什么会选择财务专业呢？<br /><br />　　答：的确，财务已经连续多年成为高校热门专业，这造成了就业时无可避免的激烈竞争。可当初我选择财务专业时并不是单纯因为它的热门程度。我早就把就业目标锁定在苏州工业园区的外资企业。根据我高三时搜集到的统计资料，园区当时有外企XXX家，而且每年以XXX%的速度在增加。以每个公司财务部至少5个人计算（总账，应收应付，税务，出纳再加上一个经理），加上园区的平均离职率是15%，则在我毕业时，可以有XXX个空缺。我毕业那年应该全国有XX相关专业的毕业生。其中可能有10%会瞄准苏州，而我填报的苏州大学在当地口碑尚可，属于中等偏上。那么，有1/2的对手能被我淘汰。再加上我就在苏州本地学习，四年中可以更早的寻求机会，所以，我很有信心的选择了这个专业。<br /><br />　　其实，没有哪个面试官会相信你真的作过如此缜密的调查分析，但你已经展示了你做决策时的思路，所以可以加分。<br /><br />　　记住：所有的回答要符合你专业的特点。不要说得太到位，可以自圆其说就行了，不然，他们会觉得你要么太虚伪，要么太狂妄。还有，应该表示对自己的专业的确感兴趣，或者增加点戏剧效果：我本来理解的财务管理就是管账，所以开始的时候还真后悔了一阵，直到大三时开始了在企业的实践，才有了改观，并真正喜欢上了我的专业。这样显得真实可信，更重要的是，很自然的由你引导到准备好的问题：实习的收获。<br /><br />　　3．为了考察您LearningontheFly这一条胜任力，通常我们会问您在实习期间的收获。此时，不要夸大自己的成绩，谦虚一点。还有，不妨说一下自己的失误（不用怕，毕竟你是在实习）。记住：详细说明当时的情况（Situation），你要达到的目的（Task），你采取了哪些步骤（Actions），事情的结果（Result），还有你得到的经验教训(Lessonlearned)以及后来怎样运用到工作中避免犯类似的错误。最后做出总结：原来书本上的知识要能够在工作中熟练运用，这期间还有很长的一段路要走。还可以说：回到学校后，我对自己的实习经历作了一番总结，发现自己在XXX方面还需要加强。所以，我很注意利用大学的最后一个学期来弥补这一不足。现在，我对自己很有信心，如果时间能够倒流，我相信我能做到更好。<br /><br />　　4．你有过和别人合作的经历吗？（千万要回答“有”）那么，在这过程中，你是如何处理意见分歧的呢？现在是考察你的ConflictsManagement。现在的绝大部分企业都不欣赏没有原则的老好人。所以，你要把自己包装得强势一些。<br /><br />　　我本人比较满意的回答：<br /><br />　　每个人在团队中都应该可以自由坦诚地发表意见，我会非常认真的聆听，分析；但对于自己的意见我不会没有原则的轻易放弃。民主过后还需要集中。我是学校英语俱乐部的主席，在组织校际年度联欢时，有两个干事的意见和我不一致。（停顿一下，让面试官记住你的这个闪光点）我和他们开了会。大家都阐述了各自的理由。很遗憾，我仍然没有说服这两人。在这种情况下，我感谢他们的积极参与，但表示仍然会采用我的方案。我的理念是：Meeting不等于Voting，完全不需要少数服从多数；我是负责人，我相信自己有能力采取最佳方案；假如失败了，我也会承担主要责任。而如果我是团队的普通一员，我会保留自己的意见，但还是认真执行管理者已经做出的决策。当然，学校毕竟不同于公司，情况会更加复杂，但我坚信，只要遵循“对事不对人”的原则，任何问题都可以得到解决。<br /><br />　　在回答时，一定要眼睛看着面试官，微笑，以冲淡你的咄咄逼人。<br /><br />　　如果你实在没有把握，可以把问题抛还给面试官，试举一例（还是同样的问题）：<br /><br />　　问：你是如何处理意见分歧的呢？<br /><br />　　答：您问的恰好也是我最困惑的一点，而学校里老师从来不给我们这样的指点。一方面，我不想做没有原则的老好人，另一方面，大家都是朝夕相处的同学，我不想让他们觉得我盛气凌人。我当时是这样做的：……可一直到现在，我都不知道是否作的正确，也许我可以从您那里得到一些指教，您说我当时这么做有问题吗？<br /><br />　　如果对方马上对你言传身教，那他铁定是菜鸟，你不用紧张了，因为，合理的反应应该是不置可否的说：其实这个问题永远不会有标准答案。<br /><br />　　5．有人问起多个问题同时出现时，应该如何解决。这就是PrioritySetting的胜任力。应对思路是把所有要处理的事情按轻重缓急分成4个象限：重要而且紧急；重要但不紧急；不重要但紧急；不重要也不紧急。但记住，不要直接叙述这个思路，这会让人觉得你不像应届生。还是通过举例说明比较好。我面试过的一个比较好的例子：我在大三时正好要复习准备英语6级考试，这时有机会可以去一家企业做兼职部门助理，但需要每周花三个半天。同时，每月一次的英语沙龙活动要组织安排，还要(不好意思地说)抽空陪陪女朋友（最后一点很重要，可以活跃气氛，还为下面制造了一个不重要不紧急的事例）。我当时利用没有课程的下午去公司工作，一般要到5点半回学校。在公车上的30分钟正好用来总结当天在公司的收获和需要了解的知识。在食堂与女朋友吃晚饭，六点半去教室自习，到十点回宿舍。英语沙龙的工作委派给大二的两个干事，他们每天会到自习教室找我谈10分钟，我会给他们一些建议。这样，我没有花太多的精力在沙龙活动上，可对整个过程都有了解和掌控。后来6级考试顺利通过，那家公司对我的工作评价很高，我也学了很多东西；英语沙龙的活动如期举行，我和女朋友也没有疏远。<br /><br />　　这同时展示了三条胜任力：prioritysetting；delegation和work/lifebalance<br /><br />　　作为应届生，在面试前，应该了解一下外企的部门架构。以欧美企业为例，一般部门内的职位从低到高依次为：助理（如果是本科生，有时候可以跳过）---专员---资深专员---主管---资深主管---部门副经理/SectionManager---部门经理。一般，主管要求有5年以上的相关工作经验，这是一个分水岭。好，回过来谈谈面试时如何回答关于职业生涯规划问题。其实,这种问题并不需要你回答得无懈可击,这也办不到---你说得通俗了,认为你胸无大志;回答太专业了,又觉得你好高鹜远.<br /><br />　　回答这类问题，有以下几点可以帮你加分：<br /><br />　　1．设定一个与自己专业相关的长远目标；这个目标要和公司的工作有关但不要局限在企业内部（因为空间有限，会遭遇许多太过于细节的问题）。例如，HR专业的学生可以说，自己的目标是在35岁之前，也就是10年内，成为一名优秀的人才测评专家或者资深企业人力资源顾问；工程技术专业的学生，可以成为精益生产专家或者黑带大师；IT专业则可以在信息资源整合和ERP解决方案方面成为专家，等等。注意，要着重在你想做些什么，而不是你想爬到什么级别。<br /><br />　　2．把这个目标分解，以1年，3年，5年，10年的进度，逐步推进。这个就要靠你自己编了，恕在下不能一一举例。记住，每个阶段都要说明你能为公司做出怎样的贡献，你能得到怎样的提高，这与你长远目标的关系在哪里。此时，可以适当联系到刚刚提到的部门架构。<br /><br />　　3．在此过程中，除了自己想办法不断充电，还要说一下你希望公司可以给你怎样的帮助。比如能够有岗位轮换的机会；或者能够参与各种项目等。不要提希望公司会给你培训，送你出国，给你报销学费，要表现得自己希望在实践中成长。<br /><br />　　4．强调自己的稳定的心态。你可以这样说：“中国人最讲究‘名正言顺’或者‘不在其位，不谋其职’，所以很多人都认为，只有给了主管的职位，才能运用主管的权利，发挥主管的作用。我的理念和别人不同，我认为恰恰相反，当你展示出了主管该具有的能力，能完成更多的工作，公司自然会考虑对你的职位进行调整。在没有足够的权力时，要使用自己的影响力，所以我鄙视那些成天想着晋升却没有任何建树的人。”这样子，你的自我包装就成功了一大半。<br /><br />　　5．最后要注意，在回答类似问题时，不要显得太胸有成竹。偶尔显示出涉世未深可以让面试官觉得你还有学生的单纯。“这个问题很大，我在学校里也曾经断断续续的考虑过，现在我简单的讲一下，可能会显得一厢情愿，希望得到您的指教”这样的一个缓冲在许多时候很管用哦。（还记得钻石法则吗？）<br /><br />　　最后想提一下面试的肢体语言。我面试过的应届生应该没有1000也有800了吧。我发现几乎90%的人在入座后都采用一种非常闭合的姿势，很拘谨。这不能说是坏事，但的确会让面试开始的破冰显得艰难。现在，闭上眼睛，想象一对亲密情侣在烛光下甜蜜交谈，他们的姿势应该是几乎一样的；你们平时和好朋友促膝长谈，相信采用的也是相同的姿势；电视上的国家元首交谈时，绝大多数情况两人的姿势也相同或接近。这是因为：两个人如果在交谈过程中采用同一种姿势，则双方在潜意识中会有彼此认同的感觉。我们把这样的姿势一致称为Rapport。我本人非常注重采用Rapport来增进沟通效果（特别是，如果有人盛气凌人的叉腰对我说话，不论他的职位高低，我也会叉腰和他说话---输人不输阵：））<br /><br />　　还有，面试时，手势的运用很重要。特别是回答较大的问题时，用手虚拟的在桌面上列出1，2，3，这样会让人觉得你很有条理，而且不会显得很稚嫩。而说到我会怎样怎样时，把手掌放在心口，真的有强化的效果。<br /><br />　　一句话：面试时，要表现的有教养，容易',1,1,1,0,0,0,'应届生在参加面试时如何有技巧的回答一系列的问题。　　开始之前务必记住：　　黄金法则：80/20---你要承担起80%的谈话而面试官只会说20%。　　白金法则：你必须试着控制面试的节奏和话题。　　钻石法则：对于没有把握的问题，抛回给面试官。　　1．在一分钟内介绍一下你自己　　这似乎是必答题。不要以为这很容易。如果你用一分钟来重复你的简历，恭喜你，你的印象加分没有了！建议你最多用二十秒钟介绍自己的姓名、学校、专业。然后话锋一转，引出自己的优势或强项。一定要在最短时间内激发起面试官对你的好感，或者至少是兴趣。　　成功的模式可以是：我叫XXX，英文名字XXX，XX省XX市人，今年6月将从XX学校XX专业本科（专科）毕业。除了简历上您看到的介绍，我愿意特别说一下我在XXX方面的特长/我最大的特点是……（给出事例）。正是基于对自己这方面的自信，使我有勇气来应聘贵公司的XXX这一职位。（看表）一分钟到了，希望我没有超时。（很阳光的微笑）　　如果面试官不是EQ太低，你的最后一句话应该会使他放松和微笑。资历嫩一点的还会接着问：“为什么你会这么认为呢？”如果他真的这么问你，Bingo!你完全有机会操纵这次面试！　　2．应届生经常会被问到的一个问题是：你为什么会选择你目前学习的专业呢？千万当心，这个问题的目的是考察你的DecisionQuality这一项胜任力，所以不要简单的说“感兴趣”或者“就业前景乐观”等。给大家一个成功的范例（同样适用于诸如‘您所做过的一个成功/最大的决定是什么”）：　　问：张先生，您为什么会选择财务专业呢？　　答：的确，财务已经连续多年成为高校热门专业，这造成了就业时无可避免的激烈竞争。可当初我选择财务专业时并不是单纯因为它的热门程度。我早就把就业目标锁定在苏州工业园区的外资企业。根据我高三时搜集到的统计资料，园区当时有外企XXX家，而且每年以XXX%的速度在增加。以每个公司财务部至少5个人计算（总账，应收应付，税务，出纳再加上一个经理），加上园区的平均离职率是15%，则在我毕业时，可以有XXX个空缺。我毕业那年应该全国有XX相关专业的毕业生。其中可能有10%会瞄准苏州，而我填报的苏州大学在当地口碑尚可，属于中等偏上。那么，有1/2的对手能被我淘汰。再加上我就在苏州本地学习，四年中可以更早的寻求机会，所以，我很有信心的选择了这个专业。　　其实，没有哪个面试官会相信你真的作过如此缜密的调查分析，但你已经展示了你做决策时的思路，所以可以加分。　　记住：所有的回答要符合你专业的特点。不要说得太到位，可以自圆其说就行了，不然，他们会觉得你要么太虚伪，要么太狂妄。还有，应该表示对自己的专业的确感兴趣，或者增加点戏剧效果：我本来理解的财务管理就是管账，所以开始的时候还真后悔了一阵，直到大三时开始了在企业的实践，才有了改观，并真正喜欢上了我的专业。这样显得真实可信，更重要的是，很自然的由你引导到准备好的问题：实习的收获。　　3．为了考察您LearningontheFly这一条胜任力，通常我们会问您在实习期间的收获。此时，不要夸大自己的成绩，谦虚一点。还有，不妨说一下自己的失误（不用怕，毕竟你是在实习）。记住：详细说明当时的情况（Situation），你要达到的目的（Task），你采取了哪些步骤（Actions），事情的结果（Result），还有你得到的经验教训(Lessonlearned)以及后来怎样运用到工作中避免犯类似的错误。最后做出总结：原来书本上的知识要能够在工作中熟练运用，这期间还有很长的一段路要走。还可以说：回到学校后，我对自己的实习经历作了一番总结，发现自己在XXX方面还需要加强。所以，我很注意利用大学的最后一个学期来弥补这一不足。现在，我对自己很有信心，如果时间能够倒流，我相信我能做到更好。　　4．你有过和别人合作的经历吗？（千万要回答“有”）那么，在这过程中，你是如何处理意见分歧的呢？现在是考察你的ConflictsManagement。现在的绝大部分企业都不欣赏没有原则的老好人。所以，你要把自己包装得强势一些。　　我本人比较满意的回答：　　每个人在团队中都应该可以自由坦诚地发表意见，我会非常认真的聆听，分析；但对于自己的意见我不会没有原则的轻易放弃。民主过后还需要集中。我是学校英语俱乐部的主席，在组织校际年度联欢时，有两个干事的意见和我不一致。（停顿一下，让面试官记住你的这个闪光点）我和他们开了会。大家都阐述了各自的理由。很遗憾，我仍然没有说服这两人。在这种情况下，我感谢他们的积极参与，但表示仍然会采用我的方案。我的理念是：Meeting不等于Voting，完全不需要少数服从多数；我是负责人，我相信自己有能力采取最佳方案；假如失败了，我也会承担主要责任。而如果我是团队的普通一员，我会保留自己的意见，但还是认真执行管理者已经做出的决策。当然，学校毕竟不同于公司，情况会更加复杂，但我坚信，只要遵循“对事不对人”的原则，任何问题都可以得到解决。　　在回答时，一定要眼睛看着面试官，微笑，以冲淡你的咄咄逼人。　　如果你实在没有把握，可以把问题抛还给面试官，试举一例（还是同样的问题）：　　问：你是如何处理意见分歧的呢？　　答：您问的恰好也是我最困惑的一点，而学校里老师从来不给我们这样的指点。一方面，我不想做没有原则的老好人，另一方面，大家都是朝夕相处的同学，我不想让他们觉得我盛气凌人。我当时是这样做的：……可一直到现在，我都不知道是否作的正确，也许我可以从您那里得到一些指教，您说我当时这么做有问题吗？　　如果对方马上对你言传身教，那他铁定是菜鸟，你不用紧张了，因为，合理的反应应该是不置可否的说：其实这个问题永远不会有标准答案。　　5．有人问起多个问题同时出现时，应该如何解决。这就是PrioritySetting的胜任力。应对思路是把所有要处理的事情按轻重缓急分成4个象限：重要而且紧急；重要但不紧急；不重要但紧急；不重要也不紧急。但记住，不要直接叙述这个思路，这会让人觉得你不像应届生。还是通过举例说明比较好。我面试过的一个比较好的例子：我在大三时正好要复习准备英语6级考试，这时有机会可以去一家企业做兼职部门助理，但需要每周花三个半天。同时，每月一次的英语沙龙活动要组织安排，还要(不好意思地说)抽空陪陪女朋友（最后一点很重要，可以活跃气氛，还为下面制造了一个不重要不紧急的事例）。我当时利用没有课程的下午去公司工作，一般要到5点半回学校。在公车上的30分钟正好用来总结当天在公司的收获和需要了解的知识。在食堂与女朋友吃晚饭，六点半去教室自习，到十点回宿舍。英语沙龙的工作委派给大二的两个干事，他们每天会到自习教室找我谈10分钟，我会给他们一些建议。这样，我没有花太多的精力在沙龙活动上，可对整个过程都有了解和掌控。后来6级考试顺利通过，那家公司对我的工作评价很高，我也学了很多东西；英语沙龙的活动如期举行，我和女朋友也没有疏远。　　这同时展示了三条胜任力：prioritysetting；delegation和work/lifebalance　　作为应届生，在面试前，应该了解一下外企的部门架构。以欧美企业为例，一般部门内的职位从低到高依次为：助理（如果是本科生，有时候可以跳过）---专员---资深专员---主管---资深主管---部门副经理/SectionManager---部门经理。一般，主管要求有5年以上的相关工作经验，这是一个分水岭。好，回过来谈谈面试时如何回答关于职业生涯规划问题。其实,这种问题并不需要你回答得无懈可击,这也办不到---你说得通俗了,认为你胸无大志;回答太专业了,又觉得你好高鹜远.　　回答这类问题，有以下几点可以帮你加分：　　1．设定一个与自己专业相关的长远目标；这个目标要和公司的工作有关但不要局限在企业内部（因为空间有限，会遭遇许多太过于细节的问题）。例如，HR专业的学生可以说，自己的目标是在35岁之前，也就是10年内，成为一名优秀的人才测评专家或者资深企业人力资源顾问；工程技术专业的学生，可以成为精益生产专家或者黑带大师；IT专业则可以在信息资源整合和ERP解决方案方面成为专家，等等。注意，要着重在你想做些什么，而不是你想爬到什么级别。　　2．把这个目标分解，以1年，3年，5年，10年的进度，逐步推进。这个就要靠你自己编了，恕在下不能一一举例。记住，每个阶段都要说明你能为公司做出怎样的贡献，你能得到怎样的提高，这与你长远目标的关系在哪里。此时，可以适当联系到刚刚提到的部门架构。　　3．在此过程中，除了自己想办法不断充电，还要说一下你希望公司可以给你怎样的帮助。比如能够有岗位轮换的机会；或者能够参与各种项目等。不要提希望公司会给你培训，送你出国，给你报销学费，要表现得自己希望在实践中成长。　　4．强调自己的稳定的心态。你可以这样说：“中国人最讲究‘名正言顺’或者‘不在其位，不谋其职’，所以很多人都认为，只有给了主管的职位，才能运用主管的权利，发挥主管的作用。我的理念和别人不同，我认为恰恰相反，当你展示出了主管该具有的能力，能完成更多的工作，公司自然会考虑对你的职位进行调整。在没有足够的权力时，要使用自己的影响力，所以我鄙视那些成天想着晋升却没有任何建树的人。”这样子，你的自我包装就成功了一大半。　　5．最后要注意，在回答类似问题时，不要显得太胸有成竹。偶尔显示出涉世未深可以让面试官觉得你还有学生的单纯。“这个问题很大，我在学校里也曾经断断续续的考虑过，现在我简单的讲一下，可能会显得一厢情愿，希望得到您的指教”这样的一个缓冲在许多时候很管用哦。（还记得钻石法则吗？）　　最后想提一下面试的肢体语言。我面试过的应届生应该没有1000也有800了吧。我发现几乎90%的人在入座后都采用一种非常闭合的姿势，很拘谨。这不能说是坏事，但的确会让面试开始的破冰显得艰难。现在，闭上眼睛，想象一对亲密情侣在烛光下甜蜜交谈，他们的姿势应该是几乎一样的；你们平时和好朋友促膝长谈，相信采用的也是相同的姿势；电视上的国家元首交谈时，绝大多数情况两人的姿势也相同或接近。这是因为：两个人如果在交谈过程中采用同一种姿势，则双方在潜意识中会有彼此认同的感觉。我们把这样的姿势一致称为Rapport。我本人非常注重采用Rapport来增进沟通效果（特别是，如果有人盛气凌人的叉腰对我说话，不论他的职位高低，我也会叉腰和他说话---输人不输阵：））　　还有，面试时，手势的运用很重要。特别是回答较大的问题时，用手虚拟的在桌面上列出1，2，3，这样会让人觉得你很有条理，而且不会显得很稚嫩。而说到我会怎样怎样时，把手掌放在心口，真的有强化的效果。　　一句话：面试时，要表现的有教养，容易','',0,'mood',0,'','zinnia/_entry_detail.html','entry_detail.html'),(167,'两步制作N合一启动U盘:DOS+WinPE+CDLinux+Windows&amp;Ubuntu安装盘','33731937200932093721350',2,'2009-04-20 09:37:21',NULL,'2009-04-20 09:37:21','2014-07-13 18:57:16','<a href=\"/static/163/3109735542699863696.jpg\" target=\"_blank\"><img alt=\"两步制作N合一启动U盘:DOS+WinPE+CDLinux+WindowsUbuntu安装盘 - hankjin - 乱-Blog\" src=\"/static/163/3109735542699863696.jpg\" style=\"width: 523px; height: 391px;\" title=\"两步制作N合一启动U盘:DOS+WinPE+CDLinux+WindowsUbuntu安装盘 - hankjin - 乱-Blog\" /></a><br />两步安装<br />1. 从<a href=\"http://dl.getdropbox.com/u/194885/Udisk.rar\" rel=\"nofollow\" target=\"_blank\">这里</a>下载压缩包, 解压缩到U盘根目录下<br />3. 运行setup/grubinst_gui.exe, 如下图所示<br /><a href=\"/static/163/3417106217267928217.jpg\" target=\"_blank\"><img alt=\"两步制作N合一启动U盘:DOS+WinPE+CDLinux+WindowsUbuntu安装盘 - hankjin - 乱-Blog\" src=\"/static/163/3417106217267928217.jpg\" title=\"两步制作N合一启动U盘:DOS+WinPE+CDLinux+WindowsUbuntu安装盘 - hankjin - 乱-Blog\" /></a><br />点击Install, 安装完成!!!!!!<br />现在可以启动到DOS, WinPE(电脑狂人版), CDLinux(0.9.2), Ghost(11.0.2)<br />只要从网上下载ubuntu-8.04-server-i386.iso, 放到setup目录下,就可以作为Ubuntu的安装盘<br />只要从网上下载CDlinux-0.9.2.iso, 放到setup目录下,就可以作为CDLinux的光盘启动<br />只要从网上下载winxp_en.iso, 放到setup目录下,就可以作为英文版Windows的安装盘<br />注: 要做Windows和Ubuntu的安装盘, 需要1G以上内存.<br />',1,1,1,0,0,0,'两步安装1. 从这里下载压缩包, 解压缩到U盘根目录下3. 运行setup/grubinst_gui.exe, 如下图所示点击Install, 安装完成!!!!!!现在可以启动到DOS, WinPE(电脑狂人版), CDLinux(0.9.2), Ghost(11.0.2)只要从网上下载ubuntu-8.04-server-i386.iso, 放到setup目录下,就可以作为Ubuntu的安装盘只要从网上下载CDlinux-0.9.2.iso, 放到setup目录下,就可以作为CDLinux的光盘启动只要从网上下载winxp_en.iso, 放到setup目录下,就可以作为英文版Windows的安装盘注: 要做Windows和Ubuntu的安装盘, 需要1G以上内存.','',0,'os',0,'','zinnia/_entry_detail.html','entry_detail.html'),(168,'scp的限速功能(流量控制)','3373193720118894730445',2,'2011-09-08 09:47:30',NULL,'2011-09-08 09:47:30','2014-07-13 18:57:16','scp 的 -l 参数可以控制传输速度,如 scp -l 5000 hankjin@localhost:/tmp/10G .<br /><br />scp 的限速用两个函数实现<br />bandwidth_limit_init()<br />bandwidth_limit()<br />第一个函数设置传输速度的阈值<br />第二个函数限制传输速度<br />循环写完1s内需要的流量数之后, 计算耗费的时间和需要的时间差, 然后动态调整下1s的流量.<br />这样带来两个问题:<br />1. 第1s内的速度较低,导致第2s的速度飙高.<br />2. 流量不平滑, 例如10M/s的速度, 实际在前0.1s内完成,然后剩下0.9s空闲.<br />在正常应用下, 这两个问题都不是问题, 也满足多数限速要求.<br />但是在某些条件下, 这两个问题显得scp的速度控制不够精细.<br /><br />1. 如果要保证其它服务的稳定性, 这时流量控制就要保证时刻都不高于指定速度.<br />2. 如果流量不平滑, 也可能影响服务, 前0.1s和服务抢带宽, 后0.9s带宽空闲. 对实时性要求不高的应用没有问题, 但是在要求精度小于0.1s的服务, 这个问题也需要解决.<br /><br />满足这两个条件的流量控制伪码如下:<br />total=0, total_time=0, last_time=0<br />WHILE TRUE:<br />&nbsp; read nbyte<br />&nbsp; total_time+=now-last_time&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #计算消耗的时间<br />&nbsp; last_time=now&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp; total+=n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #计算传输的数据量<br />&nbsp; IF total/rate &gt; total_time:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #如果传输太快, 控制一下<br />&nbsp;&nbsp;&nbsp; sleep(total/rate - total_time)<br />&nbsp; FI<br />&nbsp; if total &gt; rate:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #1s为粒度控制, 避免前1s的低导致后1s的飙高<br />&nbsp;&nbsp;&nbsp;&nbsp; total=0, total_time=0',1,1,1,0,0,0,'scp 的 -l 参数可以控制传输速度,如 scp -l 5000 hankjin@localhost:/tmp/10G .scp 的限速用两个函数实现bandwidth_limit_init()bandwidth_limit()第一个函数设置传输速度的阈值第二个函数限制传输速度循环写完1s内需要的流量数之后, 计算耗费的时间和需要的时间差, 然后动态调整下1s的流量.这样带来两个问题:1. 第1s内的速度较低,导致第2s的速度飙高.2. 流量不平滑, 例如10M/s的速度, 实际在前0.1s内完成,然后剩下0.9s空闲.在正常应用下, 这两个问题都不是问题, 也满足多数限速要求.但是在某些条件下, 这两个问题显得scp的速度控制不够精细.1. 如果要保证其它服务的稳定性, 这时流量控制就要保证时刻都不高于指定速度.2. 如果流量不平滑, 也可能影响服务, 前0.1s和服务抢带宽, 后0.9s带宽空闲. 对实时性要求不高的应用没有问题, 但是在要求精度小于0.1s的服务, 这个问题也需要解决.满足这两个条件的流量控制伪码如下:total=0, total_time=0, last_time=0WHILE TRUE:&nbsp; read nbyte&nbsp; total_time+=now-last_time&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #计算消耗的时间&nbsp; last_time=now&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; total+=n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #计算传输的数据量&nbsp; IF total/rate &gt; total_time:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #如果传输太快, 控制一下&nbsp;&nbsp;&nbsp; sleep(total/rate - total_time)&nbsp; FI&nbsp; if total &gt; rate:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #1s为粒度控制, 避免前1s的低导致后1s的飙高&nbsp;&nbsp;&nbsp;&nbsp; total=0, total_time=0','',0,'network',0,'','zinnia/_entry_detail.html','entry_detail.html'),(169,'使用tor+FoxyProxy翻墙','3373193720095257311057',2,'2009-06-25 19:31:10',NULL,'2009-06-25 19:31:10','2014-07-13 18:57:16','首先安装Tor<br />sudo apt-get install tor<br />sudo vi /etc/tor/torrc<br />SocksListenAddress 202.114.0.242:8118 # Proxy服务的IP和端口<br />SocksPolicy accept 0.0.0.0/0 #可以使用该Proxy的客户端<br />sudo /etc/init.d/tor start<br />然后安装Fireforx的FoxyProxy插件<br />https://addons.mozilla.org/zh-CN/firefox/addon/2464<br />然后点右下角的图标<br />Add New Proxy<br />打开Proxy Details 选项卡，输入上面的IP和端口<br />打开URL Patterns 选项卡，输入要使用该代理的网站，如http://www.manning.com/*<br />然后右击FoxyProxy图标，选择要使用的代理方式<br />',1,1,1,0,0,0,'首先安装Torsudo apt-get install torsudo vi /etc/tor/torrcSocksListenAddress 202.114.0.242:8118 # Proxy服务的IP和端口SocksPolicy accept 0.0.0.0/0 #可以使用该Proxy的客户端sudo /etc/init.d/tor start然后安装Fireforx的FoxyProxy插件https://addons.mozilla.org/zh-CN/firefox/addon/2464然后点右下角的图标Add New Proxy打开Proxy Details 选项卡，输入上面的IP和端口打开URL Patterns 选项卡，输入要使用该代理的网站，如http://www.manning.com/*然后右击FoxyProxy图标，选择要使用的代理方式','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(170,'WPF小程序：贪吃蛇','337319372009535108234',2,'2009-06-03 17:10:08',NULL,'2009-06-03 17:10:08','2014-07-13 18:57:16','一共两个文件：EasterEgg.xaml + EasterEgg.xaml.cs<br /><a href=\"/static/163/5694520253833674535.jpg\" target=\"_blank\"><img alt=\"WPF小程序：贪吃蛇 - hankjin - 乱-Blog\" src=\"/static/163/5694520253833674535.jpg\" title=\"WPF小程序：贪吃蛇 - hankjin - 乱-Blog\" /></a><br />EasterEgg.xaml<br />&lt;Window x:Class=\"Inspect.UI.EasterEgg\"<br />&nbsp;&nbsp;&nbsp; xmlns=\"http://schemas.microsoft.com/winfx/2006/xaml/presentation\"<br />&nbsp;&nbsp;&nbsp; xmlns:x=\"http://schemas.microsoft.com/winfx/2006/xaml\"<br />&nbsp;&nbsp;&nbsp; Title=\"Easter Egg\" Loaded=\"Window_Loaded\" Width=\"650\" Height=\"450\"<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; KeyDown=\"Window_KeyDown\" SizeChanged=\"Window_SizeChanged\"<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; WindowStartupLocation=\"CenterScreen\"&gt;<br />&nbsp;&nbsp;&nbsp; &lt;Grid&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;Grid.RowDefinitions&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;RowDefinition Height=\"40\"/&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;RowDefinition/&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/Grid.RowDefinitions&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;StackPanel Grid.Row=\"0\" Orientation=\"Horizontal\"&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;Label FontSize=\"20\"&gt;等级:&lt;/Label&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;Label FontSize=\"20\" Name=\"lblGrade\" Width=\"50\" Content=\"{Binding Path=Grade}\"&gt;&lt;/Label&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;Label FontSize=\"20\"&gt;分数:&lt;/Label&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;Label FontSize=\"20\" Name=\"lblScore\" Width=\"90\" Content=\"{Binding Path=Score}\"&gt;&lt;/Label&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;Label FontSize=\"20\"&gt;说明: 上下左右键调整方向，空格键暂停&lt;/Label&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/StackPanel&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;Border Grid.Row=\"1\" BorderThickness=\"5\" BorderBrush=\"Red\" Name=\"border\"&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;Canvas Name=\"cavas\" Background=\"#CBE9CE\"&gt;&lt;/Canvas&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/Border&gt;<br />&nbsp;&nbsp;&nbsp; &lt;/Grid&gt;<br />&lt;/Window&gt;<br /><br /><br />EasterEgg.xaml.cs<br />using System;<br />using System.Collections.Generic;<br />using System.Linq;<br />using System.Text;<br />using System.Windows;<br />using System.Windows.Controls;<br />using System.Windows.Data;<br />using System.Windows.Documents;<br />using System.Windows.Input;<br />using System.Windows.Media;<br />using System.Windows.Media.Imaging;<br />using System.Windows.Shapes;<br />using System.Timers;<br />using System.Windows.Threading;<br /><br />namespace Inspect.UI<br />{<br />&nbsp;&nbsp;&nbsp; /// &lt;summary&gt;<br />&nbsp;&nbsp;&nbsp; /// Interaction logic for EasterEgg.xaml<br />&nbsp;&nbsp;&nbsp; /// &lt;/summary&gt;<br />&nbsp;&nbsp;&nbsp; public partial class EasterEgg : Window<br />&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; enum Direction { UP, DOWN, LEFT, RIGHT };<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private Brush oldBrush = new SolidColorBrush(Color.FromArgb(100, 0, 255, 0));//brush for snake<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private Brush newBrush = new SolidColorBrush(Color.FromArgb(100, 255, 0, 0));//brush for destination<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private Direction direct;//current direction<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private DispatcherTimer timer = new DispatcherTimer();<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private List&lt;Rectangle&gt; snake = new List&lt;Rectangle&gt;();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private int snakeTail;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private Rectangle dest;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private double destTop, destLeft;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private double W, H;<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private int speed;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public static readonly DependencyProperty GradeProperty = DependencyProperty.Register(\"Grade\", typeof(int), typeof(EasterEgg));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public static readonly DependencyProperty ScoreProperty = DependencyProperty.Register(\"Score\", typeof(int), typeof(EasterEgg));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public int Grade<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; get { return Convert.ToInt32(GetValue(GradeProperty)); }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; set { SetValue(GradeProperty, value); }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public int Score<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; get { return Convert.ToInt32(GetValue(ScoreProperty)); }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; set { SetValue(ScoreProperty, value); }&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public EasterEgg()<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; InitializeComponent();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.DataContext = this;//数据绑定<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private void Window_Loaded(object sender, RoutedEventArgs e)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.InitSnake();<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //start timer<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; timer.Tick += new EventHandler(timer_Elapsed);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; timer.Start();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;summary&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// 时钟相应<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;/summary&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;param name=\"sender\"&gt;&lt;/param&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;param name=\"e\"&gt;&lt;/param&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private void timer_Elapsed(object sender, EventArgs e)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Walk();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private void Walk()<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //尾变头<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rectangle tail = snake[snakeTail];<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rectangle head = snake[(snakeTail + snake.Count - 1) % snake.Count];<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; double top = Convert.ToDouble(head.GetValue(Canvas.TopProperty));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; double left = Convert.ToDouble(head.GetValue(Canvas.LeftProperty));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; switch (direct)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; case Direction.LEFT:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; left -= 10;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; case Direction.RIGHT:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; left += 10;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; case Direction.UP:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; top -= 10;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; case Direction.DOWN:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; top += 10;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //判断是否吃到目标<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (top != destTop || left != destLeft)//如果没有吃到目标<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tail.SetValue(Canvas.TopProperty, top);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tail.SetValue(Canvas.LeftProperty, left);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //snake变长<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; dest.Fill = oldBrush;//目标变色，成为snake的一部分<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.snake.Insert(snakeTail,dest);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //分数增加<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.Score = this.Score + 100;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (this.Score % 2000 == 0)//过关<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.Grade = this.Grade + 1;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.timer.Interval = new TimeSpan(0, 0, 0, 0, speed - 20 * Grade);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //产生新目标<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rand();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //判断是否失败<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (top &lt; 0 || left &lt; 0 || top &gt; this.H - 10.0 || left &gt; this.W - 10.0)//如果跑出边界<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; GameOver();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else//判断是否咬到自己<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for (int i = 0; i &lt; snake.Count; i++)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (i == snakeTail)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; continue;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ((double)snake[i].GetValue(Canvas.TopProperty) == top &amp;&amp; (double)snake[i].GetValue(Canvas.LeftProperty) == left)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; GameOver(); <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; snakeTail = (snakeTail + 1) % snake.Count;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;summary&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// 违反规则，GameOver<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;/summary&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private void GameOver()<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.timer.Stop();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (MessageBox.Show(\"再来一盘?\", \"游戏结束\", MessageBoxButton.YesNo) == MessageBoxResult.Yes)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.InitSnake();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.timer.Start();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.Close();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;summary&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// 产生一个随机的目标<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;/summary&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private void Rand()<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; dest = Rect(newBrush);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.cavas.Children.Add(dest);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Random rand = new Random(System.DateTime.Now.Millisecond);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; destTop = rand.Next(Convert.ToInt32(this.H)/10) * 10.0;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; destLeft = rand.Next(Convert.ToInt32(this.W)/10) * 10.0;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; dest.SetValue(Canvas.TopProperty, destTop);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; dest.SetValue(Canvas.LeftProperty, destLeft);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rectangle Rect(Brush brush)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rectangle rect = new Rectangle();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; rect.Height = 10;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; rect.Width = 10;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; rect.Fill = brush;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return rect;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;summary&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// 初始化Snake<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;/summary&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private void InitSnake()<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.snake.Clear();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.cavas.Children.Clear();<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //init destination<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rand();<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //init snake<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rectangle beginer = Rect(oldBrush);//the first part of snake<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.cavas.Children.Add(beginer);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.snake.Add(beginer);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; beginer.SetValue(Canvas.TopProperty, 0.0);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; beginer.SetValue(Canvas.LeftProperty, 0.0);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.direct = Direction.RIGHT;//initial direction<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.snakeTail = 0;<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.Score = 0;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.Grade = 1;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.speed = 200;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.timer.Interval = new TimeSpan(0, 0, 0, 0, speed);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;summary&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// 相应上下左右控制键, 空格暂停<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;/summary&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;param name=\"sender\"&gt;&lt;/param&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;param name=\"e\"&gt;&lt;/param&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private void Window_KeyDown(object sender, KeyEventArgs e)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (e.Key == Key.Right)//→<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; direct = (direct != Direction.LEFT ? Direction.RIGHT : direct);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else if (e.Key == Key.Left)//←<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; direct = (direct != Direction.RIGHT ? Direction.LEFT : direct);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else if (e.Key == Key.Up)//↑<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; direct = (direct != Direction.DOWN ? Direction.UP : direct);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else if (e.Key == Key.Down)//↓<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; direct = (direct != Direction.UP ? Direction.DOWN : direct);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else if(e.Key == Key.Space &amp;&amp; timer.IsEnabled)//空格键暂停<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; timer.Stop();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(!timer.IsEnabled)//任意键继续<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; timer.Start();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Walk();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;summary&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// 窗口的大小改变时，改变两个边界变量W H<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;/summary&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;param name=\"sender\"&gt;&lt;/param&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;param name=\"e\"&gt;&lt;/param&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private void Window_SizeChanged(object sender, SizeChangedEventArgs e)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.W = Convert.ToInt32(this.cavas.ActualWidth) / 10 * 10.0;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.H = Convert.ToInt32(this.cavas.ActualHeight) / 10 * 10.0;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }<br />}<br /><br />',1,1,1,0,0,0,'一共两个文件：EasterEgg.xaml + EasterEgg.xaml.csEasterEgg.xaml&lt;Window x:Class=\"Inspect.UI.EasterEgg\"&nbsp;&nbsp;&nbsp; xmlns=\"http://schemas.microsoft.com/winfx/2006/xaml/presentation\"&nbsp;&nbsp;&nbsp; xmlns:x=\"http://schemas.microsoft.com/winfx/2006/xaml\"&nbsp;&nbsp;&nbsp; Title=\"Easter Egg\" Loaded=\"Window_Loaded\" Width=\"650\" Height=\"450\"&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; KeyDown=\"Window_KeyDown\" SizeChanged=\"Window_SizeChanged\"&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; WindowStartupLocation=\"CenterScreen\"&gt;&nbsp;&nbsp;&nbsp; &lt;Grid&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;Grid.RowDefinitions&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;RowDefinition Height=\"40\"/&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;RowDefinition/&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/Grid.RowDefinitions&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;StackPanel Grid.Row=\"0\" Orientation=\"Horizontal\"&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;Label FontSize=\"20\"&gt;等级:&lt;/Label&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;Label FontSize=\"20\" Name=\"lblGrade\" Width=\"50\" Content=\"{Binding Path=Grade}\"&gt;&lt;/Label&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;Label FontSize=\"20\"&gt;分数:&lt;/Label&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;Label FontSize=\"20\" Name=\"lblScore\" Width=\"90\" Content=\"{Binding Path=Score}\"&gt;&lt;/Label&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;Label FontSize=\"20\"&gt;说明: 上下左右键调整方向，空格键暂停&lt;/Label&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/StackPanel&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;Border Grid.Row=\"1\" BorderThickness=\"5\" BorderBrush=\"Red\" Name=\"border\"&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;Canvas Name=\"cavas\" Background=\"#CBE9CE\"&gt;&lt;/Canvas&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/Border&gt;&nbsp;&nbsp;&nbsp; &lt;/Grid&gt;&lt;/Window&gt;EasterEgg.xaml.csusing System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Windows;using System.Windows.Controls;using System.Windows.Data;using System.Windows.Documents;using System.Windows.Input;using System.Windows.Media;using System.Windows.Media.Imaging;using System.Windows.Shapes;using System.Timers;using System.Windows.Threading;namespace Inspect.UI{&nbsp;&nbsp;&nbsp; /// &lt;summary&gt;&nbsp;&nbsp;&nbsp; /// Interaction logic for EasterEgg.xaml&nbsp;&nbsp;&nbsp; /// &lt;/summary&gt;&nbsp;&nbsp;&nbsp; public partial class EasterEgg : Window&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; enum Direction { UP, DOWN, LEFT, RIGHT };&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private Brush oldBrush = new SolidColorBrush(Color.FromArgb(100, 0, 255, 0));//brush for snake&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private Brush newBrush = new SolidColorBrush(Color.FromArgb(100, 255, 0, 0));//brush for destination&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private Direction direct;//current direction&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private DispatcherTimer timer = new DispatcherTimer();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private List&lt;Rectangle&gt; snake = new List&lt;Rectangle&gt;();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private int snakeTail;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private Rectangle dest;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private double destTop, destLeft;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private double W, H;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private int speed;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public static readonly DependencyProperty GradeProperty = DependencyProperty.Register(\"Grade\", typeof(int), typeof(EasterEgg));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public static readonly DependencyProperty ScoreProperty = DependencyProperty.Register(\"Score\", typeof(int), typeof(EasterEgg));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public int Grade&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; get { return Convert.ToInt32(GetValue(GradeProperty)); }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; set { SetValue(GradeProperty, value); }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public int Score&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; get { return Convert.ToInt32(GetValue(ScoreProperty)); }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; set { SetValue(ScoreProperty, value); }&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public EasterEgg()&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; InitializeComponent();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.DataContext = this;//数据绑定&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private void Window_Loaded(object sender, RoutedEventArgs e)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.InitSnake();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //start timer&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; timer.Tick += new EventHandler(timer_Elapsed);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; timer.Start();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;summary&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// 时钟相应&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;/summary&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;param name=\"sender\"&gt;&lt;/param&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;param name=\"e\"&gt;&lt;/param&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private void timer_Elapsed(object sender, EventArgs e)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Walk();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private void Walk()&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //尾变头&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rectangle tail = snake[snakeTail];&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rectangle head = snake[(snakeTail + snake.Count - 1) % snake.Count];&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; double top = Convert.ToDouble(head.GetValue(Canvas.TopProperty));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; double left = Convert.ToDouble(head.GetValue(Canvas.LeftProperty));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; switch (direct)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; case Direction.LEFT:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; left -= 10;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; break;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; case Direction.RIGHT:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; left += 10;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; break;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; case Direction.UP:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; top -= 10;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; break;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; case Direction.DOWN:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; top += 10;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; break;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //判断是否吃到目标&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (top != destTop || left != destLeft)//如果没有吃到目标&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tail.SetValue(Canvas.TopProperty, top);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tail.SetValue(Canvas.LeftProperty, left);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //snake变长&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; dest.Fill = oldBrush;//目标变色，成为snake的一部分&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.snake.Insert(snakeTail,dest);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //分数增加&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.Score = this.Score + 100;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (this.Score % 2000 == 0)//过关&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.Grade = this.Grade + 1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.timer.Interval = new TimeSpan(0, 0, 0, 0, speed - 20 * Grade);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //产生新目标&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rand();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //判断是否失败&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (top &lt; 0 || left &lt; 0 || top &gt; this.H - 10.0 || left &gt; this.W - 10.0)//如果跑出边界&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; GameOver();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else//判断是否咬到自己&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for (int i = 0; i &lt; snake.Count; i++)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (i == snakeTail)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; continue;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ((double)snake[i].GetValue(Canvas.TopProperty) == top &amp;&amp; (double)snake[i].GetValue(Canvas.LeftProperty) == left)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; GameOver(); &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; snakeTail = (snakeTail + 1) % snake.Count;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;summary&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// 违反规则，GameOver&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;/summary&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private void GameOver()&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.timer.Stop();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (MessageBox.Show(\"再来一盘?\", \"游戏结束\", MessageBoxButton.YesNo) == MessageBoxResult.Yes)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.InitSnake();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.timer.Start();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.Close();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;summary&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// 产生一个随机的目标&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;/summary&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private void Rand()&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; dest = Rect(newBrush);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.cavas.Children.Add(dest);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Random rand = new Random(System.DateTime.Now.Millisecond);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; destTop = rand.Next(Convert.ToInt32(this.H)/10) * 10.0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; destLeft = rand.Next(Convert.ToInt32(this.W)/10) * 10.0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; dest.SetValue(Canvas.TopProperty, destTop);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; dest.SetValue(Canvas.LeftProperty, destLeft);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rectangle Rect(Brush brush)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rectangle rect = new Rectangle();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; rect.Height = 10;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; rect.Width = 10;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; rect.Fill = brush;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return rect;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;summary&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// 初始化Snake&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;/summary&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private void InitSnake()&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.snake.Clear();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.cavas.Children.Clear();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //init destination&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rand();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //init snake&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Rectangle beginer = Rect(oldBrush);//the first part of snake&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.cavas.Children.Add(beginer);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.snake.Add(beginer);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; beginer.SetValue(Canvas.TopProperty, 0.0);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; beginer.SetValue(Canvas.LeftProperty, 0.0);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.direct = Direction.RIGHT;//initial direction&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.snakeTail = 0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.Score = 0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.Grade = 1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.speed = 200;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.timer.Interval = new TimeSpan(0, 0, 0, 0, speed);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;summary&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// 相应上下左右控制键, 空格暂停&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;/summary&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;param name=\"sender\"&gt;&lt;/param&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;param name=\"e\"&gt;&lt;/param&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private void Window_KeyDown(object sender, KeyEventArgs e)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (e.Key == Key.Right)//→&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; direct = (direct != Direction.LEFT ? Direction.RIGHT : direct);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else if (e.Key == Key.Left)//←&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; direct = (direct != Direction.RIGHT ? Direction.LEFT : direct);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else if (e.Key == Key.Up)//↑&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; direct = (direct != Direction.DOWN ? Direction.UP : direct);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else if (e.Key == Key.Down)//↓&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; direct = (direct != Direction.UP ? Direction.DOWN : direct);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else if(e.Key == Key.Space &amp;&amp; timer.IsEnabled)//空格键暂停&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; timer.Stop();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(!timer.IsEnabled)//任意键继续&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; timer.Start();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Walk();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;summary&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// 窗口的大小改变时，改变两个边界变量W H&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;/summary&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;param name=\"sender\"&gt;&lt;/param&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /// &lt;param name=\"e\"&gt;&lt;/param&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private void Window_SizeChanged(object sender, SizeChangedEventArgs e)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.W = Convert.ToInt32(this.cavas.ActualWidth) / 10 * 10.0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.H = Convert.ToInt32(this.cavas.ActualHeight) / 10 * 10.0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }}','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(171,'Websphere Business Integrated Developer WID','3373193720083221057897',2,'2008-04-22 10:05:07',NULL,'2008-04-22 10:05:07','2014-07-13 18:57:16','<p>Websphere Business Modeler； Websphere Business Integrated Developer; Websphere Business Monitor; Websphere Business Monitor Development </p>\n<p>1。安装</p>\n<p>下载WID6.1.0的4个包，然后解压缩到同一个包里(如果解压到不同的包里，虽然也能开始安装，但会跳出NullPointerException异常）。然后执行launchpad.exe开始安装。</p>\n<p>安装完成后再安装MMDT，好像6.1.0的MMDT不能用，只能安装6.1.1的</p>\n<p>2。开发</p>\n<p>3.</p>',1,1,1,0,0,0,'Websphere Business Modeler； Websphere Business Integrated Developer; Websphere Business Monitor; Websphere Business Monitor Development \n1。安装\n下载WID6.1.0的4个包，然后解压缩到同一个包里(如果解压到不同的包里，虽然也能开始安装，但会跳出NullPointerException异常）。然后执行launchpad.exe开始安装。\n安装完成后再安装MMDT，好像6.1.0的MMDT不能用，只能安装6.1.1的\n2。开发\n3.','',0,'was',0,'','zinnia/_entry_detail.html','entry_detail.html'),(172,'Ubuntu下使用Groovy','33731937201052992931606',2,'2010-06-29 21:29:31',NULL,'2010-06-29 21:29:31','2014-07-13 18:57:16','$ sudo apt-get install groovy<br />groovy的UI有两个，一个是基于X的groovyConsole，另一个是基于命令行的groovysh<br />$ groovysh<br />groovy:000&gt; println \"Hello, Worlp\"<br />Hello, Worlp<br />===&gt; null<br />groovy:000&gt; l=[1,2,3,4];<br />===&gt; [1, 2, 3, 4]<br />groovy:000&gt; quit<br />$ <br />难道要从Python再跳回Java？',1,1,1,0,0,0,'$ sudo apt-get install groovygroovy的UI有两个，一个是基于X的groovyConsole，另一个是基于命令行的groovysh$ groovyshgroovy:000&gt; println \"Hello, Worlp\"Hello, Worlp===&gt; nullgroovy:000&gt; l=[1,2,3,4];===&gt; [1, 2, 3, 4]groovy:000&gt; quit$ 难道要从Python再跳回Java？','',0,'j2se',0,'','zinnia/_entry_detail.html','entry_detail.html'),(173,'程序员每天该做的事','3373193720078268103096',2,'2007-09-26 20:10:30',NULL,'2007-09-26 20:10:30','2014-07-13 18:57:16','<p>&nbsp;</p>\n<p>1、总结自己一天任务的完成情况 <br />最好的方式是写工作日志，把自己今天完成了什么事情，遇见了什么问题都记录下来，日后翻看好处多多 </p>\n<p>2、考虑自己明天应该做的主要工作 <br />把明天要做的事情列出来，并按照优先级排列，第二天应该把自己效率最高的时间分配给最重要的工作 </p>\n<p>3、考虑自己一天工作中失误的地方，并想出避免下一次再犯的方法 <br />出错不要紧，最重要的是不要重复犯相同的错误，那是愚蠢 </p>\n<p>4、考虑自己一天工作完成的质量和效率能否还能提高 <br />一天只提高1%，365天你的效率就能提高多少倍你知道吗？ (1+0.01)^365 = 37 倍 </p>\n<p>5、看一个有用的新闻网站或读一张有用的报纸，了解业界动态 <br />闭门造车是不行的，了解一下别人都在做什么，对自己能带来很多启示 </p>\n<p>6、记住一位同事的名字及其特点 <br />你认识公司的所有同事吗？你了解他们吗？ </p>\n<p>7、清理自己的代码 <br />今天完成的代码，把中间的调试信息，测试代码清理掉，按照编码风格整理好，注释都写好了吗？ </p>\n<p>8、清理自己的桌面 <br />当日事当日毕，保持清洁干劲的桌面才能让你工作时不分心，程序员特别要把电脑的桌面清理干净 </p>\n<p>程序员每周该做的事 <br />1、向你的老板汇报一次工作 <br />让你的老板知道你在做什么，这很重要。可以口头、书面、邮件，看你老板的工作方式而定 </p>\n<p>2、进行一次自我总结（非正式） <br />这周之内自己表现得怎么样？该加分还是扣分？ </p>\n<p>3、制定下周计划 <br />把下周要做的事情列出来，一样要分清楚优先级 </p>\n<p>4、整理自己的文件夹、书柜和电脑文件 <br />把桌面以外的地方也要清理干净，电脑的文件夹，收到的邮件，把过时的垃圾全部清理掉 </p>\n<p>5、与一个非公司的朋友沟通 <br />它山之石，可以攻玉 </p>\n<p>6、看一本杂志 <br />找一本适合自己的专业杂志 </p>\n<p>7、纠正自己或同事一个细节上的不正确做法 <br />《细节决定成败》看过了吗？没看过强烈建议先看看 </p>\n<p>程序员每月该做的事 <br />1、至少和一个同事一起吃饭或喝茶 <br />不光了解自己工作伙伴的工作，还要了解他们的生活 </p>\n<p>2、自我考核一次 <br />相对正式地考核自己一下，你对得起这个月的工资吗？ </p>\n<p>3、对你的同事考核一次 <br />你的同事表现怎么样？哪些人值得学习，哪些人需要帮助？ </p>\n<p>3、制定下月的计划，确定下月的工作重点 </p>\n<p>4、总结自己工作质量改进状况 <br />自己的质量提高了多少？ </p>\n<p>5、有针对性地对一项工作指标做深入地分析并得出改进的方案 <br />可以是对自己的，也可以是对公司的，一定要深入地分析后拿出自己的观点来。要想在老板面前说得上话，做的成事，工作上功夫要做足。 </p>\n<p>6、与老板沟通一次 <br />最好是面对面地沟通，好好表现一下自己，虚心听取老板的意见，更重要的是要了解老板当前关心的重点 </p>\n<p>程序员每年该做的事 <br />1、年终总结 <br />每个公司都会做的事情，但你真正认真地总结过自己吗？ </p>\n<p>2、兑现给自己、给家人的承诺 <br />给老婆、儿子的新年礼物买了没有？给自己的呢？ </p>\n<p>3、下年度工作规划 <br />好好想想自己明年的发展目标，争取升职/加薪、跳槽还是自己出来干？ </p>\n<p>4、掌握一项新技术 <br />至少是一项，作为程序员一年要是一项新技术都学不到手，那就一定会被淘汰。 <br />掌握可不是看本书就行的，要真正懂得应用，最好你能够写一篇教程发表到你的blog </p>\n<p>5、推出一种新产品 <br />可以是一个真正的产品，也可以只是一个类库，只要是你创造的东西就行，让别人使用它，也为世界作点贡献。当然如果真的很有价值，收点注册费也是应该的 </p>\n<p>6、与父母团聚一次 <br />常回家看看，常回家看看 <br /></p>',1,1,1,0,0,0,'&nbsp;\n1、总结自己一天任务的完成情况 最好的方式是写工作日志，把自己今天完成了什么事情，遇见了什么问题都记录下来，日后翻看好处多多 \n2、考虑自己明天应该做的主要工作 把明天要做的事情列出来，并按照优先级排列，第二天应该把自己效率最高的时间分配给最重要的工作 \n3、考虑自己一天工作中失误的地方，并想出避免下一次再犯的方法 出错不要紧，最重要的是不要重复犯相同的错误，那是愚蠢 \n4、考虑自己一天工作完成的质量和效率能否还能提高 一天只提高1%，365天你的效率就能提高多少倍你知道吗？ (1+0.01)^365 = 37 倍 \n5、看一个有用的新闻网站或读一张有用的报纸，了解业界动态 闭门造车是不行的，了解一下别人都在做什么，对自己能带来很多启示 \n6、记住一位同事的名字及其特点 你认识公司的所有同事吗？你了解他们吗？ \n7、清理自己的代码 今天完成的代码，把中间的调试信息，测试代码清理掉，按照编码风格整理好，注释都写好了吗？ \n8、清理自己的桌面 当日事当日毕，保持清洁干劲的桌面才能让你工作时不分心，程序员特别要把电脑的桌面清理干净 \n程序员每周该做的事 1、向你的老板汇报一次工作 让你的老板知道你在做什么，这很重要。可以口头、书面、邮件，看你老板的工作方式而定 \n2、进行一次自我总结（非正式） 这周之内自己表现得怎么样？该加分还是扣分？ \n3、制定下周计划 把下周要做的事情列出来，一样要分清楚优先级 \n4、整理自己的文件夹、书柜和电脑文件 把桌面以外的地方也要清理干净，电脑的文件夹，收到的邮件，把过时的垃圾全部清理掉 \n5、与一个非公司的朋友沟通 它山之石，可以攻玉 \n6、看一本杂志 找一本适合自己的专业杂志 \n7、纠正自己或同事一个细节上的不正确做法 《细节决定成败》看过了吗？没看过强烈建议先看看 \n程序员每月该做的事 1、至少和一个同事一起吃饭或喝茶 不光了解自己工作伙伴的工作，还要了解他们的生活 \n2、自我考核一次 相对正式地考核自己一下，你对得起这个月的工资吗？ \n3、对你的同事考核一次 你的同事表现怎么样？哪些人值得学习，哪些人需要帮助？ \n3、制定下月的计划，确定下月的工作重点 \n4、总结自己工作质量改进状况 自己的质量提高了多少？ \n5、有针对性地对一项工作指标做深入地分析并得出改进的方案 可以是对自己的，也可以是对公司的，一定要深入地分析后拿出自己的观点来。要想在老板面前说得上话，做的成事，工作上功夫要做足。 \n6、与老板沟通一次 最好是面对面地沟通，好好表现一下自己，虚心听取老板的意见，更重要的是要了解老板当前关心的重点 \n程序员每年该做的事 1、年终总结 每个公司都会做的事情，但你真正认真地总结过自己吗？ \n2、兑现给自己、给家人的承诺 给老婆、儿子的新年礼物买了没有？给自己的呢？ \n3、下年度工作规划 好好想想自己明年的发展目标，争取升职/加薪、跳槽还是自己出来干？ \n4、掌握一项新技术 至少是一项，作为程序员一年要是一项新技术都学不到手，那就一定会被淘汰。 掌握可不是看本书就行的，要真正懂得应用，最好你能够写一篇教程发表到你的blog \n5、推出一种新产品 可以是一个真正的产品，也可以只是一个类库，只要是你创造的东西就行，让别人使用它，也为世界作点贡献。当然如果真的很有价值，收点注册费也是应该的 \n6、与父母团聚一次 常回家看看，常回家看看 ','',0,'mood',0,'','zinnia/_entry_detail.html','entry_detail.html'),(174,'QGLWidget的双缓存反而使帧率降低','3373193720103147847319',2,'2010-04-14 19:08:47',NULL,'2010-04-14 19:08:47','2014-07-13 18:57:16','在glut窗口下使用双缓存进行渲染，可以达到60FPS，在QGLWidget下，也可以达到60FPS<br />然后在QGLWidget中启用双缓存<br />首先在initializeGL()中加入一句：<br /><span style=\"font-weight: bold;\">this-&gt;format().setDoubleBuffer(true);</span><br />然后在paintGL()中加入一句：<br /><span style=\"font-weight: bold;\">this-&gt;swapBuffers()</span><br />结果显示效果一样，帧率竟然降到了30FPS<br />',1,1,1,0,0,0,'在glut窗口下使用双缓存进行渲染，可以达到60FPS，在QGLWidget下，也可以达到60FPS然后在QGLWidget中启用双缓存首先在initializeGL()中加入一句：this-&gt;format().setDoubleBuffer(true);然后在paintGL()中加入一句：this-&gt;swapBuffers()结果显示效果一样，帧率竟然降到了30FPS','',0,'graphics',0,'','zinnia/_entry_detail.html','entry_detail.html'),(175,'SVNNotify邮件通知乱码问题','3373193720096911540412',2,'2009-07-09 11:54:00',NULL,'2009-07-09 11:54:00','2014-07-13 18:57:16','网上多数都是在pl中进行字符转换<br />实际上只要在post-commit里加一句话就可以了<br />REPOS=\"$1\"<br />REV=\"$2\"<br /><span style=\"color: rgb(255, 0, 0);\">export LANG=\"en_US.UTF-8\"</span><br /><br />/usr/bin/svnnotify ...<br />',1,1,1,0,0,0,'网上多数都是在pl中进行字符转换实际上只要在post-commit里加一句话就可以了REPOS=\"$1\"REV=\"$2\"export LANG=\"en_US.UTF-8\"/usr/bin/svnnotify ...','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(176,'smarty实现i18n','337319372009105114051262',2,'2009-11-05 11:40:51',NULL,'2009-11-05 11:40:51','2014-07-13 18:57:16','<font size=\"4\">分三步：</font><br />1. 判断客户端浏览器语言<br />2. 加载相应的语言文件<br />3. 显示<br /><font size=\"4\">详细步骤如下：</font><br /><font size=\"3\">首先判断客户浏览器语言：</font><br />$lang = substr($_SERVER[\'HTTP_ACCEPT_LANGUAGE\'], 0, 4);<br />\nif(preg_match(\"/zh-c/i\", $lang))<br />&nbsp; 加载中文语言文件<br />else if (preg_match(\"/en/i\", $lang))<br />&nbsp; 加载英文语言文件<br />else if ...<br />&nbsp; 加载相应语言文件<br />else<br />&nbsp; 加载默认语言文件<br /><font size=\"3\">然后加载相应的语言文件</font><br />$smarty-&gt;config_load(\'zh_CN.lang\')<br />...<br /><font size=\"3\">最后在tpl中显示相应的字符串</font><br />{#hello#}<br /><br /><font size=\"4\">源代码如下：</font><br />============i18n.php================================<br />&lt;?php<br />require_once dirname(__FILE__). \'/smarty/libs/Smarty.class.php\';<br />$smarty = new Smarty();<br />$smarty-&gt;template_dir =dirname(__FILE__).\"/smarty/templates\";<br />$smarty-&gt;compile_dir =dirname(__FILE__).\"/smarty/templates_c\";<br />$smarty-&gt;config_dir = dirname(__FILE__).\"/smarty/configs\";<br />$smarty-&gt;cache_dir =dirname(__FILE__).\"/smarty/cache\";<br />$lang = substr($_SERVER[\'HTTP_ACCEPT_LANGUAGE\'], 0, 4);<br />if(preg_match(\"/zh-c/i\", $lang))<br />&nbsp;&nbsp;&nbsp; $smarty-&gt;config_load(\'zh_CN.lang\');<br />else if (preg_match(\"/zh/i\", $lang))<br />&nbsp;&nbsp;&nbsp; $smarty-&gt;config_load(\'zh_TW.lang\');<br />else if (preg_match(\"/en/i\", $lang))<br />&nbsp;&nbsp;&nbsp; $smarty-&gt;config_load(\'en_US.lang\');<br />else if (preg_match(\"/fr/i\", $lang))<br />&nbsp;&nbsp;&nbsp; $smarty-&gt;config_load(\'fr_FR.lang\');<br />else{<br />&nbsp;&nbsp;&nbsp; if (preg_match(\"/de/i\", $lang))<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; ;<br />&nbsp;&nbsp;&nbsp; else if (preg_match(\"/jp/i\", $lang))<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; ;<br />&nbsp;&nbsp;&nbsp; else if (preg_match(\"/ko/i\", $lang))<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; ;<br />&nbsp;&nbsp;&nbsp; else if (preg_match(\"/es/i\", $lang))<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; ;<br />&nbsp;&nbsp;&nbsp; else if (preg_match(\"/sv/i\", $lang))<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; ;<br />&nbsp;&nbsp;&nbsp; else <br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; ;<br />&nbsp;&nbsp;&nbsp; $smarty-&gt;config_load(\'en_US.lang\');<br />}<br />$smarty-&gt;display(\'i18n.tpl\');<br />?&gt;<br />============smarty/templates/i18n.tpl================================<br />{#hello#} <br />============smarty/configs/zh_CN.lang================================<br />hello=你好<br />============smarty/configs/en_US.lang================================<br />hello=Hello<br />',1,1,1,0,0,0,'分三步：1. 判断客户端浏览器语言2. 加载相应的语言文件3. 显示详细步骤如下：首先判断客户浏览器语言：$lang = substr($_SERVER[\'HTTP_ACCEPT_LANGUAGE\'], 0, 4);\nif(preg_match(\"/zh-c/i\", $lang))&nbsp; 加载中文语言文件else if (preg_match(\"/en/i\", $lang))&nbsp; 加载英文语言文件else if ...&nbsp; 加载相应语言文件else&nbsp; 加载默认语言文件然后加载相应的语言文件$smarty-&gt;config_load(\'zh_CN.lang\')...最后在tpl中显示相应的字符串{#hello#}源代码如下：============i18n.php================================&lt;?phprequire_once dirname(__FILE__). \'/smarty/libs/Smarty.class.php\';$smarty = new Smarty();$smarty-&gt;template_dir =dirname(__FILE__).\"/smarty/templates\";$smarty-&gt;compile_dir =dirname(__FILE__).\"/smarty/templates_c\";$smarty-&gt;config_dir = dirname(__FILE__).\"/smarty/configs\";$smarty-&gt;cache_dir =dirname(__FILE__).\"/smarty/cache\";$lang = substr($_SERVER[\'HTTP_ACCEPT_LANGUAGE\'], 0, 4);if(preg_match(\"/zh-c/i\", $lang))&nbsp;&nbsp;&nbsp; $smarty-&gt;config_load(\'zh_CN.lang\');else if (preg_match(\"/zh/i\", $lang))&nbsp;&nbsp;&nbsp; $smarty-&gt;config_load(\'zh_TW.lang\');else if (preg_match(\"/en/i\", $lang))&nbsp;&nbsp;&nbsp; $smarty-&gt;config_load(\'en_US.lang\');else if (preg_match(\"/fr/i\", $lang))&nbsp;&nbsp;&nbsp; $smarty-&gt;config_load(\'fr_FR.lang\');else{&nbsp;&nbsp;&nbsp; if (preg_match(\"/de/i\", $lang))&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; ;&nbsp;&nbsp;&nbsp; else if (preg_match(\"/jp/i\", $lang))&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; ;&nbsp;&nbsp;&nbsp; else if (preg_match(\"/ko/i\", $lang))&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; ;&nbsp;&nbsp;&nbsp; else if (preg_match(\"/es/i\", $lang))&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; ;&nbsp;&nbsp;&nbsp; else if (preg_match(\"/sv/i\", $lang))&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; ;&nbsp;&nbsp;&nbsp; else &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; ;&nbsp;&nbsp;&nbsp; $smarty-&gt;config_load(\'en_US.lang\');}$smarty-&gt;display(\'i18n.tpl\');?&gt;============smarty/templates/i18n.tpl================================{#hello#} ============smarty/configs/zh_CN.lang================================hello=你好============smarty/configs/en_US.lang================================hello=Hello','',0,'\"web skills\"',0,'','zinnia/_entry_detail.html','entry_detail.html'),(177,'Ubuntu下使用Bochsdbg','33731937201001902924834',2,'2010-01-19 12:29:24',NULL,'2010-01-19 12:29:24','2014-07-13 18:57:16','在Windows下作用bochsdbg习惯了，在Ubuntu下，发现bochs包里竟然没有bochsdbg，要自己编译<br />$ apt-get source bochs<br />$ cd bochs-2.3.7<br />$ ./configure --enable-debugger --enable-disasm --prefix=/usr<br />$ make<br />$ sudo cp bochs /usr/bin/bochsdbg<br /><br />编译错误解决办法<br />错误一： ISO C++ forbids declaration of ‘hash_map’ with no type<br />修改：加入hash_map的命名空间，修改bx_debug/symbols.cc<br />#include &lt;ext/hash_map&gt;<br />using namespace __gnu_cxx;<br />错误二：没有找到yacc命令<br />修改：安装yacc包 <br />sudo apt-get install bison++<br />',1,1,1,0,0,0,'在Windows下作用bochsdbg习惯了，在Ubuntu下，发现bochs包里竟然没有bochsdbg，要自己编译$ apt-get source bochs$ cd bochs-2.3.7$ ./configure --enable-debugger --enable-disasm --prefix=/usr$ make$ sudo cp bochs /usr/bin/bochsdbg编译错误解决办法错误一： ISO C++ forbids declaration of ‘hash_map’ with no type修改：加入hash_map的命名空间，修改bx_debug/symbols.cc#include &lt;ext/hash_map&gt;using namespace __gnu_cxx;错误二：没有找到yacc命令修改：安装yacc包 sudo apt-get install bison++','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(178,'Qt实现窗口自动隐藏','337319372009101611345842',2,'2009-11-16 11:34:58',NULL,'2009-11-16 11:34:58','2014-07-13 18:57:16','将窗口拖到窗口最上方，或最左方，移开鼠标，窗口自动隐藏。<br />将鼠标移到窗口边界，窗口自动弹出，类音速启动效果<br /><br />from PyQt4.QtGui import *<br />from PyQt4.QtCore import *<br /><br />class Geometry(QDialog):&nbsp;&nbsp; &nbsp;<br />&nbsp;&nbsp; &nbsp;def __init__ (self):<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;QDialog.__init__(self)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.frame=1<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;layout=QGridLayout(self)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btnAbout=QToolButton()<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btnAbout.setText(\'About Qt\')<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btnAbout.setIcon(QIcon(\'hjz.png\'))<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btnAbout.setToolButtonStyle(Qt.ToolButtonTextUnderIcon)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;layout.addWidget(btnAbout,0,0,Qt.AlignTop|Qt.AlignLeft)<br /><br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.connect(btnAbout,SIGNAL(\'clicked()\'),self.slotAbout)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.setWindowFlags(Qt.WindowStaysOnTopHint)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.resize(300,200)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.show()<br /><br />&nbsp;&nbsp; &nbsp;def slotAbout(self):<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;QMessageBox.aboutQt(self)<br />&nbsp;&nbsp; &nbsp;def enterEvent(self, evt):<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.activateWindow()<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if(self.x() == self.frame-self.width()):<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.move(-self.frame,self.y())<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;elif(self.y() == self.frame-self.height()+self.y()-self.geometry().y()):<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.move(self.x(),-self.frame)<br />&nbsp;&nbsp; &nbsp;def leaveEvent(self,evt):&nbsp;&nbsp; &nbsp;<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;cx,cy=QCursor.pos().x(),QCursor.pos().y()<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if(cx &gt;= self.x() and cx &lt;= self.x()+self.width()<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;and cy &gt;= self.y() and cy &lt;= self.geometry().y()):<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return#title bar<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;elif(self.x() &lt; 0 and QCursor.pos().x()&gt;0):<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.move(self.frame-self.width(),self.y())<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;elif(self.y() &lt; 0 and QCursor.pos().y()&gt;0):<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.move(self.x(), self.frame-self.height()+self.y()-self.geometry().y())<br /><br />if __name__==\'__main__\':<br />&nbsp;&nbsp; &nbsp;app=QApplication([])<br />&nbsp;&nbsp; &nbsp;win=Geometry()<br />&nbsp;&nbsp; &nbsp;app.setActiveWindow(win)<br />&nbsp;&nbsp; &nbsp;app.exec_()<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br /><br />',1,1,1,0,0,0,'将窗口拖到窗口最上方，或最左方，移开鼠标，窗口自动隐藏。将鼠标移到窗口边界，窗口自动弹出，类音速启动效果from PyQt4.QtGui import *from PyQt4.QtCore import *class Geometry(QDialog):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def __init__ (self):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;QDialog.__init__(self)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.frame=1&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;layout=QGridLayout(self)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btnAbout=QToolButton()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btnAbout.setText(\'About Qt\')&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btnAbout.setIcon(QIcon(\'hjz.png\'))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;btnAbout.setToolButtonStyle(Qt.ToolButtonTextUnderIcon)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;layout.addWidget(btnAbout,0,0,Qt.AlignTop|Qt.AlignLeft)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.connect(btnAbout,SIGNAL(\'clicked()\'),self.slotAbout)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.setWindowFlags(Qt.WindowStaysOnTopHint)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.resize(300,200)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.show()&nbsp;&nbsp; &nbsp;def slotAbout(self):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;QMessageBox.aboutQt(self)&nbsp;&nbsp; &nbsp;def enterEvent(self, evt):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.activateWindow()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if(self.x() == self.frame-self.width()):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.move(-self.frame,self.y())&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;elif(self.y() == self.frame-self.height()+self.y()-self.geometry().y()):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.move(self.x(),-self.frame)&nbsp;&nbsp; &nbsp;def leaveEvent(self,evt):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;cx,cy=QCursor.pos().x(),QCursor.pos().y()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if(cx &gt;= self.x() and cx &lt;= self.x()+self.width()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;and cy &gt;= self.y() and cy &lt;= self.geometry().y()):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return#title bar&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;elif(self.x() &lt; 0 and QCursor.pos().x()&gt;0):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.move(self.frame-self.width(),self.y())&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;elif(self.y() &lt; 0 and QCursor.pos().y()&gt;0):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.move(self.x(), self.frame-self.height()+self.y()-self.geometry().y())if __name__==\'__main__\':&nbsp;&nbsp; &nbsp;app=QApplication([])&nbsp;&nbsp; &nbsp;win=Geometry()&nbsp;&nbsp; &nbsp;app.setActiveWindow(win)&nbsp;&nbsp; &nbsp;app.exec_()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(179,'Python之道：一生二 二生三 三生万物','33731937200952653332343',2,'2009-06-26 17:33:32',NULL,'2009-06-26 17:33:32','2014-07-13 18:57:16','一切皆是对象<br />和Java类似，Python的所有对象都有一个共同的基类：PyObject。天地如卵，PyObject 就是天地未分的“元”<br />须弥芥子，一粒沙即大千世界。PyObject 还有类型：PyBaseObject_Type；PyBaseObject_Type 还有类型：PyType_Type；PyType_Type 的类型是自身：PyType_Type。<br />一生二<br />混沌初分，清者为天，浊者为敌，阴阳始分。Python中的对象可以大致分为两类：定长对象和变长对象：<br />定长对象者，若int。int对象的值无论多大，只要4个字节足矣。使用PyObjecty_HEAD保存对象的头信息。<br />变长对象者，若string。string对象的长度几何？不可未卜先知也。使用PyObject_VAR_HEAD保存对象的头信息。<br />二生三<br />太极生两仪，两仪生四象，四象生八卦，八八六十四卦。阴阳相生，无可穷尽。<br />PyObject 分为定长与变长两种，变长对象又可以再分为两种：不可变变长对象和可变变长对象。<br />不可变变长对象者，一旦生成，大小不可变。若string，每个字符串都是不可变的，string虽然支持加号操作符，但是每次加法都是构建的新对象，而不是在原来的字符串上修改。如\"hello\"+\"world\"，实际包含了三个对象<br />可变变长对象者，生成之后，仍可增删改查。若List，大小无限（当然是在不用完内存的前提下）<br />三生万物<br />由定长对象、不可变变长对象、可变变长对象这三种基本类型，就可以表示Python中所有的数据类型。<br /><br />',1,1,1,0,0,0,'一切皆是对象和Java类似，Python的所有对象都有一个共同的基类：PyObject。天地如卵，PyObject 就是天地未分的“元”须弥芥子，一粒沙即大千世界。PyObject 还有类型：PyBaseObject_Type；PyBaseObject_Type 还有类型：PyType_Type；PyType_Type 的类型是自身：PyType_Type。一生二混沌初分，清者为天，浊者为敌，阴阳始分。Python中的对象可以大致分为两类：定长对象和变长对象：定长对象者，若int。int对象的值无论多大，只要4个字节足矣。使用PyObjecty_HEAD保存对象的头信息。变长对象者，若string。string对象的长度几何？不可未卜先知也。使用PyObject_VAR_HEAD保存对象的头信息。二生三太极生两仪，两仪生四象，四象生八卦，八八六十四卦。阴阳相生，无可穷尽。PyObject 分为定长与变长两种，变长对象又可以再分为两种：不可变变长对象和可变变长对象。不可变变长对象者，一旦生成，大小不可变。若string，每个字符串都是不可变的，string虽然支持加号操作符，但是每次加法都是构建的新对象，而不是在原来的字符串上修改。如\"hello\"+\"world\"，实际包含了三个对象可变变长对象者，生成之后，仍可增删改查。若List，大小无限（当然是在不用完内存的前提下）三生万物由定长对象、不可变变长对象、可变变长对象这三种基本类型，就可以表示Python中所有的数据类型。','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(180,'Mac受难记','337319372010425101014708',2,'2010-05-25 10:10:14',NULL,'2010-05-25 10:10:14','2014-07-13 18:57:16','1. 准备安装Mac，首先分出来两个40G的FAT32分区，卷标定为install和Mac，然后使用硬盘助手将ISO镜像写入install分区，然后用变色龙写入启动配置信息<br />2.重启电脑，发现MBR损坏，不能启动。好吧，先不管他，使用PE光盘的光盘GRUB启动<br />root (hd0,0)<br />chainloader +1<br />boot<br />3.选择变色龙选项，进入变色龙启动菜单，遗憾的是，没有出现期望中的苹果图标，好吧，看来是分区有问题，使用光盘的grub重启<br />root (hd0,0)<br />chainloader +1<br />boot<br />4. 选择Windows选项，进入Windows，然后使用PQ将install分区的分区ID改为AF，因为这个分区应该是HFS＋格式的，可能由于某些原因，导致硬盘助手该分区格式没有成功。改完分区ID后使用光盘的grub重启<br />root (hd0,0)<br />\nchainloader +1<br />\nboot<br />5. 选择变色龙选项，进入变色龙启动菜单，好吧，这次苹果图标终于出来了，使用键盘右键选中，然后回车，进入灰苹果界面，很不错<br />6.然后等了10分钟，还是灰苹果界面，好吧，不得不承认，卡住了，重启进入BIOS<br />7. 苹果硬盘要求AHCI，修改BIOS，“将SATA硬盘识别为”从IDE改为AHCI，重启，使用光盘grub重启<br />\nroot (hd0,0)<br />\nchainloader +1<br />\nboot<br />8. 终于可以正常安装了，大约10分钟后，提示安装完成，重启，好吧，使用光盘grub重启<br />root (hd0,0)<br />chainloader +1<br />boot<br />9. 选择变色龙菜单，发现Mac分区已经变成苹果图标了，理论上讲，Mac已经安装成功了。选择Mac分区，回车，传说中的灰苹果又停留了10分钟，好吧，我承认，安装失败了，使用光盘grub重启<br />root (hd0,0)<br />chainloader +1<br />boot<br />10. 先不管Mac了，选择Windows，因为我要查些资料，突然Windows蓝屏了<br />11. 想起来了，Windows没有自带SATA驱动，所以如果在BIOS里选择SATA硬盘为AHCI模式，就会重启，如果在BIOS里选择SATA硬盘为IDE兼容硬盘，Mac就没办法用（即使选了，也不一定能用），好吧，我选择放弃Mac了<br />12. 重启进入BIOS，将SATA硬盘改回IDE兼容模式，使用光盘grub引导<br />root (hd0,0)<br />chainloader +1<br />boot<br />13. 进入Windows，将install分区和mac分区删除，然后在Windows的磁盘管理里，做了一件很蠢的事，格式化一块分区，比我更蠢的Windows磁盘管理工具报告了一个错误后就退出了，好吧，我安慰自己，这很正常，虽然格式化很简单，但是我不应该用Windows的磁盘管理的，打开我的电脑，发现了一件很刺激的，让我5秒钟石化的事，我的Win7分区没了，我的xx分区没了，我的项目文件分区也没了。这种事在本科毕业的时候发生过，硬盘60G数据全部格式化，但那之前做过一次备份，损失不是太严重。这次确实致命打击了。<br />14. 淡定，我只格式化一个空分区，而且失败了，所以应该只是硬盘分区表损坏，百度搜索分区修复工具，DiskGenius，大家都说好，我以前做U盘分区的时候也用过，很好很强大，搜索分区，保存分区表，修复MBR<br />15. 现在一切正常，恢复以前XP+Win7的双系统状态。<br /><span id=\"reply_content_912843\"><pre>回首向来萧瑟处,归去,也无风雨也无晴。</pre></span>',1,1,1,0,0,0,'1. 准备安装Mac，首先分出来两个40G的FAT32分区，卷标定为install和Mac，然后使用硬盘助手将ISO镜像写入install分区，然后用变色龙写入启动配置信息2.重启电脑，发现MBR损坏，不能启动。好吧，先不管他，使用PE光盘的光盘GRUB启动root (hd0,0)chainloader +1boot3.选择变色龙选项，进入变色龙启动菜单，遗憾的是，没有出现期望中的苹果图标，好吧，看来是分区有问题，使用光盘的grub重启root (hd0,0)chainloader +1boot4. 选择Windows选项，进入Windows，然后使用PQ将install分区的分区ID改为AF，因为这个分区应该是HFS＋格式的，可能由于某些原因，导致硬盘助手该分区格式没有成功。改完分区ID后使用光盘的grub重启root (hd0,0)\nchainloader +1\nboot5. 选择变色龙选项，进入变色龙启动菜单，好吧，这次苹果图标终于出来了，使用键盘右键选中，然后回车，进入灰苹果界面，很不错6.然后等了10分钟，还是灰苹果界面，好吧，不得不承认，卡住了，重启进入BIOS7. 苹果硬盘要求AHCI，修改BIOS，“将SATA硬盘识别为”从IDE改为AHCI，重启，使用光盘grub重启\nroot (hd0,0)\nchainloader +1\nboot8. 终于可以正常安装了，大约10分钟后，提示安装完成，重启，好吧，使用光盘grub重启root (hd0,0)chainloader +1boot9. 选择变色龙菜单，发现Mac分区已经变成苹果图标了，理论上讲，Mac已经安装成功了。选择Mac分区，回车，传说中的灰苹果又停留了10分钟，好吧，我承认，安装失败了，使用光盘grub重启root (hd0,0)chainloader +1boot10. 先不管Mac了，选择Windows，因为我要查些资料，突然Windows蓝屏了11. 想起来了，Windows没有自带SATA驱动，所以如果在BIOS里选择SATA硬盘为AHCI模式，就会重启，如果在BIOS里选择SATA硬盘为IDE兼容硬盘，Mac就没办法用（即使选了，也不一定能用），好吧，我选择放弃Mac了12. 重启进入BIOS，将SATA硬盘改回IDE兼容模式，使用光盘grub引导root (hd0,0)chainloader +1boot13. 进入Windows，将install分区和mac分区删除，然后在Windows的磁盘管理里，做了一件很蠢的事，格式化一块分区，比我更蠢的Windows磁盘管理工具报告了一个错误后就退出了，好吧，我安慰自己，这很正常，虽然格式化很简单，但是我不应该用Windows的磁盘管理的，打开我的电脑，发现了一件很刺激的，让我5秒钟石化的事，我的Win7分区没了，我的xx分区没了，我的项目文件分区也没了。这种事在本科毕业的时候发生过，硬盘60G数据全部格式化，但那之前做过一次备份，损失不是太严重。这次确实致命打击了。14. 淡定，我只格式化一个空分区，而且失败了，所以应该只是硬盘分区表损坏，百度搜索分区修复工具，DiskGenius，大家都说好，我以前做U盘分区的时候也用过，很好很强大，搜索分区，保存分区表，修复MBR15. 现在一切正常，恢复以前XP+Win7的双系统状态。回首向来萧瑟处,归去,也无风雨也无晴。','',0,'admin',0,'','zinnia/_entry_detail.html','entry_detail.html'),(181,'父类、构造函数、成员对象的调用时机','33731937201010633157934',2,'2010-11-06 16:30:46',NULL,'2010-11-06 16:30:46','2014-07-13 18:57:17','在使用new创建对象时，主要完成四项工作：<br />1 构造父类成员对象<br />2 构造子类成员对象<br />3 调用父类构造函数<br />4 调用子类构造函数<br />这四项工作的时间顺序是怎样的呢？<br />原则是：<b>先父类后子类，先成员后函数</b><br /><b>一级继承</b><br />例如：<br />class X{<br />&nbsp;&nbsp;&nbsp; public:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; X(){cout&lt;&lt;\"X\";}<br />};<br />class A{<br />&nbsp;&nbsp;&nbsp; public:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; X x1;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; A(){cout&lt;&lt;\"A\";}<br />};<br />class B: public A{<br />&nbsp;&nbsp;&nbsp; public:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; X x2;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; B(){cout&lt;&lt;\"B\";}<br />};<br />int main(){<br />&nbsp;&nbsp;&nbsp; B b;<br />}<br />构造对象B时，根据第1个原则：先父类后子类，先构造A(1)，然后构造B(2)。<br />构造A(1):<br />&nbsp;然后根据第2个原则，先成员后函数，先构造A的成员x1,即先调用X构造函数，然后再调用A构造函数<br />构造B(2):<br />&nbsp;根据第2个原则，先成员后函数，先构造B的成员x2,即先调用X构造函数，然后再调用B构造函数<br />因此输出为:<br />XAXB<br /><br /><b>多级继承</b><br />在一级继承中这个比较容易分析，在多级继承中就会复杂些，但坚持这两个原则，细心分析也不会出问题的。<br />#include &lt;iostream&gt;<br />#include &lt;fstream&gt;<br />#include &lt;cstring&gt;<br />using namespace std;<br />class X{<br />&nbsp;&nbsp;&nbsp; public:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; X(){cout&lt;&lt;\"X\";}<br />};<br />class Y{<br />&nbsp;&nbsp;&nbsp; public:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Y(){cout&lt;&lt;\"Y\";}<br />};<br /><br />class A{<br />&nbsp;&nbsp;&nbsp; public:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; A(){cout&lt;&lt;\"A\";}<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ~A(){cout&lt;&lt;\"~A\";}<br />};<br />class B: public A{<br />&nbsp;&nbsp;&nbsp; public:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Y x;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; A a;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Y y;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; B(){cout&lt;&lt;\"B\";}<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ~B(){cout&lt;&lt;\"~B\";}<br />};<br />class C: public B{<br />&nbsp;&nbsp;&nbsp; protected:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; X x;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; B b;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; A a;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; X y;<br />&nbsp;&nbsp;&nbsp; public:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C(){cout&lt;&lt;\"C\";}<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ~C(){cout&lt;&lt;\"~C\";}<br />};<br />int main(){<br />&nbsp;&nbsp;&nbsp; C *c = new C();<br />&nbsp;&nbsp;&nbsp; delete c;<br />}<br />这个程序的输出就比较复杂：<br />AYAYBXAYAYBAXC~C~A~B~A~A~B~A~A<br />开始分析：<br />先父类后子类，所以先构造B(1)，然后构造C(2)<br />构造B(1)的过程如下:<br />也是先构造A(1.1)，然后构造B(1.2)<br /><br />构造A(1.1)的过程如下：<br />调用A的构造函数，输出A(第一个输出字符)<br /><br />构造B(1.2)的过程如下：<br />先成员(1.2.1)后构造(1.2.2)，<br />成员(1.2.1)的过程如下：<br />B的成员有Y,A,Y，所以构造Y,A,Y，输出YAY(第二到四个字符)<br />构造(1.2.2)的过程如下：<br />B的构造函数，输出B(第五个字符)<br />所以构造B一共会输出YAYB四个字符<br /><br />构造C(2)的过程如下：<br />先成员(2.1)后构造(2.2)<br />成员(2.1)的过程如下：<br />C的成员有XBAX<br />所以构造X，输出X(第六个字符)<br />构造B输出AYAYB(第七到十一个字符)<br />构造A输出A(第十二个字符)<br />构造X输出X(第十三个字符)<br />构造(2.2)<br />输出C(第十四个字符)<br /><br />最后按照完全相反的顺序调用析构函数',1,1,1,0,0,0,'在使用new创建对象时，主要完成四项工作：1 构造父类成员对象2 构造子类成员对象3 调用父类构造函数4 调用子类构造函数这四项工作的时间顺序是怎样的呢？原则是：先父类后子类，先成员后函数一级继承例如：class X{&nbsp;&nbsp;&nbsp; public:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; X(){cout&lt;&lt;\"X\";}};class A{&nbsp;&nbsp;&nbsp; public:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; X x1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; A(){cout&lt;&lt;\"A\";}};class B: public A{&nbsp;&nbsp;&nbsp; public:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; X x2;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; B(){cout&lt;&lt;\"B\";}};int main(){&nbsp;&nbsp;&nbsp; B b;}构造对象B时，根据第1个原则：先父类后子类，先构造A(1)，然后构造B(2)。构造A(1):&nbsp;然后根据第2个原则，先成员后函数，先构造A的成员x1,即先调用X构造函数，然后再调用A构造函数构造B(2):&nbsp;根据第2个原则，先成员后函数，先构造B的成员x2,即先调用X构造函数，然后再调用B构造函数因此输出为:XAXB多级继承在一级继承中这个比较容易分析，在多级继承中就会复杂些，但坚持这两个原则，细心分析也不会出问题的。#include &lt;iostream&gt;#include &lt;fstream&gt;#include &lt;cstring&gt;using namespace std;class X{&nbsp;&nbsp;&nbsp; public:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; X(){cout&lt;&lt;\"X\";}};class Y{&nbsp;&nbsp;&nbsp; public:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Y(){cout&lt;&lt;\"Y\";}};class A{&nbsp;&nbsp;&nbsp; public:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; A(){cout&lt;&lt;\"A\";}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ~A(){cout&lt;&lt;\"~A\";}};class B: public A{&nbsp;&nbsp;&nbsp; public:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Y x;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; A a;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Y y;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; B(){cout&lt;&lt;\"B\";}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ~B(){cout&lt;&lt;\"~B\";}};class C: public B{&nbsp;&nbsp;&nbsp; protected:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; X x;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; B b;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; A a;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; X y;&nbsp;&nbsp;&nbsp; public:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C(){cout&lt;&lt;\"C\";}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ~C(){cout&lt;&lt;\"~C\";}};int main(){&nbsp;&nbsp;&nbsp; C *c = new C();&nbsp;&nbsp;&nbsp; delete c;}这个程序的输出就比较复杂：AYAYBXAYAYBAXC~C~A~B~A~A~B~A~A开始分析：先父类后子类，所以先构造B(1)，然后构造C(2)构造B(1)的过程如下:也是先构造A(1.1)，然后构造B(1.2)构造A(1.1)的过程如下：调用A的构造函数，输出A(第一个输出字符)构造B(1.2)的过程如下：先成员(1.2.1)后构造(1.2.2)，成员(1.2.1)的过程如下：B的成员有Y,A,Y，所以构造Y,A,Y，输出YAY(第二到四个字符)构造(1.2.2)的过程如下：B的构造函数，输出B(第五个字符)所以构造B一共会输出YAYB四个字符构造C(2)的过程如下：先成员(2.1)后构造(2.2)成员(2.1)的过程如下：C的成员有XBAX所以构造X，输出X(第六个字符)构造B输出AYAYB(第七到十一个字符)构造A输出A(第十二个字符)构造X输出X(第十三个字符)构造(2.2)输出C(第十四个字符)最后按照完全相反的顺序调用析构函数','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(182,'相似性 十戒 太极图','337319372009665455215',2,'2009-07-06 17:04:55',NULL,'2009-07-06 17:04:55','2014-07-13 18:57:17','传说中的福柯，据说好比二十世纪的马克思；找来一本福柯的《词与物》，可惜对西方哲学一无所知，读来犹如天书，抱着“好读书而不求甚解，每有所得便悠然自得”的态度看下去，竟然颇有点意思。刚看到相似性理论。<br />相似性是一种普遍存在的规律，甚至表面看来完全不相干的事物也有相似性存在。<br /><br />西方哲学建立在“上帝”以及上帝的《圣经》基础上，而东方哲学建立在孔子的毫无根据的伦理道德上。简而言之，两套哲学都是为了解决“什么应该做，什么不应该做”的问题。而所谓济世之道，则是“怎么做”的问题。<br /><br />佛家的五戒：不杀生，不偷盗，不饮酒，不妄语，不淫邪<br />基督教的《The Ten Commandments》十戒，<br />1.除了我以外，你不可有别的神。<br />2.不可为自己雕刻偶像，也不可作甚么形像仿佛上天、下地，和地底下、水中的百物。<br />不可跪拜那些像，也不可事奉他，因为我耶和华—你的神是忌邪的神。恨我的，我必追讨他的罪，自父及子，直到三四代；爱我、守我诫命的，我必向他们发慈爱，直到千代。<br />3.不可妄称耶和华—你神的名；因为妄称耶和华名的，耶和华必不以他为无罪。<br />4.当纪念安息日，守为圣日。六日要劳碌作你一切的工，但第七日是向耶和华—你神当守的安息日。这一日你和你的儿女、仆婢、牲畜，并你城里寄居的客旅，无论何工都不可作；因为六日之内，耶和华造天、地、海，和其中的万物，第七日便安息，所以耶和华赐福与安息日，定为圣日。<br />5.当孝敬父母，使你的日子在耶和华—你神所赐你的地上得以长久。<br />6.不可杀人。<br />7.不可奸淫。<br />8.不可偷盗。<br />9.不可作假见证陷害人。<br />10.不可贪恋人的房屋；也不可贪恋人的妻子、仆婢、牛驴，并他一切所有的。 <br /><br />昨晚的星空，如太极图，天边一圈白云，南北两半天中间各有一块蓝色无云区，恰有两颗星各位于蓝色无云区的中心，如阴阳鱼。<br />',1,1,1,0,0,0,'传说中的福柯，据说好比二十世纪的马克思；找来一本福柯的《词与物》，可惜对西方哲学一无所知，读来犹如天书，抱着“好读书而不求甚解，每有所得便悠然自得”的态度看下去，竟然颇有点意思。刚看到相似性理论。相似性是一种普遍存在的规律，甚至表面看来完全不相干的事物也有相似性存在。西方哲学建立在“上帝”以及上帝的《圣经》基础上，而东方哲学建立在孔子的毫无根据的伦理道德上。简而言之，两套哲学都是为了解决“什么应该做，什么不应该做”的问题。而所谓济世之道，则是“怎么做”的问题。佛家的五戒：不杀生，不偷盗，不饮酒，不妄语，不淫邪基督教的《The Ten Commandments》十戒，1.除了我以外，你不可有别的神。2.不可为自己雕刻偶像，也不可作甚么形像仿佛上天、下地，和地底下、水中的百物。不可跪拜那些像，也不可事奉他，因为我耶和华—你的神是忌邪的神。恨我的，我必追讨他的罪，自父及子，直到三四代；爱我、守我诫命的，我必向他们发慈爱，直到千代。3.不可妄称耶和华—你神的名；因为妄称耶和华名的，耶和华必不以他为无罪。4.当纪念安息日，守为圣日。六日要劳碌作你一切的工，但第七日是向耶和华—你神当守的安息日。这一日你和你的儿女、仆婢、牲畜，并你城里寄居的客旅，无论何工都不可作；因为六日之内，耶和华造天、地、海，和其中的万物，第七日便安息，所以耶和华赐福与安息日，定为圣日。5.当孝敬父母，使你的日子在耶和华—你神所赐你的地上得以长久。6.不可杀人。7.不可奸淫。8.不可偷盗。9.不可作假见证陷害人。10.不可贪恋人的房屋；也不可贪恋人的妻子、仆婢、牛驴，并他一切所有的。 昨晚的星空，如太极图，天边一圈白云，南北两半天中间各有一块蓝色无云区，恰有两颗星各位于蓝色无云区的中心，如阴阳鱼。','',0,'mood',0,'','zinnia/_entry_detail.html','entry_detail.html'),(183,'C++全局指针初始化的区别','33731937201031465336423',2,'2010-04-14 18:53:36',NULL,'2010-04-14 18:53:36','2014-07-13 18:57:17','对一个轨迹球全局指针进行初始化：<br />方法一：<br />ArcBallT myBall(600.0f, 400.0f);<br />ArcBallT *arcBall = &amp;myBall;<br /><span style=\"font-weight: bold;\">一切正常</span><br />方法二：<br />ArcBallT *arcBall = new ArcBallT(600.0f, 400.0f);<br /><span style=\"font-weight: bold;\">运行正常，结果出错</span><br />为什么呢？<br />',1,1,1,0,0,0,'对一个轨迹球全局指针进行初始化：方法一：ArcBallT myBall(600.0f, 400.0f);ArcBallT *arcBall = &amp;myBall;一切正常方法二：ArcBallT *arcBall = new ArcBallT(600.0f, 400.0f);运行正常，结果出错为什么呢？','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(184,'对于模式的“十大误解”转贴','3373193720078268112757',2,'2007-09-26 20:11:02',NULL,'2007-09-26 20:11:02','2014-07-13 18:57:17','<p><br />&nbsp;<br />【译者语】现在“模式”这个词真是非常流行。就象任何流行的东西一样，对它的误解也真是不少。甚至在一些发表出来的文章中，也存在着各种各样的误解， 我想这会对读者造成非常糟糕的引导作用。早已想写一篇文章来澄清一些对模式的误解，却又因为水平所限难以成文。恰在此时， 我看到John Vlissides先生的《十大误解》，于是我便乐得当文抄公了。</p>  <p>??关于设计模式，下面有十种错误的观点??很多都是很流行的观点。且看Vlissides先生如何拨开这些迷雾。</p>  <p>??最近，围绕着模式的讨论日嚣尘上，人们对模式的混淆、惊惶和以讹传讹已经不是一点半点。这也从一个侧面反映出对于主流软件开发者来说，模式是一个多么新鲜的领域??尽管严格说来，它并不是一个新的领域。同时，模式也是一个飞速发展的领域，留下了大片的空白。而且，没错，我们这些模式的支持者也应该受批评，因为我们没有把自己的知识完完全全的教给别人。尽管我们这样想过，但是却没有这样做[BMR+96, Coplien96, CS95, GoF95, MRB98, VCK96]。</p>  <p>??因此，我觉得自己有责任来纠正一些模式方面越来越夸张的误解??我常常听到的那些误解都足以形成自己的模式了。甚至连我也曾经想以模式来组成自己的软件结构……后来我才明白：“把所有的东西都变成模式”，这种想法本身就是错误的！总之，请记住，这篇文章不是在给模式社群的人们看的。我想，绝大多数的模式专家都会同意：下面这些都是很常见的误解。但是他们也许不会同意我消除这些误解的方式。</p>  <p>??这几年以来，我听到过许多人谈论模式。把听到的这些东西整理一下，对模式的误解大概有三类：对于“模式是什么”的误解，对于“模式可以做什么”的误解，以及对于支持模式的人群的误解。我的“十大误解”都落入这三类之中，所以我就把这些误解都组织起来。首先，我们来看看关于“模式是什么”的误解。</p>  <p>??误解之一：“模式是某种场景下某个问题的解决方案”</p>  <p>??这个定义来自于Christopher Alexander[AIS+77]，所以如果把它称为“误解”，也许有人会把我目为异端。但是下面这个简单的反例就能让你看清它的缺点：</p>  <p>??问题：我获奖的奖券就快过期了，我怎样拿到奖金？<br />??场景：离截止时间只有一小时，可是我家的小狗把奖券给吞了。<br />??解决方案：把狗开膛破肚，掏出奖券，然后飞奔到最近的兑奖地点。</p>  <p>??这是“某种场景下某个问题的解决方案”，但它不是模式。还缺了什么？至少缺三样东西：</p>  <p>??1. 可重复性。解决方案应该对应于外部的场景。<br />??2. 可传授性。一个解决方案应该可以移植到问题的不同情况上。（绝大多数模式的可传授性都建立在“约束”和“效果”的基础上。）<br />??3. 用来表示这个模式的名称。</p>  <p>??确实，很难找到一个令人满意的定义。“模式讨论”邮件列表（<a href=\"mailto:patterns-discussion@cs.uiuc.edu\" rel=\"nofollow\">patterns-discussion@cs.uiuc.edu</a>）上正在进行的讨论也证明了这一点。难以定义的一大原因是：模式既是一个事物，也是对类似事物的描述。要区分这两者，有一种办法：“模式”这个术语只用来指代模式的描述，同时用“模式实例”来指代模式的具体应用。</p>  <p>??但是，术语的定义很可能是白费力气，因为一个定义可能对一个人（比如程序员）有意义，但是对另一个人（比如只能看到书面材料的项目主管）却毫无意义。当然，我不打算在这里提出什么最终定义。但是，任何对模式要素的规定，除了必须包括问题、解决方案和场景之外，都必须提及可重复性、可传授性和名称。</p>  <p>??误解之二：“模式就是行话、规则、编程技巧、数据结构……”</p>  <p>??我通常把这些误解统称为“蔑视”。如果你试图把某些不熟悉的东西简化为熟悉的东西，产生这种想法是很自然的，尤其是当你没有特别的兴趣去钻研这些不熟悉的东西时。另外，某些人经常拿新瓶装陈酒，然后大吹“创新”、“革命”一类的口号。保持警惕也是好的。</p>  <p>??但是，这种蔑视通常不是来自亲身体验，而是来自肤浅的认识和一点点冷嘲热讽的。而且，没有什么东西是真正“全新”的。人们的脑海中一直都存在着各种各样的模式，只不过我们现在刚开始为模式命名、将模式记录下来。</p>  <p>??来逐个说明这些看法：的确存在着模式的行话，例如“模式”这个词，例如“约束”，例如Alexander的“无名质量”，等等。但是模式是不能简化为行话的。与其他计算机科学领域相比，模式引入的新术语实在是少得可怜。对于听众来说，好的模式本来就很容易接受。在说明一个模式的时候也许有必要引用问题领域的行话，但是几乎没有必要使用什么特定于模式领域的术语。</p>  <p>??没有哪个模式是能让你不假思索就使用的规则（组件则正好相反），同时模式也不仅仅是“编程技巧”，尽管模式中的“方言（idiom）” 部分是特定于语言的。在我听来，“技巧”这个词带有轻蔑的意思，并且过分强调解决方案，而忽视了问题、场景和名称的重要性。</p>  <p>??毫无疑问，你也曾经听说过一次创新被接受的三个阶段：首先，它被当作垃圾扔在一边；然后，人们会认为它不具可实施性；最后，大家都明白它的意义时，它也没有意义了??“我们一直都这样做”。现在，模式还没有完全走出第一个阶段。</p>  <p>??误解之三：“理解一个，就理解了全部”</p>  <p>??一刀切的规则往往是不公平的，而人们对模式却更加一刀切。模式的领域、内容、范围、形式、质量……这个领域大得吓人，只需要随手翻翻PLoP 的书[CS95, MRB98, VCK96]就能看出。不同的人写出不同的模式，而模式的变化甚至比作者还要多。象Alistair Cockburn、Jim Coplien、Neil Harrison 和Ralph Johnson 这样的作者已经超越了最初大量记录不同领域、不同形式模式的阶段。只看几个例子就对模式做一个笼统的结论，这样的结论必定是错误的。</p>  <p>??误解之四：“模式需要有工具或方法学的支持才会有效”</p>  <p>??在过去的五年中，我记录、使用并且帮助其他人使用模式，还至少帮助完成了一个基于模式的工具[BFV+96]，所以我可以很有把握的说：模式的绝大多数好处都来自于原封不动的使用??也就是说，没有任何形式的外部支持。</p>  <p>??在谈到这个主题的时候，我通常会指出模式带来的四大好处：</p>  <p>??1. 它们记录了专家的经验，并且让非专家也能理解。<br />??2. 它们的名称构成了一份词汇表，帮助开发者更好的交流。<br />??3. 它们帮助人们更快的理解一个系统??只要这个系统是用模式的方式描述的。<br />??4. 它们使系统的重组变得更容易，不管原来的系统是否以模式的方式设计的。</p>  <p>??过去，我一直认为第一项是模式最大的好处。现在我认识到，第二条起码也同样重要。请想一想，在一个开发项目的过程中，有多少字节的信息在开发者之间流动（包括口头的和电子的）？我猜就算没有1G也有好几兆。（在推出了《设计模式》之后，我已经收到了好几十兆给GoF的电子邮件。而且我们所描述的还都是小型到中型的软件开发项目。）由于有如此之大的信息交流量，所以效率上任何微小的提升都能大量节约时间。在这个意义上，模式拓宽了人们交流的带宽。我对第三、四条的评价也在逐渐提高，特别是在项目越来越大、软件生存周期越来越长的今天。</p>  <p>??至少在短期内，模式主要存在于大脑中，而不存在于工具中。如果有了方法学或自动化工具的支持，应该还有其他的收益，但是我相信这些都只是蛋糕上的奶油，而不是蛋糕本身，甚至都不能算蛋糕的一层。</p>  <p>* * *</p>  <p>??到目前为止，我说到的误解都是关于“模式是什么”的。现在来看看关于“模式可以做什么”的误解。这里有两种不同的风格：夸大其词的和心存疑虑的。</p>  <p>??误解之五：“模式可以保证软件的复用性、更高的生产力、世界的和平……”</p>  <p>??道理很简单，模式什么都不保证。它们甚至有可能无法给你带来利益。在创造新事物的过程中，模式无法取代人的位置。它们只是带来一种希望，有可能让一个缺乏经验的、甚至是未入门的，但是有能力、有创造性的人尽快获得设计的能力。</p>  <p>??人们经常说：好的模式会让读者有“啊！”的回应。实际上，只有当模式恰好拨动了读者的某一根心弦时，他们才会有这样的反应。如果没有，模式就只象森林里普通的一棵树。因此，什么是好的模式？不管它写得有多好，如果不能引起读者的共鸣，它又怎能算是好的模式？</p>  <p>??模式只是开发者的工具箱中的另一件工具，对模式寄以过高的期望只会适得其反。准备充分，然后做最坏的打算??这就是防止受骗、消除对抗最好的方法。</p>  <p>??误解之六：“模式可以‘生成’整个软件体系”</p>  <p>??这个误解与上一个有点相似，不过侵略性稍微少些。</p>  <p>??每过一段时间，模式论坛上就会讨论一次模式的生成能力。按照我的理解，“生成能力”是指模式创建最终行为的能力。很多人认为，模式可以帮助读者解决模式本身没有明确提出的问题。我读过的一些书里也有同样的观点。</p>  <p>??对于我来说，“生成能力”的关键是模式的可传授性??约束及其解决，或者对于效果的讨论。在你定义、精炼一个体系结构时，这些观察是特别有用的。但是模式本身并不制造任何东西??任何东西都是由人来制造的。而且，模式不可能覆盖体系结构的每个方面。给我任何一个有实际价值的设计，我都能找出其中模式没有涉及的设计问题。也许这些问题不常见、不具可重复性，或者只是还没有以模式的形式记录下来。不管怎样，你都必须负责填补模式之间的空白??用你自己的创造性。</p>  <p>??误解之七：“模式是针对（面向对象）设计或实现的”</p>  <p>??另一个极端则是过分的限制，就象这个误解。坦白说，任何人都完全可能相信它，我不会有丝毫惊讶。无数的人问过我这个问题，所以它也进入了“十大误解”之列。如果你觉得这种观点实在天真幼稚，跳过这一部分吧。</p>  <p>??如果模式不能记述专家的经验，那它们就什么都不是。对于模式的作者来说，任何形式的专家经验都是可记载的。当然，在面向对象软件设计中有值得记载的经验，但是在非面向对象设计和分析、维护、测试、文档、组织结构……这些方面也同样有值得记载的经验。现在，不同领域中的模式开始浮出水面。至少已经有两本关于分析模式的书[Fowler97, Hay96]，并且每次PLoP大会都会收到新的模式类型。（特别有趣的是1996年的PLoP大会甚至关注了音乐作曲的模式！）</p>  <p>??和大多数的误解一样，这个误解也是有原因的。看看人们用来描述模式的格式，有两种基本的风格：描述高层结构的GoF风格（用于《设计模式》中）；接近文学的Christopher Alexander 风格??叙述性的，尽量少的结构图。由于已经用模式描述了面向对象设计之外的东西，所以我现在认识到GoF风格造成的偏差。对于我研究过的某些领域的专家经验，这种风格根本无法描述。为什么结构图看上去总是让人联想到C++？对于音乐作曲的模式，“实现”应该是什么？“协作”部分真的有意义吗？</p>  <p>??很明显，一种格式不能适应所有的需求。比较具有普遍意义的是模式的概念：模式是记载并传达专家经验的工具??不论是哪个领域的专家经验。</p>  <p>??误解之八：“没有证据表明模式帮助过任何人”</p>  <p>??这种观点曾经有一定的说服力，但是现在已经没有了。在Software-Practice and Experience[Kotula96]这样的杂志上、在OOPSLA[HJE95, Schmid95]和ICSE[BCC+96]这样的会议上，人们不断的报告自己从模式得到的利益。Doug Schmidt已经清楚的说明了在传授计算机科学时使用模式的收益[PD96]。尽管绝大多数的证据都只是定性的，但是据我所知，至少有一个组织得到了一些定量的结论[Prechelt97,PUS97]。</p>  <p>??随着时间推进，我们需要更好的掌握使用模式的好处和缺点。尽管最初的反馈已经让我们看到了希望，但是我们还需要更多的实践经验来做全面的估计。但是，如果仅仅因为模式的好处还没有完全证实就拒绝使用模式，那你就真的是个大傻瓜了。</p>  <p>* * *</p>  <p>??关于“模式能干什么”的谬论还真不少。下面，最后两种误解不是关于模式本身，而是关于支持使用模式的人们的。</p>  <p>??误解之九：“模式社群是精英的小圈子”</p>  <p>??我很想知道这种观念到底是从哪里来的。如果说模式社群的人们有什么引人注目的地方，那就是他们之间巨大的差异。从PLoP大会的与会者名单就能看出??人们来自世界各地，有大公司的也有小公司的，有分析员、设计者和实现者，有学生也有教授，有大名鼎鼎的作者也有初出茅庐的菜鸟。更让我惊讶的是，竟然有不少与会者都不是计算机科学工作者！这个社群仍然在不停变化，每年的与会者名单都与前一年不同。</p>  <p>??如果将模式社群的背景公开出来，别人也许会觉得奇怪：很少有学院派人士。实际上，绝大多数PLoP的与会者都是实践者。软件模式早期的推崇者??包括Kent Beck、Peter Coad 和Ward Cunningham??都没有学院背景。GoF中只有一个人（Ralph）是学院派，而且他也是我所认识的最实际的学院派。很明显，模式社群从根本上反对任何可能的宗派主义和精英论。</p>  <p>??误解之十：“模式社群是自私的，甚至是阴谋家”</p>  <p>??我不止一次的听人指责说：模式最大的用处就是为写模式书籍的人带来了源源不断的收入。他们甚至还暗示模式的发展有着某种邪恶的目的。</p>  <p>??胡说八道！</p>  <p>??作为GoF的一员，我可以肯定的告诉你：对于《设计模式》引起的反响，我们和其他任何人一样吃惊。对于它在OOPSLA 94上的初次亮相引起的暴风骤雨，我们也同样没有准备??甚至出版商都没有预料到会有这么大的销量。在整个写作过程中，我们最关心的就是尽量写出一本高质量的书，至于销售上的问题，我们根本没有时间去想。现在“模式”已经成了一个时髦的词汇，不可避免的会有一些人将这个词用来谋取私利。但是，如果你认真读过顶尖的模式作者的作品，你就会感觉到他们共同的目标：将难以获取的经验、最佳的实践、甚至是竞争中的优势??多年实践经验的累积??分享给读者，而且讲解得一清二楚。正是这种帮助读者的热情激励着每一个真诚而踏实的模式作者。如果没有这种热情，作者就会弄巧成拙??这种不负责任的作者往往正是对模式所有误解的根源 <br /></p>',1,1,1,0,0,0,'&nbsp;【译者语】现在“模式”这个词真是非常流行。就象任何流行的东西一样，对它的误解也真是不少。甚至在一些发表出来的文章中，也存在着各种各样的误解， 我想这会对读者造成非常糟糕的引导作用。早已想写一篇文章来澄清一些对模式的误解，却又因为水平所限难以成文。恰在此时， 我看到John Vlissides先生的《十大误解》，于是我便乐得当文抄公了。  ??关于设计模式，下面有十种错误的观点??很多都是很流行的观点。且看Vlissides先生如何拨开这些迷雾。  ??最近，围绕着模式的讨论日嚣尘上，人们对模式的混淆、惊惶和以讹传讹已经不是一点半点。这也从一个侧面反映出对于主流软件开发者来说，模式是一个多么新鲜的领域??尽管严格说来，它并不是一个新的领域。同时，模式也是一个飞速发展的领域，留下了大片的空白。而且，没错，我们这些模式的支持者也应该受批评，因为我们没有把自己的知识完完全全的教给别人。尽管我们这样想过，但是却没有这样做[BMR+96, Coplien96, CS95, GoF95, MRB98, VCK96]。  ??因此，我觉得自己有责任来纠正一些模式方面越来越夸张的误解??我常常听到的那些误解都足以形成自己的模式了。甚至连我也曾经想以模式来组成自己的软件结构……后来我才明白：“把所有的东西都变成模式”，这种想法本身就是错误的！总之，请记住，这篇文章不是在给模式社群的人们看的。我想，绝大多数的模式专家都会同意：下面这些都是很常见的误解。但是他们也许不会同意我消除这些误解的方式。  ??这几年以来，我听到过许多人谈论模式。把听到的这些东西整理一下，对模式的误解大概有三类：对于“模式是什么”的误解，对于“模式可以做什么”的误解，以及对于支持模式的人群的误解。我的“十大误解”都落入这三类之中，所以我就把这些误解都组织起来。首先，我们来看看关于“模式是什么”的误解。  ??误解之一：“模式是某种场景下某个问题的解决方案”  ??这个定义来自于Christopher Alexander[AIS+77]，所以如果把它称为“误解”，也许有人会把我目为异端。但是下面这个简单的反例就能让你看清它的缺点：  ??问题：我获奖的奖券就快过期了，我怎样拿到奖金？??场景：离截止时间只有一小时，可是我家的小狗把奖券给吞了。??解决方案：把狗开膛破肚，掏出奖券，然后飞奔到最近的兑奖地点。  ??这是“某种场景下某个问题的解决方案”，但它不是模式。还缺了什么？至少缺三样东西：  ??1. 可重复性。解决方案应该对应于外部的场景。??2. 可传授性。一个解决方案应该可以移植到问题的不同情况上。（绝大多数模式的可传授性都建立在“约束”和“效果”的基础上。）??3. 用来表示这个模式的名称。  ??确实，很难找到一个令人满意的定义。“模式讨论”邮件列表（patterns-discussion@cs.uiuc.edu）上正在进行的讨论也证明了这一点。难以定义的一大原因是：模式既是一个事物，也是对类似事物的描述。要区分这两者，有一种办法：“模式”这个术语只用来指代模式的描述，同时用“模式实例”来指代模式的具体应用。  ??但是，术语的定义很可能是白费力气，因为一个定义可能对一个人（比如程序员）有意义，但是对另一个人（比如只能看到书面材料的项目主管）却毫无意义。当然，我不打算在这里提出什么最终定义。但是，任何对模式要素的规定，除了必须包括问题、解决方案和场景之外，都必须提及可重复性、可传授性和名称。  ??误解之二：“模式就是行话、规则、编程技巧、数据结构……”  ??我通常把这些误解统称为“蔑视”。如果你试图把某些不熟悉的东西简化为熟悉的东西，产生这种想法是很自然的，尤其是当你没有特别的兴趣去钻研这些不熟悉的东西时。另外，某些人经常拿新瓶装陈酒，然后大吹“创新”、“革命”一类的口号。保持警惕也是好的。  ??但是，这种蔑视通常不是来自亲身体验，而是来自肤浅的认识和一点点冷嘲热讽的。而且，没有什么东西是真正“全新”的。人们的脑海中一直都存在着各种各样的模式，只不过我们现在刚开始为模式命名、将模式记录下来。  ??来逐个说明这些看法：的确存在着模式的行话，例如“模式”这个词，例如“约束”，例如Alexander的“无名质量”，等等。但是模式是不能简化为行话的。与其他计算机科学领域相比，模式引入的新术语实在是少得可怜。对于听众来说，好的模式本来就很容易接受。在说明一个模式的时候也许有必要引用问题领域的行话，但是几乎没有必要使用什么特定于模式领域的术语。  ??没有哪个模式是能让你不假思索就使用的规则（组件则正好相反），同时模式也不仅仅是“编程技巧”，尽管模式中的“方言（idiom）” 部分是特定于语言的。在我听来，“技巧”这个词带有轻蔑的意思，并且过分强调解决方案，而忽视了问题、场景和名称的重要性。  ??毫无疑问，你也曾经听说过一次创新被接受的三个阶段：首先，它被当作垃圾扔在一边；然后，人们会认为它不具可实施性；最后，大家都明白它的意义时，它也没有意义了??“我们一直都这样做”。现在，模式还没有完全走出第一个阶段。  ??误解之三：“理解一个，就理解了全部”  ??一刀切的规则往往是不公平的，而人们对模式却更加一刀切。模式的领域、内容、范围、形式、质量……这个领域大得吓人，只需要随手翻翻PLoP 的书[CS95, MRB98, VCK96]就能看出。不同的人写出不同的模式，而模式的变化甚至比作者还要多。象Alistair Cockburn、Jim Coplien、Neil Harrison 和Ralph Johnson 这样的作者已经超越了最初大量记录不同领域、不同形式模式的阶段。只看几个例子就对模式做一个笼统的结论，这样的结论必定是错误的。  ??误解之四：“模式需要有工具或方法学的支持才会有效”  ??在过去的五年中，我记录、使用并且帮助其他人使用模式，还至少帮助完成了一个基于模式的工具[BFV+96]，所以我可以很有把握的说：模式的绝大多数好处都来自于原封不动的使用??也就是说，没有任何形式的外部支持。  ??在谈到这个主题的时候，我通常会指出模式带来的四大好处：  ??1. 它们记录了专家的经验，并且让非专家也能理解。??2. 它们的名称构成了一份词汇表，帮助开发者更好的交流。??3. 它们帮助人们更快的理解一个系统??只要这个系统是用模式的方式描述的。??4. 它们使系统的重组变得更容易，不管原来的系统是否以模式的方式设计的。  ??过去，我一直认为第一项是模式最大的好处。现在我认识到，第二条起码也同样重要。请想一想，在一个开发项目的过程中，有多少字节的信息在开发者之间流动（包括口头的和电子的）？我猜就算没有1G也有好几兆。（在推出了《设计模式》之后，我已经收到了好几十兆给GoF的电子邮件。而且我们所描述的还都是小型到中型的软件开发项目。）由于有如此之大的信息交流量，所以效率上任何微小的提升都能大量节约时间。在这个意义上，模式拓宽了人们交流的带宽。我对第三、四条的评价也在逐渐提高，特别是在项目越来越大、软件生存周期越来越长的今天。  ??至少在短期内，模式主要存在于大脑中，而不存在于工具中。如果有了方法学或自动化工具的支持，应该还有其他的收益，但是我相信这些都只是蛋糕上的奶油，而不是蛋糕本身，甚至都不能算蛋糕的一层。  * * *  ??到目前为止，我说到的误解都是关于“模式是什么”的。现在来看看关于“模式可以做什么”的误解。这里有两种不同的风格：夸大其词的和心存疑虑的。  ??误解之五：“模式可以保证软件的复用性、更高的生产力、世界的和平……”  ??道理很简单，模式什么都不保证。它们甚至有可能无法给你带来利益。在创造新事物的过程中，模式无法取代人的位置。它们只是带来一种希望，有可能让一个缺乏经验的、甚至是未入门的，但是有能力、有创造性的人尽快获得设计的能力。  ??人们经常说：好的模式会让读者有“啊！”的回应。实际上，只有当模式恰好拨动了读者的某一根心弦时，他们才会有这样的反应。如果没有，模式就只象森林里普通的一棵树。因此，什么是好的模式？不管它写得有多好，如果不能引起读者的共鸣，它又怎能算是好的模式？  ??模式只是开发者的工具箱中的另一件工具，对模式寄以过高的期望只会适得其反。准备充分，然后做最坏的打算??这就是防止受骗、消除对抗最好的方法。  ??误解之六：“模式可以‘生成’整个软件体系”  ??这个误解与上一个有点相似，不过侵略性稍微少些。  ??每过一段时间，模式论坛上就会讨论一次模式的生成能力。按照我的理解，“生成能力”是指模式创建最终行为的能力。很多人认为，模式可以帮助读者解决模式本身没有明确提出的问题。我读过的一些书里也有同样的观点。  ??对于我来说，“生成能力”的关键是模式的可传授性??约束及其解决，或者对于效果的讨论。在你定义、精炼一个体系结构时，这些观察是特别有用的。但是模式本身并不制造任何东西??任何东西都是由人来制造的。而且，模式不可能覆盖体系结构的每个方面。给我任何一个有实际价值的设计，我都能找出其中模式没有涉及的设计问题。也许这些问题不常见、不具可重复性，或者只是还没有以模式的形式记录下来。不管怎样，你都必须负责填补模式之间的空白??用你自己的创造性。  ??误解之七：“模式是针对（面向对象）设计或实现的”  ??另一个极端则是过分的限制，就象这个误解。坦白说，任何人都完全可能相信它，我不会有丝毫惊讶。无数的人问过我这个问题，所以它也进入了“十大误解”之列。如果你觉得这种观点实在天真幼稚，跳过这一部分吧。  ??如果模式不能记述专家的经验，那它们就什么都不是。对于模式的作者来说，任何形式的专家经验都是可记载的。当然，在面向对象软件设计中有值得记载的经验，但是在非面向对象设计和分析、维护、测试、文档、组织结构……这些方面也同样有值得记载的经验。现在，不同领域中的模式开始浮出水面。至少已经有两本关于分析模式的书[Fowler97, Hay96]，并且每次PLoP大会都会收到新的模式类型。（特别有趣的是1996年的PLoP大会甚至关注了音乐作曲的模式！）  ??和大多数的误解一样，这个误解也是有原因的。看看人们用来描述模式的格式，有两种基本的风格：描述高层结构的GoF风格（用于《设计模式》中）；接近文学的Christopher Alexander 风格??叙述性的，尽量少的结构图。由于已经用模式描述了面向对象设计之外的东西，所以我现在认识到GoF风格造成的偏差。对于我研究过的某些领域的专家经验，这种风格根本无法描述。为什么结构图看上去总是让人联想到C++？对于音乐作曲的模式，“实现”应该是什么？“协作”部分真的有意义吗？  ??很明显，一种格式不能适应所有的需求。比较具有普遍意义的是模式的概念：模式是记载并传达专家经验的工具??不论是哪个领域的专家经验。  ??误解之八：“没有证据表明模式帮助过任何人”  ??这种观点曾经有一定的说服力，但是现在已经没有了。在Software-Practice and Experience[Kotula96]这样的杂志上、在OOPSLA[HJE95, Schmid95]和ICSE[BCC+96]这样的会议上，人们不断的报告自己从模式得到的利益。Doug Schmidt已经清楚的说明了在传授计算机科学时使用模式的收益[PD96]。尽管绝大多数的证据都只是定性的，但是据我所知，至少有一个组织得到了一些定量的结论[Prechelt97,PUS97]。  ??随着时间推进，我们需要更好的掌握使用模式的好处和缺点。尽管最初的反馈已经让我们看到了希望，但是我们还需要更多的实践经验来做全面的估计。但是，如果仅仅因为模式的好处还没有完全证实就拒绝使用模式，那你就真的是个大傻瓜了。  * * *  ??关于“模式能干什么”的谬论还真不少。下面，最后两种误解不是关于模式本身，而是关于支持使用模式的人们的。  ??误解之九：“模式社群是精英的小圈子”  ??我很想知道这种观念到底是从哪里来的。如果说模式社群的人们有什么引人注目的地方，那就是他们之间巨大的差异。从PLoP大会的与会者名单就能看出??人们来自世界各地，有大公司的也有小公司的，有分析员、设计者和实现者，有学生也有教授，有大名鼎鼎的作者也有初出茅庐的菜鸟。更让我惊讶的是，竟然有不少与会者都不是计算机科学工作者！这个社群仍然在不停变化，每年的与会者名单都与前一年不同。  ??如果将模式社群的背景公开出来，别人也许会觉得奇怪：很少有学院派人士。实际上，绝大多数PLoP的与会者都是实践者。软件模式早期的推崇者??包括Kent Beck、Peter Coad 和Ward Cunningham??都没有学院背景。GoF中只有一个人（Ralph）是学院派，而且他也是我所认识的最实际的学院派。很明显，模式社群从根本上反对任何可能的宗派主义和精英论。  ??误解之十：“模式社群是自私的，甚至是阴谋家”  ??我不止一次的听人指责说：模式最大的用处就是为写模式书籍的人带来了源源不断的收入。他们甚至还暗示模式的发展有着某种邪恶的目的。  ??胡说八道！  ??作为GoF的一员，我可以肯定的告诉你：对于《设计模式》引起的反响，我们和其他任何人一样吃惊。对于它在OOPSLA 94上的初次亮相引起的暴风骤雨，我们也同样没有准备??甚至出版商都没有预料到会有这么大的销量。在整个写作过程中，我们最关心的就是尽量写出一本高质量的书，至于销售上的问题，我们根本没有时间去想。现在“模式”已经成了一个时髦的词汇，不可避免的会有一些人将这个词用来谋取私利。但是，如果你认真读过顶尖的模式作者的作品，你就会感觉到他们共同的目标：将难以获取的经验、最佳的实践、甚至是竞争中的优势??多年实践经验的累积??分享给读者，而且讲解得一清二楚。正是这种帮助读者的热情激励着每一个真诚而踏实的模式作者。如果没有这种热情，作者就会弄巧成拙??这种不负责任的作者往往正是对模式所有误解的根源 ','',0,'j2ee',0,'','zinnia/_entry_detail.html','entry_detail.html'),(185,'import名字奇怪的python文件','3373193720121274648251',2,'2012-02-02 19:46:48',NULL,'2012-02-02 19:46:48','2014-07-13 18:57:17','abc.py 直接 import abc就行了<br />ab-c.py 就要用__import__(\'ab-c\')<br />ab-c 就要用 abc=imp.load_source(\'abc\',\'.\', \'ab-c\')<br />汗',1,1,1,0,0,0,'abc.py 直接 import abc就行了ab-c.py 就要用__import__(\'ab-c\')ab-c 就要用 abc=imp.load_source(\'abc\',\'.\', \'ab-c\')汗','',0,'',0,'','zinnia/_entry_detail.html','entry_detail.html'),(186,'Ubungu下编写Prolog','337319372010111311282896',2,'2010-12-13 23:28:02',NULL,'2010-12-13 23:28:02','2014-07-13 18:57:17','Prolog是一种逻辑编程语言。<br />安装编程环境<br />$ sudo apt-get install swi-prolog<br />进入交互环境<br />$ prolog<br />?- a is 3+4.<br />false.<br />?- A is 3+4.<br />A = 7.<br /><b>编写规则</b><br />$ cat &gt; love.pl&lt;&lt;EOF<br />#!/usr/bin/prolog -s<br />love(a,b).<br />love(b,c).<br />love(c,d).<br />love(d,a).<br />love(b,a).<br />lovers(X,Y):-love(X,Y),love(Y,X).<br />EOF<br /><b>编译运行</b><br />$ prolog -o love -c love.pl<br />% love.pl compiled 0.00 sec, 2,648 bytes<br />$ ./love<br />\n?- love(X,a).<br />\nX = d ;<br />\nX = b<br />\n?- love(a,X).<br />\nX = b<br />\n?- lovers(a,b).<br />\nYes<br />\n?- lovers(a,X).<br />\nX = b<br /><b>脚本运行</b><br />$ chmod +x love.pl<br />$ ./love.pl<br />?- love(X,a).<br />X = d;<br />X = b<br />...',1,1,1,0,0,0,'Prolog是一种逻辑编程语言。安装编程环境$ sudo apt-get install swi-prolog进入交互环境$ prolog?- a is 3+4.false.?- A is 3+4.A = 7.编写规则$ cat &gt; love.pl&lt;&lt;EOF#!/usr/bin/prolog -slove(a,b).love(b,c).love(c,d).love(d,a).love(b,a).lovers(X,Y):-love(X,Y),love(Y,X).EOF编译运行$ prolog -o love -c love.pl% love.pl compiled 0.00 sec, 2,648 bytes$ ./love\n?- love(X,a).\nX = d ;\nX = b\n?- love(a,X).\nX = b\n?- lovers(a,b).\nYes\n?- lovers(a,X).\nX = b脚本运行$ chmod +x love.pl$ ./love.pl?- love(X,a).X = d;X = b...','',0,'other',0,'','zinnia/_entry_detail.html','entry_detail.html'),(187,'从另一个线程将recvfrom从阻塞状态唤醒','33731937201155103633967',2,'2011-06-05 10:42:46',NULL,'2011-06-05 10:42:46','2014-07-13 18:57:17','UDP的多线程程序，一般开一个线程循环调用recvfrom接收消息，当程序中止的时候，如果这个线程阻塞在recvfrom调用，并且没有消息到达，则这个线程无法终止，造成资源泄露等问题。<br />这里终止这个线程有六种方法：<br />1. 释放监听的fd，这个是最简单的方法，但是有一点要注意，就是close(fd)系统调用无效，必须要使用<b>shutdown(fd, SHUT_RDWR)</b>来将recvfrom唤醒，因为close只关闭写通道，recvfrom在fd的写通道等待列表中，只要关闭写通道时才会将recvfrom阻塞唤醒。<br /><br />2. 在调用recvfrom前将recvfrom的fd设置一个超时，这个超时时间较难设置，但比较简单，也容易实现<br />3. 在调用recvfrom前将recvfrom的fd设置为非阻塞的，这个方法容易导致循环过快，CPU使用率上升<br />4. 使用select/epoll来接收，<br />5. fake一个消息包发给这个相应的端口<br />6. pthread_cancel强制杀掉线程',1,1,1,0,0,0,'UDP的多线程程序，一般开一个线程循环调用recvfrom接收消息，当程序中止的时候，如果这个线程阻塞在recvfrom调用，并且没有消息到达，则这个线程无法终止，造成资源泄露等问题。这里终止这个线程有六种方法：1. 释放监听的fd，这个是最简单的方法，但是有一点要注意，就是close(fd)系统调用无效，必须要使用shutdown(fd, SHUT_RDWR)来将recvfrom唤醒，因为close只关闭写通道，recvfrom在fd的写通道等待列表中，只要关闭写通道时才会将recvfrom阻塞唤醒。2. 在调用recvfrom前将recvfrom的fd设置一个超时，这个超时时间较难设置，但比较简单，也容易实现3. 在调用recvfrom前将recvfrom的fd设置为非阻塞的，这个方法容易导致循环过快，CPU使用率上升4. 使用select/epoll来接收，5. fake一个消息包发给这个相应的端口6. pthread_cancel强制杀掉线程','',0,'network',0,'','zinnia/_entry_detail.html','entry_detail.html'),(188,'ClassLoader Class.forName动态载入类','33731937200689754390',2,'2006-09-09 19:54:39',NULL,'2006-09-09 19:54:39','2014-07-13 18:57:17','天气:<img src=\"/static/163/4278982595955566812.gif\" />心情:<img src=\"/static/163/5131851775388843392.gif\" /><br />\n<p><span>1。Main</span></p>\n<p><span>class Office <br />{<br />&nbsp;public static void main(String[] args) throws Exception<br />&nbsp;{<br />&nbsp;&nbsp;String word = \"Word\";<br />&nbsp;&nbsp;String xls = \"Excel\";<br />&nbsp;&nbsp;Object o = Class.forName(word).newInstance();<br />&nbsp;&nbsp;Office_I of = (Office_I)o;<br />&nbsp;&nbsp;of.work();<br />&nbsp;}<br />}<br /></span></p>\n<p><span>2。Interface</span></p>\n<p><span>public interface Office_I<br />{<br />&nbsp;public void work();<br />}</span></p>\n<p><span>3。Implement</span></p>\n<p><span>public class Excel implements Office_I<br />{<br />&nbsp;public void work()<br />&nbsp;{<br />&nbsp;&nbsp;System.out.println(\"Exce works\");<br />&nbsp;}<br />}</span></p>',1,1,1,0,0,0,'天气:心情:\n1。Main\nclass Office {&nbsp;public static void main(String[] args) throws Exception&nbsp;{&nbsp;&nbsp;String word = \"Word\";&nbsp;&nbsp;String xls = \"Excel\";&nbsp;&nbsp;Object o = Class.forName(word).newInstance();&nbsp;&nbsp;Office_I of = (Office_I)o;&nbsp;&nbsp;of.work();&nbsp;}}\n2。Interface\npublic interface Office_I{&nbsp;public void work();}\n3。Implement\npublic class Excel implements Office_I{&nbsp;public void work()&nbsp;{&nbsp;&nbsp;System.out.println(\"Exce works\");&nbsp;}}','',0,'j2se',0,'','zinnia/_entry_detail.html','entry_detail.html'),(189,'.type xxx,@function','3373193720100871030788',2,'2010-01-08 19:10:30',NULL,'2010-01-08 19:10:30','2014-07-13 18:57:17','AT汇编中的函数定义，经常出现这么一句<br />.type areafunc,@function<br />删掉以后一切正常，只有一种情况下会出错，就是在用动态连接库的时候，如果没有第二行的这句话，就会出错<br />as&nbsp;&nbsp; -o areafunc.o areafunc.s<br />gcc -shared -o libhjz.so areafunc.o -Wl<br />gcc -o sotest&nbsp; -L. -lhjz floatest.c<br />/usr/bin/ld: warning: type and size of dynamic symbol `areafunc\' are not defined<br />/usr/bin/ld: dynamic variable `areafunc\' is zero size<br />/usr/bin/ld: /tmp/ccIBDF3a.o(.text+0x1f): unresolvable R_386_PC32 relocation against symbol `areafunc\'<br />/usr/bin/ld: final link failed: Nonrepresentable section on output<br />collect2: ld returned 1 exit status<br />make: *** [sotest] Error 1<br /><br />#areafunc.s<br />.section .text<br />\n#.type areafunc,@function<br />\n.global areafunc<br />\nareafunc:<br />\npushl %ebp<br />\nmovl %esp,%ebp<br />\n<br />\nfldpi<br />\nfilds 8(%ebp)<br />\nfmul %st(0),%st(0)<br />\nfmul %st(1),%st(0)<br />\n<br />\nmovl %ebp,%esp<br />\npopl %ebp<br />\nret<br />',1,1,1,0,0,0,'AT汇编中的函数定义，经常出现这么一句.type areafunc,@function删掉以后一切正常，只有一种情况下会出错，就是在用动态连接库的时候，如果没有第二行的这句话，就会出错as&nbsp;&nbsp; -o areafunc.o areafunc.sgcc -shared -o libhjz.so areafunc.o -Wlgcc -o sotest&nbsp; -L. -lhjz floatest.c/usr/bin/ld: warning: type and size of dynamic symbol `areafunc\' are not defined/usr/bin/ld: dynamic variable `areafunc\' is zero size/usr/bin/ld: /tmp/ccIBDF3a.o(.text+0x1f): unresolvable R_386_PC32 relocation against symbol `areafunc\'/usr/bin/ld: final link failed: Nonrepresentable section on outputcollect2: ld returned 1 exit statusmake: *** [sotest] Error 1#areafunc.s.section .text\n#.type areafunc,@function\n.global areafunc\nareafunc:\npushl %ebp\nmovl %esp,%ebp\n\nfldpi\nfilds 8(%ebp)\nfmul %st(0),%st(0)\nfmul %st(1),%st(0)\n\nmovl %ebp,%esp\npopl %ebp\nret','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(190,'CPU亲和力affinity','3373193720107191428377',2,'2010-08-19 13:06:07',NULL,'2010-08-19 13:06:07','2014-07-13 18:57:17','通过sched_setaffinity函数，可以设置CPU的亲和力，让指定的进程运行于指定的CPU上。<br />cc smp.c -o smp<br />./smp &gt; smp.log<br /><br />查看效果的方法：<br />运行top，然后按1，即可看到：（当然，你的CPU必须是多核的）<br />top - 13:03:30 up 3 days,&nbsp; 3:03,&nbsp; 3 users,&nbsp; load average: 0.88, 0.28, 0.16<br />Tasks: 123 total,&nbsp;&nbsp; 3 running, 120 sleeping,&nbsp;&nbsp; 0 stopped,&nbsp;&nbsp; 0 zombie<br /><b>Cpu0&nbsp;</b> :&nbsp; <b>0.0%us</b>,&nbsp; 3.5%sy,&nbsp; 0.0%ni, 24.9%id, 71.1%wa,&nbsp; 0.0%hi,&nbsp; 0.5%si,&nbsp; 0.0%st<br /><b>Cpu1</b>&nbsp; : <b>25.1%us</b>, 19.6%sy,&nbsp; 0.0%ni,&nbsp; 0.0%id, 62.8%wa,&nbsp; 0.0%hi,&nbsp; 0.0%si,&nbsp; 0.0%st<br />Mem:&nbsp;&nbsp; 1023980k total,&nbsp; 1008392k used,&nbsp;&nbsp;&nbsp; 15588k free,&nbsp;&nbsp;&nbsp; 56520k buffers<br />Swap:&nbsp;&nbsp; 979956k total,&nbsp;&nbsp;&nbsp;&nbsp; 5172k used,&nbsp;&nbsp; 974784k free,&nbsp;&nbsp; 808188k cached<br /><br /><br /><b>代码smp.c</b><br />#define _GNU_SOURCE<br />#include &lt;sched.h&gt;<br />#include &lt;stdio.h&gt;<br /><br />int main(){<br />&nbsp;&nbsp;&nbsp; cpu_set_t mask;<br />&nbsp;&nbsp;&nbsp; CPU_ZERO(&amp;mask);<br />&nbsp;&nbsp;&nbsp; CPU_SET(<b>1</b>,&amp;mask);//在CPU <b>1</b> 上运行<br />&nbsp;&nbsp;&nbsp; if(sched_setaffinity(0, sizeof(cpu_set_t), &amp;mask)==-1)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"Error happend\");<br />&nbsp;&nbsp;&nbsp; while(1){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"Running on CPU 1\\n\");<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; return 0;<br />}',1,1,1,0,0,0,'通过sched_setaffinity函数，可以设置CPU的亲和力，让指定的进程运行于指定的CPU上。cc smp.c -o smp./smp &gt; smp.log查看效果的方法：运行top，然后按1，即可看到：（当然，你的CPU必须是多核的）top - 13:03:30 up 3 days,&nbsp; 3:03,&nbsp; 3 users,&nbsp; load average: 0.88, 0.28, 0.16Tasks: 123 total,&nbsp;&nbsp; 3 running, 120 sleeping,&nbsp;&nbsp; 0 stopped,&nbsp;&nbsp; 0 zombieCpu0&nbsp; :&nbsp; 0.0%us,&nbsp; 3.5%sy,&nbsp; 0.0%ni, 24.9%id, 71.1%wa,&nbsp; 0.0%hi,&nbsp; 0.5%si,&nbsp; 0.0%stCpu1&nbsp; : 25.1%us, 19.6%sy,&nbsp; 0.0%ni,&nbsp; 0.0%id, 62.8%wa,&nbsp; 0.0%hi,&nbsp; 0.0%si,&nbsp; 0.0%stMem:&nbsp;&nbsp; 1023980k total,&nbsp; 1008392k used,&nbsp;&nbsp;&nbsp; 15588k free,&nbsp;&nbsp;&nbsp; 56520k buffersSwap:&nbsp;&nbsp; 979956k total,&nbsp;&nbsp;&nbsp;&nbsp; 5172k used,&nbsp;&nbsp; 974784k free,&nbsp;&nbsp; 808188k cached代码smp.c#define _GNU_SOURCE#include &lt;sched.h&gt;#include &lt;stdio.h&gt;int main(){&nbsp;&nbsp;&nbsp; cpu_set_t mask;&nbsp;&nbsp;&nbsp; CPU_ZERO(&amp;mask);&nbsp;&nbsp;&nbsp; CPU_SET(1,&amp;mask);//在CPU 1 上运行&nbsp;&nbsp;&nbsp; if(sched_setaffinity(0, sizeof(cpu_set_t), &amp;mask)==-1)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"Error happend\");&nbsp;&nbsp;&nbsp; while(1){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"Running on CPU 1\\n\");&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; return 0;}','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(191,'W3C DOM学习','33731937200832565948788',2,'2008-04-25 18:59:48',NULL,'2008-04-25 18:59:48','2014-07-13 18:57:17','TRAN,NAM1,VA1,N2,v2,PRB<br />1.DOM概念集及关系<br />2.使用Firebug学W3C Document<br /><br />1.DOM概念集及关系<br />Document, Node, NodeList, NamedNodeMap<br />1.1 Document<br />documentElement, createElement(), createTextNode(), createAttribute(), getElementsByTabName()<br />1.2 Node<br />attributes&lt;NamedNodeMap&gt;, parentNode&lt;Node&gt;, childNodes&lt;NodeList&gt;, firstChild, lastChild, previousSibling, nextSibling, nodeName, nodeType, hasChildNodes(), appendChild(), cloneNode() insertBefore(), removeChild(), replaceChild()<br />1.3 NodeList<br />length, item(index), []<br />1.4 NamedNodeMap<br />getNamedItem, removeNamedItem, setNamedItem<br />1.5 MSXML<br />load(), loadXML(), readystate, onreadystatechange()<br />documentMethods: xml nextNode() selectNodes(), selectSingleNode(), transformNode(), transformNodeToObject()<br />var doc = new ActiveXObject(\"Microsfot.XMLDOM\");<br />1.6 Mozilla<br />var doc = document.implementation.createDocument(\"\",\"\",null);<br /><br /><br />2.使用Firebug学W3C Document<br />2.1. 按F12打开Firebug，然后选左面的Console标签，然后就可以在右面的窗口输入命令并执行了。<br />2.2. 输入document.documentElement.appendChild(document.createElement(\"Hello\"));按Ctrl+Enter执行。<br />2.3. 输入alert(document.documentElement.lastChild.nodeName);按Ctrl+Enter执行，弹出对话框Hello，成功<br /><br />可运行的脚本<br />//添加一个子节点<br />document.documentElement.appendChild(document.createElement(\"hello\"));<br />//移除一个子节点<br />document.documentElement.removeChild(document.documentElement.lastChild);<br />//替换一个子节点<br />document.documentElement.replaceChild(<br />&nbsp;&nbsp;&nbsp; document.createElement(\"hank\"),<br />&nbsp;&nbsp;&nbsp; document.documentElement.lastChild);<br />//显示一个子节点的节点名<br />alert(document.documentElement.lastChild.nodeName);<br />//length<br />alert(document.documentElement.childNodes.length);<br />//item<br />alert(document.documentElement.childNodes.item(0).nodeName);<br />alert(document.documentElement.childNodes[1].nodeName);<br />//set attribute<br />var att = document.createAttribute(\"hello\");<br />att.value=\"world\";<br />document.documentElement.lastChild.attributes.setNamedItem(att);<br />alert(document.documentElement.lastChild.attributes.length);<br />alert(document.documentElement.lastChild.attributes[0].nodeValue);<br />//get attribute.<br />alert(document.documentElement.lastChild.attributes.getNamedItem(\"hello\").nodeValue);<br /><br />//MSXML<br />document.load(\"dvd.xml\");<br />document.loadXML(\'&lt;?xml version=\"1.0\"?&gt;&lt;library/&gt;\');<br />//Mozia xml<br />var oDOM = new DOMParser().parseFromString(\'&lt;?xml version=\"1.0\"?&gt;&lt;lib/&gt;\',\"text/xml\");<br />document.write(oDOM.childNodes[0].nodeName);',1,1,1,0,0,0,'TRAN,NAM1,VA1,N2,v2,PRB1.DOM概念集及关系2.使用Firebug学W3C Document1.DOM概念集及关系Document, Node, NodeList, NamedNodeMap1.1 DocumentdocumentElement, createElement(), createTextNode(), createAttribute(), getElementsByTabName()1.2 Nodeattributes&lt;NamedNodeMap&gt;, parentNode&lt;Node&gt;, childNodes&lt;NodeList&gt;, firstChild, lastChild, previousSibling, nextSibling, nodeName, nodeType, hasChildNodes(), appendChild(), cloneNode() insertBefore(), removeChild(), replaceChild()1.3 NodeListlength, item(index), []1.4 NamedNodeMapgetNamedItem, removeNamedItem, setNamedItem1.5 MSXMLload(), loadXML(), readystate, onreadystatechange()documentMethods: xml nextNode() selectNodes(), selectSingleNode(), transformNode(), transformNodeToObject()var doc = new ActiveXObject(\"Microsfot.XMLDOM\");1.6 Mozillavar doc = document.implementation.createDocument(\"\",\"\",null);2.使用Firebug学W3C Document2.1. 按F12打开Firebug，然后选左面的Console标签，然后就可以在右面的窗口输入命令并执行了。2.2. 输入document.documentElement.appendChild(document.createElement(\"Hello\"));按Ctrl+Enter执行。2.3. 输入alert(document.documentElement.lastChild.nodeName);按Ctrl+Enter执行，弹出对话框Hello，成功可运行的脚本//添加一个子节点document.documentElement.appendChild(document.createElement(\"hello\"));//移除一个子节点document.documentElement.removeChild(document.documentElement.lastChild);//替换一个子节点document.documentElement.replaceChild(&nbsp;&nbsp;&nbsp; document.createElement(\"hank\"),&nbsp;&nbsp;&nbsp; document.documentElement.lastChild);//显示一个子节点的节点名alert(document.documentElement.lastChild.nodeName);//lengthalert(document.documentElement.childNodes.length);//itemalert(document.documentElement.childNodes.item(0).nodeName);alert(document.documentElement.childNodes[1].nodeName);//set attributevar att = document.createAttribute(\"hello\");att.value=\"world\";document.documentElement.lastChild.attributes.setNamedItem(att);alert(document.documentElement.lastChild.attributes.length);alert(document.documentElement.lastChild.attributes[0].nodeValue);//get attribute.alert(document.documentElement.lastChild.attributes.getNamedItem(\"hello\").nodeValue);//MSXMLdocument.load(\"dvd.xml\");document.loadXML(\'&lt;?xml version=\"1.0\"?&gt;&lt;library/&gt;\');//Mozia xmlvar oDOM = new DOMParser().parseFromString(\'&lt;?xml version=\"1.0\"?&gt;&lt;lib/&gt;\',\"text/xml\");document.write(oDOM.childNodes[0].nodeName);','',0,'\"web skills\"',0,'','zinnia/_entry_detail.html','entry_detail.html'),(192,'screen','33731937200981310227771',2,'2009-09-13 22:02:27',NULL,'2009-09-13 22:02:27','2014-07-13 18:57:17','创建Screen<br />screen<br />screen &lt;cmd&gt;<br />断开Screen<br />Ctrl-a d<br />恢复Screen<br />screen -r<br />screen -x<br />退出Screen<br />exit<br />',1,1,1,0,0,0,'创建Screenscreenscreen &lt;cmd&gt;断开ScreenCtrl-a d恢复Screenscreen -rscreen -x退出Screenexit','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(193,'[Fw]1个用户变成100万的9种办法','3373193720099179841627',2,'2009-10-17 09:08:41',NULL,'2009-10-17 09:08:41','2014-07-13 18:57:17','是digg创始人(也是WeFollow and Revision3，pownce的创始人)Kevin Rose在The Future\nof Web Apps London (FOWA)的演说，Kevin\nRose和大家分享了一个网站怎么从0个用户迅速发展到100万用户的9个方法。我大致把一些要点翻译出来，和大家分享下。大道至简，做起来难。<p>　<strong>　#1. 自我Ego</strong></p><p>　　1. 问自己：我的这些功能特征提高了用户的自我价值感或者是自尊了吗?</p><p>　　2. 如果一个用户对网站做了贡献(发表帖子，简单的顶一下或者打分等)，他们有没有那种被奖赏的心情呢?用户能得到什么可见的奖赏吗?</p><p>　　3. twitter的followers的数量，给用户的奖赏是数字越大心理满足越大;还有digg的digg数量等，都给用户一个很直观的奖赏信息;</p><p><strong>　　#2. 简洁Simplicity</strong></p><p>　　1. 停止去创建更多的功能;</p><p>　　2. 专注在一两个特色功能;</p><p>　　3 问问自己：还有哪些功能特征还能够去掉?</p><p><strong>　　#3. 开发和发布Build &amp; Release</strong></p><p>　　1. 不要去想你了解你的用户;</p><p>　　2. 从用户在你网站上实际做的事情中去了解，而不是你认为哪些事情用户会做;</p><p>　　3. 决定一些你要做的事情，然后去做。</p><p>　　4. 开发，发布，迭代和反复</p><p><strong>　　#4. 向媒体发出声音Hack The Press</strong></p><p>　　1. 只通过系统邀请(pownce digg v3)</p><p>　　2.和一些资历较浅的blogger交流</p><p>　　3.参加一些交流活动，利用社交关系宣传，出席活动的时候带上网站的demo;</p><p><strong>　　#5.和自己的社区交流 Connect with your community</strong></p><p>　　1. 启动一个播客;</p><p>　　2.举行一个发布会，然后年度/季度活动-邀请新闻/有影响力的个人-不要告诉栏</p><p>　　3. 参与社区，活跃在自己的生态体系中;</p><p><strong>　　#6. 咨询顾问Advisors</strong></p><p>　　1. 遇到什么技术难题?</p><p>　　2.顾问可以在各领域发挥作用(市场营销/招聘/业务开发)</p><p>　　3. 股票报酬，通常不是一个董事会席位，坚实的顾问有助于筹款;</p><p>　　<strong>#7. 利用你的用户群，口碑相传Leverage your userbase to spread the word</strong></p><p>　　<strong>#8. 你的产品对于第三方网站是否有价值?Does your product provide value for 3rd party sites?</strong></p><p>　　<strong>#9. 分析你的流量Analyze your traffic</strong></p><p>　　1. 安装google ****ytics</p><p>　　2.分析流量来源(搜索?)</p><p>　　3.用户在网站上的访问轨迹</p><p>　　4.分析那些退出率高的页面</p>',1,1,1,0,0,0,'是digg创始人(也是WeFollow and Revision3，pownce的创始人)Kevin Rose在The Future\nof Web Apps London (FOWA)的演说，Kevin\nRose和大家分享了一个网站怎么从0个用户迅速发展到100万用户的9个方法。我大致把一些要点翻译出来，和大家分享下。大道至简，做起来难。　　#1. 自我Ego　　1. 问自己：我的这些功能特征提高了用户的自我价值感或者是自尊了吗?　　2. 如果一个用户对网站做了贡献(发表帖子，简单的顶一下或者打分等)，他们有没有那种被奖赏的心情呢?用户能得到什么可见的奖赏吗?　　3. twitter的followers的数量，给用户的奖赏是数字越大心理满足越大;还有digg的digg数量等，都给用户一个很直观的奖赏信息;　　#2. 简洁Simplicity　　1. 停止去创建更多的功能;　　2. 专注在一两个特色功能;　　3 问问自己：还有哪些功能特征还能够去掉?　　#3. 开发和发布Build &amp; Release　　1. 不要去想你了解你的用户;　　2. 从用户在你网站上实际做的事情中去了解，而不是你认为哪些事情用户会做;　　3. 决定一些你要做的事情，然后去做。　　4. 开发，发布，迭代和反复　　#4. 向媒体发出声音Hack The Press　　1. 只通过系统邀请(pownce digg v3)　　2.和一些资历较浅的blogger交流　　3.参加一些交流活动，利用社交关系宣传，出席活动的时候带上网站的demo;　　#5.和自己的社区交流 Connect with your community　　1. 启动一个播客;　　2.举行一个发布会，然后年度/季度活动-邀请新闻/有影响力的个人-不要告诉栏　　3. 参与社区，活跃在自己的生态体系中;　　#6. 咨询顾问Advisors　　1. 遇到什么技术难题?　　2.顾问可以在各领域发挥作用(市场营销/招聘/业务开发)　　3. 股票报酬，通常不是一个董事会席位，坚实的顾问有助于筹款;　　#7. 利用你的用户群，口碑相传Leverage your userbase to spread the word　　#8. 你的产品对于第三方网站是否有价值?Does your product provide value for 3rd party sites?　　#9. 分析你的流量Analyze your traffic　　1. 安装google ****ytics　　2.分析流量来源(搜索?)　　3.用户在网站上的访问轨迹　　4.分析那些退出率高的页面','',0,'\"web skills\"',0,'','zinnia/_entry_detail.html','entry_detail.html'),(194,'网络程序外挂的无奈','33731937200972192750690',2,'2009-08-21 09:27:50',NULL,'2009-08-21 09:27:50','2014-07-13 18:57:17','对于服务器的每次升级，都需要耗费时间调试修改代码，维护的代价太昂贵了<br />是否有可能结合Firebug与GreaseMonkey，做出一个网页操作宏的程序，根据用户的操作生成相应的代码，然后只要调整某些的代码，即可生成外挂程序。类似界面开发IDE，如Eclipse的VE<br /><br />2009年8月21日校内网的开心农场升级后的代码<br />#!/usr/bin/env python<br />#encoding: utf-8<br />#2009-8-21 升级后<br />import urllib,urllib2,cookielib<br />import time,zlib,re,md5<br />import cStringIO,gzip<br />import json<br />import pickle<br />import thread,sys,os<br />class Farm:<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;debug=False<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;status={} #self\'s farm status<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;friends={} #list of friends<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;shop={} #information of seeds<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;bag={} #背包<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;uid=0 #user id<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;autoweed=True #自动除草<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;autonorm=True #自动杀虫<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;autowater=True #自动浇水<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;autofert=True #自动施肥<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;autoplant=True #自动种植<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;autosteal=True #自动收获<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;#创建Cookie<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def __init__(self):<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;cj=cookielib.LWPCookieJar()<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;opener=urllib2.build_opener(urllib2.HTTPCookieProcessor(cj))<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;opener.addheaders=[(\'User-agent\',\'Opera/9.23\')]<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;urllib2.install_opener(opener)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 请求网页的工具函数，直接返回网页内容<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def req(self,url,body=()):<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if len(body)==0:<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;req=urllib2.Request(url)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;else:<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;req=urllib2.Request(url,urllib.urlencode(body))<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;while True:<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;try:<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;raw=urllib2.urlopen(req)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;break<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;except:<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print \'ReqError url:%s\' % url<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;pass<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if self.debug: #输出调试信息<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print url<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print body<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return raw.read()<br /><br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def param(self): # build params farmKey and farmTime<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farmTime=str(time.time())[0:10]<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farmKey=md5.md5(farmTime+\'15l3h4kh\').hexdigest()<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return (farmKey, farmTime)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 登录校内网 获取校内网到开心网的链接地址,并取得Cookie<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def login(self, email, password):<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\"http://login.renren.com/Login.do\"<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;body=((\"email\",email),(\"password\",password))<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;ret=self.req(url,body)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;uids=re.findall(\'http\\:\\/\\/renren\\.com\\/profile\\.do\\?id\\=(\\d+)\',ret)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if len(uids)&gt;0:<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.uid=uids[0]<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;else:<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print ret<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# build cookie to faminutes<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\'http://apps.renren.com/happyfarm?origin=104\'<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=re.findall(\'iframe_canvas\" src=\"([^\"]+)\"\',self.req(url))[0]<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=url.replace(\'?\',\'/?\').replace(\'&amp;amp;\',\'&amp;\') # fuck url, it lacks a \'/\'<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;raw=self.req(url)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# read current status<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&n