; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;open(\'test.bmp\',\'w\').write(self.req(url))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;verify=sys.stdin.readline()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\'http://friend.renren.com/DelFriend.do\'&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;body={\'code\':verify[0:len(verify)-1],\'id\':fid}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return json.read(self.req(url,body))#在时间T自动去偷def threadSteal(farm, T, fid, place):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;time.sleep(T-int(time.time())+2)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if fid==-1:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;temp=farm.harvest(place)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;else:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;temp=farm.steal(fid,place)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if temp.has_key(\'harvest\'):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print u\'Thread 偷得 %d at %s:%d\'.encode(\'utf-8\') % (temp[\'harvest\'],fid,place)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;else:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print u\'Thread Failed %s at %s:%d\'.encode(\'utf-8\') % (temp,fid,place)if __name__==\'__main__\':&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farm = Farm()#&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farm.debug=True&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print \'Login...\'&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;while True:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;try:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if True:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print u\'请输入校内网用户邮箱:\'.encode(\'utf-8\')&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;email=sys.stdin.readline()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print u\'请输入密码:\'.encode(\'utf-8\')&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;password=sys.stdin.readline()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farm.login(email[0:len(email)-1],password[0:len(password)-1])&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;else:#自动登录&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farm.login(\'校内网用户邮箱\',\'密码\')&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;#&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farm.addFriends();&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;interval=20*60&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;while True:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print \'Auto run...\'&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;farm.autorun(interval=interval) #自动运行&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print u\'%s等待下次运行:%d分钟后\'.encode(\'utf-8\') % (time.ctime(),interval/60)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;sys.stdout.flush()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;time.sleep(interval)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;except:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;continue','',0,'network',0,'','zinnia/_entry_detail.html','entry_detail.html'),(195,'Ubuntu8.10安装nginx和php','33731937201061764117939',2,'2010-07-17 18:41:17',NULL,'2010-07-17 18:41:17','2014-07-13 18:57:17','1. 安装软件 <br />sudo apt-get install nginx<br />sudo apt-get install lighttpd<br />sudo update-rc.d -f lighttpd remove<br /><br />2. 配置nginx的php转发<br />sudo vi /usr/local/nginx/conf/nginx.conf，在location ~ \\.php$附近加入<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; location ~ \\.php$ {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fastcgi_pass 127.0.0.1:9000;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fastcgi_index index.php;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fastcgi_param SCRIPT_FILENAME /var/www/$fastcgi_script_name;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; include fastcgi_params;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />#编写php测试文件: test.php<br />sudo cat&gt;&gt;/var/www/test.php&lt;&lt;EOF<br />&lt;?php<br />echo \"Hello, Worlp\";<br />?&gt;<br />EOF<br /><br />3. 启动测试<br />sudo /usr/bin/spawn-fcgi -a 127.0.0.1 -p 9000 -C 5 -u www-data -g www-data -f /usr/bin/php-cgi<br />sudo /usr/local/nginx/sbin/nginx<br />curl http://localhost/test.php',1,1,1,0,0,0,'1. 安装软件 sudo apt-get install nginxsudo apt-get install lighttpdsudo update-rc.d -f lighttpd remove2. 配置nginx的php转发sudo vi /usr/local/nginx/conf/nginx.conf，在location ~ \\.php$附近加入&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; location ~ \\.php$ {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fastcgi_pass 127.0.0.1:9000;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fastcgi_index index.php;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fastcgi_param SCRIPT_FILENAME /var/www/$fastcgi_script_name;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; include fastcgi_params;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }#编写php测试文件: test.phpsudo cat&gt;&gt;/var/www/test.php&lt;&lt;EOF&lt;?phpecho \"Hello, Worlp\";?&gt;EOF3. 启动测试sudo /usr/bin/spawn-fcgi -a 127.0.0.1 -p 9000 -C 5 -u www-data -g www-data -f /usr/bin/php-cgisudo /usr/local/nginx/sbin/nginxcurl http://localhost/test.php','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(196,'Tomcat支持多用户','337319372008491270454',2,'2008-05-09 13:27:00',NULL,'2008-05-09 13:27:00','2014-07-13 18:57:17','许多web服务器会自动将以\"~\"和用户名开始的URI映射到用户主目录下的某个目录（通常为public_html）。在Catalina中，通过使用特殊的Listener元素，可以完成上述功能<br />方法：修改&lt;tomcat&gt;/conf/server.xml,在&lt;Host&gt;节点之间增加<br />Unix下常用/etc/passwd文件记录用户信息，可以这样配置（未测试）<br />&lt;Listener className=\"org.apache.catalina.startup.UserConfig\"<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; directoryName=\"public_html\"<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; userClass=\"org.apache.catalina.startup.PasswdUserDatabase\"/&gt;<br />在Windows平台下，加入一下一段Listener配置（测试成功)。<br />&lt;Host ...&gt;<br />...<br />&lt;Listener className=\"org.apache.catalina.startup.UserConfig\"<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; directoryName=\"public_html\"<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; homeBase=<span style=\"color: rgb(255, 0, 0);\">\"E:\\websites\"</span><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; userClass=\"org.apache.catalina.startup.HomesUserDatabase\"/&gt;<br />&lt;/Host&gt;<br />文件目录结构 <br />E:.<br />└─websites<br />&nbsp;&nbsp;&nbsp; ├─hankjin<br />&nbsp;&nbsp;&nbsp; │&nbsp; └─public_html<br />&nbsp;&nbsp;&nbsp; │&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; index.html<br />&nbsp;&nbsp;&nbsp; │<br />&nbsp;&nbsp;&nbsp; └─john<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; └─public_html<br />然后访问http://localhost:8080/~hankjin/index.html即可<br />',1,1,1,0,0,0,'许多web服务器会自动将以\"~\"和用户名开始的URI映射到用户主目录下的某个目录（通常为public_html）。在Catalina中，通过使用特殊的Listener元素，可以完成上述功能方法：修改&lt;tomcat&gt;/conf/server.xml,在&lt;Host&gt;节点之间增加Unix下常用/etc/passwd文件记录用户信息，可以这样配置（未测试）&lt;Listener className=\"org.apache.catalina.startup.UserConfig\"&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; directoryName=\"public_html\"&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; userClass=\"org.apache.catalina.startup.PasswdUserDatabase\"/&gt;在Windows平台下，加入一下一段Listener配置（测试成功)。&lt;Host ...&gt;...&lt;Listener className=\"org.apache.catalina.startup.UserConfig\"&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; directoryName=\"public_html\"&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; homeBase=\"E:\\websites\"&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; userClass=\"org.apache.catalina.startup.HomesUserDatabase\"/&gt;&lt;/Host&gt;文件目录结构 E:.└─websites&nbsp;&nbsp;&nbsp; ├─hankjin&nbsp;&nbsp;&nbsp; │&nbsp; └─public_html&nbsp;&nbsp;&nbsp; │&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; index.html&nbsp;&nbsp;&nbsp; │&nbsp;&nbsp;&nbsp; └─john&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; └─public_html然后访问http://localhost:8080/~hankjin/index.html即可','',0,'j2ee',0,'','zinnia/_entry_detail.html','entry_detail.html'),(197,'ubuntu8.10上启动mysql cluster','3373193720105217332949',2,'2010-06-21 19:03:32',NULL,'2010-06-21 19:03:32','2014-07-13 18:57:18','环境两台Ubuntu8.10服务器，分别为：<br />mysql1: 192.201.0.61<br />mysql2: 192.201.0.62<br />其中mysql1作为管理节点+数据节点1+mysql节点1，mysql2作为数据节点1+mysql节点2<br />首先分别在各个节点上安装软件<br />sudo apt-get install mysql-server<br />然后配置管理节点mysql1<br />sudo vi /etc/mysql/ndb_mgmd.cnf<br />[NDBD DEFAULT]<br />NoOfReplicas=2 #这里有mysql1和mysql2两个数据节点<br />DataMemory=250M<br />IndexMemory=100M<br />[TCP DEFAULT]<br />portnumber=2202<br />#管理节点<br />[NDB_MGMD]<br />id=1<br />hostname=192.201.0.61<br />datadir=/home/hankjin/mysql-cluster<br />#数据节点1<br />[NDBD]<br />id=2<br />hostname=192.201.0.61<br />datadir=/home/hankjin/mysql/data<br />#数据节点2<br />[NDBD]<br />id=3<br />hostname=192.201.0.62<br />datadir=/home/hankjin/mysql/data<br />#mysql节点1<br />[MYSQLD]<br />hostname=192.201.0.61<br />#mysql节点2<br />[MYSQLD]<br />hostname=192.201.0.62<br />然后修改各个节点的配置文件<br />sudo vi /etc/mysql/my.cnf<br />找到[mysqld]，然后在这一行的下面添加两行<br />ndbcluster<br />ndb-connectstring=192.201.0.61 #管理节点<br />然后找到CLUSTER，并取消注释<br />[MYSQL_CLUSTER]<br />ndb-connectstring=192.201.0.61 #管理节点<br /><br />然后开始启动CLUSTER，<br />首先启动管理节点<br />sudo /etc/init.d/mysql-ndb-mgm start<br />然后分别启动两个数据节点,第一次启动的时候用start-initial，以后就可以直接用start<br />sudo /etc/init.d/mysql-ndb start-initial<br />最后启动mysql节点<br />sudo /etc/init.d/mysql start<br /><br />测试管理节点<br />ndb_mgm<br />&gt;&gt;show<br />测试cluster<br />在mysql1的mysql节点上<br />create database test;<br />use test;<br />create table t(name varchar(20)) engine=ndb;<br />insert into t values(\'hank\');<br />然后在mysql2的mysql节点上<br />create database test;<br />use test;<br />show tables;<br />#可以直接看到t，但是数据库test必须手工创建<br />select * from t;<br />#在节点1上插入的数据在这里也可以看到，这里插入的数据在节点1上也可以看到。<br />insert into t values(\'hjz\');<br />从管理节点关闭cluster<br />ndb_mgm<br />&gt;&gt;shutdown',1,1,1,0,0,0,'环境两台Ubuntu8.10服务器，分别为：mysql1: 192.201.0.61mysql2: 192.201.0.62其中mysql1作为管理节点+数据节点1+mysql节点1，mysql2作为数据节点1+mysql节点2首先分别在各个节点上安装软件sudo apt-get install mysql-server然后配置管理节点mysql1sudo vi /etc/mysql/ndb_mgmd.cnf[NDBD DEFAULT]NoOfReplicas=2 #这里有mysql1和mysql2两个数据节点DataMemory=250MIndexMemory=100M[TCP DEFAULT]portnumber=2202#管理节点[NDB_MGMD]id=1hostname=192.201.0.61datadir=/home/hankjin/mysql-cluster#数据节点1[NDBD]id=2hostname=192.201.0.61datadir=/home/hankjin/mysql/data#数据节点2[NDBD]id=3hostname=192.201.0.62datadir=/home/hankjin/mysql/data#mysql节点1[MYSQLD]hostname=192.201.0.61#mysql节点2[MYSQLD]hostname=192.201.0.62然后修改各个节点的配置文件sudo vi /etc/mysql/my.cnf找到[mysqld]，然后在这一行的下面添加两行ndbclusterndb-connectstring=192.201.0.61 #管理节点然后找到CLUSTER，并取消注释[MYSQL_CLUSTER]ndb-connectstring=192.201.0.61 #管理节点然后开始启动CLUSTER，首先启动管理节点sudo /etc/init.d/mysql-ndb-mgm start然后分别启动两个数据节点,第一次启动的时候用start-initial，以后就可以直接用startsudo /etc/init.d/mysql-ndb start-initial最后启动mysql节点sudo /etc/init.d/mysql start测试管理节点ndb_mgm&gt;&gt;show测试cluster在mysql1的mysql节点上create database test;use test;create table t(name varchar(20)) engine=ndb;insert into t values(\'hank\');然后在mysql2的mysql节点上create database test;use test;show tables;#可以直接看到t，但是数据库test必须手工创建select * from t;#在节点1上插入的数据在这里也可以看到，这里插入的数据在节点1上也可以看到。insert into t values(\'hjz\');从管理节点关闭clusterndb_mgm&gt;&gt;shutdown','',0,'database',0,'','zinnia/_entry_detail.html','entry_detail.html'),(198,'JUDDI安装于配置','33731937200832111427127',2,'2008-04-21 23:42:07',NULL,'2008-04-21 23:42:07','2014-07-13 18:57:18','<p>1。下载jUDDI-2.0r5.war，放入webapps/目录下</p>  <p>2。启动Tomcat，生成juddi目录，进入目录，查看juddi.properteis文件，里面用到了数据源</p>  <p>3。打开WEB-INF/lib目录下的juddi-xxx.jar文件，找到里面的mysql/creaetdatabases.sql和publishser.sql，在mysql中创建数据库juddi，用户名和密码都是juddi,然后修改执行这两个sql</p>  <p>4。配置数据源，在conf/context.xml的&lt;Context&gt;中间加上</p>  <p>&nbsp;&lt;Resource name=\"jdbc/juddiDB\" auth=\"Container\" type=\"javax.sql.DataSource\"<br />&nbsp;&nbsp;maxActive=\"100\" maxIdle=\"30\" maxWait=\"10000\"<br />&nbsp;&nbsp;username=\"juddi\" password=\"juddi\" <br />&nbsp;&nbsp;driverClassName=\"com.mysql.jdbc.Driver\"<br />&nbsp;&nbsp;url=\"jdbc:mysql://127.0.0.1:3306/juddi\"/&gt;<br />在/WEB-INF/web.xml中加上（默认就有）</p>  <p>&lt;resource-ref&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;description&gt;DB Connection&lt;/description&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;res-ref-name&gt;jdbc/juddiDB&lt;/res-ref-name&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;res-type&gt;javax.sql.DataSource&lt;/res-type&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;res-auth&gt;Container&lt;/res-auth&gt;<br /> &lt;/resource-ref&gt;</p>  <p>5。打开<a href=\"http://localhost:8080/juddi/\" rel=\"nofollow\">http://localhost:8080/juddi/</a>&nbsp;&nbsp; validate</p>  <p>6。测试</p>  <p>下载uddi4j.jar和soap.jar，配置props文件，运行</p>',1,1,1,0,0,0,'1。下载jUDDI-2.0r5.war，放入webapps/目录下  2。启动Tomcat，生成juddi目录，进入目录，查看juddi.properteis文件，里面用到了数据源  3。打开WEB-INF/lib目录下的juddi-xxx.jar文件，找到里面的mysql/creaetdatabases.sql和publishser.sql，在mysql中创建数据库juddi，用户名和密码都是juddi,然后修改执行这两个sql  4。配置数据源，在conf/context.xml的&lt;Context&gt;中间加上  &nbsp;&lt;Resource name=\"jdbc/juddiDB\" auth=\"Container\" type=\"javax.sql.DataSource\"&nbsp;&nbsp;maxActive=\"100\" maxIdle=\"30\" maxWait=\"10000\"&nbsp;&nbsp;username=\"juddi\" password=\"juddi\" &nbsp;&nbsp;driverClassName=\"com.mysql.jdbc.Driver\"&nbsp;&nbsp;url=\"jdbc:mysql://127.0.0.1:3306/juddi\"/&gt;在/WEB-INF/web.xml中加上（默认就有）  &lt;resource-ref&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;description&gt;DB Connection&lt;/description&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;res-ref-name&gt;jdbc/juddiDB&lt;/res-ref-name&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;res-type&gt;javax.sql.DataSource&lt;/res-type&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;res-auth&gt;Container&lt;/res-auth&gt; &lt;/resource-ref&gt;  5。打开http://localhost:8080/juddi/&nbsp;&nbsp; validate  6。测试  下载uddi4j.jar和soap.jar，配置props文件，运行','',0,'j2ee',0,'','zinnia/_entry_detail.html','entry_detail.html'),(199,'网上匿名投票的矛与盾','337319372009103095317820',2,'2009-11-30 09:53:17',NULL,'2009-11-30 09:53:17','2014-07-13 18:57:18','网上匿名投票<br /><br />0.法律于道德<br />目前并没有找到相关的法律条文，因此些行为不算违法；从道德上讲，如果别人在刷，自己刷吗？如果一定要说什么众人皆醉我独醒，哪又何必去参选呢？<br /><br />1.匿名投票的实现方式<br />无限制：虽然有一小部分投票完全没有限制，但目前的网投多数都有不同的限制方式：<br />Cookie限制：投完票后在客户机本地保存一份Cookie，然后投票按钮不再显示。<br />IP限制：每个IP只允许投一票，将投过票的IP保存到数据库中<br /><br />2.矛：简单的刷票方法<br />无限制投票：最简单的方法就是不停的按F5刷新浏览器；或者用bash同时启动N多wget进程<br />Cookie限制：每次投完票后，IE-&gt;工具-&gt;Internet选项-&gt;清除Cookie，然后就可以再次投票了；或者写一个脚本，先建立连接，获得Cookie，再投票，同时启动N个该脚本。<br />IP限制：和突破Cookie的方法基本相同，只是建立连接的时候要使用代理，这样就等于伪装成代理的IP了。<br /><br />3.盾：刷票的屏蔽方法<br />限制IP段：只允许指定IP段的用户投票，且每IP只能投一票。这个方法很厉害，可是有些麻烦，而且有时候不太适用。很多为了突破GFW而用代理的人怎么办？<br />限制代理：多数代理服务器，会在HTTP请求的头部加上原始请求的IP X-Forwarded-For，如果将这个和IP头部的IP一起检测，就可以防止Client-&gt;Proxy-&gt;Server的刷票方式。但是如果 Client-&gt;Proxy-&gt;Proxy-&gt;Server的刷票方式是可行的话，还是不能根除。<br />验证码技术：每次投票都要手工输入验证码，可以有效防止机器刷票<br />投票间隔检测：对同一被投者的短时间大量不明票源，可假设为刷票，进行进一步检测。<br />终极方案：不要匿名投票，注册投票，注册时用身份证进行区分。<br /><br />几种刷票方式的python伪代码：<br /><br />1)无限制投票<br /><font face=\"monospace\">#!/usr/bin/python<br />import urllib,time<br />url=\"http://www.xxxx.com/post.asp?type=int&amp;name=click&amp;time=\"<br />while True:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;now=time.time()*1000<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;site=urllib.urlopen(\'%s%d\' %(url,int(now)))<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;print site.read()</font><br />2)Cookie和代理投票<br />去掉proxy相关的内容就是Cookie投票，不去掉也可以<br /><div>python xvote.py &lt; proxy.ok</div>  <div>&nbsp;</div>  <div>代理列表proxy.ok文件</div>  <div><font face=\"monospace\">http://119.62.128.38:80</font></div>  <div><font face=\"monospace\">http://148.233.239.24:3128</font></div>  <div><font face=\"monospace\">http://148.233.239.23:80</font></div>  <div><font face=\"monospace\">http://148.233.239.23:3128</font></div>  <div><br /> xvote.py文件</div>  <div>#!/usr/bin/python</div>  <div>#xvote.py</div>  <div>#encoding: utf-8</div>  <div>import urllib2,urllib,cookielib,socket</div>  <div>import re</div>  <div>import thread,time,os,sys</div>  <div>def vote(index,proxy):</div>  <div>&nbsp;&nbsp;&nbsp;url=\'<a href=\"http://sunshine.hust.edu.cn/zt/footballbaby2009/Default.aspx%27\" rel=\"nofollow\">http://xxxx.edu.cn/xxxx/Default.aspx\'</a></div>  <div>&nbsp;&nbsp;&nbsp;cj=cookielib.CookieJar()</div>  <div>&nbsp;&nbsp;&nbsp;opener=urllib2.build_opener(urllib2.HTTPCookieProcessor(cj),</div>  <div>&nbsp;&nbsp;&nbsp;urllib2.ProxyHandler(proxy))</div>  <div>&nbsp;&nbsp;&nbsp;urllib2.install_opener(opener)</div>  <div>&nbsp;</div>  <div>&nbsp;&nbsp;&nbsp;#get cookie</div>  <div>&nbsp;&nbsp;&nbsp;try:</div>  <div>raw=urllib2.urlopen(url).read()</div>  <div>&nbsp;&nbsp;&nbsp;except:</div>  <div>print \'proxy unavailable\'</div>  <div>return</div>  <div>&nbsp;&nbsp;&nbsp;try:</div>  <div>viewState=re.findall(\'__VIEWSTATE\" value=\"([^\"]+)\"\',raw)[0]</div>  <div>eventValid=re.findall(\'__EVENTVALIDATION\" value=\"([^\"]+)\"\',raw)[0]</div>  <div>&nbsp;&nbsp;&nbsp;except:</div>  <div>open(\'raw.raw\',\'w\').write(raw)</div>  <div>return</div>  <div>&nbsp;&nbsp;&nbsp;data={\'__EVENTARGUMENT\':\'\',</div>  <div>&nbsp;&nbsp;&nbsp;\'__EVENTTARGET\':\'\',</div>  <div>&nbsp;&nbsp;&nbsp;\'__EVENTVALIDATION\':eventValid,</div>  <div>&nbsp;&nbsp;&nbsp;\'__LASTFOCUS\':\'\',</div>  <div>&nbsp;&nbsp;&nbsp;\'__VIEWSTATE\':viewState,</div>  <div>&nbsp;&nbsp;&nbsp;\'ctl00$masterPlace$playerList$ct001$playerVote\':\'on\',</div>  <div>&nbsp;&nbsp;&nbsp;\'ctl00$masterPlace$playerList$ct002$playerVote\':\'on\',</div>  <div>&nbsp;&nbsp;&nbsp;\'ctl00$masterPlace$voteSubmit\':\'投票\'}</div>  <div>&nbsp;</div>  <div>&nbsp;&nbsp;&nbsp;#vote</div>  <div>&nbsp;&nbsp;&nbsp;try:</div>  <div>raw=urllib2.urlopen(url,urllib.urlencode(data)).read()</div>  <div>&nbsp;&nbsp;&nbsp;except:</div>  <div>print \'proxy data failed\'</div>  <div>return</div>  <div>&nbsp;&nbsp;&nbsp;open(\'raw.%d\' % index,\'w\').write(raw)</div>  <div>&nbsp;&nbsp;&nbsp;voteResult=re.findall(\'voteResult([^/]+)\',raw)</div>  <div>&nbsp;&nbsp;&nbsp;if len(voteResult) &gt; 0:</div>  <div>print index,voteResult[0]</div>  <div>&nbsp;&nbsp;&nbsp;else:</div>  <div>print \'vote failed\'</div>  <div>if __name__==\'__main__\':</div>  <div>&nbsp;&nbsp;&nbsp;socket.setdefaulttimeout(15)</div>  <div>&nbsp;&nbsp;&nbsp;raw=sys.stdin.readlines()#open(\'pedu.txt\').readlines()</div>  <div>&nbsp;&nbsp;&nbsp;pids=[]</div>  <div>&nbsp;&nbsp;&nbsp;for i in range(len(raw)):</div>  <div>pid=os.fork()</div>  <div>proxy={\'http\':raw[i].strip(\'\\n\')}</div>  <div>print \'start \',i</div>  <div>if pid == 0:</div>  <div>&nbsp;&nbsp;&nbsp;vote(i,proxy)</div>  <div>&nbsp;&nbsp;&nbsp;sys.exit(0)</div>  <div>else:</div>  <div>&nbsp;&nbsp;&nbsp;pids.append(pid)</div>  <div>&nbsp;&nbsp;&nbsp;for x in pids:</div>  <div>os.waitpid(x,0)</div><br />',1,1,1,0,0,0,'网上匿名投票0.法律于道德目前并没有找到相关的法律条文，因此些行为不算违法；从道德上讲，如果别人在刷，自己刷吗？如果一定要说什么众人皆醉我独醒，哪又何必去参选呢？1.匿名投票的实现方式无限制：虽然有一小部分投票完全没有限制，但目前的网投多数都有不同的限制方式：Cookie限制：投完票后在客户机本地保存一份Cookie，然后投票按钮不再显示。IP限制：每个IP只允许投一票，将投过票的IP保存到数据库中2.矛：简单的刷票方法无限制投票：最简单的方法就是不停的按F5刷新浏览器；或者用bash同时启动N多wget进程Cookie限制：每次投完票后，IE-&gt;工具-&gt;Internet选项-&gt;清除Cookie，然后就可以再次投票了；或者写一个脚本，先建立连接，获得Cookie，再投票，同时启动N个该脚本。IP限制：和突破Cookie的方法基本相同，只是建立连接的时候要使用代理，这样就等于伪装成代理的IP了。3.盾：刷票的屏蔽方法限制IP段：只允许指定IP段的用户投票，且每IP只能投一票。这个方法很厉害，可是有些麻烦，而且有时候不太适用。很多为了突破GFW而用代理的人怎么办？限制代理：多数代理服务器，会在HTTP请求的头部加上原始请求的IP X-Forwarded-For，如果将这个和IP头部的IP一起检测，就可以防止Client-&gt;Proxy-&gt;Server的刷票方式。但是如果 Client-&gt;Proxy-&gt;Proxy-&gt;Server的刷票方式是可行的话，还是不能根除。验证码技术：每次投票都要手工输入验证码，可以有效防止机器刷票投票间隔检测：对同一被投者的短时间大量不明票源，可假设为刷票，进行进一步检测。终极方案：不要匿名投票，注册投票，注册时用身份证进行区分。几种刷票方式的python伪代码：1)无限制投票#!/usr/bin/pythonimport urllib,timeurl=\"http://www.xxxx.com/post.asp?type=int&amp;name=click&amp;time=\"while True:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;now=time.time()*1000&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;site=urllib.urlopen(\'%s%d\' %(url,int(now)))&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;print site.read()2)Cookie和代理投票去掉proxy相关的内容就是Cookie投票，不去掉也可以python xvote.py &lt; proxy.ok  &nbsp;  代理列表proxy.ok文件  http://119.62.128.38:80  http://148.233.239.24:3128  http://148.233.239.23:80  http://148.233.239.23:3128   xvote.py文件  #!/usr/bin/python  #xvote.py  #encoding: utf-8  import urllib2,urllib,cookielib,socket  import re  import thread,time,os,sys  def vote(index,proxy):  &nbsp;&nbsp;&nbsp;url=\'http://xxxx.edu.cn/xxxx/Default.aspx\'  &nbsp;&nbsp;&nbsp;cj=cookielib.CookieJar()  &nbsp;&nbsp;&nbsp;opener=urllib2.build_opener(urllib2.HTTPCookieProcessor(cj),  &nbsp;&nbsp;&nbsp;urllib2.ProxyHandler(proxy))  &nbsp;&nbsp;&nbsp;urllib2.install_opener(opener)  &nbsp;  &nbsp;&nbsp;&nbsp;#get cookie  &nbsp;&nbsp;&nbsp;try:  raw=urllib2.urlopen(url).read()  &nbsp;&nbsp;&nbsp;except:  print \'proxy unavailable\'  return  &nbsp;&nbsp;&nbsp;try:  viewState=re.findall(\'__VIEWSTATE\" value=\"([^\"]+)\"\',raw)[0]  eventValid=re.findall(\'__EVENTVALIDATION\" value=\"([^\"]+)\"\',raw)[0]  &nbsp;&nbsp;&nbsp;except:  open(\'raw.raw\',\'w\').write(raw)  return  &nbsp;&nbsp;&nbsp;data={\'__EVENTARGUMENT\':\'\',  &nbsp;&nbsp;&nbsp;\'__EVENTTARGET\':\'\',  &nbsp;&nbsp;&nbsp;\'__EVENTVALIDATION\':eventValid,  &nbsp;&nbsp;&nbsp;\'__LASTFOCUS\':\'\',  &nbsp;&nbsp;&nbsp;\'__VIEWSTATE\':viewState,  &nbsp;&nbsp;&nbsp;\'ctl00$masterPlace$playerList$ct001$playerVote\':\'on\',  &nbsp;&nbsp;&nbsp;\'ctl00$masterPlace$playerList$ct002$playerVote\':\'on\',  &nbsp;&nbsp;&nbsp;\'ctl00$masterPlace$voteSubmit\':\'投票\'}  &nbsp;  &nbsp;&nbsp;&nbsp;#vote  &nbsp;&nbsp;&nbsp;try:  raw=urllib2.urlopen(url,urllib.urlencode(data)).read()  &nbsp;&nbsp;&nbsp;except:  print \'proxy data failed\'  return  &nbsp;&nbsp;&nbsp;open(\'raw.%d\' % index,\'w\').write(raw)  &nbsp;&nbsp;&nbsp;voteResult=re.findall(\'voteResult([^/]+)\',raw)  &nbsp;&nbsp;&nbsp;if len(voteResult) &gt; 0:  print index,voteResult[0]  &nbsp;&nbsp;&nbsp;else:  print \'vote failed\'  if __name__==\'__main__\':  &nbsp;&nbsp;&nbsp;socket.setdefaulttimeout(15)  &nbsp;&nbsp;&nbsp;raw=sys.stdin.readlines()#open(\'pedu.txt\').readlines()  &nbsp;&nbsp;&nbsp;pids=[]  &nbsp;&nbsp;&nbsp;for i in range(len(raw)):  pid=os.fork()  proxy={\'http\':raw[i].strip(\'\\n\')}  print \'start \',i  if pid == 0:  &nbsp;&nbsp;&nbsp;vote(i,proxy)  &nbsp;&nbsp;&nbsp;sys.exit(0)  else:  &nbsp;&nbsp;&nbsp;pids.append(pid)  &nbsp;&nbsp;&nbsp;for x in pids:  os.waitpid(x,0)','',0,'network',0,'','zinnia/_entry_detail.html','entry_detail.html'),(200,'百度地图在BSD下飞一样的速度','3373193720078774815668',2,'2007-09-07 07:48:15',NULL,'2007-09-07 07:48:15','2014-07-13 18:57:18','今天用BSD试了一下百度的地图，不知道是早上网速好还是BSD的缘故，速度超好。就跟显示本机图片似的。<br />',1,1,1,0,0,0,'今天用BSD试了一下百度的地图，不知道是早上网速好还是BSD的缘故，速度超好。就跟显示本机图片似的。','',0,'mood',0,'','zinnia/_entry_detail.html','entry_detail.html'),(201,'用Kindle3作raspberry pi的显示器','33731937201292805247715',2,'2012-10-28 01:28:22',NULL,'2012-10-28 01:28:22','2014-07-13 18:57:18','把Kindle作为raspberry pi的屏幕，再加一个USB键盘，一个便携的小霸王编程机就诞生了，看hello worlp：<div><div><img alt=\"用Kindle3作raspberry pi的显示器 - hankjin - 云水居\" src=\"/static/163/6597615124423755525.jpg\" style=\"margin-top: 0px; margin-right: 10px; margin-bottom: 0px; margin-left: 0px; width: 300px; height: auto;\" title=\"用Kindle3作raspberry pi的显示器 - hankjin - 云水居\" /></div>&nbsp;<br /><div><b>硬件：</b></div><div>1. Kindle3：淘宝</div><div>2. raspberry PI：易络盟</div><div><span style=\"line-height: 22px;\">3. microUSB充电器一个：淘宝</span></div><div>4. USB转microUSB数据线一条：淘宝</div><div>5. USB键盘一个：淘宝</div><div>6. 视频线一条+电视一个 或 HDMI线一条+显示器一个：用于初始化设置raspberry PI, 以后就不用了。</div><div>7. SD卡一个：SanDisk闪迪 Ultra 高速 SD卡 SDHC 16G Class10 30M/s 200X 高清</div><div>8. SSD读卡器一个：淘宝</div><div>9. 网线：淘宝</div><div><b>软件：</b></div><div>1. Kindle3 越狱包：update_jailbreak_0.6.N_k3g_install.bin</div><div>2. Kindle3 usbnetwork：update_usbnetwork_0.33.N_k3g_install.bin</div><div>3. Kindle3 launchpad：update_launchpad_0.0.1c_k3g_install.bin</div><div>4. Kindle3 myts（Kindle3的虚拟终端）：myts-7.zip</div><div>5. Kindle3 usbnetwork的Windows驱动：XP-RNDIS-Ethernet-Gadget</div><div>6. win32DiskImage：Win32DiskImager.exe</div><div>7. raspberry pi镜像：2012-09-18-wheezy-raspbian.img</div><div>8. raspberry tmux：使用电视启动后，sudo apt-get install</div><div>9. raspberry dropbear：<span style=\"line-height: 22px;\">使用电视启动后，sudo apt-get install</span></div><div><b>原理：</b></div><div>1. pi启动时自动登录，且自动使用tmux/screen共享session</div><div><span style=\"line-height: 22px;\">2. kindle和pi使用usbnetwork创建连接</span> </div><div>3. kindle自动登录pi，且自动使用tmux/screen共享session</div><div>4. USB键盘和Kindle键盘分别操作两个pi和kindle，但是两者共享session，所以看起来是一样的。</div><div><div><b>参考：</b></div></div><div><a href=\"http://projectdp.wordpress.com/2012/09/24/pi-k3w-kindle-3-display-for-raspberry-pi/\" rel=\"nofollow\">http://projectdp.wordpress.com/2012/09/24/pi-k3w-kindle-3-display-for-raspberry-pi/</a> </div></div>',1,1,1,0,0,0,'把Kindle作为raspberry pi的屏幕，再加一个USB键盘，一个便携的小霸王编程机就诞生了，看hello worlp：&nbsp;硬件：1. Kindle3：淘宝2. raspberry PI：易络盟3. microUSB充电器一个：淘宝4. USB转microUSB数据线一条：淘宝5. USB键盘一个：淘宝6. 视频线一条+电视一个 或 HDMI线一条+显示器一个：用于初始化设置raspberry PI, 以后就不用了。7. SD卡一个：SanDisk闪迪 Ultra 高速 SD卡 SDHC 16G Class10 30M/s 200X 高清8. SSD读卡器一个：淘宝9. 网线：淘宝软件：1. Kindle3 越狱包：update_jailbreak_0.6.N_k3g_install.bin2. Kindle3 usbnetwork：update_usbnetwork_0.33.N_k3g_install.bin3. Kindle3 launchpad：update_launchpad_0.0.1c_k3g_install.bin4. Kindle3 myts（Kindle3的虚拟终端）：myts-7.zip5. Kindle3 usbnetwork的Windows驱动：XP-RNDIS-Ethernet-Gadget6. win32DiskImage：Win32DiskImager.exe7. raspberry pi镜像：2012-09-18-wheezy-raspbian.img8. raspberry tmux：使用电视启动后，sudo apt-get install9. raspberry dropbear：使用电视启动后，sudo apt-get install原理：1. pi启动时自动登录，且自动使用tmux/screen共享session2. kindle和pi使用usbnetwork创建连接 3. kindle自动登录pi，且自动使用tmux/screen共享session4. USB键盘和Kindle键盘分别操作两个pi和kindle，但是两者共享session，所以看起来是一样的。参考：http://projectdp.wordpress.com/2012/09/24/pi-k3w-kindle-3-display-for-raspberry-pi/ ','',0,'os',0,'','zinnia/_entry_detail.html','entry_detail.html'),(202,'Windows和Ubuntu下的图片中的文字识别OCR','33731937200942382132968',2,'2009-05-23 20:21:32',NULL,'2009-05-23 20:21:32','2014-07-13 18:57:18','在Windows上，最简单的办法就是使用Office的Document Image工具<br />点开始-程序-控制面板-添加/删除程序，找到Office-修改 找到Microsoft Office Document Imaging 这个组件，Microsoft Office Document Imaging Writer 点在本机上运行，安装就可以了。<br />首先把图片转换为，打开要识别的图片，点打印，打印机选择Microsoft Office Document Image Writer，打印到硬盘的某个文件夹下，记住位置，下一步要用<br />从开始菜单启动“Microsoft Office/ Microsoft Office 工具/Microsoft Office Document Imageing”。点击文件-》打开，然后选择刚才打印到的文件<br />点击工具栏中的“使用OCR识别文字”按键，就开始对刚才扫描的文件进行识别了。按下“将文本发送到Word”按键即可将识别出来的文字转换到 Word中去了。如果你要获取部分文字，只需要用鼠标框选所需文字，然后点击鼠标右键选择“将文本发送到Word”就将选中区域的文字发送到Word中了。<br /><br />在Ubuntu下，就有很多可以用了，例如下面这三个<br />tesseract-ocr<br />ocrad<br />gocr <br />其中gocr是一个命令行的工具，不过很方便的<br />#安装<br />sudo apt-get install gocr<br />#Linux下的命令还是很好的，不会用就让他告诉你怎么用，直接输入gocr，它会提示你怎么用<br />gocr<br />#根据上一步命令的提示，查看使用说明<br />gocr -h<br />#gocr -h显示的帮助信息很全，只要看example就可以了，尤其是最后一行<br /># examples:<br />#&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; gocr -m 4 text1.pbm&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; # do layout analyzis<br />#&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; gocr -m 130 -p ./database/ text1.pbm&nbsp; # extend database<br />#&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; djpeg -pnm -gray text.jpg | gocr -&nbsp;&nbsp;&nbsp; # use jpeg-file via pipe<br />#试一下，先找一个写满文字的jpg图片，如hank.jpg,<br />djpeg -pnm -gray hank.jpg | gocr -<br />#识别结果直接显示在终端上，虽然效果不太好<br />',1,1,1,0,0,0,'在Windows上，最简单的办法就是使用Office的Document Image工具点开始-程序-控制面板-添加/删除程序，找到Office-修改 找到Microsoft Office Document Imaging 这个组件，Microsoft Office Document Imaging Writer 点在本机上运行，安装就可以了。首先把图片转换为，打开要识别的图片，点打印，打印机选择Microsoft Office Document Image Writer，打印到硬盘的某个文件夹下，记住位置，下一步要用从开始菜单启动“Microsoft Office/ Microsoft Office 工具/Microsoft Office Document Imageing”。点击文件-》打开，然后选择刚才打印到的文件点击工具栏中的“使用OCR识别文字”按键，就开始对刚才扫描的文件进行识别了。按下“将文本发送到Word”按键即可将识别出来的文字转换到 Word中去了。如果你要获取部分文字，只需要用鼠标框选所需文字，然后点击鼠标右键选择“将文本发送到Word”就将选中区域的文字发送到Word中了。在Ubuntu下，就有很多可以用了，例如下面这三个tesseract-ocrocradgocr 其中gocr是一个命令行的工具，不过很方便的#安装sudo apt-get install gocr#Linux下的命令还是很好的，不会用就让他告诉你怎么用，直接输入gocr，它会提示你怎么用gocr#根据上一步命令的提示，查看使用说明gocr -h#gocr -h显示的帮助信息很全，只要看example就可以了，尤其是最后一行# examples:#&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; gocr -m 4 text1.pbm&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; # do layout analyzis#&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; gocr -m 130 -p ./database/ text1.pbm&nbsp; # extend database#&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; djpeg -pnm -gray text.jpg | gocr -&nbsp;&nbsp;&nbsp; # use jpeg-file via pipe#试一下，先找一个写满文字的jpg图片，如hank.jpg,djpeg -pnm -gray hank.jpg | gocr -#识别结果直接显示在终端上，虽然效果不太好','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(203,'在Firefox中使用IRC','3373193720098242225902',2,'2009-09-02 16:22:25',NULL,'2009-09-02 16:22:25','2014-07-13 18:57:18','首先安装Firefox的ChatZilla插件<br />然后从Firefox中打开ChatZilla， Tools-&gt;ChatZilla<br /><br />进入freenode的irc网络<br />/attach freenode<br /><br />注册账号<br />/nick hankjin<br />/msg nickserv register &lt;password&gt; &lt;email&gt;<br />#查收邮件，确认注册<br /><span>/msg nickserv VERIFY REGISTER hankjin kyaguqqbfoaj</span><br /><br />查看频道列表<br />/rlist ubuntu<br />进入频道<br />/join #ubuntu-cn<br />/join #python<br />',1,1,1,0,0,0,'首先安装Firefox的ChatZilla插件然后从Firefox中打开ChatZilla， Tools-&gt;ChatZilla进入freenode的irc网络/attach freenode注册账号/nick hankjin/msg nickserv register &lt;password&gt; &lt;email&gt;#查收邮件，确认注册/msg nickserv VERIFY REGISTER hankjin kyaguqqbfoaj查看频道列表/rlist ubuntu进入频道/join #ubuntu-cn/join #python','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(204,'Linux timer functions','33731937201421811512609',2,'2014-03-18 11:05:12',NULL,'2014-03-18 11:05:12','2014-07-13 18:57:18','<div><br /></div>&nbsp;<img alt=\"Linux timer functions - hankjin - 云水居\" src=\"/static/163/6597941679379273519.png\" style=\"line-height: 28px; margin: 0px 10px 0px 0px;\" title=\"Linux timer functions - hankjin - 云水居\" /><br />&nbsp;man for detail',1,1,1,0,0,0,'&nbsp;&nbsp;man for detail','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(205,'2011购书','3373193720111272572780',2,'2011-02-27 15:11:13',NULL,'2011-02-27 15:11:13','2014-07-13 18:57:18','列个书单<br />《美的历程》<br />《日知录》<br />《梦溪笔谈》<br />《颜氏家训》<br />《梦的解析》<br />《美学》<br />《人口原理》<br />《少年维特之烦恼》<br />《国富论》<br />选择一：书店<br />选择二：淘宝<br />选择三：卓越<br />价格上肯定淘宝最低，但淘宝要运费，而且一般每个书店都只有一两本合适的；<br />卓越免运费，但一者价格不满意，二者很多缺货；<br />宅得久了，逛逛书店倒也不错。<br />但基本每本都有好几个版本，又是一件头疼的事儿，书店一般只有一个版本，没有办法比较。<br /><font id=\"word_name\"><br />智慧的代价是矛盾，这是人生对人生观开的玩笑。</font>研究人员发现，选择太多令人迷惘，甚至可能导致抑郁。最简单的办法就是抛硬币，更有技术含量的是卜一卦，顺便买本《周易》',1,1,1,0,0,0,'列个书单《美的历程》《日知录》《梦溪笔谈》《颜氏家训》《梦的解析》《美学》《人口原理》《少年维特之烦恼》《国富论》选择一：书店选择二：淘宝选择三：卓越价格上肯定淘宝最低，但淘宝要运费，而且一般每个书店都只有一两本合适的；卓越免运费，但一者价格不满意，二者很多缺货；宅得久了，逛逛书店倒也不错。但基本每本都有好几个版本，又是一件头疼的事儿，书店一般只有一个版本，没有办法比较。智慧的代价是矛盾，这是人生对人生观开的玩笑。研究人员发现，选择太多令人迷惘，甚至可能导致抑郁。最简单的办法就是抛硬币，更有技术含量的是卜一卦，顺便买本《周易》','',0,'',0,'','zinnia/_entry_detail.html','entry_detail.html'),(206,'Ubuntu下开始Haskell编程','337319372010111313923456',2,'2010-12-13 14:01:06',NULL,'2010-12-13 14:01:06','2014-07-13 18:57:18','Haskell是一种纯函数编程语言，也是现在最广泛被应用研究的一门语言，它的很多思想对于现代编程思想和设计模式有重要的影响。所以学一下还是有一定的价值的。<br /><br />首先安装Haskell环境<br />sudo apt-get install ghc6<br /><b>交互式环境</b><br />使用ghci命令，可以进入交互式命令行，执行haskell操作并返回输出，例如：<br />$ghci<br />Prelude&gt; 3+4<br />7<br />Prelude&gt; putStrLn \"Hello, Haskell\"<br />Hello, Haskell<br />Prelude&gt; <br />按Ctrl+D退出<br /><br /><b>脚本编程</b><br />编写haskell脚本语言，然后保存成&lt;name&gt;.hs的文件，在haskell交互式环境下，可以使用:l &lt;name&gt;加载脚本，并使用。例如：<br />$cat &gt; test.hs &lt;&lt;EOF<br />add x y=x+y<br />EOF<br />$ghci<br />Prelude&gt; :l test<br />[1 of 1] Compiling Main&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( test.hs, interpreted )<br />Ok, modules loaded: Main.<br />*Main&gt; add 3 4<br />7<br />也可以通过添加#!/usr/bin/runghc6，按脚本方式执行。<br />$cat &gt; hello.hs&lt;&lt;EOF<br />\n#!/usr/bin/runghc6<br />\nmain=putStrLn \"Hello, Haskell from elf\"<br />\nEOF<br />$ chmod +x hello.hs;./hello.hs<br />\n\nHello, Haskell from elf<br /><br /><b>编译执行<br /></b>另外，Haskell脚本也可以编译成可执行文件。使用ghc命令，可以将Haskell脚本编译成可执行文件。例如：<br />$cat &gt; hello.hs&lt;&lt;EOF<br />#!/usr/bin/runghc6<br />main=putStrLn \"Hello, Haskell from elf\"<br />EOF<br />$ghc --make hello<br />[1 of 1] Compiling Main&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( hello.hs, hello.o )<br />Linking hello ...<br />$ ./hello<br />Hello, Haskell from elf',1,1,1,0,0,0,'Haskell是一种纯函数编程语言，也是现在最广泛被应用研究的一门语言，它的很多思想对于现代编程思想和设计模式有重要的影响。所以学一下还是有一定的价值的。首先安装Haskell环境sudo apt-get install ghc6交互式环境使用ghci命令，可以进入交互式命令行，执行haskell操作并返回输出，例如：$ghciPrelude&gt; 3+47Prelude&gt; putStrLn \"Hello, Haskell\"Hello, HaskellPrelude&gt; 按Ctrl+D退出脚本编程编写haskell脚本语言，然后保存成&lt;name&gt;.hs的文件，在haskell交互式环境下，可以使用:l &lt;name&gt;加载脚本，并使用。例如：$cat &gt; test.hs &lt;&lt;EOFadd x y=x+yEOF$ghciPrelude&gt; :l test[1 of 1] Compiling Main&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( test.hs, interpreted )Ok, modules loaded: Main.*Main&gt; add 3 47也可以通过添加#!/usr/bin/runghc6，按脚本方式执行。$cat &gt; hello.hs&lt;&lt;EOF\n#!/usr/bin/runghc6\nmain=putStrLn \"Hello, Haskell from elf\"\nEOF$ chmod +x hello.hs;./hello.hs\n\nHello, Haskell from elf编译执行另外，Haskell脚本也可以编译成可执行文件。使用ghc命令，可以将Haskell脚本编译成可执行文件。例如：$cat &gt; hello.hs&lt;&lt;EOF#!/usr/bin/runghc6main=putStrLn \"Hello, Haskell from elf\"EOF$ghc --make hello[1 of 1] Compiling Main&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( hello.hs, hello.o )Linking hello ...$ ./helloHello, Haskell from elf','',0,'other',0,'','zinnia/_entry_detail.html','entry_detail.html'),(207,'JAVA字符编码的奥秘','337319372006829055530',2,'2006-09-29 12:55:53',NULL,'2006-09-29 12:55:53','2014-07-13 18:57:18','天气:<img src=\"/static/163/4278982595955566812.gif\" />心情:<img src=\"/static/163/5131851775388843392.gif\" /><br />\n<p>一、概要<br />在JAVA应用程序特别是基于WEB的程序中，经常遇到字符的编码问题。为了防止出现 </p>\n<p>乱码，首先需要了解JAVA是如何处理字符的，这样就可以有目的地在输入/输出环节中</p>\n<p>增加必要的转码。其次，由于各种服务器有不同的处理方式，还需要多做试验，确保使</p>\n<p>用中不出现乱码。<br />二、基本概念<br />2．1 JAVA中字符的表达<br />JAVA中有char、byte、String这几个概念。char 指的是一个UNICODE字符，为16位的</p>\n<p>整数。byte 是字节，字符串在网络传输或存储前需要转换为byte数组。在从网络接收</p>\n<p>或从存储设备读取后需要将byte数组转换成String。String是字符串，可以看成是由</p>\n<p>char组成的数组。String 和 char 为内存形式，byte是网络传输或存储的序列化形式。<br />&nbsp;举例：<br />英<br />String ying = “英”;<br />char ying = ying.charAt(0);<br />String yingHex = Integer.toHexString(ying);<br />82 F1&nbsp;&nbsp; <br />byte yingGBBytes = ying.getBytes(“GBK”);<br />GB编码的字节数值<br />D3 A2 <br />2．2 编码方式的简介<br />&nbsp;String序列化成byte数组或反序列化时需要选择正确的编码方式。如果编码方式不正确</p>\n<p>，就会得到一些0x3F的值。常用的字符编码方式有ISO8859_1、GB2312、GBK、UTF</p>\n<p>-8/UTF-16/UTF-32。<br />ISO8859_1用来编码拉丁文，它由单字节（0－255）组成。<br />&nbsp;GB2312、GBK用来编码简体中文，它有单字节和双字节混合组成。最高位为1的字节</p>\n<p>和下一个字节构成一个汉字，最高位为0的字节是ASCII码。<br />UTF-8/UTF-16/UTF-32是国际标准UNICODE的编码方式。 用得最多的是UTF-8，主要</p>\n<p>是因为它在对拉丁文编码时节约空间。<br />UNICODE值 UTF-8编码<br />U-00000000 - U-0000007F: 0xxxxxxx<br />U-00000080 - U-000007FF:&nbsp; 110xxxxx 10xxxxxx <br />U-00000800 - U-0000FFFF:&nbsp; 1110xxxx 10xxxxxx 10xxxxxx <br />U-00010000 - U-001FFFFF:&nbsp; 11110xxx 10xxxxxx 10xxxxxx 10xxxxxx <br />U-00200000 - U-03FFFFFF:&nbsp; 111110xx 10xxxxxx 10xxxxxx 10xxxxxx 10xxxxxx <br />U-04000000 - U-7FFFFFFF:&nbsp; 1111110x 10xxxxxx 10xxxxxx 10xxxxxx 10xxxxxx </p>\n<p>10xxxxxx <br />三、J2SE中相关的函数<br />String str =”英”;<br />&nbsp;//取得GB2312编码的字节<br />byte[] bytesGB2312 = str.getBytes(“GB2312”); <br />//取得平台缺省编码的字节(solaris为ISO8859_1,windows为GB2312)<br />byte[] bytesDefault = str.getBytes();<br />//用指定的编码将字节转换成字符串<br />&nbsp;String newStrGB = new String(bytesGB2312, “GB2312”);<br />&nbsp;//用平台缺省的编码将字节转换成字符串(solaris为ISO8859_1,windows为GB2312)<br />&nbsp;String newStrDefault = new String(bytesDefault);<br />&nbsp;//用指定的编码从字节流里面读取字符<br />InputStream in = xxx;<br />InputStreamReader reader = InputStreamReader( in, “GB2312”);<br />char aChar = reader.read();<br />四、JSP、数据库的编码<br />4．1 JSP中的编码<br />(1) 静态声明:<br />CHARSET有两个作用：<br />JSP文件的编码方式：在读取JSP文件、生成JAVA类时，源JSP文件中汉字的编码<br />JSP输出流的编码方式：在执行JSP时，往response流里面写入数据的编码方式<br />(2) 动态改变:在往response流里面写数据前可以调用response.setContentType()，设</p>\n<p>定正确的编码类型。<br />(3) 在TOMCAT中，由Request.getParameter() 得到的参数，编码方式都是</p>\n<p>ISO8859_1。所以如果在浏览器输入框内输入一个汉字“英”，在服务器端就得到一个</p>\n<p>ISO8859_1编码的（0x00,0xD3,0x00,0xA2）。所以通常在接收参数时转码：<br />String wrongStr = response.getParameter(“name”);<br />String correctStr = new String(wrongStr.getBytes(“ISO8859_1”),”GB2312”);<br />在最新的SERVLET规范里面，也可以在获取参数之前执行如下代码：<br />request.setCharacterEncoding(“GB2312”);<br />4．2 数据库的编码<br />(1) 数据库使用UTF-16<br />如果String中是UNICODE字符，写入读出时不需要转码<br />(2) 数据库使用ISO8859_1<br />如果String中是UNICODE字符，写入读出时需要转码<br />写入：String newStr = new String(oldStr.getByte(“GB2312”), “ISO8859_1”);<br />读出：String newStr = new String(oldStr.getByte(“ISO8859_1”),”GB2312”);</p>',1,1,1,0,0,0,'天气:心情:\n一、概要在JAVA应用程序特别是基于WEB的程序中，经常遇到字符的编码问题。为了防止出现 \n乱码，首先需要了解JAVA是如何处理字符的，这样就可以有目的地在输入/输出环节中\n增加必要的转码。其次，由于各种服务器有不同的处理方式，还需要多做试验，确保使\n用中不出现乱码。二、基本概念2．1 JAVA中字符的表达JAVA中有char、byte、String这几个概念。char 指的是一个UNICODE字符，为16位的\n整数。byte 是字节，字符串在网络传输或存储前需要转换为byte数组。在从网络接收\n或从存储设备读取后需要将byte数组转换成String。String是字符串，可以看成是由\nchar组成的数组。String 和 char 为内存形式，byte是网络传输或存储的序列化形式。&nbsp;举例：英String ying = “英”;char ying = ying.charAt(0);String yingHex = Integer.toHexString(ying);82 F1&nbsp;&nbsp; byte yingGBBytes = ying.getBytes(“GBK”);GB编码的字节数值D3 A2 2．2 编码方式的简介&nbsp;String序列化成byte数组或反序列化时需要选择正确的编码方式。如果编码方式不正确\n，就会得到一些0x3F的值。常用的字符编码方式有ISO8859_1、GB2312、GBK、UTF\n-8/UTF-16/UTF-32。ISO8859_1用来编码拉丁文，它由单字节（0－255）组成。&nbsp;GB2312、GBK用来编码简体中文，它有单字节和双字节混合组成。最高位为1的字节\n和下一个字节构成一个汉字，最高位为0的字节是ASCII码。UTF-8/UTF-16/UTF-32是国际标准UNICODE的编码方式。 用得最多的是UTF-8，主要\n是因为它在对拉丁文编码时节约空间。UNICODE值 UTF-8编码U-00000000 - U-0000007F: 0xxxxxxxU-00000080 - U-000007FF:&nbsp; 110xxxxx 10xxxxxx U-00000800 - U-0000FFFF:&nbsp; 1110xxxx 10xxxxxx 10xxxxxx U-00010000 - U-001FFFFF:&nbsp; 11110xxx 10xxxxxx 10xxxxxx 10xxxxxx U-00200000 - U-03FFFFFF:&nbsp; 111110xx 10xxxxxx 10xxxxxx 10xxxxxx 10xxxxxx U-04000000 - U-7FFFFFFF:&nbsp; 1111110x 10xxxxxx 10xxxxxx 10xxxxxx 10xxxxxx \n10xxxxxx 三、J2SE中相关的函数String str =”英”;&nbsp;//取得GB2312编码的字节byte[] bytesGB2312 = str.getBytes(“GB2312”); //取得平台缺省编码的字节(solaris为ISO8859_1,windows为GB2312)byte[] bytesDefault = str.getBytes();//用指定的编码将字节转换成字符串&nbsp;String newStrGB = new String(bytesGB2312, “GB2312”);&nbsp;//用平台缺省的编码将字节转换成字符串(solaris为ISO8859_1,windows为GB2312)&nbsp;String newStrDefault = new String(bytesDefault);&nbsp;//用指定的编码从字节流里面读取字符InputStream in = xxx;InputStreamReader reader = InputStreamReader( in, “GB2312”);char aChar = reader.read();四、JSP、数据库的编码4．1 JSP中的编码(1) 静态声明:CHARSET有两个作用：JSP文件的编码方式：在读取JSP文件、生成JAVA类时，源JSP文件中汉字的编码JSP输出流的编码方式：在执行JSP时，往response流里面写入数据的编码方式(2) 动态改变:在往response流里面写数据前可以调用response.setContentType()，设\n定正确的编码类型。(3) 在TOMCAT中，由Request.getParameter() 得到的参数，编码方式都是\nISO8859_1。所以如果在浏览器输入框内输入一个汉字“英”，在服务器端就得到一个\nISO8859_1编码的（0x00,0xD3,0x00,0xA2）。所以通常在接收参数时转码：String wrongStr = response.getParameter(“name”);String correctStr = new String(wrongStr.getBytes(“ISO8859_1”),”GB2312”);在最新的SERVLET规范里面，也可以在获取参数之前执行如下代码：request.setCharacterEncoding(“GB2312”);4．2 数据库的编码(1) 数据库使用UTF-16如果String中是UNICODE字符，写入读出时不需要转码(2) 数据库使用ISO8859_1如果String中是UNICODE字符，写入读出时需要转码写入：String newStr = new String(oldStr.getByte(“GB2312”), “ISO8859_1”);读出：String newStr = new String(oldStr.getByte(“ISO8859_1”),”GB2312”);','',0,'j2se',0,'','zinnia/_entry_detail.html','entry_detail.html'),(208,'Spring get started','337319372008324105848897',2,'2008-04-24 22:58:48',NULL,'2008-04-24 22:58:48','2014-07-13 18:57:18','<p>1. Spring的IoC控件主要专注于如何利用classes、对象和服务去组成一个企业级应用，通过规范的方式，将各种不同的控件整合成一个完整的应用。Spring中使用了很多被实践证明的最佳实践和正规的设计模式，并且进行了编码实现。如果你是一个，构架师或者开发人员完全可以取出它们集成到你自己的应用之中。这对于那些使用了Spring Framework的组织和机构来说，在spring基础上实现应用不仅可以构建优秀的，可维护的应用并对Spring的设计进行验证，<em>确实是一件好事情</em>。</p>  <p>2. Example: Test.java+ShowMessage.java+test.xml</p>  <p>依赖类哭 spring.jar+common-logging.jar</p>  <p>javac *.java</p>  <p>java Test</p>  <p>2.1Test.java</p>  <p>import org.springframework.context.ApplicationContext;<br />import org.springframework.context.support.FileSystemXmlApplicationContext;</p>  <p>public class Test {<br />&nbsp;public static void main(String argv[]){<br />&nbsp;&nbsp;//1. init context<br />&nbsp;&nbsp;ApplicationContext ctx = new FileSystemXmlApplicationContext(\"myspring.xml\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;//2. get bean.<br />&nbsp;&nbsp;SayHi sh = (SayHi)ctx.getBean(\"myBean\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;//3. invoke method<br />&nbsp;&nbsp;sh.say();<br />&nbsp;}<br />}<br /></p>  <p>2.2 SayHi.java</p>  <p>public class SayHi {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private String name;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public void setName(String name){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.name = name;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public String getName(){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return this.name;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; /*methods*/<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public void say(){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.print(\"Hi, \" + getName());<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />}<br /></p>  <p>2.3 test.xml</p>  <p>&lt;?xml version=\"1.0\" encoding=\"UTF-8\"?&gt;<br />&lt;!DOCTYPE beans PUBLIC \"-//SPRING//DTD BEAN//EN\" \"<a href=\"http://www.springframework.org/dtd/spring-beans.dtd\" rel=\"nofollow\">http://www.springframework.org/dtd/spring-beans.dtd</a>\"&gt;<br />&lt;beans&gt;<br />&nbsp;&nbsp;&nbsp; &lt;bean id = \"myBean\" class=\"SayHi\"&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;property name=\"name\"&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;value&gt;hank&lt;/value&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/property&gt;<br />&nbsp;&nbsp;&nbsp; &lt;/bean&gt;<br />&lt;/beans&gt;<br /></p>  <p>&nbsp;</p>  <p>References:</p>  <p><a href=\"http://www.springframework.org/\" rel=\"nofollow\">http://www.springframework.org/</a></p>',1,1,1,0,0,0,'1. Spring的IoC控件主要专注于如何利用classes、对象和服务去组成一个企业级应用，通过规范的方式，将各种不同的控件整合成一个完整的应用。Spring中使用了很多被实践证明的最佳实践和正规的设计模式，并且进行了编码实现。如果你是一个，构架师或者开发人员完全可以取出它们集成到你自己的应用之中。这对于那些使用了Spring Framework的组织和机构来说，在spring基础上实现应用不仅可以构建优秀的，可维护的应用并对Spring的设计进行验证，确实是一件好事情。  2. Example: Test.java+ShowMessage.java+test.xml  依赖类哭 spring.jar+common-logging.jar  javac *.java  java Test  2.1Test.java  import org.springframework.context.ApplicationContext;import org.springframework.context.support.FileSystemXmlApplicationContext;  public class Test {&nbsp;public static void main(String argv[]){&nbsp;&nbsp;//1. init context&nbsp;&nbsp;ApplicationContext ctx = new FileSystemXmlApplicationContext(\"myspring.xml\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;//2. get bean.&nbsp;&nbsp;SayHi sh = (SayHi)ctx.getBean(\"myBean\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;//3. invoke method&nbsp;&nbsp;sh.say();&nbsp;}}  2.2 SayHi.java  public class SayHi {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private String name;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public void setName(String name){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.name = name;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public String getName(){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return this.name;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; /*methods*/&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public void say(){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.print(\"Hi, \" + getName());&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }}  2.3 test.xml  &lt;?xml version=\"1.0\" encoding=\"UTF-8\"?&gt;&lt;!DOCTYPE beans PUBLIC \"-//SPRING//DTD BEAN//EN\" \"http://www.springframework.org/dtd/spring-beans.dtd\"&gt;&lt;beans&gt;&nbsp;&nbsp;&nbsp; &lt;bean id = \"myBean\" class=\"SayHi\"&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;property name=\"name\"&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;value&gt;hank&lt;/value&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/property&gt;&nbsp;&nbsp;&nbsp; &lt;/bean&gt;&lt;/beans&gt;  &nbsp;  References:  http://www.springframework.org/','',0,'j2ee',0,'','zinnia/_entry_detail.html','entry_detail.html'),(209,'计算字符串中最长不重复子串的长度','337319372010829111040124',2,'2010-09-29 23:10:40',NULL,'2010-09-29 23:10:40','2014-07-13 18:57:18','求一个字符串中的无重复字符的子串的最大长度<br />如xabcdabxb<br />最长子串是：xabcd，长度是5，<br />而xabcda中有重复字符：两个a，所以不是最长子串。<br /><br />#include &lt;algorithm&gt;<br />#include &lt;iostream&gt;<br />using namespace std;<br /><br />int search(char *text){<br />&nbsp;&nbsp;&nbsp; int lastpos[256], lmax=0, curmax=0;<br />&nbsp;&nbsp;&nbsp; for(int i=0;i&lt;256;i++)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; lastpos[i]=-1;<br />&nbsp;&nbsp;&nbsp; for(int i=0; text[i]; i++){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(i-lastpos[ text[i] ] &gt; curmax){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; curmax++;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; lmax = max(lmax, curmax);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp; &nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; curmax = i-lastpos[ text[i] ];<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; lastpos[ text[i] ] = i;<br />&nbsp;&nbsp;&nbsp; }&nbsp; &nbsp;<br />&nbsp;&nbsp;&nbsp; return lmax;<br />}<br />int main(){<br />&nbsp;&nbsp;&nbsp; char raw[] = \"xabcdabxb\";<br />&nbsp;&nbsp;&nbsp; cout&lt;&lt;search(raw)&lt;&lt;endl;<br />&nbsp;&nbsp;&nbsp; return 0;<br />}',1,1,1,0,0,0,'求一个字符串中的无重复字符的子串的最大长度如xabcdabxb最长子串是：xabcd，长度是5，而xabcda中有重复字符：两个a，所以不是最长子串。#include &lt;algorithm&gt;#include &lt;iostream&gt;using namespace std;int search(char *text){&nbsp;&nbsp;&nbsp; int lastpos[256], lmax=0, curmax=0;&nbsp;&nbsp;&nbsp; for(int i=0;i&lt;256;i++)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; lastpos[i]=-1;&nbsp;&nbsp;&nbsp; for(int i=0; text[i]; i++){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(i-lastpos[ text[i] ] &gt; curmax){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; curmax++;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; lmax = max(lmax, curmax);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; curmax = i-lastpos[ text[i] ];&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; lastpos[ text[i] ] = i;&nbsp;&nbsp;&nbsp; }&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; return lmax;}int main(){&nbsp;&nbsp;&nbsp; char raw[] = \"xabcdabxb\";&nbsp;&nbsp;&nbsp; cout&lt;&lt;search(raw)&lt;&lt;endl;&nbsp;&nbsp;&nbsp; return 0;}','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(210,'Fw网络基础入门','337319372008414104653438',2,'2008-05-14 10:46:53',NULL,'2008-05-14 10:46:53','2014-07-13 18:57:18','术语解释：计算机网络　Internet　Intranet　TCP/IP　IP地址　网关　DOMAIN　主机　域名　DNS　WWW　EMAIL　FTP　Telnet　URL　HTML　主页　浏览器　搜索引擎　下载　计算机病毒　计算机木马　垃圾邮件　防火墙　JAVA　网络电话<br /><br />常用网络命令: arp　finger　ftp　nbtstat　netstat　ping　route　tftp　tracert<br />什么是计算机网络<br /><br />　　计算机网络，它是指利用通信线路连接起来的、相互独立的计算机的集合。这包含两个概念：首先，这些计算机之间必须存在通信线路；其次，这些计算机必须相互独立。从这个定义出发，传统的主机/终端式系统就不能算是严格意义上的计算机网络。<br />　　按地理位置划分，计算机网络可分为四种：网际网、广域网(WAN)、城域网(MAN)和局域网(LAN)。 Internet就是世界上最大的网际网，另外还存在其他网际网类型，它们通常连接着处于同一大洲或同一地域范围的多个国家；广域网一般指连接一个国家的各个地区的网络，分布距离一般在100到1000公里之间；城域网又称都市网，它的覆盖范围一般为一个城市，方圆不超过10到几十公里；局域网的地理分布则相对较小，例如建筑物或一般学校等。<br />　　计算机网络的传输速率通常以每秒钟传输的数据位来计算。从传输速度看，局域网的通信率是最高的，例如以太网的速率一般为10Mbps，快速以太网为 100Mbps；城域网的速度低于局域；网际网的速度则相对更低。通信线路及联人网络的计算机结合在一起的总体布局模型称为网络拓朴。按网络拓朴划分，计算机网络可分为总线网络、环网、星形网和混合网络等。局域网中最常用的以太网是典型的总线网络。（返回）<br />什么是Internet<br /><br />　　Internet是一个由各种不同类型和规模的独立运行与管理的计算机网络组成的全球范围的计算机网络。组成Internet的计算机网络包括局域网（LAN)、城域网（MAN)以及大规模的广域网（WAN)等。这些网络通过普通电话、高速率专用线路、卫星、微波和光缆等通讯线路把不同国家的大学、公司、科研机构以及军事和政治等组织的网络连接起来。Internet网络互连采用的基本协议是TCP/IP。（返回）<br />什么是Intranet<br /><br />　　Intranet指采用Internet技术建立的企业内部专用网络。它以TCP/IP协议作为基础，以Web为核心应用，构成统一和便利的信息交换平台。（返回）<br />什么是TCP/IP<br /><br />　　TCP/IP（传输控制协议/网间协议）是一种网络通信协议，它规范了网络上的所有通信设备，尤其是一个主机与另一个主机之间的数据往来格式以及传送方式。TCP/IP是INTERNET的基础协议，也是一种电脑数据打包和寻址的标准方法。在数据传送中，可以形象地理解为有两个信封，TCP和IP就像是信封，要传递的信息被划分成若干段，每一段塞入一个TCP信封，并在该信封面上记录有分段号的信息，再将TCP信封塞入IP大信封，发送上网。在接受端，一个TCP软件包收集信封，抽出数据，按发送前的顺序还原，并加以校验，若发现差错，TCP将会要求重发。因此，TCP/IP在INTERNET中几乎可以无差错地传送数据。对普通用户来说，并不需要了解网络协议的整个结构，仅需了解IP的地址格式，即可与世界各地进行网络通信。（返回）<br />什么是IP地址<br /><br />　　为了使Internet上的众多电脑主机在通信时能够相互识别，Internet上的每一台主机都分配有一个唯一的32位地址，该地址称为IP地址，也称作网际地址。IP地址由4个数组成，每个数可取值0～255，各数之间用一个点号“.”分开（如：202.103.8.46）。事实上，每个IP地址都是由网络号和主机号两部分组成的。网络号表明主机所联接的网络，主机号标识了该网络上特定的那台主机。如：上例中202.103是网络号，8.46是主机号。（返回）<br />什么是域名<br /><br />　　IP地址是以数字来代表的主机地址，比较难记。为了使用和记忆方便，也为了便于网络地址的分层管理和分配，INTERNET在1984年采用了域名管理系统（DOMAIN NAME SYSTEM），入网的每台主机都具有类似于下列结构的域名：主机号.机构名.网络名.最高层域名 。域名用一组简短的英文表达，比用数字表达的IP地址容易记忆。例如：北京电报局的一台与INTERNET联网的电脑主机的IP地址是 202.96.0.97，域名为PUBLIC.BTA.NET.CN。加入INTERNET的各级网络依照域名管理系统的命名规则对本网内的主机命名和分配网内主机号，并负责完成通信时域名到IP地址的转换。对使用者来说，我们一般不需要使用IP地址，而直接使用域名，INTERNET上的服务系统自动地转为IP类型的地址。（返回）<br />什么是HTML<br /><br />　　HTML（HyperTextMark-upLanguage）即超文本标记语言，是WWW的描述语言。设计HTML语言的目的是为了能把存放在一台电脑中的文本或图形与另一台电脑中的文本或图形方便地联系在一起，形成有机的整体，人们不用考虑具体信息是在当前电脑上还是在网络的其它电脑上。我们只需使用鼠标在某一文档中点取一个图标，Internet就会马上转到与此图标相关的内容上去，而这些信息可能存放在网络的另一台电脑中。HTML文本是由 HTML命令组成的描述性文本，HTML命令可以说明文字、图形、动画、声音、表格、链接等。HTML的结构包括头部（Head）、主体（Body）两大部分，其中头部描述浏览器所需的信息，而主体则包含所要说明的具体内容。（返回）<br />什么是HomePage（主页）<br /><br />　　HomePage是一种用超文本标记语言（描述性语言），由它将信息组织好，再经过相应的解释器或浏览器翻译出的包括文字、图象、声音、动画等多种信息组织方式。（返回）<br />什么是JAVA<br /><br />　　Java是Sun公司推出的一种编程语言。它是一种通过解释方式来执行的语言，语法规则和C++类似。同时，Java也是一种跨平台的程序设计语言。用 Java语言编写的程序叫做“Applet”（小应用程序），用编译器将它编译成类文件后，将它存在WWW页面中，并在HTML档上作好相应标记，用户端只要装上Java的客户软件就可以在网上直接运行“Applet”。Java非常适合于企业网络和Internet环境，现在已成为Internet中最受欢迎、最有影响的编程语言之一。 Java有许多值得称道的优点，如简单、面向对象、分布式、解释性、可靠、安全、结构中立性、可移植性、高性能、多线程、动态性等。Java摈弃了C++ 中各种弊大于利的功能和许多很少用到的功能。Jave可以运行与任何微处理器，用Java开发的程序可以在网络上传输，并运行于任何客户机上。 （返回）<br />什么是WWW<br /><br />　　WWW（World Wide Web）是一种建立在Internet上的全球性、交互性的动态、多平台、分布式、图形信息系统，它只是建立在Internet上的一种网络服务。其最初开发的目的是为了在科学家之间共享成果，科学家们可以将科研成果以图文形式在网上进行共享，它最基本的概念就是Hypertext（超文本）。现在WWW 的应用已远远超出了原设想，成为Internet上最受欢迎的应用之一，它的出现极大地推动了Internet的推广。（返回）<br />什么是Browser（浏览器）<br /><br />　　WWW服务和Gopher服务是通过客户端程序访问的，这种程序被称为Browser（浏览器),　因为它允许用户根据超文本链接(HyperText 　Link）进行漫游，而不必进行有目的的查询。浏览器鼓励偶然的发现，并因此而得名。Windows系统自带的浏览器为Microsoft(微软)公司的Internet Explorer。（返回）<br />什么是EMAIL<br /><br />　　Email是Internet上的重要信息服务方式，它为世界各地的Internet用户提供了一种极为快速、简便和经济的通信以及交换信息的方法。Email地址是以域为基础的地址，Email的传递是一个标准化的简单邮件传输协议SMTP来完成的（返回）。<br />什么是计算机病毒<br /><br />　　计算机病毒，是指编制或者在计算机程序中插入的破坏计算机功能或者毁坏数据，影响计算机使用，并能自我复制的一组计算机指令或者程序代码。（返回）<br />什么是计算机木马<br /><br />　　全称“特洛伊木马(Trojan Horse)”，原指古希腊士兵藏在木马内进入敌方城市从而占领敌方城市的故事。在Internet上，“特洛伊木马”指的是一种经过伪装的欺骗性程序，它通过将自身伪装吸引用户下载执行，从而破坏或窃取使用者的重要文件和资料。（返回）<br />什么是Domain(域)<br /><br />　　Domain(域)指某一类Internet主机。一些重要的域包括：.COM(商业机构)，.EDU(教育org组织机构)等，.GOV(政府部门)，和.MIL(军事机构)。大部分国家也拥有自己独立的域，例如　.US(美国)，.UK((英国)，.CN(中国)，.JP(日本)等等。（返回）<br />什么是Host（主机）<br /><br />　　Host（主机）是在Internet上具有固定IP地址的计算机，在其上建立服务器端软件后可以向远程Internet用户发布信息或传送数据.（返回）<br />什么是DNS<br />　　域名管理系统DNS（Domain Name System）是域名解析服务器的意思.它在互联网的作用是：把域名转换成为网络可以识别的ip地址。比如：我们上网时输入的www.sohu.com会自动转换成为61.135.132.6 （返回）<br />什么是FTP(文件传输)<br /><br />　　文件传输协议FTP(File Transfer Protocol)是Internet传统的服务之一。FTP使用户能在两个联网的计算机之间传输文件，它是Internet传递文件最主要的方法。使用匿名(Anonymous)FTP， 用户可以免费获取Internet丰富的资源。除此之外，FTP还提供登录、目录查询、文件操作及其他会话控制功能。（返回）<br />什么是URL<br /><br />　　URL（Uniform Resoure Locator：统一资源定位器）是WWW页的地址，它从左到右由：<br />　　Internet资源类型（scheme）：指出WWW客户程序用来操作的工具。如“http：//”表示WWW服务器，“ftp：//”表示FTP服务器。<br />　　服务器地址（host）：指出WWW页所在的服务器域名。<br />　　端口（port）：对某些资源的访问来说，需给出相应的服务器提供端口号。<br />　　路径（path）：指明服务器上某资源的位置，通常有目录/子目录/文件名这样结构组成。<br />　　URL地址格式排列为：scheme：//host：port/path，例如http：//www.sohu.com/domain/HXWZ就是一个典型的URL地址。其中PORT和PATH部分并不总是需要的。（返回）<br />什么是Search Engine(搜索引擎)<br /><br />　　Search Engine(搜索引擎)是WWW环境中的信息检索系统，是使用World Wide Web查找信息不可缺少的工具，有Internet导航台之称，它包括目录服务和关键字检索两种服务方式。（返回）<br />什么是Download(下载)<br /><br />　　Download (下载)是指从远程主机上拷贝文字,图片，声音等信息或者软件到本地硬盘上。（返回）<br />什么是垃圾邮件<br /><br />　　垃圾邮件是指与内容无关，而且收件人并没有明确要求接受该邮件的发送给多个收件人的信件或张贴物。也可以是发送给与信件主题不相关的新闻组或者列表服务器的同一信件的重复张贴物。在因特网上的其它常见词汇是UCE（Unsolicited Commercial Email，不请自来的商业电子邮件）和 UBE（Unsolicited Bulk Email，不请自来的批量电子邮件），他们与通常所称的垃圾邮件是一样的。（返回）<br />什么是Internet Phone（Internet电话）<br /><br />　　Internet Phone（Internet电话）指通过 Internet传输交互语音的应用和软件，它可以从数量级上减少长途电话尤其是国际长途电话的费用。这种技术最早由一家以色列电子公司VocalTec 推出。目前最先进的　　Internet电话已经在话音质量，话音数据完整性，响应延迟和使用方便性等方面接近实用水平。（返回）<br />什么是Telnet(Telnet远程登录)<br /><br />　　Telnet是进行远程登录的标准协议和主要方式它为用户提供了在本地计算机上完成远程主机工作的能力。通过使用Telnet,Internet用户可以与全世界许多信息中心图书馆及其它信息资源联系。Telnet远程登录的使用主要有两种情况。第一种是用户在远程主祝上有自己的帐号 (Account)，即用户拥有注册的用户名和口令；第二种是许多Internet主机为用户提供了某种形式的公共Telnet信息资源，这种资源对于每一个Telnet用户都是开放的。（返回）<br />什么是Firework（防火墙）<br /><br />　　Firework(防火墙)是加强加Internet 与 Intranet（内部网）之间安全防范的一个或一组系统。防火墙可以确定哪些内部服务允许外部访问，哪些外人被许可访问所允许的内部服务，田些外部服务可由内部人员访问。为了使防火墙发挥效力，来自和发往Internet的所有信息都必须经由防火墙出入。防火墙只允许授权信息通过，而防火墙本身不能被渗透。（返回）<br />什么是Gateway（网关）<br /><br />　　Gateway(网关)以及Gatebridge(网桥)均指不同网络之间的通讯接口设备和程序，只不过两者针对的网络层次不同。现在Internet技术上越来越少用这两个词汇，而由Router(路由器)统指此类接口设备和程序。（返回）<br />Arp<br /><br />显示和修改“地址解析协议”(ARP) 所使用的到以太网的 IP 或令牌环物理地址翻译表。该命令只有在安装了 TCP/IP 协议之后才可用。<br />arp -a [inet_addr] [-N [if_addr]]<br />arp -d inet_addr [if_addr]<br />arp -s inet_addr ether_addr [if_addr]<br />参数<br />-a 通过询问 TCP/IP 显示当前 ARP 项。如果指定了 inet_addr，则只显示指定计算机的 IP 和物理地址。<br />-g 与 -a 相同。<br />inet_addr 以加点的十进制标记指定 IP 地址。<br />-N 显示由 if_addr 指定的网络界面 ARP 项。<br />if_addr 指定需要修改其地址转换表接口的 IP 地址（如果有的话）。如果不存在，将使用第一个可适用的接口。<br />-d 删除由 inet_addr 指定的项。<br />-s 在 ARP 缓存中添加项，将 IP 地址 inet_addr 和物理地址 ether_addr 关联。物理地址由以连字符分隔的 6 个十六进制字节给定。<br />使用带点的十进制标记指定 IP 地址。项是永久性的，即在超时到期后项自动从缓存删除。（返回）<br />ether_addr 指定物理地址。<br />Finger<br /><br />在运行 Finger 服务的指定系统上显示有关用户的信息。根据远程系统输出不同的变量。该命令只有在安装了 TCP/IP 协议之后才可用。<br />finger [-l] [user]@computer[...]<br />参数<br />-l 以长列表格式显示信息。<br />user 指定要获得相关信息的用户。省略用户参数以显示指定计算机@computer上所有用户的信息（返回）<br />Ftp<br /><br />将文件传送到正在运行 FTP 服务的远程计算机或从正在运行 FTP 服务的远程计算机传送文件（有时称作 daemon）。Ftp 可以交互使用。单击“相关主题”列表中的“ftp 命令”以获得可用的“ftp”子命令描述。该命令只有在安装了 TCP/IP 协议之后才可用。Ftp 是一种服务，一旦启动，将创建在其中可以使用 ftp 命令的子环境，通过键入 quit 子命令可以从子环境返回到 Windows 2000 命令提示符。当 ftp 子环境运行时，它由 ftp 命令提示符代表。<br />ftp [-v] [-n] [-i] [-d] [-g] [-s:filename] [-a] [-w:windowsize] [computer]<br />参数<br />-v 禁止显示远程服务器响应。<br />-n 禁止自动登录到初始连接。<br />-i 多个文件传送时关闭交互提示。<br />-d 启用调试、显示在客户端和服务器之间传递的所有 ftp 命令。<br />-g 禁用文件名组，它允许在本地文件和路径名中使用通配符字符（* 和 ?）。（请参阅联机“命令参考”中的 glob 命令。）<br />-s: filename 指定包含 ftp 命令的文本文件；当 ftp 启动后，这些命令将自动运行。该参数中不允许有空格。使用该开关而不是重定向 (&gt; )。<br />-a 在捆绑数据连接时使用任何本地接口。<br />-w:windowsize 替代默认大小为 4096 的传送缓冲区。<br />computer 指定要连接到远程计算机的计算机名或 IP 地址。如果指定，计算机必须是行的最后一个参数。（返回）<br />Nbtstat<br /><br />该诊断命令使用 NBT（TCP/IP 上的 NetBIOS）显示协议统计和当前 TCP/IP 连接。该命令只有在安装了 TCP/IP 协议之后才可用。<br />nbtstat [-a remotename] [-A IP address] [-c] [-n] [-R] [-r] [-S] [-s] [interval]<br />参数<br />-a remotename 使用远程计算机的名称列出其名称表。<br />-A IP address 使用远程计算机的 IP 地址并列出名称表。<br />-c 给定每个名称的 IP 地址并列出 NetBIOS 名称缓存的内容。<br />-n 列出本地 NetBIOS 名称。“已注册”表明该名称已被广播 (Bnode) 或者 WINS（其他节点类型）注册。<br />-R 清除 NetBIOS 名称缓存中的所有名称后，重新装入 Lmhosts 文件。<br />-r 列出 Windows 网络名称解析的名称解析统计。在配置使用 WINS 的 Windows 2000 计算机上，此选项返回要通过广播或 WINS 来解析和注册的名称数。<br />-S 显示客户端和服务器会话，只通过 IP 地址列出远程计算机。<br />-s 显示客户端和服务器会话。尝试将远程计算机 IP 地址转换成使用主机文件的名称。<br />interval 重新显示选中的统计，在每个显示之间暂停 interval 秒。按 CTRL+C 停止重新显示统计信息。如果省略该参数，nbtstat 打印一次当前的配置信息。（返回）<br />Netstat<br /><br />显示协议统计和当前的 TCP/IP 网络连接。该命令只有在安装了 TCP/IP 协议后才可以使用。<br />netstat [-a] [-e] [-n] [-s] [-p protocol] [-r] [interval]<br />参数<br />-a 显示所有连接和侦听端口。服务器连接通常不显示。<br />-e 显示以太网统计。该参数可以与 -s 选项结合使用。<br />-n 以数字格式显示地址和端口号（而不是尝试查找名称）。<br />-s 显示每个协议的统计。默认情况下，显示 TCP、UDP、ICMP 和 IP 的统计。-p 选项可以用来指定默认的子集。<br />-p protocol 显示由 protocol 指定的协议的连接；protocol 可以是 tcp 或 udp。如果与 -s 选项一同使用显示每个协议的统计，protocol 可以是 tcp、udp、icmp 或 ip。<br />-r 显示路由表的内容。<br />interval 重新显示所选的统计，在每次显示之间暂停 interval 秒。按 CTRL+B 停止重新显示统计。如果省略该参数，netstat 将打印一次当前的配置信息。（返回）<br />Ping<br /><br />验证与远程计算机的连接。该命令只有在安装了 TCP/IP 协议后才可以使用。<br />ping [-t] [-a] [-n count] [-l length] [-f] [-i ttl] [-v tos] [-r count] [-s count] [[-j computer-list] | [-k computer-list]] [-w timeout] destination-list<br />参数<br />-t Ping 指定的计算机直到中断。<br />-a 将地址解析为计算机名。<br />-n count 发送 count 指定的 ECHO 数据包数。默认值为 4。<br />-l length 发送包含由 length 指定的数据量的 ECHO 数据包。默认为 32 字节；最大值是 65,527。<br />-f 在数据包中发送“不要分段”标志。数据包就不会被路由上的网关分段。<br />-i ttl 将“生存时间”字段设置为 ttl 指定的值。<br />-v tos 将“服务类型”字段设置为 tos 指定的值。<br />-r count 在“记录路由”字段中记录传出和返回数据包的路由。count 可以指定最少 1 台，最多 9 台计算机。<br />-s count 指定 count 指定的跃点数的时间戳。<br />-j computer-list 利用 computer-list 指定的计算机列表路由数据包。连续计算机可以被中间网关分隔（路由稀疏源）IP 允许的最大数量为 9。<br />-k computer-list 利用 computer-list 指定的计算机列表路由数据包。连续计算机不能被中间网关分隔（路由严格源）IP 允许的最大数量为 9。<br />-w timeout 指定超时间隔，单位为毫秒。<br />destination-list 指定要 ping 的远程计算机。（返回）<br />Route<br /><br />控制网络路由表。该命令只有在安装了 TCP/IP 协议后才可以使用。<br />route [-f] [-p] [command [destination] [mask subnetmask] [gateway] [metric costmetric]]<br />参数<br />-f 清除所有网关入口的路由表。如果该参数与某个命令组合使用，路由表将在运行命令前清除。<br />-p 该参数与 add 命令一起使用时，将使路由在系统引导程序之间持久存在。默认情况下，系统重新启动时不保留路由。与 print 命令一起使用时，显示已注册的持久路由列表。忽略其他所有总是影响相应持久路由的命令。<br />command 指定下列的一个命令。 print 打印路由 ;add 添加路由 ;delete 删除路由 ;change 更改现存路由<br />destination 指定发送 command 的计算机。<br />mask subnetmask 指定与该路由条目关联的子网掩码。如果没有指定，将使用 255.255.255.255。<br />gateway 指定网关<br />名为 Networks 的网络数据库文件和名为 Hosts 的计算机名数据库文件中均引用全部 destination 或 gateway 使用的符号名称。如果命令是 print 或 delete，目标和网关还可以使用通配符，也可以省略网关参数。<br />metric costmetric 指派整数跃点数（从 1 到 9999）在计算最快速、最可靠和（或）最便宜的路由时使用。（返回）<br />Tftp<br /><br />将文件传输到正在运行 TFTP 服务的远程计算机或从正在运行 TFTP 服务的远程计算机传输文件。该命令只有在安装了 TCP/IP 协议后才可以使用。<br />tftp [-i] computer [get | put] source [destination]<br />参数<br />-i 指定二进制图像传送模式（也称为“八位字节”）。在二进制图像模式中，文件一个字节接一个字节地逐字移动。在传送二进制文件时使用该模式。如果省略了 -i，文件将以 ASCII 模式传送。这是默认的传送模式。此模式将 EOL 字符转换为 UNIX 的回车符和个人计算机的回车符/换行符。在传送文本文件时应使用此模式。如果文件传送成功，将显示数据传输率。<br />computer 指定本地或远程计算机。<br />put 将本地计算机上的文件 destination 传送到远程计算机上的文件 source。<br />get 将远程计算机上的文件 destination 传送到本地计算机上的文件 source。<br />如果将本地计算机上的文件 file-two 传送到远程计算机上的文件 file-one，请指定 put。如果将远程计算机上的文件 file-two 传送到远程计算机上的文件 file-one，请指定 get。 因为 tftp 协议不支持用户身份验证，所以用户必须登录，并且文件在远程计算机上必须可以写入。<br />source 指定要传送的文件。如果本地文件指定为 -，则远程文件在 stdout 上打印出来（如果获取），或从 stdin（如果放置）读取。<br />destination 指定将文件传送到的位置。如果省略了 destination，将假定与 source 同名。（返回）<br />Tracert<br /><br />该诊断实用程序将包含不同生存时间 (TTL) 值的 Internet 控制消息协议 (ICMP) 回显数据包发送到目标，以决定到达目标采用的路由。要在转发数据包上的 TTL 之前至少递减 1，必需路径上的每个路由器，所以 TTL 是有效的跃点计数。数据包上的 TTL 到达 0 时，路由器应该将“ICMP 已超时”的消息发送回源系统。Tracert 先发送 TTL 为 1 的回显数据包,并在随后的每次发送过程将 TTL 递增 1，直到目标响应或 TTL 达到最大值，从而确定路由。路由通过检查中级路由器发送回的“ICMP 已超时”的消息来确定路由。不过，有些路由器悄悄地下传包含过期 TTL 值的数据包，而 tracert 看不到。<br />tracert [-d] [-h maximum_hops] [-j computer-list] [-w timeout] target_name<br />参数<br />/d 指定不将地址解析为计算机名。<br />-h maximum_hops 指定搜索目标的最大跃点数。<br />-j computer-list 指定沿 computer-list 的稀疏源路由。<br />-w timeout 每次应答等待 timeout 指定的微秒数。<br />target_name 目标计算机的名称。（返回）',1,1,1,0,0,0,'术语解释：计算机网络　Internet　Intranet　TCP/IP　IP地址　网关　DOMAIN　主机　域名　DNS　WWW　EMAIL　FTP　Telnet　URL　HTML　主页　浏览器　搜索引擎　下载　计算机病毒　计算机木马　垃圾邮件　防火墙　JAVA　网络电话常用网络命令: arp　finger　ftp　nbtstat　netstat　ping　route　tftp　tracert什么是计算机网络　　计算机网络，它是指利用通信线路连接起来的、相互独立的计算机的集合。这包含两个概念：首先，这些计算机之间必须存在通信线路；其次，这些计算机必须相互独立。从这个定义出发，传统的主机/终端式系统就不能算是严格意义上的计算机网络。　　按地理位置划分，计算机网络可分为四种：网际网、广域网(WAN)、城域网(MAN)和局域网(LAN)。 Internet就是世界上最大的网际网，另外还存在其他网际网类型，它们通常连接着处于同一大洲或同一地域范围的多个国家；广域网一般指连接一个国家的各个地区的网络，分布距离一般在100到1000公里之间；城域网又称都市网，它的覆盖范围一般为一个城市，方圆不超过10到几十公里；局域网的地理分布则相对较小，例如建筑物或一般学校等。　　计算机网络的传输速率通常以每秒钟传输的数据位来计算。从传输速度看，局域网的通信率是最高的，例如以太网的速率一般为10Mbps，快速以太网为 100Mbps；城域网的速度低于局域；网际网的速度则相对更低。通信线路及联人网络的计算机结合在一起的总体布局模型称为网络拓朴。按网络拓朴划分，计算机网络可分为总线网络、环网、星形网和混合网络等。局域网中最常用的以太网是典型的总线网络。（返回）什么是Internet　　Internet是一个由各种不同类型和规模的独立运行与管理的计算机网络组成的全球范围的计算机网络。组成Internet的计算机网络包括局域网（LAN)、城域网（MAN)以及大规模的广域网（WAN)等。这些网络通过普通电话、高速率专用线路、卫星、微波和光缆等通讯线路把不同国家的大学、公司、科研机构以及军事和政治等组织的网络连接起来。Internet网络互连采用的基本协议是TCP/IP。（返回）什么是Intranet　　Intranet指采用Internet技术建立的企业内部专用网络。它以TCP/IP协议作为基础，以Web为核心应用，构成统一和便利的信息交换平台。（返回）什么是TCP/IP　　TCP/IP（传输控制协议/网间协议）是一种网络通信协议，它规范了网络上的所有通信设备，尤其是一个主机与另一个主机之间的数据往来格式以及传送方式。TCP/IP是INTERNET的基础协议，也是一种电脑数据打包和寻址的标准方法。在数据传送中，可以形象地理解为有两个信封，TCP和IP就像是信封，要传递的信息被划分成若干段，每一段塞入一个TCP信封，并在该信封面上记录有分段号的信息，再将TCP信封塞入IP大信封，发送上网。在接受端，一个TCP软件包收集信封，抽出数据，按发送前的顺序还原，并加以校验，若发现差错，TCP将会要求重发。因此，TCP/IP在INTERNET中几乎可以无差错地传送数据。对普通用户来说，并不需要了解网络协议的整个结构，仅需了解IP的地址格式，即可与世界各地进行网络通信。（返回）什么是IP地址　　为了使Internet上的众多电脑主机在通信时能够相互识别，Internet上的每一台主机都分配有一个唯一的32位地址，该地址称为IP地址，也称作网际地址。IP地址由4个数组成，每个数可取值0～255，各数之间用一个点号“.”分开（如：202.103.8.46）。事实上，每个IP地址都是由网络号和主机号两部分组成的。网络号表明主机所联接的网络，主机号标识了该网络上特定的那台主机。如：上例中202.103是网络号，8.46是主机号。（返回）什么是域名　　IP地址是以数字来代表的主机地址，比较难记。为了使用和记忆方便，也为了便于网络地址的分层管理和分配，INTERNET在1984年采用了域名管理系统（DOMAIN NAME SYSTEM），入网的每台主机都具有类似于下列结构的域名：主机号.机构名.网络名.最高层域名 。域名用一组简短的英文表达，比用数字表达的IP地址容易记忆。例如：北京电报局的一台与INTERNET联网的电脑主机的IP地址是 202.96.0.97，域名为PUBLIC.BTA.NET.CN。加入INTERNET的各级网络依照域名管理系统的命名规则对本网内的主机命名和分配网内主机号，并负责完成通信时域名到IP地址的转换。对使用者来说，我们一般不需要使用IP地址，而直接使用域名，INTERNET上的服务系统自动地转为IP类型的地址。（返回）什么是HTML　　HTML（HyperTextMark-upLanguage）即超文本标记语言，是WWW的描述语言。设计HTML语言的目的是为了能把存放在一台电脑中的文本或图形与另一台电脑中的文本或图形方便地联系在一起，形成有机的整体，人们不用考虑具体信息是在当前电脑上还是在网络的其它电脑上。我们只需使用鼠标在某一文档中点取一个图标，Internet就会马上转到与此图标相关的内容上去，而这些信息可能存放在网络的另一台电脑中。HTML文本是由 HTML命令组成的描述性文本，HTML命令可以说明文字、图形、动画、声音、表格、链接等。HTML的结构包括头部（Head）、主体（Body）两大部分，其中头部描述浏览器所需的信息，而主体则包含所要说明的具体内容。（返回）什么是HomePage（主页）　　HomePage是一种用超文本标记语言（描述性语言），由它将信息组织好，再经过相应的解释器或浏览器翻译出的包括文字、图象、声音、动画等多种信息组织方式。（返回）什么是JAVA　　Java是Sun公司推出的一种编程语言。它是一种通过解释方式来执行的语言，语法规则和C++类似。同时，Java也是一种跨平台的程序设计语言。用 Java语言编写的程序叫做“Applet”（小应用程序），用编译器将它编译成类文件后，将它存在WWW页面中，并在HTML档上作好相应标记，用户端只要装上Java的客户软件就可以在网上直接运行“Applet”。Java非常适合于企业网络和Internet环境，现在已成为Internet中最受欢迎、最有影响的编程语言之一。 Java有许多值得称道的优点，如简单、面向对象、分布式、解释性、可靠、安全、结构中立性、可移植性、高性能、多线程、动态性等。Java摈弃了C++ 中各种弊大于利的功能和许多很少用到的功能。Jave可以运行与任何微处理器，用Java开发的程序可以在网络上传输，并运行于任何客户机上。 （返回）什么是WWW　　WWW（World Wide Web）是一种建立在Internet上的全球性、交互性的动态、多平台、分布式、图形信息系统，它只是建立在Internet上的一种网络服务。其最初开发的目的是为了在科学家之间共享成果，科学家们可以将科研成果以图文形式在网上进行共享，它最基本的概念就是Hypertext（超文本）。现在WWW 的应用已远远超出了原设想，成为Internet上最受欢迎的应用之一，它的出现极大地推动了Internet的推广。（返回）什么是Browser（浏览器）　　WWW服务和Gopher服务是通过客户端程序访问的，这种程序被称为Browser（浏览器),　因为它允许用户根据超文本链接(HyperText 　Link）进行漫游，而不必进行有目的的查询。浏览器鼓励偶然的发现，并因此而得名。Windows系统自带的浏览器为Microsoft(微软)公司的Internet Explorer。（返回）什么是EMAIL　　Email是Internet上的重要信息服务方式，它为世界各地的Internet用户提供了一种极为快速、简便和经济的通信以及交换信息的方法。Email地址是以域为基础的地址，Email的传递是一个标准化的简单邮件传输协议SMTP来完成的（返回）。什么是计算机病毒　　计算机病毒，是指编制或者在计算机程序中插入的破坏计算机功能或者毁坏数据，影响计算机使用，并能自我复制的一组计算机指令或者程序代码。（返回）什么是计算机木马　　全称“特洛伊木马(Trojan Horse)”，原指古希腊士兵藏在木马内进入敌方城市从而占领敌方城市的故事。在Internet上，“特洛伊木马”指的是一种经过伪装的欺骗性程序，它通过将自身伪装吸引用户下载执行，从而破坏或窃取使用者的重要文件和资料。（返回）什么是Domain(域)　　Domain(域)指某一类Internet主机。一些重要的域包括：.COM(商业机构)，.EDU(教育org组织机构)等，.GOV(政府部门)，和.MIL(军事机构)。大部分国家也拥有自己独立的域，例如　.US(美国)，.UK((英国)，.CN(中国)，.JP(日本)等等。（返回）什么是Host（主机）　　Host（主机）是在Internet上具有固定IP地址的计算机，在其上建立服务器端软件后可以向远程Internet用户发布信息或传送数据.（返回）什么是DNS　　域名管理系统DNS（Domain Name System）是域名解析服务器的意思.它在互联网的作用是：把域名转换成为网络可以识别的ip地址。比如：我们上网时输入的www.sohu.com会自动转换成为61.135.132.6 （返回）什么是FTP(文件传输)　　文件传输协议FTP(File Transfer Protocol)是Internet传统的服务之一。FTP使用户能在两个联网的计算机之间传输文件，它是Internet传递文件最主要的方法。使用匿名(Anonymous)FTP， 用户可以免费获取Internet丰富的资源。除此之外，FTP还提供登录、目录查询、文件操作及其他会话控制功能。（返回）什么是URL　　URL（Uniform Resoure Locator：统一资源定位器）是WWW页的地址，它从左到右由：　　Internet资源类型（scheme）：指出WWW客户程序用来操作的工具。如“http：//”表示WWW服务器，“ftp：//”表示FTP服务器。　　服务器地址（host）：指出WWW页所在的服务器域名。　　端口（port）：对某些资源的访问来说，需给出相应的服务器提供端口号。　　路径（path）：指明服务器上某资源的位置，通常有目录/子目录/文件名这样结构组成。　　URL地址格式排列为：scheme：//host：port/path，例如http：//www.sohu.com/domain/HXWZ就是一个典型的URL地址。其中PORT和PATH部分并不总是需要的。（返回）什么是Search Engine(搜索引擎)　　Search Engine(搜索引擎)是WWW环境中的信息检索系统，是使用World Wide Web查找信息不可缺少的工具，有Internet导航台之称，它包括目录服务和关键字检索两种服务方式。（返回）什么是Download(下载)　　Download (下载)是指从远程主机上拷贝文字,图片，声音等信息或者软件到本地硬盘上。（返回）什么是垃圾邮件　　垃圾邮件是指与内容无关，而且收件人并没有明确要求接受该邮件的发送给多个收件人的信件或张贴物。也可以是发送给与信件主题不相关的新闻组或者列表服务器的同一信件的重复张贴物。在因特网上的其它常见词汇是UCE（Unsolicited Commercial Email，不请自来的商业电子邮件）和 UBE（Unsolicited Bulk Email，不请自来的批量电子邮件），他们与通常所称的垃圾邮件是一样的。（返回）什么是Internet Phone（Internet电话）　　Internet Phone（Internet电话）指通过 Internet传输交互语音的应用和软件，它可以从数量级上减少长途电话尤其是国际长途电话的费用。这种技术最早由一家以色列电子公司VocalTec 推出。目前最先进的　　Internet电话已经在话音质量，话音数据完整性，响应延迟和使用方便性等方面接近实用水平。（返回）什么是Telnet(Telnet远程登录)　　Telnet是进行远程登录的标准协议和主要方式它为用户提供了在本地计算机上完成远程主机工作的能力。通过使用Telnet,Internet用户可以与全世界许多信息中心图书馆及其它信息资源联系。Telnet远程登录的使用主要有两种情况。第一种是用户在远程主祝上有自己的帐号 (Account)，即用户拥有注册的用户名和口令；第二种是许多Internet主机为用户提供了某种形式的公共Telnet信息资源，这种资源对于每一个Telnet用户都是开放的。（返回）什么是Firework（防火墙）　　Firework(防火墙)是加强加Internet 与 Intranet（内部网）之间安全防范的一个或一组系统。防火墙可以确定哪些内部服务允许外部访问，哪些外人被许可访问所允许的内部服务，田些外部服务可由内部人员访问。为了使防火墙发挥效力，来自和发往Internet的所有信息都必须经由防火墙出入。防火墙只允许授权信息通过，而防火墙本身不能被渗透。（返回）什么是Gateway（网关）　　Gateway(网关)以及Gatebridge(网桥)均指不同网络之间的通讯接口设备和程序，只不过两者针对的网络层次不同。现在Internet技术上越来越少用这两个词汇，而由Router(路由器)统指此类接口设备和程序。（返回）Arp显示和修改“地址解析协议”(ARP) 所使用的到以太网的 IP 或令牌环物理地址翻译表。该命令只有在安装了 TCP/IP 协议之后才可用。arp -a [inet_addr] [-N [if_addr]]arp -d inet_addr [if_addr]arp -s inet_addr ether_addr [if_addr]参数-a 通过询问 TCP/IP 显示当前 ARP 项。如果指定了 inet_addr，则只显示指定计算机的 IP 和物理地址。-g 与 -a 相同。inet_addr 以加点的十进制标记指定 IP 地址。-N 显示由 if_addr 指定的网络界面 ARP 项。if_addr 指定需要修改其地址转换表接口的 IP 地址（如果有的话）。如果不存在，将使用第一个可适用的接口。-d 删除由 inet_addr 指定的项。-s 在 ARP 缓存中添加项，将 IP 地址 inet_addr 和物理地址 ether_addr 关联。物理地址由以连字符分隔的 6 个十六进制字节给定。使用带点的十进制标记指定 IP 地址。项是永久性的，即在超时到期后项自动从缓存删除。（返回）ether_addr 指定物理地址。Finger在运行 Finger 服务的指定系统上显示有关用户的信息。根据远程系统输出不同的变量。该命令只有在安装了 TCP/IP 协议之后才可用。finger [-l] [user]@computer[...]参数-l 以长列表格式显示信息。user 指定要获得相关信息的用户。省略用户参数以显示指定计算机@computer上所有用户的信息（返回）Ftp将文件传送到正在运行 FTP 服务的远程计算机或从正在运行 FTP 服务的远程计算机传送文件（有时称作 daemon）。Ftp 可以交互使用。单击“相关主题”列表中的“ftp 命令”以获得可用的“ftp”子命令描述。该命令只有在安装了 TCP/IP 协议之后才可用。Ftp 是一种服务，一旦启动，将创建在其中可以使用 ftp 命令的子环境，通过键入 quit 子命令可以从子环境返回到 Windows 2000 命令提示符。当 ftp 子环境运行时，它由 ftp 命令提示符代表。ftp [-v] [-n] [-i] [-d] [-g] [-s:filename] [-a] [-w:windowsize] [computer]参数-v 禁止显示远程服务器响应。-n 禁止自动登录到初始连接。-i 多个文件传送时关闭交互提示。-d 启用调试、显示在客户端和服务器之间传递的所有 ftp 命令。-g 禁用文件名组，它允许在本地文件和路径名中使用通配符字符（* 和 ?）。（请参阅联机“命令参考”中的 glob 命令。）-s: filename 指定包含 ftp 命令的文本文件；当 ftp 启动后，这些命令将自动运行。该参数中不允许有空格。使用该开关而不是重定向 (&gt; )。-a 在捆绑数据连接时使用任何本地接口。-w:windowsize 替代默认大小为 4096 的传送缓冲区。computer 指定要连接到远程计算机的计算机名或 IP 地址。如果指定，计算机必须是行的最后一个参数。（返回）Nbtstat该诊断命令使用 NBT（TCP/IP 上的 NetBIOS）显示协议统计和当前 TCP/IP 连接。该命令只有在安装了 TCP/IP 协议之后才可用。nbtstat [-a remotename] [-A IP address] [-c] [-n] [-R] [-r] [-S] [-s] [interval]参数-a remotename 使用远程计算机的名称列出其名称表。-A IP address 使用远程计算机的 IP 地址并列出名称表。-c 给定每个名称的 IP 地址并列出 NetBIOS 名称缓存的内容。-n 列出本地 NetBIOS 名称。“已注册”表明该名称已被广播 (Bnode) 或者 WINS（其他节点类型）注册。-R 清除 NetBIOS 名称缓存中的所有名称后，重新装入 Lmhosts 文件。-r 列出 Windows 网络名称解析的名称解析统计。在配置使用 WINS 的 Windows 2000 计算机上，此选项返回要通过广播或 WINS 来解析和注册的名称数。-S 显示客户端和服务器会话，只通过 IP 地址列出远程计算机。-s 显示客户端和服务器会话。尝试将远程计算机 IP 地址转换成使用主机文件的名称。interval 重新显示选中的统计，在每个显示之间暂停 interval 秒。按 CTRL+C 停止重新显示统计信息。如果省略该参数，nbtstat 打印一次当前的配置信息。（返回）Netstat显示协议统计和当前的 TCP/IP 网络连接。该命令只有在安装了 TCP/IP 协议后才可以使用。netstat [-a] [-e] [-n] [-s] [-p protocol] [-r] [interval]参数-a 显示所有连接和侦听端口。服务器连接通常不显示。-e 显示以太网统计。该参数可以与 -s 选项结合使用。-n 以数字格式显示地址和端口号（而不是尝试查找名称）。-s 显示每个协议的统计。默认情况下，显示 TCP、UDP、ICMP 和 IP 的统计。-p 选项可以用来指定默认的子集。-p protocol 显示由 protocol 指定的协议的连接；protocol 可以是 tcp 或 udp。如果与 -s 选项一同使用显示每个协议的统计，protocol 可以是 tcp、udp、icmp 或 ip。-r 显示路由表的内容。interval 重新显示所选的统计，在每次显示之间暂停 interval 秒。按 CTRL+B 停止重新显示统计。如果省略该参数，netstat 将打印一次当前的配置信息。（返回）Ping验证与远程计算机的连接。该命令只有在安装了 TCP/IP 协议后才可以使用。ping [-t] [-a] [-n count] [-l length] [-f] [-i ttl] [-v tos] [-r count] [-s count] [[-j computer-list] | [-k computer-list]] [-w timeout] destination-list参数-t Ping 指定的计算机直到中断。-a 将地址解析为计算机名。-n count 发送 count 指定的 ECHO 数据包数。默认值为 4。-l length 发送包含由 length 指定的数据量的 ECHO 数据包。默认为 32 字节；最大值是 65,527。-f 在数据包中发送“不要分段”标志。数据包就不会被路由上的网关分段。-i ttl 将“生存时间”字段设置为 ttl 指定的值。-v tos 将“服务类型”字段设置为 tos 指定的值。-r count 在“记录路由”字段中记录传出和返回数据包的路由。count 可以指定最少 1 台，最多 9 台计算机。-s count 指定 count 指定的跃点数的时间戳。-j computer-list 利用 computer-list 指定的计算机列表路由数据包。连续计算机可以被中间网关分隔（路由稀疏源）IP 允许的最大数量为 9。-k computer-list 利用 computer-list 指定的计算机列表路由数据包。连续计算机不能被中间网关分隔（路由严格源）IP 允许的最大数量为 9。-w timeout 指定超时间隔，单位为毫秒。destination-list 指定要 ping 的远程计算机。（返回）Route控制网络路由表。该命令只有在安装了 TCP/IP 协议后才可以使用。route [-f] [-p] [command [destination] [mask subnetmask] [gateway] [metric costmetric]]参数-f 清除所有网关入口的路由表。如果该参数与某个命令组合使用，路由表将在运行命令前清除。-p 该参数与 add 命令一起使用时，将使路由在系统引导程序之间持久存在。默认情况下，系统重新启动时不保留路由。与 print 命令一起使用时，显示已注册的持久路由列表。忽略其他所有总是影响相应持久路由的命令。command 指定下列的一个命令。 print 打印路由 ;add 添加路由 ;delete 删除路由 ;change 更改现存路由destination 指定发送 command 的计算机。mask subnetmask 指定与该路由条目关联的子网掩码。如果没有指定，将使用 255.255.255.255。gateway 指定网关名为 Networks 的网络数据库文件和名为 Hosts 的计算机名数据库文件中均引用全部 destination 或 gateway 使用的符号名称。如果命令是 print 或 delete，目标和网关还可以使用通配符，也可以省略网关参数。metric costmetric 指派整数跃点数（从 1 到 9999）在计算最快速、最可靠和（或）最便宜的路由时使用。（返回）Tftp将文件传输到正在运行 TFTP 服务的远程计算机或从正在运行 TFTP 服务的远程计算机传输文件。该命令只有在安装了 TCP/IP 协议后才可以使用。tftp [-i] computer [get | put] source [destination]参数-i 指定二进制图像传送模式（也称为“八位字节”）。在二进制图像模式中，文件一个字节接一个字节地逐字移动。在传送二进制文件时使用该模式。如果省略了 -i，文件将以 ASCII 模式传送。这是默认的传送模式。此模式将 EOL 字符转换为 UNIX 的回车符和个人计算机的回车符/换行符。在传送文本文件时应使用此模式。如果文件传送成功，将显示数据传输率。computer 指定本地或远程计算机。put 将本地计算机上的文件 destination 传送到远程计算机上的文件 source。get 将远程计算机上的文件 destination 传送到本地计算机上的文件 source。如果将本地计算机上的文件 file-two 传送到远程计算机上的文件 file-one，请指定 put。如果将远程计算机上的文件 file-two 传送到远程计算机上的文件 file-one，请指定 get。 因为 tftp 协议不支持用户身份验证，所以用户必须登录，并且文件在远程计算机上必须可以写入。source 指定要传送的文件。如果本地文件指定为 -，则远程文件在 stdout 上打印出来（如果获取），或从 stdin（如果放置）读取。destination 指定将文件传送到的位置。如果省略了 destination，将假定与 source 同名。（返回）Tracert该诊断实用程序将包含不同生存时间 (TTL) 值的 Internet 控制消息协议 (ICMP) 回显数据包发送到目标，以决定到达目标采用的路由。要在转发数据包上的 TTL 之前至少递减 1，必需路径上的每个路由器，所以 TTL 是有效的跃点计数。数据包上的 TTL 到达 0 时，路由器应该将“ICMP 已超时”的消息发送回源系统。Tracert 先发送 TTL 为 1 的回显数据包,并在随后的每次发送过程将 TTL 递增 1，直到目标响应或 TTL 达到最大值，从而确定路由。路由通过检查中级路由器发送回的“ICMP 已超时”的消息来确定路由。不过，有些路由器悄悄地下传包含过期 TTL 值的数据包，而 tracert 看不到。tracert [-d] [-h maximum_hops] [-j computer-list] [-w timeout] target_name参数/d 指定不将地址解析为计算机名。-h maximum_hops 指定搜索目标的最大跃点数。-j computer-list 指定沿 computer-list 的稀疏源路由。-w timeout 每次应答等待 timeout 指定的微秒数。target_name 目标计算机的名称。（返回）','',0,'network',0,'','zinnia/_entry_detail.html','entry_detail.html'),(211,'REBOL的网络功能','33731937200845101630758',2,'2008-05-05 10:16:30',NULL,'2008-05-05 10:16:30','2014-07-13 18:57:18','<p>Ftp的验证采取<a href=\"ftp://user:pass@url/\" rel=\"nofollow\">ftp://user:pass@url</a>的方式</p> <p>SMTP的验证没有看到，但是好像这里的网络竟然不用SMTP验证。</p> <p>一个验证方法，因为这里的防火墙，所以还没尝试。</p> <p>set-net [hankjin@163.com smtp.163.com pop3.163.com none<br />&nbsp;&nbsp;&nbsp; none none \"hankjin\" \"password\"]<br />send <a href=\"mailto:jindongh@cn.ibm.com\" rel=\"nofollow\">jindongh@cn.ibm.com</a> \"This is from 163.com\"</p> <p>顺便看看一些缩略词的全称：</p> <p>DNS - Domain Name Service<br />TCP - Transmission Control Protocol<br />UDP - User Datagram Protocol<br />HTTP - Hyper Text Transfer Protocol<br />FTP - File Transfer Protocol<br />SMTP - Simple Mail Transport Protocol<br />POP - Post Office Protocol<br />NNTP - Network News Transfer Protocol<br />CGI - Common Gateway Interface</p> <p>BBS - Bulletin Board System</p> <p><a href=\"http://www.rebol.com/docs/core23/rebolcore-13.html#section-3\" rel=\"nofollow\">http://www.rebol.com/docs/core23/rebolcore-13.html#section-3</a></p>',1,1,1,0,0,0,'Ftp的验证采取ftp://user:pass@url的方式 SMTP的验证没有看到，但是好像这里的网络竟然不用SMTP验证。 一个验证方法，因为这里的防火墙，所以还没尝试。 set-net [hankjin@163.com smtp.163.com pop3.163.com none&nbsp;&nbsp;&nbsp; none none \"hankjin\" \"password\"]send jindongh@cn.ibm.com \"This is from 163.com\" 顺便看看一些缩略词的全称： DNS - Domain Name ServiceTCP - Transmission Control ProtocolUDP - User Datagram ProtocolHTTP - Hyper Text Transfer ProtocolFTP - File Transfer ProtocolSMTP - Simple Mail Transport ProtocolPOP - Post Office ProtocolNNTP - Network News Transfer ProtocolCGI - Common Gateway Interface BBS - Bulletin Board System http://www.rebol.com/docs/core23/rebolcore-13.html#section-3','',0,'other',0,'','zinnia/_entry_detail.html','entry_detail.html'),(212,'单机搭建学习网络UserModeLinux','33731937201010129263187',2,'2010-11-12 21:37:46',NULL,'2010-11-12 21:37:46','2014-07-13 18:57:18','如果163博客支持导入Word文档就好了<br />  <div>  <p align=\"center\" style=\"text-align: center;\"><span lang=\"EN-US\" style=\"font-size: 42pt;\">&nbsp;</span></p>  <p align=\"center\" style=\"text-align: center;\"><span lang=\"EN-US\" style=\"font-size: 42pt;\">&nbsp;</span></p>  <p align=\"center\" style=\"text-align: center;\"><span lang=\"EN-US\" style=\"font-size: 42pt;\">&nbsp;</span></p>  <p align=\"center\" style=\"text-align: center;\"><b><span>单机搭建学习网络</span></b><b><span lang=\"EN-US\" style=\"font-size: 42pt;\"></span></b></p>  <p align=\"center\" style=\"text-align: center;\"><b><span lang=\"EN-US\" style=\"font-size: 42pt;\">User Mode Linux</span></b></p>  <p align=\"center\" style=\"text-align: center;\"><b><span lang=\"EN-US\" style=\"font-size: 22pt;\">Version 1.0</span></b></p>  <p><span lang=\"EN-US\" style=\"font-size: 12pt;\">&nbsp;</span></p>  <p><span lang=\"EN-US\" style=\"font-size: 12pt;\">&nbsp;</span></p>  <p><span lang=\"EN-US\" style=\"font-size: 12pt;\">&nbsp;</span></p>  <p><span lang=\"EN-US\" style=\"font-size: 12pt;\">&nbsp;</span></p>  <p><span lang=\"EN-US\" style=\"font-size: 12pt;\">&nbsp;</span></p>  <p><span lang=\"EN-US\" style=\"font-size: 12pt;\">&nbsp;</span></p>  <p><span lang=\"EN-US\" style=\"font-size: 12pt;\">&nbsp;</span></p>  <p><span lang=\"EN-US\" style=\"font-size: 12pt;\">&nbsp;</span></p>  <p><span lang=\"EN-US\" style=\"font-size: 12pt;\">&nbsp;</span></p>  <p align=\"center\" style=\"text-align: center;\"><span lang=\"EN-US\" style=\"font-size: 12pt;\">&nbsp;</span></p>  <p align=\"center\" style=\"text-align: center;\"><span lang=\"EN-US\" style=\"font-size: 12pt;\">&nbsp;</span></p>  <p align=\"center\" style=\"text-align: center;\"><span lang=\"EN-US\" style=\"font-size: 12pt;\">&nbsp;</span></p>  <p align=\"center\" style=\"text-align: center;\"><span lang=\"EN-US\" style=\"font-size: 12pt;\">&nbsp;</span></p>  <p align=\"center\" style=\"text-align: center;\"><span lang=\"EN-US\" style=\"font-size: 16pt;\">Copyleft@ysmonk</span></p>  <p align=\"center\" style=\"text-align: center;\"><span lang=\"EN-US\" style=\"font-size: 16pt;\">2010</span><span>年</span><span lang=\"EN-US\" style=\"font-size: 16pt;\">1</span><span>月</span><span lang=\"EN-US\" style=\"font-size: 16pt;\"></span></p>  </div>  <span lang=\"EN-US\"><br clear=\"all\" /> </span>  <div>  <p align=\"center\" style=\"text-align: center;\"><span>目录</span><span lang=\"EN-US\" style=\"font-size: 12pt;\"></span></p><span><span lang=\"EN-US\"><a href=\"http://hankjin.blog.163.com/blog/#_Toc277360879\"><span lang=\"EN-US\"><span lang=\"EN-US\"></span></span><span style=\"color: windowtext; display: none; text-decoration: none;\"><span>... </span></span><span style=\"color: windowtext; display: none; text-decoration: none;\">12</span><span style=\"color: windowtext; display: none; text-decoration: none;\"></span></a></span></span><span lang=\"EN-US\"></span>    </div>  <span lang=\"EN-US\">1&nbsp;&nbsp;&nbsp;&nbsp; 系统简介... 1<br />2&nbsp;&nbsp;&nbsp;&nbsp; 快速搭建... 2<br />2.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 准备工具和软件... 2<br />2.2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 运行... 2<br />3&nbsp;&nbsp;&nbsp;&nbsp; 准备工作... 3<br />3.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 预备知识... 3<br />3.1.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; user-mode-linux. 3<br />3.1.2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; uml_net 3<br />3.1.3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; screen命令... 3<br />3.2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 安装软件... 3<br />3.3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 制作文件系统... 3<br />4&nbsp;&nbsp;&nbsp;&nbsp; 开始搭建... 5<br />4.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 虚拟交换机... 5<br />4.2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 定制文件系统... 5<br />4.2.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; serverA. 5<br />4.2.2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; serverB. 5<br />4.2.3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; clientA. 6<br />4.2.4&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; clientB1. 6<br />4.2.5&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; clientB2. 6<br />4.3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 实现NAT. 7<br />4.3.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 设置serverA. 7<br />4.3.2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 设置serverB. 7<br />4.3.3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 设置host 7<br />5&nbsp;&nbsp;&nbsp;&nbsp; 测试... 9<br />5.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 测试交换机... 9<br />5.1.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 测试switch1. 9<br />5.1.2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 测试switchA. 9<br />5.1.3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 测试switchB. 9<br />5.2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 测试NAT. 9<br />5.2.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; clientA. 9<br />5.2.2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; clientB. 10<br />5.3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 高级测试... 10<br />5.3.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 测试VPN.. 10<br />6&nbsp;&nbsp;&nbsp;&nbsp; 常见错误<br clear=\"all\" /> </span>  <div>  <h1><a name=\"_Toc277360846\" rel=\"nofollow\"><span lang=\"EN-US\"><span>1<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span>系统简介</span></a></h1>  <p style=\"text-indent: 21pt;\"><span>如下图所示的网络环境</span><span lang=\"EN-US\">, </span><span>最少需要</span><span lang=\"EN-US\">5</span><span>台工作站才可以搭建起来</span><span lang=\"EN-US\">, </span><span>这给网络学习带来极大的障碍</span><span lang=\"EN-US\">, </span><span>但是利用</span><span lang=\"EN-US\">User Mode Linux</span><span>的特性</span><span lang=\"EN-US\">, </span><span>就可以在一台工作站上模拟出这个环境</span><span lang=\"EN-US\">.</span></p>  <p style=\"text-indent: 21pt;\"><span>本文基于</span><span lang=\"EN-US\">Ubuntu Intrepid</span><span>环境</span><span lang=\"EN-US\">.</span></p>  <p align=\"center\"><span lang=\"EN-US\"></span></p><div><img alt=\"单机搭建学习网络UserModeLinux - hankjin - 云水居\" src=\"/static/163/1755840904722992039.jpg\" style=\"margin: 0pt 10px 0pt 0pt;\" title=\"单机搭建学习网络UserModeLinux - hankjin - 云水居\" /><span></span></div></div>  <span lang=\"EN-US\"> </span>  <div>  <h1><a name=\"_Toc277360847\" rel=\"nofollow\"><span lang=\"EN-US\"><span>2<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span>快速搭建</span></a></h1>  <p><span>文件系统的制作有些繁琐</span><span lang=\"EN-US\">,</span><span>因此可以直接使用已经创建好的文凭系统和脚本进行测试</span><span lang=\"EN-US\">.</span></p>  <h2><a name=\"_Toc277360848\" rel=\"nofollow\"><span lang=\"EN-US\"><span>2.1<span>&nbsp;&nbsp;&nbsp; </span></span></span><span>准备工具和软件</span></a></h2>  <p><span lang=\"EN-US\">sudo apt-get install uml_utilities user-mode-linux</span></p>  <p><span lang=\"EN-US\">wget ftp://cbib.cnkk.org/incomming/hank-fs-AS</span></p>  <p><span lang=\"EN-US\">wget ftp://cbib.cnkk.org/incomming/hank-fs-BS</span></p>  <p><span lang=\"EN-US\">wget ftp://cbib.cnkk.org/incomming/hank-fs-A1</span></p>  <p><span lang=\"EN-US\">wget ftp://cbib.cnkk.org/incomming/hank-fs-B1</span></p>  <p><span lang=\"EN-US\">wget ftp://cbib.cnkk.org/incomming/hank-fs-B2</span></p>  <p><span lang=\"EN-US\">wget ftp://cbib.cnkk.org/incomming/simnet.sh</span></p>  <p><span lang=\"EN-US\">chmod +x simnet.sh</span></p>  <h2><a name=\"_Toc277360849\" rel=\"nofollow\"><span lang=\"EN-US\"><span>2.2<span>&nbsp;&nbsp;&nbsp; </span></span></span><span>运行</span></a></h2>  <p><span lang=\"EN-US\">sudo –i</span></p>  <p><span lang=\"EN-US\">./simnet.sh</span></p>  <p><span>现在网络已经启动</span><span lang=\"EN-US\">,</span><span>可以通过以下命令进入相应的模拟设备操作界面</span><span lang=\"EN-US\">.</span><span>从而进行第</span><span lang=\"EN-US\">5</span><span>节的测试</span><span lang=\"EN-US\">.</span></p>  <table border=\"1\" cellpadding=\"0\" cellspacing=\"0\" style=\"border-collapse: collapse; border: medium none;\">  <tbody><tr>   <td style=\"width: 185.4pt; padding: 0cm 5.4pt;\" valign=\"top\" width=\"309\">   <p><b><span>命令</span><span lang=\"EN-US\"></span></b></p>   </td>   <td valign=\"top\" width=\"401\">   <p><b><span>虚拟设备</span><span lang=\"EN-US\"></span></b></p>   </td>  </tr>  <tr>   <td valign=\"top\" width=\"309\">   <p><span lang=\"EN-US\">screen –r sw1</span></p>   </td>   <td valign=\"top\" width=\"401\">   <p><span lang=\"EN-US\">Switch 1</span></p>   </td>  </tr>  <tr>   <td valign=\"top\" width=\"309\">   <p><span lang=\"EN-US\">screen –r swA</span></p>   </td>   <td valign=\"top\" width=\"401\">   <p><span lang=\"EN-US\">Switch A</span></p>   </td>  </tr>  <tr>   <td valign=\"top\" width=\"309\">   <p><span lang=\"EN-US\">screen –r swB</span></p>   </td>   <td valign=\"top\" width=\"401\">   <p><span lang=\"EN-US\">Switch B</span></p>   </td>  </tr>  <tr>   <td valign=\"top\" width=\"309\">   <p><span lang=\"EN-US\">screen –r vmAS</span></p>   </td>   <td valign=\"top\" width=\"401\">   <p><span lang=\"EN-US\">Server A</span></p>   </td>  </tr>  <tr>   <td valign=\"top\" width=\"309\">   <p><span lang=\"EN-US\">screen –r vmBS</span></p>   </td>   <td valign=\"top\" width=\"401\">   <p><span lang=\"EN-US\">Server B</span></p>   </td>  </tr>  <tr>   <td valign=\"top\" width=\"309\">   <p><span lang=\"EN-US\">screen –r vmA1</span></p>   </td>   <td valign=\"top\" width=\"401\">   <p><span lang=\"EN-US\">Client A</span></p>   </td>  </tr>  <tr>   <td valign=\"top\" width=\"309\">   <p><span lang=\"EN-US\">screen –r vmB1</span></p>   </td>   <td valign=\"top\" width=\"401\">   <p><span lang=\"EN-US\">Client B1</span></p>   </td>  </tr>  <tr>   <td valign=\"top\" width=\"309\">   <p><span lang=\"EN-US\">screen –r vmB2</span></p>   </td>   <td valign=\"top\" width=\"401\">   <p><span lang=\"EN-US\">Client B2</span></p>   </td>  </tr> </table>  <p><span lang=\"EN-US\">&nbsp;</span></p>  </div>  <span lang=\"EN-US\"><br clear=\"all\" /> </span>  <div>  <h1><a name=\"_Toc277360850\" rel=\"nofollow\"><span lang=\"EN-US\"><span>3<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span>准备工作</span></a></h1>  <h2><a name=\"_Toc277360851\" rel=\"nofollow\"><span lang=\"EN-US\"><span>3.1<span>&nbsp;&nbsp;&nbsp; </span></span></span><span>预备知识</span></a></h2>  <h3><a name=\"_Toc277360852\" rel=\"nofollow\"><span lang=\"EN-US\"><span>3.1.1<span>&nbsp;&nbsp;&nbsp; </span></span></span><span lang=\"EN-US\">user-mode-linux</span></a></h3>  <p style=\"text-indent: 21pt;\"><span lang=\"EN-US\">User-mode-linux (UML) </span><span>是让一个</span><span lang=\"EN-US\">Linux</span><span>作为一个独立进程运行在另一个</span><span lang=\"EN-US\">linux</span><span>上。</span></p>  <p style=\"text-indent: 21pt;\"><span lang=\"EN-US\">UML </span><span>是一种在同一时间运行多</span><span lang=\"EN-US\">Linux </span><span>的安全方式。每个进程都是独立去其他的，这非常安全，例如在同一机器上进行多种测试和研发而不互相干扰。如果一些测试进程损坏并不会影响宿主系统或研发用进程。</span></p>  <h3><a name=\"_Toc277360853\" rel=\"nofollow\"><span lang=\"EN-US\"><span>3.1.2<span>&nbsp;&nbsp;&nbsp; </span></span></span><span lang=\"EN-US\">uml_net</span></a></h3>  <p style=\"text-indent: 21pt;\"><span lang=\"EN-US\">User Mode Linux</span><span>连接网络有三种方式</span><span lang=\"EN-US\">,</span><span>分别是</span><span lang=\"EN-US\">slirp, tuntap</span><span>和</span><span lang=\"EN-US\">uml_switch.</span></p>  <p style=\"text-indent: 21pt;\"><span lang=\"EN-US\">slirp</span><span>方式最简单</span><span lang=\"EN-US\">, </span><span>只要安装</span><span lang=\"EN-US\">slirp</span><span>软件</span><span lang=\"EN-US\">,</span><span>在启动时指定</span><span lang=\"EN-US\">eth0=slirp,</span><span>并将</span><span lang=\"EN-US\">UML</span><span>的</span><span lang=\"EN-US\">ip</span><span>设置为</span><span lang=\"EN-US\">10.0.2</span><span lang=\"EN-US\">.15</span><span>即可</span><span lang=\"EN-US\">, </span><span>但是这个方法只支持</span><span lang=\"EN-US\">TCP</span><span>和</span><span lang=\"EN-US\">UDP,</span><span>不支持</span><span lang=\"EN-US\">ICMP,</span><span>因此</span><span lang=\"EN-US\">ping</span><span>不能主机</span><span lang=\"EN-US\">.</span></p>  <p style=\"text-indent: 21pt;\"><span lang=\"EN-US\">tuntap</span><span>方式稍微复杂一些</span><span lang=\"EN-US\">, </span><span>启动时指定</span><span lang=\"EN-US\">eth0=tuntap,,,192.168.1.201, </span><span>这样会在主机上安装一个</span><span lang=\"EN-US\">IP</span><span>为</span><span lang=\"EN-US\">192.168.1.201</span><span>的</span><span lang=\"EN-US\">tap</span><span>设备</span><span lang=\"EN-US\">,UML</span><span>中设置同一网段的</span><span lang=\"EN-US\">IP</span><span>即可</span><span lang=\"EN-US\">.</span></p>  <p style=\"text-indent: 21pt;\"><span lang=\"EN-US\">uml_switch</span><span>的方法最为强大</span><span lang=\"EN-US\">, uml_switch</span><span>命令会创建一个虚拟交换机</span><span lang=\"EN-US\">,</span><span>并在主机上安装一个</span><span lang=\"EN-US\">tap</span><span>设备</span><span lang=\"EN-US\">, </span><span>如</span><span lang=\"EN-US\">uml_switch –tap tap1 –unix /tmp/switch1, </span><span>将会生成</span><span lang=\"EN-US\">tap1</span><span>设备和虚拟交换机</span><span lang=\"EN-US\">/tmp/switch1, UML</span><span>只要指定</span><span lang=\"EN-US\">eth0=daemon,,unix,/tmp/switch1</span><span>即可连上指定的交换机</span><span lang=\"EN-US\">.</span></p>  <h3><a name=\"_Toc277360854\" rel=\"nofollow\"><span lang=\"EN-US\"><span>3.1.3<span>&nbsp;&nbsp;&nbsp; </span></span></span><span lang=\"EN-US\">screen</span></a><span><span>命令</span></span></h3>  <p><span>使用</span><span lang=\"EN-US\">screen</span><span>命令</span><span lang=\"EN-US\">,</span><span>可以在一个</span><span lang=\"EN-US\">putty</span><span>上完成所有这些操作</span><span lang=\"EN-US\">,</span><span>下面这个命令的意思是</span><span lang=\"EN-US\">:</span></p>  <p><span>在后台启动一个</span><span lang=\"EN-US\">screen, </span><span>名称为</span><span lang=\"EN-US\">&lt;screenName&gt;, </span><span>并在启动的</span><span lang=\"EN-US\">screen</span><span>上执行</span><span lang=\"EN-US\">&lt;command&gt;</span><span>命令</span></p>  <p><span><span lang=\"EN-US\" style=\"font-size: 9pt;\">screen –S &lt;screenName&gt; -d –m &lt;command&gt;</span></span></p>  <p><span>例如</span></p>  <p><span><span lang=\"EN-US\" style=\"font-size: 9pt;\">screen –S vmA1 –d –m linux ubd0=hank-fs-A1 eth0=daemon,,unix,/tmp/switchA</span></span></p>  <h2><a name=\"_Toc277360855\" rel=\"nofollow\"><span lang=\"EN-US\"><span>3.2<span>&nbsp;&nbsp;&nbsp; </span></span></span><span>安装软件</span></a></h2>  <p><span lang=\"EN-US\">sudo apt-get install uml_utilities user-mode-linux iptables</span></p>  <h2><a name=\"_Toc277360856\" rel=\"nofollow\"><span lang=\"EN-US\"><span>3.3<span>&nbsp;&nbsp;&nbsp; </span></span></span><span>制作文件系统</span></a></h2>  <p><span lang=\"EN-US\">#</span><span>以下命令都使用</span><span lang=\"EN-US\">root</span><span>帐户操作</span></p>  <p><span lang=\"EN-US\">#1. </span><span>创建映像文件，并格式文件系统</span></p>  <p><span lang=\"EN-US\">dd if=/dev/zero of=hank-fs bs=1024K count=400</span></p>  <p><span lang=\"EN-US\">mkfs.ext3 hank-fs</span></p>  <p><span lang=\"EN-US\">#2.1 </span><span>加载基本文件</span></p>  <p><span lang=\"EN-US\">mount -o loop hank-fs /mnt</span></p>  <p><span lang=\"EN-US\">debootstrap --arch=i386 intrepid /mnt http://debian.ustc.edu.cn/ubuntu</span></p>  <p><span lang=\"EN-US\">#2.2. </span><span>拷贝</span><span lang=\"EN-US\">modules</span><span>到文件系统</span></p>  <p><span lang=\"EN-US\">cp -r /usr/lib/uml/modules/* /mnt/lib/modules/</span></p>  <p><span lang=\"EN-US\">#3. </span><span>定制网络</span> <span>文件系统</span> <span>终端</span> <span>用户</span></p>  <p><span lang=\"EN-US\">chroot /mnt</span></p>  <p><span lang=\"EN-US\">#3.1 </span><span>网络</span><span lang=\"EN-US\"> /etc/network/interface</span></p>  <p><span lang=\"EN-US\">auto lo</span></p>  <p><span lang=\"EN-US\">iface lo inet loopback</span></p>  <p><span lang=\"EN-US\">auto eth0</span></p>  <p><span lang=\"EN-US\">iface eth0 inet static</span></p>  <p><span lang=\"EN-US\">address 192.168.1.2</span></p>  <p><span lang=\"EN-US\">netmask 255.255.255.0</span></p>  <p><span lang=\"EN-US\">gateway 192.168.1.1</span></p>  <p><span lang=\"EN-US\">#3.2 </span><span>文件</span><span lang=\"EN-US\"> /etc/fstab</span></p>  <p><span lang=\"EN-US\">/dev/ubda<span>&nbsp; </span>/<span>&nbsp; </span>ext3 defaults 0 1</span></p>  <p><span lang=\"EN-US\">proc<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>/proc<span>&nbsp; </span>proc defaults 0 0</span></p>  <p><span lang=\"EN-US\">#3.3 </span><span>终端</span><span lang=\"EN-US\"> /etc/default/console-setup, </span><span>注释掉这下面一句</span></p>  <p><span lang=\"EN-US\">#ACTIVE_CONSOLE=tty[1-6]</span></p>  <p><span lang=\"EN-US\">cd /etc/event.d/</span></p>  <p><span lang=\"EN-US\">rm tty[2-6]</span></p>  <p><span lang=\"EN-US\">mv tty1 tty0</span></p>  <p><span lang=\"EN-US\">#/etc/event.d/tty0 </span><span>的最后一行，将</span><span lang=\"EN-US\">tty1</span><span>改为</span><span lang=\"EN-US\">tty0</span></p>  <p><span lang=\"EN-US\">exec /sbin/getty 38400 tty0</span></p>  <p><span lang=\"EN-US\">#3.4 </span><span>创建用户，并加入</span><span lang=\"EN-US\">sudoers</span></p>  <p><span lang=\"EN-US\">adduser hankjin</span></p>  <p><span lang=\"EN-US\">#</span><span>修改</span><span lang=\"EN-US\">/etc/sudoers</span></p>  <p><span lang=\"EN-US\">root<span>&nbsp;&nbsp;&nbsp; </span>ALL=(ALL) ALL</span></p>  <p><span lang=\"EN-US\">hankjin ALL=(ALL) ALL</span></p>  </div>  <b><span lang=\"EN-US\"><br clear=\"all\" /> </span></b>  <div>  <h1><a name=\"_Toc277360857\" rel=\"nofollow\"><span lang=\"EN-US\"><span>4<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span>开始搭建</span></a></h1>  <p style=\"text-indent: 21pt;\"><span>首先使用</span><span lang=\"EN-US\">uml_switch</span><span>命令创建三个虚拟交换机</span><span lang=\"EN-US\">, </span><span>同时自动在</span><span lang=\"EN-US\">host</span><span>上生成有一个</span><span lang=\"EN-US\">tap,</span><span>然后启动五个</span><span lang=\"EN-US\">User Mode Linux,</span><span>同时连接到虚拟交换机上</span><span lang=\"EN-US\">.</span></p>  <h2><a name=\"_Toc277360858\" rel=\"nofollow\"><span lang=\"EN-US\"><span>4.1<span>&nbsp;&nbsp;&nbsp; </span></span></span><span>虚拟交换机</span></a></h2>  <p><span>使用</span><span lang=\"EN-US\">uml_switch</span><span>命令虚拟三台交换机</span><span lang=\"EN-US\">:switch1, switchA</span><span>和</span><span lang=\"EN-US\">switchB, </span><span>其中</span><span lang=\"EN-US\">:</span></p>  <p><span lang=\"EN-US\">switch1</span><span>的网络为</span><span lang=\"EN-US\">10.0.0</span><span lang=\"EN-US\">.0/24,</span><span>启动方式为</span></p>  <p><span><span lang=\"EN-US\" style=\"font-size: 9pt;\">sudo uml_switch –tap tap1 –unix /tmp/switch1</span></span></p>  <p><span lang=\"EN-US\">switchA</span><span>的网络为</span><span lang=\"EN-US\">192.168.11.0/24,</span><span>启动方式为</span></p>  <p><span><span lang=\"EN-US\" style=\"font-size: 9pt;\">sudo uml_switch –tap tapA –unix /tmp/switchA</span></span></p>  <p><span lang=\"EN-US\">switchB</span><span>的网络为</span><span lang=\"EN-US\">192.168.22.0/24,</span><span>启动方式为</span><span lang=\"EN-US\">:</span></p>  <p><span><span lang=\"EN-US\" style=\"font-size: 9pt;\">sudo uml_switch –tap tapB –unix /tmp/switchB</span></span></p>  <h2><a name=\"_Toc277360859\" rel=\"nofollow\"><span lang=\"EN-US\"><span>4.2<span>&nbsp;&nbsp;&nbsp; </span></span></span><span>定制文件系统</span></a></h2>  <p><span lang=\"EN-US\">User Mode Linux</span><span>支持</span><span lang=\"EN-US\">Copy On Write</span><span>技术</span><span lang=\"EN-US\">, </span><span>可以</span><span lang=\"EN-US\">xxx,</span><span>但是这里为了简化环境</span><span lang=\"EN-US\">,</span><span>直接将上面制作的</span><span lang=\"EN-US\">hank-fs</span><span>拷贝一个复本即可</span><span lang=\"EN-US\">, </span><span>因此</span><span lang=\"EN-US\">, </span><span>对每个</span><span lang=\"EN-US\">User Mode Linux,</span><span>我们都先拷贝一个复本出来</span><span lang=\"EN-US\">.</span></p>  <h3><a name=\"_Toc277360860\" rel=\"nofollow\"><span lang=\"EN-US\"><span>4.2.1<span>&nbsp;&nbsp;&nbsp; </span></span></span><span lang=\"EN-US\">serverA</span></a></h3>  <p style=\"text-indent: 21pt;\"><span>首先从</span><span lang=\"EN-US\">hank-fs</span><span>拷贝一个复本</span><span lang=\"EN-US\"> cp hank-fs hank-fs-AS</span></p>  <p style=\"text-indent: 21pt;\"><span lang=\"EN-US\">serverA</span><span>有两个网卡</span><span lang=\"EN-US\">,</span><span>一个连接</span><span lang=\"EN-US\">switch1(eth0),</span><span>一个连接</span><span lang=\"EN-US\">switchA(eth1),</span><span>因此启动</span><span lang=\"EN-US\">serverA</span><span>的命令为</span></p>  <p><span lang=\"EN-US\">linux ubd0=hank-fs-AS eth0=daemon,,unix,/tmp/switch1 eth1=daemon,,unix,/tmp/switchA</span></p>  <p><span lang=\"EN-US\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span>启动后设置</span><span lang=\"EN-US\">serverA</span><span>的网络</span><span lang=\"EN-US\">, sudo vi /etc/network/interface:</span></p>  <p><span><span lang=\"EN-US\" style=\"font-size: 9pt;\">auto eth0</span></span></p>  <p><span><span lang=\"EN-US\" style=\"font-size: 9pt;\">iface eth0 inet static</span></span></p>  <p><span><span lang=\"EN-US\" style=\"font-size: 9pt;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>address 10.0.0.10</span></span></p>  <p><span><span lang=\"EN-US\" style=\"font-size: 9pt;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>netmask 255.255.255.0</span></span></p>  <p><span><span lang=\"EN-US\" style=\"font-size: 9pt;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>gateway 10.0.0.1</span></span></p>  <p><span><span lang=\"EN-US\" style=\"font-size: 9pt;\">auto eth1</span></span></p>  <p><span><span lang=\"EN-US\" style=\"font-size: 9pt;\">iface eth1 inet static</span></span></p>  <p><span><span lang=\"EN-US\" style=\"font-size: 9pt;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>address 192.168.11.3</span></span></p>  <p><span><span lang=\"EN-US\" style=\"font-size: 9pt;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>netmask 255.255.255.0</span></span></p>  <h3><a name=\"_Toc277360861\" rel=\"nofollow\"><span lang=\"EN-US\"><span>4.2.2<span>&nbsp;&nbsp;&nbsp; </span></span></span><span lang=\"EN-US\">serverB</span></a></h3>  <p style=\"text-indent: 21pt;\"><span>首先从</span><span lang=\"EN-US\">hank-fs</span><span>拷贝一个复本</span><span lang=\"EN-US\"> cp hank-fs hank-fs-BS</span></p>  <p style=\"text-indent: 21pt;\"><span lang=\"EN-US\">serverB</span><span>有两个网卡</span><span lang=\"EN-US\">,</span><span>一个连接</span><span lang=\"EN-US\">switch1(eth0),</span><span>一个连接</span><span lang=\"EN-US\">switchA(eth1),</span><span>因此启动</span><span lang=\"EN-US\">serverB</span><span>的命令为</span></p>  <p><span lang=\"EN-US\">linux ubd0=hank-fs-BS eth0=daemon,,unix,/tmp/switch1 eth1=daemon,,unix,/tmp/switchB<