>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;&nbsp; result&nbsp;&nbsp; =&nbsp;&nbsp; JOptionPane.showOptionDialog(MyJar.this,&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;msg,&nbsp;&nbsp; \"Warning\",&nbsp;&nbsp; JOptionPane.DEFAULT_OPTION,&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;JOptionPane.WARNING_MESSAGE,&nbsp;&nbsp; null,&nbsp;&nbsp; options,options[0]);&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(result&nbsp;&nbsp; ==&nbsp;&nbsp; 2)&nbsp;&nbsp; //&nbsp;&nbsp; No&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;continue;&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else&nbsp;&nbsp; if(&nbsp;&nbsp; result&nbsp;&nbsp; ==&nbsp;&nbsp; 1)&nbsp;&nbsp; //YesToAll&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;overwrite&nbsp;&nbsp; =&nbsp;&nbsp; true;&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;File&nbsp;&nbsp; parent&nbsp;&nbsp; =&nbsp;&nbsp; new&nbsp;&nbsp; File(outFile.getParent());&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;&nbsp; (parent&nbsp;&nbsp; !=&nbsp;&nbsp; null&nbsp;&nbsp; &amp;&amp;&nbsp;&nbsp; !parent.exists())&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;parent.mkdirs();&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;out&nbsp;&nbsp; =&nbsp;&nbsp; new&nbsp;&nbsp; FileOutputStream(outFile);&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;while&nbsp;&nbsp; (true)&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;&nbsp; nRead&nbsp;&nbsp; =&nbsp;&nbsp; in.read(buf,&nbsp;&nbsp; 0,&nbsp;&nbsp; buf.length);&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;&nbsp; (nRead&nbsp;&nbsp; &lt;=&nbsp;&nbsp; 0)&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;out.write(buf,&nbsp;&nbsp; 0,&nbsp;&nbsp; nRead);&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;out.close();&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;outFile.setLastModified(archiveTime.getTime());&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;pm.close();&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;zf.close();&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;getToolkit().beep();&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;JOptionPane.showMessageDialog(MyJar.this,&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;\"Extracted&nbsp;&nbsp; \"&nbsp;&nbsp; +&nbsp;&nbsp; extracted&nbsp;&nbsp; +&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;\"&nbsp;&nbsp; file\"&nbsp;&nbsp; +&nbsp;&nbsp; ((extracted&nbsp;&nbsp; &gt;&nbsp;&nbsp; 1)&nbsp;&nbsp; ?&nbsp;&nbsp; \"s\":&nbsp;&nbsp; \"\")&nbsp;&nbsp; +&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;\"&nbsp;&nbsp; from&nbsp;&nbsp; the\\n\"&nbsp;&nbsp; +&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;zipfile&nbsp;&nbsp; +&nbsp;&nbsp; \"\\narchive&nbsp;&nbsp; into&nbsp;&nbsp; the\\n\"&nbsp;&nbsp; +&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;outputDir.getPath()&nbsp;&nbsp; +&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;\"\\ndirectory.\",&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;\"Zip&nbsp;&nbsp; Self&nbsp;&nbsp; Extractor\",&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;JOptionPane.INFORMATION_MESSAGE);&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;catch&nbsp;&nbsp; (Exception&nbsp;&nbsp; e)&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;{&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;System.out.println(e);&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;if(zf!=null)&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;try<br />&nbsp;&nbsp;&nbsp;&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;zf.close();&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;}<br />&nbsp;&nbsp;&nbsp;&nbsp;catch(IOException&nbsp;&nbsp; ioe)&nbsp;&nbsp; {;}&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;if(out!=null)<br />&nbsp;&nbsp;&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;try&nbsp;&nbsp; {out.close();}&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;catch(IOException&nbsp;&nbsp; ioe)&nbsp;&nbsp; {;}&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;if(in!=null)&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;try&nbsp;&nbsp; {&nbsp;&nbsp; in.close();&nbsp;&nbsp; }&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;catch(IOException&nbsp;&nbsp; ioe)&nbsp;&nbsp; {;}&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp; <br />}</span>',1,1,1,0,0,0,'天气:心情:这是一篇描述怎样制作自解压jar文件的文章，作者通过自己编写的一个自解压程序，并把这个自解压程序以及一个manifest文件一起加入原始的jar文件中，就制作出一个可以在各种支持java的平台上运行的自解压的jar &nbsp; 文件。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 自解压文件 &nbsp; &nbsp; &nbsp; 我们先来了解一下自解压文件，在window下可以用自解压制作工具如winzip &nbsp; self-Extractor来制作自解压文件，这些工具会把一个zip文件与解压程序打包在一起而产生一个新的可执行文件。然后只要运行这个可执行文件，就可以把zip文件中的内容解开。那为什么要创建自解压jar文件呢，创建成自解压zip文件不就好了？我们应该注意到自解压jar文件可以在任意支持java的平台上解开并执行,例如，可以在linux下执行。创建jar自解压文件很简单，只需要一个特殊的JAR &nbsp; manifest文件、一个基于java的解压程序（这个程序是原作者写的）、包含基本文件的jar &nbsp; 或者zip文件以及任何jsdk的jar应用程序 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; manifest文件 &nbsp; &nbsp; &nbsp; 要生成可执行jar文件，需要在META-INF &nbsp; 目录下的manifest文件，文件名为：MANIFEST.MF &nbsp; ，但在我们这里我们只需要在文件中指定在这个基于java &nbsp; 的解压程序中包含main()的类的名称：Main-Class: &nbsp; ZipSelfExtractor &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 我们已经把一个叫做jarmanifest的文件加入到这个技巧的源程序包中。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 解压程序 &nbsp; &nbsp; &nbsp; 你可以用各种方法来实现这个解压程序，在我们这里使用了一个简单直接的办法。首先，解压程序判断这个自解压jar文件的名称，有了这个文件名，解压程序使用解压标准，把文件解开。具体的可以查看在源码包中的ZipSelfExtractor.java文件。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 值得一提的是这里用了一个很巧妙的办法获取jar文件的文件名，虽然在命令行中出现的这个文件的名字，但它并没有作为参数传入类的main()中，因此，这里使用了以下的代码来获取文件名： &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; private &nbsp; String &nbsp; getJarFileName &nbsp; () &nbsp; &nbsp; &nbsp; { &nbsp; &nbsp; &nbsp; myClassName &nbsp; = &nbsp; this.getClass().getName() &nbsp; + &nbsp; \".class\"; &nbsp; &nbsp; &nbsp; URL &nbsp; urlJar &nbsp; = &nbsp; &nbsp; &nbsp; this.getClass().getClassLoader().getSystemResource(myClassName); &nbsp; &nbsp; &nbsp; String &nbsp; urlStr &nbsp; = &nbsp; urlJar.toString(); &nbsp; &nbsp; &nbsp; int &nbsp; from &nbsp; = &nbsp; \"jar:file:\".length(); &nbsp; &nbsp; &nbsp; int &nbsp; to &nbsp; = &nbsp; urlStr.indexOf(\"!/\"); &nbsp; &nbsp; &nbsp; return &nbsp; urlStr.substring(from, &nbsp; to); &nbsp; &nbsp; &nbsp; } &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 请注意：getSystemResource() &nbsp; 中使用了myClassName而不是ZipSelfExtractor.class作参数，这使得我们可以更改加压程序的名字而不需要修改代码。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 接下来，我们来分析获得这个jar文件的名字。首先，可以获取指向包含正在运行类的文件,urlStr &nbsp; = &nbsp; urlJar.toString();有了这个url,把jar文件名去掉，剩下的就是我们想要的，下面是这个url的格式： &nbsp; &nbsp; &nbsp; jar:file:/home/test/zipper.jar!/ZipSelfExtractor.class &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 有了文件名，就可以开始解压，详细的解压算法请大家自己看源码。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 为了可以更方便实用，程序使用了图形界面，程序中使用了JFileChooser类可以选择要解压的目标目录。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 最后程序还确保不把这两个文件：manifest文件和extractor\'s &nbsp; .class（在我们这里是ZipSelfExtractor.class）文件也解出来，程序是用来解开原始的jar的内容，而这两个文件并属于jar原始内容。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 打包jar文件 &nbsp; &nbsp; &nbsp; 有了manifest文件与解压程序，我们就可以创建自解压jar文件了，以下是一个例子： &nbsp; &nbsp; &nbsp; 1.创建一个zip文件Myzip.zip &nbsp; &nbsp; &nbsp; 2.下载zipper.jar &nbsp; &nbsp; &nbsp; 3.把文件解到当前目录，准备制作自解压jar文件 &nbsp; &nbsp; &nbsp; java &nbsp; -jar &nbsp; zipper.jar &nbsp; &nbsp; &nbsp; 4.把zipper.class拷贝成 &nbsp; ZipSelfExtractor.class &nbsp; &nbsp; &nbsp; 5.把 &nbsp; myzip.zip &nbsp; 重命名为 &nbsp; myzip.jar &nbsp; &nbsp; &nbsp; 6.把myzip.jar中的内容替换为jarmanifest和ZipSelfExtractor.class这两个文件 &nbsp; &nbsp; &nbsp; jar &nbsp; uvfm &nbsp; myzip.jar &nbsp; jarmanifest &nbsp; ZipSelfExtractor.class &nbsp; &nbsp; &nbsp; 7.执行java &nbsp; -jar &nbsp; myzip.jar就可以看到效果了，试试看 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 后记 &nbsp; &nbsp; &nbsp; 一个自解压的jar文件能够很好的跨平台使用，自解压jar文件创建简单,只需要有jre1.2或或者更新的版本就可以实现了。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 附自解压程序的源代码：&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /*&nbsp;&nbsp; MyJar.java&nbsp;&nbsp; */&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; /*&nbsp;&nbsp; Author:&nbsp;&nbsp; Z.S.&nbsp;&nbsp; Jin&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; Updates:&nbsp;&nbsp; John&nbsp;&nbsp; D.&nbsp;&nbsp; Mitchell&nbsp;&nbsp; */&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; import&nbsp;&nbsp; java.io.*;&nbsp;&nbsp;&nbsp;&nbsp; import&nbsp;&nbsp; java.net.*;&nbsp;&nbsp;&nbsp;&nbsp; import&nbsp;&nbsp; javax.swing.*;&nbsp;&nbsp;&nbsp;&nbsp; import&nbsp;&nbsp; java.util.zip.*;&nbsp;&nbsp;&nbsp;&nbsp; import&nbsp;&nbsp; java.util.*;&nbsp;&nbsp;&nbsp;&nbsp; import&nbsp;&nbsp; java.text.*;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; public&nbsp;&nbsp; class&nbsp; MyJar&nbsp;&nbsp; extends&nbsp;&nbsp; JFrame&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp;private&nbsp;&nbsp; String&nbsp;&nbsp; myClassName;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;static&nbsp;&nbsp; String&nbsp;&nbsp; MANIFEST&nbsp;&nbsp; =&nbsp;&nbsp; \"META-INF/MANIFEST.MF\";&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;public&nbsp;&nbsp; static&nbsp;&nbsp; void&nbsp;&nbsp; main(String[]&nbsp;&nbsp; args)&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;{&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;MyJar&nbsp;&nbsp; zse&nbsp;&nbsp; =&nbsp;&nbsp; new&nbsp;&nbsp; MyJar();&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;String&nbsp;&nbsp; jarFileName&nbsp;&nbsp; =&nbsp;&nbsp; zse.getJarFileName();&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;zse.extract(jarFileName);&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;System.exit(0);&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;MyJar()&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;{&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;private&nbsp;&nbsp; String&nbsp;&nbsp; getJarFileName()&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;{&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;myClassName&nbsp;&nbsp; =&nbsp;&nbsp; this.getClass().getName()&nbsp;&nbsp; +&nbsp;&nbsp; \".class\";&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;URL&nbsp;&nbsp; urlJar&nbsp;&nbsp; =&nbsp;&nbsp; this.getClass().getClassLoader().getSystemResource(myClassName);&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;String&nbsp;&nbsp; urlStr&nbsp;&nbsp; =&nbsp;&nbsp; urlJar.toString();&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;int&nbsp;&nbsp; from&nbsp;&nbsp; =&nbsp;&nbsp; \"jar:file:\".length();&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;int&nbsp;&nbsp; to&nbsp;&nbsp; =&nbsp;&nbsp; urlStr.indexOf(\"!/\");&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;return&nbsp;&nbsp; urlStr.substring(from,&nbsp;&nbsp; to);&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;public&nbsp;&nbsp; void&nbsp;&nbsp; extract(String&nbsp;&nbsp; zipfile)&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;{&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;File&nbsp;&nbsp; currentArchive&nbsp;&nbsp; =&nbsp;&nbsp; new&nbsp;&nbsp; File(zipfile);&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;JFileChooser&nbsp;&nbsp; fc&nbsp;&nbsp; =&nbsp;&nbsp; new&nbsp;&nbsp; JFileChooser();&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;fc.setCurrentDirectory(new&nbsp;&nbsp; File(\".\"));&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;fc.setDialogType(JFileChooser.OPEN_DIALOG);&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;fc.setDialogTitle(\"Select&nbsp;&nbsp; destination&nbsp;&nbsp; directory&nbsp;&nbsp; for&nbsp;&nbsp; extracting&nbsp;&nbsp; \"&nbsp;&nbsp; +&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;currentArchive.getName());&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;f