sp;&nbsp; &nbsp;} finally {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;try {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;socket.close();&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;} catch (Exception e1) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;try {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;outbound.close();&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;} catch (Exception e2) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;void pipe(InputStream is0, InputStream is1, OutputStream os0,&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;OutputStream os1) throws IOException {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;try {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;int ir;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;byte bytes[] = new byte[BUFSIZ];&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;while (true) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;try {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if ((ir = is0.read(bytes)) &gt; 0) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;os0.write(bytes, 0, ir);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if (logging)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;writeLog(bytes, 0, ir, true);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;} else if (ir &lt; 0)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;break;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;} catch (InterruptedIOException e) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;try {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if ((ir = is1.read(bytes)) &gt; 0) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;os1.write(bytes, 0, ir);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if (logging)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;writeLog(bytes, 0, ir, false);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;} else if (ir &lt; 0)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;break;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;} catch (InterruptedIOException e) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;} catch (Exception e0) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(\"PipeException: \" + e0);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;static public void startProxy(int port, Class clobj) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;ServerSocket ssock;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Socket sock;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;try {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;ssock = new ServerSocket(port);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;while (true) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Class[] sarg = new Class[1];&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Object[] arg = new Object[1];&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;sarg[0] = Socket.class;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;try {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;java.lang.reflect.Constructor cons = clobj&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;.getDeclaredConstructor(sarg);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;arg[0] = ssock.accept();&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;cons.newInstance(arg); // Create HttpProxy or sons&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;} catch (Exception e) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Socket esock = (Socket) arg[0];&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;try {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;esock.close();&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;} catch (Exception ec) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;} catch (IOException e) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;// test&nbsp;&nbsp; &nbsp;static public void main(String args[]) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(\"start in 8080\\n\");&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;JavaProxyTest.log = System.out;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;JavaProxyTest.logging = false;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;JavaProxyTest.startProxy(8080, JavaProxyTest.class);&nbsp;&nbsp; &nbsp;}}Client:import java.io.BufferedReader;import java.io.InputStreamReader;import java.net.URL;import java.net.URLConnection;import java.util.Properties;public class Test {&nbsp;&nbsp; &nbsp;public static void main(String args[]) throws Exception {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;String strUrl = \"http://blog.csdn.net/cqq/\";&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;URL url = new URL(strUrl);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;URLConnection conn = url.openConnection();&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;String strProxy = \"127.0.0.1\";//\"198.133.224.145\";&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;String strPort = \"8080\";//\"3128\";&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Properties systemProperties = System.getProperties();&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;systemProperties.setProperty(\"http.proxyHost\", strProxy);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;systemProperties.setProperty(\"http.proxyPort\", strPort);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;BufferedReader rd = new BufferedReader(new InputStreamReader(conn&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;.getInputStream()));&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;String ss = \"\";&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;while (( ss = rd.readLine()) != null) {&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(ss);&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;rd.close();&nbsp;&nbsp; &nbsp;}}','',0,'j2se',0,'','zinnia/_entry_detail.html','entry_detail.html'),(282,'gdb常用命令','33731937201082403726994',2,'2010-09-24 12:42:49',NULL,'2010-09-24 12:42:49','2014-07-13 18:57:22','编译时使用-g选项，才可以执行gdb操作<br />cc -g test.c<br />gdb a.out<br />设置命令行参数<br />set args 8 7 hello<br /><br />设置条件断点<br />break 24 if i==8<br />显示断点<br />info break<br />删除断点<br />delete break 1<br />delete break<br /><br />显示变量值<br />p name<br />显示变量类型<br />whatis name<br /><br />监视变量<br />watch i<br />退出循环<br />util',1,1,1,0,0,0,'编译时使用-g选项，才可以执行gdb操作cc -g test.cgdb a.out设置命令行参数set args 8 7 hello设置条件断点break 24 if i==8显示断点info break删除断点delete break 1delete break显示变量值p name显示变量类型whatis name监视变量watch i退出循环util','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(283,'[Fw]农历和阳历转换表的由来','337319372009111005529309',2,'2009-12-10 12:55:29',NULL,'2009-12-10 12:55:29','2014-07-13 18:57:22','<p>几乎所有的农历转换都是用的一个转换表，这个转换表是怎么得来的呢？公式如下：</p>  <p>转自 <a href=\"http://blog.chinaunix.net/u/25381/showart_199977.html\" rel=\"nofollow\">http://blog.chinaunix.net/u/25381/showart_199977.html</a></p>  <p>一、节气的计算<br />　　先给节气进行编号，从近日点开始的第一个节气编为0，编号如下及其相应的月份如下：<br />0 小寒 腊月<br />1 大寒 腊月<br />2 立春&nbsp; 正月<br />...</p>  <p>把当天和1900年1月0日（星期日）的差称为积日，那么第y年（1900年算第0年）第x 个节气的积日是<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; F = 365.242 * y + 6.2 + 15.22 * x - 1.9 * sin(0.262 * x)<br />这个公式的误差在0.05天左右。<br />二、朔日的计算<br />　　从1900年开始的第m个朔日的公式是<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; M = 1.6 + 29.5306 * m + 0.4 * sin(1 - 0.45058 * m)<br />这个公式的误差在0.2天左右。<br />三、年份的确定<br />　　1864年1月0日是农历癸亥年，所以用当年减去1864，用10除得的余数作为年份天干的，用12除得的余数作为年份的地支，数字对应的天干和地支如下。<br />当年的1月0日换算为积日，可以用年份减去1900得到的年数被4整除，所得商数作为 y(D4)，余数作为y(M4)，y(M4)为零的年份是公历闰年，积日是<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; D(1) = 1461 * y(D4) - 1<br />y(M4)不为零的年份是公历平年，积日是<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; D(1) = 1461 * y(D4) + 365 * y(M4)<br />四、月份的确定<br />　　计算前一年冬至的积日F(0)，并用F(0)计算冬至所在的朔月m及其朔日M(0)，就可以推算冬至的农历日期，冬至所在的农历月份总是十一月。计算下一个中气F(1)和下一个朔日M(1)，如果F(1)&lt;M(1)，那么该月就是上一个月的闰月，并把这个中气作为F(2)，以后的中气、朔日和农历月份也这样确定。</p>',1,1,1,0,0,0,'几乎所有的农历转换都是用的一个转换表，这个转换表是怎么得来的呢？公式如下：  转自 http://blog.chinaunix.net/u/25381/showart_199977.html  一、节气的计算　　先给节气进行编号，从近日点开始的第一个节气编为0，编号如下及其相应的月份如下：0 小寒 腊月1 大寒 腊月2 立春&nbsp; 正月...  把当天和1900年1月0日（星期日）的差称为积日，那么第y年（1900年算第0年）第x 个节气的积日是&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; F = 365.242 * y + 6.2 + 15.22 * x - 1.9 * sin(0.262 * x)这个公式的误差在0.05天左右。二、朔日的计算　　从1900年开始的第m个朔日的公式是&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; M = 1.6 + 29.5306 * m + 0.4 * sin(1 - 0.45058 * m)这个公式的误差在0.2天左右。三、年份的确定　　1864年1月0日是农历癸亥年，所以用当年减去1864，用10除得的余数作为年份天干的，用12除得的余数作为年份的地支，数字对应的天干和地支如下。当年的1月0日换算为积日，可以用年份减去1900得到的年数被4整除，所得商数作为 y(D4)，余数作为y(M4)，y(M4)为零的年份是公历闰年，积日是&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; D(1) = 1461 * y(D4) - 1y(M4)不为零的年份是公历平年，积日是&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; D(1) = 1461 * y(D4) + 365 * y(M4)四、月份的确定　　计算前一年冬至的积日F(0)，并用F(0)计算冬至所在的朔月m及其朔日M(0)，就可以推算冬至的农历日期，冬至所在的农历月份总是十一月。计算下一个中气F(1)和下一个朔日M(1)，如果F(1)&lt;M(1)，那么该月就是上一个月的闰月，并把这个中气作为F(2)，以后的中气、朔日和农历月份也这样确定。','',0,'other',0,'','zinnia/_entry_detail.html','entry_detail.html'),(284,'ubuntu上编译Thrift','337319372010512115826446',2,'2010-06-12 11:58:26',NULL,'2010-06-12 11:58:26','2014-07-13 18:57:22','Thrift是Apache的一个开源项目，用来实现不同语言之间的通信，要使用编程语言操作cassandra，就需要Thrift。<br />可惜的是Ubuntu还不能使用apt-get安装Thrift，因此需要手工编译<br />编译环境：Ubuntu 8.10 (8.04的libtool包不能用)<br />首先安装必要的包<br />sudo apt-get install build-essential<br />sudo apt-get install bison flex <br />sudo apt-get install libboost-dev python-dev<br />sudo apt-get install autoconf automake libtool pkg-config<br />sudo apt-get install git<br />然后下载Thrift的源码包，并编译安装 <br />wget http://labs.renren.com/apache-mirror/incubator/thrift/0.2.0-incubating/thrift-0.2.0-incubating.tar.gz<br />tar xf thrift-0.2.0-incubating.tar.gz<br />cd thrift-0.2.0<br />./configure<br />make<br />sudo make install<br />安装成功，<br /><br />使用Python进行测试<br />cd tutorial<br />thrift --gen py thrift.thrift<br />thrift --gen py shared.thrift<br />cp py/* gen-py<br />cd gen-py<br />python PythonServer.py &amp;<br />python PythonClient.py',1,1,1,0,0,0,'Thrift是Apache的一个开源项目，用来实现不同语言之间的通信，要使用编程语言操作cassandra，就需要Thrift。可惜的是Ubuntu还不能使用apt-get安装Thrift，因此需要手工编译编译环境：Ubuntu 8.10 (8.04的libtool包不能用)首先安装必要的包sudo apt-get install build-essentialsudo apt-get install bison flex sudo apt-get install libboost-dev python-devsudo apt-get install autoconf automake libtool pkg-configsudo apt-get install git然后下载Thrift的源码包，并编译安装 wget http://labs.renren.com/apache-mirror/incubator/thrift/0.2.0-incubating/thrift-0.2.0-incubating.tar.gztar xf thrift-0.2.0-incubating.tar.gzcd thrift-0.2.0./configuremakesudo make install安装成功，使用Python进行测试cd tutorialthrift --gen py thrift.thriftthrift --gen py shared.thriftcp py/* gen-pycd gen-pypython PythonServer.py &amp;python PythonClient.py','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(285,'程序员的幽默uml_switch崩溃后','3373193720100943835158',2,'2010-01-09 16:38:35',NULL,'2010-01-09 16:38:35','2014-07-13 18:57:23','uml_switch被kill -9 干掉了，然后uml_switch给出如下提示信息：<br /><span style=\"font-weight: bold;\">Suddenly the Dungeon collapses!! - You die...</span><br />地下城塌了，你挂了！<br /><br />Dungeon：《Dungeon Runners》是NCSOFT北美分公司开发的MMORPG，游戏以幻想世界为舞台，玩家在地面和地牢中进行探险，完成游戏任务。<br /><br />',1,1,1,0,0,0,'uml_switch被kill -9 干掉了，然后uml_switch给出如下提示信息：Suddenly the Dungeon collapses!! - You die...地下城塌了，你挂了！Dungeon：《Dungeon Runners》是NCSOFT北美分公司开发的MMORPG，游戏以幻想世界为舞台，玩家在地面和地牢中进行探险，完成游戏任务。','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(286,'使用STL的sort','3373193720105612830308',2,'2010-06-06 13:28:30',NULL,'2010-06-06 13:28:30','2014-07-13 18:57:23','qsort的第三个参数的函数原型是<br />int cmp(const void *p1, const void *p2)<br />而STL的sort方法可以用第三个参数，也可以直接为结构体增加方法<br />bool operator &lt; (const struct X &amp;x) const{<br />&nbsp;///...<br />}<br /><br />#include &lt;algorithm&gt;<br />using namespace std;<br /><br />struct X{<br />&nbsp;int v;<br />&nbsp;bool operator &lt; (const struct X &amp;x){<br />&nbsp; return v &lt; x.v;<br />&nbsp;}<br />}xs[5];<br />int main(){<br />&nbsp; sort(&amp;xs[0], &amp;xs[5]);<br />&nbsp; return 0;<br />}',1,1,1,0,0,0,'qsort的第三个参数的函数原型是int cmp(const void *p1, const void *p2)而STL的sort方法可以用第三个参数，也可以直接为结构体增加方法bool operator &lt; (const struct X &amp;x) const{&nbsp;///...}#include &lt;algorithm&gt;using namespace std;struct X{&nbsp;int v;&nbsp;bool operator &lt; (const struct X &amp;x){&nbsp; return v &lt; x.v;&nbsp;}}xs[5];int main(){&nbsp; sort(&amp;xs[0], &amp;xs[5]);&nbsp; return 0;}','',0,'cc',0,'','zinnia/_entry_detail.html','entry_detail.html'),(287,'后悔当初没有使用CVS','33731937200786115943386',2,'2007-09-06 11:59:43',NULL,'2007-09-06 11:59:43','2014-07-13 18:57:23','<p>刚开始徐就提出使用CVS来控制我们的协同，可是我当时觉得用CVS太麻烦，而且每个人的模块都基本独立，因此提议大家自己控制版本。在Ftp上用版本编号的方式，现在随着程序越来越多，越来越后悔当时的决定。</p>\n<p>昨天晚上配置了一下CVS，记之：</p>\n<p>首先安装CVS服务器，这里用的是Windows下的cvsnt--Concurrent Versions System 2.0.58d；安装过程很简单，想Windows下的多数软件一样，下一步就行了，安装完成后配置：打开Service Control Panel，在Repositories选项卡中设置根目录（eg:/CVS E:/ide/eclipse/CVS），然后为了能通过Eclipse使用，需要在Compatibility选项卡中选中第一个CheckBox，（这个好像不用选也可以）。</p>\n<p>然后就可以通过Eclipse来使用了，新建工程，然后右击工程-〉Team-〉ShareProject，用户名和密码是Windows帐户和密码，然后就可以用Team-〉Submit来更新到CVS系统中。</p>\n<p>然后用Eclipse新建工程-〉CVS-〉CVS Project 签出项目。</p>\n<p>然后可以用Team-〉Show History来查看历史信息，选中不同的几个版本，然后Compare来比较。</p>\n<p>Concurrent Version System！</p>\n<p>&nbsp;</p>',1,1,1,0,0,0,'刚开始徐就提出使用CVS来控制我们的协同，可是我当时觉得用CVS太麻烦，而且每个人的模块都基本独立，因此提议大家自己控制版本。在Ftp上用版本编号的方式，现在随着程序越来越多，越来越后悔当时的决定。\n昨天晚上配置了一下CVS，记之：\n首先安装CVS服务器，这里用的是Windows下的cvsnt--Concurrent Versions System 2.0.58d；安装过程很简单，想Windows下的多数软件一样，下一步就行了，安装完成后配置：打开Service Control Panel，在Repositories选项卡中设置根目录（eg:/CVS E:/ide/eclipse/CVS），然后为了能通过Eclipse使用，需要在Compatibility选项卡中选中第一个CheckBox，（这个好像不用选也可以）。\n然后就可以通过Eclipse来使用了，新建工程，然后右击工程-〉Team-〉ShareProject，用户名和密码是Windows帐户和密码，然后就可以用Team-〉Submit来更新到CVS系统中。\n然后用Eclipse新建工程-〉CVS-〉CVS Project 签出项目。\n然后可以用Team-〉Show History来查看历史信息，选中不同的几个版本，然后Compare来比较。\nConcurrent Version System！\n&nbsp;','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(288,'MySQL终于扛不住了marked as crashed and should be repaired','3373193720106753957549',2,'2010-07-07 17:39:57',NULL,'2010-07-07 17:39:57','2014-07-13 18:57:23','python爬虫天以大约2M/s的速度连续14抓网页，并保存到MySQL数据表里，今天终于扛不住挂掉了<br />两个数据表，一个4.29G，一个4.17G，错误信息如下：<br />&nbsp; File \"build\\bdist.win32\\egg\\MySQLdb\\cursors.py\", line 174, in execute<br />&nbsp; File \"build\\bdist.win32\\egg\\MySQLdb\\connections.py\", line 36, in defaulterrorhandler<br />_mysql_exceptions.InternalError: (145, \"Table \'.\\\\xiaonei\\\\user\' is marked as crashed and should be repaired\")<br />很明显，是数据表出错了，应该不是程序错误，很可能就是硬盘顶不住了，或者MySQL的问题，<br />mysql&gt; use xiaonei;<br />mysql&gt; select count(*) from user;<br />ERROR 145 (HY000): Table \'.\\xiaonei\\user\' is marked as crashed and should be repaired<br />用得是MYISAM存储引擎，解决方式很简单，进入数据表存储路径，执行myisamchk命令修复就可以了，根据上面的提示信息是xiaonei数据库的user表坏了，进入xiaonei目录，执行myisamchk -r user命令，这里的C:\\ProgramData\\MySQL\\MySQL Server 5.1\\data\\是Win7下的MySQL默认安装路径，在WinXP下应该是C:\\Program Files\\MySQL\\MySQL Server 5.1\\data\\, under ubuntu 10下是/var/lib/mysql<br />C:\\ProgramData\\MySQL\\MySQL Server 5.1\\data\\xiaonei&gt;myisamchk -r user<br />- recovering (with sort) MyISAM-table \'user\'<br />Data records: 40126243<br />- Fixing index 1<br />Wrong bytesec:&nbsp;&nbsp; 0-&nbsp; 0-&nbsp; 0 at 4605839108; Skipped<br />Data records: 40126222<br />修复完成<br />可以看出：共40126243条记录，在4605839108条的地方出现了错误，修复完成后剩下40126222条记录，损失了21条记录。',1,1,1,0,0,0,'python爬虫天以大约2M/s的速度连续14抓网页，并保存到MySQL数据表里，今天终于扛不住挂掉了两个数据表，一个4.29G，一个4.17G，错误信息如下：&nbsp; File \"build\\bdist.win32\\egg\\MySQLdb\\cursors.py\", line 174, in execute&nbsp; File \"build\\bdist.win32\\egg\\MySQLdb\\connections.py\", line 36, in defaulterrorhandler_mysql_exceptions.InternalError: (145, \"Table \'.\\\\xiaonei\\\\user\' is marked as crashed and should be repaired\")很明显，是数据表出错了，应该不是程序错误，很可能就是硬盘顶不住了，或者MySQL的问题，mysql&gt; use xiaonei;mysql&gt; select count(*) from user;ERROR 145 (HY000): Table \'.\\xiaonei\\user\' is marked as crashed and should be repaired用得是MYISAM存储引擎，解决方式很简单，进入数据表存储路径，执行myisamchk命令修复就可以了，根据上面的提示信息是xiaonei数据库的user表坏了，进入xiaonei目录，执行myisamchk -r user命令，这里的C:\\ProgramData\\MySQL\\MySQL Server 5.1\\data\\是Win7下的MySQL默认安装路径，在WinXP下应该是C:\\Program Files\\MySQL\\MySQL Server 5.1\\data\\, under ubuntu 10下是/var/lib/mysqlC:\\ProgramData\\MySQL\\MySQL Server 5.1\\data\\xiaonei&gt;myisamchk -r user- recovering (with sort) MyISAM-table \'user\'Data records: 40126243- Fixing index 1Wrong bytesec:&nbsp;&nbsp; 0-&nbsp; 0-&nbsp; 0 at 4605839108; SkippedData records: 40126222修复完成可以看出：共40126243条记录，在4605839108条的地方出现了错误，修复完成后剩下40126222条记录，损失了21条记录。','',0,'database',0,'','zinnia/_entry_detail.html','entry_detail.html'),(289,'git','33731937201032191432747',2,'2010-04-21 21:14:32',NULL,'2010-04-21 21:14:32','2014-07-13 18:57:23','git与git-daemon<br />ssh方式<br /><span style=\"font-weight: bold;\">Client:</span><br />ssh-keygen<br /><span style=\"font-weight: bold;\">Server:</span><br />sudo adduser git<br />su git<br />cat hank.pub &gt;&gt; .ssh/authrized_keys<br />mkdir gitproj<br />cd gitproj<br />git -bare&nbsp; init<br /><span style=\"font-weight: bold;\">Client:<br /></span>mkdir gitproj<br />git remote add origin git@myserver:/home/git/gitproj<br />git push origin master<br /><br />git-daemon方式<br /><div>1. 安装git与git-daemon</div>\n<div>sudo apt-get install git-core git-daemon-run</div>\n<div>&nbsp;</div>\n<div>2. 配置git-daemon</div>\n<div><font face=\"monospace\">sudo vi /etc/sv/git-daemon/run</font></div>\n<div><font face=\"monospace\">#!/bin/sh<br />exec 2&gt;&amp;1<br />echo \'git-daemon starting.\'<br />exec git-daemon --verbose --export-all --base-path=/var/git --enable=receive-pack</font></div>\n<div>sudo /etc/init.d/git-daemon restart</div>\n<div>&nbsp;</div>\n<div>3.创建项目</div>\n<div><strong>Server:</strong></div>\n<div>cd /var/git</div>\n<div>mkdir hankjin</div>\n<div>cd hankjin</div>\n<div>git init</div>\n<div>touch hello.c</div>\n<div>git add hello.c</div>\n<div>git commit -m \"hello\"</div>\n<div>&nbsp;</div>\n<div><strong>Client:</strong></div>\n<div>cd /home/hankjin/git</div>\n<div>git clone git://localhost/hankjin</div>\n<div>cd hankjin</div>\n<div>git pull</div>\n<div>vi hello.c</div>\n<div>git add .</div>\n<div>git commit</div>\n<div>git push</div>\n<div>&nbsp;</div>\n<div><strong>Server:</strong></div>\n<div>git reset --hard<br /><br /><span style=\"font-weight: bold;\">常见错误：</span><br />1.git fatal: The remote end hung up unexpectedl<br />\n检查<font face=\"monospace\">/etc/sv/git-daemon/run的--base-path参数</font>，确保和server的git目录相同<br />2.git fatal no matching remote head<br />server上如果没有任何commit，则会出现该错误<br />3.git \'receive-pack\': service not enabled for<br /><font face=\"monospace\">在/etc/sv/git-daemon/run的git-daemon命令中加入： --enable=receive-pack</font><br /></div>',1,1,1,0,0,0,'git与git-daemonssh方式Client:ssh-keygenServer:sudo adduser gitsu gitcat hank.pub &gt;&gt; .ssh/authrized_keysmkdir gitprojcd gitprojgit -bare&nbsp; initClient:mkdir gitprojgit remote add origin git@myserver:/home/git/gitprojgit push origin mastergit-daemon方式1. 安装git与git-daemon\nsudo apt-get install git-core git-daemon-run\n&nbsp;\n2. 配置git-daemon\nsudo vi /etc/sv/git-daemon/run\n#!/bin/shexec 2&gt;&amp;1echo \'git-daemon starting.\'exec git-daemon --verbose --export-all --base-path=/var/git --enable=receive-pack\nsudo /etc/init.d/git-daemon restart\n&nbsp;\n3.创建项目\nServer:\ncd /var/git\nmkdir hankjin\ncd hankjin\ngit init\ntouch hello.c\ngit add hello.c\ngit commit -m \"hello\"\n&nbsp;\nClient:\ncd /home/hankjin/git\ngit clone git://localhost/hankjin\ncd hankjin\ngit pull\nvi hello.c\ngit add .\ngit commit\ngit push\n&nbsp;\nServer:\ngit reset --hard常见错误：1.git fatal: The remote end hung up unexpectedl\n检查/etc/sv/git-daemon/run的--base-path参数，确保和server的git目录相同2.git fatal no matching remote headserver上如果没有任何commit，则会出现该错误3.git \'receive-pack\': service not enabled for在/etc/sv/git-daemon/run的git-daemon命令中加入： --enable=receive-pack','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(290,'test','33731937200771713245917',2,'2007-08-17 13:32:45',NULL,'2007-08-17 13:32:45','2014-07-13 18:57:23','<div><font face=\"Verdana\"><font size=\"2\">email blog</font></font></div>\n<div><font size=\"2\"></font>&nbsp;</div>\n<div align=\"left\"><font size=\"2\">\n<hr size=\"2\" style=\"WIDTH: 122px; HEIGHT: 2px;\" />\n</font></div>\n<div><font color=\"#c0c0c0\"><font size=\"2\">黄进东</font></font></div>\n<div><font size=\"2\">2007-08-17</font></div>\n<table border=\"0\" cellspacing=\"0\">\n<tbody></tbody></table>',1,1,1,0,0,0,'email blog\n&nbsp;\n\n\n\n黄进东\n2007-08-17\n\n','',0,'mood',0,'','zinnia/_entry_detail.html','entry_detail.html'),(291,'Ubuntu8.04 VNC4server','3373193720084143421219',2,'2008-05-14 15:04:21',NULL,'2008-05-14 15:04:21','2014-07-13 18:57:23','sudo apt-get install vnc4server<br />vnc4server<br />vnc4server stop<br />vnc4server -kill:1<br />vi ~/.vnc/xstartup<br />#twm &amp;<br />gnome-session &amp;<br />vnc4server<br /><br />logon from windows, port:5901<br />',1,1,1,0,0,0,'sudo apt-get install vnc4servervnc4servervnc4server stopvnc4server -kill:1vi ~/.vnc/xstartup#twm &amp;gnome-session &amp;vnc4serverlogon from windows, port:5901','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(292,'执行本地命令并获取输出','337319372007101731115771',2,'2007-11-17 15:11:15',NULL,'2007-11-17 15:11:15','2014-07-13 18:57:23','<p>&nbsp;&nbsp;String s1 = \"ipconfig /all\";<br />&nbsp;&nbsp;//相当执行本地命令ipconfig/all<br />&nbsp;&nbsp;Process process = Runtime.getRuntime().exec(s1); <br />&nbsp;&nbsp;BufferedReader bufferedreader = new BufferedReader(new <br />&nbsp;&nbsp;&nbsp;InputStreamReader(process.getInputStream()));<br />&nbsp;&nbsp;String line = bufferedreader.readLine();<br />&nbsp;&nbsp;//筛选出Mac地址<br />&nbsp;&nbsp;while(line != null) {<br />&nbsp;&nbsp;&nbsp;line = bufferedreader.readLine();<br />&nbsp;&nbsp;&nbsp;System.out.println(line);<br />&nbsp;&nbsp;}<br />&nbsp;&nbsp;bufferedreader.close();<br />&nbsp;&nbsp;process.waitFor();</p>',1,1,1,0,0,0,'&nbsp;&nbsp;String s1 = \"ipconfig /all\";&nbsp;&nbsp;//相当执行本地命令ipconfig/all&nbsp;&nbsp;Process process = Runtime.getRuntime().exec(s1); &nbsp;&nbsp;BufferedReader bufferedreader = new BufferedReader(new &nbsp;&nbsp;&nbsp;InputStreamReader(process.getInputStream()));&nbsp;&nbsp;String line = bufferedreader.readLine();&nbsp;&nbsp;//筛选出Mac地址&nbsp;&nbsp;while(line != null) {&nbsp;&nbsp;&nbsp;line = bufferedreader.readLine();&nbsp;&nbsp;&nbsp;System.out.println(line);&nbsp;&nbsp;}&nbsp;&nbsp;bufferedreader.close();&nbsp;&nbsp;process.waitFor();','',0,'j2se',0,'','zinnia/_entry_detail.html','entry_detail.html'),(293,'JDK6－原生的WebService','3373193720076115355444',2,'2007-07-11 17:03:55',NULL,'2007-07-11 17:03:55','2014-07-13 18:57:23','<p>JDK1.6原生支持WebService。</p>  <p>不用任何集成环境，也不用J2EE服务器，只要一个1.6的JDK，就可以开发WebService了。而且相当方便简捷。</p>  <p>1.编写WebService Hello.java</p>  <p>2.编译WebService javac Hello.java</p>  <p>3.发布WebService&nbsp; java Hello</p>  <p>4.测试WebService <a href=\"http://localhost:8080/HelloService?wsdl\" rel=\"nofollow\">http://localhost:8080/HelloService?wsdl</a></p>  <p>5.获得WebService接口 wsimport -keep&nbsp; <a href=\"http://localhost:8080/HelloService?wsdl\" rel=\"nofollow\">http://localhost:8080/HelloService?wsdl</a></p>  <p>6.编写WebClient Test.java</p>  <p>7.编译，运行 javac Test.java; java Test</p>  <p>//Hello.java</p>  <p>package cn.hjz;</p>  <p>import javax.jws.WebMethod;<br />import javax.jws.WebService;<br />import javax.jws.soap.SOAPBinding;</p>  <p>@WebService(targetNamespace = \"<a href=\"http://hjz.cn/test\" rel=\"nofollow\">http://hjz.cn/test</a>\")<br />@SOAPBinding(style = SOAPBinding.Style.RPC)<br />public class Hello {<br />&nbsp;@WebMethod<br />&nbsp;public String sayHello(String name) {<br />&nbsp;&nbsp;return \"hello:\" + name;<br />&nbsp;}<br />&nbsp;public static void main(String[] args){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Endpoint.publish(\"<a href=\"http://localhost:8080/HelloService\" rel=\"nofollow\">http://localhost:8080/HelloService</a>\", new Hello());<br />&nbsp;&nbsp;&nbsp; }<br />}<br /></p>  <p>//Test.java</p>  <p><span style=\"COLOR: rgb(0,0,255);\"><font color=\"#000000\">package org.hermit.study.jdk.client.test;</font></span></p>  <p><span style=\"COLOR: rgb(0,0,255);\"><font color=\"#000000\">import cn.hjz.test.Hello;<br />import cn.hjz.test.HelloService;</font></span></p>  <p><span style=\"COLOR: rgb(0,0,255);\"><font color=\"#000000\"><br />public class Test {<br />&nbsp;&nbsp;&nbsp; public static void main(String[] args) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; HelloService service = new HelloService();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Hello _hello = service.getHelloPort();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println(_hello.sayHello(\"hankjin\"));<br />&nbsp;&nbsp;&nbsp; }<br />}</font></span></p><span style=\"COLOR: rgb(0,0,255);\"><font color=\"#000000\">  <p></p></font></span>&nbsp;',1,1,1,0,0,0,'JDK1.6原生支持WebService。  不用任何集成环境，也不用J2EE服务器，只要一个1.6的JDK，就可以开发WebService了。而且相当方便简捷。  1.编写WebService Hello.java  2.编译WebService javac Hello.java  3.发布WebService&nbsp; java Hello  4.测试WebService http://localhost:8080/HelloService?wsdl  5.获得WebService接口 wsimport -keep&nbsp; http://localhost:8080/HelloService?wsdl  6.编写WebClient Test.java  7.编译，运行 javac Test.java; java Test  //Hello.java  package cn.hjz;  import javax.jws.WebMethod;import javax.jws.WebService;import javax.jws.soap.SOAPBinding;  @WebService(targetNamespace = \"http://hjz.cn/test\")@SOAPBinding(style = SOAPBinding.Style.RPC)public class Hello {&nbsp;@WebMethod&nbsp;public String sayHello(String name) {&nbsp;&nbsp;return \"hello:\" + name;&nbsp;}&nbsp;public static void main(String[] args){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Endpoint.publish(\"http://localhost:8080/HelloService\", new Hello());&nbsp;&nbsp;&nbsp; }}  //Test.java  package org.hermit.study.jdk.client.test;  import cn.hjz.test.Hello;import cn.hjz.test.HelloService;  public class Test {&nbsp;&nbsp;&nbsp; public static void main(String[] args) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; HelloService service = new HelloService();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Hello _hello = service.getHelloPort();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println(_hello.sayHello(\"hankjin\"));&nbsp;&nbsp;&nbsp; }}  &nbsp;','',0,'j2se',0,'','zinnia/_entry_detail.html','entry_detail.html'),(294,'富文本编辑器FCKeditor','337319372008441042788',2,'2008-05-04 22:04:02',NULL,'2008-05-04 22:04:02','2014-07-13 18:57:23','<p><a href=\"http://www.fckeditor.net/download/\" rel=\"nofollow\">http://www.fckeditor.net/download/</a></p>  <p>很好的支持JSP，可以在JSP中做HTML编辑器。</p>',1,1,1,0,0,0,'http://www.fckeditor.net/download/  很好的支持JSP，可以在JSP中做HTML编辑器。','',0,'\"web skills\"',0,'','zinnia/_entry_detail.html','entry_detail.html'),(295,'用Alt打出特殊字符','337319372009929102047159',2,'2009-10-29 10:20:47',NULL,'2009-10-29 10:20:47','2014-07-13 18:57:23','<h2>Letters with Accents</h2>           <p>This list is organized by <b>Accent</b> type. To determine the appropriate         numeric code, match the accent with the vowel. See the <a href=\"http://tlt.its.psu.edu/suggestions/international/bylanguage/index.html\" rel=\"nofollow\">individual By Language pages</a> to see how to handle other accents. </p>                  <table border=\"0\" cellpadding=\"5\" cellspacing=\"0\">        <tbody><tr>          <th scope=\"col\">Accent</th>          <th scope=\"col\">A</th>          <th scope=\"col\">E</th>          <th scope=\"col\">I</th>          <th scope=\"col\">O</th>          <th scope=\"col\">U</th>          <th scope=\"col\">Y</th>        </tr>        <tr>          <th scope=\"row\">Grave<span><br />        (Cap)</span></th>          <td><b>?</b><br />        0192</td>          <td><b>?</b> <br />        0200</td>          <td><b>?</b><br />        0204</td>          <td><b>?</b><br />        0210</td>          <td><b>?</b> <br />        0217</td>          <td>&nbsp;</td>        </tr>        <tr>          <th scope=\"row\">Grave (Lower)</th>          <td><b>à</b><br />        0224</td>          <td><b>è</b><br />        0232</td>          <td><b>ì</b><br />        0236</td>          <td><b>ò</b><br />        0242</td>          <td><b>ù</b><br />        0249</td>          <td>&nbsp;</td>        </tr>        <tr>          <th scope=\"row\">Acute <span><br />        (Cap)</span></th>          <td><b>?</b><br />        0193</td>          <td><b>?</b><br />        0201</td>          <td><b>?</b><br />        0205</td>          <td><b>?</b><br />        0211</td>          <td><b>?</b><br />        0218</td>          <td><b>?</b><br />        0221</td>        </tr>        <tr>          <th scope=\"row\">Acute (Lower)</th>          <td><b>á</b><br />        0225</td>          <td><b>é</b><br />        0233</td>          <td><b>í</b><br />        0237</td>          <td><b>ó</b><br />        0243</td>          <td><b>ú</b><br />        0250</td>          <td><b>?</b><br />        0253</td>        </tr>        <tr>          <th scope=\"row\">Circumflex<br />          <span>(Cap)</span></th>          <td><b>?</b><br />        0194</td>          <td><b>?</b><br />        0202</td>          <td><b>?</b><br />        0206</td>          <td><b>?</b><br />        0212</td>          <td><b>?</b><br />        0219</td>          <td>&nbsp;</td>        </tr>        <tr>          <th scope=\"row\">Circumflex (Lower)</th>          <td><b>?</b><br />        0226</td>          <td><b>ê</b><br />        0234</td>          <td><b>?</b><br />        0238</td>          <td><b>?</b><br />        0244</td>          <td><b>?</b><br />        0251</td>          <td>&nbsp;</td>        </tr>        <tr>          <th scope=\"row\">Tilde <br />          <span>(Cap)</span></th>          <td><b>?</b><br />        0195</td>          <td>--</td>          <td><b>?</b><br />        0209</td>          <td><b>?</b><br />        0213</td>          <td>-- </td>          <td>&nbsp;</td>        </tr>        <tr>          <th scope=\"row\">Tilde (Lower)</th>          <td><b>?</b><br />        0227</td>          <td>--</td>          <td><b>?</b><br />        0241</td>          <td><b>?</b><br />        0245</td>          <td>-- </td>          <td>&nbsp;</td>        </tr>        <tr>          <th scope=\"row\">Umlaut <span><br />        (Cap)</span></th>          <td><b>?</b><br />        0196</td>          <td><b>?</b><br />        0203</td>          <td><b>?</b><br />        0207</td>          <td><b>?</b><br />        0214</td>          <td><b>?</b> <br />        0220</td>          <td><b>?</b><br />        0159</td>        </tr>        <tr>          <th scope=\"row\">Umlaut (Lower)</th>          <td><b>?</b><br />        0228</td>          <td><b>?</b><br />        0235</td>          <td><b>?</b><br />        0239</td>          <td><b>?</b><br />        0246</td>          <td><b>ü</b><br />        0252</td>          <td><b>?</b><br />        0255</td>        </tr>      </table>      <p>&nbsp;</p>      <p><b>Example: </b>To input the acute a <b>á</b> <b> (0225)</b>, hold         down the <b>ALT</b> key, type <b>0225 </b>on the numeric keypad, then release         the <b>ALT</b> key.</p>      <p>If you are having problems inputting these codes, please review the instructions         for <a href=\"http://tlt.its.psu.edu/suggestions/international/accents/codealt.html#using\" rel=\"nofollow\">using the codes</a> at the bottom of this Web page.</p>        <p> <a href=\"http://tlt.its.psu.edu/suggestions/international/accents/codealt.html#top\" rel=\"nofollow\">Top of Page</a></p>    <h2><a rel=\"nofollow\"></a>Other Foreign Characters</h2>    <p>These include special punctuation and unique consonant and vowel symbols.</p>                 <table border=\"0\" cellpadding=\"5\" cellspacing=\"0\">        <tbody><tr>          <th scope=\"col\">SYMBOL</th>           <th scope=\"col\">NAME</th>               <th scope=\"col\">CODE NUMBER </th>        </tr>        <tr>           <td> ?</td>          <td width=\"40%\">Upside-down exclamation mark</td>          <td align=\"center\"><b>0161</b></td>        </tr>        <tr>           <td> ?</td>          <td>Upside-down question mark</td>          <td align=\"center\"><b>0191</b></td>        </tr>        <tr>           <td> ?, ?</td>          <td>French C cedille (caps/lowecase)</td>          <td a